import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_67" tabindex="-1"><a class="header-anchor" href="#_67" aria-hidden="true">#</a> 67</h1><p>\u98CE\u58F0\u54CD\u8D77\uFF0C\u8FDC\u5904\u4E00\u5411\u5E73\u9759\u65E0\u6CE2\u7684\u6F84\u6E56\uFF0C\u4E5F\u4F20\u6765\u6C34\u5CB8\u76F8\u51FB\u7684\u8F7B\u54CD\u3002\u5BC6\u6797\u91CC\uFF0C\u5DE8\u6811\u9AD8\u5927\u7684\u9634\u5F71\u5E26\u7740\u8FEB\u4EBA\u7684\u5BD2\u6C14\uFF0C\u5C06\u4E09\u4EBA\u7B3C\u7F69\u5728\u6DF1\u9083\u7684\u5E7D\u6697\u4E2D\u3002</p><p>\u826F\u4E45\uFF0C\u7D2B\u73AB\u70B9\u4E86\u70B9\u5934\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u8BB0\u4F4F\u4E86\u3002\u300D</p><p>\u8089\u68D2\u5212\u8FC7\u957F\u957F\u7684\u8DDD\u79BB\uFF0C\u91CD\u91CD\u9876\u5728\u5BAB\u9888\u4E0A\u3002\u7D2B\u73AB\u5A07\u8EAF\u4E00\u7D27\uFF0C\u7EC6\u7709\u8F7B\u76B1\u3002\u5979\u5782\u4E0B\u773C\u775B\uFF0C\u52AA\u529B\u633A\u8D77\u7389\u81C0\uFF0C\u7528\u5973\u6027\u72EC\u6709\u7684\u5668\u5B98\u4F9B\u8EAB\u540E\u7684\u79BD\u517D\u53D6\u4E50\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5934\u90E8\u4EF0\u8D77\uFF0C\u65E0\u6CD5\u770B\u5230\u7231\u5F92\u3002\u7C89\u788E\u7684\u8098\u3001\u819D\u5DF2\u7ECF\u53D8\u5F97\u7D2B\u9ED1\uFF0C\u8FC7\u4E0D\u4E86\u591A\u4E45\uFF0C\u624B\u81C2\u548C\u5C0F\u817F\u5C31\u4F1A\u574F\u6B7B\u2014\u2014\u5230\u90A3\u65F6\uFF0C\u4E5F\u5C31\u662F\u81EA\u5DF1\u4E27\u547D\u7684\u65F6\u523B\u4E86\u3002\u5267\u75DB\u5E76\u672A\u80FD\u9EBB\u75F9\u5979\u7684\u610F\u5FD7\uFF0C\u795E\u5C3C\u7684\u773C\u795E\u4F9D\u7136\u5BD2\u51B7\u800C\u9510\u5229\uFF0C\u91CC\u9762\u53EA\u6709\u65E0\u8FB9\u7684\u6068\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u62BD\u9001\uFF0C\u4E00\u8FB9\u6CE8\u89C6\u7740\u7D2B\u73AB\u7684\u53CD\u5E94\u3002\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u4ED6\u62D4\u51FA\u9633\u5177\uFF0C\u7B11\u9053\uFF1A\u300C\u8FC7\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u9ED8\u9ED8\u8D77\u8EAB\uFF0C\u89C1\u6155\u5BB9\u9F99\u9AD8\u5750\u5728\u94C1\u7B3C\u4E0A\uFF0C\u4E0D\u7531\u6115\u7136\u3002</p><p>\u6155\u5BB9\u9F99\u7684\u7B11\u5BB9\u91CC\u5E26\u7740\u6D53\u6D53\u7684\u90AA\u6076\uFF0C\u300C\u4E0A\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u4F9D\u8A00\u6500\u4E0A\u94C1\u7B3C\uFF0C\u6709\u4E9B\u4E0D\u77E5\u6240\u63AA\u5730\u770B\u7740\u7B3C\u5185\u7684\u5E08\u7236\u3002\u7247\u523B\u7684\u614C\u4E71\u540E\uFF0C\u5979\u5E73\u9759\u4E0B\u6765\u3002\u4E0D\u7528\u6155\u5BB9\u9F99\u5F00\u53E3\uFF0C\u7D2B\u73AB\u4FBF\u5F2F\u4E0B\u8170\uFF0C\u4E00\u624B\u6276\u7740\u4ED6\u7684\u80A9\u5934\uFF0C\u4E00\u624B\u63E1\u7740\u8089\u68D2\uFF0C\u8BD5\u63A2\u7740\u5750\u4E86\u4E0B\u53BB\u3002</p><p>\u8089\u68D2\u8FD8\u5E26\u7740\u81EA\u5DF1\u7684\u4F53\u6DB2\uFF0C\u53C8\u6E7F\u53C8\u70ED\uFF0C\u6307\u5C16\u63A0\u8FC7\u5BC6\u5E03\u7684\u575A\u786C\u9897\u7C92\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u9690\u9690\u53D1\u98A4\u3002\u5979\u600E\u4E48\u90FD\u96BE\u4EE5\u76F8\u4FE1\uFF0C\u8FD9\u6839\u644A\u5F00\u624B\u638C\u4E5F\u65E0\u6CD5\u63E1\u4F4F\u7684\u5DE8\u7269\uFF0C\u7ADF\u7136\u80FD\u8FDB\u5165\u81EA\u5DF1\u4F53\u5185\u2026\u2026</p><p>\u4F46\u8FD9\u4E9B\u72B9\u8C6B\u548C\u60CA\u60E7\u88AB\u6DF1\u6DF1\u57CB\u5728\u5FC3\u5E95\uFF0C\u7D2B\u73AB\u8138\u4E0A\u6BEB\u65E0\u5F02\u72B6\uFF0C\u52A8\u4F5C\u4E5F\u6CA1\u6709\u4E00\u4E1D\u505C\u987F\u3002\u5979\u751A\u81F3\u6CA1\u6709\u9732\u51FA\u4E00\u4E1D\u7F9E\u803B\u548C\u96BE\u582A\uFF0C\u5C31\u8FD9\u6837\u5728\u4E25\u5389\u800C\u53C8\u6148\u7231\u7684\u5E08\u7236\u8138\u4E0A\uFF0C\u4E3B\u52A8\u6C89\u4E0B\u8170\u80A2\uFF0C\u8FCE\u5411\u7537\u4EBA\u7684\u9633\u5177\u3002</p><p>\u5149\u6DA6\u7684\u96EA\u81C0\u8D8A\u6765\u8D8A\u8FD1\uFF0C\u5A07\u67D4\u7684\u82B1\u74E3\u8FD8\u5E26\u7740\u5904\u5B50\u7684\u7A1A\u5AE9\uFF0C\u5728\u773C\u524D\u5C42\u5C42\u7EFD\u5F00\uFF0C\u6700\u540E\u843D\u5728\u7D2B\u4EAE\u7684\u9F9F\u5934\u4E0A\u3002\u5B83\u79BB\u5F97\u5982\u6B64\u4E4B\u8FD1\uFF0C\u96EA\u5CF0\u795E\u5C3C\u751A\u81F3\u80FD\u770B\u6E05\u5AE9\u8089\u7EC6\u5FAE\u7684\u8815\u52A8\u3002</p><p>\u82B1\u74E3\u5728\u9F9F\u5934\u4E0A\u7565\u7565\u4E00\u987F\uFF0C\u4FBF\u987A\u4ECE\u5730\u67D4\u67D4\u5206\u5F00\u3002\u8089\u7A74\u7D27\u7A84\u7684\u5165\u53E3\u88AB\u5B8C\u5168\u6491\u5F00\uFF0C\u5145\u6EE1\u5F39\u6027\u5730\u5F20\u6210\u6D51\u5706\uFF0C\u5C06\u8089\u68D2\u541E\u5165\u5176\u4E2D\u3002</p><p>\u9633\u5177\u62B5\u4F4F\u4E0B\u4F53\u7684\u4E00\u523B\uFF0C\u7D2B\u73AB\u4FBF\u677E\u5F00\u8089\u68D2\uFF0C\u4E24\u624B\u90FD\u6276\u5728\u6155\u5BB9\u9F99\u80A9\u5934\uFF0C\u81C0\u90E8\u8F7B\u6643\u7740\u7F13\u7F13\u5750\u4E0B\u3002\u5979\u659C\u7740\u8EAB\u5B50\uFF0C\u9999\u80A9\u540E\u4EF0\uFF0C\u628A\u4E0B\u4F53\u51D1\u5411\u6155\u5BB9\u9F99\u7684\u5C0F\u8179\u3002\u5F85\u8089\u68D2\u8FDB\u5165\u534A\u6570\u4E4B\u540E\uFF0C\u7D2B\u73AB\u79C0\u7709\u4E00\u7D27\uFF0C\u96EA\u767D\u7684\u5589\u5934\u5FAE\u5FAE\u8815\u52A8\u4E86\u4E00\u4E0B\uFF0C\u7136\u540E\u5C4F\u4F4F\u547C\u5438\uFF0C\u6781\u529B\u6C89\u8170\uFF0C\u53FD\u7684\u4E00\u58F0\uFF0C\u628A\u90A3\u4E2A\u5DE8\u5927\u7684\u8089\u7624\u7EB3\u5165\u4F53\u5185\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u4E0D\u5FCD\uFF0C\u5C55\u81C2\u5C06\u542C\u8BDD\u7684\u59B9\u59B9\u6402\u5728\u6000\u4E2D\uFF0C\u6063\u610F\u7231\u629A\u3002\u7389\u4EBA\u901A\u4F53\u51B0\u51C9\uFF0C\u8089\u7A74\u5185\u5374\u70BD\u70ED\u5982\u706B\uFF0C\u8170\u8EAB\u8FD8\u672A\u52A8\u4F5C\uFF0C\u8089\u58C1\u4FBF\u81EA\u884C\u4E00\u677E\u4E00\u7D27\u5730\u6536\u7F29\u8D77\u6765\u3002\u575A\u633A\u7684\u4E73\u5CF0\u7D27\u7D27\u8D34\u5728\u80F8\u524D\uFF0C\u51E0\u4E4E\u80FD\u611F\u89C9\u5230\u786C\u786C\u7684\u4E73\u5934\u3002\u6155\u5BB9\u9F99\u628A\u9F3B\u5B50\u4F38\u8FDB\u59B9\u59B9\u8033\u540E\u7684\u53D1\u4E1D\u4E2D\uFF0C\u6DF1\u6DF1\u55C5\u7740\u59BB\u5B50\u8FF7\u4EBA\u7684\u53D1\u9999\u3002</p><p>\u300C\u7B49\u6211\u6062\u590D\u5927\u71D5\uFF0C\u5F53\u4E0A\u7687\u5E1D\uFF0C\u4F60\u5C31\u662F\u6211\u7684\u7687\u540E\u2026\u2026\u300D\u6155\u5BB9\u9F99\u8214\u8210\u7740\u7D2B\u73AB\u6676\u83B9\u7684\u8033\u5782\uFF0C\u5462\u54DD\u7740\u8BF4\u3002</p><p>\u300C\u55EF\u2026\u2026\u300D\u7D2B\u73AB\u6E29\u987A\u5730\u4F0F\u5728\u6155\u5BB9\u9F99\u6000\u4E2D\uFF0C\u5A07\u8EAF\u67D4\u82E5\u65E0\u9AA8\uFF0C\u82B3\u9999\u56DB\u6EA2\u3002\u5979\u5C0F\u732B\u822C\u4E56\u4E56\u70B9\u4E86\u70B9\u5934\uFF0C\u8F7B\u8F7B\u7B54\u5E94\u4E00\u58F0\u3002</p><p>\u6B64\u65F6\u9633\u5177\u5DF2\u7ECF\u5B8C\u5168\u8FDB\u5165\u4EE4\u4EBA\u9B42\u9500\u7684\u8089\u7A74\uFF0C\u56DB\u5468\u5C3D\u662F\u6ED1\u817B\u65E0\u6BD4\u7684\u8F6F\u8089\uFF0C\u4EFF\u4F5B\u63E1\u7740\u67AA\u950B\u7684\u7EC6\u5AE9\u67D4\u8351\uFF0C\u7D27\u5BC6\u5305\u88F9\u7740\u90A3\u6839\u72F0\u72DE\u7684\u5DE8\u68D2\u3002</p><p>\u6B64\u65F6\u6155\u5BB9\u9F99\u65E9\u5DF2\u539F\u8C05\u4E86\u7D2B\u73AB\u90A3\u4E9B\u5C0F\u5C0F\u7684\u53CD\u6297\uFF0C\u89E6\u624B\u65E0\u9650\u6E29\u5B58\u5730\u7FFB\u5F00\u82B1\u74E3\uFF0C\u8F7B\u8F7B\u6311\u5F04\u5176\u4E2D\u7684\u82B1\u8482\u3002</p><p>\u7D2B\u73AB\u5A07\u547B\u4E00\u58F0\uFF0C\u4E0B\u4F53\u6DEB\u6DB2\u6CC9\u6D8C\u3002</p><p>\u6E29\u70ED\u7684\u6DB2\u4F53\u6253\u5728\u795E\u5C3C\u8138\u4E0A\uFF0C\u7D27\u95ED\u7684\u53CC\u773C\u970D\u7136\u5F20\u5F00\uFF0C\u6068\u6068\u76EF\u7740\u4E24\u4EBA\u4EA4\u5408\u7684\u90E8\u4F4D\u3002</p><p>\u773C\u73E0\u4E00\u8F6C\uFF0C\u5979\u53D1\u73B0\u6155\u5BB9\u9F99\u7684\u777E\u4E38\u5C31\u5782\u5728\u5507\u8FB9\uFF0C\u96EA\u5CF0\u795E\u5C3C\u6BEB\u4E0D\u72B9\u8C6B\u5730\u5F20\u53E3\u54AC\u4E0B\u3002</p><p>\u4E00\u9635\u5927\u7B11\u4ECE\u5934\u9876\u4F20\u6765\uFF0C\u6155\u5BB9\u9F99\u65E9\u5DF2\u7B97\u597D\u4E86\u8DDD\u79BB\uFF0C\u795E\u5C3C\u4E00\u53E3\u54AC\u4E0B\uFF0C\u624D\u53D1\u89C9\u81EA\u5DF1\u7684\u7259\u9F7F\u8FD8\u5DEE\u7740\u4E00\u4E1D\u624D\u80FD\u54AC\u5230\uFF0C\u6B64\u65F6\u53EA\u662F\u628A\u777E\u4E38\u542B\u5728\u5507\u95F4\u3002</p><p>\u300C\u54C8\u54C8\uFF0C\u5E08\u592A\u771F\u662F\u6BB7\u52E4\uFF0C\u5C45\u7136\u4E3B\u52A8\u66FF\u672C\u5BAB\u5438\u5C4C\u2026\u2026\u662F\u4E0D\u662F\u5C44\u75D2\u4E86\uFF0C\u60F3\u8BA9\u4E3B\u5B50\u64CD\u5462\uFF1F\u300D\u6155\u5BB9\u9F99\u7B11\u563B\u563B\u8BF4\u7740\uFF0C\u4F38\u817F\u4E00\u52FE\uFF0C\u811A\u8DDF\u6B63\u8E22\u5728\u795E\u5C3C\u81C0\u95F4\u7684\u677E\u679D\u4E0A\u3002</p><p>\u711A\u60C5\u818F\u521D\u7528\u5E76\u65E0\u611F\u89C9\uFF0C\u4F46\u795E\u5C3C\u7684\u4E0B\u4F53\u8FC7\u4E8E\u654F\u611F\uFF0C\u7EB5\u7136\u662F\u4E00\u6839\u6CA1\u6709\u751F\u547D\u7684\u677E\u679D\uFF0C\u4E5F\u4F7F\u5979\u7389\u4F53\u5267\u98A4\u3002</p><p>\u811A\u8DDF\u4E00\u6E7F\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7531\u5931\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u597D\u751F\u6DEB\u8361\uFF0C\u8FD9\u4E5F\u80FD\u6D6A\u5F97\u6EF4\u6C34\u513F\u2026\u2026\u300D</p><p>\u811A\u8DDF\u7528\u529B\u4E00\u8E22\uFF0C\u5C06\u534A\u5C3A\u957F\u7684\u677E\u679D\u6574\u6839\u8E22\u8FDB\u8089\u7A74\uFF0C\u8FDE\u809B\u4E2D\u90A3\u6839\u8F83\u7EC6\u7684\u4E5F\u672A\u80FD\u5E78\u514D\u3002</p><p>\u8098\u3001\u819D\u88AB\u751F\u751F\u634F\u788E\u90FD\u4E00\u58F0\u4E0D\u54CD\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u6B64\u65F6\u5374\u95F7\u54FC\u4E00\u58F0\uFF0C\u5659\u7740\u6155\u5BB9\u9F99\u777E\u4E38\u7684\u7EA2\u5507\u4E0D\u4F4F\u6218\u6817\u3002</p><p>\u6155\u5BB9\u9F99\u653E\u58F0\u5927\u7B11\uFF0C\u62AC\u811A\u53C8\u5F85\u91CD\u91CD\u8E22\u53BB\uFF0C\u6000\u91CC\u7684\u7389\u4EBA\u5FFD\u7136\u4E00\u52A8\uFF0C\u7D2B\u73AB\u594B\u529B\u62AC\u8D77\u8170\u80A2\uFF0C\u4E3B\u52A8\u5957\u5F04\u8D77\u6765\u3002\u6155\u5BB9\u9F99\u7565\u4E00\u72B9\u8C6B\uFF0C\u7F13\u7F13\u653E\u4E0B\u817F\uFF0C\u51DD\u89C6\u59B9\u59B9\u7247\u523B\uFF0C\u5FC3\u91CC\u5FAE\u53F9\u4E00\u58F0\uFF0C\u7136\u540E\u772F\u8D77\u53CC\u773C\uFF0C\u4EAB\u53D7\u7740\u5C11\u5973\u6E7F\u6DA6\u7684\u8089\u7A74\u3002</p><p>\u7D2B\u73AB\u660E\u4EAE\u7684\u5927\u773C\u71A0\u71A0\u751F\u8F89\uFF0C\u5979\u4ED4\u7EC6\u89C2\u5BDF\u7740\u6155\u5BB9\u9F99\u82F1\u4FCA\u7684\u8138\u5E9E\uFF0C\u6839\u636E\u4ED6\u7684\u795E\u8272\u8C03\u6574\u81EA\u5DF1\u7684\u52A8\u4F5C\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u540E\uFF0C\u7D2B\u73AB\u5DF2\u7ECF\u5957\u5F04\u5F97\u8170\u9178\u817F\u8F6F\uFF0C\u5468\u8EAB\u6CDB\u8D77\u73AB\u7470\u8272\u7684\u5A07\u8273\u7EA2\u8272\u3002\u9999\u6C57\u6DF7\u7740\u6DEB\u6C34\uFF0C\u96E8\u70B9\u822C\u843D\u5728\u795E\u5C3C\u9762\u4E0A\u3002\u82B1\u74E3\u5185\u6405\u52A8\u7684\u89E6\u624B\u8D8A\u6765\u8D8A\u591A\uFF0C\u5979\u5DF2\u7ECF\u5206\u4E0D\u51FA\u54EA\u4E9B\u662F\u5728\u62E8\u5F04\u81EA\u5DF1\u7684\u82B1\u74E3\uFF0C\u54EA\u4E9B\u662F\u5728\u6311\u5F04\u82B1\u8482\u3002</p><p>\u4E0B\u4F53\u7684\u5FEB\u611F\u8D8A\u6765\u8D8A\u5F3A\u70C8\uFF0C\u5F53\u4E00\u4E1B\u89E6\u624B\u7A81\u7136\u94BB\u8FDB\u83CA\u809B\uFF0C\u5F3A\u70C8\u5730\u5FEB\u611F\u987F\u65F6\u6DF9\u6CA1\u4E86\u5979\u7684\u8EAB\u5FC3\u3002\u82B1\u5FC3\u4E00\u9635\u5267\u98A4\uFF0C\u9634\u7CBE\u503E\u6CC4\u800C\u51FA\u3002</p><p>\u7D2B\u73AB\u8F6F\u7EF5\u7EF5\u504E\u4F9D\u5728\u6155\u5BB9\u9F99\u80F8\u819B\u4E0A\uFF0C\u9AD8\u6F6E\u7684\u6218\u6817\u8FD8\u672A\u6B62\u6B47\uFF0C\u5979\u8138\u4E0A\u7684\u6F6E\u7EA2\u5374\u5FFD\u7136\u892A\u53BB\uFF0C\u53D8\u5F97\u7070\u767D\u3002\u5979\u4E5F\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u795E\u8272\uFF0C\u8FDE\u5FD9\u5782\u9996\u4FEF\u5728\u6155\u5BB9\u9F99\u80A9\u540E\uFF0C\u4E0D\u8BA9\u4ED6\u770B\u5230\u81EA\u5DF1\u7684\u5931\u671B\u3002</p><p>\u6155\u5BB9\u9F99\u4F38\u51FA\u820C\u5C16\uFF0C\u5728\u59B9\u59B9\u5E03\u6EE1\u7684\u9999\u6C57\u96EA\u767D\u67D4\u9888\u4E0A\u8F7B\u8F7B\u8214\u8210\uFF0C\u300C\u59B9\u59B9\u7D2F\u4E86\u5427\uFF1F</p><p>\u8BA9\u54E5\u54E5\u597D\u597D\u75BC\u4F60\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u62B1\u8D77\u59B9\u59B9\u8F7B\u76C8\u7684\u8EAB\u4F53\uFF0C\u5C06\u96EA\u81C0\u76F4\u63A5\u653E\u5728\u795E\u5C3C\u8138\u4E0A\uFF0C\u7136\u540E\u7528\u624B\u81C2\u6321\u7740\u7D2B\u73AB\u7684\u819D\u5F2F\uFF0C\u7F13\u62BD\u6025\u9001\u3002</p><p>\u7D2B\u73AB\u4E0D\u8FC7\u662F\u521A\u7ECF\u4EBA\u4E8B\u7684\u5C11\u5973\uFF0C\u867D\u7136\u6EE1\u5FC3\u60F3\u7528\u8089\u4F53\u6765\u5F81\u670D\u8FD9\u4E2A\u66B4\u623E\u7684\u79BD\u517D\uFF0C\u4F46\u5728\u6155\u5BB9\u9F99\u7684\u6DEB\u6280\u4E0B\u8FD8\u662F\u53C8\u4E00\u6B21\u8D25\u4E0B\u9635\u6765\u3002\u5979\u54AC\u4F4F\u7EA2\u5507\uFF0C\u5FCD\u53D7\u7740\u8089\u4F53\u80CC\u53DB\u5FC3\u7075\u7684\u65E0\u5948\u3002</p><p>\u5728\u7D2B\u73AB\u706B\u70ED\u7684\u8154\u4F53\u5185\uFF0C\u6155\u5BB9\u9F99\u4E5F\u672A\u80FD\u652F\u6491\u592A\u957F\u65F6\u5019\u3002\u5728\u7D2B\u73AB\u53C8\u4E00\u6B21\u9AD8\u6F6E\u7684\u540C\u65F6\uFF0C\u4ED6\u4E5F\u52B2\u8EAF\u4E00\u6296\uFF0C\u5F00\u59CB\u4E86\u4ECA\u5929\u7684\u7B2C\u4E8C\u6B21\u5C04\u7CBE\u3002</p><p>\u5FEB\u611F\u4F59\u97F5\u672A\u6B62\uFF0C\u6155\u5BB9\u9F99\u62E5\u7740\u7D2B\u73AB\u5728\u7A7A\u4E2D\u4E00\u4E2A\u7FFB\u6EDA\uFF0C\u8F7B\u8F7B\u843D\u5728\u8349\u5730\u4E0A\u3002\u4ED6\u7231\u601C\u65E0\u9650\u5730\u5728\u7D2B\u73AB\u5507\u89D2\u8F7B\u8F7B\u4E00\u543B\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u4F60\u5148\u4F11\u606F\u4E00\u4F1A\u513F\u2026\u2026\u817F\u5408\u8D77\u6765\uFF0C\u55EF\uFF0C\u5BF9\u4E86\u3002\u624B\u6309\u597D\uFF0C\u8D76\u7D27\u50CF\u5A18\u4E00\u6837\u7ED9\u54E5\u54E5\u517B\u4E2A\u5C0F\u5B9D\u5B9D\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5B9B\u5982\u4E00\u8F6E\u660E\u6708\uFF0C\u9759\u9759\u8EBA\u5728\u78A7\u7EFF\u7684\u957F\u8349\u4E2D\u3002\u5979\u5FC3\u91CC\u7FFB\u6EDA\u6EDA\uFF0C\u4F3C\u4E4E\u6709\u8BB8\u591A\u529E\u6CD5\uFF0C\u5374\u53C8\u4F3C\u4E4E\u5BF9\u4E00\u5207\u675F\u624B\u65E0\u7B56\u3002\u5B50\u5BAB\u53E3\u5DF2\u7ECF\u95ED\u7D27\uFF0C\u7CBE\u6DB2\u88AB\u79EF\u5728\u5B50\u5BAB\u5185\uFF0C\u7B49\u5F85\u4E0E\u5375\u5B50\u7ED3\u5408\u2014\u2014\u81EA\u5DF1\u8FD8\u6709\u591A\u5C11\u65F6\u95F4\u5462\uFF1F</p><p>\u6155\u5BB9\u9F99\u6709\u4E9B\u60CB\u60DC\u5730\u7231\u629A\u7740\u795E\u5C3C\u4EC5\u5B58\u7684\u5DE6\u819D\uFF0C\u50CF\u662F\u4E0B\u4E86\u5F88\u5927\u51B3\u5FC3\u822C\u8BF4\u9053\uFF1A\u300C\u8BF4\u4E00\u53E5\uFF1A\u6DEB\u5987\u96EA\u5CF0\u613F\u751F\u751F\u4E16\u4E16\u505A\u4E3B\u4EBA\u7684\u5974\u5A62\uFF0C\u8001\u5B50\u5C31\u653E\u8FC7\u4F60\u8FD9\u6761\u817F\u3002\u300D</p><p>\u56DE\u7B54\u4ED6\u7684\u53EA\u6709\u6C89\u9ED8\u3002</p><p>\u300C\u5509\u2026\u2026\u300D\u6155\u5BB9\u9F99\u53F9\u606F\u7740\u6447\u5934\u3002\u300C\u60F3\u5F53\u65E5\u5E08\u592A\u95EF\u5BAB\u65F6\u5982\u4F55\u5A01\u98CE\uFF01\u4E24\u4F4D\u62A4\u6CD5\u3001\u56DB\u4F4D\u957F\u8001\u5C3D\u6570\u8D25\u5728\u4F60\u624B\u4E2D\u3002\u6211\u8FD8\u8BB0\u5F97\u4F60\u5355\u51ED\u7A7A\u624B\u5C31\u6321\u4F4F\u4E86\u4E00\u679A\u7834\u7A7A\u96F7\u2026\u2026\u300D\u4ED6\u63D0\u8D77\u795E\u5C3C\u7684\u53F3\u81C2\uFF0C\u8F7B\u8F7B\u4E00\u62D7\uFF0C\u624B\u81C2\u4FBF\u4E0D\u53EF\u601D\u8BAE\u7684\u62E7\u8F6C\u4E86\u4E00\u4E2A\u602A\u5F02\u7684\u89D2\u5EA6\u3002\u63A5\u7740\u8F6F\u8F6F\u6389\u5728\u7B3C\u5E95\uFF0C\u518D\u6CA1\u6709\u5F80\u65E5\u7684\u534A\u5206\u6C14\u52BF\u3002</p><p>\u6155\u5BB9\u9F99\u5148\u6500\u4F4F\u795E\u5C3C\u80A9\u5934\u7684\u94A2\u94FE\u62FD\u4E86\u51E0\u628A\uFF0C\u7136\u540E\u624B\u638C\u968F\u7740\u5149\u6ED1\u7684\u808C\u80A4\u6478\u5230\u540A\u949F\u72B6\u7684\u8C6A\u4E73\u4E0A\uFF0C\u4E24\u6307\u637B\u7740\u4E73\u5934\u4F5C\u52BF\u6B32\u634F\uFF0C\u5F85\u795E\u5C3C\u6D51\u8EAB\u7EF7\u7D27\uFF0C\u5374\u53C8\u4E00\u7B11\u653E\u624B\uFF0C\u300C\u5E08\u592A\u83AB\u6015\uFF0C\u8FD9\u4E2A\u82E5\u662F\u5F04\u574F\u4E86\uFF0C\u5927\u5BB6\u64CD\u8D77\u6765\u672A\u514D\u4E0D\u591F\u5C3D\u5174\u2026\u2026\u300D</p><p>\u624B\u638C\u4ECE\u8170\u81C0\u4E00\u8DEF\u6ED1\u8FC7\uFF0C\u6700\u540E\u505C\u5728\u5DE6\u819D\u3002</p><p>\u5706\u6DA6\u7684\u819D\u76D6\u66F2\u7EBF\u4F18\u7F8E\uFF0C\u5149\u6CFD\u5982\u7389\u3002\u6155\u5BB9\u9F99\u611F\u53D7\u7740\u808C\u80A4\u7684\u6ED1\u817B\uFF0C\u6D45\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u8F7B\u529F\u8FC7\u4EBA\uFF0C\u6614\u65E5\u7ACB\u5728\u679D\u5934\u7528\u7684\u5C31\u662F\u8FD9\u6761\u817F\u5427\u3002\u4E0D\u77E5\u634F\u788E\u4E4B\u540E\uFF0C\u662F\u4E0D\u662F\u8FD8\u80FD\u6765\u53BB\u5982\u98CE\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u8138\u4E0A\u6EE1\u662F\u7CBE\u6591\u3001\u5C3F\u8FF9\uFF0C\u8FD8\u6DCC\u7740\u5F92\u513F\u7684\u6DEB\u6DB2\u3002\u4E8B\u5DF2\u81F3\u6B64\uFF0C\u4EFB\u4F55\u8BDD\u90FD\u662F\u5F92\u60F9\u8BA5\u7B11\u3002\u5979\u95ED\u7740\u773C\uFF0C\u4EFB\u51ED\u6EE1\u8154\u7684\u6124\u6068\u5728\u80F8\u53E3\u6FC0\u8361\uFF0C\u53EA\u662F\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u624B\u6307\u7F13\u7F13\u6536\u62E2\uFF0C\u4E0E\u6B64\u540C\u65F6\uFF0C\u819D\u9AA8\u4F3C\u4E4E\u6162\u6162\u53D8\u5F97\u575A\u786C\uFF0C\u4E0E\u6307\u529B\u5BF9\u6297\u3002</p><p>\u65F6\u95F4\u957F\u5F97\u4EFF\u4F5B\u6CA1\u6709\u5C3D\u5934\uFF0C\u5F53\u300C\u683C\u300D\u7684\u4E00\u58F0\u8106\u54CD\u4F20\u6765\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4EFF\u4F5B\u89E3\u8131\u822C\u59D4\u987F\u4E8E\u5730\u3002\u7B49\u5267\u75DB\u88AD\u6765\uFF0C\u5979\u624D\u610F\u8BC6\u5230\u81EA\u5DF1\u56DB\u80A2\u5C3D\u88AB\u751F\u751F\u634F\u788E\uFF0C\u9AA8\u788E\u7684\u8106\u54CD\u672A\u6B47\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7A81\u7136\u5C16\u53EB\u4E00\u58F0\uFF1A\u300C\u6155\u5BB9\u9F99\uFF01\uFF01\uFF01\u300D</p><p>\u6495\u5FC3\u88C2\u80BA\u7684\u547C\u558A\uFF0C\u4F7F\u6155\u5BB9\u9F99\u4E5F\u4E3A\u4E4B\u8272\u53D8\u3002\u4E00\u77AC\u95F4\uFF0C\u4ED6\u89C9\u5F97\u5468\u8EAB\u53D1\u51B7\uFF0C\u80CC\u540E\u4F3C\u4E4E\u4F38\u51FA\u65E0\u6570\u51B0\u51B7\u7684\u624B\u81C2\uFF0C\u86C7\u4E00\u822C\u7F20\u5728\u8EAB\u4E0A\u3002\u6155\u5BB9\u9F99\u4E0D\u5F97\u4E0D\u7528\u4E00\u58F0\u5927\u7B11\u6765\u63A9\u9970\u81EA\u5DF1\u6447\u8361\u7684\u5FC3\u65CC\uFF0C\u4F46\u7A7A\u843D\u843D\u7684\u7B11\u58F0\u5374\u4F7F\u7A7A\u6C14\u6108\u53D1\u51B0\u51B7\u3002</p><p>\u4E00\u65F6\u95F4\u6797\u4E2D\u6084\u65E0\u58F0\u606F\uFF0C\u53EA\u5269\u96EA\u5CF0\u795E\u5C3C\u51C4\u5389\u5730\u58F0\u97F3\u9690\u9690\u56DE\u54CD\u3002</p><p>\u8840\u7EA2\u8272\u7684\u5915\u7167\u6D78\u6CA1\u5929\u5730\uFF0C\u4E09\u5177\u8D64\u88F8\u7684\u8EAB\u4F53\u6C90\u6D74\u5728\u65E0\u8FB9\u8840\u8272\u4E2D\uFF0C\u4EFF\u4F5B\u9884\u793A\u7740\u4ED6\u4EEC\u6D74\u8840\u7684\u547D\u8FD0\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u73AB\u7470\u822C\u7684\u5C11\u5973\u67D4\u987A\u5730\u8DEA\u5728\u7537\u5B50\u9762\u524D\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u59B9\u59B9\u60F3\u8DDF\u5E08\u7236\u8BF4\u51E0\u53E5\u8BDD\u3002\u300D</p><p>\u7537\u5B50\u76EF\u7740\u94C1\u7B3C\u4E2D\u56DB\u80A2\u4FF1\u5E9F\u7684\u7F8E\u5987\uFF0C\u534A\u664C\u540E\u51B7\u51B7\u8BF4\u9053\uFF1A\u300C\u597D\uFF01\u300D</p>',54);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
