import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_68" tabindex="-1"><a class="header-anchor" href="#_68" aria-hidden="true">#</a> 68</h1><p>\u300C\u5E08\u7236\u3002\u4F60\u60F3\u6B7B\u5417\uFF1F\u300D\u5C11\u5973\u8F7B\u58F0\u95EE\u3002</p><p>\u7F8E\u5987\u8270\u96BE\u5730\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4E0D\uFF0C\u6211\u8981\u7B49\u7740\u770B\u4ED6\u6B7B\uFF01\u300D</p><p>\u5C11\u5973\u6C89\u9ED8\u7247\u523B\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u5E08\u7236\uFF0C\u4F60\u602A\u6211\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u3002\u4E0D\u4F1A\u3002\u300D</p><p>\u5C11\u5973\u51C4\u7136\u4E00\u7B11\uFF0C\u9694\u7740\u94C1\u7B3C\u628A\u8138\u8D34\u5728\u7F8E\u5987\u6EE1\u662F\u6C61\u6E0D\u7684\u8138\u5E9E\u4E0A\uFF0C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u8C22\u8C22\u5E08\u7236\u2026\u2026\u300D\u5979\u628A\u58F0\u97F3\u538B\u5F97\u66F4\u4F4E\uFF0C\u300C\u5F92\u513F\u7834\u4F53\u4EE5\u540E\uFF0C\u6563\u4E71\u7684\u771F\u6C14\u867D\u7136\u65E0\u6CD5\u805A\u62E2\uFF0C\u4F46\u4F3C\u4E4E\u53D8\u5F97\u66F4\u5F3A\u4E86\u3002\u300D</p><p>\u7F8E\u5987\u773C\u4E2D\u7ACB\u5373\u7CBE\u5149\u5927\u76DB\uFF0C\u6D51\u4E0D\u4F3C\u56DB\u80A2\u88AB\u6B8B\u7684\u5E9F\u4EBA\u3002</p><p>\u5C11\u5973\u9759\u9759\u8BF4\uFF1A\u300C\u90A3\u79BD\u517D\u51E0\u6B21\u8BD5\u56FE\u5438\u53D6\u5F92\u513F\u7684\u771F\u5143\uFF0C\u6BCF\u4E00\u6B21\u5F92\u513F\u90FD\u89C9\u5F97\u6709\u4ED6\u7684\u771F\u6C14\u51B2\u649E\u4E39\u7530\u3002\u5F92\u513F\u5185\u529F\u88AB\u5236\uFF0C\u65E0\u6CD5\u7EC3\u529F\uFF0C\u4F46\u88AB\u771F\u6C14\u51B2\u649E\u540E\uFF0C\u4E39\u7530\u5185\u7684\u771F\u6C14\u4F3C\u4E4E\u589E\u957F\u3002\u300D</p><p>\u826F\u4E45\u4E4B\u540E\uFF0C\u8EAB\u4F53\u88AB\u6B8B\u7684\u7F8E\u5987\u8F7B\u53F9\u822C\u8BF4\u9053\uFF1A\u300C\u73AB\u513F\uFF0C\u770B\u6765\u5B9D\u5178\u53E6\u6709\u5965\u5999\uFF0C\u4F46\u5E08\u7236\u73B0\u5728\u518D\u4E5F\u5E2E\u4E0D\u4E86\u4F60\u4E86\u2026\u2026\u4F60\u597D\u81EA\u4E3A\u77E5\uFF0C\u4E0D\u5FC5\u6302\u5FF5\u4E3A\u5E08\u3002\u751F\u6B7B\uFF0C\u90FD\u662F\u865A\u5E7B\u7F62\u4E86\u2026\u2026\u300D</p><p>\u5C11\u5973\u653E\u5F00\u624B\uFF0C\u671D\u7B3C\u4E2D\u7F8E\u5987\u78D5\u4E86\u4E09\u4E2A\u5934\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u300C\u55EF\u3002\u300D\u7D2B\u73AB\u628A\u73A9\u624B\u4E2D\u65E0\u77E2\u7684\u5C0F\u5F29\uFF0C\u5982\u6C34\u7684\u79CB\u6CE2\u4E00\u8F6C\uFF0C\u5E76\u6CA1\u6709\u53EB\u767D\u7389\u83BA\u8D77\u8EAB\u3002</p><p>\u5BA4\u4E2D\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u767D\u7389\u83BA\u53D7\u4E0D\u4E86\u8FD9\u79CD\u65E0\u8A00\u7684\u538B\u529B\uFF0C\u745F\u7F29\u5730\u95EE\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u53EB\u5974\u5A62\u6709\u4EC0\u4E48\u4E8B\uFF1F\u300D</p><p>\u7D2B\u73AB\u653E\u4E0B\u5C0F\u5F29\uFF0C\u62FF\u8D77\u624B\u8FB9\u7684\u7F8A\u8102\u7389\u676F\u3002</p><p>\u767D\u7389\u83BA\u8FDE\u5FD9\u819D\u884C\u8FD1\u524D\uFF0C\u63A5\u8FC7\u7389\u676F\u659F\u4E0A\u4E00\u676F\u6D45\u7EA2\u8272\u7684\u73AB\u7470\u9732\uFF0C\u9012\u5230\u5C11\u592B\u4EBA\u624B\u4E2D\u3002</p><p>\u7D2B\u73AB\u6D45\u6D45\u996E\u4E86\u4E00\u53E3\uFF0C\u8FD9\u624D\u6DE1\u6DE1\u9053\uFF1A\u300C\u98CE\u5974\u5462\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u5C0F\u5FC3\u7B54\u9053\uFF1A\u300C\u5BAB\u4E3B\u5429\u5490\uFF0C\u4ECD\u7559\u5728\u5730\u5B57\u620C\u5BA4\u3002\u300D</p><p>\u300C\u9001\u5979\u56DE\u4EB2\u5B57\u4E01\u5BA4\u3002\u300D</p><p>\u767D\u7389\u83BA\u55EB\u5685\u7740\u8BF4\u9053\uFF1A\u300C\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u300C\u4F60\u5148\u9001\u5979\u56DE\u53BB\u3002\u6211\u81EA\u4F1A\u8DDF\u4ED6\u8BF4\u3002\u7EAA\u5974\u5462\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u54BD\u4E86\u553E\u6CAB\uFF0C\u5979\u4E0D\u77E5\u9053\u73AB\u7470\u4ED9\u5B50\u600E\u4E48\u4E00\u5929\u4E4B\u95F4\u5C31\u53D8\u5F97\u8FD9\u6837\u6C14\u6D3E\u5341\u8DB3\uFF0C\u4FE8\u7136\u4EE5\u5973\u4E3B\u4EBA\u81EA\u5C45\u2014\u2014\u8FD8\u4E0D\u90FD\u662F\u88AB\u63B3\u6765\u7684\u5973\u4EBA\u5417\uFF1F\u300C\u5BAB\u4E3B\u547D\u7EAA\u5974\u53BB\u4F8D\u5949\u7075\u7389\u957F\u8001\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u795E\u8272\u4E0D\u53D8\uFF0C\u8F7B\u8F7B\u653E\u4E0B\u7389\u676F\uFF0C\u5E73\u9759\u7684\u58F0\u97F3\u91CC\u591A\u4E86\u51E0\u5206\u5BD2\u610F\uFF0C\u300C\u8BA9\u9E42\u5974\u53BB\u4F20\u6211\u5429\u5490\uFF0C\u53EB\u5979\u5148\u56DE\u6765\u3002\u300D</p><p>\u8FD9\u6446\u660E\u662F\u8BA9\u59B9\u59B9\u767D\u7389\u9E42\u53BB\u66FF\u6362\u7EAA\u7709\u59A9\uFF0C\u4F46\u767D\u7389\u83BA\u4E0D\u6562\u53CD\u6297\uFF0C\u53EA\u5F97\u4F4E\u58F0\u7B54\u5E94\u3002</p><p>\u767D\u7389\u83BA\u79BB\u5F00\u540E\uFF0C\u7D2B\u73AB\u5750\u4E86\u7247\u523B\uFF0C\u7AD9\u8D77\u8EAB\u6765\u3002\u4E34\u884C\u524D\uFF0C\u5979\u4E60\u60EF\u6027\u5730\u628A\u7A7A\u5F29\u7CFB\u5728\u8170\u95F4\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6B63\u5728\u752C\u9053\u5185\u7A83\u7A83\u79C1\u8BED\uFF0C\u89C1\u5C11\u592B\u4EBA\u51FA\u6765\uFF0C\u8FDE\u5FD9\u8E72\u8EAB\u65BD\u793C\uFF0C\u767D\u7389\u9E42\u6084\u6084\u770B\u4E86\u4E00\u773C\u8138\u4E0A\u6BEB\u65E0\u8868\u60C5\u7684\u73AB\u7470\u4ED9\u5B50\uFF0C\u5782\u7740\u5934\u79BB\u5F00\u5723\u5BAB\u53BB\u627E\u7075\u7389\u771F\u4EBA\u3002\u767D\u7389\u83BA\u5219\u4E00\u58F0\u4E0D\u54CD\u5730\u8DDF\u5728\u5C11\u592B\u4EBA\u8EAB\u540E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8D70\u8FDB\u8F9B\u5BA4\uFF0C\u7D2B\u73AB\u6DF1\u6DF1\u7EB3\u4E86\u4E2A\u798F\u3002</p><p>\u53F6\u884C\u5357\u8FD8\u662F\u5934\u4E00\u6B21\u89C1\u5979\u5982\u6B64\u5BA2\u6C14\uFF0C\u4E0D\u7981\u77AA\u76EE\u7ED3\u820C\u3002</p><p>\u300C\u53F6\u62A4\u6CD5\uFF0C\u5C0F\u5973\u5B50\u6765\u53D6\u98CE\u5974\u6240\u7528\u836F\u7269\u3002\u300D</p><p>\u300C\u5662\u2026\u2026\u300D\u53F6\u884C\u5357\u8FD9\u624D\u56DE\u8FC7\u795E\u6765\uFF0C\u300C\u55EF\u55EF\u2026\u2026\u300D\u4ED6\u8FDE\u8FDE\u70B9\u5934\uFF0C\u4ECE\u836F\u6A71\u4E2D\u53D6\u51FA\u5931\u795E\u4E39\u548C\u72AC\u836F\u3002</p><p>\u4E0D\u7B49\u767D\u7389\u83BA\u4E0A\u524D\u6765\u63A5\uFF0C\u7D2B\u73AB\u4FBF\u4EB2\u624B\u53D6\u8FC7\u836F\u7269\uFF0C\u7136\u540E\u671D\u53F6\u884C\u5357\u5AE3\u7136\u4E00\u7B11\uFF0C\u300C\u591A\u8C22\u62A4\u6CD5\u3002\u300D</p><p>\u7D2B\u73AB\u79BB\u5F00\u534A\u5929\uFF0C\u53F6\u884C\u5357\u624D\u4E00\u5C41\u80A1\u5750\u5728\u6905\u4E2D\uFF0C\u767E\u601D\u4E0D\u5F97\u5176\u89E3\uFF0C\u300C\u5C0F\u4E2B\u5934\u8FD9\u662F\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u628A\u4F60\u7684\u94A5\u5319\u62FF\u6765\u3002\u300D</p><p>\u767D\u7389\u83BA\u672C\u6765\u60F3\u8BF4\u6CA1\u6709\uFF0C\u4F46\u4E00\u770B\u5979\u51B0\u51B7\u7684\u773C\u795E\uFF0C\u4FBF\u660E\u767D\u5C11\u592B\u4EBA\u5DF2\u7ECF\u77E5\u9053\u94A5\u5319\u662F\u5728\u81EA\u5DF1\u624B\u4E2D\u3002</p><p>\u591C\u660E\u73E0\u5728\u6155\u5BB9\u9F99\u624B\u91CC\uFF0C\u752C\u9053\u7684\u73E0\u8F89\u53C8\u65E0\u6CD5\u7167\u5165\u77F3\u5BA4\uFF0C\u7D2B\u73AB\u4FBF\u70B9\u4E86\u4E00\u679D\u8721\u70DB\u3002</p><p>\u77F3\u95E8\u8F67\u8F67\u6D1E\u5F00\uFF0C\u5BA4\u5185\u56DE\u6C64\u7684\u5A07\u5598\u7ACB\u5373\u54CD\u4EAE\u8D77\u6765\u3002</p><p>\u98CE\u665A\u534E\u56DB\u80A2\u7740\u5730\uFF0C\u9AD8\u7FD8\u7740\u96EA\u81C0\u62FC\u547D\u633A\u52A8\u3002\u5728\u5979\u8EAB\u540E\uFF0C\u4E00\u6761\u7EAF\u9ED1\u7684\u5DE8\u72AC\u4E0E\u5979\u81C0\u90E8\u76F8\u63A5\uFF0C\u8840\u7EA2\u7684\u72D7\u9633\u5D4C\u5728\u8089\u7A74\u8DF3\u52A8\u4E0D\u6B62\u3002\u98CE\u665A\u534E\u6EE1\u8138\u6F6E\u7EA2\uFF0C\u5634\u91CC\u300C\u54A6\u54A6\u5440\u5440\u300D\u53EB\u4E2A\u4E0D\u505C\u3002\u9EDD\u9ED1\u7684\u76AE\u6BDB\u51FB\u6253\u5728\u96EA\u5AE9\u7684\u5706\u81C0\u4E0A\uFF0C\u53D1\u51FA\u6E05\u8106\u7684\u300C\u556A\u556A\u300D\u58F0\uFF0C\u5979\u76EE\u5149\u5446\u6EDE\uFF0C\u575A\u633A\u7684\u7389\u4E73\u56DB\u4E0B\u4E71\u6643\uFF0C\u8FDE\u90A3\u53EA\u88AB\u524A\u6389\u4E00\u534A\u7684\u4E73\u5934\u4E5F\u786C\u786C\u7A81\u8D77\u3002</p><p>\u4E00\u6EF4\u6EDA\u70EB\u7684\u70DB\u6CB9\u6EF4\u5728\u6307\u4E0A\uFF0C\u7D2B\u73AB\u624D\u731B\u7136\u60CA\u9192\u3002\u770B\u7740\u5E08\u59D0\u6BCD\u72D7\u822C\u72C2\u6B22\u7684\u6DEB\u6001\uFF0C\u5FC3\u91CC\u586B\u6EE1\u82E6\u6DA9\u7684\u6ECB\u5473\u3002</p><p>\u7EDD\u5BF9\u4E0D\u80FD\u8BA9\u5E08\u59D0\u5728\u8FD9\u91CC\u518D\u4F4F\u4E0B\u53BB\uFF0C\u8FD8\u662F\u56DE\u53BB\u7684\u597D\u3002\u518D\u600E\u4E48\u90A3\u4E5F\u662F\u4EBA\u4F4F\u7684\u5730\u65B9\u2026\u2026\u7D2B\u73AB\u9EEF\u7136\u795E\u4F24\uFF0C\u628A\u8721\u70DB\u9012\u7ED9\u767D\u7389\u83BA\uFF0C\u81EA\u5DF1\u638F\u51FA\u4E1D\u5DFE\uFF0C\u4ED4\u7EC6\u62B9\u53BB\u5E08\u59D0\u8138\u4E0A\u7684\u6C57\u6C34\u3002</p><p>\u98CE\u665A\u534E\u5DF2\u7ECF\u88AB\u836F\u7269\u7834\u574F\u4E86\u795E\u667A\uFF0C\u4E0E\u53D1\u60C5\u7684\u5DE8\u72AC\u540C\u5C45\u7684\u8FD9\u4E9B\u65E5\u5B50\uFF0C\u534A\u662F\u5F3A\u8FEB\uFF0C\u534A\u662F\u6697\u793A\uFF0C\u5931\u795E\u7684\u5927\u8111\u5DF2\u7ECF\u63A5\u53D7\u4E86\u81EA\u5DF1\u6BCD\u72D7\u7684\u8EAB\u4EFD\uFF0C\u5979\u5BF9\u7D2B\u73AB\u7684\u51FA\u73B0\u6CA1\u6709\u4EFB\u4F55\u53CD\u5E94\uFF0C\u53EA\u662F\u6B23\u559C\u82E5\u72C2\u5730\u4E0E\u72AC\u53EA\u4EA4\u5408\u7740\u3002\u6614\u65E5\u98CE\u91C7\u4EAE\u4E3D\uFF0C\u6C14\u52BF\u8FEB\u4EBA\u7684\u6D41\u971C\u5251\uFF0C\u5982\u4ECA\u65E0\u8BBA\u4E3E\u6B62\u5F62\u6001\uFF0C\u90FD\u4E0E\u4E00\u6761\u6BCD\u72D7\u65E0\u5F02\u3002</p><p>\u7D2B\u73AB\u8BD5\u63A2\u7740\u628A\u624B\u4F38\u5230\u4EBA\u72D7\u76F8\u63A5\u7684\u90E8\u4F4D\uFF0C\u60F3\u62D4\u51FA\u72D7\u9633\uFF0C\u5E26\u5E08\u59D0\u79BB\u5F00\u3002\u4F46\u7528\u529B\u4E00\u626F\uFF0C\u96EA\u81C0\u95F4\u5AE9\u8089\u7A81\u8D77\uFF0C\u72D7\u97AD\u7D27\u7D27\u5361\u5728\u5176\u4E2D\uFF0C\u52A8\u5F39\u4E0D\u5F97\u3002\u518D\u4E00\u62FD\uFF0C\u98CE\u665A\u534E\u5374\u5403\u75DB\u4F3C\u7684\u4F4E\u53EB\u4E00\u58F0\uFF0C\u63A5\u7740\u626D\u52A8\u8170\u81C0\uFF0C\u8BA9\u8089\u68D2\u8FDB\u5F97\u66F4\u6DF1\u3002</p><p>\u8EAB\u540E\u4E00\u4E2A\u602F\u602F\u7684\u58F0\u97F3\u54CD\u8D77\uFF0C\u300C\u5C11\u592B\u4EBA\uFF0C\u62D4\u4E0D\u51FA\u6765\u7684\u2026\u2026\u72D7\u2026\u2026\u5728\u91CC\u9762\u5F88\u5927\u7684\u3002\u300D</p><p>\u7D2B\u73AB\u5FAE\u5FAE\u56DE\u9996\uFF0C\u4E0D\u7531\u4E00\u6123\u3002\u767D\u7389\u83BA\u5C45\u7136\u50CF\u65B0\u5A5A\u6D1E\u623F\u4E4B\u591C\u4E00\u6837\uFF0C\u5706\u81C0\u9AD8\u4E3E\uFF0C\u552F\u4E00\u4E0D\u540C\u7684\u662F\u8FD9\u6B21\u7684\u8721\u70DB\u8F83\u7EC6\uFF0C\u5979\u6015\u8089\u7A74\u65E0\u6CD5\u5939\u7D27\uFF0C\u4FBF\u63D2\u5728\u4E86\u83CA\u809B\u4E2D\u3002</p><p>\u7D2B\u73AB\u5F20\u53E3\u60F3\u8BF4\u81EA\u5DF1\u5E76\u4E0D\u662F\u8FD9\u4E2A\u610F\u601D\uFF0C\u4F46\u8F6C\u5FF5\u4E00\u60F3\uFF0C\u81EA\u5DF1\u4EC0\u4E48\u90FD\u6CA1\u8BF4\uFF0C\u5979\u5C31\u4E3B\u52A8\u62FF\u8089\u4F53\u5F53\u70DB\u53F0\uFF0C\u5B9E\u5728\u662F\u4E0B\u8D31\uFF01\u5FC3\u91CC\u6068\u610F\u4E00\u8D77\uFF0C\u4FBF\u626D\u8FC7\u5934\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u7D2B\u73AB\u5E2E\u5E08\u59D0\u64E6\u4E86\u53C8\u64E6\uFF0C\u624B\u91CC\u7684\u4E1D\u5DFE\u65E9\u5DF2\u6E7F\u900F\u4E86\uFF0C\u5DE8\u72AC\u5374\u4E1D\u6BEB\u6CA1\u6709\u505C\u6B62\u7684\u610F\u601D\u3002</p><p>\u5979\u5FC3\u6025\u5982\u711A\uFF0C\u4E24\u773C\u5192\u706B\u5730\u76EF\u7740\u56A3\u5F20\u7684\u72D7\u9633\u2014\u2014\u82E5\u6709\u5229\u5203\u5728\u624B\u5C31\u597D\u4E86\u3002</p><p>\u8721\u70DB\u8D8A\u70E7\u8D8A\u77ED\uFF0C\u5F53\u767D\u7389\u83BA\u611F\u89C9\u5230\u6447\u66F3\u7684\u706B\u7130\u8FDB\u5165\u81C0\u7F1D\u65F6\uFF0C\u9ED1\u72AC\u7EC8\u4E8E\u5486\u54EE\u7740\u5C04\u51FA\u6EDA\u70EB\u5730\u6D53\u7CBE\u3002</p><p>\u53FD\u549B\u4E00\u58F0\uFF0C\u72D7\u9633\u4ECE\u6E7F\u900F\u7684\u8089\u7A74\u4E2D\u6389\u51FA\u3002\u98CE\u665A\u534E\u5A9A\u773C\u5982\u4E1D\uFF0C\u8FC7\u5EA6\u7684\u4EA4\u5408\u8017\u5C3D\u4E86\u5979\u5355\u8584\u7684\u4F53\u529B\uFF0C\u4F46\u5979\u4ECD\u4E0D\u80AF\u4F11\u606F\uFF0C\u800C\u662F\u52C9\u529B\u6491\u8D77\u5706\u81C0\uFF0C\u7B49\u5F85\u4E0B\u4E00\u53EA\u8089\u68D2\u3002</p><p>\u65C1\u8FB9\u7684\u82B1\u72AC\u61D2\u6D0B\u6D0B\u722C\u4E86\u8D77\u6765\uFF0C\u6447\u7740\u5C3E\u5DF4\u671D\u8D64\u88F8\u7684\u6BCD\u72D7\u8D70\u6765\u3002\u7D2B\u73AB\u6BEB\u4E0D\u72B9\u8C6B\u5730\u62D6\u8D77\u5E08\u59D0\uFF0C\u7136\u540E\u4E00\u628A\u5C06\u767D\u7389\u83BA\u63A8\u5230\u8EAB\u524D\uFF0C\u6321\u4F4F\u82B1\u72AC\u7684\u53BB\u8DEF\u3002</p><p>\u767D\u7389\u83BA\u53C8\u60CA\u53C8\u6015\uFF0C\u6123\u6123\u770B\u7740\u5C11\u592B\u4EBA\u5E26\u7740\u98CE\u5974\u4ECE\u5BB9\u79BB\u53BB\u3002\u76F4\u5230\u83CA\u809B\u7099\u75DB\uFF0C\u5979\u624D\u5C16\u53EB\u7740\u62FC\u547D\u722C\u8D77\u3002</p><p>\u96EA\u81C0\u4E2D\u5DF2\u7ECF\u770B\u4E0D\u5230\u70DB\u8EAB\uFF0C\u706B\u82D7\u76F4\u63A5\u71C3\u70E7\u5728\u6D45\u8910\u8272\u7684\u83CA\u7EB9\u4E2D\u3002\u767D\u7389\u83BA\u60CA\u6050\u4E07\u72B6\uFF0C\u987E\u4E0D\u5F97\u809B\u4E2D\u7684\u7099\u75DB\uFF0C\u6323\u624E\u7740\u722C\u5411\u655E\u5F00\u7684\u77F3\u95E8\u3002</p><p>\u8EAB\u540E\u98CE\u58F0\u4E00\u7D27\uFF0C\u70DB\u706B\u4E00\u95EA\u5373\u706D\u3002\u63A5\u7740\u9ED1\u6697\u4E2D\u4F20\u6765\u5C11\u5973\u60CA\u6016\u800C\u53C8\u75DB\u695A\u7684\u60E8\u53EB\u3002</p><p>\u7D2B\u73AB\u534A\u62D6\u534A\u62B1\u5730\u62E5\u7740\u5E08\u59D0\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u79BB\u5F00\u5730\u5B57\u752C\u9053\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EAA\u7709\u59A9\u8E72\u5728\u5730\u4E0A\uFF0C\u5C0F\u5FC3\u5730\u6D17\u6DA4\u4E0B\u4F53\u3002\u88AB\u65E0\u6570\u4EBA\u5978\u6DEB\u8FC7\u81EA\u5DF1\u51B0\u6E05\u7389\u6D01\u7684\u8EAB\u4F53\u4E4B\u540E\uFF0C\u5979\u7684\u6D01\u7656\u65E9\u5DF2\u70DF\u9500\u4E91\u6563\u3002\u4F46\u591A\u5E74\u7684\u4E60\u60EF\u8FD8\u662F\u65E0\u6CD5\u6539\u53D8\u3002</p><p>\u6E29\u70ED\u7684\u6BDB\u5DFE\u64E6\u8FC7\u79D8\u5904\uFF0C\u7ACB\u65F6\u5FEB\u611F\u8FDE\u8FDE\u3002\u522B\u4EBA\u7684\u7CBE\u6DB2\u53EF\u4EE5\u6D17\u6389\uFF0C\u81EA\u5DF1\u7565\u4E00\u78B0\u89E6\u5C31\u6CDB\u6EE5\u7684\u6DEB\u6DB2\u5374\u600E\u4E48\u4E5F\u65E0\u6CD5\u6D17\u51C0\u3002\u7EAA\u7709\u59A9\u6342\u7740\u4E0B\u8179\uFF0C\u6014\u6014\u51FA\u795E\u3002</p><p>\u70ED\u6C34\u7684\u523A\u6FC0\u4E0B\uFF0C\u82B1\u8482\u6E10\u6E10\u53D1\u786C\uFF0C\u7EAA\u7709\u59A9\u4E0B\u610F\u8BC6\u5730\u7389\u624B\u4E00\u52A8\uFF0C\u82B1\u8482\u89E6\u7535\u822C\u4F20\u6765\u566C\u9AA8\u7684\u9707\u98A4\u3002\u88AB\u711A\u60C5\u818F\u5F81\u670D\u7684\u8089\u4F53\u518D\u4E5F\u65E0\u6CD5\u6297\u62D2\uFF0C\u6D01\u767D\u7684\u6BDB\u5DFE\u4E00\u677E\uFF0C\u843D\u5728\u76C6\u5185\u6CDB\u767D\u7684\u6C61\u6C34\u4E2D\u3002</p><p>\u7D2B\u73AB\u63A8\u95E8\u800C\u5165\uFF0C\u614C\u5FD9\u4FA7\u8FC7\u8138\u3002</p><p>\u7EAA\u7709\u59A9\u8DEA\u5750\u5728\u5730\u4E0A\uFF0C\u7EA2\u5507\u5FAE\u5206\uFF0C\u767D\u7699\u7684\u624B\u6307\u6B63\u5728\u80BF\u80C0\u7684\u82B1\u74E3\u5185\u7AED\u529B\u62E8\u5F04\u3002\u7B49\u5979\u5728\u9AD8\u6F6E\u7684\u6218\u6817\u4E2D\u7741\u5F00\u773C\uFF0C\u4E24\u5973\u56DB\u76EE\u4EA4\u6295\uFF0C\u5374\u8BF4\u4E0D\u51FA\u4E00\u53E5\u8BDD\u3002</p><p>\u826F\u4E45\uFF0C\u7EAA\u7709\u59A9\u8138\u4E0A\u9732\u51FA\u51C4\u51C9\u7684\u82E6\u7B11\uFF0C\u8D77\u8EAB\u63A5\u8FC7\u5E08\u59D0\u3002</p><p>\u660F\u7761\u4E2D\u7684\u98CE\u665A\u534E\u4ECD\u7136\u8737\u7F29\u7740\u8EAB\u4F53\uFF0C\u7EB5\u7136\u653E\u5728\u69BB\u4E0A\u4E5F\u4E0D\u613F\u5C55\u5F00\u56DB\u80A2\u3002</p><p>\u300C\u4ECA\u665A\u5148\u653E\u5728\u8FD9\u91CC\uFF0C\u660E\u5929\u6211\u9001\u5E08\u59D0\u56DE\u539F\u6765\u7684\u623F\u95F4\u3002\u300D\u7D2B\u73AB\u58F0\u97F3\u8F7B\u98D8\u98D8\u6BEB\u65E0\u529B\u6C14\u3002\u96BE\u8A00\u7684\u81EA\u8D23\u4FB5\u8680\u7740\u5979\u7684\u5FC3\u7075\uFF0C\u82E5\u975E\u81EA\u5DF1\u8981\u6C42\uFF0C\u5927\u5E08\u59D0\u548C\u4E09\u5E08\u59D0\u4E5F\u4E0D\u4F1A\u843D\u5230\u5982\u6B64\u5730\u6B65\u3002\u8FD8\u6709\uFF0C\u90A3\u4E9B\u836F\u90FD\u662F\u81EA\u5DF1\u4EB2\u624B\u6D82\u4E0A\u7684\u2014\u2014\u5979\u6C38\u8FDC\u90FD\u5FD8\u4E0D\u4E86\u3002</p><p>\u7EAA\u7709\u59A9\u70B9\u70B9\u5934\uFF0C\u6B32\u8A00\u53C8\u6B62\u3002</p><p>\u7D2B\u73AB\u77E5\u9053\u5979\u60F3\u95EE\u4EC0\u4E48\uFF0C\u4F46\u5979\u81EA\u5DF1\u4E5F\u4E0D\u77E5\u4ECA\u540E\u8BE5\u600E\u4E48\u529E\u2014\u2014\u51B5\u4E14\uFF0C\u5BF9\u88AB\u6DEB\u836F\u6539\u9020\u800C\u6C89\u6EBA\u4E8E\u8089\u6B32\u7684\u7EAA\u5E08\u59D0\uFF0C\u5979\u4E5F\u4E0D\u613F\u8F7B\u6613\u5410\u9732\u81EA\u5DF1\u7684\u60F3\u6CD5\u3002</p><p>\u7EAA\u7709\u59A9\u65E0\u8A00\u5730\u5782\u4E0B\u67D4\u9888\uFF0C\u4ED4\u7EC6\u6396\u597D\u88AB\u89D2\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u6127\u759A\uFF0C\u62B1\u4F4F\u7EAA\u7709\u59A9\u8F7B\u8F7B\u4E00\u62E5\uFF0C\u8F6C\u8EAB\u79BB\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u521A\u521A\u5546\u8BAE\u5B8C\u7684\u7EC6\u8282\uFF0C\u610F\u6C14\u98CE\u53D1\u5730\u56DE\u5230\u5723\u5BAB\u3002\u8FD9\u6B21\u79BB\u5BAB\uFF0C\u662F\u4ED6\u5F81\u670D\u5929\u4E0B\u7684\u7B2C\u4E00\u6B65\uFF0C\u4ECE\u6B64\u661F\u6708\u6E56\u5C06\u6210\u4E3A\u4E00\u652F\u65B0\u5174\u52BF\u529B\uFF0C\u5D1B\u8D77\u4E8E\u7FA4\u96C4\u7EB7\u4E89\u7684\u65F6\u4EE3\u3002</p><p>\u7D2B\u73AB\u50CF\u4E00\u4E2A\u6E29\u987A\u7684\u59BB\u5B50\u822C\u8E72\u8EAB\u5E2E\u4ED6\u89E3\u4E0B\u8170\u5E26\uFF0C\u9664\u53BB\u5916\u888D\uFF0C\u4E00\u4E3E\u4E00\u52A8\u90FD\u663E\u793A\u51FA\u4F3C\u6C34\u7684\u67D4\u60C5\u3002</p><p>\u6155\u5BB9\u9F99\u6CE8\u610F\u5230\u5979\u62FF\u8D77\u7247\u7389\u65F6\uFF0C\u773C\u4E2D\u6D41\u9732\u51FA\u4E00\u4E1D\u9690\u7EA6\u7684\u51C4\u7136\uFF0C\u7136\u540E\u4FBF\u518D\u4E0D\u53BB\u770B\u5B83\u3002\u5C0F\u4E2B\u5934\u771F\u7684\u6B7B\u5FC3\u4E86\uFF1F</p><p>\u6536\u62FE\u5B8C\u4E00\u5207\uFF0C\u7D2B\u73AB\u4FBF\u9759\u9759\u5750\u5728\u5E8A\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u6402\u4F4F\u59B9\u59B9\u9999\u8F6F\u7684\u8EAF\u4F53\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u8FD8\u75DB\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u53C8\u8F7B\u8F7B\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u5927\u7B11\u7740\u543B\u4E0A\u7389\u4EBA\u9C9C\u8273\u7684\u7EA2\u5507\u3002\u7D2B\u73AB\u4E56\u4E56\u5F20\u5F00\u5C0F\u5634\uFF0C\u4E3B\u52A8\u5410\u51FA\u9999\u820C\uFF0C\u8BA9\u4ED6\u5438\u7D27\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u677E\u5F00\u5634\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u665A\u4E0A\u505A\u4EC0\u4E48\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u5A07\u5598\u7EC6\u7EC6\uFF0C\u6EE1\u8138\u6655\u7EA2\u5730\u5C0F\u58F0\u8BF4\uFF1A\u300C\u6211\u60F3\u9001\u98CE\u5974\u56DE\u53BB\u2026\u2026\u83BA\u513F\u548C\u9E42\u513F\u5E2E\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u300C\u53EF\u4EE5\u3002\u4F60\u662F\u5BAB\u4E3B\u592B\u4EBA\uFF0C\u81EA\u7136\u6709\u6743\u7BA1\u7406\u540E\u5BAB\u3002\u300D</p><p>\u300C\u59B9\u59B9\u77E5\u9053\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u633D\u4F4F\u5979\u67D4\u8F6F\u7684\u8170\u8EAB\uFF0C\u5411\u540E\u5012\u53BB\uFF0C\u8F7B\u7B11\u9053\uFF1A\u300C\u6765\uFF0C\u8BA9\u54E5\u54E5\u518D\u75BC\u4F60\u4E00\u56DE\u2026\u2026\u300D</p><p>\u6DE1\u6DE1\u7684\u73E0\u8F89\u4E2D\uFF0C\u6CDB\u8D77\u52A8\u4EBA\u7684\u6625\u8272\u3002</p>',81);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
