import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_69" tabindex="-1"><a class="header-anchor" href="#_69" aria-hidden="true">#</a> 69</h1><p>\u6D1B\u9633\u4F20\u6765\u6D88\u606F\uFF0C\u5F53\u5730\u7684\u56DB\u5E2E\u4E09\u4F1A\u8054\u6210\u4E00\u4F53\uFF0C\u5BF9\u6297\u795E\u6559\uFF0C\u970D\u957F\u8001\u6FC0\u6218\u6570\u573A\uFF0C\u90FD\u672A\u80FD\u53D6\u80DC\u3002\u8521\u957F\u8001\u661F\u591C\u9A70\u63F4\uFF0C\u4EE5\u96F7\u9706\u4E07\u94A7\u4E4B\u52BF\u4E00\u4E3E\u706D\u6389\u6C10\u4EBA\u7684\u6D1B\u9A6C\u5E2E\uFF0C\u5360\u636E\u4E1C\u57CE\u4E00\u9685\uFF0C\u4E0E\u5269\u4E0B\u516D\u5E2E\u6297\u8861\u3002</p><p>\u6155\u5BB9\u9F99\u300C\u5530\u300D\u7684\u6254\u6389\u98DE\u9E3D\u5E26\u56DE\u7684\u60C5\u62A5\u3002\u4ED6\u5988\u7684\uFF01\u970D\u72C2\u7130\u8FD9\u4E2A\u83BD\u6C49\uFF0C\u6210\u4E8B\u4E0D\u8DB3\u8D25\u4E8B\u6709\u4F59\uFF01\u63A7\u5236\u6D1B\u9633\u662F\u4E3A\u4E86\u63A7\u5236\u5F53\u5730\u7684\u5546\u4F1A\uFF0C\u5C3D\u91CF\u907F\u514D\u4E0E\u767D\u9053\u6B63\u9762\u51B2\u7A81\u3002\u5982\u4ECA\u5012\u597D\uFF0C\u4E0B\u5C71\u4E0D\u8FC7\u6570\u65E5\u4FBF\u903C\u5F97\u6D1B\u9633\u6B66\u6797\u8054\u624B\u5BF9\u5916\u3002</p><p>\u6C90\u58F0\u4F20\u957F\u8896\u4E00\u5377\uFF0C\u6361\u8D77\u7EB8\u7247\u770B\u4E86\u7247\u523B\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u4E0D\u59A8\u3002\u6D1B\u9633\u8FD9\u56DB\u5E2E\u4E09\u4F1A\u4EE5\u957F\u9E70\u4F1A\u4E3A\u9996\uFF0C\u5E2E\u4E3B\u859B\u957F\u9E70\u5C5E\u4E0B\u66FE\u7ECF\u89C1\u8FC7\uFF0C\u5E76\u65E0\u591A\u5C11\u771F\u624D\u5B9E\u5B66\uFF0C\u53EA\u662F\u51FA\u8EAB\u4E8E\u4E5D\u534E\u5251\u6D3E\uFF0C\u5E08\u95E8\u663E\u8D6B\uFF0C\u4EA4\u6E38\u751A\u5E7F\uFF0C\u53C8\u64C5\u4E8E\u52FE\u8054\u5B98\u5E9C\uFF0C\u624D\u5F53\u4E0A\u5E2E\u4E3B\u3002\u300D\u4ED6\u987F\u4E86\u987F\uFF0C\u53C8\u9053\uFF1A\u300C\u5E7F\u9633\u5E2E\u7684\u5B59\u540C\u8F89\u575A\u6BC5\u679C\u51B3\uFF0C\u5012\u662F\u4E2A\u4EBA\u7269\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u541F\u591A\u65F6\uFF0C\u300C\u5982\u6B64\uFF0C\u8BF7\u6C90\u62A4\u6CD5\u5750\u9547\u6559\u4E2D\u3002\u672C\u5BAB\u660E\u65E5\u4FBF\u8D76\u8D74\u6D1B\u9633\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8427\u4F5B\u5974\u7EC6\u7709\u98A6\u7D27\uFF0C\u773C\u5DF4\u5DF4\u770B\u7740\u767D\u6C0F\u59D0\u59B9\u3002\u4F46\u767D\u6C0F\u59D0\u59B9\u53EA\u987E\u6E05\u7406\u5BB6\u4EC0\uFF0C\u8C01\u90FD\u6CA1\u770B\u5979\u4E00\u773C\u3002</p><p>\u6628\u665A\u767D\u7389\u9E42\u88AB\u7075\u7389\u8E42\u8E8F\u4E86\u4E00\u591C\uFF0C\u4E0B\u4F53\u88AB\u8FD9\u4E2A\u6076\u9053\u73A9\u5F04\u5F97\u7EA2\u80BF\u4E0D\u582A\u3002\u5F53\u7075\u7389\u8BA9\u5979\u628A\u65B0\u5236\u7684\u62C2\u5C18\u7EB3\u5165\u4F53\u5185\uFF0C\u4E3A\u5175\u5203\u4F5C\u796D\u65F6\uFF0C\u767D\u7389\u9E42\u5BF9\u7D2B\u73AB\u7684\u6068\u610F\u4E5F\u5230\u4E86\u6781\u70B9\u3002</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u767D\u7389\u83BA\u7684\u906D\u9047\u66F4\u60E8\u3002\u5979\u8131\u8EAB\u4E0D\u53CA\uFF0C\u88AB\u5DE8\u72AC\u6309\u5728\u5730\u4E0A\u3002\u4EFB\u5979\u4E00\u8EAB\u6B66\u529F\uFF0C\u72D7\u9633\u8FDB\u5165\u4F53\u5185\u4E4B\u540E\uFF0C\u4E5F\u53EA\u80FD\u633A\u7740\u4E0B\u4F53\u7B49\u5B83\u5C04\u7CBE\u3002\u7ED3\u679C\u6574\u6574\u88AB\u5978\u6DEB\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u624D\u6323\u624E\u7740\u722C\u51FA\u620C\u5BA4\u3002\u5979\u8D39\u5C3D\u529B\u6C14\u6392\u51FA\u51FA\u6DF1\u5165\u80A0\u9053\u7684\u6B8B\u70DB\uFF0C\u6068\u4E0D\u80FD\u628A\u73AB\u7470\u4ED9\u5B50\u4E00\u8F88\u5B50\u90FD\u9501\u5728\u5BA4\u5185\u8BA9\u72D7\u5978\u6DEB\u5230\u6B7B\uFF01</p><p>\u8427\u4F5B\u5974\u8F7B\u8F7B\u54FC\u4E86\u4E00\u58F0\uFF0C\u8BD5\u56FE\u8BA9\u4E24\u4EBA\u770B\u5230\u5979\u7A98\u6001\u3002\u4F46\u4E24\u5973\u4F3C\u4E4E\u6CA1\u6709\u542C\u89C1\u3002\u867D\u7136\u767E\u822C\u4E0D\u613F\uFF0C\u4F46\u80A1\u95F4\u7684\u5F02\u72B6\u5374\u8FEB\u4F7F\u5979\u4E0D\u5F97\u4E0D\u52A0\u5927\u97F3\u91CF\uFF0C\u547B\u541F\u4E86\u4E00\u58F0\u3002</p><p>\u59D0\u59B9\u4FE9\u5BF9\u671B\u4E00\u773C\uFF0C\u767D\u7389\u83BA\u5FAE\u7B11\u7740\u7F13\u6B65\u8D70\u5230\u69BB\u8FB9\uFF0C\u300C\u592B\u4EBA\uFF0C\u6709\u4EC0\u4E48\u4E0D\u8212\u670D\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u5979\u4FA7\u8FC7\u8138\u4E0D\u6562\u770B\u5A62\u5973\u620F\u8C11\u7684\u795E\u60C5\uFF0C\u50CF\u7F9E\u6DA9\u7684\u5C0F\u5973\u5B69\u822C\u54AC\u5F04\u7740\u5507\u74E3\u3002</p><p>\u300C\u6CA1\u4EC0\u4E48\u4E8B\uFF0C\u5974\u5A62\u5C31\u544A\u9000\u4E86\u3002\u300D\u767D\u7389\u83BA\u7B11\u76C8\u76C8\u76F4\u8D77\u8170\uFF0C\u4F5C\u52BF\u79BB\u5F00\u3002</p><p>\u300C\u4E0D\u8981\u8D70\u2026\u2026\u300D\u8427\u4F5B\u5974\u820C\u5934\u4F24\u52BF\u521A\u7652\u5408\uFF0C\u8BF4\u8BDD\u8FD8\u6709\u4E9B\u4E0D\u65B9\u4FBF\uFF0C\u5979\u7EC6\u58F0\u7EC6\u6C14\u5730\u8BF4\u9053\uFF0C\u300C\u6211\uFF0C\u6211\u6709\u4E9B\u4E0D\u8212\u670D\u2026\u2026\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u767D\u7389\u83BA\u957F\u957F\u7684\u776B\u6BDB\u4E00\u95EA\uFF0C\u5E26\u7740\u5C0F\u5C0F\u7684\u60CA\u6115\u8BF4\u9053\uFF1A\u300C\u5440\uFF0C\u592B\u4EBA\u5148\u5FCD\u4E00\u4E0B\uFF0C\u5974\u5A62\u8FD9\u5C31\u53BB\u8BF7\u53F6\u795E\u533B\u3002\u300D</p><p>\u300C\u4E0D\u8981\uFF01\u300D\u8427\u4F5B\u5974\u6025\u5207\u5730\u53EB\u9053\uFF0C\u300C\u4E0D\u7528\u53EB\u4ED6\uFF0C\u6211\u2026\u2026\u6211\u53EA\u662F\u2026\u2026\u300D\u58F0\u97F3\u6E10\u6E10\u4F4E\u4E86\u4E0B\u53BB\u3002</p><p>\u767D\u7389\u83BA\u51B7\u51B7\u770B\u7740\u7F8E\u8273\u7684\u5973\u4E3B\u4EBA\uFF0C\u8138\u4E0A\u5E26\u7740\u732B\u6349\u8001\u9F20\u822C\u6076\u6BD2\u7684\u7B11\u5BB9\u3002\u534A\u664C\u540E\u5979\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u6CA1\u4E8B\u5C31\u7B97\u4E86\uFF01\u5C0F\u9E42\uFF0C\u6211\u4EEC\u8D70\uFF01\u300D\u8BF4\u7740\u7529\u624B\u79BB\u5F00\u3002</p><p>\u300C\u7B49\u4E00\u4E0B\uFF01\u300D\u8427\u4F5B\u5974\u6025\u5F97\u5FEB\u8981\u54ED\u51FA\u6765\u4E86\uFF0C\u300C\u6211\u2026\u2026\u5B83\u2026\u2026\u53C8\u51FA\u6765\u4E86\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u51FA\u6765\u4E86\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7EC6\u82E5\u868A\u868B\u7684\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5927\u4FBF\u2026\u2026\u300D</p><p>\u300C\u8BF7\u592B\u4EBA\u58F0\u97F3\u5927\u4E00\u70B9\uFF0C\u5974\u5A62\u542C\u4E0D\u6E05\u695A\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u5507\u98A4\u6296\uFF0C\u4EFF\u4F5B\u7528\u5C3D\u4E86\u5168\u8EAB\u7684\u529B\u6C14\u624D\u53D1\u51FA\u58F0\u97F3\uFF0C\u300C\u5927\u4FBF\u3002\u300D\u8BDD\u97F3\u672A\u843D\uFF0C\u7F9E\u803B\u7684\u6CEA\u6C34\u4FBF\u4ECE\u6EDA\u70EB\u7684\u4FCF\u8138\u4E0A\u6084\u7136\u6ED1\u843D\u3002</p><p>\u300C\u5440\uFF01\u592B\u4EBA\u53C8\u62C9\u51FA\u6765\u4E86\uFF1F\u300D\u4E24\u5973\u604D\u7136\u5927\u609F\uFF0C\u5F02\u53E3\u540C\u58F0\u5730\u8BF4\u9053\u3002</p><p>\u8427\u4F5B\u5974\u7F9E\u5F97\u65E0\u5730\u81EA\u5BB9\uFF0C\u53EA\u6068\u4E24\u624B\u8F6F\u7EF5\u7EF5\u4F7F\u4E0D\u4E0A\u4E00\u4E1D\u529B\u6C14\uFF0C\u65E0\u6CD5\u63A9\u4F4F\u81EA\u5DF1\u7F9E\u8D67\u7684\u7389\u8138\u3002</p><p>\u624B\u6307\u786C\u6886\u6886\u4F38\u5230\u80F8\u524D\uFF0C\u5148\u89E3\u5F00\u4E86\u4E0A\u8EAB\u7684\u4EB5\u8863\uFF0C\u7F8E\u5987\u5C0F\u58F0\u4E5E\u6C42\u9053\uFF1A\u300C\u4E0D\u8981\u8131\u2026\u2026\u8FD9\u4E2A\u4E0D\u8981\u8131\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u4E00\u8138\u8083\u7136\uFF0C\u300C\u4E0D\u8131\u600E\u4E48\u884C\uFF0C\u4E07\u4E00\u6CBE\u4E0A\u4E86\u5C4E\u5C3F\u4F60\u6D17\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u987F\u65F6\u54D1\u53E3\u65E0\u8A00\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u52A8\u4F5C\u6781\u5FEB\uFF0C\u7247\u523B\u5DE5\u592B\uFF0C\u5C31\u5C06\u8427\u4F5B\u5974\u7684\u8D34\u8EAB\u5C0F\u8863\u8131\u4E86\u4E2A\u5E72\u5E72\u51C0\u51C0\u3002</p><p>\u8D64\u88F8\u7684\u5973\u4F53\u66F2\u7EBF\u9971\u6EE1\uFF0C\u808C\u80A4\u767D\u5AE9\uFF0C\u5145\u6EE1\u6210\u719F\u5973\u6027\u7684\u8FF7\u4EBA\u9B45\u529B\u3002\u4F46\u5728\u8FD9\u5177\u5149\u6DA6\u5982\u7389\uFF0C\u7F8E\u8273\u65E0\u5339\u7684\u8EAB\u4F53\u4E2D\u95F4\uFF0C\u5374\u80E1\u4E71\u5305\u7740\u4E00\u5757\u76B1\u5DF4\u5DF4\u7684\u68C9\u5E03\uFF0C\u5728\u80A1\u95F4\u539A\u539A\u7F20\u6210\u4E00\u56E2\uFF0C\u50CF\u662F\u4E2A\u5305\u7740\u5C3F\u5E03\u7684\u5A74\u513F\u3002</p><p>\u4E24\u5973\u53C9\u624B\u53C9\u811A\u5C06\u7F8E\u5987\u7C97\u9C81\u5730\u7FFB\u8F6C\u8FC7\u6765\u3002\u8427\u4F5B\u5974\u628A\u8138\u57CB\u5728\u88AB\u887E\u4E2D\uFF0C\u5C0F\u58F0\u555C\u6CE3\u7740\u3002</p><p>\u300C\u8BF7\u592B\u4EBA\u628A\u8170\u62AC\u8D77\u6765\uFF0C\u597D\u8BA9\u5974\u5A62\u4F3A\u5019\u3002\u300D</p><p>\u5979\u5FC3\u91CC\u6323\u624E\u4E86\u4E00\u4E0B\uFF0C\u5C48\u8FB1\u5730\u7528\u529B\u633A\u8D77\u8170\u8EAB\u3002\u8FD9\u4E5F\u662F\u5979\u552F\u4E00\u80FD\u505A\u5230\u7684\u4E8B\u60C5\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u628A\u8427\u4F5B\u5974\u7684\u4E24\u817F\u6253\u5F00\u5230\u5B8C\u5168\u4E0D\u5FC5\u8981\u7684\u5BBD\u5EA6\uFF0C\u7136\u540E\u4E03\u624B\u516B\u811A\u5730\u89E3\u4E0B\u5C3F\u5E03\u3002\u4E00\u8FB9\u89E3\u4E00\u8FB9\u5351\u5937\u5730\u8BF4\uFF1A\u300C\u521A\u6362\u8FC7\u5C3F\u5E03\u53C8\u5F04\u810F\u4E86\uFF0C\u8FDE\u4E24\u5C81\u7684\u5B69\u5B50\u90FD\u4E0D\u5982\uFF01\u300D</p><p>\u7F8E\u5987\u65E0\u8A00\u4EE5\u5BF9\uFF0C\u53EA\u80FD\u4EFB\u4E24\u4EBA\u595A\u843D\u3002</p><p>\u5C3F\u5E03\u677E\u5F00\uFF0C\u4E24\u5973\u540C\u65F6\u626D\u8FC7\u5934\u53BB\uFF0C\u634F\u7740\u9F3B\u5B50\u8BF4\uFF1A\u300C\u600E\u4E48\u8FD8\u5728\u62C9\uFF1F\u771F\u6076\u5FC3\uFF01\u300D\u767D\u7389\u83BA\u62AC\u624B\u6253\u5728\u80A5\u767D\u7FD8\u633A\u7684\u5706\u81C0\u4E0A\uFF0C\u5A07\u559D\u9053\uFF1A\u300C\u4F60\u6709\u5B8C\u6CA1\u5B8C\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u7F9E\u6124\u6B32\u6B7B\uFF0C\u7AED\u529B\u6536\u7D27\u83CA\u809B\u3002\u53EA\u89C1\u5979\u7EA4\u8170\u5FAE\u633A\uFF0C\u96EA\u81C0\u7D27\u7EF7\uFF0C\u4F46\u81C0\u7F1D\u5E95\u90E8\u7684\u83CA\u6D1E\u5374\u677E\u677E\u57AE\u57AE\u4F7F\u4E0D\u4E0A\u4E00\u70B9\u529B\u6C14\u3002\u7EFD\u88C2\u7684\u809B\u95E8\u7EA2\u8089\u7FFB\u5377\uFF0C\u809B\u7AA6\u5410\u9732\uFF0C\u6D41\u8D28\u72B6\u7684\u6C61\u7269\u6CCA\u6CCA\u6D8C\u51FA\u3002\u867D\u7136\u4F24\u53E3\u5DF2\u7ECF\u7652\u5408\uFF0C\u4F46\u53D7\u635F\u7684\u80A0\u9053\u8FD8\u6709\u7F15\u7F15\u8840\u4E1D\u3002</p><p>\u767D\u7389\u83BA\u5728\u81C0\u4E0A\u7528\u529B\u4E00\u62E7\uFF0C\u6CA1\u60F3\u5230\u81C0\u8089\u6ED1\u4E0D\u6E9C\u624B\uFF0C\u5C45\u7136\u6CA1\u80FD\u62E7\u4F4F\u3002\u5979\u54AC\u7259\u518D\u6B21\u62E7\u4F4F\u7EC6\u5AE9\u7684\u81C0\u8089\uFF0C\u72E0\u72E0\u4E00\u626D\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u5FEB\u4E9B\u62C9\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u5931\u58F0\u75DB\u53EB\uFF0C\u83CA\u6D1E\u4E00\u9635\u8815\u52A8\uFF0C\u63A5\u7740\u4E00\u80A1\u6C14\u4F53\u5939\u7740\u6C61\u7269\u503E\u6CC4\u800C\u51FA\u3002</p><p>\u300C\u53C8\u662F\u62C9\u5C4E\u53C8\u662F\u653E\u5C41\uFF0C\u597D\u6076\u5FC3\uFF01\u300D\u4E24\u5973\u4E00\u8FB9\u64E6\u62ED\uFF0C\u4E00\u8FB9\u8BA5\u7B11\u3002</p><p>\u51FA\u5356\u7D2B\u73AB\u4E4B\u540E\u4E24\u4EBA\u5C31\u6709\u5FC3\u75C5\uFF0C\u6628\u591C\u53C8\u5404\u53D7\u4E00\u756A\u6298\u78E8\uFF0C\u6240\u6709\u7684\u5185\u759A\u80C6\u602F\u90FD\u53D8\u4F5C\u4E86\u6068\u610F\uFF0C\u5BF9\u73AB\u7470\u4ED9\u5B50\u6068\u4E4B\u5165\u9AA8\u3002\u6B64\u65F6\u5979\u4EEC\u628A\u6012\u6C14\u90FD\u53D1\u6CC4\u5728\u4EC7\u4EBA\u7684\u5A18\u4EB2\u8EAB\u4E0A\uFF0C\u4E24\u4EBA\u5C06\u8427\u4F5B\u5974\u96EA\u81C0\u5B8C\u5168\u63B0\u5F00\uFF0C\u6BDB\u5DFE\u987A\u7740\u81C0\u7F1D\u91CD\u91CD\u62B9\u62ED\u3002\u6700\u540E\u767D\u7389\u83BA\u628A\u6BDB\u5DFE\u88F9\u5728\u6307\u4E0A\uFF0C\u63D2\u8FDB\u677E\u5F1B\u7684\u809B\u6D1E\u4E71\u6345\u4E71\u62A0\u3002</p><p>\u7F8E\u5987\u6839\u672C\u65E0\u4ECE\u53CD\u6297\uFF0C\u53EA\u80FD\u95ED\u4E0A\u773C\u9006\u6765\u987A\u53D7\uFF0C\u4EFB\u4E24\u4E2A\u5974\u5A62\u8086\u610F\u6298\u8FB1\u3002\u53EA\u662F\u6BDB\u5DFE\u6DF1\u5165\u83CA\u6D1E\u65F6\uFF0C\u5FCD\u4E0D\u4F4F\u95F7\u54FC\u4E86\u4E00\u58F0\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5BF9\u89C6\u4E00\u773C\uFF0C\u6362\u4E0A\u7B11\u5BB9\u9053\uFF1A\u300C\u592B\u4EBA\uFF0C\u8BE5\u7ED9\u60A8\u62B9\u836F\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B64\u65F6\u7D2B\u73AB\u6B63\u5728\u5B89\u7F6E\u5927\u5E08\u59D0\u98CE\u665A\u534E\u3002\u5979\u6307\u540D\u8981\u4E86\u5E08\u59D0\u539F\u6765\u6240\u5728\u7684\u4E01\u5BA4\uFF0C\u7136\u540E\u628A\u5E2E\u4F17\u90FD\u8D76\u4E86\u51FA\u53BB\u3002</p><p>\u5408\u4E0A\u95E8\uFF0C\u7D2B\u73AB\u7ACB\u5373\u4F0F\u5728\u58C1\u89D2\uFF0C\u4ED4\u7EC6\u641C\u5BFB\u90A3\u4E2A\u4F3C\u82B1\u4F3C\u4E91\u7684\u56FE\u5F62\u3002\u4E94\u95F4\u77F3\u5BA4\u5DF2\u5F97\u5176\u4E09\uFF0C\u4E0B\u4E00\u4E2A\u60F3\u6765\u5C31\u662F\u8FD9\u95F4\u4E86\u3002</p><p>\u521A\u521A\u770B\u5B8C\u4E00\u9762\u5899\u58C1\uFF0C\u4E00\u62AC\u773C\uFF0C\u7D2B\u73AB\u987F\u65F6\u5413\u4E86\u4E00\u8DF3\u3002\u98CE\u665A\u534E\u56DB\u80A2\u7740\u5730\uFF0C\u50BB\u7B11\u7740\u770B\u7740\u5979\u3002</p><p>\u7D2B\u73AB\u8FD9\u624D\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u59FF\u52BF\uFF0C\u8FDE\u5FD9\u722C\u8D77\u6765\u633D\u4F4F\u5E08\u59D0\u7684\u624B\u81C2\uFF0C\u300C\u5E08\u59D0\u8D77\u6765\u5427\u2026\u2026\u8D77\u6765\u554A\u2026\u2026\u300D</p><p>\u4EFB\u5979\u600E\u4E48\u4F7F\u529B\uFF0C\u98CE\u665A\u534E\u90FD\u7262\u7262\u8DB4\u5728\u5730\u4E0A\uFF0C\u633A\u7740\u81C0\u90E8\u5DE6\u53F3\u6447\u6446\uFF0C\u50CF\u662F\u6643\u52A8\u7740\u4E00\u6839\u65E0\u5F62\u7684\u5C3E\u5DF4\u3002\u5FFD\u7136\u95F4\uFF0C\u5979\u7EA2\u5507\u4E00\u5F20\uFF0C\u300C\u6C6A\u300D\u7684\u53EB\u4E86\u4E00\u58F0\u3002</p><p>\u58F0\u97F3\u867D\u5C0F\uFF0C\u7D2B\u73AB\u80F8\u53E3\u5374\u4E00\u4E0B\u5B50\u88AB\u5835\u4F4F\u4E86\u3002</p><p>\u98CE\u665A\u534E\u5BF9\u5979\u7684\u795E\u60C5\u4E00\u65E0\u6240\u77E5\uFF0C\u53EB\u4E86\u4E00\u58F0\u540E\uFF0C\u5979\u4F3C\u4E4E\u53D1\u73B0\u4E86\u4E00\u4E2A\u65B0\u5929\u5730\uFF0C\u53C8\u5174\u594B\u5730\u8FDE\u53EB\u6570\u58F0\u3002\u7136\u540E\u4F38\u51FA\u9999\u820C\uFF0C\u62FC\u547D\u6643\u52A8\u5706\u81C0\u3002</p><p>\u7F8E\u4E3D\u7684\u8138\u5E9E\u5728\u7D2B\u73AB\u773C\u91CC\u8D8A\u6765\u8D8A\u964C\u751F\uFF0C\u5979\u50BB\u50BB\u770B\u7740\u5B8C\u5168\u53D8\u6210\u4E00\u6761\u72D7\u7684\u5E08\u59D0\uFF0C\u65E0\u8FB9\u7684\u6050\u60E7\u5E2D\u5377\u800C\u6765\u3002\u7D2B\u73AB\u4E0D\u6562\u518D\u5F85\u4E0B\u53BB\uFF0C\u987E\u4E0D\u5F97\u53BB\u5BFB\u627E\u5B9D\u85CF\u7684\u7EBF\u7D22\uFF0C\u60CA\u614C\u5931\u63AA\u5730\u8DD1\u51FA\u77F3\u5BA4\u3002\u76F4\u5230\u8DD1\u56DE\u5723\u5BAB\uFF0C\u5750\u5728\u81EA\u5DF1\u623F\u5185\uFF0C\u8EAB\u4F53\u8FD8\u4E0D\u4F4F\u98A4\u6296\u3002</p><p>\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u542C\u5230\u6025\u4FC3\u7684\u811A\u6B65\u58F0\uFF0C\u767D\u6C0F\u59D0\u59B9\u8FDE\u5FD9\u4F4F\u624B\u3002\u7B49\u4E86\u4E00\u4F1A\u513F\u4E0D\u89C1\u6709\u4EBA\u8FDB\u6765\uFF0C\u4E24\u4EBA\u80C6\u5B50\u53C8\u5927\u4E86\u8D77\u6765\u3002</p><p>\u5979\u4EEC\u6084\u6084\u9876\u4E0A\u95E8\uFF0C\u7136\u540E\u7B11\u563B\u563B\u9053\uFF1A\u300C\u5974\u5A62\u7ED9\u592B\u4EBA\u62B9\u836F\uFF0C\u8BF7\u592B\u4EBA\u653E\u677E\u8D35\u4F53\u2026\u2026\u300D</p><p>\u78A7\u7EFF\u8272\u7684\u836F\u818F\u88AB\u7EC6\u7EC6\u6D82\u62B9\u5728\u83CA\u6D1E\u5185\u5916\uFF0C\u6BCF\u4E00\u4E2A\u7EC6\u5C0F\u7684\u8936\u76B1\u90FD\u4E0D\u653E\u8FC7\u3002\u4E0D\u591A\u65F6\u711A\u60C5\u818F\u4FBF\u88AB\u5AE9\u8089\u5438\u6536\uFF0C\u6CA1\u6709\u7559\u4E0B\u4E00\u70B9\u75D5\u8FF9\u3002\u8FD9\u662F\u5BAB\u4E3B\u7684\u5429\u5490\uFF0C\u7528\u4E0D\u4E86\u51E0\u5929\uFF0C\u762B\u8F6F\u5728\u5E8A\u7684\u7F8E\u5987\u5C31\u4F1A\u53D8\u6210\u9760\u809B\u95E8\u83B7\u5F97\u5FEB\u611F\u7684\u6DEB\u7269\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u628A\u7279\u5236\u7684\u8309\u8389\u82B1\u6CB9\u5012\u5728\u624B\u4E2D\uFF0C\u56DB\u53EA\u5C0F\u624B\u540C\u65F6\u629A\u6478\u7740\u7F8E\u5987\u5149\u6D01\u7684\u7389\u80A9\u3002</p><p>\u624B\u638C\u8FC7\u540E\uFF0C\u5438\u6536\u4E86\u6CB9\u8102\u7684\u808C\u80A4\u6108\u53D1\u767D\u817B\u52A8\u4EBA\uFF0C\u4EFF\u4F5B\u80FD\u6324\u51FA\u6C34\u822C\u5149\u6DA6\u6ED1\u5AE9\u3002</p><p>\u300C\u592B\u4EBA\u7684\u76AE\u80A4\u771F\u597D\u2026\u2026\u53C8\u7EC6\u53C8\u6ED1\uFF0C\u6BD4\u7F0E\u5B50\u8FD8\u5149\u5462\u3002\u300D</p><p>\u300C\u53EF\u4E0D\u662F\u561B\uFF0C\u5BAB\u4E3B\u6700\u559C\u6B22\u592B\u4EBA\u7684\u5C41\u80A1\u4E86\u3002\u8FD9\u6837\u4E00\u62B9\uFF0C\u767D\u767D\u5AE9\u5AE9\uFF0C\u5BAB\u4E3B\u64CD\u8D77\u6765\u5C31\u66F4\u8212\u670D\u4E86\uFF0C\u300D\u4E24\u5973\u53FD\u53FD\u5495\u5495\u8BF4\u7B11\u7740\uFF0C\u6D51\u4E0D\u7406\u4F1A\u8427\u4F5B\u5974\u8138\u65C1\u7684\u6CEA\u6C34\u3002</p><p>\u62B9\u5B8C\u80CC\u90E8\uFF0C\u4E24\u5973\u5C06\u8427\u4F5B\u5974\u7FFB\u4E86\u4E2A\u8EAB\uFF0C\u7EE7\u7EED\u6309\u6469\u6B63\u9762\u3002</p><p>\u300C\u592B\u4EBA\u4E00\u76F4\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u597D\u50CF\u80D6\u4E86\u4E00\u4E9B\u5462\u3002\u300D</p><p>\u300C\u55EF\uFF0C\u5BAB\u4E3B\u6628\u5929\u8FD8\u8BF4\uFF0C\u8BA9\u54B1\u4EEC\u591A\u7ED9\u592B\u4EBA\u6309\u6469\u6309\u6469\uFF0C\u514D\u5F97\u8FD9\u4E48\u6F02\u4EAE\u7684\u592B\u4EBA\u53D8\u6210\u4E2A\u53C8\u80D6\u53C8\u81ED\u7684\u810F\u6BCD\u732A\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u9EEF\u7136\u795E\u4F24\uFF0C\u5979\u4E5F\u611F\u89C9\u81EA\u5DF1\u7565\u5FAE\u6709\u4E9B\u53D1\u798F\uFF0C\u4F46\u6CA1\u60F3\u5230\u513F\u5B50\u7ADF\u4F1A\u8FD9\u6837\u5ACC\u5F03\u81EA\u5DF1\u3002</p><p>\u300C\u4F60\u77A7\uFF0C\u592B\u4EBA\u7684\u8170\u4E0D\u662F\u7C97\u4E86\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u51D1\u8FC7\u53BB\u4E00\u770B\uFF0C\u5931\u7B11\u9053\uFF1A\u300C\u8170\u5F53\u7136\u7C97\u4E86\uFF0C\u592B\u4EBA\u662F\u6000\u4E0A\u5BAB\u4E3B\u7684\u9F99\u80CE\u4E86\u3002\u300D</p><p>\u6B63\u5728\u6D41\u6CEA\u7684\u8427\u4F5B\u5974\u95FB\u8A00\u5931\u58F0\u5C16\u53EB\uFF0C\u6323\u624E\u7740\u8981\u5750\u8D77\u6765\u3002</p>',67);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
