import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_70" tabindex="-1"><a class="header-anchor" href="#_70" aria-hidden="true">#</a> 70</h1><p>\u8427\u4F5B\u5974\u6014\u6014\u770B\u7740\u81EA\u5DF1\u5E73\u5766\u7684\u5C0F\u8179\uFF0C\u600E\u4E48\u4E5F\u4E0D\u6562\u76F8\u4FE1\u4E71\u4F26\u7684\u79CD\u5B50\u5DF2\u7ECF\u5728\u5B50\u5BAB\u5185\u751F\u957F\u3002\u751F\u8FC7\u4E24\u80CE\u7684\u8427\u4F5B\u5974\u672C\u6765\u80FD\u611F\u89C9\u5230\u8EAB\u4F53\u7684\u5F02\u6837\uFF0C\u4F46\u8FDE\u65E5\u4E0D\u505C\u7684\u6298\u78E8\u4F7F\u5979\u5FFD\u7565\u4E86\u81EA\u5DF1\u7684\u751F\u7406\u53D8\u5316\u3002</p><p>\u300C\u5C11\u592B\u4EBA\u521A\u521A\u4E0E\u5BAB\u4E3B\u6210\u5A5A\uFF0C\u592B\u4EBA\u5C31\u6000\u4E0A\u4E86\u5BAB\u4E3B\u7684\u9F99\u80CE\u2026\u2026\u8FD9\u662F\u5BAB\u4E3B\u7684\u798F\u6C14\uFF0C\u4E5F\u662F\u592B\u4EBA\u7684\u798F\u6C14\u3002\u4E0D\u77E5\u9053\u8FD9\u91CC\u9762\u662F\u7537\u662F\u5973\uFF0C\u80FD\u4E0D\u80FD\u7EE7\u627F\u5BAB\u4E3B\u7684\u5B9D\u5EA7\u2026\u2026\u300D\u767D\u7389\u83BA\u5632\u8BBD\u5730\u8BF4\u3002</p><p>\u8427\u4F5B\u5974\u9759\u9759\u770B\u7740\u5C0F\u8179\uFF0C\u773C\u795E\u6E10\u6E10\u6563\u4E71\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6258\u7740\u5979\u7684\u80A9\u8180\u7B49\u4E86\u534A\u5929\uFF0C\u89C1\u5979\u8FD8\u662F\u4E00\u58F0\u4E0D\u54CD\uFF0C\u4E0D\u7531\u5FC3\u91CC\u6709\u6C14\u3002\u4E24\u4EBA\u62AC\u624B\u4E00\u6309\uFF0C\u5C06\u592B\u4EBA\u7684\u81FB\u9996\u6309\u5728\u5979\u7684\u817F\u95F4\uFF0C\u300C\u770B\u6E05\u695A\u4E86\u5417\uFF1F\u6000\u4E0A\u9F99\u5B50\u5F88\u5F97\u610F\u5427\uFF1F\u53EF\u8FD9\u7B97\u4F60\u662F\u7684\u513F\u5B50\u5462\u8FD8\u662F\u5B59\u5B50\uFF1F\u300D</p><p>\u6C89\u9ED8\u7684\u7F8E\u5987\u7A81\u7136\u53D1\u51FA\u4E00\u9635\u6E05\u8106\u7B11\u58F0\uFF0C\u5979\u5A07\u7B11\u7740\u62AC\u8D77\u5934\uFF0C\u773C\u6CE2\u6D41\u8F6C\uFF0C\u5B9B\u5982\u5F53\u65E5\u98CE\u534E\u7EDD\u4EE3\u7684\u5927\u71D5\u7687\u5983\u3002\u8FD9\u4E2A\u96BE\u4EE5\u63A5\u53D7\u7684\u73B0\u5B9E\uFF0C\u4F7F\u8427\u4F5B\u5974\u518D\u4E00\u6B21\u9677\u5165\u5931\u795E\u7684\u5883\u5730\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5FC3\u53EB\u4E0D\u5999\uFF0C\u8FDE\u5FD9\u6447\u7740\u592B\u4EBA\u7684\u9999\u80A9\uFF0C\u8BD5\u56FE\u8BA9\u5979\u6E05\u9192\u8FC7\u6765\u3002\u4EE5\u524D\u8427\u4F5B\u5974\u4E5F\u66FE\u7ECF\u6709\u8FC7\u77ED\u6682\u7684\u5931\u795E\uFF0C\u53EA\u8981\u4E00\u6447\u5C31\u80FD\u4F7F\u5979\u6E05\u9192\uFF0C\u4F46\u8FD9\u4E00\u6B21\uFF0C\u4E24\u5973\u6447\u4E86\u51E0\u4E0B\uFF0C\u8427\u4F5B\u5974\u4E0D\u4EC5\u6CA1\u6709\u6E05\u9192\uFF0C\u53CD\u800C\u5A07\u58F0\u9053\uFF1A\u300C\u597D\u54E5\u54E5\uFF0C\u4E0D\u8981\u6447\u4E86\u561B\u2026\u2026\u300D</p><p>\u59D0\u59B9\u4FE9\u76F8\u987E\u5931\u8272\uFF0C\u767D\u7389\u83BA\u4F38\u624B\u5728\u5979\u4E73\u5C16\u4E00\u62E7\uFF0C\u8427\u4F5B\u5974\u9999\u8EAF\u82B1\u679D\u822C\u4E00\u9635\u4E71\u6643\uFF0C\u98CE\u60C5\u4E07\u79CD\u5730\u5A49\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\u62B1\u6211\u2026\u2026\u5974\u5974\u624B\u811A\u4E00\u70B9\u529B\u6C14\u90FD\u6CA1\u6709\u2026\u2026\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u5FC3\u7075\u76F8\u901A\uFF0C\u53EA\u5BF9\u89C6\u4E00\u773C\u4FBF\u5F7C\u6B64\u4F1A\u610F\u3002\u767D\u7389\u9E42\u4F38\u624B\u63A2\u5230\u592B\u4EBA\u4E0B\u4F53\uFF0C\u63B0\u5F00\u82B1\u74E3\uFF0C\u767D\u7389\u83BA\u637B\u4F4F\u52C3\u8D77\u7684\u82B1\u8482\uFF0C\u8F7B\u63C9\u6162\u6311\uFF0C\u9017\u5F97\u8427\u4F5B\u5974\u5A9A\u53EB\u8FDE\u8FDE\u3002</p><p>\u8427\u4F5B\u5974\u7EA2\u971E\u6EE1\u8138\uFF0C\u5A07\u8273\u6B32\u6EF4\uFF0C\u300C\u597D\u54E5\u54E5\u2026\u2026\u8F7B\u4E00\u4E9B\u2026\u2026\u54CE\u5440\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u89C1\u5979\u7389\u6237\u6DEB\u6DB2\u6A2A\u6D41\uFF0C\u624B\u6307\u4E00\u52FE\uFF0C\u63A2\u8FDB\u8089\u7A74\u3002\u8427\u4F5B\u5974\u8FDE\u5FD9\u626D\u52A8\u8170\u80A2\uFF0C\u5A07\u6EF4\u6EF4\u5730\u8BF4\uFF1A\u300C\u54E5\u54E5\u522B\u8FDB\u53BB\u2026\u2026\u4E0D\u8981\u538B\u574F\u4E86\u54B1\u4EEC\u7684\u9F99\u5B50\u2026\u2026\u300D</p><p>\u59D0\u59B9\u4FE9\u628A\u8427\u4F5B\u5974\u5E73\u653E\u5728\u69BB\u4E0A\uFF0C\u4E24\u624B\u62E8\u5F04\u5979\u7684\u79D8\u5904\uFF0C\u53E6\u5916\u4E24\u53EA\u624B\u5219\u5728\u7389\u4F53\u4E0A\u56DB\u5904\u6E38\u8D70\u3002\u540C\u65F6\u4FEF\u9996\u542B\u4F4F\u7F8E\u5987\u7684\u4E73\u5934\uFF0C\u7AED\u529B\u8214\u8210\u3002\u4E0D\u591A\u65F6\uFF0C\u8427\u4F5B\u5974\u4FBF\u5A07\u8EAF\u5267\u98A4\uFF0C\u9AD8\u6F6E\u8FED\u8D77\uFF0C\u5C0F\u5634\u4E00\u5F20\uFF0C\u4FBF\u8981\u6D6A\u53EB\u51FA\u58F0\u3002</p><p>\u867D\u7136\u77F3\u5BA4\u7684\u9694\u97F3\u6781\u597D\uFF0C\u4F46\u4E24\u5973\u8FD8\u662F\u4E0D\u7EA6\u800C\u540C\u5730\u4F38\u624B\u6309\u4F4F\u8427\u4F5B\u5974\u7684\u7EA2\u5507\u3002\u7B49\u7F8E\u5987\u5514\u5514\u7684\u4F4E\u53EB\u6E10\u6E10\u6D88\u5931\uFF0C\u660F\u660F\u6C89\u6C89\u7684\u7761\u7740\uFF0C\u624D\u653E\u5F00\u624B\u3002</p><p>\u300C\u592B\u4EBA\u7761\u4E86\u8FD9\u4E48\u4E45\uFF0C\u64E6\u5B8C\u8EAB\u5B50\u4E5F\u6CA1\u9192\u2026\u2026\u4F1A\u4E0D\u4F1A\u51FA\u4EC0\u4E48\u610F\u5916\uFF1F\u300D</p><p>\u300C\u592B\u4EBA\u7ECF\u5E38\u8FD9\u6837\uFF0C\u6709\u65F6\u5019\u9192\u4E86\u8FDE\u5BAB\u4E3B\u90FD\u4E0D\u8BA4\u8BC6\u5462\u3002\u300D</p><p>\u300C\u2026\u2026\u6211\u8FD8\u662F\u6709\u4E9B\u4E0D\u653E\u5FC3\uFF0C\u8981\u4E0D\u8981\u53BB\u62A5\u544A\u5BAB\u4E3B\u548C\u5C11\u592B\u4EBA\u5462\uFF1F\u300D\u767D\u6C0F\u59D0\u59B9\u5FE7\u5FC3\u5FE1\u5FE1\u5730\u8BF4\u7740\uFF0C\u4F46\u5634\u89D2\u5374\u6302\u7740\u4E00\u4E1D\u76F8\u540C\u7684\u7B11\u610F\u3002</p><p>\u300C\u54CE\u5440\uFF01\u300D\u767D\u7389\u9E42\u7A81\u7136\u53D1\u51FA\u4E00\u58F0\u60CA\u547C\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u5979\u53C8\u62C9\u4E86\u2026\u2026\u300D</p><p>\u5C3F\u5E03\u8FD8\u6CA1\u6709\u6765\u5F97\u53CA\u88F9\uFF0C\u6C61\u7269\u4ECE\u4E24\u817F\u95F4\u7F13\u7F13\u6D41\u51FA\uFF0C\u76F4\u63A5\u6CBE\u5728\u6DCC\u6EE1\u6DEB\u6C34\u7684\u5E8A\u5355\u4E0A\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u76D8\u819D\u5750\u5728\u9759\u5BA4\uFF0C\u6253\u5750\u8FD0\u529F\u3002</p><p>\u6210\u5A5A\u540E\uFF0C\u4ED6\u53D1\u73B0\u6BCF\u6B21\u4E0E\u59B9\u59B9\u4EA4\u5408\uFF0C\u90FD\u4F1A\u5185\u529F\u5927\u8FDB\uFF0C\u76F4\u6BD4\u5F53\u65E5\u6731\u90AA\u9752\u6811\u52A9\u4ED6\u6253\u901A\u4EFB\u7763\u4E8C\u8109\u7684\u7A81\u98DE\u731B\u8FDB\u3002\u77ED\u77ED\u6570\u65E5\u95F4\uFF0C\u592A\u4E00\u7ECF\u7684\u4FEE\u4E3A\u5DF2\u7ECF\u7A81\u7834\u70BC\u6C14\u5316\u795E\u7684\u5883\u754C\uFF0C\u8FDB\u5165\u7B2C\u4E09\u5C42\u4E94\u6C14\u671D\u5143\u3002\u4EE5\u76EE\u524D\u7684\u8FDB\u5883\uFF0C\u4ED6\u6709\u628A\u63E1\u5728\u534A\u5E74\u5185\u5B8C\u6210\u53D6\u574E\u586B\u79BB\uFF0C\u8FBE\u5230\u9634\u59EC\u7EC8\u751F\u672A\u80FD\u5B8C\u6210\u7684\u7B2C\u56DB\u5C42\u3002</p><p>\u4E00\u53EA\u4E0D\u77E5\u4ECE\u4F55\u5904\u94BB\u5165\u7684\u868A\u5B50\u843D\u5728\u6155\u5BB9\u9F99\u80CC\u4E0A\uFF0C\u5FFD\u7136\u4E00\u9053\u8089\u773C\u96BE\u4EE5\u770B\u6E05\u7684\u767D\u5149\u95EA\u8FC7\uFF0C\u8863\u670D\u7EB9\u4E1D\u672A\u52A8\uFF0C\u90A3\u53EA\u868A\u5B50\u5DF2\u7ECF\u65E0\u5F71\u65E0\u8E2A\u3002</p><p>\u6155\u5BB9\u9F99\u7F13\u7F13\u5410\u6C14\u6536\u529F\uFF0C\u53CC\u76EE\u4E00\u7741\uFF0C\u5B9B\u5982\u591C\u7A7A\u4E2D\u7684\u5BD2\u661F\uFF0C\u7CBE\u5149\u56DB\u5C04\u3002\u4ED6\u8896\u5B50\u4E00\u626C\uFF0C\u4ECE\u8155\u4E0B\u6478\u51FA\u4E00\u622A\u4E03\u5B9D\u624B\u67C4\u3002\u624B\u67C4\u957F\u7EA6\u534A\u5C3A\uFF0C\u8272\u6CFD\u9EEF\u6DE1\uFF0C\u50CF\u662F\u7C97\u94C1\u6253\u5236\uFF0C\u53E4\u62D9\u751F\u786C\uFF0C\u6BEB\u4E0D\u8D77\u773C\u3002\u4F46\u67C4\u4E0A\u5927\u5927\u5C0F\u5C0F\u9576\u7740\u4E03\u9897\u8272\u6CFD\u5404\u5F02\u7684\u5B9D\u77F3\uFF0C\u7EDA\u70C2\u593A\u76EE\u3002\u8FD9\u6B63\u662F\u661F\u6708\u6E56\u9547\u6559\u795E\u5175\u4E4B\u4E00\uFF0C\u8361\u661F\u97AD\u3002</p><p>\u5F53\u65E5\u6155\u5BB9\u9F99\u4EE5\u6B64\u4E0E\u96EA\u5CF0\u795E\u5C3C\u5BF9\u654C\uFF0C\u731D\u4E0D\u53CA\u9632\u4E0B\uFF0C\u8361\u661F\u97AD\u5A01\u529B\u8FD8\u672A\u65BD\u5C55\u4FBF\u88AB\u795E\u5C3C\u9707\u788E\u97AD\u8EAB\u3002\u4F46\u6B64\u97AD\u5965\u5999\u5728\u4E8E\u97AD\u67C4\u7684\u5947\u5F02\uFF0C\u4E0D\u4EC5\u67C4\u5185\u4E2D\u7A7A\u53EF\u5BB9\u7EB3\u97AD\u4F53\uFF0C\u4E00\u65E6\u65BD\u5C55\u5F00\u6765\uFF0C\u67C4\u4E0A\u7684\u5317\u6597\u4E03\u661F\u4FBF\u5149\u8292\u56DB\u5C04\uFF0C\u751A\u81F3\u4F1A\u900F\u8FC7\u624B\u638C\uFF0C\u4E03\u5F69\u540C\u73B0\u3002</p><p>\u65E0\u8BBA\u591C\u6218\u8FD8\u662F\u663C\u6218\uFF0C\u8FD9\u79CD\u7531\u5185\u529B\u50AC\u53D1\u7684\u5149\u8292\u90FD\u4E0D\u4F1A\u88AB\u5176\u4ED6\u5149\u7EBF\u6240\u63A9\u76D6\uFF0C\u800C\u4E14\u97AD\u4F53\u7684\u67D4\u97E7\u548C\u529B\u9053\u4E5F\u4F1A\u4EE5\u500D\u6570\u589E\u52A0\u3002</p><p>\u8361\u661F\u97AD\u88AB\u6BC1\u4E4B\u540E\uFF0C\u6155\u5BB9\u9F99\u4EE5\u65E5\u6708\u94A9\u4E3A\u968F\u8EAB\u5175\u5203\u3002\u5982\u4ECA\u65E5\u6708\u94A9\u7A7F\u5728\u96EA\u5CF0\u795E\u5C3C\u80A9\u4E0A\uFF0C\u4E8E\u662F\u5BFB\u89C5\u97AD\u4F53\uFF0C\u91CD\u5236\u6B64\u97AD\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u8155\u5FAE\u52A8\uFF0C\u4E00\u6BB5\u7389\u767D\u8272\u8FD1\u4E4E\u900F\u660E\u7684\u97AD\u4F53\u4ECE\u67C4\u5185\u95EA\u7535\u822C\u6FC0\u5C04\u800C\u51FA\u3002\u5F85\u62C9\u5230\u5C3D\u5934\u5FFD\u7136\u4E00\u5F39\uFF0C\u97AD\u4F53\u5012\u5377\uFF0C\u7F20\u5728\u624B\u81C2\u4E0A\u3002\u53EA\u89C1\u97AD\u4F53\u7531\u56DB\u6839\u8D28\u5730\u76F8\u540C\u7684\u7EC6\u7EBF\u7EDE\u6210\uFF0C\u4E24\u957F\u4E24\u77ED\u3002\u5947\u602A\u7684\u662F\u7EC6\u7EBF\u4E0D\u4EC5\u97E7\u6027\u5341\u8DB3\uFF0C\u800C\u4E14\u5149\u6E9C\u6E9C\u6CA1\u6709\u4EFB\u4F55\u5236\u4F5C\u7684\u75D5\u8FF9\uFF0C\u5B9B\u5982\u5929\u6210\u3002</p><p>\u6155\u5BB9\u9F99\u6CE8\u89C6\u7740\u7EC6\u767D\u7684\u97AD\u8EAB\uFF0C\u773C\u5149\u5145\u6EE1\u4E86\u9A84\u50B2\u3001\u81EA\u4FE1\uFF0C\u8FD8\u6709\u4E00\u4E1D\u4E1D\u7684\u601C\u60AF\u3002\u4ED6\u633D\u8D77\u8361\u661F\u97AD\u653E\u5728\u8138\u4E0A\u8F7B\u8F7B\u78E8\u64E6\uFF0C\u8138\u4E0A\u73B0\u51FA\u5947\u5F02\u7684\u5FAE\u7B11\uFF0C\u300C\u5A18\uFF0C\u6211\u8981\u5E26\u7740\u4F60\u53BB\u5F81\u670D\u5929\u4E0B\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u6B3E\u6B3E\u8D77\u8EAB\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5348\u996D\u5403\u4E86\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u7AEF\u8D77\u73AB\u7470\u9732\u559D\u4E86\u4E00\u53E3\u3002</p><p>\u300C\u8981\u4E0D\u8981\u4F11\u606F\u4E00\u4F1A\u513F\uFF1F\u300D\u7D2B\u73AB\u58F0\u97F3\u5F88\u5C0F\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8138\u574F\u7B11\u5730\u770B\u7740\u59B9\u59B9\uFF0C\u679C\u7136\u7D2B\u73AB\u7684\u8138\u6E10\u6E10\u7EA2\u4E86\u8D77\u6765\u3002\u5979\u5782\u4E0B\u5934\uFF0C\u6709\u4E9B\u5C40\u4FC3\u5730\u634F\u7740\u8863\u5E26\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u6211\u4E0D\u662F\u90A3\u4E2A\u610F\u601D\u2026\u2026\u300D</p><p>\u90A3\u79CD\u8BF1\u4EBA\u7684\u7F9E\u8272\uFF0C\u8BA9\u6155\u5BB9\u9F99\u6068\u4E0D\u5F97\u4E00\u53E3\u628A\u5979\u541E\u5230\u809A\u5B50\uFF0C\u4ED6\u54C8\u54C8\u4E00\u7B11\uFF0C\u62E5\u4F4F\u59B9\u59B9\u7684\u7EA4\u8170\uFF0C\u300C\u60F3\u54E5\u54E5\u4E86\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u7740\u6025\u5730\u6273\u7740\u4ED6\u7684\u624B\u81C2\uFF0C\u4E00\u8FB9\u6323\u624E\uFF0C\u4E00\u8FB9\u8138\u8272\u901A\u7EA2\u5730\u8BF4\u9053\uFF1A\u300C\u4E0D\u8981\u2026\u2026\u300D</p><p>\u6000\u4E2D\u9165\u4E73\u7FD8\u81C0\u66F2\u7EBF\u73B2\u73D1\uFF0C\u9694\u7740\u8863\u670D\u8FD8\u80FD\u6E05\u695A\u5730\u611F\u89C9\u7389\u4EBA\u808C\u80A4\u7684\u5149\u6ED1\uFF0C\u6155\u5BB9\u9F99\u8272\u6B32\u5927\u52A8\uFF0C\u4E00\u628A\u62B1\u8D77\u6000\u4E2D\u7684\u6E29\u9999\u8F6F\u7389\uFF0C\u671D\u7389\u69BB\u8D70\u53BB\u3002\u4E00\u8FB9\u8D70\u4E00\u8FB9\u7B11\uFF0C\u300C\u4E56\u4E56\u7684\uFF0C\u54E5\u54E5\u8BA9\u4F60\u6B32\u4ED9\u6B32\u6B7B\u2026\u2026\u300D\u5FC3\u91CC\u5374\u6697\u9053\uFF1A\u300C\u987A\u4FBF\u5E2E\u54E5\u54E5\u70BC\u529F\u597D\u4E86\u3002\u300D</p><p>\u7109\u77E5\u7D2B\u73AB\u60F3\u7684\u4E0E\u4ED6\u4E00\u822C\u65E0\u4E8C\uFF0C\u5979\u5185\u529F\u88AB\u5236\uFF0C\u53EA\u80FD\u85C9\u4EA4\u5408\u4FEE\u70BC\u51E4\u51F0\u5B9D\u5178\u3002\u5F53\u4E0B\u6B32\u62D2\u8FD8\u8FCE\uFF0C\u4E56\u4E56\u4EFB\u4ED6\u62B1\u5230\u69BB\u4E0A\u3002</p><p>\u661F\u6708\u6E56\u6700\u8BB2\u7A76\u300C\u9F0E\u7089\u300D\u4E00\u7269\uFF0C\u56E0\u6B64\u5386\u4EE3\u5BAB\u4E3B\u90FD\u4E0D\u9057\u4F59\u529B\u5730\u641C\u7F57\u5929\u4E0B\u5973\u5B50\u4EE5\u4F9B\u4F7F\u7528\u3002\u95F4\u6216\u6709\u4EBA\u85C9\u6B64\u7EC3\u6210\u795E\u529F\uFF0C\u8F7D\u4E8E\u5178\u85C9\uFF0C\u66F4\u5F15\u5F97\u65E0\u6570\u4EBA\u8FFD\u6155\u5411\u5F80\u3002</p><p>\u6155\u5BB9\u9F99\u867D\u7136\u6000\u7591\u7D2B\u73AB\u7EC3\u7684\u662F\u51E4\u51F0\u5B9D\u5178\uFF0C\u4F46\u4EE5\u4E3A\u81EA\u5DF1\u529F\u529B\u5927\u8FDB\u662F\u56E0\u4E3A\u62E3\u5230\u4E00\u4E2A\u7EDD\u54C1\u300C\u9F0E\u7089\u300D\uFF0C\u800C\u4E0D\u77E5\u662F\u7531\u4E8E\u4E24\u4EBA\u4FEE\u70BC\u7684\u771F\u6C14\u5951\u5408\uFF1B\u66F4\u6CA1\u6709\u60F3\u5230\u81EA\u5DF1\u4FEE\u70BC\u7684\u540C\u65F6\uFF0C\u4E5F\u5728\u50AC\u903C\u7D2B\u73AB\u7684\u771F\u6C14\u66F4\u4E0A\u5C42\u697C\u3002</p><p>\u770B\u5230\u5C0F\u4E2B\u5934\u4E3B\u52A8\u5E2E\u81EA\u5DF1\u5BBD\u8863\u89E3\u5E26\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7531\u5FC3\u82B1\u6012\u653E\uFF0C\u6B63\u5F85\u6295\u6843\u62A5\u674E\uFF0C\u5FFD\u7136\u542C\u5230\u4E24\u58F0\u8F7B\u8F7B\u7684\u53E9\u95E8\u58F0\u3002</p><p>\u767D\u7389\u83BA\u6709\u4E9B\u60CA\u614C\u5730\u56DE\u7980\u8BF4\uFF1A\u300C\u592B\u4EBA\u660F\u7761\u4E86\u4E00\u4E0A\u5348\uFF0C\u5230\u73B0\u5728\u8FD8\u672A\u9192\u6765\u3002\u300D</p><p>\u867D\u7136\u7BAD\u5728\u5F26\u4E0A\uFF0C\u4F46\u542C\u5230\u6BCD\u4EB2\u60C5\u5F62\u6709\u5F02\uFF0C\u5144\u59B9\u4FE9\u8FDE\u5FD9\u6574\u8863\u8D77\u8EAB\uFF0C\u8D76\u5230\u5E9A\u5BA4\u3002</p><p>\u8427\u4F5B\u5974\u9759\u9759\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u5B9B\u5982\u6D77\u68E0\u6625\u7761\u3002\u5979\u8138\u4E0A\u7EA2\u6F6E\u5DF2\u892A\uFF0C\u4F46\u9AD8\u6F6E\u7684\u6109\u60A6\u5374\u5728\u5979\u8138\u4E0A\u7559\u4E0B\u9999\u751C\u7684\u7B11\u5BB9\u3002</p><p>\u6155\u5BB9\u9F99\u4E24\u6307\u642D\u5728\u6BCD\u4EB2\u8155\u4E0A\uFF0C\u4E00\u7F15\u771F\u6C14\u77AC\u606F\u6E38\u904D\u5168\u8EAB\u3002\u63A2\u5F97\u5E76\u65E0\u5F02\u72B6\uFF0C\u4ED6\u677E\u4E86\u53E3\u6C14\u3002\u63A5\u7740\u771F\u6C14\u5FAE\u5FAE\u52A0\u91CD\uFF0C\u5C06\u8427\u4F5B\u5974\u4ECE\u7761\u68A6\u4E2D\u5524\u9192\u3002</p><p>\u8427\u4F5B\u5974\u8FF7\u79BB\u5730\u7741\u5F00\u773C\u775B\uFF0C\u5F85\u770B\u6E05\u9762\u524D\u7684\u4EBA\u5F71\uFF0C\u5979\u5FFD\u7136\u751C\u751C\u4E00\u7B11\u3002\u6155\u5BB9\u9F99\u5FC3\u5934\u50CF\u88AB\u4EBA\u72E0\u634F\u4E00\u628A\uFF0C\u5DEE\u70B9\u55B7\u51FA\u8840\u6765\u3002\u6BCD\u4EB2\u5165\u5BAB\u5DF2\u7ECF\u4E24\u4E2A\u6708\u4E86\uFF0C\u8FD9\u8FD8\u662F\u5934\u4E00\u6B21\u5728\u4ED6\u9762\u524D\u9732\u51FA\u7B11\u5BB9\u2014\u2014\u9760\uFF0C\u5979\u600E\u4E48\u80FD\u7B11\u8FD9\u4E48\u751C\uFF1F</p><p>\u6155\u5BB9\u9F99\u9B42\u9B44\u8FD8\u672A\u5F52\u4F4D\uFF0C\u53EA\u542C\u8033\u8FB9\u8361\u8D77\u4E00\u7F15\u67D4\u5A9A\u5165\u9AA8\u7684\u5A07\u58F0\uFF1A\u300C\u54E5\u54E5\uFF0C\u6765\u62B1\u4EBA\u5BB6\u561B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9B42\u98DE\u5929\u5916\uFF0C\u4E00\u628A\u7D27\u7D27\u6402\u4F4F\u67D4\u82E5\u65E0\u9AA8\u7684\u5A07\u8EAF\u3002</p><p>\u7D2B\u73AB\u5374\u662F\u5FC3\u91CC\u53D1\u51C9\uFF0C\u5979\u614C\u5FD9\u6258\u8D77\u6BCD\u4EB2\u7684\u67D4\u9888\uFF0C\u5524\u9053\uFF1A\u300C\u5A18\uFF01\u5A18\uFF01\u4F60\u9192\u9192\u554A\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u773C\u4E2D\u6CE2\u5149\u4E00\u95EA\uFF0C\u5C0F\u5973\u5B69\u822C\u76B1\u8D77\u9F3B\u5B50\uFF0C\u6709\u4E9B\u4E0D\u60C5\u613F\u5730\u8BF4\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u600E\u4E48\u628A\u5979\u4E5F\u5E26\u6765\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u7740\u6025\u5730\u53EB\u9053\uFF1A\u300C\u5A18\uFF01\u4F60\u9192\u9192\u554A\uFF0C\u6211\u662F\u73AB\u513F\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u8D2A\u604B\u6BCD\u4EB2\u6B64\u65F6\u7684\u5A07\u6001\uFF0C\u4E00\u8FB9\u6B23\u8D4F\u5982\u82B1\u4F3C\u7389\u7684\u5A07\u9765\uFF0C\u4E00\u8FB9\u7B11\u5475\u5475\u9053\uFF1A\u300C\u6CA1\u4E8B\u513F\u6CA1\u4E8B\u513F\uFF0C\u4E00\u4F1A\u513F\u5C31\u597D\u4E86\u3002\u300D</p><p>\u300C\u5A18\u4F1A\u75AF\u7684\uFF01\u300D\u7D2B\u73AB\u5C16\u53EB\u9053\uFF0C\u5979\u626D\u5934\u671D\u5916\u9AD8\u58F0\u558A\u9053\uFF1A\u300C\u53F6\u62A4\u6CD5\u3001\u53F6\u62A4\u6CD5\uFF01\u83BA\u5974\uFF01\u4F60\u53BB\u8BF7\u53F6\u62A4\u6CD5\uFF0C\u5FEB\u70B9\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u8138\u8272\u4E00\u677F\uFF0C\u6B63\u5BB9\u9053\uFF1A\u300C\u4E0D\u7528\u7740\u6025\uFF0C\u6211\u5148\u7528\u5185\u529B\u5E2E\u5A18\u987A\u6C14\u6D3B\u8840\u3002\u4E00\u4E2A\u65F6\u8FB0\u4E4B\u540E\u518D\u8BF7\u53F6\u62A4\u6CD5\u3002\u300D</p><p>\u7D2B\u73AB\u6C14\u5F97\u5634\u5507\u53D1\u6296\uFF0C\u4EC0\u4E48\u987A\u6C14\u6D3B\u8840\uFF0C\u8FD8\u4E0D\u662F\u60F3\u85C9\u673A\u5978\u6DEB\u6BCD\u4EB2\uFF01\u771F\u662F\u4E2A\u755C\u7272\uFF01</p><p>\u6DF7\u86CB\uFF01\u5979\u6068\u6068\u4E00\u987F\u8DB3\uFF0C\u6454\u95E8\u800C\u53BB\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u77E5\u8DA3\u5730\u9000\u5230\u4E00\u8FB9\uFF0C\u6155\u5BB9\u9F99\u6162\u6761\u65AF\u7406\u5730\u9664\u4E0B\u6BCD\u4EB2\u7684\u8863\u886B\u3002</p><p>\u8427\u4F5B\u5974\u5A9A\u6001\u6A2A\u751F\u5730\u77A5\u4E86\u4ED6\u4E00\u773C\uFF0C\u817B\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\u53C8\u8981\u6B3A\u8D1F\u4EBA\u5BB6\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8840\u8109\u8D32\u5F20\uFF0C\u4E09\u628A\u4E24\u628A\u89E3\u5F00\u5C3F\u5E03\uFF0C\u8FD8\u597D\uFF0C\u5E72\u51C0\u7684\u3002</p><p>\u5F53\u624B\u6307\u89E6\u5230\u4E0B\u4F53\uFF0C\u8427\u4F5B\u5974\u4F4E\u53EB\u9053\uFF1A\u300C\u4E0D\u8981\u2026\u2026\u300D\u5979\u5A07\u8EAF\u4E00\u626D\uFF0C\u7EC6\u7709\u8F7B\u8F7B\u76B1\u8D77\uFF0C\u300C\u8D31\u59BE\u8EAB\u5B50\u597D\u56F0\u2026\u2026\u54CE\u5440\uFF0C\u4F60\u4E0D\u8981\u8FDB\u53BB\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u6D41\u4E86\u597D\u591A\u6C34\u5462\uFF0C\u96BE\u9053\u4E0D\u60F3\u8BA9\u54E5\u54E5\u8FDB\u53BB\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u8981\u7B11\u4EBA\u5BB6\u2026\u2026\u300D\u8427\u4F5B\u5974\u7F9E\u6DA9\u7684\u8BF4\uFF0C\u300C\u4F60\u6478\u6478\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u5934\u96FE\u6C34\uFF0C\u4F38\u624B\u62E8\u5F00\u5A07\u5AE9\u7684\u82B1\u74E3\u3002</p><p>\u300C\u4E0D\u662F\u5566\u2026\u2026\u300D\u8427\u4F5B\u5974\u6EE1\u8138\u7EA2\u6655\uFF0C\u300C\u4E0A\u8FB9\u2026\u2026\u4E0D\u662F\uFF01\u54E5\u54E5\u4F60\u597D\u574F\u2026\u2026\u4E0A\u8FB9\uFF0C\u55EF\uFF0C\u6478\u5230\u4E86\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u624B\u638C\u505C\u5728\u6ED1\u817B\u7684\u5C0F\u8179\u4E0A\uFF0C\u9759\u9759\u770B\u7740\u6BCD\u4EB2\u3002</p><p>\u8427\u4F5B\u5974\u5374\u6CA1\u6709\u6CE8\u610F\u4ED6\u795E\u8272\u7684\u53D8\u5316\uFF0C\u559C\u5B5C\u5B5C\u5730\u8BF4\uFF1A\u300C\u6478\u5230\u4E86\u5417\uFF1F\u6211\u4EEC\u7684\u5B69\u5B50\u2026\u2026\u300D</p><p>\u300C\u55EF\u3002\u300D</p><p>\u300C\u592A\u533B\u8BF4\u624D\u4E24\u4E2A\u6708\u2026\u2026\u7941\u54E5\uFF0C\u4F60\u9AD8\u5174\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u58F0\u4E0D\u54CD\u3002</p><p>\u8427\u4F5B\u5974\u6EE1\u8138\u5E78\u798F\u7684\u559C\u60A6\uFF0C\u5782\u7740\u773C\u5EC9\u67D4\u58F0\u9053\uFF1A\u300C\u8FD9\u662F\u54B1\u4EEC\u7B2C\u4E00\u4E2A\u5B69\u5B50\uFF0C\u5C31\u53EB\u4ED6\u2014\u2014\u9F99\u513F\uFF0C\u6155\u5BB9\u9F99\u3002\u7941\u54E5\uFF0C\u4F60\u8BF4\u597D\u4E0D\u597D\uFF1F\u2026\u2026\u4ED6\u957F\u5927\u4E86\uFF0C\u4E00\u5B9A\u50CF\u4F60\u90A3\u4E48\u5E05\uFF0C\u53C8\u806A\u660E\uFF0C\u53C8\u52C7\u6562\uFF0C\u53C8\u6709\u529B\u6C14\u2026\u2026\u6211\u8981\u6559\u4ED6\u8BFB\u4E66\u5199\u5B57\uFF0C\u4F60\u6559\u4ED6\u9A91\u9A6C\u5C04\u7BAD\u2026\u2026\u4ED6\u5C06\u6765\u4E00\u5B9A\u4F1A\u662F\u4E2A\u597D\u7687\u5E1D\uFF0C\u8BA9\u6155\u5BB9\u6C0F\u5B50\u5B59\u5EF6\u7EF5\u2026\u2026\u300D</p><p>\u542C\u7740\u8033\u8FB9\u5982\u8BD7\u5982\u68A6\u7684\u5583\u5583\u4F4E\u8BC9\uFF0C\u6155\u5BB9\u9F99\u5589\u5934\u54FD\u4F4F\uFF0C\u4ECE\u4E03\u5C81\u8D77\u5C31\u5E72\u6DB8\u7684\u773C\u7736\u53C8\u4E00\u6B21\u6E7F\u6DA6\u3002\u4ED6\u628A\u8138\u8D34\u5728\u6BCD\u4EB2\u7684\u5C0F\u8179\u4E0A\uFF0C\u6C79\u6D8C\u7684\u6CEA\u6C34\u6EF4\u5728\u767D\u817B\u7684\u808C\u80A4\u4E0A\uFF0C\u9732\u73E0\u822C\u6EDA\u52A8\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u9762\u9762\u76F8\u89D1\uFF0C\u5979\u4EEC\u6CA1\u60F3\u5230\uFF0C\u8FD9\u4E2A\u66B4\u8650\u6210\u6027\u559C\u6012\u65E0\u5E38\u7684\u4E3B\u5B50\uFF0C\u7ADF\u7136\u4E5F\u4F1A\u50CF\u4E2A\u5B69\u5B50\u4E00\u6837\u54ED\u6CE3\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u6155\u5BB9\u9F99\u7F13\u7F13\u62AC\u8D77\u5934\uFF0C\u8BF4\u9053\uFF1A\u300C\u53BB\u8BF7\u53F6\u62A4\u6CD5\u3002\u300D</p><p>\u4ED6\u7684\u58F0\u97F3\u51B0\u51B7\u5982\u5E38\uFF0C\u6CA1\u6709\u4EFB\u4F55\u6CE2\u52A8\u3002\u82F1\u4FCA\u7684\u9762\u5B54\u4E0A\u4E5F\u6BEB\u65E0\u8868\u60C5\uFF0C\u82E5\u975E\u8FD8\u6CBE\u7740\u6CEA\u75D5\uFF0C\u767D\u6C0F\u59D0\u59B9\u771F\u4E0D\u6562\u76F8\u4FE1\u5BAB\u4E3B\u521A\u521A\u771F\u7684\u54ED\u8FC7\u3002</p>',75);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
