import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_71" tabindex="-1"><a class="header-anchor" href="#_71" aria-hidden="true">#</a> 71</h1><p>\u4E1C\u65B9\u7684\u5929\u9645\u521A\u6CDB\u8D77\u9C7C\u809A\u767D\uFF0C\u62AB\u895F\u7A84\u8896\u4E00\u8EAB\u9C9C\u5351\u8D35\u65CF\u6253\u626E\u7684\u6155\u5BB9\u9F99\u4FBF\u7ACB\u5728\u9636\u524D\uFF0C\u8FDC\u8FDC\u773A\u671B\u8FDE\u7EF5\u7684\u7EC8\u5357\u7FA4\u5CF0\u3002\u5728\u4ED6\u8EAB\u540E\uFF0C\u7559\u5B88\u795E\u6559\u4E0E\u968F\u884C\u7684\u9AD8\u624B\u5206\u6210\u4E24\u5217\uFF0C\u96C1\u884C\u6392\u5F00\u3002</p><p>\u5DE6\u8FB9\u4E00\u5217\u4EE5\u91D1\u5F00\u7532\u4E3A\u9996\uFF0C\u4ED6\u8EAB\u7740\u94F6\u767D\u77ED\u886B\uFF0C\u6D53\u53D1\u6563\u5728\u8111\u540E\uFF0C\u9AA0\u608D\u4E2D\u53C8\u5E26\u7740\u4E45\u7ECF\u6218\u9635\u7684\u6C89\u7A33\uFF1B\u7D27\u968F\u5176\u540E\u7684\u662F\u7075\u7389\u771F\u4EBA\uFF0C\u4ED6\u7684\u9053\u888D\u5DF2\u7ECF\u6362\u6210\u672C\u5802\u7684\u9752\u8272\uFF0C\u8D1F\u624B\u800C\u7ACB\uFF0C\u795E\u8272\u6DE1\u7136\uFF0C\u4F46\u773C\u4E2D\u9690\u7EA6\u95EA\u52A8\u7684\u7CBE\u5149\uFF0C\u5374\u6709\u79CD\u55DC\u8840\u7684\u6B8B\u5FCD\uFF1B\u4E0E\u4E24\u4F4D\u957F\u8001\u76F8\u6BD4\uFF0C\u77F3\u880D\u663E\u5F97\u6740\u6C14\u5916\u9732\uFF0C\u6574\u4E2A\u4EBA\u5C31\u50CF\u4ED6\u8170\u95F4\u7684\u880D\u5C3E\u94A9\uFF0C\u968F\u65F6\u90FD\u51C6\u5907\u4E0E\u4EBA\u6027\u547D\u76F8\u640F\u3002</p><p>\u5BAB\u767D\u7FBD\u8EAB\u6750\u77EE\u5C0F\uFF0C\u80A4\u8272\u9EDD\u9ED1\uFF0C\u867D\u7136\u8C8C\u4E0D\u60CA\u4EBA\uFF0C\u4F46\u6F5C\u8E2A\u533F\u8FF9\uFF0C\u72EC\u95EF\u7981\u5BAB\u5982\u5C65\u5E73\u5730\u7684\u529F\u592B\u5374\u5728\u4F17\u4EBA\u4E4B\u4E0A\u3002</p><p>\u53F3\u8FB9\u7B2C\u4E00\u4F4D\u662F\u9752\u888D\u5E03\u5C65\u7684\u6C90\u58F0\u4F20\uFF0C\u5176\u540E\u7AD9\u7740\u5C60\u6000\u6C89\u3001\u767D\u94F6\u3001\u9752\u94DC\u7B49\u4EBA\uFF0C\u7559\u5B88\u661F\u6708\u6E56\u3002</p><p>\u300C\u53F6\u62A4\u6CD5\u5462\uFF1F\u300D\u6155\u5BB9\u9F99\u95EE\u9053\u3002</p><p>\u300C\u53F6\u62A4\u6CD5\u6B63\u5728\u7ED9\u592B\u4EBA\u5907\u836F\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u6628\u591C\u53F6\u884C\u5357\u65BD\u9488\u4E4B\u540E\uFF0C\u8427\u4F5B\u5974\u7684\u795E\u667A\u7565\u5FAE\u6E05\u9192\u4E00\u4E9B\uFF0C\u4F46\u8FD8\u65F6\u6709\u53CD\u8986\u3002\u4EE5\u5979\u5A07\u5F31\u7684\u8EAB\u4F53\uFF0C\u672C\u6765\u9700\u5728\u5BAB\u4E2D\u9759\u517B\uFF0C\u53EF\u6B64\u53BB\u9F99\u57CE\u6765\u56DE\u6570\u6708\uFF0C\u6155\u5BB9\u9F99\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u613F\u4E0E\u6BCD\u4EB2\u5206\u79BB\u8FD9\u4E48\u4E45\uFF0C\u4E8E\u662F\u4E0D\u987E\u59B9\u59B9\u7684\u6CE3\u6C42\uFF0C\u53F6\u884C\u5357\u7684\u529D\u963B\uFF0C\u6267\u610F\u643A\u8427\u4F5B\u5974\u540C\u884C\u3002\u968F\u884C\u7684\u5973\u7737\u9664\u4E86\u6BCD\u4EB2\u548C\u59B9\u59B9\uFF0C\u8FD8\u6709\u767D\u6C0F\u59D0\u59B9\u6CBF\u9014\u4F3A\u5019\uFF0C\u4EE5\u53CA\u7EAA\u7709\u59A9\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8309\u8389\u82B1\u6CB9\u591A\u5E26\u4E0D\u4FBF\uFF0C\u8FD9\u4E9B\u4F7F\u5B8C\uFF0C\u9014\u4E2D\u8D2D\u4E70\u5373\u53EF\u3002\u7528\u524D\u5148\u5C06\u8FD9\u4E9B\u836F\u7C89\u63BA\u5165\uFF0C\u4E0D\u9700\u592A\u591A\uFF0C\u4E00\u5200\u572D\u5373\u53EF\uFF0C\u8FD9\u4E9B\u8DB3\u591F\u534A\u5E74\u4E4B\u7528\u3002\u6B64\u836F\u5B89\u80CE\u5B81\u795E\uFF0C\u7EDD\u65E0\u4ED6\u5F02\u2026\u2026\u592B\u4EBA\u79C9\u6027\u67D4\u5F31\uFF0C\u53C8\u5367\u5E8A\u4E0D\u8D77\uFF0C\u8840\u884C\u4E0D\u7545\uFF0C\u53C8\u6613\u611F\u98CE\u5BD2\uFF0C\u5FC5\u987B\u6309\u6469\u4E0D\u8F8D\u3002\u82E5\u5929\u6C14\u775B\u6717\uFF0C\u53EF\u966A\u592B\u4EBA\u51FA\u5916\u6563\u5FC3\uFF0C\u501F\u666F\u6021\u60C5\u2026\u2026\u5343\u4E07\u4E0D\u53EF\u518D\u53D7\u60CA\u5413\uFF0C\u592B\u4EBA\u867D\u7136\u82B3\u534E\u6B63\u76DB\uFF0C\u4E00\u65E6\u52A8\u4E86\u80CE\u6C14\uFF0C\u540E\u679C\u96BE\u8A00\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u7D6E\u7D6E\u53E8\u53E8\u8BF4\u7740\uFF0C\u5C06\u5404\u79CD\u836F\u7269\u7EC6\u7EC6\u5305\u597D\uFF0C\u9012\u5230\u7D2B\u73AB\u624B\u4E2D\u3002</p><p>\u7D2B\u73AB\u628A\u4ED6\u7684\u8BDD\u4E00\u4E00\u8BB0\u5728\u5FC3\u5E95\uFF0C\u62AC\u624B\u63A5\u8FC7\u836F\u5305\uFF0C\u7A81\u7136\u5C48\u819D\u8DEA\u4E0B\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5C0F\u5973\u5B50\u5E74\u5E7C\u65E0\u77E5\uFF0C\u4EE5\u5F80\u591A\u6709\u5F97\u7F6A\uFF0C\u6C42\u53F6\u62A4\u6CD5\u5BBD\u6055\u3002\u300D\u8BF4\u7740\u91CD\u91CD\u78D5\u4E0B\u5934\u53BB\u3002</p><p>\u53F6\u884C\u5357\u624B\u5FD9\u811A\u4E71\u5730\u6276\u8D77\u7D2B\u73AB\uFF0C\u300C\u5C11\u592B\u4EBA\u8A00\u91CD\u4E86\uFF0C\u5FEB\u8BF7\u8D77\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u7262\u7262\u8DEA\u5728\u5730\u4E0A\uFF0C\u4EF0\u8D77\u5A07\u7F8E\u7EDD\u4F26\u7684\u82B1\u9765\uFF0C\u542B\u6CEA\u9053\uFF1A\u300C\u53F6\u62A4\u6CD5\u5BF9\u6211\u7684\u7231\u62A4\uFF0C\u5C0F\u5973\u5B50\u70B9\u70B9\u6EF4\u6EF4\u90FD\u8BB0\u5728\u5FC3\u91CC\u3002\u6B64\u53BB\u9F99\u57CE\uFF0C\u4E00\u522B\u6570\u6708\uFF0C\u6709\u51E0\u4EF6\u4E8B\u8FD8\u6C42\u62A4\u6CD5\u8D39\u5FC3\u3002\u300D</p><p>\u300C\u597D\u8BF4\u597D\u8BF4\uFF0C\u6211\u7B54\u5E94\u6211\u7B54\u5E94\uFF0C\u522B\u54ED\uFF0C\u5FEB\u8D77\u6765\u5427\u3002\u300D\u53F6\u884C\u5357\u5475\u54C4\u7740\u8BF4\u9053\u3002</p><p>\u300C\u4E00\u4E2A\u662F\u6211\u5AC2\u5AC2\uFF0C\u5979\u53CC\u76EE\u5931\u660E\uFF0C\u53C8\u88AB\u9501\u5728\u6BBF\u5916\uFF0C\u98CE\u5439\u65E5\u6652\u2026\u2026\u6C42\u62A4\u6CD5\u6148\u60B2\u3002\u300D</p><p>\u300C\u55EF\u55EF\u55EF\uFF0C\u8FD9\u4E2A\uFF0C\u5BAB\u4E3B\u2026\u2026\u6211\u6765\u60F3\u529E\u6CD5\u3002\u300D</p><p>\u300C\u4E00\u4E2A\u662F\u6211\u5927\u5E08\u59D0\u3002\u5979\u795E\u667A\u5DF2\u5931\uFF0C\u624B\u81C2\u53C8\u6709\u6B8B\u75BE\uFF0C\u8FD8\u6C42\u62A4\u6CD5\u7167\u6599\u3002\u300D</p><p>\u300C\u53EF\u4EE5\u53EF\u4EE5\uFF0C\u6211\u6D3E\u4EBA\u7167\u770B\u3002\u300D</p><p>\u7D2B\u73AB\u58F0\u6CEA\u4FF1\u4E0B\uFF0C\u300C\u8FD8\u6709\u6211\u5E08\u7236\u2026\u2026\u5979\u56DB\u80A2\u4FF1\u5E9F\uFF0C\u53C8\u88AB\u7A7F\u9AA8\u52FE\u7B4B\u2026\u2026\u6C42\u62A4\u6CD5\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u8E0C\u8E87\u8D77\u6765\uFF0C\u6628\u665A\u8BCA\u6CBB\u592B\u4EBA\u4E4B\u540E\uFF0C\u5BAB\u4E3B\u66FE\u7279\u5730\u4EA4\u5F85\u8FC7\u96EA\u5CF0\u795E\u5C3C\u3002\u4E0D\u7BA1\u4F1A\u75AF\u4F1A\u50BB\uFF0C\u65E0\u8BBA\u5982\u4F55\u4F7F\u7528\u4EC0\u4E48\u624B\u6BB5\uFF0C\u90FD\u8981\u9996\u5148\u51FB\u788E\u5979\u7684\u81EA\u5C0A\uFF0C\u8BA9\u795E\u5C3C\u6C89\u6D78\u5728\u8089\u6B32\u4E2D\u65E0\u6CD5\u81EA\u62D4\uFF0C\u53D8\u6210\u4E00\u5934\u4E0D\u77E5\u7F9E\u803B\u7684\u6DEB\u517D\uFF1B\u5176\u6B21\u662F\u8981\u627E\u51FA\u529E\u6CD5\u6765\u6C72\u53D6\u5979\u7684\u529F\u529B\u3002\u5BAB\u4E3B\u8A00\u72B9\u5728\u8033\uFF0C\u4F46\u4E00\u770B\u5230\u5C11\u592B\u4EBA\u4E5E\u601C\u7684\u773C\u795E\uFF0C\u53F6\u884C\u5357\u5FC3\u4E00\u4E0B\u5B50\u5C31\u8F6F\u4E86\u3002</p><p>\u7D2B\u73AB\u54FD\u54BD\u9053\uFF1A\u300C\u73AB\u513F\u77E5\u9053\u5BAB\u4E3B\u547D\u4EE4\u4E0D\u53EF\u8FDD\u80CC\uFF0C\u53EA\u6C42\u53F6\u4F2F\u4F2F\u5782\u601C\u2026\u2026\u4FDD\u4F4F\u5979\u4EEC\u7684\u6027\u547D\u2026\u2026\u300D</p><p>\u4FDD\u4F4F\u6027\u547D\u5E76\u975E\u96BE\u4E8B\uFF0C\u53F6\u884C\u5357\u4F4E\u53F9\u4E00\u58F0\uFF0C\u6400\u8D77\u54ED\u5F97\u68A8\u82B1\u5E26\u96E8\u7684\u5C11\u5973\uFF0C\u300C\u8BF7\u5C11\u592B\u4EBA\u653E\u5FC3\uFF0C\u5728\u4E0B\u5C3D\u529B\u800C\u4E3A\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u3001\u6155\u5BB9\u7D2B\u73AB\u3001\u767D\u6C0F\u59D0\u59B9\u3001\u7EAA\u7709\u59A9\uFF0C\u4E00\u4F17\u82B1\u679D\u62DB\u5C55\u7684\u5973\u5B50\u83BA\u83BA\u71D5\u71D5\u4E0A\u4E86\u5927\u8F66\u3002\u6C90\u58F0\u4F20\u5FC3\u4E0B\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u4F46\u60F3\u5230\u81EA\u5DF1\u5C11\u5E74\u65F6\u4E5F\u662F\u4E00\u822C\uFF0C\u4ED6\u53EA\u662F\u82E6\u7B11\u4E00\u58F0\uFF0C\u62F1\u624B\u82CD\u58F0\u9053\uFF1A\u300C\u795D\u5BAB\u4E3B\u6B64\u53BB\u65D7\u5F00\u5F97\u80DC\u3002\u300D</p><p>\u8EAB\u540E\u7684\u5C60\u6000\u6C89\u7B49\u5E2E\u4F17\u9F50\u58F0\u53EB\u9053\uFF1A\u300C\u795D\u5BAB\u4E3B\u65D7\u5F00\u5F97\u80DC\uFF0C\u6211\u661F\u6708\u6E56\u5A01\u9707\u5929\u4E0B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6717\u7136\u4E00\u7B11\uFF0C\u8E0C\u8E87\u6EE1\u5FD7\u5730\u6602\u9996\u5411\u5929\u3002</p><p>\u8FDC\u5904\u4E00\u53EA\u77EB\u5065\u7684\u96C4\u9E70\u51B2\u5929\u800C\u8D77\uFF0C\u98DE\u51FA\u7FA4\u5CF0\u5408\u62B1\u7684\u5C71\u8C37\uFF0C\u5C06\u65E0\u8FB9\u7684\u5C71\u6CB3\u7B3C\u7F69\u5728\u81EA\u5DF1\u7684\u5DE8\u7FC5\u4E4B\u4E0B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u4ECE\u7EC8\u5357\u5317\u9E93\u4E0B\u5C71\uFF0C\u6CBF\u6E2D\u6C34\u5411\u4E1C\uFF0C\u7ECF\u8FC7\u6F7C\u5173\u5929\u9669\uFF0C\u4E94\u65E5\u540E\u4FBF\u53EF\u5230\u8FBE\u6D1B\u9633\u3002\u300D</p><p>\u91D1\u5F00\u7532\u626C\u97AD\u6307\u5411\u8FDC\u65B9\uFF0C\u300C\u7136\u540E\u4ECE\u6D1B\u9633\u4E00\u8DEF\u5317\u4E0A\uFF0C\u7ECF\u957F\u5E73\u3001\u4E0A\u515A\u3001\u8944\u56FD\u3001\u8D75\u90E1\u3001\u4E0A\u8C37\uFF0C\u5230\u6DBF\u90E1\u4E4B\u540E\uFF0C\u518D\u671D\u4E1C\u5317\u7ECF\u6E14\u9633\u3001\u767D\u72FC\uFF0C\u5373\u53EF\u5230\u8FBE\u9F99\u57CE\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5982\u6B64\u542C\u6765\u9F99\u57CE\u50CF\u662F\u8FDC\u5728\u5929\u8FB9\uFF0C\u82E6\u5BD2\u4E0D\u6BDB\u4E4B\u5730\u3002\u300D</p><p>\u91D1\u5F00\u7532\u7B11\u9053\uFF1A\u300C\u4E8C\u5341\u5E74\u524D\u5C5E\u4E0B\u66FE\u53BB\u8FC7\u9F99\u57CE\u3002\u5176\u5730\u8FDC\u975E\u82E6\u5BD2\uFF0C\u800C\u4E14\u662F\u4E09\u71D5\u6545\u90FD\uFF0C\u751A\u4E3A\u7E41\u534E\u3002\u56DB\u5468\u6C83\u91CE\u5343\u91CC\uFF0C\u6C11\u98CE\u5F3A\u608D\uFF0C\u8FDC\u975E\u4E2D\u539F\u53EF\u6BD4\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u95ED\u4E0A\u773C\u775B\uFF0C\u60A0\u7136\u795E\u5F80\uFF0C\u300C\u6211\u6155\u5BB9\u6C0F\u5D1B\u8D77\u9F99\u57CE\u4E00\u9685\uFF0C\u767E\u4F59\u5E74\u95F4\u4FBF\u79F0\u96C4\u5929\u4E0B\uFF0C\u56DB\u5EFA\u71D5\u56FD\u3002\u7956\u5B97\u7687\u56FE\u9738\u4E1A\uFF0C\u96C4\u97EC\u4F1F\u7565\uFF0C\u4EE4\u540E\u4EBA\u8FFD\u6155\u2026\u2026\u300D\u4ED6\u970D\u7136\u7741\u5F00\u53CC\u76EE\uFF0C\u773C\u4E2D\u71C3\u70E7\u7740\u65E0\u7A77\u7684\u96C4\u5FC3\u58EE\u5FD7\uFF0C\u300C\u8EAB\u4E3A\u6155\u5BB9\u6C0F\u5B50\u5B59\uFF0C\u6211\u6155\u5BB9\u9F99\u5FC5\u8981\u91CD\u5EFA\u57FA\u4E1A\uFF0C\u590D\u5174\u5927\u71D5\uFF0C\u4E0D\u8D1F\u7956\u5B97\u8840\u8109\uFF01\u300D</p><p>\u6155\u5BB9\u6C0F\u82F1\u6770\u8F88\u51FA\uFF0C\u767E\u5E74\u95F4\u5C06\u5929\u4E0B\u6405\u5F97\u5929\u7FFB\u5730\u8986\uFF0C\u91D1\u5F00\u7532\u8EAB\u4E3A\u5308\u5974\u65CF\u88D4\u4E5F\u662F\u5FC3\u4E0B\u4F69\u670D\u3002</p><p>\u7075\u7389\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u4ED6\u5BF9\u5973\u4EBA\u7684\u5174\u8DA3\u8FDC\u6BD4\u4E89\u593A\u5929\u4E0B\u8981\u5927\uFF0C\u4F46\u5BAB\u4E3B\u6709\u6B64\u96C4\u5FC3\uFF0C\u4ED6\u4E5F\u613F\u5C3D\u529B\u8F85\u4F50\uFF0C\u4E8E\u662F\u7EB5\u9A6C\u4E0A\u524D\uFF0C\u5F00\u53E3\u9053\uFF1A\u300C\u5982\u4ECA\u5929\u4E0B\u5206\u5D29\uFF0C\u5317\u65B9\u5468\u3001\u79E6\u3001\u51C9\u3001\u590F\u56DB\u56FD\u5272\u636E\uFF0C\u5B8B\u56FD\u5360\u636E\u6C5F\u4E1C\uFF0C\u90D1\u56FD\u72EC\u5B88\u5DF4\u8700\u3002\u795E\u6559\u4F4D\u4E8E\u5468\u3001\u79E6\u3001\u5B8B\u3001\u90D1\u56DB\u56FD\u4E4B\u95F4\uFF0C\u4E0D\u77E5\u5BAB\u4E3B\u4ECE\u4F55\u5904\u4E0B\u624B\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u4EE5\u957F\u8001\u4E4B\u89C1\u5462\uFF1F\u300D</p><p>\u7075\u7389\u6C89\u541F\u9053\uFF1A\u300C\u5B8B\u56FD\u79C9\u627F\u534E\u590F\u8863\u51A0\uFF0C\u867D\u7136\u5175\u5F31\uFF0C\u4F46\u96BE\u4E3A\u5BAB\u4E3B\u6240\u7528\uFF1B\u90D1\u56FD\u504F\u636E\u4E00\u9685\uFF0C\u56E0\u5730\u52BF\u6240\u9650\uFF0C\u7EB5\u7136\u53D6\u800C\u4EE3\u4E4B\uFF0C\u4E5F\u96BE\u6709\u4F5C\u4E3A\uFF1B\u5468\u56FD\u56FD\u52BF\u65B9\u76DB\uFF0C\u4E0E\u67D4\u7136\u8054\u59FB\u540E\u5DF2\u65E0\u540E\u987E\u4E4B\u5FE7\uFF0C\u5982\u4ECA\u6B63\u79E3\u5175\u7C9D\u9A6C\u610F\u56FE\u897F\u8FDB\uFF1B\u79E6\u56FD\u5317\u90BB\u67D4\u7136\u3001\u94C1\u5F17\u3001\u7A81\u53A5\u8BF8\u90E8\uFF0C\u5C61\u7ECF\u5175\u7978\u3002\u53BB\u5C81\u53C8\u906D\u5927\u65F1\uFF0C\u65E5\u524D\u4E0E\u5468\u56FD\u5728\u6F7C\u5173\u4E00\u6218\uFF0C\u867D\u7136\u82E6\u6218\u672A\u5931\uFF0C\u4F46\u56FD\u52BF\u5DF2\u7136\u52A8\u6C64\u3002\u5BAB\u4E3B\u82E5\u8D81\u673A\u8D77\u5175\uFF0C\u897F\u5165\u957F\u5B89\uFF0C\u5927\u4E8B\u53EF\u6210\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u7740\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4E0D\u3002\u6211\u8981\u5148\u53D6\u5468\u56FD\u3002\u300D</p><p>\u7075\u7389\u4E00\u756A\u5206\u6790\u5165\u60C5\u5165\u7406\uFF0C\u6CA1\u60F3\u5230\u5BAB\u4E3B\u5374\u9009\u62E9\u4E86\u6700\u96BE\u8D77\u4E8B\u7684\u5927\u5468\uFF0C\u4E0D\u7531\u6EE1\u8179\u7591\u95EE\u3002\u65C1\u8FB9\u7684\u91D1\u5F00\u7532\u5374\u662F\u5FC3\u4E0B\u4E86\u7136\uFF0C\u5F97\u77E5\u6155\u5BB9\u9F99\u8EAB\u4E16\u4E4B\u540E\uFF0C\u4ED6\u5C31\u77E5\u9053\u5BAB\u4E3B\u7EDD\u4E0D\u4F1A\u653E\u8FC7\u5468\u56FD\u3002</p><p>\u5468\u5E1D\u59DA\u5174\u672C\u662F\u71D5\u56FD\u91CD\u5C06\uFF0C\u5341\u516D\u5E74\u524D\u6B63\u662F\u4ED6\u7684\u7A81\u7136\u53CD\u53DB\u624D\u4F7F\u71D5\u56FD\u6BC1\u4E8E\u4E00\u65E6\u3002\u9664\u6155\u5BB9\u9F99\u88AB\u661F\u6708\u6E56\u63B3\u8D70\uFF0C\u7687\u5983\u8427\u4F5B\u5974\u7531\u8FD1\u536B\u6551\u51FA\u4EE5\u5916\uFF0C\u5176\u4ED6\u6155\u5BB9\u6C0F\u7687\u65CF\u5C3D\u88AB\u5C60\u622E\uFF0C\u5982\u6B64\u8840\u6D77\u6DF1\u4EC7\uFF0C\u602A\u4E0D\u5F97\u5BAB\u4E3B\u4F1A\u5FF5\u5FF5\u4E0D\u5FD8\u3002\u53EA\u662F\u6210\u5927\u4E8B\u8005\u600E\u53EF\u4EE5\u79C1\u4EC7\u4E3A\u5148\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u770B\u51FA\u4E24\u4EBA\u7684\u7591\u8651\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u7075\u7389\u957F\u8001\u5BF9\u5404\u56FD\u60C5\u5F62\u4E86\u5982\u6307\u638C\u3002\u82E5\u8981\u5728\u79E6\u56FD\u8D77\u4E8B\uFF0C\u81EA\u7136\u8F7B\u800C\u6613\u4E3E\u3002\u4F46\u6211\u82E5\u5360\u636E\u957F\u5B89\uFF0C\u79E6\u56FD\u5982\u4ECA\u7684\u56F0\u5883\uFF0C\u4E5F\u5C31\u662F\u5C06\u6765\u5927\u71D5\u7684\u56F0\u5883\uFF1A\u4E00\u662F\u5317\u65B9\u8BF8\u90E8\u7684\u5A01\u80C1\uFF0C\u4E8C\u662F\u5468\u56FD\u7684\u5A01\u80C1\uFF0C\u6700\u91CD\u8981\u662F\u5F53\u5730\u7684\u9965\u8352\u3002\u63A5\u4E0B\u90A3\u4E48\u4E2A\u70C2\u644A\u5B50\uFF0C\u767E\u5BB3\u800C\u65E0\u4E00\u5229\u3002\u300D</p><p>\u7075\u7389\u771F\u4EBA\u4E0E\u91D1\u5F00\u7532\u5BF9\u89C6\u4E00\u773C\uFF0C\u5747\u89C9\u5BAB\u4E3B\u6240\u8A00\u6709\u7406\u3002</p><p>\u6155\u5BB9\u9F99\u82E6\u7B11\u9053\uFF1A\u300C\u6211\u661F\u6708\u6E56\u867D\u7136\u79F0\u96C4\u6B66\u6797\uFF0C\u4F46\u82E5\u8981\u4E89\u593A\u5929\u4E0B\uFF0C\u53EA\u80FD\u7B97\u662F\u4E4C\u5408\u4E4B\u4F17\u3002\u6CA1\u6709\u4E00\u5E74\u65F6\u95F4\u8BAD\u7EC3\u90E8\u4F0D\uFF0C\u5355\u9760\u5404\u5802\u5E2E\u4F17\u4E0E\u79E6\u519B\u4F5C\u6218\u2026\u2026\u300D</p><p>\u91D1\u5F00\u7532\u795E\u60C5\u6E10\u6E10\u51DD\u91CD\uFF0C\u6C5F\u6E56\u4EBA\u58EB\u7684\u5F7C\u6B64\u4E89\u6597\u4E0E\u884C\u519B\u4F5C\u6218\u53EF\u662F\u5927\u76F8\u8FF3\u5EAD\u3002\u73B0\u5728\u8D77\u4E8B\uFF0C\u786E\u5B9E\u64CD\u4E4B\u8FC7\u6025\u3002</p><p>\u300C\u5468\u56FD\u770B\u8D77\u6765\u5175\u5F3A\u519B\u76DB\uFF0C\u4E5F\u5E76\u975E\u6CA1\u6709\u53EF\u8D81\u4E4B\u673A\u3002\u59DA\u5174\u672C\u662F\u6C49\u4EBA\uFF0C\u867D\u7136\u5916\u8054\u67D4\u7136\uFF0C\u4F46\u5BF9\u5883\u5185\u7684\u5F02\u65CF\u5374\u5927\u52A0\u6392\u65A5\u3002\u5982\u4ECA\u5468\u56FD\u5883\u5185\u6C49\u4EBA\u4E0D\u8DB3\u534A\u6570\uFF0C\u5404\u5730\u53C8\u5821\u58C1\u6797\u7ACB\uFF0C\u7ED3\u5BE8\u81EA\u5B88\u2014\u2014\u4E0D\u8FC7\u662F\u5EFA\u5728\u6D41\u6C99\u4E0A\u7684\u5F3A\u56FD\u7F62\u4E86\u3002\u300D</p><p>\u7075\u7389\u957F\u5401\u4E86\u4E00\u53E3\u6C14\uFF0C\u70B9\u5934\u9053\uFF1A\u300C\u5BAB\u4E3B\u89C1\u89E3\u6781\u662F\uFF0C\u5C5E\u4E0B\u96BE\u53CA\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u770B\u7740\u7FA4\u5CF0\u4E4B\u4E0A\u7684\u6D6E\u4E91\uFF0C\u58F0\u97F3\u8F7B\u5F97\u51E0\u4E4E\u542C\u4E0D\u6E05\u695A\uFF0C\u300C\u8FD9\u90FD\u662F\u6731\u90AA\u62A4\u6CD5\u6559\u6211\u7684\u3002\u300D</p><p>\u300C\u54E5\u54E5\u2026\u2026\u300D\u7D2B\u73AB\u4ECE\u8F66\u7A97\u63A2\u51FA\u5934\u6765\uFF0C\u7126\u6025\u5730\u53EB\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u8FDE\u5FD9\u62E8\u8F6C\u9A6C\u5934\uFF0C\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u5A18\u2026\u2026\u300D\u7D2B\u73AB\u8BDD\u97F3\u672A\u843D\uFF0C\u6155\u5BB9\u9F99\u5DF2\u7ECF\u79BB\u978D\u800C\u8D77\uFF0C\u98DE\u8EAB\u63A0\u5165\u5927\u8F66\u3002</p><p>\u7D2B\u73AB\u62E5\u7740\u6BCD\u4EB2\uFF0C\u60F6\u6025\u5730\u8BF4\uFF1A\u300C\u5A18\u53C8\u75C5\u4E86\uFF01\u6211\u90FD\u8BF4\u4E0D\u8BA9\u5A18\u51FA\u6765\uFF01\u300D\u5979\u6025\u5F97\u773C\u6CEA\u6C6A\u6C6A\uFF0C\u4E00\u4E2A\u52B2\u513F\u5730\u57CB\u6028\u6155\u5BB9\u9F99\u3002</p><p>\u8427\u4F5B\u5974\u8138\u8272\u82CD\u767D\uFF0C\u504E\u5728\u5973\u513F\u81C2\u4E2D\uFF0C\u8270\u96BE\u5730\u5598\u606F\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u8FDE\u5FD9\u63A5\u8FC7\u6BCD\u4EB2\uFF0C\u4E00\u8FB9\u5728\u5979\u80CC\u4E0A\u8F7B\u8F7B\u62CD\u7740\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u5A18\uFF0C\u600E\u4E48\u4E0D\u8212\u670D\u4E86\uFF1F\u300D</p><p>\u6B63\u8BF4\u95F4\uFF0C\u8427\u4F5B\u5974\u7EC6\u7709\u62E7\u6210\u4E00\u56E2\uFF0C\u5589\u5934\u5455\u5455\u4F5C\u54CD\uFF0C\u5374\u6CA1\u6709\u5410\u51FA\u4EC0\u4E48\u4E1C\u897F\u3002</p><p>\u7D2B\u73AB\u4E00\u6380\u8F66\u5EC9\uFF0C\u4FBF\u6B32\u4E0B\u8F66\u3002</p><p>\u300C\u4F60\u8981\u5E72\u4EC0\u4E48\uFF1F\u300D\u6155\u5BB9\u9F99\u95EE\u9053\u3002</p><p>\u300C\u53BB\u627E\u53F6\u62A4\u6CD5\u3002\u5A18\u521A\u51FA\u95E8\u5C31\u75C5\u6210\u8FD9\u6837\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u771F\u662F\u4E2A\u50BB\u4E2B\u5934\uFF01\u5A18\u6000\u7740\u5B69\u5B50\uFF0C\u8FD9\u6837\u5455\u5410\u662F\u6B63\u5E38\u7684\u3002\u300D</p><p>\u7D2B\u73AB\u534A\u4FE1\u534A\u7591\uFF0C\u300C\u4F60\u53C8\u6CA1\u6000\u8FC7\u5B69\u5B50\uFF0C\u600E\u4E48\u4F1A\u77E5\u9053\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u638F\u51FA\u4E1D\u5DFE\u64E6\u7740\u6BCD\u4EB2\u7684\u7EA2\u5507\uFF0C\u300C\u5A18\u6709\u4F60\u7684\u65F6\u5019\uFF0C\u6211\u5DF2\u7ECF\u4E94\u5C81\u4E86\u3002\u90A3\u65F6\u5019\u5A18\u5410\u5F97\u5F88\u5389\u5BB3\u2026\u2026\u300D</p><p>\u4ED6\u50CF\u62B1\u5B69\u5B50\u4E00\u822C\u628A\u8427\u4F5B\u5974\u5A07\u5C0F\u7684\u8EAB\u4F53\u62B1\u5728\u6000\u4E2D\uFF0C\u7AEF\u8BE6\u7740\u6BCD\u4EB2\u7CBE\u81F4\u7684\u7389\u5BB9\uFF0C\u300C\u6CA1\u6709\u4EBA\u4F1A\u50CF\u513F\u5B50\u8FD9\u6837\u7231\u4F60\uFF0C\u6240\u4EE5\u4F60\u4E5F\u8981\u540C\u6837\u7231\u6211\u3002\u5373\u662F\u6211\u7684\u6BCD\u4EB2\uFF0C\u4E5F\u662F\u6211\u7684\u59BB\u5B50\u3002\u4ECE\u4ECA\u5F80\u540E\uFF0C\u4F60\u4E0D\u80FD\u518D\u60F3\u522B\u7684\u7537\u4EBA\u2014\u2014\u8FDE\u6155\u5BB9\u7941\u4E5F\u4E0D\u8BB8\uFF01\u300D</p><p>\u7D2B\u73AB\u4ECE\u4ED6\u53D8\u5E7B\u7684\u773C\u795E\u4E2D\u770B\u51FA\u7AEF\u502A\uFF0C\u4E0D\u7531\u60CA\u5446\u4E86\u3002\u4ED6\u7ADF\u7136\u4F1A\u6709\u8FD9\u4E48\u75AF\u72C2\u7684\u60F3\u6CD5\u2026\u2026</p>',63);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
