import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_72" tabindex="-1"><a class="header-anchor" href="#_72" aria-hidden="true">#</a> 72</h1><p>\u6F7C\u5173\u7684\u6218\u4E8B\u5DF2\u7ECF\u7ED3\u675F\uFF0C\u4F46\u6218\u573A\u4E2D\u4ECD\u662F\u4F0F\u5C38\u5904\u5904\uFF0C\u8840\u6D41\u6210\u6CB3\u3002\u884C\u4EBA\u5BF9\u8FD9\u91CC\u907F\u4E4B\u552F\u6050\u4E0D\u53CA\uFF0C\u6155\u5BB9\u9F99\u5374\u5E26\u9886\u661F\u6708\u6E56\u4F17\u4EBA\u5F84\u76F4\u4ECE\u6218\u573A\u7A7F\u8FC7\u3002</p><p>\u7D2B\u73AB\u628A\u8F66\u7A97\u8F66\u95E8\u5168\u90E8\u5835\u4F4F\uFF0C\u70B9\u71C3\u85B0\u9999\uFF0C\u53C8\u7528\u4E00\u5757\u6D78\u8FC7\u9999\u6599\u7684\u4E1D\u5DFE\u906E\u5728\u6BCD\u4EB2\u8138\u4E0A\uFF0C\u53EA\u9732\u4E24\u773C\u5728\u5916\uFF0C\u53EF\u8F66\u53A2\u4E2D\u5F25\u6F2B\u7684\u8840\u8165\u6C14\u4ECD\u6325\u4E4B\u4E0D\u53BB\u3002\u8427\u4F5B\u5974\u65F6\u660F\u65F6\u9192\uFF0C\u597D\u5728\u6709\u7D2B\u73AB\u65E0\u5FAE\u4E0D\u81F4\u7684\u7167\u6599\uFF0C\u795E\u667A\u4E00\u5929\u5929\u597D\u8F6C\u3002</p><p>\u6155\u5BB9\u9F99\u7EB5\u9A6C\u79BB\u5F00\u5927\u961F\uFF0C\u9A70\u4E0A\u5C71\u4E18\uFF0C\u56DB\u4E0B\u6253\u91CF\u8FD9\u5730\u72F1\u822C\u7684\u6218\u573A\u3002</p><p>\u6F7C\u5173\u53F7\u79F0\u300C\u4E09\u79E6\u9501\u94A5\u300D\u3001\u300C\u56DB\u9547\u54BD\u5589\u300D\uFF0C\u5B83\u5317\u4F9D\u9EC4\u6CB3\uFF0C\u5357\u63A5\u79E6\u5CAD\uFF0C\u4E1C\u8FDE\u51FD\u8C37\uFF0C\u897F\u62F1\u534E\u5CB3\uFF0C\u81EA\u53E4\u4FBF\u662F\u53EF\u653B\u53EF\u5B88\u53EF\u6218\u7684\u4E09\u6218\u4E4B\u5730\uFF0C\u83BD\u83BD\u9EC4\u571F\uFF0C\u4E0D\u77E5\u63A9\u57CB\u4E86\u591A\u5C11\u82F1\u96C4\u3002</p><p>\u300C\u6B64\u5730\u5C71\u9AD8\u8C37\u6DF1\uFF0C\u6C9F\u5CEA\u7EB5\u6A2A\u3002\u300D\u91D1\u5F00\u7532\u6307\u7740\u4E18\u4E0B\u4E00\u6761\u5357\u5317\u8D70\u5411\u7684\u6DF1\u58D5\uFF0C\u300C\u8FD9\u4E9B\u6C9F\u5CEA\u662F\u6CB3\u6C34\u51B2\u5237\u800C\u6210\uFF0C\u957F\u56DB\u5341\u4F59\u91CC\uFF0C\u6DF1\u8FBE\u4E03\u5341\u4E08\u3002\u82E5\u60F3\u5175\u4E34\u57CE\u4E0B\uFF0C\u8981\u7ECF\u8FC7\u4E03\u6761\u50CF\u8FD9\u6837\u7684\u6C9F\u5CEA\u3002\u300D\u4ED6\u6307\u70B9\u5730\u52BF\uFF0C\u4E0D\u7531\u8C6A\u60C5\u5927\u53D1\uFF0C\u300C\u5982\u6B64\u96C4\u5173\u5929\u9669\uFF0C\u5C5E\u4E0B\u53EA\u9700\u4E00\u5343\u7CBE\u5175\uFF0C\u4EFB\u4ED6\u767E\u4E07\u96C4\u5E08\u4E5F\u53EA\u80FD\u5F98\u5F8A\u5173\u5916\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6E38\u76EE\u56DB\u987E\uFF0C\u6307\u7740\u6218\u573A\u4E2D\u7684\u4F0F\u5C38\u9053\uFF1A\u300C\u5468\u519B\u4E09\u65E5\u524D\u4FBF\u5DF2\u9000\u5175\uFF0C\u4E3A\u4F55\u79E6\u519B\u8FD8\u672A\u6536\u62FE\u6218\u573A\uFF1F\u300D</p><p>\u300C\u79E6\u519B\u6B64\u6218\u5FC5\u662F\u60E8\u80DC\u3002\u300D\u91D1\u5F00\u7532\u864E\u76EE\u7F13\u7F13\u626B\u8FC7\u6218\u573A\uFF0C\u300C\u5468\u5F3A\u79E6\u5F31\uFF0C\u95ED\u5173\u81EA\u5B88\u4E43\u662F\u4E0A\u8BA1\u3002\u4F46\u79E6\u519B\u7ADF\u7136\u820D\u5F03\u5929\u9669\uFF0C\u4E0E\u52B2\u654C\u8840\u6218\u5173\u5916\u2026\u2026\u300D\u4ED6\u6447\u4E86\u6447\u5934\uFF0C\u89C9\u5F97\u96BE\u4EE5\u7406\u89E3\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u5939\u9A6C\u8179\uFF0C\u7BAD\u77E2\u822C\u671D\u6C9F\u5CEA\u51B2\u53BB\u3002\u773C\u770B\u5C31\u8981\u51B2\u4E0B\u60AC\u5D16\uFF0C\u6155\u5BB9\u9F99\u4E00\u52D2\u7F30\u7EF3\uFF0C\u5750\u9A91\u4EBA\u7ACB\u800C\u8D77\uFF0C\u63A5\u7740\u524D\u8E44\u60AC\u7A7A\u4E00\u62E7\uFF0C\u7D27\u6328\u7740\u5CED\u58C1\u8FB9\u7F18\u505C\u4E86\u4E0B\u6765\u3002</p><p>\u4ECE\u978D\u4E0A\u4FA7\u8EAB\u671D\u5CEA\u5E95\u770B\u53BB\uFF0C\u53EA\u89C1\u5CEA\u5185\u4EBA\u9A6C\u4EA4\u76F8\u6795\u85C9\uFF0C\u8840\u8089\u6A2A\u98DE\uFF0C\u60E8\u70C8\u65E0\u6BD4\u3002</p><p>\u8EAB\u540E\u8E44\u58F0\u5927\u9707\uFF0C\u6155\u5BB9\u9F99\u5934\u4E5F\u4E0D\u56DE\u5730\u8BF4\uFF1A\u300C\u6B64\u5730\u9A91\u5175\u96BE\u4EE5\u9A70\u9A8B\uFF0C\u4E3A\u4F55\u4F1A\u6709\u5982\u6B64\u4E4B\u591A\u7684\u8F7B\u9A91\u846C\u8EAB\u5CEA\u5E95\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u5BA1\u89C6\u7247\u523B\uFF0C\u72EC\u76EE\u7CBE\u5149\u4E00\u95EA\uFF0C\u65AD\u8A00\u9053\uFF1A\u300C\u5FC5\u662F\u79E6\u519B\u4E4F\u7CAE\uFF0C\u56E0\u6B64\u6D3E\u9063\u8F7B\u9A91\uFF0C\u501F\u6C9F\u5CEA\u7ED5\u5F80\u5468\u5E08\u80CC\u540E\u52AB\u7CAE\u3002\u7ED3\u679C\u5728\u6B64\u4E0E\u5468\u519B\u906D\u9047\uFF0C\u8840\u6218\u8986\u6CA1\u3002\u5BAB\u4E3B\u8BF7\u770B\uFF0C\u79E6\u519B\u9A6C\u5339\u90FD\u4EE5\u5E03\u5E1B\u5305\u88F9\u9A6C\u8E44\uFF0C\u82E5\u8BF4\u662F\u5077\u88AD\u5468\u519B\uFF0C\u519B\u58EB\u53C8\u672A\u643A\u5E26\u91CD\u578B\u5175\u5668\u3002\u56E0\u6B64\u5B9A\u662F\u52AB\u7CAE\u7684\u8F7B\u9A91\u3002\u300D</p><p>\u4ED6\u62AC\u8D77\u5934\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u6F7C\u5173\u5B88\u519B\u5E76\u672A\u88AB\u5468\u519B\u5305\u56F4\uFF0C\u4FBF\u7CAE\u8349\u4E0D\u7EE7\u2014\u2014\u79E6\u56FD\u56FD\u52BF\u4E4B\u5F31\u53EF\u89C1\u4E00\u6591\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4FEF\u8EAB\u62E3\u8D77\u4E00\u679D\u65AD\u7BAD\uFF0C\u6253\u91CF\u7740\u7BAD\u7C07\u7684\u5236\u5DE5\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u79E6\u519B\u5982\u6B64\u75B2\u655D\uFF0C\u8FD8\u80FD\u903C\u9000\u864E\u72FC\u4E4B\u5E08\u2014\u2014\u300D\u4ED6\u4E22\u6389\u65AD\u7BAD\uFF0C\u8F6C\u9996\u56DE\u671B\u8FDC\u5904\u7684\u5173\u9698\uFF0C\u300C\u6F7C\u5173\u679C\u7136\u662F\u96C4\u5173\u5929\u9669\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u66AE\u8272\u56DB\u5408\uFF0C\u5728\u5D0E\u5C96\u7684\u6218\u573A\u4E2D\u5DDD\u884C\u6570\u5341\u91CC\u4E4B\u540E\uFF0C\u661F\u6708\u6E56\u4E00\u884C\u4E09\u5341\u4F59\u4EBA\u5728\u9EC4\u660F\u65F6\u5206\u8D76\u5230\u98CE\u9675\u6E21\u3002</p><p>\u8427\u4F5B\u5974\u4E00\u8DEF\u4E0A\u5410\u5F97\u5929\u660F\u5730\u6697\uFF0C\u8EBA\u5728\u5BA2\u623F\u7684\u7095\u4E0A\u624D\u7565\u597D\u4E86\u4E00\u4E9B\u3002</p><p>\u300C\u8FC7\u6765\u3002\u300D</p><p>\u6B63\u5728\u7ED9\u6BCD\u4EB2\u64E6\u6D17\u8EAB\u4F53\u7684\u7D2B\u73AB\u65E0\u5948\u5730\u5C0F\u58F0\u9053\uFF1A\u300C\u4F60\u7B49\u4E00\u4F1A\u513F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6BEB\u4E0D\u7406\u4F1A\u65C1\u8FB9\u7684\u767D\u6C0F\u59D0\u59B9\uFF0C\u8FF3\u76F4\u8D70\u5230\u7D2B\u73AB\u8EAB\u540E\uFF0C\u64A9\u8D77\u88D9\u88FE\u3002</p><p>\u65F6\u503C\u76DB\u590F\uFF0C\u7D2B\u73AB\u53EA\u7A7F\u4E86\u4E00\u6761\u8F7B\u7EB1\u647A\u88D9\u3002\u6155\u5BB9\u9F99\u89E3\u5F00\u8863\u5E26\uFF0C\u624B\u6307\u4E00\u677E\uFF0C\u4EB5\u88E4\u4FBF\u6ED1\u843D\u5728\u5730\uFF0C\u9732\u51FA\u767D\u751F\u751F\u7684\u96EA\u81C0\u3002</p><p>\u7D2B\u73AB\u6068\u6068\u4E00\u7529\u6BDB\u5DFE\uFF0C\u6321\u4F4F\u90A3\u53EA\u4F38\u8FDB\u80A1\u95F4\u7684\u5927\u624B\uFF0C\u538B\u4F4E\u58F0\u97F3\u9053\uFF1A\u300C\u5230\u9694\u58C1\u53BB\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5728\u59B9\u59B9\u96EA\u767D\u7684\u9888\u540E\u4E00\u543B\uFF0C\u7B11\u9053\uFF1A\u300C\u5728\u8FD9\u91CC\u53C8\u6709\u4F55\u59A8\uFF1F\u5A18\u770B\u5230\u6211\u4EEC\u5144\u59B9\u592B\u59BB\u6069\u7231\uFF0C\u9AD8\u5174\u90FD\u6765\u4E0D\u53CA\u5462\u3002\u300D\u8BF4\u7740\u8D34\u5728\u7D2B\u73AB\u80CC\u4E0A\uFF0C\u628A\u5979\u538B\u5F97\u5F2F\u4E0B\u8170\u6765\u3002</p><p>\u7D2B\u73AB\u4E00\u624B\u65E0\u6CD5\u652F\u6491\uFF0C\u5979\u6015\u538B\u4F4F\u6BCD\u4EB2\uFF0C\u53EA\u597D\u677E\u5F00\u624B\uFF0C\u4E24\u81C2\u6491\u4F4F\u7095\u6CBF\u3002\u81C0\u540E\u8170\u8179\u4E00\u633A\uFF0C\u8089\u68D2\u4ECE\u4E24\u817F\u95F4\u72E0\u72E0\u6345\u5165\u3002\u7D2B\u73AB\u88AB\u4ED6\u51F6\u731B\u7684\u52A8\u4F5C\u5413\u5F97\u6D51\u8EAB\u4E00\u98A4\uFF0C\u6025\u5FD9\u54AC\u7D27\u7259\u5173\uFF0C\u62B5\u6297\u5373\u5C06\u6765\u5230\u7684\u5267\u75DB\u3002</p><p>\u7136\u800C\u4E0B\u4F53\u5E76\u65E0\u5F02\u72B6\uFF0C\u575A\u786C\u7684\u8089\u68D2\u4E00\u8DF3\u4E00\u8DF3\uFF0C\u8C03\u76AE\u5730\u6572\u6253\u7740\u5C0F\u8179\u3002\u7D2B\u73AB\u8FD9\u624D\u77E5\u9053\u8089\u68D2\u5E76\u6CA1\u6709\u8FDB\u5165\u81EA\u5DF1\u4F53\u5185\uFF0C\u800C\u662F\u4ECE\u80A1\u95F4\u7A7F\u8FC7\uFF0C\u7AD6\u5728\u809A\u8179\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u59B9\u59B9\u5413\u5F97\u4FCF\u8138\u53D1\u767D\uFF0C\u4E0D\u7531\u54C8\u54C8\u7B11\u8D77\u6765\uFF0C\u4ED6\u62B1\u7740\u7D2B\u73AB\u7D27\u7D27\u4E00\u62E5\uFF0C\u8FD9\u624D\u9F13\u8D77\u9633\u5177\u6839\u90E8\u7684\u89E6\u624B\uFF0C\u4F38\u8FDB\u79D8\u5904\u6765\u56DE\u62E8\u5F04\u3002</p><p>\u6BCD\u5973\u4FE9\u4E00\u5367\u4E00\u7ACB\uFF0C\u4E24\u5F20\u65E0\u7455\u7684\u7389\u8138\u76F8\u8DDD\u4E0D\u8FC7\u5BF8\u8BB8\u3002\u7D2B\u73AB\u751F\u6015\u60CA\u9192\u6BCD\u4EB2\uFF0C\u7AED\u529B\u5C4F\u4F4F\u547C\u5438\uFF0C\u5FCD\u53D7\u7740\u6155\u5BB9\u9F99\u7684\u620F\u5F04\u3002</p><p>\u6311\u9017\u7247\u523B\u540E\uFF0C\u7D2B\u73AB\u79D8\u5904\u6E10\u6E10\u6E7F\u6DA6\uFF0C\u6155\u5BB9\u9F99\u4E24\u624B\u62C7\u6307\u4F38\u5165\u7F8A\u8102\u822C\u7684\u7389\u80A1\uFF0C\u63B0\u5F00\u81C0\u8089\uFF0C\u5C06\u5C11\u5973\u7684\u79D8\u5904\u7684\u66B4\u9732\u5728\u5916\u3002\u7136\u540E\u8089\u68D2\u4E00\u4E3E\uFF0C\u9876\u4F4F\u6F6E\u70ED\u7684\u8089\u7A74\uFF0C\u7F13\u7F13\u8FDB\u5165\u3002</p><p>\u6ED1\u817B\u7684\u5AE9\u8089\u5F39\u6027\u5341\u8DB3\uFF0C\u4EFF\u4F5B\u4E00\u5F20\u70ED\u60C5\u7684\u5C0F\u5634\uFF0C\u4E0D\u4F4F\u5438\u542E\u3002\u6155\u5BB9\u9F99\u8F7B\u62BD\u7F13\u9001\uFF0C\u523B\u610F\u8981\u8BA9\u59B9\u59B9\u5728\u6BCD\u4EB2\u9762\u524D\u9732\u51FA\u6DEB\u6001\u3002</p><p>\u7D2B\u73AB\u8EAB\u6750\u5A07\u5C0F\uFF0C\u4E0D\u5F97\u4E0D\u8E2E\u8D77\u811A\u5C16\uFF0C\u4E3E\u81C0\u8FCE\u5408\u8089\u68D2\u7684\u62BD\u9001\u3002\u5979\u5168\u8EAB\u7684\u529B\u6C14\u90FD\u96C6\u4E2D\u5728\u80A1\u95F4\uFF0C\u5BF9\u5DE8\u7269\u7684\u523A\u6FC0\u5206\u5916\u654F\u611F\uFF0C\u4E0D\u591A\u65F6\u4FBF\u7389\u4F53\u6CDB\u7EA2\uFF0C\u7231\u6DB2\u6A2A\u6D41\u3002</p><p>\u6E29\u6DA6\u7684\u8089\u7A74\u4F9D\u7136\u5982\u5C11\u5973\u822C\u7D27\u5BC6\u72ED\u7A84\uFF0C\u5927\u5982\u513F\u62F3\u7684\u9F9F\u5934\u786C\u751F\u751F\u6324\u5165\u4EC5\u6709\u6307\u5C16\u5927\u5C0F\u7684\u871C\u7A74\uFF0C\u7545\u7F8E\u96BE\u8A00\u3002\u6155\u5BB9\u9F99\u6027\u6B32\u5927\u53D1\uFF0C\u4E00\u8FB9\u62BD\u9001\uFF0C\u4E00\u8FB9\u89E3\u5F00\u7D2B\u73AB\u7684\u8863\u895F\uFF0C\u626F\u4E0B\u62B9\u80F8\uFF0C\u63E1\u7740\u7C89\u96D5\u7389\u7422\u7684\u4E00\u5BF9\u9165\u4E73\u8086\u610F\u628A\u73A9\u3002</p><p>\u7D2B\u73AB\u547C\u5438\u6E10\u6E10\u6025\u4FC3\uFF0C\u5979\u8E59\u989D\u98A6\u7709\uFF0C\u652F\u6491\u5F97\u8F9B\u82E6\u4E07\u5206\u3002\u767D\u6C0F\u59D0\u59B9\u89C1\u73AB\u7470\u4ED9\u5B50\u5982\u6B64\u7A98\u6001\uFF0C\u90FD\u662F\u76EE\u9732\u8BA5\u7B11\u4E4B\u8272\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u6155\u5BB9\u9F99\u4E0D\u518D\u62BD\u9001\uFF0C\u800C\u662F\u6C14\u8D2F\u68D2\u4F53\uFF0C\u9F9F\u5934\u62B5\u4F4F\u82B1\u5FC3\u6765\u56DE\u7814\u78E8\u3002</p><p>\u53EA\u7814\u78E8\u6570\u4E0B\uFF0C\u7D2B\u73AB\u5A07\u8EAF\u731B\u7136\u4E00\u98A4\uFF0C\u82B1\u5FC3\u5438\u555C\u7740\uFF0C\u65AD\u65AD\u7EED\u7EED\u55B7\u51FA\u4E00\u80A1\u9634\u7CBE\u3002\u5979\u7AED\u529B\u538B\u6291\u4EE4\u4EBA\u5931\u795E\u7684\u5FEB\u611F\uFF0C\u8EAB\u4F53\u5374\u7981\u4E0D\u4F4F\u98A4\u6296\u8D77\u6765\u3002</p><p>\u5C31\u5728\u8FD9\u65F6\uFF0C\u719F\u7761\u7684\u8427\u4F5B\u5974\u776B\u6BDB\u4E00\u52A8\uFF0C\u7F13\u7F13\u7741\u5F00\u7F8E\u76EE\u3002</p><p>\u7D2B\u73AB\u53C8\u7F9E\u53C8\u6025\uFF0C\u4F46\u4E0B\u4F53\u5FEB\u611F\u8FDE\u8FDE\uFF0C\u53EA\u6015\u5F20\u5F00\u53E3\u5C31\u4F1A\u53EB\u558A\u51FA\u58F0\uFF0C\u53EA\u597D\u54AC\u4F4F\u5507\u74E3\uFF0C\u6371\u8FC7\u8FD9\u96BE\u582A\u7684\u6C89\u9ED8\u3002\u65F6\u95F4\u6162\u5F97\u4F3C\u4E4E\u505C\u6EDE\uFF0C\u9AD8\u6F6E\u7684\u6218\u6817\u6E10\u6E10\u5E73\u606F\uFF0C\u5979\u624D\u52C9\u5F3A\u6324\u51FA\u4E00\u4E1D\u9178\u6DA9\u7684\u7B11\u5BB9\uFF0C\u8F7B\u8F7B\u53EB\u4E86\u58F0\uFF0C\u300C\u5A18\u2026\u2026\u300D</p><p>\u795E\u667A\u6E10\u590D\u7684\u7F8E\u5987\u8BA4\u51FA\u773C\u524D\u662F\u81EA\u5DF1\u7684\u4E00\u53CC\u513F\u5973\uFF0C\u6B63\u884C\u5982\u79BD\u517D\u7684\u505A\u7740\u4E71\u4F26\u4E4B\u4E3E\uFF0C\u4E0D\u7981\u67D4\u80A0\u5BF8\u65AD\uFF0C\u4FA7\u8FC7\u8138\u6697\u81EA\u795E\u4F24\u3002</p><p>\u300C\u817F\u5206\u5F00\u4E9B\uFF0C\u54E5\u54E5\u8981\u5C04\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u5728\u7D2B\u73AB\u4E73\u5C16\u626D\u4E86\u4E00\u628A\uFF0C\u52A8\u4F5C\u84E6\u7136\u52A0\u5FEB\u3002</p><p>\u8FD9\u4E00\u756A\u6025\u653B\u4E4B\u4E0B\uFF0C\u7D2B\u73AB\u8FDE\u6C14\u90FD\u5598\u4E0D\u8FC7\u6765\uFF0C\u4E00\u76F4\u8E2E\u7740\u7684\u811A\u5C16\u518D\u4E5F\u652F\u6301\u4E0D\u4F4F\uFF0C\u4FEF\u8EAB\u8DCC\u5728\u6BCD\u4EB2\u80F8\u524D\u3002</p><p>\u6155\u5BB9\u9F99\u62B1\u7740\u59B9\u59B9\u7684\u8170\u80A2\uFF0C\u50CF\u62B1\u7740\u4E00\u4E2A\u6F02\u4EAE\u73A9\u5177\u822C\u72E0\u72E0\u5957\u5F04\u7740\u3002\u5C31\u5728\u7D2B\u73AB\u5FCD\u4E0D\u4F4F\u8981\u6D41\u4E0B\u6CEA\u65F6\uFF0C\u8089\u68D2\u7EC8\u4E8E\u8DF3\u52A8\u7740\u5C04\u51FA\u6EDA\u70EB\u7684\u9633\u7CBE\u3002</p><p>\u6155\u5BB9\u9F99\u4ECD\u538B\u5728\u7D2B\u73AB\u80CC\u4E0A\uFF0C\u62AC\u624B\u6E29\u67D4\u5730\u64A9\u8D77\u8427\u4F5B\u5974\u8138\u4E0A\u7684\u79C0\u53D1\uFF0C\u300C\u5A18\uFF0C\u4ECA\u5929\u597D\u4E9B\u4E86\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u54FD\u54BD\u58F0\u6E10\u6E10\u54CD\u8D77\u3002</p><p>\u300C\u8FD9\u4E00\u8DEF\u98A0\u7C38\u786E\u5B9E\u8F9B\u82E6\uFF0C\u4F46\u5B69\u513F\u600E\u4E48\u820D\u5F97\u8BA9\u5A18\u4E00\u4E2A\u4EBA\u7559\u5728\u5BAB\u91CC\u5462\uFF1F\u51B5\u4E14\u8FD8\u662F\u796D\u7940\u6155\u5BB9\u6C0F\u7956\u5148\u7684\u5927\u4E8B\u2026\u2026\u522B\u54ED\u4E86\u3002\u5230\u6D1B\u9633\u4F11\u606F\u51E0\u5929\uFF0C\u6211\u548C\u59B9\u59B9\u5E26\u4F60\u51FA\u53BB\u6563\u6563\u5FC3\u2026\u2026\u83BA\u5974\u3001\u9E42\u5974\uFF0C\u4F3A\u5019\u592B\u4EBA\u3002\u300D\u6155\u5BB9\u9F99\u5429\u5490\u5B8C\uFF0C\u4E00\u628A\u5C06\u7D2B\u73AB\u6A2A\u62B1\u5728\u6000\u4E2D\uFF0C\u671D\u95E8\u53E3\u8D70\u53BB\u3002</p><p>\u7D2B\u73AB\u6323\u624E\u7740\u76B1\u8D77\u7709\u5934\uFF1A\u300C\u4F60\u5E72\u561B\u2026\u2026\u300D</p><p>\u300C\u5A18\u5B50\uFF0C\u5148\u966A\u592B\u541B\u6563\u6563\u5FC3\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u7740\u8BF4\u9053\u3002</p><p>\u300C\u6211\u7684\u8863\u670D\u2026\u2026\u4F60\u522B\u5F00\u95E8\uFF01\u300D\u4EB5\u88E4\u8FD8\u4E00\u8361\u4E00\u8361\u5730\u540A\u5728\u811A\u8E1D\u4E0A\uFF0C\u7D2B\u73AB\u5728\u4ED6\u6000\u4E2D\u5F2F\u8D77\u8170\uFF0C\u62FC\u547D\u62C9\u626F\u3002</p><p>\u8427\u4F5B\u5974\u4E00\u8FB9\u6D41\u6CEA\uFF0C\u4E00\u8FB9\u5728\u5FC3\u91CC\u4E0D\u4F4F\u4E5E\u6C42\u4F5B\u7956\u4FDD\u4F51\uFF0C\u613F\u4EE5\u5DF1\u8EAB\u76F8\u820D\uFF0C\u6D17\u53BB\u513F\u5973\u7684\u7F6A\u5B7D\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5915\u9633\u4E2D\u91D1\u9EC4\u7684\u6CB3\u6C34\u9759\u82E5\u5904\u5B50\uFF0C\u6D69\u6D69\u8361\u8361\u6D8C\u5411\u4E1C\u65B9\u7684\u5927\u6D77\u3002\u7EFF\u8349\u840B\u840B\u7684\u5CB8\u8FB9\uFF0C\u4E00\u5BF9\u5C11\u5E74\u60C5\u4FA3\u4EB2\u5BC6\u5730\u76F8\u62E5\u800C\u884C\u3002\u7537\u5B50\u8EAB\u6750\u633A\u62D4\uFF0C\u82F1\u4FCA\u6F47\u6D12\uFF0C\u65C1\u8FB9\u7684\u5C11\u5973\u66F4\u662F\u4E3D\u8272\u5929\u6210\uFF0C\u5B9B\u5982\u4E00\u9897\u6676\u83B9\u7684\u660E\u73E0\uFF0C\u5438\u5F15\u4E86\u6240\u6709\u7684\u76EE\u5149\u3002</p><p>\u4E00\u7537\u4E00\u5973\u76F4\u5982\u4EBA\u4E2D\u9F99\u51E4\uFF0C\u4E16\u95F4\u4ED9\u4FA3\uFF0C\u7FA1\u715E\u82B8\u82B8\u4F17\u751F\u3002\u4F46\u7EC6\u7EC6\u770B\u6765\uFF0C\u4E24\u4EBA\u7709\u76EE\u95F4\u5374\u4F9D\u7A00\u6709\u51E0\u5206\u76F8\u4F3C\uFF0C\u5012\u50CF\u662F\u4E00\u5BF9\u5144\u59B9\u3002</p><p>\u7D2B\u73AB\u4F59\u6012\u672A\u6D88\uFF0C\u7EF7\u7740\u8138\u4E5F\u4E0D\u8BF4\u8BDD\u3002</p><p>\u6155\u5BB9\u9F99\u8FD8\u662F\u7B2C\u4E00\u6B21\u51FA\u5BAB\u8FDC\u884C\uFF0C\u6B64\u65F6\u770B\u5230\u5927\u6CB3\u6C34\u5149\u63A5\u5929\u7684\u96C4\u6D51\u4E4B\u52BF\uFF0C\u4E0D\u7531\u7CBE\u795E\u4E00\u632F\uFF0C\u53EA\u89C9\u80FD\u6000\u62E5\u7F8E\u4EBA\u94C1\u8E44\u5E2D\u5377\u5929\u4E0B\uFF0C\u4EBA\u751F\u518D\u65E0\u61BE\u4E8B\u3002</p><p>\u300C\u7D2F\u4E86\u3002\u300D\u7D2B\u73AB\u505C\u4E0B\u811A\u6B65\u3002</p><p>\u300C\u597D\u597D\u597D\uFF0C\u6B47\u4E00\u4F1A\u513F\u3002\u300D\u6155\u5BB9\u9F99\u4F53\u8D34\u7684\u627E\u4E86\u5904\u957F\u8349\u8302\u5BC6\u7684\u5730\u65B9\uFF0C\u4E0E\u59B9\u59B9\u5E76\u80A9\u5750\u4E0B\u3002\u3016\u8D3C\u5427\uFF3Aei8\u3002\uFF23om\u7535\u5B50\u4E66\u4E0B\u8F7D\uFF1AZei8.me \u8D3C\u5427\u7535\u5B50\u4E66\u3017</p><p>\u300C\u957F\u6CB3\u4F59\u6656\uFF0C\u98CE\u51CC\u665A\u6E21\uFF0C\u8FD8\u6709\u59B9\u59B9\u8FD9\u6837\u7684\u2026\u2026\u300D</p><p>\u300C\u6155\u5BB9\u9F99\uFF01\u300D\u7D2B\u73AB\u677F\u7740\u8138\u6253\u65AD\u4ED6\u7684\u8BDD\uFF0C\u300C\u4F60\u4EE5\u540E\u4E0D\u8981\u5728\u5A18\u9762\u524D\u90A3\u6837\u5B50\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6258\u8D77\u7D2B\u73AB\u5C0F\u5DE7\u7684\u4E0B\u5DF4\uFF0C\u773C\u91CC\u5BD2\u5149\u4E00\u95EA\u3002</p><p>\u7D2B\u73AB\u5782\u4E0B\u5934\uFF0C\u53E3\u6C14\u8F6F\u5316\u4E0B\u6765\uFF0C\u300C\u5A18\u8EAB\u4F53\u4E0D\u597D\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u54B1\u4EEC\u4E00\u5BB6\u4EBA\u8054\u5E8A\u540C\u6B22\u6069\u6069\u7231\u7231\u6709\u4EC0\u4E48\u4E0D\u597D\u7684\uFF1F\u300D\u770B\u5230\u59B9\u59B9\u6CEB\u7136\u6B32\u6EF4\u7684\u695A\u695A\u795E\u60C5\uFF0C\u4ED6\u5FC3\u91CC\u4E00\u8F6F\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u597D\u4E86\u597D\u4E86\uFF0C\u54E5\u54E5\u77E5\u9053\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u5438\u5438\u9F3B\u5B50\uFF0C\u62D4\u8D77\u4E00\u6839\u8349\uFF0C\u4E00\u6BB5\u4E00\u6BB5\u63EA\u5F00\u3002</p><p>\u300C\u9EC4\u6CB3\u4F4D\u5C45\u5929\u4E0B\u5927\u5DDD\u4E4B\u9996\uFF0C\u4ECA\u65E5\u4E00\u89C1\uFF0C\u679C\u7136\u540D\u4E0D\u865A\u4F20\u3002\u300D\u6155\u5BB9\u9F99\u5C94\u5F00\u8BDD\u9898\uFF0C\u611F\u559F\u9053\u3002</p><p>\u300C\u6709\u4EC0\u4E48\u597D\u770B\u7684\u3002\u300D\u4F0F\u9F99\u6DA7\u5728\u9EC4\u6CB3\u4E0A\u6E38\uFF0C\u5F53\u65E5\u7D2B\u73AB\u5355\u9A91\u5357\u4E0B\uFF0C\u6B63\u662F\u4ECE\u98CE\u9675\u6E21\u6E21\u8FC7\u9EC4\u6CB3\uFF0C\u8D76\u81F3\u6D1B\u9633\uFF0C\u5BF9\u9EC4\u6CB3\u65E9\u5DF2\u4E0D\u964C\u751F\u4E86\u3002\u7EA4\u624B\u4E00\u626C\uFF0C\u788E\u8349\u98D8\u821E\u7740\u98DE\u5165\u6CB3\u4E2D\uFF0C\u7D2B\u73AB\u6709\u4E9B\u60C6\u6005\u5730\u8BF4\uFF1A\u300C\u6C34\u8FD9\u4E48\u6E05\uFF0C\u600E\u4E48\u53EB\u9EC4\u6CB3\u5462\uFF1F\u300D</p><p>\u300C\u6570\u767E\u5E74\u524D\uFF0C\u7267\u65CF\u94C1\u9A91\u5357\u4E0B\uFF0C\u5173\u4E2D\u3001\u4E2D\u539F\u5343\u91CC\u826F\u7530\u5C3D\u6210\u7267\u573A\uFF0C\u6CB3\u6C34\u5C31\u6E05\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u628A\u7D2B\u73AB\u7684\u7EA4\u624B\u63E1\u5728\u638C\u4E2D\uFF0C\u76EE\u5149\u8D8A\u8FC7\u9EC4\u6CB3\uFF0C\u770B\u7740\u8FDC\u65B9\u7684\u4E2D\u6761\u5C71\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u7EC8\u6709\u4E00\u65E5\uFF0C\u6574\u4E2A\u5929\u4E0B\u90FD\u5C06\u6210\u4E3A\u6211\u6155\u5BB9\u6C0F\u7684\u7267\u573A\u3002\u300D</p>',64);function t(i,_){return a}var d=e(r,[["render",t]]);export{d as default};
