import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_73" tabindex="-1"><a class="header-anchor" href="#_73" aria-hidden="true">#</a> 73</h1><p>\u8E44\u58F0\u6E10\u54CD\uFF0C\u4E09\u9A91\u6CBF\u6CB3\u6025\u9A70\u3002\u9A6C\u5339\u4ECE\u4E24\u4EBA\u8EAB\u8FB9\u5954\u8FC7\u65F6\uFF0C\u4E09\u4EBA\u773C\u4E2D\u5747\u662F\u4E00\u4EAE\uFF0C\u5176\u4E2D\u4E00\u4EBA\u8BB6\u9053\uFF1A\u300C\u8FD9\u5973\u5B50\u53EF\u6F02\u4EAE\u5F97\u7D27\u554A\uFF0C\u6BD4\u859B\u5927\u5C0F\u59D0\u8FD8\u80DC\u4E0A\u51E0\u5206\u3002\u300D</p><p>\u300C\u7B97\u4E86\u5427\u8001\u9648\u3002\u8D76\u8DEF\u8981\u7D27\uFF0C\u4E24\u5929\u5185\u5FC5\u987B\u8D76\u56DE\u6D1B\u9633\uFF0C\u522B\u591A\u4E8B\u4E86\u3002\u300D</p><p>\u542C\u5230\u300C\u6D1B\u9633\u300D\u4E24\u5B57\uFF0C\u7D2B\u73AB\u8EAB\u8FB9\u4EBA\u5F71\u4E00\u95EA\uFF0C\u6155\u5BB9\u9F99\u5DF2\u817E\u8EAB\u800C\u8D77\u3002\u5F85\u7D2B\u73AB\u626D\u5934\u770B\u53BB\uFF0C\u6155\u5BB9\u9F99\u5DF2\u4ECE\u4E24\u5339\u6025\u9A70\u7684\u9A8F\u9A6C\u4E4B\u95F4\u4E00\u6643\u800C\u8FC7\uFF0C\u5C06\u6700\u524D\u9762\u4E00\u9A91\u4ECE\u9A6C\u80CC\u4E0A\u63EA\u4E86\u4E0B\u6765\u3002</p><p>\u8FD9\u65F6\u53E6\u4E24\u4EBA\u624D\u8DCC\u843D\u9A6C\u4E0B\uFF0C\u4F0F\u5728\u5730\u4E0A\u4E00\u52A8\u4E0D\u52A8\u3002\u7D2B\u73AB\u6697\u6697\u62BD\u4E86\u53E3\u51C9\u6C14\uFF0C\u4E0D\u8FC7\u6708\u4F59\u65F6\u95F4\uFF0C\u8FD9\u5BB6\u4F19\u6B66\u529F\u53C8\u5F3A\u4E86\u8BB8\u591A\u3002</p><p>\u300C\u53EB\u4EC0\u4E48\u540D\u5B57\uFF1F\u56DE\u6D1B\u9633\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u7A81\u7136\u88AB\u4EBA\u4ECE\u6025\u9A70\u7684\u9A6C\u80CC\u4E0A\u63EA\u4E0B\u6765\uFF0C\u90A3\u4EBA\u5F20\u53E3\u7ED3\u820C\uFF0C\u4F5C\u58F0\u4E0D\u5F97\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u4ED6\u5934\u4E0A\u4E00\u62CD\uFF0C\u53CD\u624B\u6293\u8D77\u53E6\u5916\u4E00\u4EBA\uFF0C\u300C\u53EB\u4EC0\u4E48\u540D\u5B57\uFF1F\u56DE\u6D1B\u9633\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u90A3\u4EBA\u773C\u770B\u7740\u540C\u4F34\u76F4\u633A\u633A\u8DEA\u5728\u5730\u4E0A\uFF0C\u773C\u9F3B\u4E2D\u9C9C\u8840\u8FF8\u6D8C\u7684\u60E8\u72B6\uFF0C\u66F4\u662F\u60CA\u6050\u4E07\u5206\u3002</p><p>\u6155\u5BB9\u9F99\u56DE\u5934\u5BF9\u7D2B\u73AB\u7B11\u9053\uFF1A\u300C\u54E5\u54E5\u8FD9\u4E00\u638C\u4E0B\u53BB\uFF0C\u53EA\u8BA9\u4ED6\u534A\u8FB9\u7ECF\u8109\u5C3D\u788E\uFF0C\u53E6\u5916\u534A\u8FB9\u5B8C\u597D\u65E0\u635F\uFF0C\u4F60\u4FE1\u4E0D\u4FE1\uFF1F\u300D\u4E0D\u7B49\u7D2B\u73AB\u56DE\u7B54\uFF0C\u624B\u638C\u5DF2\u8F7B\u8F7B\u62CD\u4E0B\u3002</p><p>\u90A3\u4EBA\u53F3\u8FB9\u7684\u8EAB\u4F53\u6BEB\u65E0\u52A8\u4F5C\uFF0C\u5DE6\u624B\u5DE6\u811A\u5374\u4E0D\u4F4F\u6323\u624E\u626D\u52A8\uFF0C\u9762\u5BB9\u626D\u66F2\uFF0C\u8BE1\u5F02\u975E\u5E38\uFF0C\u770B\u6765\u4E00\u65F6\u534A\u523B\u96BE\u4EE5\u6BD9\u547D\u3002</p><p>\u6155\u5BB9\u9F99\u6EE1\u610F\u5730\u7B11\u4E86\u7B11\uFF0C\u62AC\u773C\u770B\u5411\u6700\u540E\u4E00\u4EBA\u3002</p><p>\u300C\u9648\u3001\u9648\u5A01\u3001\u590D\u3001\u8986\u547D\u3002\u300D\u90A3\u4EBA\u52C9\u5F3A\u8BF4\u5B8C\u8FD9\u51E0\u4E2A\u5B57\uFF0C\u4FBF\u5927\u53E3\u5927\u53E3\u5730\u5598\u6C14\uFF0C\u8EAB\u4F53\u4E0D\u4F4F\u54C6\u55E6\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u62C2\u6653\u65F6\u5206\uFF0C\u8F66\u961F\u4ECE\u5BA2\u6808\u7F13\u7F13\u5F00\u51FA\u3002\u6155\u5BB9\u9F99\u4E0D\u7D27\u4E0D\u6162\u5730\u4E58\u9A6C\u800C\u884C\uFF0C\u4E00\u8DEF\u4E0A\u4E0E\u91D1\u5F00\u7532\u6307\u70B9\u6C5F\u5C71\uFF0C\u7814\u8BA8\u5175\u6CD5\uFF0C\u6E38\u5C71\u73A9\u6C34\u822C\u671D\u6D1B\u9633\u8FDB\u53D1\u3002\u4F46\u968F\u884C\u7684\u5E2E\u4F17\u5374\u5C11\u4E86\u4E00\u534A\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u518D\u5F53\u7740\u6BCD\u4EB2\u7684\u9762\u5F3A\u8FEB\u7D2B\u73AB\uFF0C\u53EA\u662F\u665A\u95F4\u7531\u5979\u4F8D\u5BDD\u3002\u8FD9\u4F7F\u7D2B\u73AB\u677E\u4E86\u53E3\u6C14\uFF0C\u5E8A\u7B2C\u95F4\u6781\u5C3D\u598D\u6001\uFF0C\u5176\u59A9\u5A9A\u5A49\u8F6C\u4E4B\u5904\uFF0C\u8FDE\u9605\u5973\u65E0\u6570\u7684\u6155\u5BB9\u9F99\u4E5F\u7559\u604B\u4E0D\u5DF2\uFF0C\u5BF9\u5979\u6108\u53D1\u75BC\u7231\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6BCF\u65E5\u7ED9\u592B\u4EBA\u6309\u6469\u3001\u6D82\u836F\u3001\u6362\u6D17\u5C3F\u5E03\uFF0C\u4E24\u5973\u89C1\u767E\u82B1\u89C2\u97F3\u8F6F\u5F31\u53EF\u6B3A\uFF0C\u867D\u7136\u4E0D\u6562\u6076\u8BED\u76F8\u5411\uFF0C\u4F46\u8D81\u6CA1\u4EBA\u7684\u65F6\u5019\u603B\u4F1A\u5632\u8BBD\u51E0\u53E5\u3002\u8427\u4F5B\u5974\u4E0D\u613F\u544A\u8BC9\u513F\u5B50\uFF0C\u53C8\u6015\u5973\u513F\u751F\u6C14\uFF0C\u53EA\u597D\u5FCD\u6C14\u541E\u58F0\uFF0C\u552F\u6709\u5FF5\u4F5B\u800C\u5DF2\u3002</p><p>\u7EAA\u7709\u59A9\u5219\u88AB\u5F53\u4F5C\u4F17\u4EBA\u6CC4\u6B32\u7684\u5668\u5177\uFF0C\u5979\u72EC\u4E58\u4E00\u8F86\u5927\u8F66\uFF0C\u65E0\u8BBA\u4F55\u65F6\uFF0C\u53EA\u8981\u6709\u4EBA\u9700\u8981\uFF0C\u5979\u5C31\u5F97\u7AED\u529B\u5949\u8FCE\u3002\u5802\u5802\u8C6A\u95E8\u5343\u91D1\uFF0C\u6B66\u6797\u540D\u5A9B\uFF0C\u53EA\u5982\u968F\u884C\u8425\u5993\u4E00\u822C\uFF0C\u4EFB\u4EBA\u91C7\u64B7\uFF0C\u800C\u5979\u4E5F\u5728\u8089\u6B32\u4E2D\u8D8A\u9677\u8D8A\u6DF1\u3002</p><p>\u5728\u9177\u6691\u5C06\u81F3\u7684\u56DB\u6708\u672B\uFF0C\u4E00\u884C\u4EBA\u7EC8\u4E8E\u62B5\u8FBE\u6D1B\u9633\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u53E4\u4ECA\u5174\u5E9F\u4E8B\uFF0C\u8FD8\u770B\u6D1B\u9633\u57CE\u3002</p><p>\u7ECF\u8FC7\u5341\u4F59\u5E74\u7684\u592A\u5E73\u5C81\u6708\uFF0C\u8FD9\u5EA7\u8BB0\u8F7D\u4E86\u65E0\u6570\u60B2\u6B22\u8363\u8FB1\u7684\u4E2D\u5DDE\u540D\u90FD\u6E10\u6E10\u6062\u590D\u4E86\u5143\u6C14\u3002</p><p>\u6A2A\u8DE8\u6D1B\u6C34\u7684\u9752\u77F3\u957F\u6865\u4E0A\uFF0C\u5546\u65C5\u4E91\u96C6\uFF0C\u5DDD\u6D41\u4E0D\u606F\u3002\u7A7F\u8FC7\u5DCD\u5CE8\u7684\u57CE\u95E8\uFF0C\u9762\u524D\u51FA\u73B0\u4E00\u6761\u7B14\u76F4\u7684\u957F\u8857\u3002\u8857\u9053\u4E24\u65C1\u4F9D\u6B21\u6446\u653E\u7740\u4E00\u5BF9\u5BF9\u77F3\u96D5\u7684\u7F8A\u3001\u9A6C\u3001\u5929\u7984\u3001\u8F9F\u90AA\u3001\u9E92\u9E9F\uFF0C\u518D\u5F80\u524D\u662F\u94DC\u5236\u7684\u627F\u9732\u76D8\u3001\u4ED9\u4EBA\u638C\u3001\u9F9F\u3001\u51E4\u3001\u9F99\u3001\u9A6C\uFF0C\u5728\u957F\u8857\u5C3D\u5934\u7684\u53F8\u9A6C\u95E8\u524D\uFF0C\u77D7\u7ACB\u7740\u4E00\u5BF9\u6C14\u5B87\u8F69\u6602\u7684\u94DC\u9A7C\u3002\u8FD9\u4FBF\u662F\u5929\u4E0B\u6700\u4E3A\u7E41\u534E\u7684\u94DC\u9A7C\u5927\u8857\u4E86\u3002</p><p>\u8857\u4E0A\u7684\u884C\u4EBA\u5546\u8D29\u670D\u8272\u5404\u5F02\uFF0C\u6C10\u3001\u7F8C\u3001\u7FAF\u3001\u5C60\u5404\u3001\u7A3D\u3001\u5308\u5974\u8BF8\u65CF\u6742\u9648\uFF0C\u6765\u5F80\u5C3D\u662F\u9EC4\u987B\u5377\u53D1\u3001\u51F8\u9F3B\u6DF1\u76EE\u7684\u80E1\u4EBA\uFF0C\u5728\u8FD9\u5EA7\u4E2D\u539F\u53E4\u90FD\u7684\u5927\u8857\u4E0A\uFF0C\u7ED3\u53D1\u5E26\u51A0\u7684\u6C49\u4EBA\u5374\u662F\u5C11\u6570\u3002\u76F8\u6BD4\u4E8E\u5706\u886B\u690E\u5E3D\uFF0C\u751A\u81F3\u62AB\u895F\u8892\u81C2\u7684\u7C97\u72B7\u80E1\u670D\uFF0C\u6155\u5BB9\u9F99\u4E00\u8EAB\u9C9C\u5351\u8D35\u65CF\u670D\u9970\uFF0C\u5E76\u4E0D\u5F15\u4EBA\u6CE8\u76EE\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E00\u8DEF\u4E0A\u60F3\u4E86\u65E0\u6570\u8131\u8EAB\u7684\u8BA1\u7B56\uFF0C\u4F46\u4E34\u884C\u524D\u6155\u5BB9\u9F99\u3001\u6C90\u58F0\u4F20\u548C\u53F6\u884C\u5357\u4E09\u4EBA\u8054\u624B\uFF0C\u5728\u5979\u8EAB\u4E0A\u65BD\u4E0B\u6BD4\u51DD\u771F\u4E5D\u523A\u66F4\u4E25\u5BC6\u7684\u91CD\u697C\u6C14\u9501\uFF0C\u5C06\u5979\u7684\u771F\u6C14\u5B8C\u5168\u5236\u4F4F\u3002\u5982\u6B64\u4E00\u6765\u867D\u7136\u884C\u52A8\u5982\u5E38\uFF0C\u4F46\u65E0\u6CD5\u518D\u7528\u5185\u529B\uFF0C\u5F62\u540C\u5E9F\u95EA\u3002\u7EB5\u7136\u4E00\u65F6\u9003\u8131\u4E5F\u65E0\u6CD5\u907F\u5F00\u4ED6\u4EEC\u7684\u8FFD\u6355\uFF0C\u53EA\u597D\u637A\u4E0B\u6027\u5B50\uFF0C\u6162\u6162\u5BFB\u627E\u673A\u4F1A\u3002</p><p>\u6155\u5BB9\u9F99\u56DE\u9A6C\u64A9\u5F00\u7A97\u5EC9\uFF0C\u7B11\u9053\uFF1A\u300C\u524D\u9762\u5C31\u662F\u7EAA\u5A4A\u5B50\u5BB6\u7684\u5927\u5C06\u519B\u5E9C\u4E86\u2014\u2014\u53EF\u60DC\u7EAA\u91CD\u9886\u5175\u5728\u5916\uFF0C\u770B\u4E0D\u5230\u4ED6\u5973\u513F\u63A5\u5BA2\u7684\u4E56\u5DE7\u6A21\u6837\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u9ED8\u4E0D\u4F5C\u58F0\uFF0C\u5FC3\u91CC\u5374\u7D27\u5F20\u8D77\u6765\u3002\u5979\u4E00\u76F4\u5947\u602A\u6155\u5BB9\u9F99\u4E3A\u4F55\u8981\u5E26\u7EAA\u5E08\u59D0\u540C\u884C\uFF0C\u6B64\u65F6\u542C\u4ED6\u7684\u53E3\u6C14\u2026\u2026\u5979\u4E0D\u6562\u518D\u60F3\u4E0B\u53BB\uFF0C\u53EA\u63E1\u7740\u6BCD\u4EB2\u67D4\u8F6F\u7684\u624B\u638C\u5FAE\u5FAE\u98A4\u6296\u3002</p><p>\u8F66\u961F\u4ECE\u7EAA\u5E9C\u95E8\u524D\u7ECF\u8FC7\u65F6\uFF0C\u5176\u4E2D\u4E00\u8F86\u8F66\u5185\u7A81\u7136\u4F20\u6765\u5973\u5B50\u7684\u60CA\u53EB\u58F0\u3002\u63A5\u7740\u4F4E\u6C89\u4E0B\u53BB\uFF0C\u53D8\u6210\u75DB\u82E6\u7684\u4F4E\u547C\u3002\u58F0\u97F3\u65F6\u65AD\u65F6\u7EED\uFF0C\u5145\u6EE1\u6DEB\u8361\u610F\u5473\uFF0C\u4E0D\u7528\u770B\u5C31\u77E5\u9053\u91CC\u9762\u6B63\u5728\u53D1\u751F\u4EC0\u4E48\u3002</p><p>\u7EAA\u5E9C\u5927\u95E8\u524D\u7684\u51E0\u540D\u5B88\u536B\u8138\u4E0A\u9732\u51FA\u66A7\u6627\u7684\u7B11\u5BB9\uFF0C\u671D\u58F0\u97F3\u4F20\u51FA\u7684\u5927\u8F66\u770B\u53BB\u3002</p><p>\u5927\u8F66\u9752\u5E03\u4E3A\u5E54\uFF0C\u770B\u4E0A\u53BB\u6BEB\u4E0D\u8D77\u773C\uFF0C\u4F46\u8F66\u524D\u7684\u9A6C\u5339\u5374\u663E\u793A\u51FA\u4E3B\u4EBA\u7684\u8C6A\u5BCC\u3002\u5F53\u65F6\u6218\u4E71\u4E0D\u6B62\uFF0C\u9A6C\u5339\u662F\u6781\u4E3A\u73CD\u8D35\u7684\u519B\u4E8B\u7269\u8D44\uFF0C\u5373\u4F7F\u6D1B\u9633\u8FD9\u6837\u7684\u5927\u90FD\uFF0C\u4E00\u822C\u5B98\u5BA6\u4E4B\u5BB6\uFF0C\u4E5F\u53EA\u80FD\u4EE5\u725B\u8F66\u4EE3\u6B65\u3002\u4E0D\u77E5\u9053\u90A3\u4E2A\u80E1\u670D\u9752\u5E74\u662F\u54EA\u5BB6\u8D35\u65CF\u5B50\u5F1F\u2026\u2026</p><p>\u6B63\u5BFB\u601D\u95F4\uFF0C\u9A6C\u8F66\u7A97\u5EC9\u5FFD\u7136\u6380\u5F00\uFF0C\u4E00\u4E2A\u8D64\u88F8\u7684\u5973\u5B50\u88AB\u4EBA\u4ECE\u7A97\u4E2D\u63A8\u51FA\uFF0C\u51E0\u4EBA\u7684\u76EE\u5149\u987F\u65F6\u88AB\u90A3\u5BF9\u767D\u5AE9\u9971\u6EE1\u7684\u9999\u4E73\u5438\u5F15\uFF0C\u773C\u73E0\u968F\u7740\u4E73\u623F\u7684\u6446\u52A8\u6765\u56DE\u6253\u8F6C\uFF0C\u8FDE\u5973\u5B50\u75DB\u82E6\u7684\u795E\u60C5\u90FD\u672A\u7559\u610F\u3002</p><p>\u300C\u770B\u4EC0\u4E48\u770B\uFF01\u300D\u7BA1\u5BB6\u7EAA\u8BDA\u5389\u559D\u4E00\u58F0\uFF0C\u626B\u4E86\u4E00\u773C\u6CEA\u6C34\u6A21\u7CCA\u7684\u5973\u4EBA\uFF0C\u677F\u7740\u8138\u628A\u5B88\u536B\u8D76\u8FDB\u5E9C\u5185\uFF0C\u300C\u546F\u300D\u7684\u5408\u4E0A\u95E8\uFF0C\u9A82\u9053\uFF1A\u300C\u4E0D\u77E5\u7F9E\u803B\u7684\u80E1\u72D7\uFF01\u300D</p><p>\u8FD9\u4E00\u5207\u90FD\u6CA1\u6709\u9003\u8FC7\u6155\u5BB9\u9F99\u7684\u8033\u76EE\uFF0C\u4ED6\u9AD8\u8E1E\u9A6C\u4E0A\uFF0C\u4E0E\u7D2B\u73AB\u8C08\u7B11\u664F\u664F\uFF0C\u4F3C\u4E4E\u53EA\u662F\u4E2A\u98CE\u6D41\u501C\u50A5\u7684\u8D35\u516C\u5B50\uFF0C\u4F46\u6BD4\u5E38\u4EBA\u654F\u611F\u767E\u500D\u7684\u542C\u89C9\u5374\u5C06\u5468\u56F4\u4E8B\u7269\u949C\u7EC6\u65E0\u9057\u5C3D\u6536\u8033\u5E95\u3002</p><p>\u79BB\u7EAA\u5E9C\u4E0D\u8FDC\uFF0C\u4FBF\u662F\u5E7F\u9633\u5E2E\u6240\u5728\u7684\u7389\u9E21\u574A\u3002\u6155\u5BB9\u9F99\u51DD\u795E\u6253\u91CF\uFF0C\u53EA\u89C1\u5E2E\u5185\u5E73\u9759\u5982\u5E38\uFF0C\u4E1D\u6BEB\u6CA1\u6709\u5982\u4E34\u5927\u654C\u7684\u614C\u5F20\u3002\u4F46\u4ED6\u6E05\u695A\u7684\u611F\u5E94\u5230\uFF0C\u5728\u90A3\u4E9B\u7D27\u95ED\u7684\u95E8\u7A97\u540E\uFF0C\u6709\u65E0\u6570\u773C\u775B\u6B63\u6CE8\u89C6\u7740\u8857\u4E0A\u5F80\u6765\u7684\u4EBA\u7FA4\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u7B56\u9A91\u626C\u957F\u800C\u8FC7\u3002</p><p>\u8F66\u961F\u6765\u5230\u4F4D\u4E8E\u6D1B\u9633\u4E1C\u5317\u7684\u5174\u827A\u574A\uFF0C\u4E00\u540D\u6C49\u5B50\u4ECE\u8DEF\u65C1\u95EA\u51FA\uFF0C\u4E0D\u8A00\u58F0\u5730\u9886\u7740\u4F17\u4EBA\u8FDB\u5165\u574A\u5185\u7684\u5BA2\u6808\u3002</p><p>\u300C\u53C2\u89C1\u5BAB\u4E3B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6446\u4E86\u6446\u624B\uFF0C\u8FF3\u76F4\u8D70\u5165\u5BA4\u5185\u3002</p><p>\u300C\u5C5E\u4E0B\u4E09\u65E5\u524D\u8D76\u81F3\u6B64\u5904\uFF0C\u4F9D\u7167\u5BAB\u4E3B\u5429\u5490\uFF0C\u5E76\u672A\u901A\u77E5\u8521\u3001\u970D\u4E24\u4F4D\u957F\u8001\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u644A\u5F00\u684C\u4E0A\u7684\u5730\u56FE\uFF0C\u7565\u7565\u770B\u4E86\u4E00\u904D\uFF0C\u6447\u5934\u9053\uFF1A\u300C\u970D\u72C2\u7130\u53EA\u8BF4\u5728\u57CE\u897F\u7ACB\u4F4F\u811A\u8DDF\uFF0C\u539F\u6765\u662F\u88AB\u4EBA\u903C\u5230\u57CE\u8FB9\u7684\u5E7F\u5229\u574A\u3002\u82E5\u975E\u6709\u8521\u4E91\u5CF0\u76F8\u52A9\uFF0C\u53EA\u6015\u4ED6\u5DF2\u7ECF\u88AB\u8D76\u56DE\u795E\u6559\u4E86\u3002\u6D1B\u9633\u73B0\u5728\u60C5\u5F62\u5982\u4F55\uFF1F\u300D</p><p>\u300C\u5BAB\u4F9B\u5949\u4E09\u6B21\u6F5C\u5165\u957F\u9E70\u4F1A\uFF0C\u5DF2\u7ECF\u63A2\u5F97\u865A\u5B9E\u3002\u300D\u7075\u7389\u7EC6\u957F\u7684\u624B\u6307\u70B9\u5728\u5730\u56FE\u4E0A\uFF0C\u300C\u6D1B\u9633\u5341\u4E8C\u5EA7\u57CE\u95E8\u90FD\u6709\u6559\u4E2D\u5F1F\u5B50\u628A\u5B88\uFF0C\u8FDE\u65E5\u6765\u8FDB\u5165\u57CE\u5185\u7684\u6B66\u6797\u4EBA\u58EB\u5171\u6709\u4E03\u5341\u4E5D\u4EBA\uFF0C\u5206\u5C5E\u5341\u4E00\u4E2A\u95E8\u6D3E\uFF0C\u73B0\u5728\u5168\u90FD\u96C6\u4E2D\u5728\u957F\u9E70\u4F1A\u5185\u3002\u300D</p><p>\u300C\u5341\u4E00\u4E2A\u95E8\u6D3E\uFF1F\u77ED\u77ED\u516B\u5929\u65F6\u95F4\u5C31\u6765\u4E86\u8FD9\u4E48\u591A\uFF0C\u4E5D\u534E\u5251\u6D3E\u597D\u5927\u7684\u9762\u5B50\u3002\u300D</p><p>\u300C\u9664\u88AB\u8521\u957F\u8001\u51FB\u6E83\u7684\u6D1B\u9A6C\u5E2E\u5916\uFF0C\u5176\u4F59\u4E09\u5E2E\u4E09\u4F1A\u5DF2\u7ECF\u96C6\u5408\u4EBA\u624B\uFF0C\u51C6\u5907\u4E0E\u6211\u6559\u51B3\u4E00\u6B7B\u6218\u3002\u300D\u7075\u7389\u5FE7\u5F62\u4E8E\u8272\uFF0C\u300C\u5355\u662F\u957F\u9E70\u4F1A\u5C31\u6709\u5343\u4F59\u4EBA\u9A6C\uFF0C\u5176\u4ED6\u4E94\u5E2E\u76F8\u5408\uFF0C\u4E5F\u6709\u6B64\u6570\u3002\u518D\u52A0\u4E0A\u9646\u7EED\u6765\u5230\u7684\u63F4\u624B\uFF0C\u5B9E\u529B\u4E0D\u53EF\u5C0F\u89D1\u3002\u300D</p><p>\u300C\u970D\u72C2\u7130\u6253\u8349\u60CA\u86C7\uFF0C\u4E0D\u667A\u4E4B\u6781\u3002\u300D\u6155\u5BB9\u9F99\u4E00\u51FB\u684C\u9762\uFF0C\u957F\u8EAB\u800C\u8D77\u3002</p><p>\u77F3\u880D\u8214\u4E86\u8214\u5634\u5507\uFF0C\u72DE\u58F0\u9053\uFF1A\u300C\u6015\u4ED6\u4E2A\u540A\uFF01\u6211\u53BB\u8DDF\u59D3\u859B\u7684\u6597\u4E00\u573A\uFF01\u975E\u628A\u4ED6\u7684\u8111\u888B\u62E7\u4E0B\u6765\u7ED9\u5BAB\u4E3B\u5F53\u591C\u58F6\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u5927\u7B11\uFF0C\u62CD\u7740\u77F3\u880D\u7684\u80A9\u9053\uFF1A\u300C\u880D\u738B\u679C\u7136\u8C6A\u6C14\u5E72\u4E91\u3002\u4E0D\u8FC7\u859B\u957F\u9E70\u65E2\u7136\u5E7F\u9080\u540C\u9053\uFF0C\u6446\u660E\u4E86\u4E0D\u4F1A\u8DDF\u54B1\u4EEC\u5355\u6253\u72EC\u6597\u3002\u300D</p><p>\u5BAB\u767D\u7FBD\u9053\uFF1A\u300C\u859B\u957F\u9E70\u5DF2\u7ECF\u9012\u4E0B\u6218\u4E66\uFF0C\u9080\u970D\u3001\u8521\u4E24\u4F4D\u957F\u8001\u4E94\u6708\u5341\u4E8C\u5728\u9F99\u864E\u6EE9\u51B3\u6597\u3002\u300D</p><p>\u300C\u5594\uFF1F\u859B\u957F\u9E70\u8FD8\u6709\u8FD9\u4EFD\u80C6\u91CF\uFF1F\u83AB\u975E\u6709\u4EC0\u4E48\u5E2E\u624B\uFF1F\u300D</p><p>\u300C\u5BAB\u4E3B\u6240\u6599\u6B63\u662F\u3002\u300D\u7075\u7389\u9053\uFF0C\u300C\u5E7F\u9633\u5E2E\u5B59\u540C\u8F89\u51FA\u9762\uFF0C\u9080\u8BF7\u4E86\u6E05\u51C9\u5C71\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u5706\u901A\u5927\u5E08\u3002\u300D</p><p>\u300C\u5706\u901A\uFF1F\u4ED6\u96BE\u9053\u6BD4\u96EA\u5CF0\u8FD8\u5389\u5BB3\uFF1F\u300D\u6155\u5BB9\u9F99\u4E00\u7B11\u7F6E\u4E4B\u3002</p><p>\u7075\u7389\u95FB\u8A00\u4E5F\u662F\u4E00\u7B11\uFF0C\u300C\u5706\u901A\u6BD4\u96EA\u5CF0\u81EA\u662F\u8FDC\u8FDC\u4E0D\u53CA\u3002\u4E0D\u8FC7\u5927\u5B5A\u7075\u9E6B\u5BFA\u867D\u4E0D\u53CA\u98D8\u6885\u5CF0\u51FA\u7C7B\u62D4\u8403\uFF0C\u4F46\u80FD\u81EA\u6C49\u672B\u4EE5\u6765\u957F\u76DB\u4E0D\u8870\uFF0C\u4E5F\u6709\u5176\u8FC7\u4EBA\u4E4B\u5904\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u5934\u9053\uFF1A\u300C\u9053\u957F\u8BF4\u7684\u662F\uFF0C\u672C\u5BAB\u6709\u4E9B\u8F7B\u654C\u4E86\u3002\u300D</p><p>\u91D1\u5F00\u7532\u5FFD\u7136\u9053\uFF1A\u300C\u5B59\u540C\u8F89\u7ADF\u80FD\u8BF7\u5F97\u52A8\u5706\u901A\u548C\u5C1A\uFF0C\u7A76\u7ADF\u662F\u4EC0\u4E48\u6765\u5934\uFF1F\u300D\u91D1\u5802\u52BF\u529B\u8303\u56F4\u5728\u7EC8\u5357\u4EE5\u897F\uFF0C\u5BF9\u6D1B\u9633\u5E2E\u4F1A\u8FDC\u4E0D\u5982\u6728\u5802\u719F\u6089\u3002</p><p>\u7075\u7389\u9053\uFF1A\u300C\u5B59\u540C\u8F89\u672C\u662F\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u4FD7\u5BB6\u5F1F\u5B50\uFF0C\u751A\u5F97\u65B9\u4E08\u5706\u76F8\u3001\u7EF4\u90A3\u5706\u5149\u7B49\u4EBA\u5668\u91CD\u3002\u5706\u901A\u662F\u5BFA\u5185\u9996\u5EA7\uFF0C\u6B66\u529F\u5F53\u5728\u8D2B\u9053\u4E4B\u4E0A\u3002\u300D</p><p>\u300C\u9053\u957F\u8FC7\u8C26\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u63A8\u5F00\u7A97\u6237\uFF0C\u671D\u90BB\u574A\u7684\u957F\u9E70\u4F1A\u5927\u5802\u671B\u53BB\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5706\u901A\u4E00\u4EBA\u4E0D\u8DB3\u4E3A\u8651\uFF0C\u4F46\u4E0E\u4ED6\u52A8\u624B\uFF0C\u4FBF\u662F\u4E0E\u6574\u4E2A\u767D\u9053\u6B66\u6797\u4E3A\u654C\uFF0C\u5BF9\u6211\u661F\u6708\u6E56\u5927\u4E1A\u5371\u5BB3\u81F3\u5927\u3002\u300D</p><p>\u591C\u8272\u5DF2\u6D53\uFF0C\u4F46\u4ECE\u6155\u5BB9\u9F99\u773C\u91CC\u770B\u6765\uFF0C\u767E\u4E08\u4E4B\u5916\u7684\u89D2\u697C\u91CC\u4EFB\u4F55\u4E00\u4E2A\u7EC6\u8282\u90FD\u5386\u5386\u5728\u76EE\uFF0C\u751A\u81F3\u8FDE\u6A90\u4E0A\u6F5C\u4F0F\u7684\u6697\u54E8\u4E5F\u770B\u5F97\u4E00\u6E05\u4E8C\u695A\u3002</p><p>\u4E0D\u80FD\u4E0E\u5706\u901A\u7B49\u4EBA\u5BF9\u654C\uFF0C\u53C8\u8981\u5F81\u670D\u6D1B\u9633\u6B66\u6797\uFF0C\u4E00\u5411\u7EB5\u6A2A\u6C5F\u6E56\u5FEB\u610F\u6069\u4EC7\u7684\u7075\u7389\u7B49\u4EBA\uFF0C\u90FD\u89C9\u5F97\u7F1A\u624B\u7F1A\u811A\uFF0C\u65E0\u8BA1\u53EF\u65BD\u3002\u53EA\u6709\u6DF1\u6089\u661F\u6708\u6E56\u624B\u6BB5\u7684\u91D1\u5F00\u7532\u77E5\u9053\u5BAB\u4E3B\u6240\u8F6C\u7684\u5FF5\u5934\u3002</p><p>\u534A\u664C\u540E\uFF0C\u6155\u5BB9\u9F99\u7F13\u7F13\u9053\uFF1A\u300C\u9053\u957F\uFF0C\u4E24\u4F4D\u4F9B\u5949\uFF0C\u4ECA\u591C\u6211\u4EEC\u4E00\u8D77\u53BB\u6D1B\u9633\u7B2C\u4E00\u5927\u5E2E\u770B\u770B\u3002\u8FD9\u91CC\u7531\u91D1\u957F\u8001\u5750\u9547\uFF0C\u65E0\u8BBA\u53D1\u751F\u4EC0\u4E48\u4E8B\uFF0C\u90FD\u52A1\u5FC5\u4FDD\u4F4F\u592B\u4EBA\u548C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u4F17\u4EBA\u9F50\u58F0\u5E94\u8BFA\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
