import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_75" tabindex="-1"><a class="header-anchor" href="#_75" aria-hidden="true">#</a> 75</h1><p>\u300C\u8BF8\u4F4D\u597D\u53CB\u3002\u300D\u859B\u957F\u9E70\u4F3C\u4E4E\u5728\u4E00\u591C\u4E4B\u95F4\u8001\u4E86\u5341\u5E74\uFF0C\u58F0\u97F3\u4E5F\u663E\u5F97\u4E2D\u6C14\u4E0D\u8DB3\u3002</p><p>\u8D76\u6765\u52A9\u6218\u7684\u8BF8\u6D3E\u9AD8\u624B\u5927\u6E05\u65E9\u5C31\u88AB\u8BF7\u5230\u98DE\u9E70\u5802\uFF0C\u5FC3\u4E0B\u90FD\u6709\u4E9B\u7EB3\u95F7\u3002\u6628\u665A\u8FD8\u610F\u6C14\u98CE\u53D1\u7684\u5927\u9F99\u5934\uFF0C\u4ECA\u65E9\u770B\u8D77\u6765\u600E\u4E48\u4E00\u5E45\u795E\u60C5\u604D\u60DA\uFF0C\u9B42\u4E0D\u5B88\u820D\u7684\u6837\u5B50\uFF1F</p><p>\u4E00\u5411\u4EE5\u8C08\u7B11\u98CE\u751F\uFF0C\u6325\u6D12\u81EA\u5982\u7740\u79F0\u7684\u859B\u957F\u9E70\u4F3C\u4E4E\u5FD8\u4E86\u8BCD\uFF0C\u6123\u4E86\u4E00\u4F1A\u513F\u624D\u6DA9\u7136\u5F00\u53E3\uFF0C\u300C\u5728\u4E0B\u8BF7\u5404\u4F4D\u3001\u6765\u5230\u655D\u5E2E\u3002\u662F\u4E3A\u4E86\u6D1B\u9633\u3001\u6B66\u6797\u7684\u5B89\u5371\u3002\u300D\u4ED6\u6014\u6014\u770B\u7740\u5927\u5802\u7684\u95E8\u6D1E\uFF0C\u300C\u534A\u6708\u524D\u4E00\u5925\u5F3A\u532A\u524D\u6765\u6311\u6218\uFF0C\u6C14\u52BF\u6C79\u6C79\u2026\u2026\u6211\u6D1B\u9633\u56DB\u5E2E\u4E09\u4F1A\u8054\u76DF\uFF0C\u5148\u540E\u4EA4\u624B\u6570\u6B21\u3002\u635F\u5175\u6298\u5C06\u3002\u6D1B\u9A6C\u5E2E\u8986\u6CA1\u3002\u300D</p><p>\u300C\u654C\u4EBA\u52BF\u529B\u4E4B\u5F3A\u608D\uFF0C\u51FA\u4E4E\u5728\u4E0B\u610F\u6599\u3002\u56E0\u6B64\u8146\u989C\u8BF7\u5404\u4F4D\u597D\u53CB\u524D\u6765\u52A9\u9635\u3002\u300D\u859B\u957F\u9E70\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u8270\u96BE\u5730\u8BF4\uFF1A\u300C\u5F7C\u7B49\u795E\u51FA\u9B3C\u6CA1\uFF0C\u5BF9\u6211\u5404\u5E2E\u865A\u5B9E\u4E86\u5982\u6307\u638C\uFF0C\u5728\u4E0B\u65E9\u5DF2\u751F\u7591\u3002\u6D1B\u9A6C\u5E2E\u88AB\u706D\uFF0C\u5728\u4E0B\u5FC3\u77E5\u5176\u4E2D\u5FC5\u6709\u7384\u865A\u3002\u7ECF\u8FC7\u591A\u65B9\u67E5\u8BE2\uFF0C\u6628\u591C\u7EC8\u4E8E\u5F97\u77E5\u90A3\u4E9B\u5F3A\u532A\u80CC\u540E\u7684\u9ED1\u624B\u5C31\u662F\u2014\u2014\u5E7F\u9633\u5E2E\u3002\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u5802\u5185\u7ACB\u523B\u5927\u54D7\uFF0C\u8FDE\u957F\u9E70\u4F1A\u5E2E\u4F17\u4E5F\u90FD\u9762\u9732\u8BB6\u8272\uFF0C\u7B80\u76F4\u4E0D\u6562\u76F8\u4FE1\u81EA\u5DF1\u7684\u8033\u6735\u3002</p><p>\u300C\u5B59\u540C\u8F89\u72FC\u5B50\u91CE\u5FC3\uFF0C\u79C1\u84C4\u5F3A\u5F92\u5984\u56FE\u72EC\u9738\u6D1B\u9633\u3002\u6B64\u4E2D\u539F\u59D4\uFF0C\u4E00\u8A00\u96BE\u5C3D\u3002\u300D\u859B\u957F\u9E70\u9762\u5BB9\u5446\u6EDE\uFF0C\u6709\u6C14\u65E0\u529B\u5730\u8BF4\u9053\uFF1A\u300C\u5E26\u9648\u5A01\u2026\u2026\u300D</p><p>\u4E00\u540D\u6C49\u5B50\u88AB\u5E26\u5230\u5802\u4E2D\uFF0C\u6709\u4EBA\u8BA4\u51FA\u6B63\u662F\u5E7F\u9633\u5E2E\u7684\u9648\u5A01\u3002</p><p>\u9648\u5A01\u8DEA\u5728\u5730\u4E0A\u6ED4\u6ED4\u4E0D\u7EDD\u5730\u8BF4\u4E86\u8D77\u6765\uFF0C\u5C06\u5B59\u540C\u8F89\u5982\u4F55\u706D\u6389\u6D1B\u9A6C\u5E2E\uFF1B\u5982\u4F55\u6D3E\u4ED6\u53BB\u8054\u7EDC\u516B\u6781\u95E8\u7684\u9AD8\u624B\uFF0C\u4E00\u540C\u5BF9\u4ED8\u957F\u9E70\u4F1A\uFF1B\u4ED6\u5982\u4F55\u826F\u5FC3\u672A\u6CEF\uFF0C\u6295\u5954\u4E86\u859B\u5E2E\u4E3B\u2026\u2026\u8BF4\u5F97\u4E00\u6E05\u4E8C\u695A\u3002\u6700\u540E\u8BF4\u660E\uFF0C\u90A3\u4F19\u5F3A\u532A\u5176\u5B9E\u5C31\u662F\u5B59\u540C\u8F89\u7528\u6765\u72EC\u9738\u6D1B\u9633\u6B66\u6797\u7684\u79C1\u4EBA\u52BF\u529B\uFF0C\u5982\u4ECA\u5C31\u8EB2\u5728\u5E7F\u9633\u5E2E\u5185\uFF0C\u6240\u4EE5\u8054\u76DF\u624D\u4F1A\u56DB\u4E0B\u6253\u542C\uFF0C\u6BEB\u65E0\u90A3\u4E9B\u5F3A\u532A\u7684\u7EBF\u7D22\u3002</p><p>\u542C\u4E86\u8FD9\u756A\u8BDD\uFF0C\u4F17\u4EBA\u867D\u7136\u8FD8\u6709\u4E9B\u7591\u60D1\uFF0C\u4F46\u859B\u957F\u9E70\u53EA\u662F\u5904\u4E8B\u5706\u6ED1\uFF0C\u5E76\u975E\u5FC3\u673A\u6DF1\u6C89\u4E4B\u8F88\uFF0C\u56E0\u6B64\u5DF2\u4FE1\u4E86\u516D\u5206\u3002\u540C\u65F6\u5FC3\u4E0B\u6697\u53F9\uFF1A\u6C5F\u6E56\u9669\u6076\uFF0C\u53D7\u6B64\u6253\u51FB\uFF0C\u96BE\u602A\u5927\u9F99\u5934\u4F1A\u5982\u6B64\u6D88\u6C89\u3002</p><p>\u300C\u6211\u859B\u957F\u9E70\u6709\u773C\u65E0\u73E0\uFF0C\u6CA1\u80FD\u65E9\u4E00\u65E5\u53D1\u73B0\u5B59\u67D0\u7684\u5978\u8BA1\uFF0C\u8BEF\u4E86\u6D1B\u9A6C\u5E2E\u5144\u5F1F\u7684\u6027\u547D\uFF0C\u518D\u65E0\u989C\u505A\u6B64\u5E2E\u4E3B\u2026\u2026\u300D</p><p>\u4F17\u4EBA\u773C\u5149\u90FD\u671B\u5411\u9893\u7136\u5FC3\u6B7B\u7684\u859B\u957F\u9E70\uFF0C\u9759\u542C\u4E0B\u6587\u3002</p><p>\u300C\u5F85\u706D\u4E86\u5E7F\u9633\u5E2E\uFF0C\u9664\u6389\u5978\u8D3C\u5B59\u540C\u8F89\u4E4B\u540E\uFF0C\u5728\u4E0B\u7ACB\u5373\u9000\u4F4D\uFF0C\u7531\u5C0F\u5973\u63A5\u4EFB\u957F\u9E70\u4F1A\u5E2E\u4E3B\u4E4B\u4F4D\u3002\u300D</p><p>\u5802\u4E0B\u53CD\u5E94\u7075\u654F\u4E4B\u8F88\u7ACB\u65F6\u5FC3\u4E0B\u6697\u8D5E\uFF0C\u859B\u957F\u9E70\u8FD9\u4E00\u624B\u4EE5\u9000\u4E3A\u8FDB\uFF0C\u505A\u5F97\u771F\u662F\u6F02\u4EAE\uFF0C\u65E2\u635E\u4E86\u5B9E\u60E0\uFF0C\u53C8\u5835\u4E86\u4F17\u4EBA\u7684\u5634\u3002\u4E00\u65E6\u706D\u4E86\u5E7F\u9633\u5E2E\uFF0C\u8FD9\u6D1B\u9633\u57CE\u5C31\u662F\u957F\u9E70\u4F1A\u7684\u5929\u4E0B\u4E86\u3002</p><p>\u53EA\u662F\u2026\u2026\u5B59\u540C\u8F89\u771F\u662F\u90A3\u79CD\u5978\u8BC8\u4E4B\u8F88\uFF1F</p><p>\u859B\u957F\u9E70\u52C9\u5F3A\u632F\u4F5C\u7CBE\u795E\uFF0C\u8BF4\u9053\uFF1A\u300C\u672C\u5E2E\u5F1F\u5B50\u542C\u4EE4\u3002\u300D\u4ED6\u6307\u7740\u4E00\u76F4\u7AD9\u5728\u8EAB\u8FB9\u7684\u77EE\u5C0F\u6C49\u5B50\uFF0C\u300C\u8FD9\u4F4D\u5BAB\u5927\u4FA0\u662F\u65B0\u8FD1\u6295\u5954\u672C\u5E2E\u7684\u58EE\u58EB\uFF0C\u7531\u4ED6\u5E26\u9886\u5927\u5BB6\u56F4\u527F\u5E7F\u9633\u5E2E\u3002\u300D</p><p>\u5BAB\u767D\u7FBD\u8DE8\u524D\u4E00\u6B65\uFF0C\u6602\u7136\u9053\uFF1A\u300C\u5728\u4E0B\u8A93\u53D6\u5B59\u8D3C\u7684\u9996\u7EA7\u732E\u4E8E\u5927\u9F99\u5934\u5EA7\u4E0B\uFF01\u300D\u8BF4\u7F62\u5F84\u884C\u8C03\u96C6\u4EBA\u624B\uFF0C\u5206\u6D3E\u5E03\u7F6E\u3002</p><p>\u859B\u957F\u9E70\u5446\u5446\u5750\u5728\u6905\u4E2D\uFF0C\u8111\u4E2D\u7FFB\u7FFB\u6EDA\u6EDA\u90FD\u662F\u59BB\u5B50\u548C\u5973\u513F\u7684\u8EAB\u5F71\u3002\u8FD8\u6709\u8179\u5185\u7684\u4E24\u679A\u4E39\u836F\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u957F\u9E70\u4F1A\u540E\u5802\u7684\u4E00\u95F4\u5367\u5BA4\u5185\uFF0C\u5373\u5C06\u6210\u4E3A\u5E2E\u4E3B\u7684\u859B\u6B23\u598D\uFF0C\u8D64\u88F8\u88F8\u8EBA\u5728\u51B0\u51B7\u7684\u8840\u6CCA\u4E2D\u3002\u8FD9\u4E9B\u55DC\u8840\u7684\u6076\u9B54\uFF0C\u6CA1\u6709\u4E00\u4E2A\u4EBA\u56E0\u4E3A\u5979\u662F\u5904\u5973\u800C\u7A0D\u6709\u601C\u60DC\uFF0C\u53CD\u800C\u53D8\u672C\u52A0\u5229\uFF0C\u5C06\u5979\u6298\u78E8\u5F97\u5B8C\u5168\u865A\u8131\u3002</p><p>\u5C11\u5973\u65E0\u529B\u7684\u547C\u5438\u7740\uFF0C\u5C0F\u8179\u8D77\u4F0F\u95F4\uFF0C\u80A1\u80A1\u6D53\u7CBE\u4ECE\u6EF4\u8840\u7684\u82B1\u74E3\u8089\u4E0D\u4F4F\u6D8C\u51FA\u3002\u6240\u6709\u7684\u7F9E\u6DA9\u548C\u75DB\u82E6\u88AB\u65E0\u8FB9\u7684\u754F\u60E7\u6240\u63A9\u76D6\uFF0C\u4EFB\u4F55\u4EBA\u53D1\u51FA\u7684\u4EFB\u4F55\u58F0\u97F3\uFF0C\u90FD\u4F7F\u5979\u53D1\u81EA\u5FC3\u5E95\u7684\u6218\u6817\u3002</p><p>\u69BB\u4E0A\u9732\u51FA\u4E00\u622A\u5C0F\u817F\uFF0C\u867D\u7136\u6CBE\u6EE1\u8840\u8FF9\uFF0C\u4ECD\u80FD\u770B\u51FA\u5149\u6D01\u767D\u5AE9\u7684\u672C\u8272\u3002\u4F46\u987A\u7740\u67D4\u7F8E\u7684\u66F2\u7EBF\u5411\u4E0A\uFF0C\u5927\u817F\u6839\u90E8\u5374\u662F\u4E00\u7247\u89E6\u76EE\u60CA\u5FC3\u7684\u8840\u6C61\u3002</p><p>\u5973\u4EBA\u7684\u6027\u5668\u5DF2\u88AB\u5B8C\u5168\u6495\u88C2\uFF0C\u9634\u5507\u50CF\u7FFB\u5F00\u7684\u7EB8\u5F20\u822C\u88AB\u6380\u5230\u8179\u80A1\u90E8\u4F4D\uFF0C\u9634\u961C\u88C2\u5F00\u4E00\u9053\u952F\u9F7F\u72B6\u7684\u4F24\u53E3\uFF0C\u7FFB\u5377\u7684\u5AE9\u8089\u4E2D\uFF0C\u751A\u81F3\u80FD\u770B\u5230\u767D\u68EE\u68EE\u7684\u803B\u9AA8\u3002\u5706\u7B52\u822C\u7684\u9634\u9053\u6574\u4E2A\u626F\u5230\u4F53\u5916\uFF0C\u9690\u85CF\u7684\u8089\u58C1\u5B8C\u5168\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u7EA2\u8273\u8273\u4E00\u7247\u3002\u9634\u9053\u5C3D\u5934\u53EA\u5269\u4E0B\u51E0\u7F15\u7834\u788E\u7684\u5AE9\u8089\u3002</p><p>\u5973\u4EBA\u7684\u4E24\u53EA\u4E73\u623F\u66F4\u662F\u60E8\u4E0D\u5FCD\u7779\u3002\u5176\u4E2D\u4E00\u53EA\u88AB\u6495\u5F97\u56DB\u5206\u4E94\u88C2\uFF0C\u50CF\u4E00\u675F\u8840\u8089\u7684\u5E03\u6761\u5806\u5728\u80F8\u524D\u3002\u53E6\u4E00\u53EA\u5927\u81F4\u8FD8\u7B97\u5B8C\u6574\uFF0C\u4F46\u8868\u9762\u5E03\u6EE1\u6DF1\u6DF1\u7684\u6293\u75D5\uFF0C\u6709\u4E00\u6761\u4ECE\u4E73\u6839\u76F4\u5230\u4E73\u6655\uFF0C\u6DF1\u53EF\u76C8\u6307\uFF0C\u51E0\u4E4E\u5C06\u4E73\u623F\u5206\u6210\u4E24\u534A\u3002\u5973\u4EBA\u5634\u91CC\u54AC\u7740\u4E00\u5757\u4E09\u89D2\u5F62\u7684\u56CA\u72B6\u7269\u4F53\uFF0C\u4E0A\u9762\u51DD\u56FA\u7684\u9C9C\u8840\u5DF2\u7ECF\u53D8\u5F97\u53D1\u9ED1\u3002\u4ED4\u7EC6\u770B\u53BB\uFF0C\u624D\u80FD\u8BA4\u51FA\u90A3\u662F\u54AC\u5269\u4E00\u534A\u7684\u5B50\u5BAB\u3002</p><p>\u8FD9\u4E2A\u5403\u4E0B\u81EA\u5DF1\u5B50\u5BAB\u7684\u5973\u4EBA\uFF0C\u5C31\u662F\u957F\u9E70\u4F1A\u5E2E\u4E3B\u859B\u957F\u9E70\u7684\u592B\u4EBA\uFF0C\u540C\u65F6\u4E5F\u662F\u4E0B\u4EFB\u5E2E\u4E3B\u7684\u6BCD\u4EB2\u3002</p><p>\u524D\u9662\u7EB7\u4E71\u7684\u811A\u6B65\u58F0\u9690\u9690\u4F20\u6765\uFF0C\u76D8\u819D\u9759\u5750\u7684\u6155\u5BB9\u9F99\u5C55\u76EE\u4E00\u7B11\uFF0C\u300C\u9053\u957F\u53EF\u613F\u4E0E\u6211\u540C\u8D74\u7389\u9E21\u574A\uFF1F\u300D</p><p>\u7075\u7389\u632F\u8863\u800C\u8D77\uFF0C\u300C\u6562\u4E0D\u4ECE\u547D\u3002\u300D</p><p>\u4E5F\u4E0D\u89C1\u6155\u5BB9\u9F99\u6709\u4F55\u52A8\u4F5C\uFF0C\u4FBF\u65E0\u58F0\u65E0\u606F\u5730\u7ACB\u5728\u95E8\u65C1\uFF0C\u300C\u8BF7\u77F3\u4F9B\u5949\u901A\u77E5\u91D1\u957F\u8001\uFF0C\u5C06\u592B\u4EBA\u548C\u5C11\u592B\u4EBA\u632A\u81F3\u6B64\u5904\u3002\u300D</p><p>\u77F3\u880D\u8EAC\u8EAB\u5E94\u8BFA\u3002</p><p>\u6CA1\u6709\u4EBA\u518D\u53BB\u770B\u859B\u6B23\u598D\u4E00\u773C\uFF0C\u4FBF\u90FD\u626C\u957F\u800C\u53BB\u3002\u95E8\u5EC9\u6765\u56DE\u6447\u6446\uFF0C\u65F6\u660E\u65F6\u6697\u7684\u5149\u7EBF\u4E2D\uFF0C\u6620\u51FA\u6EE1\u5BA4\u7684\u8840\u8165\uFF0C\u5730\u4E0A\u51C4\u8273\u7684\u5C11\u5973\uFF0C\u8FD8\u6709\u69BB\u95F4\u7834\u788E\u7684\u5973\u5C38\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E00\u4E2A\u65F6\u8FB0\u540E\uFF0C\u6D88\u606F\u4F20\u6765\uFF0C\u957F\u9E70\u4F1A\u52BF\u5982\u7834\u7AF9\uFF0C\u4E00\u8DEF\u6740\u5165\u7389\u9E21\u574A\u3002\u4E00\u4F4D\u521A\u521A\u52A0\u5165\u957F\u9E70\u4F1A\u7684\u9AD8\u624B\u72EC\u6597\u5B59\u540C\u8F89\uFF0C\u5728\u7B2C\u56DB\u5341\u56DB\u62DB\uFF0C\u4E00\u5200\u65A9\u4E0B\u5B59\u540C\u8F89\u7684\u5934\u9885\u3002\u5E7F\u9633\u5E2E\u5C31\u6B64\u7070\u98DE\u70DF\u706D\u3002</p><p>\u7B2C\u4E8C\u5929\u859B\u957F\u9E70\u53EC\u96C6\u6B66\u6797\u540C\u9053\uFF0C\u5F53\u573A\u9000\u4F4D\uFF0C\u7531\u5973\u513F\u859B\u6B23\u598D\u7EE7\u4EFB\u957F\u9E70\u4F1A\u5E2E\u4E3B\u3002</p><p>\u4E00\u6CE2\u672A\u5E73\u4E00\u6CE2\u53C8\u8D77\uFF0C\u6B63\u5F53\u6C5F\u6E56\u4E2D\u4EBA\u76EE\u4E0D\u6687\u63A5\u65F6\uFF0C\u53C8\u4F20\u6765\u4E00\u4E2A\u60CA\u4EBA\u7684\u6D88\u606F\uFF1A\u957F\u9E70\u4F1A\u65B0\u4EFB\u5E2E\u4E3B\u4E0B\u4EE4\uFF0C\u5728\u7389\u9E21\u574A\u5E7F\u9633\u5E2E\u65E7\u5740\u5EFA\u8D77\u4E86\u6D1B\u9633\u6700\u5927\u7684\u5993\u9662\u2014\u2014\u9999\u6708\u697C\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u5E7F\u9633\u5E2E\u6B8B\u4F59\u7684\u5973\u5B50\u5C3D\u6570\u88AB\u5E9F\u53BB\u6B66\u529F\uFF0C\u9001\u81F3\u9999\u6708\u697C\u63A5\u5BA2\u3002\u5B59\u540C\u8F89\u7684\u592B\u4EBA\u4E0D\u582A\u53D7\u8FB1\uFF0C\u81EA\u6740\u672A\u9042\uFF0C\u88AB\u9501\u5728\u5730\u7A96\u4EFB\u4EBA\u6DEB\u8FB1\u3002</p><p>\u957F\u9E70\u4F1A\u7684\u5012\u884C\u9006\u65BD\u6FC0\u8D77\u6D1B\u9633\u6B66\u6797\u4EBA\u58EB\u7684\u6124\u6168\uFF0C\u591A\u6B21\u58F0\u8BA8\u5176\u975E\u3002\u4F46\u859B\u6B23\u598D\u4F5C\u98CE\u8FE5\u5F02\u5176\u7236\uFF0C\u884C\u4E8B\u72E0\u8FA3\u5F02\u5E38\uFF0C\u5BF9\u53CD\u5BF9\u8005\u6216\u6740\u6216\u527F\uFF0C\u6BEB\u4E0D\u7559\u60C5\u3002\u957F\u9E70\u4F1A\u7684\u51FA\u683C\u4E3E\u52A8\u53C8\u5F97\u5230\u5B98\u5E9C\u7684\u9ED8\u8BB8\uFF0C\u4E0D\u51FA\u4E00\u6708\uFF0C\u6D1B\u9633\u4FBF\u88AB\u957F\u9E70\u4F1A\u4E00\u5E2E\u72EC\u9738\u3002</p><p>\u597D\u5728\u859B\u6B23\u598D\u5E76\u672A\u65A9\u5C3D\u6740\u7EDD\uFF0C\u53EA\u8981\u4E0D\u4E0E\u957F\u9E70\u4F1A\u4E3A\u654C\uFF0C\u5927\u5BB6\u4E95\u6C34\u4E0D\u72AF\u6CB3\u6C34\uFF0C\u5012\u4E5F\u80FD\u76F8\u5B89\u65E0\u4E8B\u3002\u8FD9\u6837\u4EBA\u4EEC\u4E5F\u5C31\u9010\u6E10\u63A5\u53D7\u4E86\u73B0\u5B9E\uFF0C\u53CD\u5BF9\u7684\u58F0\u97F3\u8D8A\u6765\u8D8A\u5C0F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u540E\u6765\u7D2B\u73AB\u66FE\u7ECF\u95EE\u8FC7\u6155\u5BB9\u9F99\uFF0C\u5047\u5982\u5F53\u65F6\u90A3\u51E0\u4EBA\u5E76\u975E\u662F\u6D1B\u9633\u5E2E\u4F1A\u7684\u4FE1\u4F7F\uFF0C\u5C82\u4E0D\u662F\u8BEF\u4F1A\u4E86\u5417\uFF1F</p><p>\u6155\u5BB9\u9F99\u95FB\u8A00\u53EA\u662F\u4E00\u7B11\uFF0C\u5E76\u6CA1\u6709\u56DE\u7B54\u3002</p><p>\u7D2B\u73AB\u7ACB\u523B\u77E5\u9053\u81EA\u5DF1\u7684\u95EE\u9898\u975E\u5E38\u611A\u8822\u3002</p><p>\u5BF9\u6155\u5BB9\u9F99\u6765\u8BF4\uFF0C\u8BEF\u6740\u53C8\u5982\u4F55\uFF1F</p><p>\u5F53\u65F6\u7684\u5929\u6C14\u975E\u5E38\u708E\u70ED\uFF0C\u53EF\u6155\u5BB9\u9F99\u9759\u9759\u5750\u90A3\u91CC\uFF0C\u4EFF\u4F5B\u4E07\u53E4\u7384\u51B0\uFF0C\u6CA1\u6709\u4E00\u4E1D\u6C57\u610F\u3002</p><p>\u4ED6\u4EEC\u4F4F\u5728\u957F\u9E70\u4F1A\u7684\u540E\u9662\uFF0C\u4FE8\u7136\u5982\u5E2E\u4E2D\u4E4B\u5E2E\u3002</p><p>\u859B\u957F\u9E70\u88AB\u5B89\u7F6E\u5728\u9662\u4FA7\u7684\u4E00\u95F4\u5C0F\u623F\u5B50\u5185\uFF0C\u859B\u6B23\u598D\u5374\u6839\u672C\u6CA1\u6709\u81EA\u5DF1\u7684\u4F4F\u5904\u3002\u5728\u5916\u9762\u5979\u662F\u79F0\u5C0A\u5E1D\u90FD\u7684\u957F\u9E70\u4F1A\u5E2E\u4E3B\uFF0C\u56DE\u5230\u540E\u9662\u5374\u8FDE\u767D\u6C0F\u59D0\u59B9\u8FD9\u6837\u7684\u5A62\u5973\u4E5F\u4E0D\u5982\u3002\u6BCF\u665A\uFF0C\u859B\u6B23\u598D\u90FD\u8981\u50CF\u9999\u6708\u697C\u7684\u5973\u5B50\u4E00\u6837\uFF0C\u5A9A\u7B11\u7740\u732E\u51FA\u81EA\u5DF1\u7684\u8089\u4F53\u3002\u4E0E\u90A3\u4E9B\u5993\u5973\u4E0D\u540C\u7684\u662F\uFF0C\u5979\u7684\u591C\u665A\uFF0C\u603B\u662F\u5728\u4E0D\u540C\u7684\u69BB\u4E0A\u5EA6\u8FC7\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u62FF\u7740\u8F7B\u7F57\u56E2\u6247\uFF0C\u8F7B\u8F7B\u821E\u52A8\uFF0C\u5E2E\u6BCD\u4EB2\u62C2\u53BB\u590F\u65E5\u7684\u9177\u70ED\u3002\u8427\u4F5B\u5974\u5B89\u8BE6\u7684\u5750\u5728\u6905\u4E2D\uFF0C\u5145\u6EE1\u7231\u601C\u7684\u51DD\u89C6\u7740\u5973\u513F\u3002\u6BCD\u5973\u4FE9\u5750\u5728\u7FA4\u82B3\u4E89\u8273\u7684\u82B1\u56ED\u4E2D\uFF0C\u4EFF\u4F5B\u81EA\u82B1\u95F4\u98DE\u51FA\u7684\u7CBE\u7075\uFF0C\u51DD\u805A\u4E86\u4E16\u95F4\u6240\u6709\u7684\u7F8E\u4E3D\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u4E0D\u60C5\u613F\u5730\u6253\u7834\u8FD9\u5BC2\u9759\uFF0C\u8D70\u5230\u8427\u4F5B\u5974\u8EAB\u8FB9\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u5B69\u513F\u6276\u4F60\u56DE\u623F\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u6447\u4E86\u6447\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u5E72\u8106\u5750\u5728\u5730\u4E0A\uFF0C\u4E0E\u59B9\u59B9\u4E00\u4EBA\u4E00\u8FB9\u62E5\u7740\u6BCD\u4EB2\uFF0C\u7136\u540E\u9664\u4E0B\u8427\u4F5B\u5974\u7684\u5F13\u978B\uFF0C\u5C06\u5C0F\u5DE7\u7684\u7EA4\u8DB3\u6367\u5728\u624B\u4E2D\u534A\u662F\u73A9\u5F04\uFF0C\u534A\u662F\u6309\u6469\u7684\u7EC6\u7EC6\u63C9\u634F\u3002\u300C\u5A18\uFF0C\u8FD9\u4E00\u4E2A\u6708\u4F60\u90FD\u6CA1\u6709\u8DDF\u5B69\u513F\u8BF4\u8BDD\uFF0C\u662F\u4E0D\u662F\u751F\u5B69\u513F\u7684\u6C14\u4E86\uFF1F\u300D</p><p>\u5176\u5B9E\u8427\u4F5B\u5974\u4E0D\u4EC5\u6CA1\u6709\u4E0E\u4ED6\u8BF4\u8BDD\uFF0C\u8FDE\u7D2B\u73AB\u4E5F\u6CA1\u6709\u542C\u5230\u8FC7\u5979\u7684\u58F0\u97F3\u3002\u5979\u662F\u8BB8\u4E0B\u7684\u95ED\u53E3\u613F\uFF0C\u7948\u6C42\u4F5B\u7956\u4FDD\u4F51\u5973\u513F\u2014\u2014\u5BF9\u4E8E\u56DB\u80A2\u4FF1\u5E9F\u7684\u767E\u82B1\u89C2\u97F3\u6765\u8BF4\uFF0C\u8FD9\u4E5F\u662F\u5979\u552F\u4E00\u80FD\u505A\u7684\u4E8B\u3002</p><p>\u6155\u5BB9\u9F99\u63C9\u5B8C\u4E00\u53EA\u811A\uFF0C\u53C8\u6367\u8D77\u8427\u4F5B\u5974\u53E6\u4E00\u53EA\u811A\u6162\u6162\u63C9\u634F\u3002\u534A\u664C\uFF0C\u4ED6\u505C\u4E0B\u624B\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\u771F\u662F\u751F\u5B69\u513F\u7684\u6C14\u4E86\u3002\u300D</p><p>\u867D\u7136\u5634\u89D2\u6302\u7740\u7B11\u5BB9\uFF0C\u4F46\u58F0\u97F3\u91CC\u5374\u5E26\u7740\u9634\u51B7\u7684\u5BD2\u610F\u3002</p><p>\u7D2B\u73AB\u8FDE\u5FD9\u63A5\u53E3\u9053\uFF1A\u300C\u5A18\u820C\u5934\u4E0A\u7684\u4F24\u52BF\u8FD8\u6CA1\u597D\uFF0C\u8BF4\u8BDD\u4E0D\u65B9\u4FBF\uFF0C\u51B5\u4E14\u53C8\u4E0D\u662F\u4E0D\u8DDF\u4F60\u4E00\u4E2A\u4EBA\u8BF4\u8BDD\uFF0C\u6211\u4E5F\u6CA1\u542C\u5230\u5462\u3002\u300D</p><p>\u300C\u5A18\uFF0C\u4F60\u8BF4\u8BDD\u554A\u2026\u2026\u300D\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u6108\u53D1\u67D4\u548C\u3002</p><p>\u8427\u4F5B\u5974\u9759\u6084\u6084\u95ED\u4E0A\u7F8E\u76EE\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u6155\u5BB9\u9F99\u6162\u6162\u626D\u8FC7\u8138\uFF0C\u770B\u7740\u7D2B\u73AB\u9053\uFF1A\u300C\u8863\u670D\u8131\u4E86\uFF0C\u8BA9\u54E5\u54E5\u5728\u8FD9\u513F\u723D\u4E00\u4E0B\u3002\u300D</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u7565\u4E00\u6743\u8861\uFF0C\u6BC5\u7136\u89E3\u5F00\u8863\u94AE\u3002\u5979\u660E\u767D\u4E00\u65E6\u6FC0\u6012\u4ED6\uFF0C\u8C01\u90FD\u4E0D\u77E5\u9053\u8FD9\u4E2A\u79BD\u517D\u4F1A\u505A\u51FA\u4EC0\u4E48\u4E8B\u6765\u3002</p><p>\u5728\u6012\u653E\u7684\u82B1\u4E1B\u4E2D\uFF0C\u73AB\u7470\u4ED9\u5B50\u8131\u6389\u6700\u540E\u4E00\u4EF6\u4EB5\u8863\uFF0C\u5C06\u7F8E\u5999\u7684\u7389\u4F53\u5448\u73B0\u5728\u9633\u5149\u4E0B\u3002</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u4F5C\u58F0\uFF0C\u53EA\u51B7\u51B7\u770B\u7740\u5979\u3002</p><p>\u4E24\u4EBA\u50F5\u7ACB\u7247\u523B\uFF0C\u7D2B\u73AB\u67D4\u987A\u5730\u659C\u501A\u5728\u51C9\u4EAD\u7684\u5ECA\u6905\u4E0A\uFF0C\u7389\u817F\u5FAE\u5206\uFF0C\u4E3B\u52A8\u5265\u5F00\u82B1\u74E3\uFF0C\u9732\u51FA\u6E7F\u6DA6\u7EA2\u5AE9\u7684\u5165\u53E3\uFF0C\u7B49\u5F85\u4ED6\u7684\u8FDB\u5165\u3002</p><p>\u300C\u7FFB\u8FC7\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u4E56\u4E56\u8D77\u8EAB\uFF0C\u7565\u4E00\u72B9\u8C6B\uFF0C\u9009\u62E9\u4E86\u76F4\u7ACB\u7684\u59FF\u52BF\uFF0C\u5F13\u8EAB\u6309\u4F4F\u6276\u680F\uFF0C\u67D4\u67D4\u633A\u8D77\u7C89\u5AE9\u7684\u96EA\u81C0\u3002</p><p>\u300C\u63B0\u5F00\u3002\u300D</p><p>\u67D4\u82E5\u65E0\u9AA8\u7684\u7EA4\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u5F20\u5F00\u7389\u80A1\u95F4\u7684\u7F9E\u5904\u3002</p><p>\u300C\u4E0A\u8FB9\u3002\u300D</p><p>\u7D2B\u73AB\u95FB\u8A00\u4E00\u6014\u3002</p><p>\u300C\u54E5\u54E5\u8981\u64CD\u4F60\u7684\u5C41\u773C\u3002\u300D</p><p>\u6D51\u8EAB\u7684\u8840\u6DB2\u90FD\u6D8C\u5230\u8111\u90E8\uFF0C\u7D2B\u73AB\u987F\u65F6\u50F5\u4F4F\u4E86\u3002</p>',67);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
