import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_76" tabindex="-1"><a class="header-anchor" href="#_76" aria-hidden="true">#</a> 76</h1><p>\u6155\u5BB9\u9F99\u795E\u60C5\u606C\u6DE1\uFF0C\u4F46\u4E0D\u5BB9\u7F6E\u7591\u7684\u53E3\u543B\u5374\u6CA1\u6709\u4E1D\u6BEB\u5546\u91CF\u7684\u4F59\u5730\u3002</p><p>\u624B\u6307\u50F5\u4E86\u7247\u523B\uFF0C\u53C8\u5F00\u59CB\u7F13\u7F13\u79FB\u52A8\u3002\u7EC6\u6ED1\u7684\u81C0\u8089\u4E1D\u7EF8\u822C\u4ECE\u6307\u5C16\u6ED1\u5F00\uFF0C\u9732\u51FA\u7C89\u7EA2\u8272\u7684\u5C0F\u5DE7\u83CA\u809B\u3002</p><p>\u4EAD\u5916\u9A84\u9633\u4F3C\u706B\uFF0C\u8EAB\u4E0B\u7684\u808C\u80A4\u6E29\u51C9\u5982\u7389\u3002\u6155\u5BB9\u9F99\u5BF9\u59B9\u59B9\u7684\u6E29\u987A\u5927\u4E3A\u6EE1\u610F\uFF0C\u4ED6\u4E86\u659C\u4E86\u4E00\u773C\u6C89\u9ED8\u7684\u6BCD\u4EB2\uFF0C\u62AC\u624B\u5728\u7D2B\u73AB\u81C0\u4E0A\u62CD\u4E86\u62CD\uFF0C\u8089\u68D2\u4E00\u4E3E\uFF0C\u9876\u4F4F\u83CA\u6D1E\u3002</p><p>\u300C\u54E5\u2026\u2026\u300D\u7D2B\u73AB\u8F7B\u8F7B\u53EB\u4E86\u4E00\u58F0\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u2026\u2026\u6CA1\u4EC0\u4E48\u2026\u2026\u300D\u867D\u7136\u8FD9\u6837\u8BF4\uFF0C\u7D2B\u73AB\u7684\u5A07\u8EAF\u5374\u7981\u4E0D\u4F4F\u8F7B\u8F7B\u98A4\u6296\u3002\u90A3\u79CD\u542B\u7F9E\u5FCD\u75DB\u7684\u52A8\u4EBA\u4E4B\u6001\uFF0C\u4EE4\u6155\u5BB9\u9F99\u601C\u610F\u5927\u8D77\uFF0C\u4ED6\u77E5\u9053\u81EA\u5DF1\u7684\u9633\u7269\u592A\u8FC7\u9A87\u4EBA\uFF0C\u59B9\u59B9\u867D\u7136\u5929\u8D4B\u5999\u7269\uFF0C\u4F46\u6BCF\u6B21\u4EA4\u5408\u4E5F\u652F\u6491\u5F97\u8F9B\u82E6\u4E07\u5206\u3002\u6B64\u65F6\u660E\u77E5\u540E\u5EAD\u5F00\u82DE\u7684\u5267\u75DB\uFF0C\u5979\u4ECD\u7136\u80AF\u542C\u4ECE\u5429\u5490\u2026\u2026</p><p>\u59B9\u59B9\u6BD5\u7ADF\u8FD8\u5C0F\uFF0C\u518D\u8FC7\u4E9B\u65E5\u5B50\u4E5F\u4E0D\u8FDF\u3002\u6155\u5BB9\u9F99\u72E0\u72E0\u5FC3\uFF0C\u6297\u62D2\u7740\u7F8E\u809B\u7684\u8BF1\u60D1\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u8BF4\u513F\u5B50\u8FD9\u4F1A\u513F\u662F\u64CD\u4F60\u7684\u5C41\u773C\u513F\u597D\u5462\uFF0C\u8FD8\u662F\u64CD\u59B9\u59B9\u597D\u5462\uFF1F\u300D</p><p>\u7F8E\u5987\u54AC\u7740\u7EA2\u5507\u4E00\u8A00\u4E0D\u53D1\uFF0C\u8138\u4E0A\u5374\u6E10\u6E10\u7EA2\u4E86\u3002\u540E\u5EAD\u5F7B\u5E95\u6495\u88C2\u4E4B\u540E\uFF0C\u809B\u8089\u53CD\u800C\u6108\u53D1\u654F\u611F\u3002\u4E0D\u4EC5\u5728\u5355\u7EAF\u7684\u809B\u95E8\u6027\u4EA4\u4E2D\u5C31\u80FD\u8FBE\u5230\u9AD8\u6F6E\uFF0C\u751A\u81F3\u6BCF\u6B21\u79FD\u7269\u6D41\u51FA\uFF0C\u90FD\u4F1A\u6709\u5F3A\u70C8\u7684\u5FEB\u611F\u3002\u5979\u4E0D\u77E5\u9053\u662F\u56E0\u4E3A\u513F\u5B50\u7ED9\u5979\u65BD\u4E86\u8DB3\u4EE5\u4EE4\u77F3\u5973\u53D8\u4E3A\u6DEB\u5987\u7684\u711A\u60C5\u818F\uFF0C\u8FD8\u4EE5\u4E3A\u662F\u81EA\u5DF1\u53D8\u5F97\u6DEB\u8D31\u3002</p><p>\u6B64\u65F6\u542C\u5230\u513F\u5B50\u66A7\u6627\u7684\u53E3\u543B\uFF0C\u8427\u4F5B\u5974\u7ACB\u65F6\u611F\u53D7\u5230\u540E\u5EAD\u4F20\u6765\u7684\u9EBB\u75D2\uFF0C\u4F3C\u4E4E\u809B\u8089\u5728\u6E34\u671B\u63D2\u5165\u3002\u5FCD\u8010\u7247\u523B\uFF0C\u9965\u6E34\u975E\u4F46\u6CA1\u6709\u6D88\u892A\uFF0C\u79D8\u5904\u53CD\u800C\u6E7F\u4E86\u3002\u7F8E\u5987\u96BE\u8FC7\u7684\u4FA7\u8FC7\u8138\uFF0C\u4E3A\u81EA\u5DF1\u6DEB\u8361\u7684\u8089\u4F53\u800C\u7F9E\u6127\u3002</p><p>\u6155\u5BB9\u9F99\u6CA1\u60F3\u5230\u6BCD\u4EB2\u4ECD\u65E7\u4FDD\u6301\u6C89\u9ED8\uFF0C\u6309\u9053\u7406\u5979\u5E94\u8BE5\u6BC5\u7136\u4EE5\u8EAB\u76F8\u4EE3\uFF0C\u5FC3\u7518\u60C5\u613F\u5730\u8BA9\u81EA\u5DF1\u628A\u5979\u64CD\u4E2A\u6B7B\u53BB\u6D3B\u6765\u2026\u2026</p><p>\u6B63\u7EB3\u95F7\u95F4\uFF0C\u7D2B\u73AB\u7EA4\u624B\u4E00\u7FFB\uFF0C\u63E1\u4F4F\u4ED6\u7684\u9633\u5177\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6765\u5427\u3002\u300D\u58F0\u97F3\u867D\u7136\u575A\u51B3\uFF0C\u5374\u5FCD\u4E0D\u4F4F\u53D1\u98A4\u3002</p><p>\u6155\u5BB9\u9F99\u64CD\u5973\u4EBA\u4ECE\u6765\u6CA1\u6709\u72B9\u8C6B\u8FC7\uFF0C\u4F46\u8FD9\u6B21\u9762\u5BF9\u59B9\u59B9\u5A07\u5AE9\u65E0\u6BD4\u7684\u83CA\u809B\u5374\u6709\u4E9B\u8FDF\u7591\u4E86\u3002\u4ED6\u5728\u5C11\u5973\u81C0\u4E0A\u629A\u5F04\u826F\u4E45\uFF0C\u7136\u540E\u4E2D\u6307\u4E00\u63A2\uFF0C\u6307\u5C16\u62B5\u4F4F\u83CA\u6D1E\u7F13\u7F13\u4F38\u5165\u3002\u5C0F\u5DE7\u7684\u809B\u6D1E\u6536\u7F29\u7740\u5C06\u6307\u7AEF\u541E\u5165\uFF0C\u6E29\u8F6F\u7684\u809B\u8089\u53C8\u7D27\u53C8\u5BC6\uFF0C\u7F8E\u5999\u5F97\u4EE4\u4EBA\u7A92\u606F\u3002</p><p>\u624B\u6307\u4E00\u8282\u8282\u8FDB\u5165\u7C89\u7EA2\u8272\u7684\u96CF\u809B\uFF0C\u63A5\u7740\u7F13\u7F13\u63D2\u9001\u8D77\u6765\u3002\u7D2B\u73AB\u5F13\u8170\u4E3E\u81C0\uFF0C\u5C48\u8FB1\u5730\u4EFB\u4EC7\u4EBA\u73A9\u5F04\u81EA\u5DF1\u6700\u9690\u79D8\u7684\u90E8\u4F4D\uFF0C\u5979\u5FC3\u5934\u7F9E\u6124\u81F3\u6781\uFF0C\u8138\u4E0A\u5374\u4E0D\u6562\u9732\u51FA\u4E00\u4E1D\u6068\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u63D2\u5F04\u591A\u65F6\uFF0C\u8089\u68D2\u65E9\u5DF2\u80C0\u5F97\u751F\u75BC\u3002\u5F85\u5AE9\u8089\u6E10\u6E10\u677E\u9A70\uFF0C\u4ED6\u4E5F\u4E0D\u518D\u7406\u4F1A\u59B9\u59B9\u662F\u5426\u4F1A\u53D7\u4F24\uFF0C\u633A\u8170\u9876\u4F4F\u83CA\u6D1E\u3002</p><p>\u7389\u4EBA\u7C89\u8EAF\u987F\u65F6\u7EF7\u7D27\uFF0C\u7D2B\u73AB\u7D27\u5F20\u5F97\u5DEE\u70B9\u513F\u8981\u5927\u54ED\u4E00\u573A\u3002\u5979\u4E00\u5411\u6700\u662F\u6015\u75DB\uFF0C\u7834\u4F53\u65F6\u4E0D\u77E5\u6D41\u8FC7\u591A\u5C11\u773C\u6CEA\uFF0C\u4F55\u51B5\u7834\u809B\u7684\u75DB\u695A\u4F1A\u8FDC\u8FC7\u4E8E\u5F53\u65E5\u3002</p><p>\u7EC6\u5BC6\u7EA4\u7F8E\u7684\u83CA\u7EB9\u5728\u9F9F\u5934\u4E0B\u7EFD\u5F00\uFF0C\u6700\u540E\u53EA\u5269\u4E0B\u4E00\u5708\u7A84\u7A84\u7684\u7C89\u7EA2\u8272\u3002\u4F46\u5149\u4EAE\u7684\u9F9F\u5934\u624D\u521A\u521A\u8FDB\u5165\u3002</p><p>\u6155\u5BB9\u9F99\u5438\u4E86\u53E3\u6C14\uFF0C\u633A\u8EAB\u4E00\u9001\uFF0C\u83CA\u809B\u7ACB\u523B\u7EFD\u5F00\u51E0\u6761\u7EC6\u7EC6\u7684\u900F\u660E\u88C2\u53E3\uFF0C\u7728\u773C\u4E4B\u95F4\uFF0C\u4F24\u53E3\u4FBF\u5145\u6EE1\u9C9C\u8840\uFF0C\u7D2B\u73AB\u300C\u545C\u300D\u7684\u4E00\u58F0\u54ED\u4E86\u8D77\u6765\u3002</p><p>\u6C89\u9ED8\u7684\u8D35\u5987\u5FC3\u5982\u5200\u7EDE\uFF0C\u773C\u6CEA\u6D41\u5F97\u6BD4\u5973\u513F\u8FD8\u591A\u3002</p><p>\u6155\u5BB9\u9F99\u8170\u8EAB\u5FAE\u5FAE\u4E00\u9000\uFF0C\u5F85\u9F9F\u5934\u6CBE\u4E0A\u9C9C\u8840\uFF0C\u53C8\u65CB\u5373\u8FDB\u5165\u3002\u300C\u53FD\u300D\u7684\u4E00\u58F0\uFF0C\u9F9F\u5934\u6CA1\u5165\u83CA\u6D1E\u3002\u6676\u83B9\u7684\u7389\u80A1\u95F4\u9C9C\u8840\u957F\u6D41\uFF0C\u7D2B\u73AB\u75DB\u5F7B\u5FC3\u80BA\uFF0C\u773C\u524D\u4E00\u9ED1\uFF0C\u51E0\u6B32\u6655\u5012\u3002</p><p>\u6155\u5BB9\u9F99\u63FD\u4F4F\u59B9\u59B9\u6447\u6447\u6B32\u5760\u7684\u8170\u80A2\uFF0C\u4E0B\u4F53\u8F7B\u62BD\u7F13\u9001\uFF0C\u53EA\u7528\u9F9F\u5934\u5728\u6E85\u8840\u7684\u809B\u6D1E\u6162\u6162\u8FDB\u51FA\u3002\u521A\u521A\u5F00\u53D1\u7684\u809B\u6D1E\u7565\u663E\u751F\u6DA9\uFF0C\u867D\u6BD4\u6BCD\u4EB2\u7684\u540E\u5EAD\u7D27\u5BC6\uFF0C\u4F46\u9999\u8F6F\u67D4\u6ED1\u7A0D\u6709\u4E0D\u53CA\uFF0C\u770B\u6765\u8FD8\u8981\u597D\u597D\u8C03\u5F04\u2026\u2026</p><p>\u300C\u7980\u5BAB\u4E3B\uFF0C\u8521\u957F\u8001\u3001\u970D\u957F\u8001\u6C42\u89C1\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u66F2\u6307\u4E00\u5F39\uFF0C\u51C9\u4EAD\u4E0A\u7684\u6E58\u7AF9\u5EC9\u5782\u4E86\u4E0B\u6765\uFF0C\u300C\u8BA9\u4ED6\u4EEC\u5728\u5916\u9762\u8BF4\u5427\u3002\u300D</p><p>\u8521\u4E91\u5CF0\u548C\u970D\u72C2\u7130\u5E76\u80A9\u8D70\u5230\u6708\u6D1E\u95E8\u4E0B\uFF0C\u8EAC\u8170\u9053\uFF1A\u300C\u53C2\u89C1\u5BAB\u4E3B\uFF01\u300D</p><p>\u300C\u8521\u957F\u8001\u8BF7\u5750\u3002\u970D\u957F\u8001\u4E5F\u5750\u5427\u3002\u300D</p><p>\u8521\u4E91\u5CF0\u8C22\u8FC7\u5750\u4E0B\uFF0C\u4E3E\u5934\u770B\u5230\u7AF9\u5EC9\u4E0B\u9690\u7EA6\u663E\u9732\u7684\u7389\u4F53\uFF0C\u4E0D\u7531\u5FC3\u4E2D\u5267\u8DF3\uFF0C\u8FDE\u5FD9\u4F4E\u4E0B\u5934\u4E0D\u6562\u518D\u770B\u3002</p><p>\u970D\u72C2\u7130\u8FD9\u8D9F\u5F04\u5F97\u7070\u5934\u571F\u8138\uFF0C\u751F\u6015\u5BAB\u4E3B\u60E9\u7F5A\uFF0C\u65E2\u4E0D\u6562\u62AC\u5934\uFF0C\u66F4\u4E0D\u6562\u5F00\u53E3\u3002\u9662\u4E2D\u987F\u65F6\u5BC2\u9759\u65E0\u58F0\u3002</p><p>\u4E24\u4EBA\u5747\u662F\u8033\u529B\u8FC7\u4EBA\u4E4B\u8F88\uFF0C\u867D\u7136\u9694\u4E86\u5341\u4E08\u7684\u8DDD\u79BB\uFF0C\u8FD8\u662F\u542C\u5230\u51C9\u4EAD\u4E2D\u300C\u5575\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u970D\u72C2\u7130\u5FC3\u5934\u4E00\u8DF3\uFF0C\u8521\u4E91\u5CF0\u5374\u662F\u9762\u7EA2\u8FC7\u8033\u3002</p><p>\u5C11\u503E\uFF0C\u7AF9\u5EC9\u5377\u8D77\uFF0C\u6155\u5BB9\u9F99\u7F13\u6B65\u8D70\u4E0B\u53F0\u9636\uFF0C\u5728\u4ED6\u8EAB\u540E\uFF0C\u9762\u8272\u96EA\u767D\u7684\u73AB\u7470\u4ED9\u5B50\u4FA7\u8EAB\u501A\u5728\u5ECA\u6905\u4E0A\uFF0C\u8EAB\u4E0A\u62AB\u7740\u6DE1\u7EA2\u7684\u7F57\u8863\uFF0C\u8863\u5E26\u8F7B\u5782\u680F\u4E0B\u3002\u4E00\u53EA\u67D4\u7F8E\u7684\u7EA4\u624B\u8272\u5982\u660E\u7389\uFF0C\u8F6F\u8F6F\u642D\u5728\u817F\u4FA7\u3002\u6620\u7740\u8EAB\u524D\u8EAB\u540E\u76DB\u5F00\u7684\u767E\u82B1\uFF0C\u9C9C\u598D\u660E\u5A9A\uFF0C\u5A49\u7EA6\u5982\u753B\uFF0C\u6708\u4F59\u672A\u89C1\uFF0C\u4ED9\u5B50\u53C8\u7F8E\u4E86\u8BB8\u591A\uFF0C\u6BD4\u5C9B\u4E0A\u521D\u89C1\u65F6\u7684\u5A07\u4FCF\uFF0C\u66F4\u6DFB\u4E86\u51E0\u5206\u98CE\u97F5\u2026\u2026\u8521\u4E91\u5CF0\u5FC3\u9189\u795E\u9A70\uFF0C\u5F85\u770B\u5230\u7389\u4EBA\u8138\u4E0A\u7684\u6CEA\u75D5\uFF0C\u5FC3\u91CC\u4E0D\u7531\u4E00\u9635\u5FAE\u5FAE\u7684\u523A\u75DB\u3002</p><p>\u970D\u72C2\u7130\u773C\u91CC\u53EA\u6709\u5BAB\u4E3B\u7684\u795E\u8272\uFF0C\u5BAB\u4E3B\u8D8A\u662F\u9762\u65E0\u8868\u60C5\uFF0C\u4ED6\u5FC3\u91CC\u8D8A\u662F\u4E0D\u5B89\u3002\u6155\u5BB9\u9F99\u773C\u950B\u4E00\u626B\uFF0C\u970D\u72C2\u7130\u8D64\u8138\u987F\u65F6\u53D1\u767D\u3002</p><p>\u6C89\u9ED8\u534A\u664C\u540E\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u8521\u957F\u8001\u6B7C\u706D\u6D1B\u9A6C\u5E2E\uFF0C\u529B\u6297\u6D1B\u9633\u5E2E\u4F1A\uFF0C\u529F\u52B3\u4E0D\u5C0F\u3002\u300D</p><p>\u8521\u4E91\u5CF0\u614C\u5FD9\u62B1\u62F3\u9053\uFF1A\u300C\u5C5E\u4E0B\u65E0\u80FD\uFF0C\u6709\u8D1F\u5BAB\u4E3B\u91CD\u6258\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6446\u4E86\u6446\u624B\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u970D\u957F\u8001\u2026\u2026\u300D</p><p>\u970D\u72C2\u7130\u65E9\u5DF2\u5782\u624B\u800C\u7ACB\uFF0C\u95FB\u8A00\u300C\u5657\u901A\u300D\u8DEA\u5012\uFF0C\u53EB\u9053\uFF1A\u300C\u5C5E\u4E0B\u8BE5\u6B7B\uFF01\u300D\u7D27\u5F20\u4E4B\u4E0B\uFF0C\u58F0\u97F3\u5C16\u5F97\u523A\u8033\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u89C6\u970D\u72C2\u7130\uFF0C\u8BF4\u9053\uFF1A\u300C\u957F\u9E70\u4F1A\u5DF2\u7136\u5F52\u987A\u795E\u6559\uFF0C\u5C31\u8BF7\u8521\u957F\u8001\u7EDF\u7BA1\u6D1B\u9633\u4E00\u5E26\u4E8B\u52A1\u3002\u300D</p><p>\u300C\u9075\u547D\u3002\u300D</p><p>\u300C\u6D1B\u9633\u662F\u795E\u6559\u5728\u4E2D\u539F\u7684\u6839\u672C\uFF0C\u8521\u957F\u8001\u591A\u591A\u8D39\u5FC3\u3002\u8BB0\u4F4F\u591A\u8F9F\u8D22\u6E90\uFF0C\u5E7F\u79EF\u94B1\u7CAE\u2014\u2014\u5C11\u6811\u5F3A\u654C\u3002\u82E5\u6709\u4E0E\u6211\u6559\u4E3A\u654C\u8005\uFF0C\u52A1\u5FC5\u65A9\u8349\u9664\u6839\uFF0C\u4E0D\u7559\u540E\u60A3\uFF01\u300D</p><p>\u8521\u4E91\u5CF0\u6C89\u58F0\u5E94\u8BFA\uFF0C\u89C1\u5BAB\u4E3B\u518D\u65E0\u5429\u5490\uFF0C\u4FBF\u8EAC\u8EAB\u544A\u9000\uFF0C\u81EA\u53BB\u63A5\u7BA1\u957F\u9E70\u4F1A\u3002</p><p>\u7B49\u8521\u4E91\u5CF0\u8D70\u8FDC\uFF0C\u6155\u5BB9\u9F99\u72E0\u72E0\u8E22\u4E86\u970D\u72C2\u7130\u4E00\u811A\uFF0C\u300C\u4ED6\u5988\u7684\uFF01\u722C\u8D77\u6765\u3002\u300D</p><p>\u970D\u72C2\u7130\u5931\u9B42\u843D\u9B44\u5730\u722C\u8D77\u6765\uFF0C\u5782\u7740\u5934\u7FFB\u7740\u773C\u73E0\u5077\u770B\u5BAB\u4E3B\u7684\u8138\u8272\u3002</p><p>\u300C\u9664\u4E86\u73A9\u5973\u4EBA\uFF0C\u4F60\u4ED6\u5988\u8FD8\u4F1A\u5E72\u4EC0\u4E48\uFF1F\u300D\u6155\u5BB9\u9F99\u54AC\u7259\u5207\u9F7F\uFF0C\u300C\u9E21\u5DF4\u90FD\u6CA1\u6709\u4E86\u8FD8\u52FE\u642D\u4E2A\u5973\u4EBA\u5F62\u5F71\u4E0D\u79BB\u2014\u2014\u4F60\u7B97\u600E\u4E48\u56DE\u4E8B\uFF1F\u6CA1\u5F97\u8BA9\u65B0\u5165\u6559\u7684\u5144\u5F1F\u7B11\u8BDD\uFF01\u300D</p><p>\u970D\u72C2\u7130\u55EB\u5685\u7740\u8BF4\uFF1A\u300C\u90A3\u662F\u767D\u6C99\u6D3E\u7684\u2026\u2026\u300D</p><p>\u300C\u95ED\u5634\uFF01\u300D\u6155\u5BB9\u9F99\u559D\u9053\uFF1A\u300C\u6211\u539F\u51C6\u5907\u8BA9\u4F60\u9547\u5B88\u6D1B\u9633\uFF0C\u8FC7\u51E0\u5E74\u79EF\u529F\u53EF\u664B\u62A4\u6CD5\u2014\u2014\u77A7\u4F60\u90A3\u718A\u6837\uFF01\u80FD\u670D\u4F17\u5417\uFF1F\u300D</p><p>\u970D\u72C2\u7130\u8116\u4E0A\u9752\u7B4B\u7206\u8D77\uFF0C\u5FC3\u91CC\u4E00\u65F6\u6FC0\u52A8\u4E00\u65F6\u60ED\u6127\u3002</p><p>\u6155\u5BB9\u9F99\u8D1F\u624B\u9053\uFF1A\u300C\u4F60\u5148\u4E0D\u5FC5\u56DE\u5BAB\uFF0C\u5C31\u5728\u6D1B\u9633\u3001\u897F\u5B89\u4E24\u57CE\u4E0E\u795E\u6559\u4E4B\u95F4\u6536\u7F57\u5E2E\u4F1A\uFF0C\u4E00\u5E74\u4E4B\u5185\u82E5\u4E0D\u80FD\u5C06\u6CBF\u9014\u5E2E\u4F1A\u5C3D\u6570\u6536\u5F52\u6211\u6559\uFF0C\u4F60\u4E5F\u4E0D\u7528\u518D\u539A\u7740\u8138\u76AE\u6765\u89C1\u6211\uFF0C\u81EA\u5DF1\u5272\u4E86\u8111\u888B\u4E86\u4E8B\uFF01\u300D</p><p>\u4E0D\u66FE\u60F3\u5BAB\u4E3B\u5BF9\u81EA\u5DF1\u8FD8\u5BA0\u4FE1\u6709\u52A0\uFF0C\u970D\u72C2\u7130\u5174\u594B\u5F97\u6EE1\u8138\u7EA2\u5149\uFF0C\u6251\u5730\u91CD\u91CD\u78D5\u4E86\u4E2A\u5934\uFF0C\u9AD8\u58F0\u9053\uFF1A\u300C\u5C5E\u4E0B\u9075\u547D\uFF01\u300D</p><p>\u8FD9\u5BB6\u4F19\u867D\u7136\u9C81\u83BD\uFF0C\u4F46\u5BF9\u81EA\u5DF1\u5FE0\u5FC3\u803F\u803F\uFF0C\u6155\u5BB9\u9F99\u4E5F\u662F\u6709\u610F\u56DE\u62A4\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u597D\u597D\u5E72\uFF0C\u672C\u5BAB\u5DF2\u547D\u53F6\u62A4\u6CD5\u8BBE\u6CD5\u7ED9\u4F60\u6CBB\u4F24\u2014\u2014\u4ED6\u5988\u7684\uFF0C\u6CA1\u9E21\u5DF4\u8FD8\u7B97\u7537\u4EBA\u5417\uFF1F\u300D</p><p>\u970D\u72C2\u7130\u4E94\u5185\u4FF1\u6CB8\uFF0C\u54D1\u7740\u55D3\u5B50\u9053\uFF1A\u300C\u591A\u8C22\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u62CD\u62CD\u4ED6\u7684\u80A9\uFF0C\u300C\u53BB\u5427\u3002\u300D</p><p>\u300C\u7B49\u4E00\u4E0B\uFF01\u300D</p><p>\u970D\u72C2\u7130\u6115\u7136\u56DE\u5934\uFF0C\u53EA\u89C1\u73AB\u7470\u4ED9\u5B50\u52C9\u529B\u6491\u8D77\u8EAB\u4F53\uFF0C\u8BF4\u9053\uFF1A\u300C\u6211\u6709\u4E8B\u60F3\u95EE\u95EE\u970D\u957F\u8001\u3002\u300D</p><p>\u970D\u72C2\u7130\u770B\u4E86\u6155\u5BB9\u9F99\u4E00\u773C\uFF0C\u89C1\u5BAB\u4E3B\u5FAE\u5FAE\u70B9\u5934\uFF0C\u4FBF\u5927\u6B65\u8E0F\u524D\uFF0C\u62F1\u624B\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u8089\u68D2\u867D\u672A\u5B8C\u5168\u8FDB\u5165\uFF0C\u4F46\u7D2B\u73AB\u83CA\u809B\u5DF2\u88AB\u91CD\u521B\uFF0C\u5979\u4E00\u624B\u51ED\u680F\uFF0C\u4E00\u624B\u633D\u7740\u8DB3\u8E1D\uFF0C\u5C48\u819D\u800C\u5750\uFF0C\u5FCD\u75DB\u95EE\u9053\uFF1A\u300C\u767D\u6C99\u6D3E\u53EF\u662F\u6E58\u897F\u695A\u8FDE\u96C4\u7684\u767D\u6C99\u6D3E\uFF1F\u300D</p><p>\u300C\u695A\u8FDE\u96C4\u4E24\u6708\u524D\u5DF2\u7ECF\u9000\u4F4D\uFF0C\u7531\u5F92\u5F1F\u4F55\u5C0F\u82B8\u7EE7\u4EFB\u3002\u300D</p><p>\u300C\u767D\u6C99\u6D3E\u7684\u4EBA\u6765\u6D1B\u9633\u4F55\u4E8B\uFF1F\u300D</p><p>\u300C\u5BAB\u4E3B\u6709\u4EE4\uFF0C\u547D\u5C5E\u4E0B\u5C06\u706B\u5802\u7BA1\u8F96\u5973\u5974\u62E9\u4F18\u9001\u81F3\u6D1B\u9633\u3002\u767D\u6C99\u6D3E\u6B63\u662F\u4E3A\u6B64\u800C\u6765\u3002\u300D</p><p>\u5C11\u592B\u4EBA\u773C\u5149\u5E7D\u5E7D\u95EA\u52A8\uFF0C\u5C31\u5728\u970D\u72C2\u7130\u4EE5\u4E3A\u5DF2\u7ECF\u95EE\u5B8C\u65F6\uFF0C\u5C11\u592B\u4EBA\u4F4E\u58F0\u9053\uFF1A\u300C\u6CAE\u6E20\u660E\u5170\u4E5F\u6765\u4E86\u5417\uFF1F\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6CAE\u6E20\u660E\u5170\u5230\u6D1B\u9633\u5DF2\u7ECF\u4E00\u4E2A\u6708\u4E86\u3002\u8FD9\u4E2A\u5341\u56DB\u5C81\u7684\u5C0F\u59D1\u5A18\u81F3\u4ECA\u4E5F\u4E0D\u77E5\u9053\u5BB6\u91CC\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u5979\u53EA\u8BB0\u5F97\u90A3\u5929\u7236\u4EB2\u7684\u5934\u9885\u88AB\u4E00\u4E2A\u7EA2\u8863\u5927\u6C49\u4E00\u811A\u8E29\u788E\uFF1B\u7136\u540E\u6BCD\u4EB2\u8EAB\u4E0B\u7A81\u7136\u7206\u8D77\u4E00\u56E2\u8840\u96FE\uFF1B\u8FD8\u6709\u54E5\u54E5\u2026\u2026</p><p>\u54E5\u54E5\u88AB\u4EBA\u6309\u5728\u5730\u4E0A\uFF0C\u4E00\u6761\u624B\u81C2\u5947\u602A\u5730\u626D\u66F2\u7740\uFF0C\u540C\u65F6\u626D\u66F2\u7684\u8FD8\u6709\u90A3\u5F20\u82F1\u4FCA\u7684\u9762\u5B54\u2026\u2026</p><p>\u8FD8\u6709\u75BC\u75DB\u3002\u90A3\u4E2A\u7EA2\u8863\u4EBA\u4E00\u4E0B\u5C31\u5F04\u4F24\u4E86\u81EA\u5DF1\u3002\u5979\u54ED\u558A\u7740\u56DE\u8FC7\u5934\u53BB\uFF0C\u5374\u770B\u5230\u54E5\u54E5\u773C\u91CC\u4E00\u6EF4\u6EF4\u6D41\u7740\u9C9C\u7EA2\u7684\u8840\u2026\u2026</p><p>\u6BCF\u6B21\u60F3\u5230\u54E5\u54E5\u5F53\u65F6\u7684\u795E\u60C5\uFF0C\u5979\u90FD\u4F1A\u4ECE\u68A6\u4E2D\u5413\u9192\uFF0C\u7136\u540E\u518D\u4E5F\u65E0\u6CD5\u5165\u7720\u3002</p><p>\u540E\u6765\u5979\u624D\u77E5\u9053\u90A3\u4E2A\u7EA2\u8863\u4EBA\u53EB\u300C\u970D\u7237\u300D\u2014\u2014\u65E0\u8BBA\u54EA\u4E2A\u7537\u4EBA\uFF0C\u90FD\u8981\u53EB\u300C\u7237\u300D\uFF0C\u4E0D\u77E5\u9053\u59D3\u540D\u7684\uFF0C\u5C31\u53EB\u300C\u5927\u7237\u300D\u2014\u2014\u8FD9\u662F\u4E3B\u4EBA\u6559\u5979\u7684\u3002\u4E3B\u4EBA\u8BA9\u5979\u505A\u4E00\u4E9B\u5F88\u5947\u602A\u7684\u52A8\u4F5C\u2014\u2014\u660E\u5170\u5E74\u7EAA\u867D\u5C0F\uFF0C\u4F46\u4E5F\u77E5\u9053\u90A3\u4E9B\u52A8\u4F5C\u5F88\u4E0D\u597D\uFF0C\u800C\u4E14\u5F88\u6076\u5FC3\u3002\u4F46\u5979\u4E0D\u6562\u53CD\u6297\u3002\u540E\u6765\u8FD8\u7528\u9488\u624E\u5979\uFF0C\u624E\u4E86\u4E4B\u540E\uFF0C\u5979\u7684\u80F8\u90E8\u5C31\u53D8\u5F97\u5F88\u5927\u3002</p><p>\u7136\u540E\u5C31\u662F\u7537\u4EBA\uFF0C\u5404\u79CD\u5404\u6837\uFF0C\u8FDE\u7EED\u4E0D\u65AD\u7684\u7537\u4EBA\u3002</p><p>\u660E\u5170\u62B1\u7740\u819D\u76D6\uFF0C\u745F\u7F29\u7740\u8737\u5728\u5E8A\u89D2\u3002\u7238\u7238\u3001\u5988\u5988\u90FD\u6B7B\u4E86\uFF0C\u54E5\u54E5\u5462\u2014\u2014\u8BB0\u5FC6\u4E2D\u6700\u540E\u4E00\u5E55\uFF0C\u662F\u54E5\u54E5\u6EE1\u8138\u8840\u6CEA\u5730\u5012\u5728\u5730\u4E0A\u3002\u6CA1\u6709\u4EBA\u544A\u8BC9\u5979\uFF0C\u6700\u7231\u7684\u54E5\u54E5\u540E\u6765\u662F\u600E\u4E48\u6837\u4E86\u3002</p><p>\u623F\u95E8\u5FFD\u7136\u63A8\u5F00\uFF0C\u660E\u5170\u7ACB\u523B\u6362\u4E0A\u4E3B\u4EBA\u6559\u5979\u7684\u7B11\u5BB9\uFF0C\u8DEA\u76F4\u8EAB\u4F53\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5927\u2026\u2026\u300D</p><p>\u5979\u6CA1\u6709\u53EB\u51FA\u300C\u5927\u7237\u300D\uFF0C\u56E0\u4E3A\u8FDB\u95E8\u7684\u662F\u4E00\u4E2A\u5C11\u5973\uFF0C\u800C\u4E14\u662F\u4E00\u4E2A\u719F\u6089\u7684\u5C11\u5973\u3002</p>',67);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
