import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_77" tabindex="-1"><a class="header-anchor" href="#_77" aria-hidden="true">#</a> 77</h1><p>\u300C\u660E\u5170\uFF01\u300D\u5C11\u5973\u53EB\u4E86\u4E00\u58F0\u3002</p><p>\u5973\u5B69\u8138\u4E0A\u8FD8\u6302\u7740\u5A9A\u7B11\uFF0C\u5507\u89D2\u5374\u98A4\u6296\u7740\u5F2F\u4E86\u4E0B\u6765\uFF0C\u300C\u7D2B\u73AB\u59D0\u59D0\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5FEB\u6B65\u4E0A\u524D\uFF0C\u4ED4\u7EC6\u6253\u91CF\u7740\u660E\u5170\u3002\u660E\u5170\u7B14\u76F4\u8DEA\u5728\u69BB\u4E0A\uFF0C\u4E24\u624B\u4EA4\u53E0\u653E\u5728\u8EAB\u524D\uFF0C\u7C89\u5AE9\u7684\u8EAB\u4F53\u4ECD\u50CF\u4E00\u4E2A\u5B69\u5B50\uFF0C\u4F46\u7A1A\u6C14\u672A\u892A\u7684\u8138\u4E0A\u5374\u6302\u7740\u7528\u6765\u53D6\u60A6\u7537\u4EBA\u7684\u5A9A\u7B11\u3002</p><p>\u660E\u5170\u773C\u5708\u6162\u6162\u53D1\u7EA2\uFF0C\u5047\u5982\u81EA\u5DF1\u8FD8\u6709\u4EB2\u4EBA\u7684\u8BDD\uFF0C\u90A3\u5C31\u662F\u8FD9\u4E2A\u5F88\u53EF\u80FD\u6210\u4E3A\u81EA\u5DF1\u5AC2\u5AC2\u7684\u7D2B\u73AB\u59D0\u59D0\u4E86\u3002</p><p>\u6B63\u60F3\u6251\u5230\u59D0\u59D0\u6000\u4E2D\u75DB\u54ED\u4E00\u756A\uFF0C\u95E8\u5916\u4EBA\u5F71\u95EA\u52A8\uFF0C\u53C8\u6709\u4EBA\u8D70\u4E86\u8FDB\u6765\u3002\u5403\u5C3D\u82E6\u5934\u7684\u5973\u5B69\u7ACB\u5373\u8DEA\u76F4\uFF0C\u4E0D\u6562\u7A0D\u52A8\u3002</p><p>\u8FDB\u6765\u7684\u662F\u4E00\u4E2A\u7537\u5B50\uFF0C\u4ED6\u4E0E\u54E5\u54E5\u4E00\u6837\u7684\u8EAB\u957F\u7389\u7ACB\uFF0C\u7709\u76EE\u4F3C\u4E4E\u8FD8\u8981\u82F1\u633A\u51E0\u5206\u3002\u4ED6\u6000\u4E2D\u62B1\u7740\u4E00\u4E2A\u534E\u670D\u5973\u5B50\uFF0C\u90A3\u5973\u5B50\u8EAB\u4E0A\u73E0\u73AF\u7FE0\u7ED5\uFF0C\u4F46\u65E0\u8BBA\u4EC0\u4E48\u4E48\u7684\u9C9C\u8863\u7F8E\u9970\uFF0C\u4E5F\u65E0\u6CD5\u906E\u63A9\u5979\u7684\u660E\u8273\u548C\u4E0E\u751F\u4FF1\u6765\u7684\u534E\u8D35\u4E4B\u6C14\u3002</p><p>\u300C\u8427\u963F\u59E8\u2026\u2026\u300D\u660E\u5170\u8BA4\u8BC6\u8FD9\u4F4D\u597D\u5FC3\u80A0\u7684\u963F\u59E8\uFF0C\u5979\u542C\u5230\u4EBA\u4EEC\u90FD\u79F0\u5979\u662F\u300C\u767E\u82B1\u89C2\u97F3\u300D\u3002</p><p>\u8427\u963F\u59E8\u8FD8\u548C\u4EE5\u524D\u4E00\u6837\u5149\u91C7\u7167\u4EBA\uFF0C\u7709\u76EE\u95F4\u9690\u7EA6\u7684\u54C0\u6101\uFF0C\u66F4\u50CF\u9605\u5C3D\u82E6\u96BE\u7684\u89C2\u97F3\u83E9\u8428\u4E00\u6837\u6709\u79CD\u60B2\u60AF\u4E4B\u8272\u3002\u53EF\u73B0\u5728\uFF0C\u767E\u82B1\u89C2\u97F3\u5374\u8F6F\u7EF5\u7EF5\u504E\u4F9D\u5728\u4E00\u4E2A\u964C\u751F\u7537\u4EBA\u6000\u4E2D\u3002</p><p>\u660E\u5170\u60CA\u7591\u4E0D\u5B9A\uFF0C\u4E0D\u660E\u767D\u8427\u963F\u59E8\u4E3A\u4EC0\u4E48\u50CF\u5B69\u5B50\u4E00\u822C\u88AB\u4EBA\u62B1\u5728\u6000\u91CC\uFF0C\u800C\u4E14\u6BEB\u4E0D\u6323\u624E\u2026\u2026</p><p>\u300C\u770B\u4E86\u4E00\u8DEF\uFF0C\u5A18\u4E5F\u7D2F\u4E86\u5427\u3002\u8EBA\u4E0B\u4F11\u606F\u4E00\u4F1A\u513F\u2026\u2026\u6EDA\uFF01\u300D\u6155\u5BB9\u9F99\u671D\u8DEA\u5728\u69BB\u4E0A\u7684\u660E\u5170\u51B7\u559D\u4E00\u58F0\u3002</p><p>\u660E\u5170\u8FDE\u5FD9\u8D77\u8EAB\u907F\u8BA9\u3002\u4E24\u81C2\u4E00\u52A8\uFF0C\u7D2B\u73AB\u987F\u65F6\u60CA\u547C\u5931\u58F0\uFF0C\u300C\u660E\u5170\uFF0C\u4F60\u7684\u2026\u2026\u600E\u4E48\u2026\u2026\u300D</p><p>\u7A1A\u5AE9\u7684\u80F8\u524D\u8D6B\u7136\u662F\u4E00\u5BF9\u6C89\u7538\u7538\u7684\u80A5\u4E73\uFF0C\u6BD4\u4E24\u4E2A\u6708\u524D\u82B1\u857E\u7684\u80F8\u812F\u5927\u4E86\u6570\u500D\u3002\u867D\u7136\u53EA\u5982\u8427\u4F5B\u5974\u4E73\u623F\u5927\u5C0F\uFF0C\u4F46\u653E\u5728\u5341\u56DB\u5C81\u7684\u5C0F\u5973\u5B69\u8EAB\u4E0A\u5374\u663E\u5F97\u5206\u5916\u89E6\u76EE\u3002\u660E\u5170\u8EAB\u4F53\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u5706\u4E73\u7ACB\u523B\u6447\u6447\u6446\u6446\u5212\u7740\u5708\u5B50\uFF0C\u6380\u8D77\u4E00\u9635\u4E73\u6CE2\u3002\u5979\u4E0D\u5F97\u4E0D\u6258\u7740\u4E24\u4E73\uFF0C\u52C9\u529B\u632A\u5230\u5E8A\u4FA7\uFF0C\u53C8\u5F85\u8DEA\u4E0B\u3002</p><p>\u7D2B\u73AB\u62C9\u4F4F\u5979\u6025\u5207\u5730\u95EE\u9053\uFF1A\u300C\u600E\u4E48\u56DE\u4E8B\uFF1F\u5979\u4EEC\u7ED9\u4F60\u7528\u4E86\u4EC0\u4E48\u836F\u5417\uFF1F\u300D</p><p>\u7531\u4E8E\u4E73\u623F\u589E\u957F\u8FC7\u5FEB\uFF0C\u8F7B\u8F7B\u4E00\u78B0\u5C31\u4F1A\u75DB\u695A\u3002\u660E\u5170\u7EA2\u7740\u8138\u5782\u4E0B\u5934\uFF0C\u6258\u7740\u4E73\u623F\u9053\uFF1A\u300C\u4E3B\u4EBA\u8BF4\u8D31\u5974\u7684\u5976\u5B50\u592A\u5C0F\uFF0C\u5927\u7237\u4EEC\u4F1A\u4E0D\u9AD8\u5174\u2026\u2026\u5C31\u7ED9\u8D31\u5974\u624E\u9488\u2026\u2026\u300D\u6709\u6155\u5BB9\u9F99\u8FD9\u4E2A\u964C\u751F\u4EBA\u5728\u573A\uFF0C\u660E\u5170\u53EA\u80FD\u8FD9\u6837\u8C28\u5C0F\u614E\u5FAE\u7684\u8BF4\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u6BCD\u4EB2\u653E\u5728\u69BB\u4E0A\uFF0C\u659C\u773C\u770B\u770B\u660E\u5170\u7684\u53CC\u4E73\uFF0C\u773C\u5149\u970D\u7136\u4E00\u8DF3\uFF0C\u63A5\u7740\u8F6C\u5230\u7D2B\u73AB\u80F8\u524D\uFF0C\u5634\u89D2\u9690\u9690\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\u3002</p><p>\u4E00\u4E2A\u82B1\u679D\u62DB\u5C55\u7684\u5973\u5B50\u626D\u7740\u8170\u8D70\u5165\u623F\u5185\uFF0C\u8DEA\u5728\u6155\u5BB9\u9F99\u9762\u524D\u55F2\u58F0\u55F2\u6C14\u5730\u8BF4\uFF1A\u300C\u5974\u5A62\u4F55\u5C0F\u82B8\u53E9\u89C1\u5BAB\u4E3B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5BF9\u5979\u7684\u5DF4\u7ED3\u6BEB\u4E0D\u7406\u776C\uFF0C\u53EA\u62FF\u7740\u4E00\u67C4\u7389\u67C4\u6298\u6247\u8F7B\u8F7B\u6447\u7740\uFF0C\u4E00\u624B\u62FF\u7740\u4E1D\u5DFE\uFF0C\u64E6\u53BB\u6BCD\u4EB2\u989D\u4E0A\u7684\u9999\u6C57\u3002</p><p>\u4F55\u5C0F\u82B8\u6EE1\u8138\u7B11\u5BB9\uFF0C\u53C8\u8F6C\u8EAB\u9053\uFF1A\u300C\u5974\u5A62\u53E9\u89C1\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u5728\u660E\u5170\u773C\u91CC\uFF0C\u4E3B\u4EBA\u4E00\u5411\u662F\u81F3\u9AD8\u65E0\u4E0A\uFF0C\u5BF9\u5979\u4EEC\u60F3\u6253\u5C31\u6253\uFF0C\u60F3\u9A82\u5C31\u9A82\uFF0C\u6CA1\u60F3\u5230\u4E5F\u53EA\u662F\u4E2A\u5974\u5A62\u2014\u2014\u5979\u521A\u624D\u662F\u53EB\u300C\u5C11\u592B\u4EBA\u300D\uFF1F\u7D2B\u73AB\u59D0\u59D0\u5AC1\u4EBA\u4E86\uFF1F\u54E5\u54E5\u5462\uFF1F\u6CAE\u6E20\u660E\u5170\u4E0D\u77E5\u6240\u63AA\u5730\u770B\u7740\u7D2B\u73AB\u3002</p><p>\u300C\u5C0F\u5A4A\u5B50\uFF01\u6CA1\u4E00\u70B9\u793C\u6570\uFF0C\u8FD8\u4E0D\u5FEB\u8DEA\u4E0B\uFF01\u300D\u4F55\u5C0F\u82B8\u8BF4\u7740\u4F38\u624B\u5C31\u53BB\u62E7\u660E\u5170\u3002</p><p>\u300C\u6EDA\uFF01\u300D\u7D2B\u73AB\u4E00\u58F0\u4F4E\u559D\u3002</p><p>\u4F55\u5C0F\u82B8\u6084\u6084\u770B\u4E86\u5BAB\u4E3B\u4E00\u773C\uFF0C\u89C1\u4E3B\u5B50\u8138\u4E0A\u6CA1\u4E00\u4E1D\u8868\u60C5\uFF0C\u53EA\u597D\u6362\u4E0A\u7B11\u8138\uFF0C\u9000\u5230\u4E00\u65C1\u3002</p><p>\u300C\u6211\u54E5\u54E5\u5462\uFF1F\u300D</p><p>\u300C\u2026\u2026\u8FD8\u6D3B\u7740\u3002\u300D</p><p>\u660E\u5170\u671B\u7740\u7D2B\u73AB\uFF0C\u867D\u7136\u4E0D\u6562\u8BF4\uFF0C\u773C\u91CC\u5374\u6D41\u9732\u51FA\u4E5E\u6C42\u7684\u795E\u8272\u3002\u4E5E\u6C42\u7D2B\u73AB\u59D0\u59D0\u80FD\u5E26\u5979\u79BB\u5F00\u82E6\u6D77\u3002</p><p>\u7D2B\u73AB\u770B\u51FA\u4E86\u5979\u7684\u4E5E\u6C42\uFF0C\u4F46\u5979\u66F4\u660E\u767D\u2014\u2014\u6240\u6709\u81EA\u5DF1\u8981\u6C42\u7559\u5728\u8EAB\u8FB9\u7684\u4EB2\u4EBA\u90FD\u53D7\u5230\u4E86\u4EC0\u4E48\u6837\u7684\u6298\u78E8\u3002</p><p>\u660E\u5170\u5931\u671B\u5730\u5782\u4E0B\u5934\uFF0C\u773C\u775B\u505C\u5728\u7D2B\u73AB\u8170\u95F4\u7684\u5C0F\u5F29\u4E0A\u3002</p><p>\u7D2B\u73AB\u6267\u610F\u8981\u89C1\u660E\u5170\uFF0C\u4F46\u6B64\u65F6\u5374\u4E0D\u77E5\u8BF4\u4EC0\u4E48\u597D\uFF0C\u623F\u95F4\u91CC\u4E00\u7247\u6C89\u9ED8\uFF0C\u95F7\u70ED\u7684\u7A7A\u6C14\u91CD\u91CD\u538B\u5728\u5FC3\u5934\uFF0C\u8BA9\u4EBA\u5598\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u7D2B\u73AB\u518D\u65E0\u6CD5\u5FCD\u53D7\u8FD9\u79CD\u6C89\u91CD\u7684\u6C14\u6C1B\uFF0C\u626D\u5934\u79BB\u5F00\u623F\u95F4\u3002</p><p>\u300C\u6211\u54E5\u54E5\u2026\u2026\u300D\u660E\u5170\u8BF4\u4E86\u534A\u53E5\uFF0C\u4FBF\u96BE\u8FC7\u5730\u75DB\u54ED\u8D77\u6765\u3002\u54E5\u54E5\u5F53\u521D\u5BF9\u7D2B\u73AB\u59D0\u59D0\u90A3\u4E48\u597D\uFF0C\u53EF\u5979\u73B0\u5728\u7ADF\u7136\u5AC1\u4E86\u4EBA\uFF0C\u4E0D\u7BA1\u54E5\u54E5\u7684\u4E0B\u843D\uFF0C\u751A\u81F3\u6839\u672C\u4E0D\u7406\u81EA\u5DF1\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u542C\u51FA\u660E\u5170\u7684\u57CB\u6028\uFF0C\u4F46\u53C8\u65E0\u6CD5\u89E3\u91CA\uFF0C\u5FC3\u4E0B\u53C8\u9178\u53C8\u82E6\uFF0C\u67D4\u80A0\u767E\u8F6C\u95F4\u4E0D\u7531\u6CEA\u76C8\u4E8E\u776B\u3002\u5979\u8FDC\u8FDC\u907F\u5F00\u90A3\u4E2A\u7684\u623F\u95F4\uFF0C\u4FEF\u5728\u680F\u6746\u4E0A\uFF0C\u624B\u91CC\u7D27\u7D27\u634F\u7740\u90A3\u652F\u5C0F\u5F29\u3002</p><p>\u9999\u6708\u697C\u51E0\u4E4E\u5360\u636E\u4E86\u6574\u4E2A\u7389\u9E21\u574A\uFF0C\u6B63\u4E2D\u4E94\u5C42\u9AD8\u7684\u5DCD\u5CE8\u697C\u53F0\u539F\u672C\u662F\u5E7F\u9633\u5E2E\u7684\u603B\u90E8\uFF0C\u5982\u4ECA\u5F20\u706F\u7ED3\u5F69\uFF0C\u7C89\u9970\u4E00\u65B0\uFF0C\u5904\u5904\u8102\u9999\u7C89\u6D53\uFF0C\u5BBE\u5BA2\u5982\u4E91\uFF0C\u4FE8\u7136\u662F\u6625\u610F\u878D\u878D\u7684\u9500\u9B42\u4E4B\u6240\u3002</p><p>\u7D2B\u73AB\u6014\u6014\u770B\u7740\u811A\u4E0B\u9AD8\u6311\u7684\u98DE\u6A90\u3002\u8FD9\u4E2A\u810F\u80AE\u7684\u9999\u6708\u697C\u5C3D\u662F\u6728\u5236\uFF0C\u4E00\u628A\u706B\u5C31\u80FD\u70E7\u5F97\u5E72\u5E72\u51C0\u51C0\u3002</p><p>\u5F88\u5BB9\u6613\u7684\u4E8B\u3002</p><p>\u4F46\u70E7\u4E86\u5B83\u53C8\u80FD\u600E\u4E48\u6837\u5462\uFF1F\u4ED6\u4EEC\u8FD8\u80FD\u518D\u5EFA\u4E00\u5EA7\u3001\u4E24\u5EA7\u2026\u2026\u8FD9\u4E9B\u5973\u5B50\u4F9D\u7136\u65E0\u6CD5\u9003\u8131\u6298\u78E8\u3002</p><p>\u300C\u7528\u52B2\u513F\u8214\uFF01\u563F\uFF01\u771F\u591F\u61C2\u4E8B\u7684\uFF0C\u5C41\u80A1\u62AC\u8FD9\u4E48\u9AD8\uFF0C\u7B49\u7740\u6328\u64CD\u5462\u3002\u8BDA\u7237\uFF0C\u60A8\u8BD5\u8BD5\uFF0C\u4FE9\u6D1E\u90FD\u723D\u7740\u5462\u3002\u300D</p><p>\u300C\u55EF\u55EF\u3002\u300D\u90A3\u4E2A\u8BDA\u7237\u8FDE\u58F0\u7B54\u5E94\u3002</p><p>\u300C\u7AD9\u597D\uFF0C\u817F\u5206\u5F00\uFF01\u563F\uFF0C\u8BDA\u7237\uFF0C\u6211\u8DDF\u60A8\u51D1\u4E2A\u8DA3\u513F\uFF0C\u60A8\u524D\u8FB9\u513F\uFF0C\u6211\u540E\u8FB9\u513F\uFF0C\u4E00\u5757\u513F\u6765\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u300C\u597D\u597D\u3002\u300D</p><p>\u623F\u5185\u4F20\u6765\u5973\u5B50\u7684\u95F7\u54FC\uFF0C\u7D2B\u73AB\u76B1\u8D77\u7709\u5934\uFF0C\u671D\u65C1\u8FB9\u8D70\u4E86\u51E0\u6B65\uFF0C\u4F46\u623F\u95F4\u91CC\u7684\u6DEB\u8BCD\u6D6A\u8BED\u8FD8\u4E0D\u4F4F\u98D8\u5230\u8033\u5185\u3002</p><p>\u300C\u2026\u2026\u6E7F\u900F\u4E86\u2026\u2026\u300D</p><p>\u300C\u554A\u3001\u554A\u2026\u2026\u554A\u2014\u2014\u300D\u300C\u9760\uFF0C\u8FD9\u5C31\u53D1\u6D6A\u4E86\u2026\u2026\u300D</p><p>\u300C\u5C44\u7FFB\u5F00\u2026\u2026\u300D</p><p>\u300C\u5440\uFF01\u300D\u5973\u5B50\u50CF\u662F\u88AB\u9488\u523A\u4E86\u4E00\u4E0B\u822C\uFF0C\u7A81\u7136\u75DB\u53EB\u4E00\u58F0\u3002</p><p>\u7D2B\u73AB\u5DF2\u7ECF\u542C\u51FA\u6765\u8FD9\u662F\u4E09\u5E08\u59D0\u7EAA\u7709\u59A9\u7684\u58F0\u97F3\uFF0C\u4F46\u5979\u7AD9\u5728\u539F\u5730\u4E00\u52A8\u672A\u52A8\u3002</p><p>\u80FD\u600E\u4E48\u6837\u5462\uFF1F\u51B2\u8FDB\u53BB\u628A\u90A3\u4E24\u4E2A\u4EBA\u90FD\u6740\u4E86\uFF1F\u628A\u5E08\u59D0\u6551\u8D70\uFF1F\u522B\u50BB\u4E86\uFF0C\u8FDE\u81EA\u5DF1\u4E5F\u4FDD\u4E0D\u4F4F\u5462\u2026\u2026\u5979\u82E6\u6DA9\u5730\u7B11\u4E86\u7B11\uFF0C\u540E\u5EAD\u75DB\u610F\u8D8A\u6765\u8D8A\u5F3A\u70C8\u4E86\u2026\u2026</p><p>\u300C\u8BDA\u7237\uFF0C\u50CF\u4E0D\u50CF\uFF1F\u300D\u90A3\u4E24\u4EBA\u5B8C\u4E8B\u540E\u7B11\u563B\u563B\u51FA\u6765\u3002</p><p>\u300C\u50CF\uFF01\u50CF\uFF01\u300D\u7EAA\u8BDA\u62B9\u7740\u6C57\u8BF4\uFF0C\u300C\u771F\u662F\u592A\u50CF\u4E86\u3002\u300D</p><p>\u300C\u5F53\u521D\u5C0F\u59D0\u5728\u5E9C\u91CC\uFF0C\u5C0F\u7684\u4E5F\u6CA1\u6562\u591A\u770B\uFF0C\u8BA4\u4E0D\u51C6\uFF0C\u8FD9\u4E0D\u4E13\u95E8\u8BF7\u8BDA\u7237\u6765\u77A7\u77A7\u3002\u563F\u563F\uFF0C\u8BDA\u7237\u8BF4\u50CF\u90A3\u5C31\u662F\u771F\u50CF\u4E86\u3002\u300D</p><p>\u7EAA\u8BDA\u6709\u4E9B\u604D\u60DA\u5730\u5583\u5583\u8BF4\uFF1A\u300C\u90A3\u8138\u86CB\u7B80\u76F4\u662F\u4E00\u4E2A\u6A21\u5B50\u91CC\u5370\u51FA\u6765\u7684\uFF0C\u4E00\u6A21\u4E00\u6837\u2026\u2026\u300D</p><p>\u300C\u8BDA\u7237\u4E0D\u4F1A\u4EE5\u4E3A\u90A3\u771F\u662F\u5C0F\u59D0\u5427\uFF1F\u300D</p><p>\u7EAA\u8BDA\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u8FDE\u5FD9\u6447\u5934\uFF0C\u300C\u4E0D\u53EF\u80FD\u4E0D\u53EF\u80FD\u2026\u2026\u300D</p><p>\u300C\u5C31\u662F\uFF01\u5C0F\u59D0\u6211\u53EA\u662F\u8FDC\u8FDC\u89C1\u8FC7\u4E24\u6B21\uFF0C\u90A3\u4F53\u6001\u591A\u7AEF\u5E84\u554A\uFF0C\u6E29\u67D4\u5A34\u9759\uFF0C\u7B11\u8D77\u6765\u7259\u90FD\u4E0D\u9732\uFF0C\u8FD8\u597D\u5E72\u51C0\uFF0C\u9662\u5B50\u91CC\u90FD\u4E0D\u8BB8\u7537\u4EBA\u8FDB\u2014\u2014\u77A7\u8FD9\u5A4A\u5B50\uFF0C\u6D6A\u5C44\u53C8\u80A5\u53C8\u539A\uFF0C\u6345\u4E00\u4E0B\u6D6A\u6C34\u513F\u4E71\u6D41\uFF0C\u8BA9\u8214\u54EA\u513F\u5C31\u8214\u54EA\u513F\u2026\u2026\u300D</p><p>\u300C\u5509\uFF0C\u751F\u5F97\u4E00\u6A21\u4E00\u6837\uFF0C\u547D\u600E\u4E48\u5DEE\u8FD9\u4E48\u8FDC\uFF1F\u300D</p><p>\u300C\u53EF\u4E0D\u662F\u561B\uFF0C\u54B1\u4EEC\u5C0F\u59D0\u591A\u5BCC\u8D35\uFF0C\u542C\u8BF4\u5C06\u6765\u8FD8\u8981\u5AC1\u5230\u7687\u5BA4\uFF0C\u4E00\u8F88\u5B50\u4E07\u4EBA\u4E4B\u4E0A\u3002</p><p>\u8FD9\u4E2A\u9664\u4E86\u8138\u86CB\u957F\u5F97\u4E00\u6837\uFF0C\u5176\u4ED6\u53EF\u6CA1\u6CD5\u513F\u6BD4\u554A\uFF0C\u4E00\u8F88\u5B50\u5343\u4EBA\u538B\u4E07\u4EBA\u9A91\u2014\u2014\u63B0\u7740\u5C44\u6390\u5F97\u76F4\u6D41\u773C\u6CEA\u8FD8\u4E0D\u6562\u8EB2\u2026\u2026\u300D</p><p>\u300C\u4E0D\u597D\u4E0D\u597D\u3002\u300D\u7EAA\u8BDA\u6447\u7740\u5934\uFF0C\u4E5F\u4E0D\u77E5\u9053\u662F\u8BF4\u6390\u4EBA\u7684\u4E0D\u597D\uFF0C\u8FD8\u662F\u88AB\u6390\u7684\u4E0D\u597D\u3002</p><p>\u90A3\u4EBA\u7325\u4EB5\u5730\u7B11\u9053\uFF1A\u300C\u56DE\u5E9C\u8BA9\u5927\u4F19\u90FD\u6765\u4E50\u4E50\u2026\u2026\u300D</p><p>\u7EAA\u8BDA\u6B63\u5BB9\u9053\uFF1A\u300C\u8FD9\u4E8B\u5C3D\u91CF\u522B\u4F20\uFF0C\u5C24\u5176\u522B\u8BA9\u5C06\u519B\u8DDF\u5C0F\u59D0\u77E5\u9053\uFF01\u5F04\u4E0D\u597D\uFF0C\u7ED9\u54B1\u4EEC\u4E2A\u4E0D\u656C\u4E4B\u7F6A\u2026\u2026\u300D</p><p>\u300C\u8BDA\u7237\u60A8\u8FD9\u8BF4\u7684\u2014\u2014\u53C8\u4E0D\u662F\u54B1\u4EEC\u8BA9\u5979\u957F\u6210\u8FD9\u6837\u2026\u2026\u300D</p><p>\u4E24\u4EBA\u8BF4\u7740\u53BB\u4E86\u3002</p><p>\u7D2B\u73AB\u65E9\u5DF2\u542C\u5F97\u82B3\u5FC3\u9707\u60CA\uFF0C\u6CA1\u60F3\u5230\u5E08\u59D0\u63A5\u5BA2\u5C45\u7136\u63A5\u5230\u81EA\u5DF1\u5E9C\u4E2D\u7684\u4E0B\u4EBA\u2026\u2026\u7B49\u4E24\u4EBA\u8D70\u8FDC\uFF0C\u5979\u8FDE\u5FD9\u8F7B\u6B65\u5165\u5185\u3002</p><p>\u7EAA\u7709\u59A9\u6EE1\u9762\u6CEA\u75D5\uFF0C\u53CC\u76EE\u7D27\u95ED\u3002\u5979\u53D7\u5F97\u7F9E\u8FB1\u5DF2\u7ECF\u6570\u4E0D\u80DC\u6570\uFF0C\u4F46\u6B64\u756A\u5F53\u505A\u5993\u5973\uFF0C\u88AB\u5BB6\u91CC\u7684\u5974\u4EC6\u6765\u5AD6\uFF0C\u8FD8\u8981\u4F5C\u51FA\u79CD\u79CD\u98CE\u9A9A\u6765\u63A9\u4EBA\u8033\u76EE\uFF0C\u5176\u4E2D\u7684\u82E6\u695A\u5C48\u8FB1\u767E\u500D\u4E8E\u9762\u5BF9\u964C\u751F\u4EBA\u3002</p><p>\u5979\u50CF\u77F3\u96D5\u822C\u501A\u5728\u5E8A\u5934\uFF0C\u5750\u4E86\u826F\u4E45\u3002\u76F4\u5230\u95E8\u5916\u53C8\u4F20\u6765\u72CE\u5BA2\u7684\u811A\u6B65\u58F0\uFF0C\u624D\u614C\u5FD9\u64E6\u5E72\u6CEA\u75D5\u3002</p><p>\u300C\u542C\u8BF4\u8FD9\u6302\u724C\u7684\u7C89\u5934\u59FF\u8272\u4E0D\u4FD7\uFF0C\u5927\u7237\u4ECA\u513F\u53EF\u8981\u7EC6\u7EC6\u54C1\u5C1D\u4E00\u756A\u3002\u300D\u4E00\u4E2A\u7EA8\u88E4\u5B50\u5F1F\u6DEB\u7B11\u7740\u8D70\u4E86\u8FDB\u6765\u3002\u62AC\u773C\u4E00\u770B\uFF0C\u987F\u65F6\u6123\u4F4F\u4E86\u3002</p><p>\u4E00\u4E2A\u7EA2\u886B\u5C11\u5973\u6B3E\u6B3E\u8D70\u5230\u6765\u4EBA\u9762\u524D\uFF0C\u5AE3\u7136\u4E00\u7B11\u3002\u90A3\u4EBA\u9AA8\u5934\u90FD\u9165\u4E86\uFF0C\u50BB\u50BB\u770B\u7740\u773C\u524D\u5343\u5A07\u767E\u5A9A\u7684\u4FCF\u8138\u3002</p><p>\u5C11\u5973\u6A80\u53E3\u5FAE\u5F20\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u6211\u6765\u4F3A\u5019\u5927\u7237\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u4E0D\u77E5\u9053\u7D2B\u73AB\u662F\u4EC0\u4E48\u65F6\u5019\u8FDB\u6765\u7684\uFF0C\u95FB\u8A00\u4E0D\u7531\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u6491\u8D77\u9178\u75BC\u7684\u8EAB\u4F53\uFF0C\u53BB\u62C9\u5C11\u592B\u4EBA\u3002</p><p>\u7D2B\u73AB\u62C9\u5F00\u8863\u895F\uFF0C\u9732\u51FA\u80F8\u53E3\u4E00\u62B9\u5149\u6D01\u7684\u808C\u80A4\uFF0C\u7EA4\u6307\u8F7B\u8F7B\u4E00\u5212\uFF0C\u8138\u4E0A\u6EE1\u662F\u6311\u9017\u7684\u7B11\u5BB9\uFF0C\u7F8E\u76EE\u5374\u51B0\u51B7\u523A\u9AA8\u3002</p><p>\u90A3\u4EBA\u88AB\u7EDD\u4E16\u7684\u8273\u8272\u6240\u8FF7\uFF0C\u6251\u5730\u62B1\u7740\u7D2B\u73AB\u7684\u7EA4\u8DB3\uFF0C\u5634\u89D2\u4E00\u4E2A\u52B2\u513F\u7684\u6253\u98A4\uFF0C\u5374\u8BF4\u4E0D\u51FA\u4E00\u4E2A\u5B57\u3002</p><p>\u7D2B\u73AB\u773C\u4E2D\u6740\u610F\u4E00\u95EA\u800C\u901D\uFF0C\u633D\u8D77\u7F57\u5E26\uFF0C\u63D0\u9AD8\u58F0\u97F3\u9053\uFF1A\u300C\u4F60\u8981\u8131\u6211\u7684\u8863\u670D\u5417\u2026\u2026\u300D</p><p>\u300C\u4E0D\u8981\uFF01\u300D\u7EAA\u7709\u59A9\u614C\u5F97\u516D\u795E\u65E0\u4E3B\uFF0C\u4E0D\u77E5\u9053\u5C11\u592B\u4EBA\u8FD9\u662F\u600E\u4E48\u4E86\uFF0C\u300C\u4F60\u5FEB\u51FA\u53BB\uFF0C\u8BA9\u6211\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u626C\u9996\u770B\u7740\u5927\u95E8\uFF0C\u4EFB\u90A3\u53CC\u810F\u624B\u54C6\u55E6\u7740\u4F38\u5411\u81EA\u5DF1\u7684\u8EAB\u4F53\u3002</p><p>\u4E00\u9053\u8EAB\u5F71\u4EE5\u4F17\u4EBA\u65E0\u6CD5\u770B\u6E05\u7684\u9AD8\u901F\u75BE\u98DE\u800C\u5165\uFF0C\u63A5\u7740\u4E00\u9897\u5934\u9885\u62D4\u5730\u800C\u8D77\uFF0C\u5728\u7A7A\u4E2D\u5212\u51FA\u4E00\u4E2A\u5F27\u7EBF\uFF0C\u843D\u5728\u5730\u4E0A\u4E0D\u4F4F\u7FFB\u6EDA\u3002\u7247\u523B\u540E\uFF0C\u65AD\u9888\u4E2D\u7684\u9C9C\u8840\u624D\u6FC0\u5C04\u800C\u51FA\u3002</p><p>\u9C9C\u8840\u4EFF\u4F5B\u8377\u53F6\u4E0A\u7684\u9732\u73E0\uFF0C\u4ECE\u8138\u4E0A\u4E00\u6EF4\u6EF4\u6ED1\u843D\uFF0C\u9732\u51FA\u7EC6\u6ED1\u767D\u5AE9\u7684\u808C\u80A4\u3002\u7D2B\u73AB\u524D\u8EAB\u7684\u8863\u7269\u5C3D\u8D64\uFF0C\u8FDE\u79C0\u53D1\u548C\u776B\u6BDB\u4E5F\u90FD\u6EF4\u7740\u9C9C\u8840\u3002\u5979\u633D\u7740\u67D3\u8840\u7F57\u5E26\uFF0C\u773C\u4E2D\u5149\u6655\u6D41\u8F6C\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u540C\u6837\u662F\u5149\u8292\u95EA\u52A8\uFF0C\u4E24\u4EBA\u9694\u7740\u98DE\u6E85\u7684\u8840\u5149\uFF0C\u56DB\u76EE\u4EA4\u6295\u3002</p><p>\u300C\u5440\u2026\u2026\u300D\u65C1\u8FB9\u5F15\u5BA2\u7684\u9E28\u6BCD\u8FD9\u65F6\u624D\u60CA\u53EB\u51FA\u6765\uFF0C\u300C\u8FD9\u662F\u5F90\u592A\u5E08\u7684\u516C\u5B50\u2026\u2026\u300D</p><p>\u300C\u6254\u51FA\u53BB\uFF0C\u5582\u72D7\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u8BF4\u7740\uFF0C\u4F38\u6307\u62B9\u53BB\u5200\u950B\u4E0A\u7684\u8840\u8FF9\u3002\u4ED6\u7528\u7247\u7389\u4E00\u5200\u65A9\u65AD\u90A3\u4EBA\u7684\u5934\u9885\uFF0C\u6CA1\u8BA9\u4ED6\u6709\u673A\u4F1A\u5360\u5230\u4FBF\u5B9C\uFF0C\u6B64\u65F6\u9762\u5BF9\u59B9\u59B9\u54C0\u5A49\u7684\u773C\u795E\uFF0C\u6012\u6C14\u6E10\u6E10\u6D88\u6563\u3002</p><p>\u300C\u6D17\u6D17\u8138\uFF0C\u6211\u4EEC\u56DE\u53BB\u5427\u3002\u300D\u534A\u664C\u540E\uFF0C\u6155\u5BB9\u9F99\u67D4\u58F0\u9053\u3002</p><p>\u7D2B\u73AB\u70B9\u70B9\u5934\uFF0C\u4F9D\u8A00\u6D17\u53BB\u8EAB\u4E0A\u7684\u8840\u8FF9\u3002</p><p>\u6155\u5BB9\u9F99\u770B\u7740\u7D2B\u73AB\u7684\u80CC\u5F71\uFF0C\u5FC3\u5934\u6D8C\u4E0A\u4E00\u80A1\u9178\u6DA9\u7684\u6ECB\u5473\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u8FD8\u8981\u73A9\u8FD9\u79CD\u5C0F\u5B69\u5B50\u7684\u6E38\u620F\u5462\uFF1F\u4F60\u8FD8\u60F3\u4FDD\u62A4\u8FD9\u4E9B\u4E0B\u8D31\u7684\u5973\u5974\u5417\uFF1F\u5929\u771F\u7684\u5C0F\u4E2B\u5934\u2026\u2026</p>',83);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
