import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_78" tabindex="-1"><a class="header-anchor" href="#_78" aria-hidden="true">#</a> 78</h1><p>\u300C\u8001\u5B50\u5C31\u662F\u8981\u8FD9\u4E00\u95F4\uFF01\u300D\u4E00\u4E2A\u8EAB\u6750\u9B41\u68A7\u7684\u5927\u6C49\u7AD9\u5728\u660E\u5170\u623F\u524D\u53EB\u9053\u3002</p><p>\u300C\u519B\u7237\uFF0C\u65C1\u8FB9\u7684\u6625\u9999\u9601\u6BD4\u8FD9\u95F4\u53EF\u6F02\u4EAE\u591A\u4E86\u2014\u2014\u5170\u513F\uFF0C\u8D76\u7D27\u53BB\u4F3A\u5019\u519B\u7237\u3002\u300D\u4F55\u5C0F\u82B8\u5C06\u6CAE\u6E20\u660E\u5170\u4E00\u628A\u62D6\u5230\u95E8\u5916\u3002</p><p>\u660E\u5170\u62AB\u7740\u4E00\u5C42\u8584\u7EB1\uFF0C\u5A07\u5C0F\u7684\u8EAB\u4F53\u8FD8\u4E0D\u53CA\u90A3\u4EBA\u80F8\u53E3\u9AD8\uFF0C\u7AD9\u5728\u5927\u6C49\u8EAB\u8FB9\uFF0C\u5C31\u50CF\u4E00\u4E2A\u73A9\u5177\u74F7\u5A03\u5A03\u822C\u7EA4\u5DE7\u3002</p><p>\u4F55\u5C0F\u82B8\u6EE1\u8138\u5806\u7B11\uFF0C\u300C\u8FD9\u4E2B\u5934\u6628\u5929\u624D\u5F00\u59CB\u63A5\u5BA2\uFF0C\u519B\u7237\u597D\u6B79\u601C\u60DC\u4E9B\u3002\u6625\u9999\u9601\u6211\u5DF2\u7ECF\u547D\u4EBA\u6536\u62FE\u4E86\u2026\u2026\u300D</p><p>\u300C\u5C11\u9E21\u5DF4\u653E\u5C41\uFF01\u5976\u5B50\u8FD9\u4E48\u5927\u8FD8\u521A\u63A5\u5BA2\uFF0C\u9A97\u8C01\u5462\uFF01\u8001\u5B50\u6709\u7684\u662F\u94F6\u4E24\uFF0C\u8FD9\u95F4\u623F\u8981\u5B9A\u4E86\uFF01\u300D</p><p>\u4E3B\u6BCD\u8FD8\u5728\u623F\u5185\uFF0C\u4F55\u5C0F\u82B8\u600E\u4E48\u4E5F\u4E0D\u6562\u8BA9\u4EBA\u8FDB\u53BB\u3002\u5979\u7B11\u5F97\u6108\u53D1\u606D\u987A\uFF0C\u62AC\u624B\u626F\u5F00\u660E\u5170\u7684\u8584\u7EB1\uFF0C\u628A\u5979\u63A8\u5230\u519B\u6C49\u6000\u4E2D\uFF0C\u300C\u5170\u513F\uFF0C\u597D\u751F\u670D\u4F8D\u519B\u7237\u3002\u8FD9\u8FB9\u513F\u8BF7\u2026\u2026\u300D</p><p>\u300C\u6EDA\u5F00\uFF01\u300D\u519B\u6C49\u4E0D\u8010\u70E6\u6A2A\u81C2\u4E00\u63A8\uFF0C\u60F3\u95EF\u8FDB\u623F\u5185\u3002\u4E0D\u6599\u90A3\u5973\u5B50\u53CD\u624B\u6263\u4F4F\u4ED6\u7684\u8109\u95E8\uFF0C\u534A\u8FB9\u8EAB\u4F53\u987F\u65F6\u9178\u9EBB\u3002</p><p>\u4F55\u5C0F\u82B8\u7B11\u5BB9\u4E0D\u6539\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u519B\u7237\u606F\u6012\uFF0C\u8FD9\u95F4\u623F\u6B63\u5728\u6253\u7406\uFF0C\u5B9E\u5728\u65E0\u6CD5\u63A5\u5F85\u519B\u7237\u8FD9\u6837\u7684\u8D35\u5BA2\uFF0C\u5176\u4ED6\u519B\u7237\u5C3D\u7BA1\u5429\u5490\u2026\u2026\u300D</p><p>\u519B\u6C49\u5FC3\u4E0B\u60CA\u7591\u4E0D\u5B9A\uFF0C\u5598\u7740\u7C97\u6C14\u6012\u89C6\u4F55\u5C0F\u82B8\uFF0C\u534A\u664C\u540E\u7A81\u7136\u53EB\u9053\uFF1A\u300C\u8001\u5B50\u8981\u64CD\u4F60\uFF01\u300D</p><p>\u4F55\u5C0F\u82B8\u4E00\u8138\u5A9A\u7B11\u7740\u62B1\u4F4F\u90A3\u4EBA\u7684\u624B\u81C2\uFF0C\u7528\u4E30\u6EE1\u7684\u8EAB\u4F53\u78E8\u64E6\u7740\u817B\u58F0\u9053\uFF1A\u300C\u90A3\u5974\u5BB6\u5C31\u5728\u69BB\u4E0A\u7ED9\u5927\u7237\u8D54\u7F6A\u2026\u2026\u300D</p><p>\u5927\u6C49\u4E00\u6123\uFF0C\u65CB\u5373\u54C8\u54C8\u5927\u7B11\uFF0C\u7C97\u624B\u4F38\u5230\u660E\u5170\u80A1\u95F4\uFF0C\u4E94\u6307\u7B95\u5F20\uFF0C\u7136\u540E\u4E2D\u6307\u4E00\u5F2F\u3002</p><p>\u75DB\u53EB\u58F0\u4E2D\uFF0C\u660E\u5170\u5DF2\u88AB\u90A3\u4EBA\u52FE\u7740\u79D8\u5904\u6258\u5230\u534A\u7A7A\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u773C\u65C1\u89C2\u826F\u4E45\uFF0C\u6B64\u65F6\u624D\u62E5\u7740\u7D2B\u73AB\u7F13\u6B65\u800C\u884C\u3002\u90A3\u4EBA\u4E00\u624B\u6402\u7740\u4F55\u5C0F\u82B8\uFF0C\u4E00\u624B\u6258\u7740\u660E\u5170\uFF0C\u773C\u775B\u76F4\u52FE\u52FE\u770B\u7740\u7D2B\u73AB\u3002\u4F55\u5C0F\u82B8\u751F\u6015\u518D\u60F9\u51FA\u4EC0\u4E48\u4E71\u5B50\uFF0C\u8FDE\u5FD9\u628A\u90A3\u4EBA\u7684\u624B\u585E\u5230\u81EA\u5DF1\u895F\u4E2D\uFF0C\u55F2\u58F0\u9053\uFF1A\u300C\u519B\u7237\uFF0C\u60A8\u6478\u6478\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u76EE\u4E0D\u659C\u89C6\uFF0C\u4E0E\u660E\u5170\u64E6\u80A9\u800C\u8FC7\u3002</p><p>\u767D\u751F\u751F\u7684\u7C89\u817F\u5939\u7740\u7C97\u9ED1\u7684\u5927\u624B\u75DB\u82E6\u5730\u626D\u52A8\uFF0C\u660E\u5170\u5FC3\u91CC\u7684\u75DB\u82E6\u6BD4\u8089\u4F53\u66F4\u751A\u3002\u770B\u7740\u59D0\u59D0\u5982\u6B64\u7EDD\u60C5\uFF0C\u5973\u5B69\u6CEA\u5982\u96E8\u4E0B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65E5\u8272\u660F\u9EC4\uFF0C\u71E0\u70ED\u4F9D\u7136\u4E0D\u51CF\u3002</p><p>\u300C\u4E2D\u539F\u9177\u6691\uFF0C\u6CA1\u6709\u5C71\u91CC\u90A3\u4E48\u6E05\u51C9\uFF0C\u6D1B\u9633\u53C8\u8FC7\u4E8E\u55A7\u56A3\u2026\u2026\u7B49\u5230\u585E\u5317\u5927\u6982\u662F\u4E03\u6708\uFF0C\u6B63\u662F\u79CB\u9AD8\u6C14\u723D\uFF0C\u8349\u957F\u9A6C\u80A5\u7684\u65F6\u5019\u3002\u5A18\uFF0C\u4F60\u559C\u6B22\u9F99\u57CE\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u4E0D\u8A00\u4E0D\u8BED\uFF0C\u5B9B\u5982\u6C89\u7761\u7684\u8299\u84C9\u3002</p><p>\u300C\u4EE5\u540E\u7684\u65E5\u5B50\u8FD8\u957F\u7740\u5462\uFF0C\u4F60\u603B\u4E0D\u80FD\u4E00\u8F88\u5B50\u4E0D\u8BF4\u8BDD\u5427\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u541F\u541F\u8BF4\u7740\uFF0C\u64A9\u8D77\u5979\u8138\u4E0A\u7684\u53D1\u4E1D\u3002</p><p>\u8427\u4F5B\u5974\u8BB8\u4E0B\u95ED\u53E3\u613F\u4E4B\u540E\uFF0C\u767D\u6C0F\u59D0\u59B9\u8D8A\u53D1\u6709\u6043\u65E0\u6050\uFF0C\u5BAB\u4E3B\u521A\u521A\u79BB\u5F00\uFF0C\u4E24\u4EBA\u5C31\u51D1\u8FC7\u6765\u7B11\u9053\uFF1A\u300C\u592B\u4EBA\u597D\u5927\u7684\u67B6\u5B50\uFF0C\u8FDE\u5BAB\u4E3B\u90FD\u6562\u4E0D\u7406\u4E0D\u776C\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u5FC3\u5934\u63EA\u7D27\uFF0C\u4E00\u8DEF\u4E0A\u4E24\u5973\u867D\u7136\u4E0D\u6562\u8650\u5F85\u5979\uFF0C\u4F46\u8A00\u8BED\u95F4\u7684\u7F9E\u8FB1\u5374\u6108\u6F14\u6108\u70C8\u3002</p><p>\u90A3\u4E9B\u523B\u8584\u8A00\u8BED\u4E0E\u513F\u5B50\u79BD\u517D\u822C\u7684\u4E71\u4F26\u4E00\u6837\uFF0C\u90FD\u4EE4\u5979\u65E0\u6CD5\u627F\u53D7\u3002</p><p>\u767D\u7389\u9E42\u6367\u7740\u6D51\u5706\u7684\u7389\u4E73\uFF0C\u5C06\u624B\u4E0A\u82AC\u82B3\u7684\u6CB9\u8102\u6D82\u5728\u4E73\u8089\u4E0A\uFF0C\u300C\u592B\u4EBA\u7684\u4E73\u623F\u597D\u50CF\u53C8\u5927\u4E86\u4E00\u4E9B\u5462\u3002\u300D</p><p>\u300C\u91CC\u9762\u6709\u5976\u6C34\u4E86\uFF0C\u5F53\u7136\u4F1A\u5927\u3002\u300D\u767D\u7389\u83BA\u63A9\u53E3\u7B11\u9053\uFF1A\u300C\u4F60\u731C\u592B\u4EBA\u7684\u5976\u6C34\u662F\u5BAB\u4E3B\u5148\u559D\uFF0C\u8FD8\u662F\u5C0F\u5BAB\u4E3B\u5148\u559D\uFF1F\u300D</p><p>\u767D\u7389\u9E42\u4E24\u624B\u4ECE\u4E73\u6839\u4E00\u8DEF\u63C9\u5230\u4E73\u5C16\uFF0C\u637B\u7740\u4E73\u5934\u62FD\u4E86\u62FD\uFF0C\u300C\u80AF\u5B9A\u662F\u5BAB\u4E3B\u5148\u559D\u4E86\u3002\u300D</p><p>\u300C\u6211\u731C\u4E5F\u662F\uFF0C\u5BAB\u4E3B\u559D\u5269\u4E0B\u624D\u4F1A\u5582\u5C0F\u5BAB\u4E3B\u3002\u300D</p><p>\u767D\u7389\u9E42\u563B\u563B\u7B11\u9053\uFF1A\u300C\u5BAB\u4E3B\u559D\u5B8C\u8FD8\u80FD\u5269\u4E0B\u5417\uFF1F\u300D</p><p>\u300C\u54DF\uFF0C\u8FD9\u4E48\u5927\u7684\u5976\u5B50\u8FD8\u6015\u4E0D\u591F\u559D\u5417\uFF1F\u300D\u767D\u7389\u83BA\u542B\u7740\u8427\u4F5B\u5974\u7684\u4E73\u5934\u54C1\u5482\u7740\u8BF4\uFF0C\u300C\u592B\u4EBA\u8FD9\u4E48\u7F8E\uFF0C\u5976\u6C34\u80AF\u5B9A\u53C8\u9999\u53C8\u751C\uFF0C\u6211\u4E5F\u60F3\u559D\u4E00\u53E3\u5462\u3002\u300D</p><p>\u7F8E\u5987\u9759\u9759\u8EBA\u5728\u69BB\u4E0A\uFF0C\u7389\u5BB9\u65E0\u6CE2\u3002\u53EA\u6709\u80F8\u524D\u7684\u9999\u4E73\u8DF3\u52A8\u7740\uFF0C\u5728\u522B\u4EBA\u624B\u4E2D\u88AB\u6063\u610F\u73A9\u5F04\u3002</p><p>\u767D\u7389\u9E42\u8D34\u5728\u8427\u4F5B\u5974\u8033\u8FB9\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5BAB\u4E3B\u80FD\u559D\u5230\u592B\u4EBA\u7684\u5976\u6C34\uFF0C\u53EF\u592B\u4EBA\u53EA\u80FD\u559D\u5BAB\u4E3B\u7684\u9F99\u7CBE\u2014\u2014\u90A3\u4E1C\u897F\u82E6\u5DF4\u5DF4\u7684\uFF0C\u4E00\u70B9\u90FD\u4E0D\u597D\u559D\u2026\u2026\u300D</p><p>\u300C\u4F60\u4E0D\u559C\u6B22\uFF0C\u592B\u4EBA\u559C\u6B22\u554A\u3002\u6BCF\u6B21\u88AB\u5BAB\u4E3B\u64CD\uFF0C\u592B\u4EBA\u90FD\u9AD8\u5174\u5F97\u5FEB\u6655\u8FC7\u53BB\u4E86\uFF0C\u8925\u5B50\u80FD\u6E7F\u8FD9\u4E48\u5927\u4E00\u7247\u3002\u300D\u767D\u7389\u83BA\u4E0D\u614C\u4E0D\u5FD9\u5730\u51FB\u788E\u8427\u4F5B\u5974\u7684\u5E73\u9759\u3002</p><p>\u767D\u7389\u9E42\u6258\u8D77\u8427\u4F5B\u5974\u7684\u53CC\u817F\uFF0C\u9732\u51FA\u5305\u88F9\u7740\u5C3F\u5E03\u7684\u96EA\u81C0\uFF0C\u6446\u6210\u4EA4\u5ABE\u7684\u6A21\u6837\uFF0C\u300C\u592B\u4EBA\u6700\u559C\u6B22\u8BA9\u4EBA\u5BB6\u64CD\u5C41\u773C\u4E86\uFF0C\u5BAB\u4E3B\u7684\u9F99\u6839\u4E00\u8FDB\u53BB\uFF0C\u592B\u4EBA\u7684\u5976\u5934\u5C31\u786C\u786C\u7684\u2026\u2026\u300D</p><p>\u300C\u54A6\uFF1F\u592B\u4EBA\u600E\u4E48\u54ED\u4E86\uFF1F\u300D\u767D\u7389\u83BA\u60CA\u8BB6\u4E2D\u5E26\u7740\u63A9\u4E0D\u4F4F\u7684\u7B11\u610F\u3002\u8427\u4F5B\u5974\u6BCF\u6B21\u88AB\u4E24\u4EBA\u8BF4\u5F97\u6D41\u6CEA\uFF0C\u90FD\u4F1A\u7ED9\u5979\u4EEC\u83AB\u5927\u7684\u5FEB\u6170\u3002\u592B\u4EBA\u5C48\u8FB1\u7684\u6CEA\u6C34\uFF0C\u662F\u5979\u4EEC\u552F\u4E00\u7684\u5FEB\u4E50\u3002</p><p>\u300C\u88C5\u7684\u5427\uFF1F\u5C11\u592B\u4EBA\u4E5F\u603B\u662F\u54ED\u54ED\u557C\u557C\u7684\uFF0C\u8FD8\u4E0D\u662F\u88C5\u51FA\u53EF\u601C\u7684\u6837\u5B50\u8BA9\u5BAB\u4E3B\u591A\u64CD\u5979\u51E0\u6B21\u2026\u2026\u300D\u767D\u7389\u9E42\u6316\u82E6\u9053\u3002</p><p>\u300C\u662F\u4E86\uFF0C\u80AF\u5B9A\u662F\u88C5\u7684\u3002\u592B\u4EBA\u53C8\u662F\u4E0A\u540A\u53C8\u662F\u7EDD\u98DF\u53C8\u662F\u54AC\u820C\uFF0C\u5176\u5B9E\u8FD8\u662F\u4E0D\u60F3\u6B7B\u3002\u300D</p><p>\u767D\u7389\u83BA\u5351\u5937\u5730\u8BF4\u3002</p><p>\u8427\u4F5B\u5974\u53EF\u4EE5\u95ED\u4E0A\u773C\u775B\u4E0D\u770B\uFF0C\u53EF\u4EE5\u5408\u4E0A\u5634\u4E0D\u8BF4\u8BDD\uFF0C\u4F46\u5979\u65E0\u6CD5\u63A9\u4F4F\u8033\u6735\uFF0C\u8EB2\u907F\u5979\u4EEC\u7684\u5632\u8BBD\u3002\u5C16\u523B\u7684\u8BDD\u8BED\u4E00\u5B57\u4E00\u53E5\u523A\u5728\u5FC3\u5E95\uFF0C\u5C06\u5979\u6DF9\u6CA1\u5728\u65E0\u8FB9\u7684\u7F9E\u8FB1\u4E2D\u3002</p><p>\u300C\u4E3A\u4EC0\u4E48\u8981\u6B7B\u554A\uFF1F\u5F53\u592B\u4EBA\u4E0D\u662F\u5F88\u5F00\u5FC3\u5417\uFF1F\u300D</p><p>\u300C\u4EC0\u4E48\u592B\u4EBA\uFF0C\u53EA\u4E0D\u8FC7\u662F\u5757\u8BA9\u5BAB\u4E3B\u73A9\u7684\u7F8E\u8089\u3002\u300D\u767D\u7389\u83BA\u5728\u7F8E\u5987\u817F\u6839\u4E00\u62E7\uFF0C\u300C\u5403\u996D\u8981\u4EBA\u5582\uFF0C\u7A7F\u8863\u8981\u4EBA\u5E2E\uFF0C\u62C9\u5C4E\u62C9\u5C3F\u8FD8\u8981\u4EBA\u4F3A\u5019\u2014\u2014\u6839\u672C\u5C31\u662F\u4E2A\u5E9F\u7269\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u4E94\u5185\u4FF1\u711A\uFF0C\u7D27\u7D27\u95ED\u7740\u7F8E\u76EE\uFF0C\u70ED\u6CEA\u6EC2\u6CB1\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6108\u53D1\u5FEB\u610F\uFF0C\u4FEF\u5728\u7F8E\u5987\u8033\u8FB9\u8BF4\u9053\uFF1A\u300C\u8981\u4E0D\u662F\u6709\u51E0\u4E2A\u6D1E\u80FD\u8BA9\u5BAB\u4E3B\u63D2\u7740\u73A9\uFF0C\u4F60\u8FDE\u8DEF\u8FB9\u7684\u91CE\u72D7\u90FD\u4E0D\u5982\uFF01\u300D\u4E24\u5973\u9694\u7740\u539A\u539A\u7684\u5C3F\u5E03\u5728\u5979\u4E0B\u4F53\u7528\u529B\u6363\u5F04\uFF0C\u300C\u4F60\u73B0\u5728\u5C31\u662F\u9760\u8FD9\u4E24\u4E2A\u6D1E\u6D3B\u7740\uFF01\u660E\u767D\u5417\uFF1F\u4F60\u7684\u5C44\u548C\u5C41\u773C\uFF01\u300D</p><p>\u7F8E\u5987\u568E\u5555\u75DB\u54ED\u3002\u660F\u6697\u7684\u5149\u7EBF\u4E0B\uFF0C\u767D\u5AE9\u7684\u8089\u4F53\u4EFF\u4F5B\u4E00\u62B9\u4ECE\u6C60\u4E2D\u635E\u8D77\u7684\u51C4\u5A49\u6708\u5149\uFF0C\u6EF4\u7740\u6E7F\u6E7F\u7684\u6C34\u75D5\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6BCF\u6B21\u8427\u4F5B\u5974\u75DB\u54ED\u65F6\uFF0C\u767D\u6C0F\u59D0\u59B9\u90FD\u4F1A\u5F88\u5C0F\u5FC3\u5730\u7528\u6795\u5934\u63A9\u4F4F\u592B\u4EBA\u7684\u54ED\u58F0\u3002\u56E0\u6B64\u7D2B\u73AB\u5E76\u4E0D\u77E5\u9053\u6BCD\u4EB2\u6240\u53D7\u7684\u5C48\u8FB1\u3002\u5979\u4FEF\u5728\u7AF9\u69BB\u4E0A\uFF0C\u4FCF\u8138\u57CB\u5728\u81C2\u5F2F\uFF0C\u80CC\u81C0\u4F18\u7F8E\u7684\u66F2\u7EBF\u968F\u7740\u547C\u5438\u67D4\u67D4\u8D77\u4F0F\u3002</p><p>\u6CA1\u6709\u4EFB\u4F55\u5F81\u5146\uFF0C\u4E00\u53EA\u624B\u51ED\u7A7A\u4F38\u6765\uFF0C\u6380\u5F00\u853D\u4F53\u7684\u7EC6\u7EB1\u3002</p><p>\u6155\u5BB9\u9F99\u6084\u6084\u5165\u5185\uFF0C\u672C\u60F3\u5413\u5979\u4E00\u8DF3\uFF0C\u4F46\u59B9\u59B9\u6BEB\u65E0\u53CD\u5E94\uFF0C\u4EFF\u4F5B\u65E9\u5DF2\u77E5\u9053\u4ED6\u7684\u4E3E\u52A8\u3002</p><p>\u7EC6\u7EB1\u4E0B\u518D\u65E0\u5BF8\u7F15\uFF0C\u7C89\u80CC\u96EA\u81C0\u4E00\u89C8\u65E0\u4F59\u3002\u7EA4\u7F8E\u7684\u8170\u80A2\u73B2\u73D1\u6709\u81F4\uFF0C\u6D51\u5706\u7684\u7F8E\u81C0\u6ED1\u817B\u5982\u8102\u3002\u5904\u5904\u6E29\u9999\u8F6F\u7389\uFF0C\u6676\u83B9\u751F\u8F89\u3002\u53EA\u662F\u81C0\u7F1D\u4E2D\u5374\u9732\u51FA\u4E00\u89D2\u8584\u7EB1\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u62C9\uFF0C\u96EA\u767D\u7684\u4E1D\u5DFE\u5E94\u624B\u800C\u51FA\uFF0C\u4E0A\u9762\u8840\u8FF9\u6591\u6591\uFF0C\u5B9B\u5982\u6563\u843D\u7684\u82B1\u74E3\u3002</p><p>\u4ED6\u63B0\u5F00\u7C89\u81C0\uFF0C\u53EA\u89C1\u539F\u672C\u7C89\u8272\u7684\u83CA\u809B\u6CBE\u7740\u9C9C\u8840\uFF0C\u53C8\u7EA2\u53C8\u80BF\uFF0C\u83CA\u7EB9\u4E4D\u5F00\u4E09\u6761\u4F24\u75D5\uFF0C\u9732\u51FA\u51E0\u8BB8\u7EA2\u8089\uFF0C\u5E78\u597D\u5F53\u65F6\u5E76\u672A\u5168\u6839\u8FDB\u5165\uFF0C\u4F24\u53E3\u5E76\u4E0D\u592A\u6DF1\u3002</p><p>\u300C\u8EBA\u597D\uFF0C\u54E5\u54E5\u5E2E\u4F60\u62B9\u70B9\u836F\u3002\u300D</p><p>\u7D2B\u73AB\u626D\u8170\u5750\u8D77\uFF0C\u8138\u4E0A\u6E7F\u6E7F\u7684\uFF0C\u4E0D\u77E5\u662F\u6C57\u662F\u6CEA\u3002\u5979\u4E00\u5B57\u4E00\u53E5\u7684\u8BF4\uFF1A\u300C\u6211\u7EDD\u4E0D\u518D\u7528\u4F60\u7684\u4EFB\u4F55\u4E00\u79CD\u836F\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u51DD\u89C6\u7D2B\u73AB\u7247\u523B\uFF0C\u300C\u90A3\u4E48\uFF0C\u300D\u4ED6\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u8DB4\u4E0B\uFF0C\u8BA9\u54E5\u54E5\u5E72\u4F60\u7684\u5C41\u773C\u513F\u3002\u300D</p><p>\u7D2B\u73AB\u4E0B\u5348\u8D70\u4E86\u4E00\u8DEF\uFF0C\u540E\u5EAD\u75BC\u75DB\u4E0D\u5DF2\uFF0C\u73B0\u5728\u4F24\u5904\u672A\u7652\uFF0C\u8FD9\u6DF7\u86CB\u53C8\u8981\u8FDB\u6765\u3002\u5979\u5FC3\u4E0B\u6C14\u82E6\uFF0C\u661F\u7738\u6E10\u6E10\u6E7F\u6DA6\uFF0C\u534A\u664C\u6CE3\u58F0\u9053\uFF1A\u300C\u4F60\u4E3A\u4EC0\u4E48\u8981\u5F04\u4EBA\u5BB6\u90A3\u91CC\u2026\u2026\u300D</p><p>\u300C\u5973\u4EBA\u8EAB\u4E0A\u8FD9\u4E9B\u5730\u65B9\u90FD\u53EF\u4EE5\u7528\uFF0C\u4F60\u662F\u6211\u59BB\u5B50\uFF0C\u5F53\u7136\u5E94\u8BE5\u7528\u5B83\u6765\u8BA9\u4E08\u592B\u5F00\u5FC3\u3002\u300D</p><p>\u7D2B\u73AB\u5446\u4E86\u4E00\u4F1A\u513F\uFF0C\u4FEF\u8EAB\u4F0F\u5728\u69BB\u4E0A\u3002\u5F53\u8089\u68D2\u9876\u4F4F\u53D7\u4F24\u7684\u540E\u5EAD\uFF0C\u5979\u5FCD\u4E0D\u4F4F\u95EE\u9053\uFF1A\u300C\u4F60\u8981\u2026\u2026\u5C04\u5728\u91CC\u9762\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u538B\u5728\u9999\u8F6F\u7684\u5A07\u8EAF\u4E0A\uFF0C\u8D34\u7740\u7D2B\u73AB\u5149\u6ED1\u7684\u7389\u8138\uFF0C\u6DE1\u6DE1\u7B11\u9053\uFF1A\u300C\u65E0\u6240\u8C13\u3002\u300D</p><p>\u4ED6\u63E1\u4F4F\u59B9\u59B9\u7684\u624B\u638C\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4F60\u5DF2\u7ECF\u6000\u4E0A\u54E5\u54E5\u7684\u5B69\u5B50\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u5FC3\u5934\u731B\u7136\u4E00\u8DF3\uFF0C\u5589\u5934\u987F\u65F6\u54FD\u4F4F\uFF0C\u518D\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u300C\u4F60\u7684\u7678\u6C34\u5DF2\u7ECF\u665A\u4E86\u534A\u6708\u5427\u3002\u6211\u60F3\uFF0C\u4EE5\u540E\u4E5D\u4E2A\u6708\uFF0C\u5B83\u4E5F\u4E0D\u4F1A\u6765\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u5FCD\u4E0D\u4F4F\u6218\u6817\u8D77\u6765\uFF0C\u8111\u4E2D\u55E1\u55E1\u4F5C\u54CD\uFF0C\u300C\u4E0D\u53EF\u80FD\u2026\u2026\u6211\u600E\u4E48\u80FD\u6000\u4E0A\u4ED6\u7684\u5B69\u5B50\uFF0C\u5929\u751F\u7684\u767D\u75F4\u3001\u6B8B\u75BE\u2026\u2026\u300D</p><p>\u300C\u53F6\u62A4\u6CD5\u7684\u79CD\u5B50\u7075\u4E39\u679C\u7136\u4E0D\u9519\uFF0C\u53EA\u6015\u7B2C\u4E00\u6B21\u6B22\u5408\uFF0C\u4F60\u5C31\u73E0\u80CE\u6697\u7ED3\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u58F0\u97F3\u6E10\u6E10\u5174\u594B\u8D77\u6765\uFF0C\u300C\u7528\u4E0D\u4E86\u591A\u4E45\uFF0C\u4F60\u7684\u809A\u5B50\u5C31\u4F1A\u5927\u8D77\u6765\uFF0C\u4F1A\u548C\u5A18\u4E00\u6837\u5455\u5410\uFF0C\u53D1\u61D2\u3001\u55DC\u7761\u3002\u4F60\u4EEC\u4FE9\u540C\u65F6\u633A\u7740\u5706\u9F13\u9F13\u7684\u809A\u5B50\uFF0C\u91CC\u9762\u6709\u6211\u7684\u5B69\u5B50\u2026\u2026\u4E5D\u4E2A\u6708\u540E\uFF0C\u4F60\u4F1A\u751F\u4E0B\u4E00\u4E2A\u7740\u7EAF\u6B63\u6155\u5BB9\u6C0F\u8840\u7EDF\u7684\u5929\u624D\uFF0C\u7136\u540E\u662F\u7B2C\u4E8C\u4E2A\u3001\u7B2C\u4E09\u4E2A\u2026\u2026\u4F60\u4F1A\u7ED9\u54E5\u54E5\u751F\u4E0B\u4E00\u7FA4\u5B69\u5B50\uFF0C\u6211\u4EEC\u4ECE\u91CC\u6311\u4E00\u4E2A\u6700\u5F3A\u58EE\u3001\u6700\u806A\u660E\u3001\u6700\u6F02\u4EAE\u7684\u5F53\u592A\u5B50\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u8F7B\u8F7B\u4E00\u7B11\uFF0C\u300C\u54E5\u54E5\uFF0C\u8FDB\u4EBA\u5BB6\u524D\u9762\u5427\u3002\u7B49\u4EBA\u5BB6\u540E\u9762\u597D\u4E00\u4E9B\u518D\u7528\u5B83\u4F3A\u5019\u54E5\u54E5\uFF0C\u597D\u5417\uFF1F\u300D</p><p>\u300C\u597D\u3002\u300D\u6155\u5BB9\u9F99\u75DB\u5FEB\u5730\u7B54\u5E94\u4E86\u3002</p><p>\u5F53\u665A\u7D2B\u73AB\u5A07\u5A9A\u6A2A\u751F\uFF0C\u8BF4\u4E0D\u5C3D\u7684\u98CE\u6D41\u5A49\u8F6C\uFF0C\u4E0E\u6155\u5BB9\u9F99\u5728\u69BB\u4E0A\u6574\u6574\u7EA0\u7F20\u4E00\u591C\uFF0C\u6155\u5BB9\u9F99\u5BF9\u5979\u7A81\u7136\u8FF8\u53D1\u7684\u6FC0\u60C5\u6709\u4E9B\u83AB\u540D\u5176\u5999\uFF0C\u4F46\u65E0\u8BBA\u5982\u4F55\uFF0C\u6BD4\u8D77\u4EE5\u5F80\u7684\u6297\u62D2\uFF0C\u8FD9\u6837\u7684\u8F6C\u53D8\u4ED6\u662F\u6C42\u4E4B\u4E0D\u5F97\u3002</p><p>\u73AB\u7470\u4ED9\u5B50\u9161\u989C\u80DC\u706B\uFF0C\u9999\u6C57\u6DCB\u6F13\uFF0C\u4E00\u6B21\u53C8\u4E00\u6B21\u9AD8\u6F6E\u4F7F\u5979\u5A07\u8EAF\u9178\u8F6F\uFF0C\u4F53\u8F6F\u5982\u7EF5\u3002</p><p>\u4F46\u5979\u8FD8\u662F\u6781\u529B\u8038\u52A8\u4E0B\u8179\uFF0C\u4E0E\u5AE1\u4EB2\u54E5\u54E5\u75AF\u72C2\u5730\u4EA4\u5408\u3002\u9633\u5177\u5728\u6CE5\u6CDE\u7684\u8089\u7A74\u5185\u4E0D\u505C\u8FDB\u51FA\uFF0C\u4E0E\u6B64\u540C\u65F6\uFF0C\u4E39\u7530\u5185\u65CB\u8F6C\u7684\u771F\u6C14\u4E5F\u6108\u53D1\u84EC\u52C3\u3002</p><p>\u300C\u5E08\u7236\uFF0C\u5F92\u513F\u5F88\u5FEB\u5C31\u80FD\u7EC3\u5230\u7B2C\u516B\u5C42\u51E4\u51F0\u4E8E\u98DE\u2014\u2014\u5728\u8FD9\u4E2A\u5B7D\u79CD\u51FA\u751F\u4E4B\u524D\uFF01\u300D</p>',69);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
