import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_79" tabindex="-1"><a class="header-anchor" href="#_79" aria-hidden="true">#</a> 79</h1><p>\u7C97\u91CE\u7684\u7B11\u58F0\u5728\u77F3\u58C1\u4E0A\u56DE\u6C64\u7740\uFF0C\u77F3\u5BA4\u4E2D\u5145\u6EE1\u6D53\u91CD\u7684\u4F53\u81ED\u548C\u7CBE\u6DB2\u7684\u5473\u9053\u3002\u5F71\u5F71\u7EF0\u7EF0\u7684\u706B\u5149\u4E0B\uFF0C\u53EA\u89C1\u4E00\u5806\u8D64\u88F8\u7684\u80CC\u810A\u6324\u6210\u4E00\u56E2\uFF0C\u4EFF\u4F5B\u4E00\u7FA4\u65E0\u58F3\u7684\u8D1D\u7C7B\u4E0D\u505C\u8815\u52A8\u3002\u6BCF\u4E00\u4E2A\u80CC\u810A\u90FD\u7CBE\u58EE\u6709\u529B\uFF0C\u800C\u4E14\u5E03\u6EE1\u6C57\u6C34\u3002\u4F46\u6CA1\u6709\u4E00\u4E2A\u4EBA\u5ACC\u70ED\uFF0C\u53CD\u800C\u50CF\u89C9\u5F97\u8FD8\u4E0D\u591F\u70ED\u95F9\u4F3C\u7684\uFF0C\u62FC\u547D\u9F13\u566A\u3002</p><p>\u300C\u8377\u554A\uFF01\u300D\u4E00\u58F0\u66B4\u559D\uFF0C\u4EBA\u7FA4\u6F6E\u6C34\u822C\u5206\u5F00\u3002\u60AC\u6302\u7684\u94A2\u7D22\u6E10\u6E10\u9759\u6B62\uFF0C\u9000\u6F6E\u7684\u6C99\u6EE9\u7559\u4E0B\u4E00\u7247\u60E8\u767D\u3002\u7531\u535D\u6587\u535D\u4EBA\u535D\u4E66\u535D\u5C4B\u535D\u6574\u535D\u7406</p><p>\u4E00\u5177\u4F18\u7F8E\u7684\u5973\u4F53\u88AB\u5341\u51E0\u6839\u94A2\u7D22\u60AC\u5728\u534A\u7A7A\uFF0C\u4EFF\u4F5B\u4E00\u4E2A\u6CA1\u6709\u751F\u547D\u7684\u7275\u7EBF\u6728\u5076\u3002\u5979\u56DB\u80A2\u5F20\u5F00\uFF0C\u88AB\u63B0\u6210\u5E73\u884C\u7684\u624B\u811A\u5404\u7CFB\u7740\u4E24\u6839\u94A2\u7D22\uFF0C\u624B\u8155\u811A\u8155\u7684\u76AE\u80A4\u88AB\u5207\u5F00\u5BF8\u8BB8\uFF0C\u4F24\u53E3\u5DF2\u7ECF\u7652\u5408\uFF0C\u6D01\u767D\u7684\u7B4B\u8171\u5374\u5F13\u5F26\u822C\u6311\u9732\u5728\u5916\u3002\u80A9\u5934\u5404\u6709\u4E00\u6839\u7A7F\u900F\u7435\u7436\u9AA8\u7684\u94A2\u6761\u3002</p><p>\u4E3A\u4E86\u4F7F\u5979\u66F4\u52A0\u75DB\u82E6\uFF0C\u5973\u4F53\u5E76\u672A\u6302\u6210\u6C34\u5E73\uFF0C\u800C\u662F\u81C0\u90E8\u7565\u9AD8\uFF0C\u4F7F\u4F53\u91CD\u5C3D\u53EF\u80FD\u591A\u7684\u538B\u5728\u80A9\u5934\u3002</p><p>\u300C\u8FC7\u763E\uFF0C\u8FC7\u763E\u3002\u8FD9\u5A4A\u5B50\u4E0D\u6127\u662F\u5929\u4E0B\u7B2C\u4E00\u9AD8\u624B\uFF0C\u64CD\u8D77\u6765\u5C31\u662F\u723D\u554A\uFF01\u300D\u4E00\u540D\u6C49\u5B50\u6296\u7740\u521A\u5C04\u5B8C\u7CBE\u7684\u9633\u5177\u9AD8\u58F0\u53EB\u9053\u3002</p><p>\u300C\u65AD\u864E\u67AA\u4E5F\u4E0D\u8D56\uFF0C\u521A\u517B\u597D\u4F24\u5C31\u80FD\u5E72\u8FD9\u4E48\u4E45\u3002\u300D</p><p>\u300C\u64CD\uFF01\u618B\u4E86\u4E00\u4E2A\u6708\uFF0C\u80FD\u4E0D\u5389\u5BB3\u5417\uFF1F\u300D\u5F90\u65AD\u864E\u6478\u6478\u80F8\u53E3\u7684\u4F24\u75A4\uFF0C\u300C\u8FD9\u5A4A\u5B50\u771F\u4ED6\u5988\u72E0\uFF0C\u5DEE\u70B9\u513F\u8981\u4E86\u7237\u7684\u547D\uFF01\u300D</p><p>\u300C\u4F60\u8FD8\u7B97\u597D\u7684\uFF0C\u5DF4\u9675\u67AD\u591A\u6A2A\u554A\uFF0C\u4E00\u62DB\u80F3\u818A\u5C31\u5E9F\u4E86\u3002\u300D</p><p>\u6709\u4EBA\u95EE\u9053\uFF1A\u300C\u8FD9\u5BB6\u4F19\u600E\u4E48\u4F24\u8FD8\u6CA1\u597D\u5C31\u8D70\u4E86\uFF0C\u4E5F\u6CA1\u6765\u64CD\u8FD9\u5A4A\u5B50\u51E0\u4E0B\u51FA\u51FA\u6C14\uFF1F\u300D</p><p>\u300C\u6CA1\u9762\u5B50\u5457\u3002\u8BF4\u8D77\u6765\u4E5F\u662F\u5DDD\u8700\u8DFA\u5730\u5C71\u54CD\u7684\u4EBA\uFF0C\u8FD9\u56DE\u8138\u53EF\u4E22\u5927\u4E86\u3002\u300D</p><p>\u300C\u9E21\u5DF4\uFF01\u300D\u5F90\u65AD\u864E\u5728\u795E\u5C3C\u8179\u4E0A\u72E0\u72E0\u62CD\u4E86\u4E00\u638C\uFF0C\u300C\u80FD\u64CD\u5230\u8FD9\u5A4A\u5B50\uFF0C\u9762\u5B50\u5C31\u627E\u56DE\u6765\u4E86\uFF01\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u5C0F\u8179\u4E00\u9707\uFF0C\u6EE1\u6EA2\u7684\u6D53\u7CBE\u4ECE\u8089\u7A74\u4E2D\u55B7\u51FA\u4E00\u9053\u6D4A\u767D\u7684\u5F27\u7EBF\u3002\u53F6\u884C\u5357\u5999\u624B\u65BD\u6CBB\u4E0B\uFF0C\u5979\u8098\u819D\u7684\u788E\u9AA8\u5DF2\u88AB\u5254\u51FA\uFF0C\u624B\u811A\u8FD8\u4FDD\u6301\u5B8C\u6574\u3002</p><p>\u65E5\u4EE5\u7EE7\u591C\u7684\u75AF\u72C2\u8E42\u8E8F\uFF0C\u795E\u5C3C\u4E73\u9634\u7EA2\u80BF\u4E0D\u582A\uFF0C\u7855\u5927\u7684\u4E73\u623F\u6B6A\u5728\u4F53\u4FA7\uFF0C\u4E73\u5934\u88AB\u63EA\u6210\u62C7\u6307\u5927\u5C0F\u4E00\u622A\uFF0C\u7EA2\u5F97\u53D1\u7D2B\u3002\u79D8\u5904\u80A5\u539A\u7684\u8089\u82B1\u80BF\u6210\u4E00\u56E2\uFF0C\u7FFB\u5377\u7684\u5AE9\u8089\u5360\u636E\u4E86\u6574\u4E2A\u80A1\u95F4\uFF0C\u6EF4\u8840\u822C\u6BB7\u7EA2\u3002\u82B1\u8482\u4ECE\u62E5\u6324\u7684\u5AE9\u8089\u4E2D\u63A2\u51FA\uFF0C\u9876\u7AEF\u8DB3\u6709\u5C0F\u6307\u6307\u5C16\u5927\u5C0F\uFF0C\u51E0\u4E4E\u8981\u6DA8\u51FA\u5305\u76AE\u3002\u7D27\u6328\u7740\u7684\u83CA\u809B\u4E5F\u9AD8\u9AD8\u9F13\u8D77\uFF0C\u809B\u7AA6\u5410\u9732\uFF0C\u5206\u660E\u4E5F\u63A5\u7EB3\u8FC7\u4E0D\u5C11\u8089\u68D2\u3002</p><p>\u5F90\u65AD\u864E\u4E00\u638C\u62CD\u51FA\u7CBE\u6DB2\uFF0C\u4E0D\u7531\u5174\u81F4\u5927\u53D1\uFF0C\u4E24\u624B\u5939\u7740\u795E\u5C3C\u7684\u8170\u8179\u731B\u4E00\u7528\u529B\uFF0C\u6D53\u7CBE\u6FC0\u5C04\u800C\u51FA\u3002</p><p>\u300C\u54C8\u54C8\uFF0C\u522B\u4EBA\u5C3F\u51FA\u6765\u662F\u9EC4\u7684\uFF0C\u8FD9\u5A4A\u5B50\u5C3F\u51FA\u6765\u662F\u767D\u7684\uFF0C\u90FD\u662F\u5F1F\u5144\u4EEC\u7684\u7CBE\u6DB2\u3002\u300D</p><p>\u5F90\u65AD\u864E\u538B\u98CE\u7BB1\u4E00\u6837\u538B\u643E\u795E\u5C3C\u7684\u5C0F\u8179\uFF0C\u9633\u7CBE\u4E00\u80A1\u80A1\u6E90\u6E90\u4E0D\u65AD\u7684\u4ECE\u7EA2\u80BF\u7684\u5AE9\u8089\u4E2D\u6E85\u51FA\u3002</p><p>\u300C\u8FD9\u5A4A\u5B50\u7684\u5C44\u591F\u80FD\u76DB\u7684\u554A\u3002\u300D\u4F17\u4EBA\u56F4\u4E0A\u6765\uFF0C\u770B\u96EA\u5CF0\u795E\u5C3C\u4F53\u5185\u7A76\u7ADF\u704C\u4E86\u591A\u5C11\u7CBE\u6DB2\u3002</p><p>\u55B7\u6D8C\u7684\u9633\u7CBE\u4E00\u523B\u949F\u540E\u624D\u6162\u6162\u6B62\u6B47\uFF0C\u8DB3\u6709\u4E00\u6D77\u7897\u7684\u4EFD\u91CF\uFF0C\u6700\u540E\u5269\u4E0B\u4E00\u7F15\u767D\u8272\u6DB2\u4F53\u6302\u5728\u8179\u4E0B\u3002</p><p>\u300C\u563F\uFF01\u773C\u7741\u8FD9\u4E48\u5927\uFF0C\u662F\u4E0D\u662F\u4E5F\u60F3\u770B\u770B\uFF1F\u300D\u4E00\u4EBA\u6258\u8D77\u96EA\u5CF0\u795E\u5C3C\u4F4E\u5782\u7684\u67D4\u9888\u9053\uFF1A\u300C\u8FD9\u5A4A\u5B50\u662F\u5389\u5BB3\uFF0C\u64CD\u4E86\u4E00\u4E2A\u591A\u6708\u8FD8\u6CA1\u6B7B\uFF0C\u662F\u4E0D\u662F\u8FD8\u60F3\u9A82\u4EBA\u554A\uFF1F\u300D\u8BF4\u7740\u90A3\u4EBA\u4E00\u633A\u8170\uFF0C\u9633\u5177\u76F4\u76F4\u6345\u5165\u795E\u5C3C\u5634\u4E2D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u4EC7\u6068\u7684\u773C\u795E\u88AB\u6321\u5728\u8EAB\u540E\uFF0C\u53EA\u5269\u4E00\u4E2A\u4E0B\u5DF4\u5728\u4ED6\u80EF\u4E0B\u6447\u6446\u3002\u65E0\u4F11\u6B62\u7684\u8F6E\u5978\u4E0B\uFF0C\u5979\u65E9\u5DF2\u7CBE\u75B2\u529B\u5C3D\uFF0C\u82E5\u975E\u5185\u529F\u672A\u5931\uFF0C\u8FD9\u6837\u7684\u65E5\u5B50\u7EDD\u6491\u4E0D\u8FC7\u4E09\u5929\u3002\u71C3\u70E7\u7684\u6068\u610F\u4F7F\u795E\u5C3C\u62FC\u547D\u54AC\u7D27\u7259\u5173\uFF0C\u5634\u4E2D\u7684\u94A2\u7B8D\u683C\u683C\u4F5C\u54CD\u3002\u4F46\u90A3\u6839\u8165\u81ED\u7684\u8089\u68D2\u4ECD\u662F\u6BEB\u65E0\u963B\u62E6\u5730\u4ECE\u5979\u5507\u4E0A\u820C\u4E0A\u989A\u4E0A\u6345\u8FC7\uFF0C\u76F4\u5165\u54BD\u5589\u3002</p><p>\u4E00\u6839\u9633\u5177\u72E0\u72E0\u6345\u5165\u672A\u66FE\u5E72\u6DB8\u8FC7\u7684\u8089\u7A74\u5185\uFF0C\u53C8\u5F00\u59CB\u4E86\u4E00\u6CE2\u65B0\u7684\u5978\u6DEB\u3002\u51E0\u5341\u53EA\u624B\u540C\u65F6\u4F38\u5230\u795E\u5C3C\u8089\u4F53\u4E0A\u56DB\u5904\u4E71\u6478\uFF0C\u751A\u81F3\u6709\u4EBA\u4F7F\u51FA\u94C1\u677F\u6865\u529F\u592B\uFF0C\u4ECE\u795E\u5C3C\u80CC\u540E\u5978\u6DEB\u5979\u7684\u809B\u95E8\u3002\u4E00\u65F6\u95F4\u77F3\u5BA4\u5185\u4EBA\u58F0\u9F0E\u6CB8\uFF0C\u68D5\u8910\u8272\u7684\u810A\u80CC\u6DF9\u6CA1\u4E86\u96EA\u767D\u7684\u8089\u4F53\u3002</p><p>\u300C\u54B3\u3002\u300D\u4E00\u4E2A\u82CD\u8001\u7684\u58F0\u97F3\u54CD\u8D77\u3002</p><p>\u58F0\u97F3\u5E76\u4E0D\u5927\uFF0C\u4F46\u6BCF\u4E2A\u4EBA\u90FD\u80FD\u542C\u5230\u3002\u4F17\u4EBA\u604B\u604B\u4E0D\u820D\u7684\u79BB\u5F00\u795E\u5C3C\u7684\u8089\u4F53\uFF0C\u8BA9\u5F00\u4E00\u6761\u901A\u9053\u3002\u81EA\u6709\u4EBA\u9012\u6765\u5EA7\u6905\uFF0C\u8BA9\u4E24\u4F4D\u62A4\u6CD5\u5750\u4E0B\u3002</p><p>\u53F6\u884C\u5357\u968F\u624B\u628A\u836F\u7BB1\u653E\u5728\u795E\u5C3C\u8179\u4E0A\uFF0C\u672C\u5F85\u7FFB\u5F00\u5979\u773C\u76AE\u770B\u770B\uFF0C\u4E0D\u6599\u96EA\u5CF0\u795E\u5C3C\u7ADF\u662F\u7F8E\u76EE\u5706\u77AA\u3002\u4ED6\u677F\u7740\u8138\u68C0\u67E5\u4E86\u795E\u5C3C\u7684\u624B\u811A\u3001\u7435\u7436\u9AA8\uFF0C\u7136\u540E\u6253\u5F00\u6728\u7BB1\u3002</p><p>\u6C90\u58F0\u4F20\u5750\u5728\u5BA4\u5185\uFF0C\u5FC3\u795E\u5374\u7CFB\u5728\u6D1B\u9633\u548C\u96C1\u95E8\u3002\u5BAB\u4E3B\u752B\u51FA\u7EC8\u5357\u4FBF\u4E0D\u58F0\u4E0D\u54CD\u5730\u5E76\u541E\u4E86\u957F\u9E70\u4F1A\uFF0C\u7262\u7262\u63A7\u5236\u4F4F\u4E2D\u539F\u8D22\u6E90\uFF0C\u5E72\u5F97\u5E72\u51C0\u5229\u843D\uFF1B\u53E6\u4E00\u8FB9\u8D6B\u8FDE\u96C4\u5374\u662F\u5927\u5F20\u65D7\u9F13\uFF0C\u6253\u7740\u71D5\u738B\u7684\u65D7\u53F7\u5728\u96C1\u95E8\u8840\u6218\u6570\u573A\uFF0C\u8F6F\u786C\u517C\u65BD\uFF0C\u786C\u662F\u62A2\u4E0B\u96C1\u95E8\u9A6C\u5E02\u7684\u516D\u6210\u751F\u610F\u3002\u5F97\u6B64\u81C2\u52A9\uFF0C\u5BAB\u4E3B\u5982\u864E\u6DFB\u7FFC\uFF0C\u593A\u53D6\u5929\u4E0B\u4E0D\u8FC7\u662F\u65E9\u665A\u4E4B\u4E8B\u3002</p><p>\u601D\u7D22\u95F4\u53F6\u884C\u5357\u5DF2\u7ECF\u637B\u8D77\u795E\u5C3C\u7684\u4E73\u5934\uFF0C\u7528\u4E00\u652F\u6781\u7EC6\u7684\u954A\u5B50\u4ED4\u7EC6\u523A\u8FDB\u4E73\u773C\u4E2D\u3002\u954A\u5C16\u523A\u5165\u540E\uFF0C\u4ED6\u4FBF\u95ED\u4E0A\u773C\uFF0C\u5355\u51ED\u6307\u4E0A\u82E5\u6709\u82E5\u65E0\u7684\u89E6\u89C9\uFF0C\u5C06\u954A\u5B50\u523A\u5165\u534A\u5BF8\u6DF1\u6D45\uFF0C\u5230\u8FBE\u4E73\u5934\u4E2D\u90E8\u3002\u624B\u6307\u5FAE\u677E\uFF0C\u954A\u5B50\u7ACB\u5373\u5F39\u5F00\u5C11\u8BB8\uFF0C\u5C06\u9690\u7EA6\u53EF\u89C1\u7684\u4E73\u773C\u6491\u5F00\u4E00\u4E2A\u72ED\u957F\u7684\u5C0F\u5B54\u3002</p><p>\u4EE5\u524D\u63C9\u634F\u624D\u80FD\u4F53\u4F1A\u5230\u7684\u654F\u611F\u90E8\u4F4D\uFF0C\u6B64\u65F6\u88AB\u94A2\u954A\u76F4\u63A5\u63A2\u5165\uFF0C\u96BE\u8A00\u7684\u75DB\u75D2\u4F7F\u795E\u5C3C\u808C\u80A4\u7EF7\u7D27\u3002</p><p>\u53F6\u884C\u5357\u624B\u638C\u5728\u7BB1\u4E2D\u4E00\u63A0\u800C\u8FC7\uFF0C\u53D6\u954A\u3001\u5939\u94BB\u3001\u8638\u836F\u51E0\u4E2A\u52A8\u4F5C\u4E00\u6C14\u5475\u6210\uFF0C\u6CA1\u6709\u534A\u70B9\u505C\u987F\u3002\u5F85\u4F17\u4EBA\u770B\u6E05\uFF0C\u4ED6\u53F3\u624B\u5DF2\u591A\u4E86\u4E00\u4E2A\u540C\u6837\u7EC6\u5C0F\u7684\u954A\u5B50\uFF0C\u954A\u5C16\u5939\u7740\u4E00\u9897\u68F1\u89D2\u5206\u660E\u7684\u5C0F\u94BB\uFF0C\u95EA\u70C1\u7684\u94BB\u8F89\u4E0A\u8FD8\u8499\u7740\u4E00\u5C42\u6DE1\u7EFF\u8272\u7684\u6DB2\u4F53\u3002\u4ED6\u628A\u94BB\u77F3\u653E\u5728\u9C9C\u7EA2\u7684\u4E73\u5934\u4E0A\uFF0C\u6162\u6162\u63A8\u5165\u4E73\u773C\u4E2D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u4E73\u5934\u786C\u5982\u77F3\u5B50\uFF0C\u91CC\u9762\u5374\u67D4\u5AE9\u5F02\u5E38\uFF0C\u94BB\u77F3\u7684\u68F1\u89D2\u5212\u5728\u5AE9\u8089\u4E0A\uFF0C\u4E00\u79CD\u65E0\u6CD5\u8A00\u8868\u7684\u611F\u89C9\u6DF1\u5165\u9AA8\u9AD3\u3002\u5979\u7AED\u529B\u5FCD\u8010\uFF0C\u624D\u6CA1\u6709\u558A\u53EB\u51FA\u6765\u3002</p><p>\u6BB7\u7EA2\u7684\u4E73\u5934\u731B\u7136\u62C9\u957F\uFF0C\u53C8\u7ACB\u5373\u6062\u590D\u539F\u72B6\u3002\u53F6\u884C\u5357\u62D4\u51FA\u94A2\u954A\uFF0C\u90A3\u9897\u94BB\u77F3\u5DF2\u7ECF\u6C38\u8FDC\u7559\u5728\u4E73\u5934\u5185\u3002\u66F2\u6307\u4E00\u5F39\uFF0C\u4E73\u5934\u5185\u90E8\u4F20\u6765\u7684\u523A\u6FC0\u987F\u65F6\u7535\u6D41\u822C\u901A\u8FC7\u5168\u8EAB\uFF0C\u96EA\u5CF0\u795E\u5C3C\u7EA2\u80BF\u7684\u53F3\u4E73\u7D27\u7EF7\u7EF7\u6536\u6210\u4E00\u56E2\uFF0C\u4E0E\u80A5\u8F6F\u67D4\u5AE9\u7684\u5DE6\u4E73\u76F8\u6620\u6210\u8DA3\u3002</p><p>\u53F6\u884C\u5357\u4E00\u4E1D\u4E0D\u82DF\u5730\u5728\u5C06\u53E6\u4E00\u53EA\u4E73\u5934\u4E2D\u540C\u6837\u9576\u5165\u94BB\u77F3\uFF0C\u7136\u540E\u8D70\u5230\u795E\u5C3C\u655E\u9732\u7684\u80A1\u95F4\u3002</p><p>\u80BF\u80C0\u7684\u82B1\u74E3\u51E0\u4E4E\u906E\u655D\u4E86\u82B1\u8482\uFF0C\u5265\u5F00\u540E\u624B\u6307\u4E00\u677E\uFF0C\u82B1\u74E3\u7ACB\u5373\u56F4\u62E5\u4E0A\u6765\u3002\u53F6\u884C\u5357\u4E0D\u52A8\u58F0\u8272\uFF0C\u4E5F\u672A\u53EB\u4EBA\u5E2E\u624B\uFF0C\u5C06\u82B1\u74E3\u5B8C\u5168\u7FFB\u5F00\uFF0C\u76F4\u63A5\u62FF\u51FA\u9488\u7078\u7528\u7684\u94F6\u9488\u5C06\u5AE9\u8089\u9489\u5728\u817F\u6839\u3002</p><p>\u795E\u5C3C\u4EF0\u8D77\u7684\u4E0B\u8179\u95F4\u7FFB\u5F00\u4E00\u7247\u5DE8\u5927\u7684\u6D51\u5706\u7EA2\u8089\u3002\u76DB\u5F00\u7684\u82B1\u74E3\u4E2D\uFF0C\u4E00\u622A\u65E0\u9AA8\u7684\u8089\u82BD\u6DA6\u5982\u7EA2\u7389\u3002\u6E7F\u70ED\u7684\u79D8\u5904\u5B8C\u5168\u7FFB\u5F00\uFF0C\u82B1\u8482\u76F4\u63A5\u66B4\u9732\u5728\u7A7A\u6C14\u4E2D\uFF0C\u4E0B\u4F53\u987F\u65F6\u4E00\u9635\u6E05\u51C9\u3002</p><p>\u8089\u7A74\u6DEB\u6C34\u6E10\u6ECB\uFF0C\u523A\u7A7F\u7684\u82B1\u74E3\u8840\u8FF9\u5FAE\u73B0\uFF0C\u5C06\u6DEB\u6C34\u67D3\u6210\u6DE1\u7EA2\u8272\u3002</p><p>\u7247\u523B\u540E\u82B1\u8482\u4E0A\u4E00\u51C9\uFF0C\u4E24\u4E2A\u5C16\u9510\u7684\u7269\u4F53\u52FE\u5728\u4E0A\u9762\uFF0C\u63A5\u7740\u662F\u4E00\u9635\u5200\u5272\u822C\u7684\u5267\u75DB\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u75DB\u53EB\u5931\u58F0\uFF0C\u53EA\u89C9\u4E0B\u4F53\u706B\u8FA3\u8FA3\u4E00\u7247\uFF0C\u4F3C\u4E4E\u82B1\u8482\u88AB\u4EBA\u5272\u53BB\u3002\u4F46\u968F\u4E4B\u800C\u6765\u7684\u523A\u75DB\u5219\u544A\u8BC9\u5979\u82B1\u8482\u4F9D\u7136\u5B58\u5728\u3002</p><p>\u53F6\u884C\u5357\u62FF\u7740\u4E24\u652F\u94A2\u954A\u52FE\u4F4F\u82B1\u8482\u4E0A\u7684\u5305\u76AE\u4E00\u5206\uFF0C\u950B\u5229\u7684\u94A2\u94C1\u7ACB\u523B\u5207\u5F00\u8584\u8584\u7684\u5AE9\u8089\uFF0C\u5C06\u5305\u76AE\u4E00\u6495\u5230\u5E95\uFF0C\u7136\u540E\u624B\u8155\u4E00\u8F6C\uFF0C\u5305\u76AE\u88AB\u6574\u4E2A\u626F\u6389\uFF0C\u53EA\u5269\u4E0B\u5149\u79C3\u79C3\u7684\u9C9C\u7EA2\u8089\u82BD\u3002</p><p>\u4ED6\u5E76\u672A\u5C31\u6B64\u7F62\u4F11\uFF0C\u800C\u662F\u634F\u4F4F\u6EF4\u8840\u7684\u82B1\u8482\uFF0C\u50CF\u63D2\u5165\u4E73\u773C\u822C\u7528\u94A2\u954A\u786C\u751F\u751F\u523A\u5165\u5AE9\u8089\u5185\uFF0C\u63A5\u7740\u7EB3\u5165\u4E24\u9897\u5C0F\u94BB\u3002</p><p>\u63A5\u8FDE\u7684\u5267\u75DB\u4EE4\u96EA\u5CF0\u795E\u5C3C\u51C4\u53EB\u4E0D\u7EDD\uFF0C\u5C0F\u8179\u4E0D\u4F4F\u62BD\u6410\uFF0C\u8089\u7A74\u50CF\u62BF\u7D27\u7684\u5C0F\u5634\uFF0C\u65F6\u5F00\u65F6\u5408\u3002\u5F53\u7B2C\u4E8C\u9897\u94BB\u77F3\u9576\u5165\u65F6\uFF0C\u795E\u5C3C\u7389\u6237\u95F4\u4E00\u9635\u5267\u98A4\uFF0C\u5C3F\u6DB2\u4ECE\u8089\u7A74\u4E0A\u65B9\u7684\u5C0F\u5B54\u4E00\u6D8C\u800C\u51FA\u3002</p><p>\u5C3F\u6DB2\u521A\u55B7\u51FA\u4E00\u70B9\uFF0C\u53F6\u884C\u5357\u624B\u4E00\u6296\uFF0C\u4E00\u6839\u7259\u7B7E\u72B6\u7684\u5C0F\u6728\u68CD\u659C\u659C\u523A\u5165\u5C3F\u9053\uFF0C\u622A\u65AD\u4E86\u6C34\u6D41\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u8EAB\u4F53\u539F\u672C\u5C31\u654F\u611F\u5F02\u5E38\uFF0C\u6B64\u756A\u53C8\u5728\u6700\u654F\u611F\u7684\u90E8\u4F4D\u9576\u5165\u94BB\u77F3\uFF0C\u5265\u53BB\u5305\u76AE\uFF0C\u5267\u70C8\u7684\u523A\u6FC0\u4F7F\u5979\u51E0\u6B32\u6655\u53A5\u3002\u88AB\u5F3A\u884C\u5835\u4F4F\u7684\u5C3F\u6DB2\uFF0C\u4ECE\u6728\u68CD\u6839\u90E8\u5636\u5636\u5FAE\u54CD\u7740\u4E00\u70B9\u70B9\u6D8C\u51FA\u3002</p><p>\u9576\u5B8C\u94BB\u540E\uFF0C\u53F6\u884C\u5357\u518D\u4E0D\u770B\u795E\u5C3C\u4E00\u773C\uFF0C\u4FBF\u98D8\u7136\u800C\u53BB\u3002\u8D70\u5230\u95E8\u53E3\u65F6\u624D\u6DE1\u6DE1\u9053\uFF1A\u300C\u4ECE\u660E\u65E5\u8D77\uFF0C\u6BCF\u4E00\u4E2A\u65F6\u8FB0\u8001\u592B\u8981\u7528\u4E00\u523B\u949F\u3002\u300D</p><p>\u770B\u5F97\u76EE\u7729\u795E\u9A70\u7684\u4F17\u4EBA\u7ACB\u523B\u6028\u58F0\u56DB\u8D77\uFF0C\u6BCF\u4E2A\u65F6\u8FB0\u53F6\u62A4\u6CD5\u90FD\u8981\u6765\u4E00\u6B21\uFF0C\u4E00\u6B21\u5360\u7528\u4E00\u523B\u949F\u2014\u2014\u600E\u4E48\u80FD\u64CD\u5F97\u75DB\u5FEB\u3002</p><p>\u6C90\u58F0\u4F20\u779F\u4E86\u795E\u5C3C\u4E00\u773C\uFF0C\u632F\u8863\u800C\u8D77\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u957F\u591C\u7EC8\u4E8E\u8FC7\u53BB\uFF0C\u6155\u5BB9\u7D2B\u73AB\u6D51\u8EAB\u9165\u8F6F\uFF0C\u504E\u4F9D\u5728\u6155\u5BB9\u9F99\u6000\u4E2D\u660F\u660F\u6B32\u7761\u3002\u8FD9\u4E00\u591C\u5979\u4E0D\u77E5\u7ECF\u5386\u4E86\u591A\u5C11\u6B21\u9AD8\u6F6E\uFF0C\u4E5F\u4E0D\u77E5\u9053\u6155\u5BB9\u9F99\u5728\u5979\u4F53\u5185\u55B7\u53D1\u8FC7\u591A\u5C11\u6B21\uFF0C\u53EA\u662F\u5F53\u5979\u518D\u65E0\u529B\u8FCE\u5408\u65F6\uFF0C\u5A07\u7F8E\u7684\u8089\u7A74\u5DF2\u7ECF\u7EA2\u80BF\uFF0C\u800C\u8EAB\u4E0B\u7684\u88AB\u8925\u51E0\u4E4E\u5B8C\u5168\u6E7F\u900F\u3002\u5373\u4F7F\u4F11\u606F\u534A\u4E2A\u65F6\u8FB0\u4E4B\u540E\u7684\u73B0\u5728\uFF0C\u5979\u8FD8\u611F\u89C9\u5230\u82B1\u5F84\u5185\u7684\u5F8B\u52A8\uFF0C\u4F3C\u4E4E\u8089\u68D2\u8FD8\u5728\u91CC\u9762\u8086\u8650\u3002</p><p>\u6155\u5BB9\u9F99\u5DEE\u70B9\u513F\u88AB\u59B9\u59B9\u8FD1\u4E4E\u75AF\u72C2\u7684\u9700\u6C42\u5413\u4F4F\u4E86\uFF0C\u82E5\u975E\u4ED6\u9633\u5177\u6539\u9020\u5F97\u51F6\u731B\u5F02\u5E38\uFF0C\u6362\u4F5C\u5E73\u5E38\u4EBA\uFF0C\u4E09\u5341\u4E2A\u4E5F\u4E0D\u4E00\u5B9A\u80FD\u6EE1\u8DB3\u8FD9\u4E2A\u5C0F\u4E2B\u5934\u3002\u5373\u4F7F\u5982\u6B64\uFF0C\u540E\u6765\u4ED6\u4E5F\u4E0D\u5F97\u4E0D\u8FD0\u529F\u76F8\u52A9\uFF0C\u4E00\u9762\u8010\u5FC3\u7684\u4F7F\u59B9\u59B9\u9AD8\u6F6E\uFF0C\u4E00\u9762\u6536\u655B\u81EA\u5DF1\u7684\u5DE8\u7269\uFF0C\u514D\u5F97\u59B9\u59B9\u8131\u9634\u4F24\u4E86\u8EAB\u4F53\u3002</p><p>\u300C\u56F0\u4E86\u5427\uFF0C\u7761\u4E00\u4F1A\u513F\u3002\u300D\u6155\u5BB9\u9F99\u6402\u7740\u67D4\u82E5\u65E0\u9AA8\u7684\u9999\u8EAF\uFF0C\u67D4\u58F0\u9053\u3002</p><p>\u300C\u2026\u2026\u4EC0\u4E48\u65F6\u5019\u79BB\u5F00\u6D1B\u9633\u2026\u2026\u300D\u7D2B\u73AB\u7684\u58F0\u97F3\u8F7B\u5F97\u542C\u4E0D\u6E05\u695A\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u5F88\u5FEB\u3002\u300D\u53EA\u8981\u5A18\u5F00\u53E3\u8BF4\u8BDD\uFF0C\u54B1\u4EEC\u4E00\u5BB6\u5C31\u53EF\u4EE5\u5F00\u5F00\u5FC3\u5FC3\u4E0A\u8DEF\u4E86\u3002</p><p>\u5979\u80FD\u6491\u51E0\u5929\u5462\uFF1F</p><p>\u8BDD\u97F3\u672A\u843D\uFF0C\u7D2B\u73AB\u5DF2\u7ECF\u53D1\u51FA\u751C\u751C\u7684\u9163\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u7B49\u4E86\u7247\u523B\uFF0C\u5F85\u59B9\u59B9\u7761\u719F\uFF0C\u628A\u5979\u8F7B\u8F7B\u653E\u5728\u69BB\u4E0A\uFF0C\u7136\u540E\u4FEF\u8EAB\u62E8\u5F00\u96EA\u81C0\u3002\u81C0\u7F1D\u4E2D\u679C\u7136\u4E00\u7247\u6BB7\u7EA2\uFF0C\u83CA\u809B\u7684\u4F24\u53E3\u65E9\u5DF2\u7834\u88C2\u3002</p><p>\u6155\u5BB9\u9F99\u7528\u6E29\u6C34\u8F7B\u8F7B\u6D17\u53BB\u7D2B\u73AB\u81C0\u95F4\u7684\u8840\u8FF9\uFF0C\u6577\u4E0A\u4F24\u836F\u3002\u7136\u540E\u4E00\u4E00\u64E6\u53BB\u5979\u8EAB\u4E0A\u7684\u6C57\u6C34\u3001\u9633\u7CBE\u3001\u6DEB\u6DB2\u3002\u6676\u83B9\u7684\u5A07\u8EAF\u5B9B\u5982\u4E00\u4EF6\u7CBE\u81F4\u7684\u7389\u96D5\uFF0C\u4ED6\u8D8A\u64E6\u8D8A\u662F\u6E29\u67D4\uFF0C\u6700\u540E\u53D8\u6210\u6E29\u5B58\u5730\u7231\u629A\u3002\u4E16\u4E0A\u771F\u6709\u4E00\u4E2A\u5973\u4EBA\uFF0C\u53EF\u4EE5\u8BA9\u81EA\u5DF1\u5982\u6B64\u8FF7\u604B\u2026\u2026\u7EB5\u7136\u77E5\u9053\u5979\u4F1A\u6BEB\u4E0D\u72B9\u8C6B\u7684\u6740\u6B7B\u81EA\u5DF1\uFF0C\u4E5F\u96BE\u4EE5\u81EA\u62D4\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u6668\u5149\u4E2D\u76D8\u819D\u800C\u5750\uFF0C\u70BC\u5316\u6C72\u53D6\u7684\u771F\u5143\u3002\u6628\u665A\u771F\u662F\u628A\u5979\u643E\u5E72\u4E86\uFF0C\u6700\u540E\u51E0\u6B21\u9AD8\u6F6E\u65F6\uFF0C\u5C0F\u4E2B\u5934\u518D\u6CA1\u6709\u55B7\u51FA\u4E00\u70B9\u9634\u7CBE\uFF0C\u53EA\u5269\u4E0B\u82B1\u5FC3\u7684\u98A4\u6296\u3002</p><p>\u534A\u664C\u540E\uFF0C\u4ED6\u7741\u5F00\u773C\uFF0C\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\u3002\u517B\u4E00\u53EA\u53EF\u7231\u7684\u8001\u864E\u5F53\u5BA0\u7269\uFF0C\u53EF\u8981\u4E07\u5206\u5C0F\u5FC3\u3002</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
