import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_80" tabindex="-1"><a class="header-anchor" href="#_80" aria-hidden="true">#</a> 80</h1><p>\u7B2C\u4E8C\u5929\u8427\u4F5B\u5974\u624D\u53D1\u73B0\u5F02\u5E38\u3002\u767D\u6C0F\u59D0\u59B9\u4F9D\u7136\u6BB7\u52E4\u5730\u5582\u5979\u5403\u996D\uFF0C\u5E2E\u5979\u64E6\u6D17\u3001\u6309\u6469\u2014\u2014\u4F46\u5979\u4EEC\u50CF\u662F\u5FD8\u4E86\u81EA\u5DF1\u7684\u5C3F\u5E03\u3002</p><p>\u6E7F\u70ED\u7684\u79FD\u7269\u88AB\u68C9\u5E03\u88F9\u5728\u81C0\u95F4\uFF0C\u4E0B\u4F53\u523A\u75D2\u96BE\u5F53\u3002\u80AE\u810F\u7684\u5C4E\u5C3F\u6CBE\u5728\u5A07\u5AE9\u7684\u808C\u80A4\u4E0A\u65E0\u6CD5\u6E05\u7406\uFF0C\u60F3\u60F3\u5C31\u4E07\u5206\u6076\u5FC3\u3002\u8427\u4F5B\u5974\u67F3\u7709\u98A6\u7D27\uFF0C\u4E0D\u65F6\u52C9\u529B\u632A\u52A8\u8170\u80A2\uFF0C\u60F3\u79BB\u6C61\u7269\u8FDC\u4E00\u4E9B\u3002</p><p>\u6155\u5BB9\u9F99\u63A8\u95E8\u800C\u5165\uFF0C\u5148\u6367\u8D77\u6BCD\u4EB2\u7684\u4FCF\u8138\u75DB\u543B\u4E00\u756A\uFF0C\u8D5E\u9053\uFF1A\u300C\u771F\u9999\u3002\u300D\u7136\u540E\u659C\u8EBA\u69BB\u4E0A\uFF0C\u628A\u7F8E\u5987\u7684\u81FB\u9996\u653E\u5728\u817F\u4E0A\uFF0C\u6469\u6332\u7740\u8BF4\u9053\uFF1A\u300C\u5A18\uFF0C\u59B9\u59B9\u4E5F\u6709\u4E86\u8EAB\u5B55\u5462\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7D27\u95ED\u7684\u7F8E\u76EE\u731B\u7136\u5F20\u5F00\uFF0C\u7247\u523B\u540E\u53C8\u51C4\u7136\u5408\u7D27\uFF0C\u300C\u5929\u2026\u2026\u8FD9\u4E2A\u755C\u7272\u2026\u2026\u83E9\u8428\u4FDD\u4F51\uFF0C\u5F1F\u5B50\u7ACB\u8A93\u7EC8\u751F\u4E0D\u53D1\u4E00\u8A00\uFF0C\u4E16\u95F4\u82E6\u96BE\u5F1F\u5B50\u613F\u4E00\u8EAB\u627F\u62C5\uFF0C\u53EA\u6C42\u4F5B\u7956\u6148\u60B2\uFF0C\u8BA9\u5C0F\u5973\u9003\u8FC7\u6B64\u52AB\u2026\u2026\u300D</p><p>\u300C\u5929\u6C14\u8FD9\u4E48\u70ED\uFF0C\u5C31\u522B\u76D6\u6BEF\u5B50\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u4E00\u628A\u5C06\u8F7B\u6BEF\u6254\u5728\u5730\u4E0A\u3002\u4E5F\u4E0D\u50CF\u4EE5\u5F80\u90A3\u6837\u591A\u966A\u6BCD\u4EB2\u4E00\u4F1A\uFF0C\u4FBF\u626C\u957F\u800C\u53BB\u3002</p><p>\u623F\u95F4\u91CC\u53EA\u5269\u4E0B\u8D64\u88F8\u7684\u7F8E\u5987\uFF0C\u72EC\u81EA\u8EBA\u5728\u69BB\u4E0A\u3002\u5982\u96EA\u7684\u5A07\u8EAF\u660E\u8273\u751F\u9999\uFF0C\u53EA\u662F\u80A1\u95F4\u539A\u539A\u7684\u68C9\u5E03\uFF0C\u53EF\u7B11\u800C\u53C8\u53EF\u60B2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5C11\u592B\u4EBA\uFF0C\u8FD9\u662F\u65B0\u6C72\u7684\u4E95\u6C34\u3002\u300D</p><p>\u7D2B\u73AB\u70B9\u70B9\u5934\uFF0C\u5F85\u767D\u7389\u9E42\u9000\u4E0B\uFF0C\u5979\u5C06\u4E95\u6C34\u5012\u5728\u6728\u76C6\u5185\uFF0C\u7136\u540E\u89E3\u8863\u5750\u5165\u3002\u4E95\u6C34\u51B0\u51B7\u5F7B\u9AA8\uFF0C\u7D2B\u73AB\u51BB\u5F97\u5634\u5507\u53D1\u767D\uFF0C\u4ECD\u575A\u6301\u628A\u5C0F\u8179\u6D78\u5728\u6C34\u4E2D\uFF0C\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u300C\u6D17\u6FA1\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u5947\u602A\u5730\u95EE\u9053\u3002</p><p>\u300C\u5929\u6C14\u597D\u70ED\uFF0C\u8EAB\u4E0A\u90FD\u662F\u6C57\u2026\u2026\u300D\u7D2B\u73AB\u5A07\u61A8\u5730\u8BF4\u7740\uFF0C\u64A9\u8D77\u6C34\u6D12\u5728\u9888\u4E0A\u3002</p><p>\u767D\u7699\u7684\u808C\u80A4\u6CBE\u7740\u6676\u83B9\u7684\u6C34\u73E0\uFF0C\u6108\u53D1\u5A07\u7F8E\u3002\u6155\u5BB9\u9F99\u8E72\u8EAB\u5F20\u53E3\u4E00\u5439\uFF0C\u6C34\u73E0\u987F\u65F6\u5316\u6210\u4E00\u7247\u8499\u8499\u96FE\u6C14\uFF0C\u5728\u5982\u8102\u5982\u7389\u7684\u9165\u4E73\u524D\u5E7B\u51FA\u4E00\u9053\u5C0F\u5C0F\u7684\u5F69\u8679\u3002</p><p>\u300C\u771F\u6F02\u4EAE\u3002\u300D\u7D2B\u73AB\u559C\u5B5C\u5B5C\u5730\u8BF4\u3002</p><p>\u6155\u5BB9\u9F99\u5F97\u610F\u7684\u4E00\u7B11\uFF0C\u63AC\u8D77\u4E00\u6367\u6C34\u6D12\u5728\u59B9\u59B9\u80F8\u53E3\u3002\u6B64\u65F6\u4E95\u6C34\u5438\u6536\u4E86\u7D2B\u73AB\u4F53\u6E29\uFF0C\u5DF2\u7ECF\u6CA1\u6709\u5F53\u521D\u7684\u5BD2\u51B7\uFF0C\u4F46\u4ED6\u8FD8\u662F\u76B1\u8D77\u4E86\u7709\u5934\uFF0C\u300C\u6000\u7740\u5B69\u5B50\uFF0C\u4E0D\u8981\u7528\u51C9\u6C34\u6D17\uFF0C\u5BF9\u8EAB\u4F53\u4E0D\u597D\u3002\u300D</p><p>\u7D2B\u73AB\u542C\u8BDD\u5730\u70B9\u70B9\u5934\uFF0C\u7136\u540E\u644A\u5F00\u7389\u4F53\uFF0C\u8EBA\u5728\u76C6\u4E2D\uFF0C\u6485\u7740\u5C0F\u5634\u8BF4\u9053\uFF1A\u300C\u6D1B\u9633\u8FD9\u4E48\u70ED\uFF0C\u4EC0\u4E48\u90FD\u4E0D\u60F3\u5403\u2026\u2026\u300D</p><p>\u300C\u662F\u4E0D\u662F\u60F3\u5403\u9178\u7684\uFF1F\u300D\u6155\u5BB9\u9F99\u7B11\u9053\u3002</p><p>\u300C\u662F\u554A\u662F\u554A\uFF0C\u6211\u60F3\u5403\u674E\u5B50\u3002\u300D</p><p>\u300C\u6CA1\u95EE\u9898\u3002\u300D</p><p>\u300C\u6211\u8981\u5403\u51C9\u7684\u3002\u300D</p><p>\u300C\u54E5\u54E5\u7528\u4E95\u6C34\u7ED9\u4F60\u6E43\u4E00\u4E0B\u3002\u300D</p><p>\u7D2B\u73AB\u7A81\u53D1\u5947\u60F3\uFF0C\u300C\u6709\u6CA1\u6709\u51B0\u5757\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u8FDF\u7591\u4E86\u4E00\u4E0B\uFF0C\u300C\u4EE5\u524D\u5BAB\u91CC\u6709\u51B0\u5E93\u2026\u2026\u6211\u547D\u4EBA\u53BB\u627E\u627E\u770B\u3002\u300D</p><p>\u7D2B\u73AB\u7B11\u76C8\u76C8\u62B1\u4F4F\u6155\u5BB9\u9F99\u7684\u8116\u9888\uFF0C\u300C\u54E5\u54E5\u5BF9\u6211\u771F\u597D\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8863\u9886\u5C3D\u6E7F\uFF0C\u5374\u662F\u6EE1\u5FC3\u559C\u60A6\uFF0C\u6D51\u672A\u6CE8\u610F\u59B9\u59B9\u95EA\u52A8\u7684\u76EE\u5149\uFF0C\u300C\u5FEB\u4E9B\u6D17\uFF0C\u4E00\u4F1A\u513F\u8DDF\u54E5\u54E5\u51FA\u95E8\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u4E45\u5C45\u6DF1\u5C71\uFF0C\u5BF9\u6D1B\u9633\u7684\u7E41\u534E\u5927\u611F\u5174\u8DA3\uFF0C\u6BCF\u65E5\u90FD\u8981\u5E26\u7740\u6BCD\u4EB2\u548C\u59B9\u59B9\u6E38\u89C8\u8857\u5E02\uFF0C\u4F46\u4ECA\u5929\u5374\u4E0D\u89C1\u6BCD\u4EB2\u7684\u8E2A\u5F71\u3002</p><p>\u300C\u5A18\u5462\uFF1F\u300D</p><p>\u300C\u4E0D\u7528\u7BA1\u5979\uFF0C\u4ECA\u5929\u8C01\u4E5F\u4E0D\u5E26\uFF0C\u5C31\u6211\u4EEC\u592B\u59BB\u4FE9\u3002\u300D</p><p>\u7D2B\u73AB\u867D\u7136\u7591\u60D1\uFF0C\u4E5F\u53EA\u597D\u542C\u4ECE\u3002</p><p>\u957F\u9E70\u4F1A\u6240\u5728\u7684\u5174\u827A\u574A\u4F4D\u4E8E\u6D1B\u9633\u4E1C\u5317\u89D2\uFF0C\u7B56\u9A91\u4E0D\u591A\u65F6\u4FBF\u51FA\u4E86\u57CE\u95E8\u3002\u76F8\u6BD4\u4E8E\u5173\u4E2D\u4E00\u5E26\u7684\u6DF7\u4E71\uFF0C\u4E2D\u539F\u4E4B\u5730\u8FD8\u79F0\u5F97\u4E0A\u5E73\u9759\uFF0C\u57CE\u5916\u5927\u7247\u519C\u7530\u90FD\u6709\u4EBA\u8015\u4F5C\uFF0C\u4E0E\u6F7C\u5173\u4EE5\u897F\u7684\u6742\u8349\u4E1B\u751F\u5927\u76F8\u8FF3\u5EAD\u3002</p><p>\u7D2B\u73AB\u5934\u5E26\u6597\u7B20\uFF0C\u9762\u906E\u8F7B\u7EB1\uFF0C\u770B\u4F3C\u95F2\u6687\uFF0C\u5176\u5B9E\u65E0\u65F6\u65E0\u523B\u4E0D\u5728\u6CE8\u610F\u5468\u56F4\u7684\u8DEF\u5F84\u3002</p><p>\u6B64\u53BB\u9F99\u57CE\uFF0C\u540D\u662F\u796D\u7956\uFF0C\u5B9E\u4E3A\u90A3\u4E2A\u5B50\u865A\u4E4C\u6709\u7684\u5B9D\u85CF\uFF0C\u4E00\u65E6\u8C0E\u8BDD\u88AB\u63ED\u7A7F\u2026\u2026</p><p>\u300C\u2026\u2026\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u55EF\uFF1F\u300D\u7D2B\u73AB\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u626C\u8D77\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u770B\u5F97\u8FD9\u4E48\u51FA\u795E\u3002\u524D\u9762\u6709\u7247\u6811\u6797\uFF0C\u53BB\u4F11\u606F\u4E00\u4E0B\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u6811\u6797\u4E0D\u77E5\u662F\u54EA\u4E2A\u5BB6\u65CF\u7684\u9675\u56ED\uFF0C\u53E4\u67CF\u68EE\u68EE\uFF0C\u8349\u6728\u8471\u830F\u3002\u4E24\u4EBA\u8D70\u5230\u6797\u4E0B\uFF0C\u987F\u65F6\u6691\u610F\u5168\u6D88\u3002\u5176\u65F6\u5DF2\u8FD1\u516D\u6708\uFF0C\u6B63\u503C\u4F0F\u5929\uFF0C\u7530\u91CC\u8015\u4F5C\u7684\u519C\u592B\u53EA\u7A7F\u7740\u725B\u9F3B\u77ED\u728A\uFF0C\u80CC\u810A\u88AB\u70C8\u65E5\u6652\u5F97\u53C8\u9ED1\u53C8\u7EA2\u3002\u7D2B\u73AB\u770B\u7740\u4ED6\u4EEC\u7684\u8F9B\u82E6\uFF0C\u4E0D\u7531\u8F7B\u53F9\u4E00\u58F0\u3002</p><p>\u300C\u4F55\u5FC5\u4E3A\u8FD9\u4E9B\u8D31\u6C11\u53F9\u6C14\u3002\u300D\u6155\u5BB9\u9F99\u4E0D\u5C51\u5730\u8BF4\u3002</p><p>\u7D2B\u73AB\u4E0D\u670D\u6C14\u5730\u8BF4\uFF1A\u300C\u4F17\u751F\u5E73\u7B49\uFF0C\u4EBA\u90FD\u662F\u4E00\u6837\u7684\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u6155\u5BB9\u9F99\u54C2\u7B11\u9053\uFF1A\u300C\u4ED6\u4EEC\u600E\u4E48\u80FD\u8DDF\u6211\u4EEC\u6155\u5BB9\u6C0F\u76F8\u6BD4\uFF1F\u6211\u6155\u5BB9\u6C0F\u53D7\u4E0A\u82CD\u7737\u987E\uFF0C\u8840\u7EDF\u9AD8\u8D35\uFF0C\u5C82\u4E0E\u8FD9\u4E9B\u4F4E\u8D31\u4E4B\u5F92\u7B49\u540C\uFF01\u300D</p><p>\u300C\u6BCF\u4E2A\u4EBA\u7684\u8840\u90FD\u662F\u7EA2\u7684\uFF0C\u6709\u4EC0\u4E48\u4E0D\u4E00\u6837\u3002\u300D</p><p>\u300C\u6709\u4E9B\u4EBA\u5929\u751F\u806A\u6167\uFF0C\u6709\u4E9B\u4EBA\u5929\u751F\u611A\u8822\uFF1B\u6709\u4E9B\u4EBA\u5929\u751F\u82F1\u4FCA\uFF0C\u6709\u4E9B\u4EBA\u5929\u751F\u4E11\u964B\uFF1B\u540C\u6837\uFF0C\u6709\u4E9B\u4EBA\u5929\u751F\u9AD8\u8D35\uFF0C\u6709\u4E9B\u4EBA\u5929\u751F\u4F4E\u8D31\u3002\u6211\u6155\u5BB9\u6C0F\u5929\u751F\u5C31\u662F\u53F1\u5412\u98CE\u4E91\u7684\u8D35\u65CF\uFF0C\u300D</p><p>\u6155\u5BB9\u9F99\u6307\u7740\u8FDC\u5904\u7275\u7740\u8015\u725B\u7684\u519C\u592B\u50B2\u7136\u9053\uFF1A\u300C\u4ED6\u4EEC\u53EA\u4E0D\u8FC7\u662F\u877C\u8681\u4E00\u822C\u7684\u8D31\u6C11\uFF01\u300D</p><p>\u300C\u5C31\u662F\u877C\u8681\u4E5F\u548C\u6211\u4EEC\u4E00\u6837\uFF01\u300D\u98D8\u6885\u5CF0\u672C\u5C5E\u91CA\u6D41\uFF0C\u51B5\u4E14\u8FD8\u6709\u4FE1\u4F5B\u7684\u6BCD\u4EB2\uFF0C\u7D2B\u73AB\u8033\u718F\u76EE\u67D3\uFF0C\u5BF9\u4F17\u751F\u5E73\u7B49\u6DF1\u4FE1\u4E0D\u7591\u3002</p><p>\u300C\u5584\u54C9\u5584\u54C9\uFF0C\u5973\u65BD\u4E3B\u6240\u8A00\u6781\u662F\u3002\u300D\u4E00\u4E2A\u6D51\u539A\u7684\u58F0\u97F3\u4ECE\u80CC\u540E\u54CD\u8D77\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u4E00\u95EA\uFF0C\u6162\u6162\u8F6C\u8FC7\u8EAB\u5B50\uFF0C\u300C\u9760\uFF01\u4F60\u4EEC\u8FD9\u4E9B\u79C3\u9A74\u5C31\u4E0D\u4F1A\u8BF4\u4E9B\u522B\u7684\u3002\u300D</p><p>\u677E\u6811\u4E0B\u7AD9\u7740\u4E00\u4E2A\u7070\u888D\u50E7\u4EBA\uFF0C\u4ED6\u5E74\u7EA6\u56DB\u5341\uFF0C\u624B\u6301\u7985\u6756\uFF0C\u988C\u4E0B\u9ED1\u987B\u98D8\u626C\u3002\u542C\u5230\u8FD9\u4E2A\u80E1\u670D\u9752\u5E74\u51FA\u8A00\u5982\u6B64\u4E0D\u900A\uFF0C\u4ED6\u7709\u6BDB\u4E00\u6311\uFF0C\u8BF4\u9053\uFF1A\u300C\u8D2B\u50E7\u5706\u901A\uFF0C\u8BF7\u6559\u65BD\u4E3B\u5C0A\u59D3\u5927\u540D\uFF1F\u300D</p><p>\u300C\u54C8\u3002\u4F60\u8DDF\u7740\u6211\u8DD1\u5230\u57CE\u5916\uFF0C\u96BE\u9053\u8FD8\u4E0D\u8BA4\u8BC6\u6211\uFF1F\u300D</p><p>\u5706\u901A\u89C1\u4ED6\u4E0D\u613F\u5410\u9732\u59D3\u540D\uFF0C\u4E00\u6296\u7985\u6756\uFF0C\u53EB\u9053\uFF1A\u300C\u5996\u9B54\u90AA\u9053\uFF0C\u9664\u4E4B\u4E43\u662F\u65E0\u4E0A\u529F\u5FB7\u3002\u300D</p><p>\u300C\u5478\uFF01\u4E00\u4E2A\u51FA\u5BB6\u4EBA\u8FD8\u628A\u529F\u5FB7\u6302\u5728\u5634\u8FB9\uFF0C\u5FF5\u5FF5\u4E0D\u5FD8\uFF0C\u4F60\u4FEE\u7684\u4EC0\u4E48\u4F5B\uFF1F\u300D\u6155\u5BB9\u9F99\u63E1\u4F4F\u8896\u4E2D\u7684\u8361\u661F\u97AD\uFF0C\u6602\u7136\u9053\uFF1A\u300C\u65E2\u7136\u4F17\u751F\u5E73\u7B49\uFF0C\u4E3A\u4F55\u53C8\u5F3A\u6307\u672C\u5BAB\u662F\u5996\u9B54\uFF1F\u8981\u6597\u5C31\u6597\uFF0C\u653E\u8FD9\u4E9B\u865A\u5C41\u5B9E\u5728\u591A\u4F59\uFF01\u300D</p><p>\u8FD9\u8D3C\u79C3\u80FD\u6F5C\u5230\u8EAB\u5916\u5341\u4E08\u624D\u73B0\u8EAB\uFF0C\u6B66\u529F\u4E0D\u5728\u6559\u4E2D\u8BF8\u957F\u8001\u4E4B\u4E0B\u3002\u6155\u5BB9\u9F99\u867D\u7136\u4E0D\u60E7\uFF0C\u4F46\u5706\u901A\u7EDD\u975E\u4E00\u4EBA\uFF0C\u5982\u4F55\u4E0D\u7559\u4E00\u4E2A\u6D3B\u53E3\uFF0C\u4FDD\u4F4F\u8EAB\u4EFD\u673A\u5BC6\uFF0C\u5374\u662F\u4E0D\u6613\u3002</p><p>\u5706\u901A\u5343\u91CC\u8FE2\u8FE2\u8D76\u5230\u6D1B\u9633\uFF0C\u624D\u542C\u8BF4\u95E8\u4E0B\u5F1F\u5B50\u5B59\u540C\u8F89\u88AB\u6307\u4E3A\u52FE\u7ED3\u608D\u532A\uFF0C\u4E0D\u5229\u4E8E\u6D1B\u9633\u6B66\u6797\uFF0C\u5E7F\u9633\u5E2E\u5DF2\u88AB\u6D1B\u9633\u6B66\u6797\u76DF\u9996\u957F\u9E70\u4F1A\u6B7C\u706D\uFF0C\u5B59\u540C\u8F89\u5F53\u573A\u4F0F\u8BDB\u3002\u5706\u901A\u4E0E\u5B59\u540C\u8F89\u76F8\u8BC6\u591A\u5E74\uFF0C\u7EDD\u4E0D\u76F8\u4FE1\u4ED6\u4F1A\u505A\u51FA\u8FD9\u7B49\u4E8B\uFF0C\u4E8E\u662F\u9690\u8EAB\u6D1B\u9633\uFF0C\u56DB\u5904\u6253\u63A2\u6D88\u606F\u3002</p><p>\u4E00\u4E2A\u6708\u6765\u6D88\u606F\u867D\u7136\u6CA1\u6709\u6253\u542C\u51FA\u6765\uFF0C\u4F46\u6BCF\u65E5\u51FA\u5165\u957F\u9E70\u4F1A\u7684\u6155\u5BB9\u9F99\u5374\u5F15\u8D77\u4E86\u4ED6\u7684\u6CE8\u610F\u3002\u5706\u901A\u6B64\u756A\u8DDF\u6765\u672C\u60F3\u597D\u8A00\u76F8\u8BAF\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E2A\u80E1\u670D\u9752\u5E74\u5C45\u7136\u5982\u6B64\u56A3\u5F20\uFF0C\u4ED6\u52C3\u7136\u5927\u6012\uFF0C\u66B4\u559D\u4E00\u58F0\uFF0C\u7985\u6756\u821E\u8D77\u4E00\u7247\u5F27\u5149\u671D\u6155\u5BB9\u9F99\u8170\u95F4\u626B\u6765\u3002</p><p>\u4E00\u6761\u4EBA\u5F71\u7BAD\u77E2\u822C\u5C04\u6765\uFF0C\u300C\u94DB\u300D\u5730\u4E00\u58F0\u5DE8\u54CD\uFF0C\u90A3\u4EBA\u540E\u9000\u51E0\u6B65\uFF0C\u7ACB\u5728\u6155\u5BB9\u9F99\u8EAB\u524D\u3002</p><p>\u5706\u901A\u867D\u7136\u8EAB\u5B50\u672A\u52A8\uFF0C\u4F46\u4E5F\u6C14\u8840\u7FFB\u6D8C\uFF0C\u4E0D\u7981\u5FC3\u4E0B\u6697\u60CA\u3002\u4E00\u62DB\u4E4B\u4E0B\uFF0C\u4ED6\u5DF2\u77E5\u6765\u4EBA\u529F\u529B\u6DF1\u539A\uFF0C\u4E8E\u662F\u6536\u655B\u5FC3\u795E\uFF0C\u4ED4\u7EC6\u6253\u91CF\u8FD9\u4E2A\u4E0D\u901F\u4E4B\u5BA2\u3002</p><p>\u6765\u8005\u9E70\u9F3B\u9E20\u76EE\uFF0C\u5DE6\u624B\u63E1\u7740\u4E00\u67C4\u5F2F\u94A9\uFF0C\u53F3\u8896\u5374\u7A7A\u8361\u8361\u7CFB\u5728\u8170\u95F4\uFF0C\u6B63\u662F\u5DF4\u9675\u4E00\u67AD\u5B89\u5B50\u5B8F\u3002\u4ED6\u8170\u95F4\u8FD8\u7CFB\u7740\u4E00\u4E2A\u6EF4\u8840\u7684\u5305\u88F9\uFF0C\u5305\u88F9\u7684\u7070\u5E03\u8D28\u5730\u4E0E\u5706\u901A\u8EAB\u4E0A\u4E00\u822C\u65E0\u4E8C\uFF0C\u5206\u660E\u662F\u4ECE\u50E7\u888D\u4E0A\u6495\u4E0B\u6765\u3002</p><p>\u5B89\u5B50\u5B8F\u628A\u9F13\u9F13\u56CA\u56CA\u7684\u5305\u88F9\u6254\u5230\u5730\u4E0A\uFF0C\u9634\u607B\u607B\u9053\uFF1A\u300C\u5916\u9762\u4E24\u4E2A\u79C3\u9A74\u90FD\u5728\u8FD9\u91CC\uFF0C\u8FD8\u5DEE\u4ED6\u4E00\u4E2A\u8111\u888B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u5DF2\u660E\u767D\u8FD9\u4E2A\u6840\u6556\u4E0D\u8BAD\u7684\u5DF4\u9675\u67AD\u6709\u6295\u8BDA\u4E4B\u610F\uFF0C\u53EA\u662F\u5F53\u65E5\u5728\u6559\u4E2D\u4E00\u62DB\u4F24\u5728\u96EA\u5CF0\u795E\u5C3C\uFF0C\u6015\u81EA\u5DF1\u770B\u4E0D\u8D77\u4ED6\uFF0C\u56E0\u6B64\u4E00\u8DEF\u5C3E\u968F\u627E\u56DE\u9762\u5B50\u3002\u4ED6\u54C8\u54C8\u4E00\u7B11\uFF0C\u300C\u5B89\u4F9B\u5949\u6765\u5F97\u6B63\u597D\uFF01\u300D</p><p>\u5B89\u5B50\u5B8F\u4E4D\u95FB\u4F9B\u5949\u4E4B\u540D\uFF0C\u5FC3\u4E0B\u5927\u559C\u8FC7\u671B\uFF0C\u4E00\u4E3E\u5F2F\u94A9\u602A\u53EB\u9053\uFF1A\u300C\u79C3\u9A74\u53D7\u6B7B\uFF01\u300D</p><p>\u5DF4\u9675\u67AD\u9A84\u6A2A\u6210\u6027\uFF0C\u7EB5\u7136\u5FC3\u60A6\u8BDA\u670D\uFF0C\u6B64\u65F6\u4E5F\u4E0D\u77E5\u65BD\u793C\u9053\u8C22\u3002\u6155\u5BB9\u9F99\u5BF9\u6B64\u4E5F\u4E0D\u4EE5\u4E3A\u610F\uFF0C\u4ED6\u963B\u4F4F\u8DC3\u8DC3\u6B32\u52A8\u7684\u5B89\u5B50\u5B8F\uFF0C\u300C\u4F9B\u5949\u8FDC\u9053\u800C\u6765\uFF0C\u5148\u6B47\u606F\u7247\u523B\uFF0C\u770B\u672C\u5BAB\u6536\u62FE\u8FD9\u4E2A\u8D3C\u79C3\u3002\u300D</p><p>\u5706\u901A\u542C\u5230\u4E24\u4EBA\u4EE5\u300C\u5BAB\u4E3B\u300D\u3001\u300C\u4F9B\u5949\u300D\u76F8\u79F0\uFF0C\u8D8A\u53D1\u4E0D\u6562\u5927\u610F\uFF0C\u5FC3\u4E0B\u4E0D\u4F4F\u601D\u7D22\uFF1A\u54EA\u91CC\u6765\u4E2A\u59D3\u6155\u5BB9\u7684\u5BAB\u4E3B\uFF1F\u83AB\u975E\u662F\u664B\u5317\u4F0F\u9F99\u6DA7\u7684\u5B50\u5F1F\uFF1F\u4F46\u6155\u5BB9\u536B\u3001\u6155\u5BB9\u80DC\u7236\u5B50\u5DF2\u7ECF\u846C\u8EAB\u661F\u6708\u6E56\u5996\u5B7D\u624B\u4E2D\u2014\u2014\u4ED6\u624B\u4E2D\u4E00\u7D27\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u65BD\u4E3B\u53EF\u662F\u661F\u6708\u6E56\u95E8\u4E0B\uFF1F\u300D</p><p>\u300C\u4E0D\u662F\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u8D81\u5706\u901A\u6599\u9519\u5206\u795E\u4E4B\u673A\uFF0C\u53F3\u624B\u4E00\u626C\uFF0C\u8361\u661F\u97AD\u95EA\u7535\u822C\u6325\u51FA\uFF0C\u300C\u672C\u5BAB\u4ECE\u6765\u6CA1\u5F53\u8FC7\u65BD\u4E3B\uFF0C\u4E5F\u4E0D\u662F\u661F\u6708\u6E56\u95E8\u4E0B\uFF0C\u300D\u5E7D\u6697\u7684\u6811\u5F71\u4E2D\u7A81\u7136\u5149\u8292\u5927\u76DB\uFF0C\u97AD\u67C4\u7684\u4E03\u5F69\u5B9D\u77F3\u5947\u5149\u56DB\u5C04\uFF0C\u5939\u7740\u547C\u5578\u7684\u97AD\u5F71\uFF0C\u4E00\u80A1\u5996\u90AA\u7684\u9738\u6C14\u5145\u585E\u5BC6\u6797\uFF0C\u300C\u672C\u5BAB\u4E43\u662F\u661F\u6708\u6E56\u5BAB\u4E3B\u6155\u5BB9\u9F99\uFF01\u300D</p><p>\u5706\u901A\u76EE\u7729\u6C14\u593A\uFF0C\u4F46\u591A\u5E74\u4FEE\u70BC\u7684\u4F5B\u95E8\u6B63\u5B97\u4E5F\u81EA\u4E0D\u4FD7\uFF0C\u4ED6\u95ED\u76EE\u626C\u81C2\uFF0C\u7985\u6756\u671D\u573A\u4E2D\u6C14\u52B2\u6700\u76DB\u5904\u51FB\u53BB\u3002\u4E00\u8FDE\u4E32\u5BC6\u96C6\u7684\u6C14\u52B2\u4EA4\u96C6\u58F0\u54CD\u8D77\uFF0C\u7985\u6756\u88AB\u4E00\u6761\u67D4\u97E7\u7684\u8F6F\u97AD\u7262\u7262\u7F20\u4F4F\uFF0C\u63A5\u7740\u7985\u6756\u50CF\u662F\u6295\u5165\u4E07\u53E4\u5BD2\u6F6D\u4E2D\u4E00\u822C\uFF0C\u5BD2\u6C14\u8FEB\u4EBA\u3002</p><p>\u95ED\u4E0A\u773C\uFF0C\u4E03\u5F69\u7684\u661F\u5149\u4F9D\u7136\u6E05\u6670\u53EF\u8FA8\uFF0C\u5706\u901A\u9739\u96F3\u822C\u66B4\u559D\u4E00\u58F0\uFF0C\u96C4\u6D51\u7684\u771F\u6C14\u72C2\u6D8C\u800C\u51FA\u3002\u51B0\u51B7\u523A\u9AA8\u7684\u592A\u4E00\u771F\u6C14\u5982\u6C34\u94F6\u6CC4\u5730\uFF0C\u65E0\u5B54\u4E0D\u5165\uFF0C\u4F46\u4E0E\u5706\u901A\u8FD9\u51DD\u805A\u6BD5\u751F\u4FEE\u4E3A\u7684\u4E00\u51FB\u76F8\u6BD4\u8FD8\u662F\u5F31\u4E86\u5C11\u8BB8\uFF0C\u5F53\u4E0B\u8282\u8282\u8D25\u9000\u3002</p><p>\u5706\u901A\u867D\u7136\u76EE\u4E0D\u89C1\u7269\uFF0C\u4F46\u6839\u636E\u771F\u6C14\u7684\u53D8\u5316\u6E05\u695A\u5730\u611F\u89C9\u5230\u5BF9\u624B\u659C\u8EAB\u62A2\u4E0A\uFF0C\u5DE6\u624B\u524D\u523A\u3002\u4ED6\u4E00\u6446\u7985\u6756\u6321\u5728\u8EAB\u524D\uFF0C\u540C\u65F6\u6084\u65E0\u58F0\u606F\u5730\u8E22\u51FA\u4E00\u811A\u3002</p><p>\u300C\u53EE\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u5706\u901A\u624B\u4E0A\u4E00\u8F7B\uFF0C\u63A5\u7740\u5589\u5934\u5FAE\u51C9\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
