import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_81" tabindex="-1"><a class="header-anchor" href="#_81" aria-hidden="true">#</a> 81</h1><p>\u4E00\u6EF4\u8840\u73E0\u5728\u5982\u6C34\u7684\u5200\u5149\u4E0A\u8F7B\u8F7B\u5212\u4E86\u4E2A\u5706\u5F27\uFF0C\u60AC\u5728\u5200\u5C16\uFF0C\u7136\u540E\u6162\u6162\u6EF4\u5728\u7FE0\u7EFF\u7684\u8349\u4E1B\u4E2D\u3002\u8FDC\u5904\u5706\u901A\u7684\u5934\u9885\u53CC\u76EE\u5706\u7741\u5145\u6EE1\u96BE\u4EE5\u7F6E\u4FE1\u7684\u795E\u8272\u3002</p><p>\u97AD\u67C4\u7684\u5947\u5149\u6E10\u6E10\u6536\u655B\uFF0C\u6155\u5BB9\u9F99\u624B\u8155\u4E00\u6296\uFF0C\u4EE5\u8427\u4F5B\u5974\u7B4B\u8171\u5236\u6210\u7684\u97AD\u8EAB\u500F\u5FFD\u7F29\u8FDB\u67C4\u5185\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u597D\u97AD\u3002\u597D\u5200\u3002\u300D</p><p>\u661F\u6708\u6E56\u4E09\u5927\u9547\u6559\u795E\u5175\uFF0C\u5F53\u65E5\u5728\u795E\u6BBF\u65E5\u6708\u94A9\u4E00\u62DB\u5236\u4F4F\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4ECA\u65E5\u8361\u661F\u97AD\u53C8\u8FEB\u5F97\u5706\u901A\u53CC\u76EE\u96BE\u7741\uFF0C\u6155\u5BB9\u9F99\u8BC8\u4F5C\u4E0D\u652F\u4EE5\u7247\u7389\u4E00\u5200\u65A9\u6740\u8FD9\u4E2A\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u9996\u5EA7\uFF0C\u679C\u7136\u662F\u795E\u5A01\u65E0\u6BD4\u3002\u53EF\u60DC\u540D\u5217\u795E\u5175\u4E4B\u9996\u7684\u7384\u5929\u5251\u81F3\u4ECA\u4E0B\u843D\u4E0D\u660E\u3002</p><p>\u5B89\u5B50\u5B8F\u6697\u670D\uFF0C\u5BAB\u4E3B\u56FA\u7136\u662F\u5360\u4E86\u795E\u5175\u7684\u4FBF\u5B9C\uFF0C\u4F46\u5BF9\u96EA\u5CF0\u795E\u5C3C\u548C\u5706\u901A\u90FD\u662F\u4E00\u51FB\u5FC5\u6740\uFF0C\u8FD9\u4EFD\u773C\u5149\u548C\u529F\u529B\u4E5F\u975E\u540C\u5C0F\u53EF\u3002</p><p>\u7D2B\u73AB\u6CA1\u60F3\u5230\u7728\u773C\u5DE5\u592B\u573A\u4E2D\u5C31\u751F\u6B7B\u7ACB\u5206\uFF0C\u6709\u5FC3\u85C9\u673A\u9003\u8D70\u4E5F\u6765\u4E0D\u53CA\u3002\u6697\u6697\u53F9\u4E86\u53E3\u6C14\uFF0C\u5979\u4F4E\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u628A\u4ED6\u4EEC\u5B89\u846C\u4E86\u5427\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E0D\u613F\u62C2\u5979\u597D\u610F\uFF0C\u4E8E\u662F\u70B9\u5934\u7B54\u5E94\u3002</p><p>\u7D2B\u73AB\u6005\u7136\u770B\u7740\u5706\u901A\u7684\u5934\u9885\uFF0C\u5FC3\u91CC\u6697\u6697\u8BF4\uFF1A\u300C\u5927\u5E08\u5728\u5929\u4E4B\u7075\uFF0C\u4FDD\u4F51\u5C0F\u5973\u5B50\u9003\u79BB\u751F\u5929\uFF0C\u62A5\u4EC7\u96EA\u803B\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u56DE\u8FC7\u957F\u9E70\u4F1A\u5929\u5DF2\u8FC7\u5348\uFF0C\u5F53\u4E0B\u6155\u5BB9\u9F99\u5F15\u5B89\u5B50\u5B8F\u4E0E\u4F17\u4EBA\u60F3\u89C1\u3002\u5B89\u5B50\u5B8F\u867D\u4E0E\u7075\u7389\u771F\u4EBA\u5C0F\u6709\u82A5\u8482\uFF0C\u4F46\u5F53\u65E5\u795E\u6BBF\u8840\u6218\u96EA\u5CF0\u795E\u5C3C\uFF0C\u4E5F\u7B97\u6709\u4E9B\u60C5\u4EFD\uFF0C\u5982\u4ECA\u540C\u5C5E\u795E\u6559\uFF0C\u5BF9\u4EE5\u5F80\u7684\u8FC7\u8282\u4E00\u7B11\u800C\u7F62\u3002</p><p>\u7D2B\u73AB\u8BB0\u6302\u6BCD\u4EB2\uFF0C\u5306\u5306\u6D17\u4E86\u628A\u8138\u5C31\u8D76\u53BB\u95EE\u5B89\u3002\u4E00\u63A8\u623F\u95E8\u5374\u662F\u95E9\u7740\u7684\u3002\u5979\u4E0D\u8010\u70E6\u5730\u8BF4\uFF1A\u300C\u5F00\u95E8\uFF01\u662F\u6211\u3002\u300D</p><p>\u300C\u56DE\u5C11\u592B\u4EBA\uFF0C\u5BAB\u4E3B\u6709\u4EE4\uFF0C\u4E0D\u8BB8\u5974\u5A62\u5F00\u95E8\u3002\u300D</p><p>\u7D2B\u73AB\u7591\u60D1\u5730\u95EE\u9053\uFF1A\u300C\u4F60\u4EEC\u5728\u5E72\u561B\uFF1F\u300D</p><p>\u300C\u5974\u5A62\u5728\u4F3A\u5019\u592B\u4EBA\uFF0C\u5C11\u592B\u4EBA\u8BF7\u56DE\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u7126\u6025\u8D77\u6765\uFF0C\u6C14\u9053\uFF1A\u300C\u8D31\u5A62\uFF01\u5FEB\u5F00\u95E8\uFF01\u300D</p><p>\u623F\u5185\u606D\u987A\u5730\u8BF4\uFF1A\u300C\u5C11\u592B\u4EBA\u606F\u6012\uFF0C\u8FD9\u662F\u5BAB\u4E3B\u7684\u5429\u5490\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u8DFA\u811A\uFF0C\u53BB\u627E\u6155\u5BB9\u9F99\u5F00\u95E8\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5BF9\u671B\u4E00\u773C\uFF0C\u7B11\u9053\uFF1A\u300C\u592B\u4EBA\uFF0C\u8BE5\u5403\u996D\u4E86\u3002\u300D</p><p>\u8427\u4F5B\u5974\u88F8\u8EAB\u8EBA\u5728\u69BB\u4E0A\uFF0C\u80A1\u95F4\u6CBE\u6EE1\u79FD\u7269\uFF0C\u53C8\u662F\u7F9E\u6127\u53C8\u662F\u96BE\u53D7\uFF0C\u5982\u6C34\u7684\u4FCF\u76EE\u4E0D\u4F4F\u671D\u8FD9\u5BF9\u5A07\u7F8E\u7684\u59D0\u59B9\u82B1\u8138\u4E0A\u77A7\u53BB\uFF0C\u60F3\u63D0\u9192\u5979\u4EEC\u8BE5\u7ED9\u81EA\u5DF1\u6362\u5C3F\u5E03\u4E86\u3002</p><p>\u767D\u7389\u83BA\u7B11\u563B\u563B\u9053\uFF0C\u300C\u592B\u4EBA\u7684\u773C\u775B\u771F\u6F02\u4EAE\uFF0C\u4EAE\u6676\u6676\uFF0C\u4E00\u95EA\u4E00\u95EA\u7684\uFF0C\u597D\u50CF\u4F1A\u8BF4\u8BDD\u5462\u3002\u300D</p><p>\u767D\u7389\u9E42\u7AEF\u7740\u789F\u5B50\u51D1\u8FC7\u6765\uFF0C\u300C\u771F\u662F\u4F1A\u8BF4\u8BDD\u5462\u3002\u662F\u4E0D\u662F\u60F3\u8BF4\uFF1A\u5988\u2014\u9EBB\u2014\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u7ED9\u6211\u6362\u5C3F\u5E03\u5462\uFF1F\u300D\u5979\u5B66\u7740\u5C0F\u5973\u5B69\u7684\u5976\u58F0\u5976\u6C14\uFF0C\u4E00\u5B57\u4E00\u53E5\u8BF4\u7740\uFF0C\u9017\u5F97\u767D\u7389\u83BA\u4E00\u9635\u5A07\u7B11\u3002</p><p>\u300C\u8FD9\u4E48\u70ED\u7684\u5929\uFF0C\u5305\u7740\u5C3F\u5E03\uFF0C\u91CC\u9762\u53C8\u662F\u5C4E\u53C8\u662F\u5C3F\uFF0C\u7C98\u4E4E\u4E4E\u810F\u516E\u516E\u7684\uFF0C\u662F\u4E0D\u662F\u5F88\u96BE\u53D7\u554A\uFF1F\u300D\u767D\u7389\u83BA\u624B\u6307\u5728\u8427\u4F5B\u5974\u767D\u5AE9\u7684\u5A07\u8EAF\u4E0A\u5212\u7740\u5708\u5B50\uFF0C\u5475\u54C4\u9053\uFF1A\u300C\u54ED\u4E00\u4E2A\uFF0C\u54ED\u4E00\u4E2A\u963F\u59E8\u5C31\u7ED9\u4F60\u6362\u5C3F\u5E03\u3002\u54ED\u554A\uFF0C\u54ED\u554A\u2026\u2026\u300D</p><p>\u7F8E\u5987\u5FCD\u4E86\u7247\u523B\uFF0C\u773C\u6CEA\u8FD8\u662F\u4E00\u6EF4\u6EF4\u6DCC\u4E86\u51FA\u6765\u3002</p><p>\u767D\u7389\u83BA\u62CD\u624B\u7B11\u9053\uFF1A\u300C\u771F\u4E56\uFF0C\u53EF\u60DC\u963F\u59E8\u662F\u9A97\u4F60\u7684\u5566\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7EC8\u4E8E\u660E\u767D\u8FC7\u6765\uFF1A\u4E24\u4EBA\u662F\u6545\u610F\u4E0D\u7ED9\u81EA\u5DF1\u6362\u5C3F\u5E03\uFF0C\u5C31\u60F3\u770B\u81EA\u5DF1\u8EBA\u5728\u5C4E\u5C3F\u91CC\u7684\u5C48\u8FB1\u6A21\u6837\u3002\u5979\u5FC3\u91CC\u53C8\u662F\u7F9E\u803B\u53C8\u662F\u6C14\u6068\uFF0C\u4FCF\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u6CEA\u6C34\u6D41\u5F97\u6108\u53D1\u6C79\u6D8C\u3002</p><p>\u62CD\u95E8\u58F0\u518D\u6B21\u54CD\u8D77\uFF0C\u300C\u5F00\u95E8\u8BA9\u6211\u8FDB\u53BB\uFF01\u300D\u7D2B\u73AB\u53EB\u9053\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u4E00\u542C\u5C31\u77E5\u9053\u5BAB\u4E3B\u6CA1\u6709\u7B54\u5E94\uFF0C\u88C5\u51FA\u606D\u987A\u7684\u6837\u5B50\u67D4\u58F0\u9053\uFF1A\u300C\u6CA1\u6709\u5BAB\u4E3B\u7684\u547D\u4EE4\uFF0C\u5974\u5A62\u4E0D\u6562\u5F00\u95E8\uFF0C\u8BF7\u5C11\u592B\u4EBA\u52FF\u7F6A\u3002\u300D</p><p>\u7D2B\u73AB\u53EB\u4E86\u534A\u664C\uFF0C\u53EA\u597D\u6068\u6068\u53BB\u4E86\u3002</p><p>\u8427\u4F5B\u5974\u5B57\u5B57\u53E5\u53E5\u90FD\u542C\u5728\u5FC3\u91CC\uFF0C\u89C1\u5973\u513F\u4E5F\u65E0\u6CD5\u4FDD\u62A4\u81EA\u5DF1\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u53D1\u51C9\u3002</p><p>\u867D\u7136\u6CEA\u6D41\u6EE1\u9762\uFF0C\u767E\u82B1\u89C2\u97F3\u8138\u4E0A\u4F9D\u7136\u4E0D\u51CF\u9AD8\u8D35\uFF0C\u542B\u7F9E\u5FCD\u8FB1\u7684\u8D35\u5987\u522B\u6709\u4E00\u756A\u98CE\u97F5\uFF0C\u90A3\u79CD\u695A\u695A\u52A8\u4EBA\u7684\u7F8E\u6001\u4F7F\u767D\u7389\u83BA\u5FCD\u4E0D\u4F4F\u5FC3\u91CC\u53D1\u75D2\uFF0C\u89C1\u5C11\u592B\u4EBA\u5DF2\u53BB\uFF0C\u5979\u4FBF\u64A9\u8D77\u8863\u88D9\u9664\u4E0B\u4EB5\u88E4\uFF0C\u4E00\u5C41\u80A1\u5750\u5728\u8427\u4F5B\u5974\u8138\u4E0A\uFF0C\u7528\u9634\u6237\u5728\u5979\u53E3\u9F3B\u95F4\u4F7F\u52B2\u78E8\u64E6\u3002</p><p>\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u59D0\u59D0\u662F\u4E0D\u662F\u60F3\u7537\u4EBA\u4E86\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u5A07\u5598\u8FDE\u8FDE\uFF0C\u300C\u5BAB\u4E3B\u88AB\u73AB\u7470\u4ED9\u5B50\u90A3\u4E2A\u9A9A\u72D0\u72F8\u5929\u5929\u7F20\u7740\u9B3C\u6DF7\uFF0C\u597D\u4E45\u90FD\u6CA1\u6709\u64CD\u4EBA\u5BB6\u4E86\u3002\u300D</p><p>\u767D\u7389\u9E42\u4E5F\u89E3\u8863\u4E0A\u69BB\uFF0C\u6367\u4F4F\u8427\u4F5B\u5974\u7684\u4E73\u623F\u73A9\u5F04\u7740\u8BF4\uFF1A\u300C\u6628\u665A\u4F60\u4E0D\u662F\u8FD8\u8DDF\u77F3\u4F9B\u5949\u4E0A\u8FC7\u5E8A\u5417\uFF1F\u300D</p><p>\u300C\u4ED6\u4EEC\u54EA\u6BD4\u5F97\u4E0A\u5BAB\u4E3B\u2026\u2026\u5012\u662F\u7075\u7389\u8FD8\u6709\u4E9B\u624B\u6BB5\uFF0C\u90A3\u5929\u6211\u770B\u4F60\u8BA9\u4ED6\u5E72\u5F97\u9B42\u90FD\u6CA1\u4E86\u3002\u300D</p><p>\u767D\u7389\u9E42\u62FF\u8D77\u8427\u4F5B\u5974\u8F6F\u7EF5\u7EF5\u7684\u7EA4\u624B\u653E\u5230\u8179\u4E0B\uFF0C\u300C\u7075\u7389\u7684\u836F\u597D\u5389\u5BB3\uFF0C\u524D\u5929\u859B\u5A4A\u5B50\u7528\u4E86\u4E00\u679A\uFF0C\u7ED3\u679C\u9053\u957F\u7684\u62C2\u5C18\u585E\u5230\u5979\u5C44\u91CC\u9762\u62D4\u4E0D\u51FA\u6765\u3002\u563B\u563B\uFF0C\u540E\u6765\u5E2E\u91CC\u6709\u4E8B\uFF0C\u5979\u5C31\u63D2\u7740\u62C2\u5C18\u53BB\u4E86\u3002\u542C\u8BF4\u665A\u4E0A\u56DE\u6765\u8FD8\u5728\u91CC\u9762\uFF0C\u5927\u4F19\u53EA\u597D\u64CD\u5979\u7684\u5C41\u773C\u513F\uFF0C\u64CD\u5F97\u5979\u54ED\u90FD\u54ED\u4E0D\u51FA\u6765\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u5931\u7B11\u9053\uFF1A\u300C\u8FD9\u4E48\u5389\u5BB3\uFF0C\u54EA\u5929\u6211\u4E5F\u53BB\u8BA8\u4E00\u679A\uFF0C\u653E\u5728\u54B1\u4EEC\u592B\u4EBA\u7684\u5C41\u773C\u91CC\uFF0C\u770B\u5979\u8FD8\u6574\u5929\u4E71\u62C9\u5C4E\u3002\u300D\u8BF4\u7740\u4E0B\u4F53\u91CD\u91CD\u4E00\u62E7\u3002</p><p>\u8427\u4F5B\u5974\u62FC\u547D\u6446\u7740\u5934\uFF0C\u8EB2\u907F\u90A3\u80A1\u4EE4\u4EBA\u4F5C\u5455\u7684\u9178\u8165\u6C14\u606F\u3002</p><p>\u7B49\u767D\u7389\u83BA\u62AC\u8D77\u96EA\u81C0\uFF0C\u7F8E\u5987\u5982\u82B1\u7684\u4FCF\u8138\u4E0A\u5DF2\u7ECF\u6CBE\u6EE1\u6CEA\u6C34\u548C\u6E7F\u9ECF\u7684\u6DEB\u6DB2\u3002\u767D\u7389\u83BA\u4E5F\u4E0D\u53BB\u64E6\u62ED\uFF0C\u76F4\u63A5\u634F\u5F00\u8427\u4F5B\u5974\u7684\u7259\u5173\uFF0C\u628A\u94F6\u8033\u6C64\u704C\u5230\u5979\u7684\u5634\u4E2D\u3002</p><p>\u8427\u4F5B\u5974\u521A\u5598\u4E86\u4E00\u53E3\u6C14\uFF0C\u53C8\u88AB\u704C\u4E86\u6EE1\u53E3\u7684\u6C64\u6C34\uFF0C\u987F\u65F6\u54B3\u55FD\u8D77\u6765\u3002\u767D\u7389\u83BA\u7B49\u5979\u54B3\u5B8C\uFF0C\u7528\u6C64\u5319\u628A\u7F8E\u5987\u54B3\u51FA\u7684\u6C41\u6DB2\uFF0C\u8FDE\u540C\u5979\u9762\u4E0A\u7684\u773C\u6CEA\u3001\u6DEB\u6C34\u5C3D\u6570\u522E\u5230\u5979\u5634\u5185\uFF0C\u7B11\u9053\uFF1A\u300C\u4E56\u4E56\u559D\uFF0C\u8FD9\u662F\u6211\u4EEC\u59D0\u59B9\u4E13\u95E8\u4E3A\u592B\u4EBA\u71AC\u7684\u6C64\uFF0C\u5473\u9053\u4E0D\u9519\u5427\u3002\u300D</p><p>\u8FD9\u8FB9\u767D\u7389\u9E42\u4E5F\u5DF2\u5B8C\u4E8B\uFF0C\u5979\u66F2\u8D77\u8427\u4F5B\u5974\u7684\u624B\u81C2\uFF0C\u5C06\u6CBE\u6EE1\u81EA\u5DF1\u4F53\u6DB2\u7684\u624B\u6307\u653E\u5728\u7F8E\u5987\u53E3\u4E2D\uFF0C\u300C\u624B\u4E0A\u4E5F\u8981\u8214\u5E72\u51C0\u5662\u3002\u300D</p><p>\u8427\u4F5B\u5974\u4E00\u4E2A\u56DB\u80A2\u762B\u8F6F\u7684\u5F31\u8D28\u5973\u6D41\uFF0C\u600E\u662F\u4E24\u5973\u7684\u5BF9\u624B\uFF0C\u867D\u7136\u5FC3\u4E2D\u767E\u822C\u4E0D\u613F\uFF0C\u4E5F\u53EA\u80FD\u628A\u8FD9\u4E9B\u6076\u5FC3\u7684\u4E1C\u897F\u5C3D\u6570\u541E\u4E0B\u3002</p><p>\u8FD9\u6B21\u7684\u5348\u996D\u6BD4\u5F80\u65E5\u591A\u4E86\u4E00\u500D\u6709\u4F59\uFF0C\u4E24\u5973\u4E00\u8FB9\u5582\u4E00\u8FB9\u73A9\u5F04\uFF0C\u534A\u4E2A\u65F6\u8FB0\u8FD8\u672A\u5582\u5B8C\u3002</p><p>\u8427\u4F5B\u5974\u6E10\u6E10\u89C9\u5F97\u60C5\u5F62\u4E0D\u5BF9\uFF0C\u8179\u5185\u9F13\u9F13\u80C0\u80C0\uFF0C\u8FD8\u4E0D\u65F6\u75C9\u631B\u3002\u6B63\u72B9\u7591\u95F4\uFF0C\u5C0F\u8179\u4E00\u9707\uFF0C\u4E00\u80A1\u9ECF\u7A20\u7684\u6E7F\u70ED\u7269\u4F53\u7A81\u7136\u55B7\u6D8C\u800C\u51FA\u3002</p><p>\u7F8E\u5987\u8138\u8272\u96EA\u767D\uFF0C\u5A07\u8EAF\u4E0D\u4F4F\u62BD\u52A8\uFF0C\u4E0D\u591A\u65F6\u5C3F\u5E03\u5185\u4FBF\u5145\u6EE1\u79FD\u7269\uFF0C\u6E7F\u7C98\u7684\u6C61\u7269\u6EA2\u5230\u817F\u7F1D\u4E0A\u7F18\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u7B11\u5BB9\u6EE1\u9762\uFF0C\u767D\u7389\u9E42\u817B\u58F0\u9053\uFF1A\u300C\u6C64\u91CC\u52A0\u4E86\u4E00\u70B9\u70B9\u6CFB\u836F\uFF0C\u592B\u4EBA\u559C\u6B22\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5F20\u7740\u5C0F\u5634\uFF0C\u7EA2\u5507\u98A4\u6296\uFF0C\u65E0\u58F0\u7684\u6078\u54ED\u7740\u3002\u8179\u5185\u7684\u75C9\u631B\u521A\u521A\u505C\u6B62\uFF0C\u53C8\u5267\u70C8\u5730\u8815\u52A8\u8D77\u6765\u3002\u80A0\u9053\u5F3A\u70C8\u7684\u523A\u6FC0\u4E0B\uFF0C\u7F8E\u5987\u7EA4\u8170\u65F6\u8D77\u65F6\u843D\uFF0C\u4E0B\u4F53\u5C4E\u5C3F\u9F50\u6D41\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5BF9\u5979\u7684\u75DB\u82E6\u6BEB\u65E0\u601C\u60DC\uFF0C\u4E00\u8FB9\u4EFB\u5979\u6392\u6CC4\uFF0C\u4E00\u8FB9\u634F\u7740\u5634\u5F3A\u884C\u628A\u98DF\u7269\u586B\u5165\u3002</p><p>\u767D\u7389\u9E42\u63A9\u9F3B\u9053\uFF1A\u300C\u8FD9\u6837\u4F1A\u4E0D\u4F1A\u628A\u592B\u4EBA\u4E0B\u9762\u6CE1\u574F\u4E86\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u9053\uFF1A\u300C\u8FD9\u4F1A\u513F\u592B\u4EBA\u9634\u6237\u91CC\u53EA\u6015\u4E5F\u704C\u8FDB\u5C4E\u5C3F\u4E86\uFF0C\u8981\u662F\u6CE1\u574F\u53EF\u600E\u4E48\u529E\u5462\uFF1F\u300D</p><p>\u5979\u62FF\u7740\u6C64\u5319\u5728\u8427\u4F5B\u5974\u4E0B\u4F53\u6363\u4E86\u6363\uFF0C\u5929\u771F\u5730\u8BF4\uFF1A\u300C\u8FD9\u4E24\u4E2A\u6D1E\u574F\u4E86\uFF0C\u592B\u4EBA\u8FD8\u9760\u4EC0\u4E48\u6D3B\u5462\uFF1F\u300D\u8BF4\u7F62\u53C8\u8200\u4E86\u4E00\u5319\u6C64\u704C\u5230\u8427\u4F5B\u5974\u53E3\u4E2D\u3002</p><p>\u767D\u7389\u9E42\u5939\u8D77\u4E00\u5757\u80A5\u8089\u585E\u8FDB\u7F8E\u5987\u5634\u5185\uFF0C\u300C\u591A\u5403\u70B9\uFF0C\u591A\u62C9\u70B9\u3002\u4F60\u53EB\u6211\u4E00\u58F0\u963F\u59E8\uFF0C\u6211\u7ED9\u4F60\u89E3\u5F00\u5C3F\u5E03\u900F\u900F\u98CE\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u7EA2\u5507\u6CBE\u4E0A\u6CB9\u8102\uFF0C\u6108\u53D1\u5A07\u8273\uFF0C\u8427\u4F5B\u5974\u5F3A\u5FCD\u7740\u83CA\u809B\u7684\u75C9\u631B\uFF0C\u59CB\u7EC8\u575A\u5B88\u81EA\u5DF1\u8BB8\u4E0B\u4F5B\u613F\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002</p><p>\u300C\u771F\u4E56\uFF0C\u62C9\u809A\u5B50\u8FD8\u80FD\u5403\u8FD9\u4E48\u591A\u3002\u300D\u767D\u7389\u83BA\u7B11\u7740\u8BF4\uFF1A\u300C\u5979\u4E5F\u662F\u5728\u9A97\u4F60\u5566\uFF0C\u6CA1\u6709\u5BAB\u4E3B\u5429\u5490\uFF0C\u5974\u5A62\u600E\u4E48\u6562\u7ED9\u592B\u4EBA\u6362\u5C3F\u5E03\u5462\uFF1F\u300D\u5979\u6536\u8D77\u7897\u789F\uFF0C\u300C\u592B\u4EBA\u597D\u597D\u60F3\u60F3\uFF0C\u600E\u4E48\u8BA9\u5BAB\u4E3B\u9AD8\u5174\u2026\u2026\u300D</p><p>\u623F\u95E8\u546F\u7684\u4E00\u58F0\u5408\u4E0A\uFF0C\u63A5\u7740\u5361\u55D2\u9501\u7D27\uFF0C\u623F\u95F4\u91CC\u53EA\u5269\u4E0B\u5A07\u5F31\u7684\u7F8E\u5987\u6A2A\u9648\u69BB\u4E0A\u3002\u5979\u5931\u795E\u5730\u770B\u7740\u623F\u9876\uFF0C\u7F8E\u8273\u7684\u7389\u4F53\u9707\u98A4\u4E0D\u5DF2\uFF0C\u6CC4\u51FA\u80A1\u80A1\u6C61\u7269\u3002</p><p>\u65E5\u5F71\u897F\u659C\uFF0C\u8427\u4F5B\u5974\u6CEA\u6C34\u6E10\u6E10\u5E72\u6DB8\uFF0C\u4F46\u4FBF\u610F\u8FD8\u662F\u4E0D\u4F4F\u88AD\u6765\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u5979\u7684\u4E73\u5934\u4E5F\u786C\u786C\u6311\u8D77\u3002\u5438\u6536\u4E86\u711A\u60C5\u818F\u7684\u83CA\u809B\u654F\u9510\u5F02\u5E38\uFF0C\u6BCF\u4E00\u6B21\u55B7\u53D1\u90FD\u4F34\u7740\u96BE\u8A00\u7684\u5FEB\u611F\u3002</p><p>\u8427\u4F5B\u5974\u7741\u7740\u7A7A\u6D1E\u7684\u7F8E\u76EE\uFF0C\u5FC3\u91CC\u5583\u5583\u9053\uFF1A\u300C\u4F5B\u7956\uFF0C\u4F60\u8FD8\u4FDD\u4F51\u6211\u5417\uFF1F\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5B50\u591C\uFF0C\u4E00\u8EAB\u9ED1\u8863\u7684\u6155\u5BB9\u9F99\u6084\u65E0\u58F0\u606F\u7684\u56DE\u5230\u522B\u9662\u3002</p><p>\u7D2B\u73AB\u652F\u9890\u5750\u5728\u51E0\u65C1\uFF0C\u6EE1\u773C\u6101\u6028\u5730\u770B\u7740\u70DB\u5149\uFF0C\u76F4\u5230\u6155\u5BB9\u9F99\u8D70\u5230\u8EAB\u540E\u624D\u8B66\u89C9\u8FC7\u6765\u3002</p><p>\u770B\u5230\u6155\u5BB9\u9F99\u63D0\u7740\u4E00\u4E2A\u5DE8\u5927\u7684\u5305\u88F9\uFF0C\u4E0D\u7531\u95EE\u9053\uFF1A\u300C\u90A3\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u628A\u5305\u88F9\u653E\u5728\u69BB\u4E0A\uFF0C\u7D2B\u73AB\u624D\u53D1\u73B0\u90A3\u662F\u4E00\u5E8A\u68C9\u88AB\u3002\u89E3\u5F00\u4E00\u770B\uFF0C\u91CC\u9762\u662F\u51E0\u5757\u6676\u5149\u95EA\u52A8\u7684\u5DE8\u51B0\u3002\u5979\u987F\u65F6\u660E\u767D\u8FC7\u6765\uFF0C\u300C\u4F60\u5165\u5BAB\u4E86\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u53D6\u51FA\u4E00\u5757\u653E\u5728\u69BB\u8FB9\uFF0C\u5C06\u5176\u4F59\u5305\u597D\u3002</p><p>\u7D2B\u73AB\u6478\u4E86\u6478\u51B0\u5757\uFF0C\u6B23\u559C\u5730\u8BF4\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u4EB2\u81EA\u53BB\u7ED9\u6211\u53D6\u51B0\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u7B11\uFF0C\u76F4\u8170\u7AD9\u8D77\u3002\u7D2B\u73AB\u67D4\u987A\u5730\u89E3\u5F00\u4ED6\u7684\u591C\u884C\u8863\uFF0C\u9664\u53BB\u978B\u889C\uFF0C\u7136\u540E\u4E56\u4E56\u8EBA\u5728\u4ED6\u6000\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u5E76\u6CA1\u6709\u50CF\u4EE5\u5F80\u90A3\u6837\u6025\u5207\u5730\u4E0E\u5979\u540C\u6548\u4E8E\u98DE\u4E4B\u4E50\uFF0C\u4ED6\u4E00\u624B\u62E5\u7740\u59B9\u59B9\uFF0C\u4E00\u624B\u6469\u6332\u7740\u5BD2\u51B0\uFF0C\u9759\u9759\u770B\u7740\u51B0\u5757\u4E2D\u8DF3\u52A8\u7684\u70DB\u5149\u3002\u51B0\u5757\u5BD2\u6C14\u6C24\u6C32\uFF0C\u5BA4\u5185\u7684\u71E0\u70ED\u6E10\u6E10\u6D88\u9000\uFF0C\u8FDC\u8FD1\u5BC2\u65E0\u4EBA\u58F0\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u6211\u89C1\u5230\u59DA\u5174\u4E86\u3002\u300D</p><p>\u300C\u59DA\u5174\uFF1F\u300D\u7D2B\u73AB\u6014\u4E86\u4E00\u4E0B\uFF0C\u65CB\u5373\u60F3\u8D77\u662F\u5468\u5E1D\u59DA\u5174\uFF0C\u81EA\u5DF1\u7684\u6740\u7236\u4EC7\u4EBA\u3002</p><p>\u300C\u4ED6\u770B\u4E0A\u53BB\u4E94\u5341\u591A\u5C81\uFF0C\u6709\u4E9B\u53D1\u798F\uFF0C\u767D\u767D\u80D6\u80D6\uFF0C\u600E\u4E48\u4E5F\u4E0D\u50CF\u4E0A\u8FC7\u6218\u573A\u7684\u4EBA\u3002\u60F3\u6765\u65E5\u5B50\u8FC7\u5F97\u4E0D\u9519\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7684\u53E3\u6C14\u5F88\u6DE1\uFF0C\u4F46\u523B\u9AA8\u7684\u6068\u610F\u5374\u4F7F\u7D2B\u73AB\u6253\u4E86\u4E2A\u5BD2\u5664\u3002\u7D2B\u73AB\u662F\u9057\u8179\u5B50\uFF0C\u4ECE\u6765\u6CA1\u6709\u89C1\u8FC7\u7236\u4EB2\u6155\u5BB9\u7941\uFF0C\u4E49\u7236\u6155\u5BB9\u536B\u53C8\u5BF9\u5979\u73CD\u7231\u4E07\u5206\uFF0C\u56E0\u6B64\u4E0D\u50CF\u6155\u5BB9\u9F99\u90A3\u6837\u6709\u5207\u80A4\u4E4B\u75DB\u3002\u534A\u664C\uFF0C\u5979\u8F7B\u58F0\u9053\uFF1A\u300C\u4F60\u8981\u600E\u4E48\u6837\u5462\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u95ED\u4E0A\u773C\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u6211\u5E0C\u671B\u4ED6\u4E0D\u8981\u65E9\u6B7B\u3002\u300D</p>',70);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
