import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_82" tabindex="-1"><a class="header-anchor" href="#_82" aria-hidden="true">#</a> 82</h1><p>\u300C\u522B\u4E71\u6324\uFF01\u6309\u53F7\u6392\u961F\u3002\u300D\u5F90\u65AD\u864E\u4F24\u7652\u540E\u52A0\u5165\u571F\u5802\uFF0C\u4E5F\u5F53\u4E0A\u4E86\u4E2A\u5C0F\u5934\u76EE\uFF0C\u5949\u547D\u5728\u752C\u9053\u5185\u7EF4\u6301\u79E9\u5E8F\u3002</p><p>\u4F17\u4EBA\u867D\u7136\u9A82\u9A82\u54A7\u54A7\uFF0C\u4F46\u4E5F\u4E0D\u6562\u574F\u4E86\u6C90\u62A4\u6CD5\u8BA2\u4E0B\u7684\u89C4\u77E9\uFF0C\u8001\u8001\u5B9E\u5B9E\u5728\u77F3\u5BA4\u5916\u6392\u6210\u4E00\u961F\u3002</p><p>\u95E8\u65C1\u653E\u7740\u4E00\u4E2A\u94DC\u5236\u7684\u6CB9\u7F38\uFF0C\u8DB3\u6709\u534A\u4EBA\u9AD8\u3002\u4E00\u540D\u5E2E\u4F17\u94BB\u8FDB\u95E8\uFF0C\u638F\u51FA\u4E00\u679A\u94DC\u94B1\u5F80\u7F38\u91CC\u4E00\u4E22\uFF0C\u6025\u5306\u5306\u8131\u4E0B\u8863\u670D\u3002</p><p>\u5BA4\u5185\u7AD6\u7740\u4E00\u5835\u6F06\u6210\u9ED1\u8272\u7684\u6728\u677F\uFF0C\u5C06\u77F3\u5BA4\u9694\u6210\u5185\u5916\u4E24\u95F4\u3002\u677F\u58C1\u671D\u5916\u4E00\u9762\uFF0C\u9732\u51FA\u4E00\u56E2\u6CB9\u8102\u822C\u96EA\u767D\u6ED1\u817B\u7684\u8089\u4F53\uFF0C\u5F62\u72B6\u6D51\u5706\u3002\u8089\u56E2\u4E0A\u90E8\u9686\u8D77\u4E24\u9053\u6ED1\u817B\u7684\u96EA\u767D\uFF0C\u5706\u6DA6\u7684\u7389\u67F1\u822C\u6D88\u5931\u5728\u677F\u58C1\u4E4B\u4E2D\u3002\u8089\u56E2\u6B63\u4E2D\uFF0C\u9F13\u8D77\u4E00\u84EC\u5927\u5982\u624B\u638C\u7684\u9C9C\u7EA2\u5AE9\u8089\uFF0C\u6E7F\u6DCB\u6DCB\u7FFB\u5377\u5982\u76DB\u5F00\u7684\u9C9C\u82B1\u3002\u8089\u82B1\u4E0B\u65B9\uFF0C\u662F\u4E00\u4E2A\u7C89\u7EA2\u7684\u5C0F\u5DE7\u8089\u7A74\uFF0C\u540C\u6837\u9F13\u51FA\u534A\u5BF8\uFF0C\u5FAE\u5FAE\u8815\u52A8\u3002</p><p>\u90A3\u5927\u6C49\u633A\u7740\u8089\u68D2\u8D70\u6765\uFF0C\u72E0\u72E0\u6345\u5165\u76DB\u5F00\u7684\u8089\u82B1\u4E4B\u5185\uFF0C\u7136\u540E\u62B1\u7740\u677F\u58C1\u4E0A\u7684\u8089\u56E2\u633A\u52A8\u8D77\u6765\u3002</p><p>\u4ED6\u8EAB\u8FB9\u7684\u677F\u58C1\u8D34\u7740\u4E00\u5F20\u767D\u7EB8\uFF0C\u4E0A\u9762\u5199\u7740\uFF1A\u300C\u8D31\u4EBA\u96EA\u5CF0\uFF0C\u4E3A\u5974\u795E\u6559\uFF0C\u51E1\u6211\u5E2E\u4F17\uFF0C\u4E00\u6587\u4E00\u64CD\u3002\u300D</p><p>\u677F\u58C1\u662F\u7ED9\u96EA\u5CF0\u795E\u5C3C\u91CF\u8EAB\u5B9A\u505A\u7684\uFF0C\u5408\u7D27\u540E\u6B63\u597D\u5C06\u5979\u7684\u81C0\u90E8\u5361\u5728\u58C1\u4E2D\u3002\u4ECE\u5916\u9762\u770B\u6765\uFF0C\u9EDD\u9ED1\u7684\u5899\u4E0A\u53EA\u6709\u5149\u79C3\u79C3\u4E00\u56E2\u80A5\u767D\u7684\u96EA\u81C0\uFF0C\u79D8\u5904\u5E73\u5E73\u671D\u4E0A\uFF0C\u4E0A\u4E0B\u4E24\u4E2A\u8089\u7A74\u6B63\u5728\u8089\u56E2\u9876\u7AEF\u3002</p><p>\u677F\u58C1\u53E6\u4E00\u9762\uFF0C\u53F6\u884C\u5357\u597D\u6574\u4EE5\u6687\u7684\u6B63\u895F\u5371\u5750\uFF0C\u4ED4\u7EC6\u68C0\u67E5\u795E\u5C3C\u7684\u8109\u52A8\u548C\u4F53\u5185\u6C14\u606F\u7684\u8FD0\u884C\uFF0C\u8BD5\u56FE\u627E\u51FA\u4E00\u4E2A\u63D0\u53D6\u529F\u529B\u7684\u529E\u6CD5\u3002</p><p>\u4ECE\u8FD9\u8FB9\u770B\u6765\uFF0C\u795E\u5C3C\u4EF0\u5929\u800C\u5367\uFF0C\u624B\u81C2\u5E73\u5206\uFF0C\u4E24\u6761\u5927\u817F\u6298\u5728\u9888\u4FA7\uFF0C\u8170\u90E8\u4EE5\u4E0B\u5374\u6D88\u5931\u5728\u677F\u58C1\u95F4\u3002\u7389\u4F53\u4F9D\u7136\u662F\u94A2\u7D22\u7F16\u7ED5\uFF0C\u80F8\u524D\u7684\u80A5\u4E73\u6324\u5728\u817F\u4E2D\uFF0C\u50CF\u4E24\u56E2\u6D41\u6EA2\u7684\u6ED1\u8102\uFF0C\u968F\u7740\u547C\u5438\u4E0D\u4F4F\u6643\u52A8\u3002\u96EA\u5CF0\u795E\u5C3C\u4E00\u8EAB\u529F\u529B\u50B2\u89C6\u5929\u4E0B\uFF0C\u6B64\u65F6\u5374\u88AB\u5361\u5728\u58C1\u4E2D\uFF0C\u53EA\u9732\u80A5\u81C0\u5728\u5916\uFF0C\u5B8C\u5168\u53D8\u6210\u4F9B\u4EBA\u53D1\u6CC4\u7684\u6DEB\u5668\u3002</p><p>\u300C\u4E00\u547C\uFF0C\u8109\u518D\u52A8\uFF0C\u6C14\u884C\u4E09\u5BF8\uFF1B\u4E00\u5438\uFF0C\u8109\u4EA6\u518D\u52A8\uFF0C\u6C14\u884C\u4E09\u5BF8\u3002\u300D\u53F6\u884C\u5357\u4ED4\u7EC6\u7EAA\u5F55\u4E0B\u6C14\u8109\u7684\u8FD0\u884C\u72B6\u51B5\uFF0C\u4E0D\u65F6\u4EE5\u91D1\u9488\u523A\u5165\u795E\u5C3C\u8BF8\u5904\u5927\u7A74\uFF0C\u7528\u5FC3\u63A8\u7B97\u51E4\u51F0\u771F\u6C14\u7684\u5F02\u5904\u3002</p><p>\u826F\u4E45\uFF0C\u4ED6\u653E\u4E0B\u7B14\uFF0C\u8D1F\u624B\u5728\u5BA4\u5185\u6765\u56DE\u8E31\u6B65\u3002</p><p>\u300C\u96BE\u9053\u8981\u7528\u593A\u80CE\u82B1\uFF1F\u300D\u53F6\u884C\u5357\u72B9\u8C6B\u4E0D\u51B3\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5374\u6CA1\u6709\u6CE8\u610F\u4ED6\u7684\u795E\u8272\uFF0C\u8EAB\u4F53\u7684\u75BC\u75DB\u65E9\u5DF2\u9EBB\u6728\uFF0C\u53EA\u5269\u4E0B\u81EA\u5DF1\u65E0\u6CD5\u770B\u5230\u7684\u7F9E\u5904\uFF0C\u611F\u89C9\u5206\u5916\u6E05\u6670\u3002\u8089\u68D2\u4E00\u8FDB\u4E00\u51FA\uFF0C\u4F3C\u4E4E\u957F\u5F97\u6CA1\u6709\u5C3D\u5934\u3002\u80A5\u539A\u7684\u82B1\u74E3\u6241\u6241\u644A\u5F00\uFF0C\u53C8\u6E7F\u53C8\u9ECF\uFF0C\u76F4\u81F3\u7C98\u5728\u7537\u4EBA\u8179\u4E0B\uFF0C\u88AB\u52A8\u7684\u5F00\u5408\u7740\u3002</p><p>\u62BD\u9001\u4E00\u4F1A\u513F\u540E\uFF0C\u8089\u68D2\u7A81\u7136\u6574\u6839\u62D4\u51FA\u4F53\u5916\u3002\u96EA\u5CF0\u795E\u5C3C\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u653E\u677E\u4E86\u81C0\u8089\u3002\u679C\u7136\u8089\u68D2\u8FDB\u5165\u809B\u95E8\uFF0C\u5728\u80A0\u9053\u5185\u6345\u5F97\u864E\u864E\u751F\u98CE\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7D27\u7D27\u54AC\u4F4F\u7259\u7B8D\uFF0C\u56E0\u4E3A\u5979\u77E5\u9053\u8FD9\u4E9B\u7537\u4EBA\u5728\u809B\u4EA4\u65F6\u6700\u559C\u6B22\u505A\u4EC0\u4E48\u3002\u90A3\u53CC\u624B\u4E0E\u5979\u60F3\u5F97\u5206\u6BEB\u4E0D\u5DEE\uFF0C\u679C\u7136\u662F\u626F\u4F4F\u82B1\u74E3\u8FB9\u7F18\u671D\u4E24\u8FB9\u62C9\u5F00\u3002\u79D8\u5904\u5C55\u5F00\u5230\u96BE\u4EE5\u60F3\u50CF\u5BBD\u5EA6\uFF0C\u6BCF\u4E00\u4E2A\u7EC6\u5C0F\u7684\u8936\u76B1\u90FD\u88AB\u62C9\u5E73\u2014\u2014\u5927\u6982\u6709\u7897\u53E3\u5927\u5C0F\u4E86\u5427\uFF0C\u8584\u5F97\u51E0\u4E4E\u900F\u660E\uFF0C\u4E0A\u9762\u7684\u8840\u7BA1\u4E5F\u80FD\u770B\u6E05\u695A\u2026\u2026</p><p>\u5185\u5C42\u7684\u82B1\u74E3\u50CF\u88AB\u62C9\u5E73\u4E86\u2026\u2026\u4E0D\u80FD\u518D\u62C9\u4E86\u2026\u2026\u65E0\u8282\u5236\u7684\u4F38\u5C55\u4F7F\u795E\u5C3C\u75BC\u75DB\u8D77\u6765\uFF0C\u5979\u95F7\u54FC\u4E00\u58F0\uFF0C\u6536\u7D27\u83CA\u809B\u3002</p><p>\u624B\u6307\u4E00\u677E\uFF0C\u5145\u6EE1\u5F39\u6027\u7684\u5AE9\u8089\u500F\u5FFD\u5408\u7D27\uFF0C\u53D1\u51FA\u556A\u53FD\u4E00\u58F0\u8F7B\u54CD\u3002\u7A81\u7FD8\u7684\u82B1\u8482\u88AB\u5AE9\u8089\u731B\u7136\u4E00\u5939\uFF0C\u987F\u65F6\u786C\u8D77\u3002\u5931\u53BB\u5305\u76AE\u7684\u82B1\u8482\u654F\u611F\u5F02\u5E38\uFF0C\u51B5\u4E14\u91CC\u9762\u8FD8\u6709\u4E24\u7C92\u5C0F\u94BB\u3002\u4F46\u795E\u5C3C\u77E5\u9053\uFF0C\u75DB\u82E6\u7684\u8FD8\u5728\u540E\u9762\u3002</p><p>\u624B\u6307\u4F38\u8FDB\u82B1\u74E3\u4E00\u9635\u638F\u6316\uFF0C\u7C97\u66B4\u5730\u6293\u4F4F\u82B1\u8482\uFF0C\u5C06\u53D1\u786C\u7684\u8089\u82BD\u626F\u5230\u82B1\u74E3\u4E4B\u5916\u3002\u63A5\u7740\u4E24\u6839\u624B\u6307\u5939\u4F4F\u8089\u82BD\u4E0A\u4E0B\u634B\u52A8\u3002\u4E24\u7C92\u94BB\u77F3\u4F3C\u4E4E\u5728\u8089\u82BD\u5185\u6ED1\u52A8\u4E00\u822C\uFF0C\u523A\u6FC0\u4E07\u5206\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u52C9\u5F3A\u8C03\u5300\u547C\u5438\uFF0C\u4E73\u5934\u5374\u6E10\u6E10\u53D1\u786C\u3002\u867D\u7136\u6CA1\u4EBA\u73A9\u5F04\u4E73\u623F\uFF0C\u4F46\u4E73\u5934\u4E00\u786C\uFF0C\u91CC\u9762\u7684\u94BB\u77F3\u7ACB\u523B\u68F1\u89D2\u5206\u660E\u8D77\u6765\u3002\u5D4C\u7740\u94BB\u77F3\u7684\u4E73\u773C\u6E05\u695A\u5730\u611F\u5E94\u7740\u6BCF\u4E00\u6B21\u5FC3\u8DF3\uFF0C\u94BB\u77F3\u7684\u68F1\u89D2\u5361\u5728\u5A07\u5AE9\u654F\u611F\u7684\u4E73\u773C\u5185\uFF0C\u523A\u6FC0\u4E1D\u6BEB\u4E0D\u4E9A\u4E8E\u88AB\u634B\u5F04\u7684\u9634\u8482\u3002</p><p>\u4E0D\u591A\u65F6\uFF0C\u8089\u7A74\u54C6\u55E6\u7740\u6E85\u51FA\u51E0\u6EF4\u6DB2\u4F53\uFF0C\u4E0B\u4F53\u6108\u53D1\u6E7F\u6DA6\u3002</p><p>\u7B49\u8089\u68D2\u5728\u76F4\u80A0\u5185\u8DF3\u52A8\u7740\u5C04\u51FA\u9633\u7CBE\u3002\u96EA\u5CF0\u795E\u5C3C\u7EC8\u4E8E\u6709\u7247\u523B\u5598\u606F\u3002\u4F46\u8089\u68D2\u521A\u521A\u62D4\u51FA\uFF0C\u5979\u5C31\u542C\u5230\u300C\u5F53\u300D\u7684\u4E00\u58F0\u8106\u54CD\u3002\u53C8\u6709\u4EBA\u4ED8\u51FA\u4E00\u6587\u94B1\u7684\u4EE3\u4EF7\uFF0C\u6765\u73A9\u5F04\u81EA\u5DF1\u7684\u5C41\u80A1\u4E86\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u60F3\u4E86\u7247\u523B\uFF0C\u6BC5\u7136\u8D77\u8EAB\uFF0C\u62FF\u8D77\u51B0\u5757\u5F80\u5730\u4E0A\u4E00\u6454\u3002</p><p>\u300C\u5E94\u8BE5\u4E0D\u4F1A\u6B7B\u5427\u2026\u2026\u300D\u7D2B\u73AB\u8DB4\u5728\u684C\u4E0A\uFF0C\u7EA4\u6307\u62E8\u5F04\u7740\u76D8\u5185\u7684\u51B0\u5757\u3002\u51B0\u5757\u5927\u5C0F\u4E0D\u4E00\uFF0C\u4F46\u95EA\u52A8\u7740\u540C\u6837\u7684\u6676\u5149\u3002\u5979\u62C8\u8D77\u4E00\u5757\u653E\u5728\u53E3\u4E2D\u3002\u51C9\u51C9\u7684\uFF0C\u6DE1\u6DE1\u7684\uFF0C\u6CA1\u6709\u4E00\u70B9\u5473\u9053\u3002</p><p>\u51B0\u5757\u5728\u5BA4\u6E29\u4E0B\u6025\u901F\u878D\u5316\uFF0C\u4E0D\u591A\u65F6\u76D8\u5185\u4FBF\u79EF\u4E86\u4E00\u5C42\u6E05\u6C34\u3002\u7D2B\u73AB\u4E00\u8DFA\u811A\uFF0C\u8DF3\u5230\u69BB\u4E0A\uFF0C\u89E3\u5F00\u7F57\u5E26\u3002</p><p>\u4EB5\u88E4\u892A\u5230\u811A\u8E1D\uFF0C\u73AB\u7470\u4ED9\u5B50\u88F8\u7740\u4E0B\u4F53\uFF0C\u8DEA\u5750\u5728\u69BB\u4E0A\uFF0C\u8170\u80A2\u540E\u4EF0\uFF0C\u7C89\u80CC\u8D34\u4F4F\u7AF9\u69BB\uFF0C\u4E24\u819D\u5F20\u5F00\uFF0C\u9AD8\u9AD8\u633A\u8D77\u4E0B\u4F53\u3002</p><p>\u5149\u6DA6\u7684\u7389\u6237\u7EA2\u767D\u76F8\u95F4\uFF0C\u79C0\u7F8E\u52A8\u4EBA\uFF0C\u7D2B\u73AB\u6478\u7D22\u7740\u6491\u5F00\u8089\u7A74\uFF0C\u7136\u540E\u62FF\u8D77\u4E00\u5757\u788E\u51B0\u3002</p><p>\u67D4\u7F8E\u6D01\u767D\u7684\u624B\u6307\u63D2\u8FDB\u7EA2\u6DA6\u7684\u8089\u7A74\uFF0C\u62D4\u51FA\u65F6\uFF0C\u51B0\u5757\u5DF2\u7ECF\u6D88\u5931\u3002</p><p>\u7D2B\u73AB\u4E00\u9F13\u4F5C\u6C14\uFF0C\u628A\u51B0\u5757\u5168\u90E8\u585E\u5230\u4F53\u5185\uFF0C\u7136\u540E\u5C31\u5F00\u59CB\u540E\u6094\u8D77\u6765\u3002\u5B9E\u5728\u662F\u592A\u51C9\u4E86\u3002</p><p>\u521A\u5F00\u59CB\u8FD8\u4E0D\u89C9\u5F97\uFF0C\u6B64\u65F6\u4ECE\u7A74\u53E3\u5230\u809A\u8110\u4E0B\u65B9\uFF0C\u6574\u6761\u82B1\u5F84\u50CF\u88AB\u51BB\u6210\u4E00\u6761\u51B0\u6D1E\uFF0C\u786C\u786C\u7AD6\u5728\u8179\u5185\uFF0C\u8089\u58C1\u66F4\u662F\u51BB\u5F97\u751F\u75BC\u3002</p><p>\u7D2B\u73AB\u4E24\u624B\u7D27\u7D27\u6342\u4F4F\u80A1\u95F4\uFF0C\u51BB\u5F97\u773C\u6CEA\u4E71\u6EF4\u3002\u4E0D\u4F46\u5B50\u5BAB\uFF0C\u6574\u4E2A\u8179\u8154\u4F3C\u4E4E\u90FD\u88AB\u4E00\u56E2\u5DE8\u5927\u7684\u51B0\u5757\u5145\u6EE1\u3002\u51B0\u5757\u8D8A\u6765\u8D8A\u5927\uFF0C\u50CF\u662F\u8981\u80C0\u7834\u8EAB\u4F53\u4F3C\u7684\u75BC\u75DB\u3002\u5979\u624B\u5FC3\u6B63\u5BF9\u7740\u8089\u7A74\uFF0C\u53EA\u89C9\u4E00\u80A1\u80A1\u68EE\u5BD2\u4E4B\u6C14\u4ECE\u4E00\u5411\u6E29\u6DA6\u7684\u8089\u6D1E\u5185\u6D8C\u51FA\uFF0C\u547C\u5438\u822C\u5728\u624B\u5FC3\u4E2D\u5439\u62C2\u7740\u3002</p><p>\u7D2B\u73AB\u52C9\u5F3A\u62C9\u8D77\u8584\u8584\u7684\u5DFE\u88AB\uFF0C\u5C06\u81EA\u5DF1\u7D27\u7D27\u88F9\u4F4F\u3002\u5634\u5507\u53D1\u767D\uFF0C\u5A07\u8EAF\u8737\u6210\u4E00\u56E2\uFF0C\u4E0D\u505C\u6218\u6817\u3002\u6E05\u4EAE\u7684\u51B0\u6C34\u4ECE\u6307\u7F1D\u4E2D\u7F13\u7F13\u6EA2\u51FA\uFF0C\u5E26\u7740\u5C11\u5973\u4F53\u5185\u7684\u6E29\u5EA6\uFF0C\u6253\u6E7F\u4E86\u8EAB\u4E0A\u8584\u88AB\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u5BD2\u610F\u6E10\u6E10\u892A\u53BB\uFF0C\u7D2B\u73AB\u64E6\u64E6\u6CEA\u6C34\uFF0C\u6342\u7740\u5C0F\u8179\u6068\u6068\u9053\uFF1A\u300C\u4E0D\u8BC6\u76F8\u7684\u5B7D\u79CD\uFF0C\u8FD9\u4E0B\u975E\u8981\u4F60\u7684\u5C0F\u547D\uFF01\u300D</p><p>\u7D2B\u73AB\u8E72\u8EAB\u6392\u51FA\u9634\u9053\u5185\u7684\u51B0\u6C34\uFF0C\u7136\u540E\u82E5\u65E0\u5176\u4E8B\u5730\u6B3E\u6B3E\u7A7F\u4E0A\u4EB5\u88E4\uFF0C\u675F\u597D\u8863\u886B\uFF0C\u5BF9\u7740\u94DC\u955C\u7406\u4E86\u7406\u53D1\u9B13\uFF0C\u9732\u51FA\u4E00\u4E2A\u5A07\u5A9A\u7684\u7B11\u5BB9\uFF0C\u8F6C\u8EAB\u62C9\u5F00\u623F\u95E8\u3002</p><p>\u6155\u5BB9\u9F99\u50CF\u4E00\u5C0A\u51B0\u96D5\uFF0C\u68EE\u7136\u7ACB\u5728\u95E8\u53E3\u3002\u95EA\u70C1\u7684\u773C\u5149\u4E2D\u5145\u6EE1\u4E86\u6124\u6012\u3001\u75DB\u6068\uFF0C\u8FD8\u6709\u4E00\u4E1D\u4F24\u611F\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F60\u662F\u4E0D\u662F\u5F88\u559C\u6B22\u653E\u4E1C\u897F\u8FDB\u53BB\uFF1F\u300D</p><p>\u7D2B\u73AB\u626C\u7740\u8138\uFF0C\u9ED8\u4E0D\u4F5C\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u5589\u7ED3\u52A8\u4E86\u4E00\u4E0B\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u77E5\u4E0D\u77E5\u9053\u8FD9\u6837\u4F1A\u7EC8\u751F\u4E0D\u80B2\uFF01\u300D</p><p>\u300C\u6211\u8FD8\u4E0D\u5230\u5341\u516D\uFF0C\u6211\u4E0D\u60F3\u751F\u5B69\u5B50\u3002\u300D</p><p>\u300C\u556A\uFF01\u300D\u6155\u5BB9\u9F99\u72E0\u72E0\u7ED9\u4E86\u7D2B\u73AB\u4E00\u4E2A\u8033\u5149\u3002</p><p>\u7D2B\u73AB\u79C0\u53D1\u5782\u4E0B\u4E00\u7F15\uFF0C\u5979\u6342\u7740\u8138\u53EB\u9053\uFF1A\u300C\u4F60\u8FD9\u4E2A\u6DF7\u86CB\uFF01\u6211\u4E0D\u8981\u7ED9\u4F60\u751F\u5B69\u5B50\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u9762\u8272\u94C1\u9752\uFF0C\u4E00\u628A\u53C9\u4F4F\u7D2B\u73AB\u7684\u67D4\u9888\u72E0\u72E0\u9053\uFF1A\u300C\u82E5\u975E\u4F60\u4E5F\u59D3\u6155\u5BB9\uFF0C\u8EAB\u4E0A\u6D41\u7740\u4E0E\u6211\u4E00\u6837\u7684\u8840\u6DB2\uFF0C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u4F1A\u50CF\u73B0\u5728\u8FD9\u6837\u81EA\u5728\u5417\uFF1F\u300D\u4ED6\u631F\u8D77\u7D2B\u73AB\uFF0C\u98CE\u4E00\u822C\u63A0\u5230\u6BCD\u4EB2\u7684\u623F\u95F4\uFF0C\u4E00\u811A\u8E22\u5F00\u623F\u95E8\u3002</p><p>\u8427\u4F5B\u5974\u88AB\u7A81\u5982\u5176\u6765\u7684\u5DE8\u54CD\u5413\u5F97\u5A07\u8EAF\u4E00\u98A4\uFF0C\u60CA\u6050\u5730\u770B\u7740\u8138\u8272\u94C1\u9752\u7684\u513F\u5B50\u3002</p><p>\u767D\u5AE9\u7684\u8EAB\u4F53\u5A07\u8273\u5982\u6614\uFF0C\u80A1\u95F4\u7684\u5C3F\u5E03\u5374\u80C0\u6210\u4E00\u56E2\uFF0C\u6C61\u7269\u900F\u8FC7\u539A\u539A\u7684\u68C9\u5E03\uFF0C\u5728\u4E0A\u9762\u5E72\u7ED3\u6210\u4E00\u5708\u5708\u6D45\u9EC4\u7684\u82B1\u7EB9\u3002\u4FEE\u957F\u7684\u53CC\u817F\u5E76\u5728\u4E00\u8D77\uFF0C\u5149\u6D01\u5982\u7389\uFF0C\u7C89\u5AE9\u7684\u817F\u6839\u5374\u6EA2\u51FA\u4E00\u7247\u7A00\u9ECF\u7684\u6D41\u8D28\u3002\u6251\u9F3B\u7684\u6076\u81ED\u6DF7\u7740\u8309\u8389\u82B1\u6CB9\u7684\u751C\u9999\uFF0C\u4EE4\u4EBA\u4F5C\u5455\u3002</p><p>\u300C\u5A18\uFF01\u300D\u7D2B\u73AB\u5931\u58F0\u53EB\u9053\u3002</p><p>\u5728\u5C4E\u5C3F\u4E2D\u6574\u6574\u8EBA\u4E86\u4E24\u5929\uFF0C\u8427\u4F5B\u5974\u7684\u610F\u5FD7\u51E0\u8FD1\u5D29\u6E83\uFF0C\u6B64\u65F6\u81EA\u5DF1\u7684\u7A98\u6001\u843D\u5728\u513F\u5973\u773C\u4E2D\uFF0C\u66F4\u662F\u7F9E\u6127\u96BE\u5F53\u3002</p><p>\u6155\u5BB9\u9F99\u62AC\u624B\u5C06\u7D2B\u73AB\u6254\u5230\u69BB\u4E0A\uFF0C\u7D2B\u73AB\u987E\u4E0D\u5F97\u75BC\u75DB\uFF0C\u7ACB\u523B\u53BB\u89E3\u6BCD\u4EB2\u7684\u5C3F\u5E03\u3002</p><p>\u300C\u4F60\u6562\u89E3\u4E0B\u6765\uFF0C\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u6211\u5C31\u6562\u8BA9\u5A18\u628A\u5B83\u4EEC\u5168\u5403\u4E0B\u53BB\u3002\u300D</p><p>\u7D2B\u73AB\u7684\u624B\u6307\u50F5\u4F4F\u4E86\uFF0C\u5979\u4FCF\u76EE\u542B\u6CEA\uFF0C\u626D\u5934\u9A82\u9053\uFF1A\u300C\u755C\u7272\uFF01\u4F60\u600E\u4E48\u80FD\u8FD9\u6837\u5BF9\u5A18\uFF01\u300D</p><p>\u5979\u5FC3\u75BC\u4E07\u5206\u5730\u4F0F\u5728\u6BCD\u4EB2\u8EAB\u4E0A\u568E\u5555\u75DB\u54ED\uFF0C\u300C\u5A18\u597D\u53EF\u601C\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u54AC\u7740\u5634\u5507\uFF0C\u51C4\u7136\u6CEA\u4E0B\u3002</p><p>\u300C\u5472\u300D\u7684\u4E00\u58F0\uFF0C\u7D2B\u73AB\u7EA2\u886B\u7EFD\u88C2\uFF0C\u9732\u51FA\u96EA\u767D\u7684\u808C\u80A4\u3002</p><p>\u300C\u4F60\u5E72\u4EC0\u4E48\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u628A\u7D2B\u73AB\u4E24\u8155\u634F\u5728\u4E00\u8D77\uFF0C\u7247\u523B\u4FBF\u5C06\u5979\u5265\u5F97\u4E00\u4E1D\u4E0D\u6302\u3002\u7136\u540E\u4E24\u624B\u7528\u529B\u6263\u4F4F\u6ED1\u817B\u7684\u817F\u6839\uFF0C\u731B\u7136\u523A\u5165\u3002</p><p>\u7D2B\u73AB\u706B\u70E7\u822C\u63A9\u4F4F\u79D8\u5904\uFF0C\u4F46\u624B\u81C2\u521A\u6325\u51FA\u4E00\u534A\uFF0C\u5C31\u75DB\u82E6\u7684\u8737\u5230\u80F8\u524D\uFF0C\u4E24\u624B\u62B1\u5728\u4E00\u8D77\uFF0C\u6321\u4F4F\u53E3\u4E2D\u7684\u75DB\u53EB\u3002</p><p>\u8089\u7A74\u5185\u867D\u7136\u8FD8\u6709\u6B8B\u4F59\u7684\u51B0\u6C34\uFF0C\u4F46\u8089\u58C1\u5E76\u672A\u8212\u5C55\uFF0C\u51B5\u4E14\u8FD8\u56E0\u53D7\u51B7\u800C\u6536\u7D27\uFF0C\u62BD\u9001\u95F4\uFF0C\u51E0\u6BD4\u7834\u4F53\u65F6\u7684\u5267\u75DB\u3002</p><p>\u6155\u5BB9\u9F99\u9762\u6C89\u4F3C\u6C34\uFF0C\u9633\u5177\u6240\u53CA\uFF0C\u4EE5\u5F80\u6E7F\u6DA6\u6ED1\u817B\u7684\u8089\u58C1\u6B64\u65F6\u53C8\u51B7\u53C8\u7D27\uFF0C\u51B0\u51C9\u5F97\u8BA9\u4EBA\u75DB\u6068\uFF01</p><p>\u300C\u4F60\u4E0D\u662F\u559C\u6B22\u5F80\u5C44\u91CC\u585E\u4E1C\u897F\u5417\uFF1F\u6211\u64CD\u70C2\u4F60\u7684\u8D31\u5C44\uFF01\u300D\u6155\u5BB9\u9F99\u6012\u53EB\u7740\u6781\u529B\u633A\u5F04\u3002</p><p>\u4E00\u53E3\u6C14\u62BD\u9001\u4E86\u534A\u4E2A\u65F6\u8FB0\uFF0C\u76F4\u5230\u8089\u7A74\u80BF\u80C0\uFF0C\u624D\u62D4\u51FA\u8089\u68D2\u3002</p><p>\u7D2B\u73AB\u88AB\u4ED6\u4E00\u756A\u66B4\u5978\u6345\u5F97\u6C14\u90FD\u5598\u4E0D\u8FC7\u6765\uFF0C\u53EA\u65E0\u529B\u5730\u5F20\u7740\u5C0F\u5634\u3002</p><p>\u8BE5\u6B7B\u7684\u5C0F\u8D31\u4EBA\uFF01\u6211\u8981\u7ED9\u4F60\u4E2A\u6C38\u4E16\u96BE\u5FD8\u7684\u6559\u8BAD\uFF01\u6155\u5BB9\u9F99\u4E24\u81C2\u4E00\u7D27\uFF0C\u5C06\u7D2B\u73AB\u8170\u81C0\u6258\u8D77\uFF0C\u7136\u540E\u628A\u7C89\u817F\u63B0\u5230\u8EAB\u4E0B\uFF0C\u8BA9\u5979\u4E0B\u4F53\u671D\u5929\u655E\u9732\uFF0C\u63A5\u7740\u5DE8\u7269\u76F4\u843D\uFF0C\u731B\u7136\u523A\u5165\u83CA\u857E\u3002</p><p>\u540E\u5EAD\u88AB\u4E00\u53EA\u94C1\u62F3\u6BEB\u4E0D\u7559\u60C5\u5730\u6345\u5165\uFF0C\u5A07\u5AE9\u7684\u809B\u8089\u5E94\u58F0\u6495\u88C2\uFF0C\u80A0\u9053\u88AB\u72E0\u72E0\u62C9\u76F4\u3002</p><p>\u5267\u75DB\u4E0B\u7D2B\u73AB\u54AC\u5F97\u7389\u6307\u9C9C\u8840\u957F\u6D41\u3002</p><p>\u6155\u5BB9\u9F99\u8170\u8EAB\u4E00\u62AC\uFF0C\u5DE8\u7269\u5E26\u7740\u4E00\u56E2\u9C9C\u8840\u4ECE\u809B\u6D1E\u5185\u62D4\u51FA\u3002\u5BC6\u5BC6\u9EBB\u9EBB\u7684\u8089\u523A\u6CBE\u6EE1\u8840\u8FF9\uFF0C\u72F0\u72DE\u65E0\u6BD4\u3002\u4ED6\u7565\u4E00\u505C\u987F\uFF0C\u65CB\u5373\u52A0\u529B\u6C89\u8170\u3002\u9C9C\u8840\u98DE\u6E85\u4E2D\uFF0C\u5DE8\u7269\u5DF2\u5168\u6839\u800C\u5165\u3002</p><p>\u8427\u4F5B\u5974\u5999\u76EE\u5706\u7741\uFF0C\u60F3\u8D77\u81EA\u5DF1\u7684\u906D\u9047\uFF0C\u5FC3\u91CC\u5200\u5272\u822C\u62BD\u75BC\u8D77\u6765\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
