import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_83" tabindex="-1"><a class="header-anchor" href="#_83" aria-hidden="true">#</a> 83</h1><p>\u300C\u53FD\u300D\uFF0C\u8089\u68D2\u79BB\u5F00\u7EFD\u88C2\u7684\u83CA\u809B\u3002\u539F\u6765\u5C0F\u5DE7\u7684\u8089\u7A74\u53D8\u6210\u4E00\u4E2A\u6D51\u5706\u7684\u8840\u6D1E\uFF0C\u6DF7\u7740\u9633\u7CBE\u7684\u9C9C\u8840\u5728\u7834\u788E\u7684\u8089\u58C1\u4E2D\u7F13\u7F13\u5347\u8D77\uFF0C\u76F4\u5230\u79EF\u6EE1\u6EA2\u51FA\u3002</p><p>\u7D2B\u73AB\u8138\u8272\u82CD\u767D\uFF0C\u6C14\u82E5\u6E38\u4E1D\uFF0C\u65E9\u5DF2\u660F\u8FF7\u591A\u65F6\u3002</p><p>\u6155\u5BB9\u9F99\u5C06\u8089\u68D2\u4E0A\u7684\u9C9C\u8840\u62B9\u5728\u6BCD\u4EB2\u7EA2\u5507\u4E0A\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u8D31\u4EBA\uFF0C\u6211\u770B\u4F60\u8FD8\u80FD\u633A\u591A\u4E45\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7389\u5BB9\u60E8\u6DE1\uFF0C\u5446\u5446\u51DD\u89C6\u7740\u7D2B\u73AB\uFF0C\u6D51\u6CA1\u6CE8\u610F\u6155\u5BB9\u9F99\u7684\u8A00\u8BED\u3002\u8BB8\u4E0B\u7684\u4F5B\u613F\u963B\u6B62\u5979\u7684\u547C\u5524\uFF0C\u6BCD\u4EB2\u53EA\u80FD\u9759\u9759\u770B\u7740\u5973\u513F\uFF0C\u7B49\u5F85\u5979\u6162\u6162\u9192\u8F6C\u3002</p><p>\u300C\u4F60\u4EEC\u5728\u5E72\u4EC0\u4E48\u2026\u2026\u300D\u7D2B\u73AB\u6709\u6C14\u65E0\u529B\u7684\u53EB\u9053\u3002\u9192\u6765\u7B2C\u4E00\u773C\uFF0C\u5148\u770B\u5230\u6BCD\u4EB2\u5634\u4E2D\u63D2\u7740\u4E00\u4E2A\u6F0F\u6597\uFF0C\u767D\u6C0F\u59D0\u59B9\u6B63\u4E3E\u7740\u74F6\u5B50\u5F80\u91CC\u704C\u6C34\u3002</p><p>\u300C\u9192\u4E86\uFF1F\u90A3\u5C31\u5148\u7ED9\u4F60\u704C\u5427\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u6446\u624B\uFF0C\u767D\u6C0F\u59D0\u59B9\u653E\u5F00\u8427\u4F5B\u5974\uFF0C\u628A\u7D2B\u73AB\u6309\u6210\u8DEA\u4F0F\u7684\u59FF\u52BF\u3002\u80FD\u4EB2\u624B\u6298\u78E8\u73AB\u7470\u4ED9\u5B50\uFF0C\u4E24\u5973\u5FC3\u91CC\u90FD\u4E50\u7FFB\u4E86\u5929\uFF0C\u4F46\u8138\u4E0A\u8FD8\u5E26\u7740\u606D\u656C\u7684\u7B11\u5BB9\u3002</p><p>\u957F\u65F6\u95F4\u7684\u8179\u6CFB\u4F7F\u8427\u4F5B\u5974\u6709\u4E9B\u8131\u6C34\uFF0C\u5A07\u5AE9\u7684\u808C\u80A4\u7565\u663E\u67AF\u840E\uFF0C\u4F46\u5C0F\u8179\u5374\u5706\u9F13\u9F13\u6DA8\u6210\u7403\u72B6\u3002\u7D2B\u73AB\u660F\u8FF7\u7684\u65F6\u5019\uFF0C\u6155\u5BB9\u9F99\u5343\u65B9\u767E\u8BA1\u903C\u5979\u5F00\u53E3\uFF0C\u5979\u59CB\u7EC8\u4E00\u8A00\u4E0D\u53D1\uFF0C\u6155\u5BB9\u9F99\u53C8\u6C14\u53C8\u6068\uFF0C\u4E5F\u4E0D\u7BA1\u6BCD\u4EB2\u8FD8\u6000\u7740\u8EAB\u5B55\uFF0C\u7D22\u6027\u704C\u8D77\u4E86\u51C9\u6C34\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u63B0\u5F00\u73AB\u7470\u4ED9\u5B50\u6CBE\u8840\u7684\u96EA\u81C0\uFF0C\u7D2B\u73AB\u809B\u4E2D\u4E00\u75BC\uFF0C\u6F0F\u6597\u7684\u94C1\u5236\u5C16\u5634\u5DF2\u63D2\u5165\u8179\u5185\u3002</p><p>\u767D\u7389\u83BA\u4E3E\u74F6\u6B32\u5012\uFF0C\u6155\u5BB9\u9F99\u51B7\u51B7\u9053\uFF1A\u300C\u90A3\u4E00\u74F6\uFF01\u300D</p><p>\u51B0\u51B7\u7684\u6DB2\u4F53\u6D41\u5165\u76F4\u80A0\uFF0C\u4F24\u53E3\u5200\u5272\u822C\u970D\u970D\u4F5C\u75BC\u3002\u7B49\u6F0F\u6597\u62D4\u51FA\uFF0C\u83CA\u6D1E\u4E2D\u8840\u6C34\u6A2A\u6EA2\u3002</p><p>\u7D2B\u73AB\u5BF9\u6155\u5BB9\u9F99\u8FD9\u6837\u73A9\u5F04\u81EA\u5DF1\u5207\u9F7F\u6DF1\u6068\uFF0C\u4F46\u5979\u4E0D\u77E5\u9053\uFF0C\u90A3\u74F6\u6C34\u4E2D\u662F\u63BA\u8FC7\u4F24\u836F\u7684\u3002</p><p>\u6155\u5BB9\u9F99\u4E5F\u4E0D\u89E3\u91CA\uFF0C\u4E00\u6446\u624B\uFF0C\u767D\u6C0F\u59D0\u59B9\u5F84\u76F4\u5C06\u6CBE\u7740\u7D2B\u73AB\u8840\u8FF9\u6C61\u7269\u7684\u6F0F\u6597\u63D2\u8FDB\u8427\u4F5B\u5974\u5634\u4E2D\uFF0C\u7EE7\u7EED\u704C\u5165\u51C9\u6C34\u3002</p><p>\u7D2B\u73AB\u8F6F\u7EF5\u7EF5\u5367\u5728\u69BB\u89D2\uFF0C\u7EDD\u671B\u5730\u95ED\u4E0A\u773C\u3002</p><p>\u5F85\u51C9\u6C34\u4ECE\u6F0F\u6597\u4E2D\u6EA2\u51FA\uFF0C\u518D\u65E0\u6CD5\u704C\u5165\u4E1D\u6BEB\uFF0C\u6155\u5BB9\u9F99\u4F38\u624B\u5728\u7F8E\u5987\u8179\u4E0A\u4E00\u6309\u3002\u6D51\u5706\u7684\u5C0F\u8179\u5E94\u624B\u800C\u9677\uFF0C\u76AE\u7403\u822C\u762A\u4E86\u4E0B\u53BB\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u5C3F\u5E03\u9707\u52A8\u7740\u9F13\u80C0\u8D77\u6765\uFF0C\u6C61\u7269\u4ECE\u96EA\u767D\u7684\u817F\u7F1D\u95F4\u5192\u51FA\u9ECF\u9ECF\u4E00\u7247\u3002</p><p>\u8427\u4F5B\u5974\u67D4\u9888\u62F1\u8D77\uFF0C\u5634\u89D2\u75DB\u82E6\u5730\u6EA2\u51FA\u6E05\u6C34\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u7740\u5429\u5490\u9053\uFF1A\u300C\u518D\u704C\u4E00\u74F6\u3002\u300D</p><p>\u767D\u7389\u83BA\u773C\u73E0\u4E00\u8F6C\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u7980\u5BAB\u4E3B\uFF0C\u5929\u6C14\u708E\u70ED\uFF0C\u82E5\u592B\u4EBA\u4E0B\u4F53\u751F\u86C6\u600E\u4E48\u662F\u597D\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u9887\u4E3A\u6B23\u8D4F\u5730\u770B\u4E86\u8FD9\u4E2A\u673A\u7075\u7684\u5974\u5A62\u4E00\u773C\uFF0C\u300C\u592B\u4EBA\u751F\u5C31\u8363\u534E\u5C0A\u8D35\uFF0C\u4E0D\u4E00\u5B9A\u77E5\u9053\u4EC0\u4E48\u662F\u86C6\u5462\u2026\u2026\u4F60\u53BB\u7ED9\u592B\u4EBA\u4ED4\u7EC6\u8BB2\u8BB2\u3002\u300D</p><p>\u767D\u7389\u83BA\u629A\u6478\u7740\u8427\u4F5B\u5974\u7684\u7389\u817F\uFF0C\u7ED8\u58F0\u7ED8\u8272\u5730\u8BF4\uFF1A\u300C\u8FD9\u4E9B\u810F\u4E1C\u897F\u653E\u5F97\u4E45\u4E86\uFF0C\u91CC\u9762\u4F1A\u957F\u51FA\u4E00\u5806\u767D\u767D\u7684\u5C0F\u866B\u5B50\uFF0C\u592B\u4EBA\u4E0D\u5FC5\u6015\uFF0C\u5B83\u4EEC\u4E0D\u4F1A\u54AC\u4EBA\uFF0C\u5F88\u5C0F\u7684\uFF0C\u6CA1\u5934\u6CA1\u5C3E\u4E5F\u6CA1\u6709\u9AA8\u5934\uFF0C\u53EA\u4F1A\u5230\u5904\u4E71\u94BB\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u8138\u4E0A\u8840\u8272\u5C3D\u892A\uFF0C\u7D27\u95ED\u7684\u53CC\u773C\u776B\u6BDB\u5FAE\u98A4\u3002</p><p>\u7D2B\u73AB\u89C1\u6BCD\u4EB2\u5413\u5F97\u5C4F\u4F4F\u547C\u5438\uFF0C\u7981\u4E0D\u4F4F\u54ED\u9053\uFF1A\u300C\u4F60\u7A76\u7ADF\u8981\u600E\u4E48\u6837\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u770B\u7740\u4E0E\u81EA\u5DF1\u8840\u8109\u76F8\u8FDE\u7684\u6BCD\u5973\u4FE9\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u662F\u6211\u7684\u6B63\u59BB\uFF0C\u9664\u4E86\u4F3A\u5949\u6211\u4E4B\u5916\u8FD8\u8981\u7ED9\u6211\u751F\u513F\u80B2\u5973\u3002\u5979\u662F\u6211\u7EB3\u7684\u4F8D\u59BE\uFF0C\u65E0\u8BBA\u505A\u4EC0\u4E48\uFF0C\u90FD\u8981\u8BA9\u6211\u5F00\u5FC3\u3002\u300D</p><p>\u7D2B\u73AB\u8FDE\u8FDE\u70B9\u5934\uFF0C\u300C\u59B9\u59B9\u77E5\u9053\u4E86\uFF0C\u77E5\u9053\u4E86\u2026\u2026\u300D</p><p>\u300C\u77E5\u9053\u4F60\u8FD8\u6562\u5815\u80CE\uFF01\u300D\u6155\u5BB9\u9F99\u5486\u54EE\u9053\u3002</p><p>\u300C\u6211\u77E5\u9053\u9519\u4E86\uFF0C\u4EE5\u540E\u518D\u4E5F\u4E0D\u6562\u4E86\u2026\u2026\u300D\u7D2B\u73AB\u6CE3\u4E0D\u6210\u58F0\u3002</p><p>\u8FD9\u79CD\u627F\u8BFA\u6155\u5BB9\u9F99\u5DF2\u7ECF\u542C\u8FC7\u592A\u591A\uFF0C\u4ED6\u51B7\u54FC\u4E00\u58F0\uFF0C\u62AC\u773C\u76EF\u7740\u8427\u4F5B\u5974\u3002</p><p>\u767D\u7389\u83BA\u5FAA\u5FAA\u5584\u8BF1\u5730\u8BF4\uFF1A\u300C\u90A3\u4E9B\u5C0F\u866B\u5B50\u4F1A\u8D8A\u6765\u8D8A\u591A\uFF0C\u5B83\u4EEC\u722C\u5440\u722C\u5440\uFF0C\u6709\u6D1E\u5C31\u4F1A\u94BB\u8FDB\u53BB\uFF0C\u5728\u91CC\u9762\u722C\u6765\u722C\u53BB\u8D8A\u957F\u8D8A\u5927\u2026\u2026\u770B\uFF01\u300D\u5979\u7A81\u7136\u53EB\u4E86\u4E00\u58F0\uFF0C\u8427\u4F5B\u5974\u5A07\u8EAF\u987F\u65F6\u4E00\u9707\u3002</p><p>\u767D\u7389\u83BA\u624B\u6307\u5728\u8427\u4F5B\u5974\u8179\u4E0A\u8F7B\u8F7B\u4E00\u62C2\uFF0C\u7C89\u5AE9\u7684\u808C\u80A4\u7ACB\u65F6\u6CDB\u51FA\u4E00\u5C42\u7EC6\u5BC6\u7684\u8089\u7C92\u3002</p><p>\u5979\u8D34\u5728\u8427\u4F5B\u5974\u8033\u8FB9\u8BF4\u9053\uFF1A\u300C\u592B\u4EBA\u2026\u2026\u5B83\u4EEC\u6B63\u5728\u60A8\u817F\u91CC\u9762\u8815\u52A8\uFF0C\u5F80\u8EAB\u4F53\u91CC\u9762\u94BB\u5462\u2026\u2026\u300D</p><p>\u7F8E\u5987\u547C\u5438\u6E10\u6E10\u6025\u4FC3\uFF0C\u88AB\u6C61\u7269\u6D78\u6CE1\u4E24\u65E5\u7684\u4E0B\u4F53\u523A\u75D2\u96BE\u5F53\uFF0C\u6D3B\u50CF\u6709\u4E00\u7A9D\u5BC6\u5BC6\u9EBB\u9EBB\u7684\u767D\u8272\u5C0F\u866B\u5728\u91CC\u9762\u4E71\u62F1\u2026\u2026</p><p>\u300C\u5440\uFF01\u300D\u5979\u5C16\u53EB\u9053\uFF1A\u300C\u5FEB\u89E3\u5F00\uFF0C\u5FEB\u89E3\u5F00\u554A\u2026\u2026\u4F5B\u7956\u2026\u2026\u300D\u8427\u4F5B\u5974\u558A\u53EB\u7740\u8170\u81C0\u62FC\u547D\u633A\u52A8\uFF0C\u60C5\u6025\u4E4B\u4E0B\uFF0C\u518D\u987E\u4E0D\u5F97\u81EA\u5DF1\u7684\u4F5B\u613F\u3002</p><p>\u767D\u7389\u83BA\u5C0F\u58F0\u9053\uFF1A\u300C\u4F60\u8EAB\u4E0A\u53C8\u810F\u53C8\u81ED\uFF0C\u8FD8\u662F\u6000\u7740\u5B69\u5B50\u7684\u4E0D\u6D01\u4E4B\u8EAB\uFF0C\u96BE\u9053\u83E9\u8428\u8FD8\u4F1A\u4FDD\u4F51\u4F60\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u50F5\u4E86\u7247\u523B\uFF0C\u60F3\u5230\u81EA\u5DF1\u6000\u7740\u7684\u80CE\u513F\u8FD8\u662F\u4EB2\u5B50\u7684\u5B7D\u79CD\uFF0C\u4E0D\u7531\u51C4\u7136\u4E00\u7B11\uFF0C\u68A6\u5453\u822C\u5462\u54DD\u9053\uFF1A\u300C\u8EAB\u5B50\u8FD9\u4E48\u810F\u2026\u2026\u4F5B\u7956\u4E0D\u8981\u6211\u4E86\u2026\u2026\u300D\u6CEA\u773C\u6726\u80E7\u4E2D\uFF0C\u4F3C\u4E4E\u770B\u5230\u89C2\u97F3\u6148\u7965\u7684\u9762\u5BB9\u8F7B\u70DF\u822C\u6E10\u6E10\u6DE1\u5316\uFF0C\u300C\u83E9\u8428\u2026\u2026\u300D</p><p>\u300C\u6211\u8981\u4F60\u3002\u300D\u4E00\u4E2A\u58F0\u97F3\u6E29\u5B58\u5730\u8BF4\u9053\uFF1A\u300C\u65E0\u8BBA\u4F60\u53D8\u6210\u4EC0\u4E48\u6837\u5B50\uFF0C\u6211\u90FD\u4E0D\u4F1A\u4E22\u4E0B\u4F60\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5C06\u8427\u4F5B\u5974\u62B1\u5728\u6000\u4E2D\uFF0C\u4E00\u8FB9\u7231\u629A\uFF0C\u4E00\u8FB9\u4F4E\u58F0\u9053\uFF1A\u300C\u6211\u4F1A\u6C38\u8FDC\u7231\u62A4\u4F60\uFF0C\u5FC3\u75BC\u4F60\uFF0C\u628A\u4F60\u5F53\u6210\u6700\u5FC3\u7231\u7684\u5973\u4EBA\u6765\u73CD\u60DC\uFF0C\u53EA\u8981\u4F60\u4E5F\u4E00\u6837\u7231\u6211\u2026\u2026\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u50CF\u5728\u6CA1\u9876\u7684\u6CE2\u6D9B\u4E2D\u63E1\u5230\u4E00\u53EA\u575A\u5B9A\u7684\u624B\u81C2\uFF0C\u8427\u4F5B\u5974\u53C8\u662F\u832B\u7136\u53C8\u662F\u611F\u6FC0\uFF0C\u60C5\u4E0D\u81EA\u7981\u5730\u8F7B\u8F7B\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u7F8E\u5987\u5507\u89D2\u4E00\u543B\uFF0C\u300C\u53EB\u58F0\u54E5\u54E5\u2026\u2026\u300D</p><p>\u300C\u2026\u2026\u54E5\u54E5\u2026\u2026\u300D\u8427\u4F5B\u5974\u6EE1\u8138\u7EA2\u6655\uFF0C\u5A07\u7F9E\u65E0\u9650\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u4E00\u8361\uFF0C\u65CB\u5373\u53C8\u60F3\u8D77\u5F53\u65E5\u5979\u628A\u81EA\u5DF1\u9519\u8BA4\u6210\u7236\u4EB2\u7684\u4E8B\u6765\uFF0C\u4E8E\u662F\u8138\u4E00\u677F\uFF0C\u300C\u4F60\u8FD9\u4F1A\u513F\u60F3\u7684\u662F\u8C01\u5462\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u4E00\u6014\u62AC\u8D77\u81FB\u9996\uFF0C\u5982\u6C34\u7684\u773C\u6CE2\u6EE1\u662F\u4E0D\u89E3\u3002</p><p>\u300C\u662F\u6211\u6155\u5BB9\u9F99\uFF0C\u8FD8\u662F\u6B7B\u9B3C\u6155\u5BB9\u7941\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u987F\u65F6\u610F\u8BC6\u5230\u9762\u524D\u662F\u81EA\u5DF1\u7684\u4EB2\u751F\u513F\u5B50\uFF0C\u773C\u7736\u500F\u5FFD\u5659\u6EE1\u6CEA\u6C34\u3002</p><p>\u300C\u8BF4\uFF01\u300D</p><p>\u5389\u559D\u4F7F\u8427\u4F5B\u5974\u5A07\u8EAF\u5FAE\u98A4\uFF0C\u5979\u5782\u4E0B\u5934\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u662F\u9F99\u54E5\u54E5\u2026\u2026\u300D\u8BF4\u7740\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u6389\u5728\u6D01\u767D\u7684\u80F8\u53E3\u3002</p><p>\u300C\u6155\u5BB9\u7941\u662F\u4EC0\u4E48\u4E1C\u897F\uFF1F\u300D</p><p>\u300C\u2026\u2026\u662F\u5974\u5BB6\u4EE5\u524D\u7684\u4E08\u592B\u2026\u2026\u300D</p><p>\u300C\u5C41\uFF01\u4ED6\u662F\u4E2A\u6DF7\u86CB\uFF01\u8BF4\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u54FD\u54BD\u7740\u8BF4\uFF1A\u300C\u4ED6\u662F\u4E2A\u6DF7\u86CB\u2026\u2026\u300D</p><p>\u300C\u4ED6\u65E2\u7136\u5A36\u4E86\u4F60\uFF0C\u53C8\u52FE\u642D\u522B\u7684\u5973\u4EBA\uFF0C\u7ED3\u679C\u8001\u5A46\u6CA6\u843D\u4E3A\u62BC\u5BE8\u592B\u4EBA\uFF0C\u513F\u5B50\u88AB\u4EBA\u63B3\u8D70\uFF0C\u5403\u5C3D\u82E6\u5934\uFF01\u4F60\u7ED9\u6211\u9A82\uFF01\u300D</p><p>\u7F8E\u5987\u54ED\u5F97\u68A8\u82B1\u5E26\u96E8\uFF0C\u58F0\u6CEA\u4FF1\u4E0B\u5730\u8BF4\u54ED\u8BC9\u9053\uFF1A\u300C\u4ED6\u629B\u4E0B\u5974\u5BB6\uFF0C\u604B\u4E0A\u522B\u7684\u5973\u4EBA\u2026\u2026\u53EA\u987E\u4EAB\u4E50\uFF0C\u4E0D\u7406\u671D\u653F\uFF0C\u4E0D\u77E5\u9053\u6709\u4EBA\u8C0B\u53CD\u2026\u2026\u7ED3\u679C\u56FD\u7834\u5BB6\u4EA1\uFF0C\u59BB\u79BB\u5B50\u6563\uFF0C\u6211\u548C\u73AB\u513F\u6D41\u843D\u6C5F\u6E56\uFF0C\u9F99\u513F\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u53D7\u5C3D\u6298\u78E8\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u53C8\u662F\u75DB\u6068\u53C8\u662F\u5FEB\u610F\uFF0C\u4E00\u628A\u5C06\u5C3F\u5E03\u6252\u5230\u81C0\u4E0B\u3002</p><p>\u5C3F\u5E03\u5916\u5C42\u5DF2\u7ECF\u5E72\u7ED3\u53D1\u786C\uFF0C\u4E00\u626F\u4E4B\u4E0B\u786C\u786C\u7AD6\u5728\u80A1\u7F1D\u5185\uFF0C\u4ECD\u4FDD\u6301\u7740\u5706\u81C0\u7684\u5F62\u72B6\u3002</p><p>\u5C3F\u5E03\u5185\u6EE1\u662F\u9ECF\u7A20\u7684\u79FD\u7269\uFF0C\u5728\u767D\u5AE9\u7684\u96EA\u81C0\u4E0A\u56DB\u5904\u6D41\u52A8\u3002\u80A5\u81C0\u4E4B\u95F4\uFF0C\u6D45\u9EC4\u8272\u7684\u6D41\u8D28\u6CBF\u7740\u66F2\u7EBF\u4F18\u7F8E\u7684\u81C0\u7F1D\uFF0C\u4E00\u76F4\u6DCC\u5230\u9634\u6237\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u987E\u79FD\u7269\u6563\u53D1\u51FA\u7684\u6076\u81ED\uFF0C\u5341\u6307\u5982\u94A9\uFF0C\u72E0\u72E0\u6293\u4F4F\u4E24\u53EA\u6D51\u5706\u7684\u7F8E\u81C0\u671D\u4E24\u4FA7\u4E00\u63B0\u3002\u6E7F\u7C98\u7684\u6C61\u7269\u6EDA\u6EDA\u800C\u843D\uFF0C\u9690\u7EA6\u9732\u51FA\u5AE9\u7EA2\u7684\u83CA\u6D1E\u3002\u56E0\u711A\u60C5\u818F\u800C\u53D8\u5F97\u80A5\u5927\u7684\u809B\u7AA6\u7FFB\u5377\u9F13\u8D77\uFF0C\u4E0D\u65F6\u56E0\u8179\u6CFB\u800C\u53D1\u51FA\u300C\u5657\u53FD\u5657\u53FD\u300D\u7684\u5FAE\u54CD\u3002</p><p>\u6B64\u65F6\u7F8E\u5987\u80A0\u9053\u5185\u5DF2\u6CA1\u6709\u5BBF\u4FBF\uFF0C\u7EB5\u7136\u8179\u6CFB\u4E0D\u6B62\uFF0C\u6392\u51FA\u7684\u4E5F\u53EA\u6709\u521A\u624D\u704C\u5165\u7684\u51C9\u6C34\uFF0C\u6DF7\u7740\u5012\u704C\u809B\u5185\u7684\u6C61\u7269\uFF0C\u53C8\u7A00\u53C8\u810F\uFF0C\u5206\u4E0D\u6E05\u662F\u5C4E\u662F\u5C3F\u3002</p><p>\u7EDD\u7F8E\u7684\u5706\u81C0\u4E0E\u4EE4\u4EBA\u4F5C\u5455\u7684\u80AE\u810F\u53CD\u800C\u6FC0\u8D77\u4E86\u6155\u5BB9\u9F99\u7684\u517D\u6027\uFF0C\u521A\u5C04\u8FC7\u7CBE\u7684\u8089\u68D2\u7ACB\u523B\u575A\u786C\u5982\u94C1\u3002\u4ED6\u5C06\u7F8E\u5987\u4FEF\u8EAB\u6309\u5728\u69BB\u4E0A\uFF0C\u8089\u68D2\u5BF9\u7740\u6392\u6CC4\u4E0D\u6B62\u7684\u83CA\u6D1E\u7528\u529B\u6345\u5165\u3002\u809B\u5185\u7684\u6C61\u7269\u7A00\u5982\u4F53\u6DB2\uFF0C\u9F9F\u5934\u6BEB\u4E0D\u8D39\u529B\u4FBF\u94BB\u5165\u591A\u6C41\u7684\u8089\u6D1E\u5185\u3002\u5DE8\u9633\u6CA1\u5165\u5904\uFF0C\u6C61\u7269\u98DE\u6E85\u3002</p><p>\u8427\u4F5B\u5974\u5DF2\u7ECF\u88AB\u65E0\u6CD5\u63A7\u5236\u7684\u8179\u6CFB\u5F04\u5F97\u80A0\u9053\u9178\u75BC\uFF0C\u6B64\u65F6\u5DE8\u9633\u8FDB\u5165\uFF0C\u53CD\u800C\u6709\u79CD\u5F02\u6837\u7684\u5FEB\u611F\uFF0C\u706B\u70ED\u7684\u8089\u68D2\u4EFF\u4F5B\u662F\u5728\u6309\u6469\u9178\u56F0\u7684\u76F4\u80A0\u3002\u90A3\u79CD\u6709\u529B\u800C\u575A\u5F3A\u5145\u5B9E\u611F\uFF0C\u4F7F\u5979\u5A07\u547B\u51FA\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u5728\u8427\u4F5B\u5974\u540E\u5EAD\u72E0\u72E0\u63D2\u9001\uFF0C\u559D\u9053\uFF1A\u300C\u63A5\u7740\u8BF4\uFF01\u300D</p><p>\u300C\u4ED6\u76EE\u5149\u77ED\u6D45\u2026\u2026\u300D</p><p>\u300C\u8C01\uFF01\u300D\u6155\u5BB9\u9F99\u72E0\u72E0\u4E00\u9876\u3002</p><p>\u8427\u4F5B\u5974\u7EC6\u7709\u62E7\u7D27\uFF0C\u5A07\u557C\u9053\uFF1A\u300C\u6155\u5BB9\u7941\u3002\u6155\u5BB9\u7941\u76EE\u5149\u77ED\u6D45\uFF0C\u554A\uFF01\u65E0\u5FB7\u65E0\u80FD\uFF0C\u8F9C\u8D1F\u4E86\u6211\u4EEC\u2026\u2026\u5662\u2026\u2026\u6BCD\u5B50\u2026\u2026\u5440\u5440\u2026\u2026\u300D</p><p>\u58F0\u97F3\u5A49\u8F6C\u5982\u6B4C\uFF0C\u51C4\u7F8E\u52A8\u4EBA\uFF0C\u4F46\u6155\u5BB9\u9F99\u5374\u4E0D\u6EE1\u610F\u3002\u4ED6\u5389\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u7941\u662F\u4E2A\u7B28\u86CB\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u4F4E\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u7941\u662F\u4E2A\u7B28\u86CB\u2026\u2026\u300D</p><p>\u300C\u5927\u70B9\u58F0\uFF01\u6155\u5BB9\u7941\u662F\u4E2A\u50BB\u5C4C\uFF0C\u8FDE\u6211\u7684\u9E21\u5DF4\u90FD\u4E0D\u5982\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u54ED\u53EB\u9053\uFF1A\u300C\u6155\u5BB9\u7941\u662F\u4E2A\u50BB\u5C4C\uFF0C\u8FDE\u9F99\u54E5\u54E5\u7684\u9E21\u5DF4\u90FD\u4E0D\u5982\uFF01\u300D</p><p>\u9AD8\u8D35\u7F8E\u5987\u904D\u4F53\u6C61\u7269\uFF0C\u88AB\u4EBA\u6309\u7740\u5C41\u80A1\u731B\u64CD\u5C41\u773C\uFF0C\u8FD8\u7528\u5A07\u7F8E\u7684\u58F0\u97F3\u9A82\u51FA\u8FD9\u6837\u7C97\u4FD7\u7684\u8BDD\u8A00\uFF0C\u6155\u5BB9\u9F99\u4E0D\u7981\u54C8\u54C8\u5927\u7B11\uFF0C\u5E72\u5F97\u6108\u53D1\u7528\u529B\u3002</p><p>\u8089\u68D2\u8D77\u843D\u95F4\uFF0C\u79FD\u7269\u56DB\u6E85\u3002\u8427\u4F5B\u5974\u7684\u54ED\u6CE3\u6E10\u6E10\u53D8\u6210\u67D4\u5A9A\u7684\u6D6A\u53EB\u3002\u5979\u4E91\u9AFB\u6563\u4E71\uFF0C\u661F\u7738\u5982\u9189\uFF0C\u7EA4\u8170\u7F8E\u81C0\u6D82\u6EE1\u6C61\u79FD\uFF0C\u751A\u81F3\u767D\u51C0\u7684\u7389\u817F\u4E5F\u6CBE\u4E0A\u81EA\u5DF1\u7684\u5C4E\u5C3F\u3002</p><p>\u6155\u5BB9\u9F99\u4FCA\u76EE\u8840\u7EA2\uFF0C\u9AD8\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u7941\u7684\u9E21\u5DF4\u6709\u6211\u7684\u5389\u5BB3\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5931\u795E\u5730\u53EB\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\u7684\u9E21\u5DF4\u53C8\u7C97\u53C8\u5927\uFF0C\u6BD4\u6155\u5BB9\u7941\u5389\u5BB3\u2014\u2014\u5440\u2026\u2026\u300D</p><p>\u300C\u723D\u4E0D\u723D\uFF01\u300D</p><p>\u300C\u9F99\u54E5\u54E5\u64CD\u5F97\u4EBA\u5BB6\u597D\u8212\u670D\u2026\u2026\u5A18\u7684\u5C41\u773C\u8981\u88AB\u6345\u7A7F\u5566\u2026\u2026\u300D\u7F8E\u5987\u75AF\u72C2\u5730\u558A\u53EB\u7740\uFF0C</p><p>\u300C\u5A18\u6700\u7231\u9F99\u54E5\u54E5\u7684\u5927\u9E21\u5DF4\u2026\u2026\u54CE\u5440\u2026\u2026\u6700\u559C\u6B22\u54E5\u54E5\u64CD\u5974\u5BB6\u5C41\u773C\u2026\u2026\u54E5\u54E5\u64CD\u6B7B\u5A18\u4E86</p><p>\u2026\u2026\u597D\u723D\u2026\u2026\u5A18\u7684\u8EAB\u5B50\u2026\u2026\u90FD\u662F\u54E5\u54E5\u7684\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u8EBA\u5728\u4E00\u89D2\uFF0C\u6D51\u8EAB\u7684\u529B\u6C14\u4F3C\u4E4E\u90FD\u6D88\u5931\u4E86\uFF0C\u53EA\u9759\u9759\u770B\u7740\u6BCD\u4EB2\uFF0C\u8111\u4E2D\u4E00\u7247\u7A7A\u767D\u3002</p><p>\u90A3\u4E2A\u66FE\u7ECF\u534E\u7F8E\u9AD8\u8D35\uFF0C\u88AB\u4EBA\u79F0\u4F5C\u300C\u767E\u82B1\u89C2\u97F3\u300D\u7684\u6BCD\u4EB2\uFF0C\u5982\u4ECA\u5374\u5F62\u540C\u79BD\u517D\uFF0C\u5728\u6EE1\u69BB\u5C4E\u5C3F\u4E2D\u4E0E\u4EB2\u751F\u513F\u5B50\u75AF\u72C2\u5730\u4EA4\u5408\uFF0C\u518D\u6CA1\u6709\u66FE\u7ECF\u7684\u8EAB\u4EFD\u548C\u5730\u4F4D\uFF0C\u53EA\u5269\u4E0B\u8D64\u88F8\u88F8\u7684\u8089\u4F53\u548C\u6B32\u671B\u3002</p><p>\u5FC3\u50CF\u5728\u6DF1\u4E0D\u89C1\u5E95\u7684\u5BD2\u6F02\u4E4B\u4E2D\u98DE\u901F\u4E0B\u6C89\uFF0C\u8D8A\u6765\u8D8A\u51C9\u3002\u7D2B\u73AB\u9EEF\u7136\u5408\u4E0A\u773C\u775B\u3002\u660F\u9EC4\u7684\u5929\u8272\u4E2D\uFF0C\u80A1\u95F4\u90A3\u7247\u6BB7\u7EA2\u7684\u8840\u8FF9\uFF0C\u6108\u53D1\u593A\u76EE\u3002</p>',78);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
