import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_84" tabindex="-1"><a class="header-anchor" href="#_84" aria-hidden="true">#</a> 84</h1><p>\u6728\u68CD\u7B14\u76F4\u63D2\u5165\u8089\u7A74\u5185\u6405\u52A8\u8D77\u6765\uFF0C\u6D53\u7CBE\u6C69\u6C69\u800C\u51FA\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u77E5\u9053\u81EA\u5DF1\u8089\u7A74\u53C8\u88AB\u7CBE\u6DB2\u704C\u6EE1\uFF0C\u90A3\u4EBA\u6B63\u62FF\u4E13\u7528\u7684\u6728\u68CD\u6765\u6392\u51FA\u90A3\u4E9B\u6C61\u7269\u3002</p><p>\u6405\u4E86\u7247\u523B\uFF0C\u5927\u6982\u662F\u5DEE\u4E0D\u591A\u4E86\uFF0C\u6728\u68CD\u5F53\u7684\u4E22\u5728\u5730\u4E0A\uFF0C\u9633\u5177\u65CB\u5373\u63D2\u8FDB\u4F53\u5185\u3002</p><p>\u4E0D\u8DB3\u4E24\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u7A76\u7ADF\u63A5\u7EB3\u8FC7\u591A\u5C11\u8089\u68D2\uFF0C\u795E\u5C3C\u5DF2\u7ECF\u6570\u4E0D\u6E05\u4E86\u3002\u5927\u6982\u8FD9\u91CC\u6BCF\u4E2A\u4EBA\u90FD\u64CD\u8FC7\u81EA\u5DF1\u5427\u3002</p><p>\u9634\u8482\u88AB\u4EBA\u63EA\u8D77\uFF0C\u90A3\u4EBA\u634F\u7740\u5AE9\u8089\u628A\u94BB\u77F3\u634B\u5230\u4E00\u8D77\uFF0C\u53C8\u7528\u6307\u7532\u628A\u5B83\u4EEC\u91CD\u65B0\u5206\u5F00\u3002</p><p>\u94BB\u77F3\u5728\u5AE9\u8089\u4E2D\u6ED1\u6765\u6ED1\u53BB\uFF0C\u73A9\u5F97\u4E0D\u4EA6\u4E50\u4E4E\u3002\u8FD9\u5DF2\u7ECF\u6210\u4E3A\u4ED6\u4EEC\u7684\u4E00\u4E2A\u6E38\u620F\uFF0C\u56E0\u4E3A\u7528\u4E0D\u4E86\u51E0\u4E0B\uFF0C\u5927\u5C41\u80A1\u4E2D\u5C31\u4F1A\u55B7\u51FA\u9634\u7CBE\u3002\u8FD9\u662F\u6781\u7AEF\u75DB\u82E6\u7684\u9AD8\u6F6E\u3002</p><p>\u300C\u53F6\u62A4\u6CD5\uFF0C\u5BAB\u4E3B\u6709\u4FE1\u3002\u300D</p><p>\u53F6\u884C\u5357\u63A5\u8FC7\u4E66\u4FE1\u770B\u4E86\u4E24\u884C\uFF0C\u987F\u65F6\u7709\u5F00\u773C\u7B11\uFF0C\u8FDE\u8FDE\u70B9\u5934\uFF0C\u770B\u5230\u540E\u9762\u8138\u987F\u65F6\u57AE\u4E86\u4E0B\u53BB\u3002\u8FD9\u4E2A\u5C0F\u4E2B\u5934\uFF0C\u771F\u662F\u80E1\u95F9\uFF01</p><p>\u4ED6\u628A\u4FE1\u5F80\u6848\u4E0A\u4E00\u62CD\uFF0C\u6C14\u51B2\u51B2\u5728\u5BA4\u5185\u8D70\u4E86\u4E24\u5708\uFF0C\u6700\u540E\u957F\u53F9\u4E00\u58F0\uFF0C\u5750\u4E0B\u6765\u7814\u58A8\u91AE\u7B14\uFF0C\u4ED4\u7EC6\u5199\u4E0B\u8C03\u8865\u7684\u836F\u65B9\u3002</p><p>\u5199\u5B8C\u836F\u65B9\uFF0C\u53F6\u884C\u5357\u659F\u914C\u826F\u4E45\uFF0C\u63D0\u7B14\u5199\u4E0B\uFF1A\u6559\u4E2D\u8BF8\u4E8B\u987A\u5229\uFF0C\u5BAB\u4E3B\u656C\u8BF7\u653E\u5FC3\u3002\u884C\u5357\u5C06\u4E8E\u660E\u65E5\u4F7F\u7528\u593A\u80CE\u82B1\uFF0C\u5FC5\u4E0D\u8D1F\u5BAB\u4E3B\u6240\u6258\u3002</p><p>\u4ED6\u90D1\u91CD\u5730\u5377\u8D77\u4E66\u4FE1\uFF0C\u53C8\u62FF\u4E86\u51E0\u679A\u5B89\u80CE\u6ECB\u9634\u7684\u4E39\u836F\u4E00\u5E76\u585E\u5230\u7AF9\u7B52\u4E2D\uFF0C\u4EA4\u7ED9\u8D1F\u8D23\u7BA1\u7406\u4FE1\u9E3D\u7684\u5E2E\u4F17\u3002</p><p>\u6536\u62FE\u7740\u7B14\u58A8\uFF0C\u53F6\u884C\u5357\u6162\u541E\u541E\u8BF4\u9053\uFF1A\u300C\u606D\u559C\u5E08\u592A\uFF0C\u5C11\u592B\u4EBA\u5DF2\u7ECF\u6709\u559C\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u9762\u51B7\u5982\u51B0\uFF0C\u5FC3\u91CC\u5374\u6697\u6697\u6CDB\u8D77\u4E00\u4E1D\u82E6\u6DA9\u3002\u7D2B\u73AB\u662F\u4F17\u4EBA\u552F\u4E00\u7684\u5E0C\u671B\uFF0C\u73B0\u5728\u5979\u6000\u4E86\u8EAB\u5B55\uFF0C\u5230\u65F6\u8D70\u8DEF\u90FD\u4E0D\u65B9\u4FBF\uFF0C\u7A76\u7ADF\u8FD8\u80FD\u4E0D\u80FD\u6551\u51FA\u5979\u4EEC\u5462\u3002</p><p>\u53F6\u884C\u5357\u7768\u89C6\u7740\u96EA\u5CF0\u795E\u5C3C\uFF0C\u5C06\u91D1\u9488\u6162\u6162\u6536\u597D\u3002\u5FC3\u91CC\u76D8\u7B97\u9053\uFF1A\u660E\u65E5\u690D\u5165\u593A\u80CE\u82B1\uFF0C\u5BAB\u4E3B\u56DE\u6765\u6B63\u80FD\u8D76\u4E0A\u5206\u5A29\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6D1B\u9633\u8BF8\u4E8B\u5DF2\u6BD5\uFF0C\u4E94\u6708\u4E8C\u5341\u4E5D\uFF0C\u6155\u5BB9\u9F99\u5E26\u7740\u4F17\u4EBA\u8D76\u8D74\u9F99\u57CE\u3002\u5BAB\u767D\u7FBD\u7559\u5B88\u957F\u9E70\u4F1A\uFF0C\u7EAA\u7709\u59A9\u5728\u9999\u6708\u697C\u6302\u724C\u63A5\u5BA2\uFF0C\u5176\u4F59\u4E09\u5341\u4E00\u4EBA\u5206\u4E58\u56DB\u8F86\u5927\u8F66\uFF0C\u4E8C\u5341\u4F59\u5339\u9A6C\u4E00\u8DEF\u5317\u4E0A\u3002</p><p>\u91D1\u5F00\u7532\u7CBE\u901A\u5175\u6CD5\uFF0C\u6CBF\u9014\u6307\u70B9\u6C5F\u5C71\uFF0C\u5BF9\u53E4\u4ECA\u6218\u4E8B\u5982\u6570\u5BB6\u73CD\uFF1B\u7075\u7389\u535A\u95FB\u5F3A\u8BB0\uFF0C\u4E00\u8DEF\u4E0A\u63A2\u7A76\u6570\u7406\uFF0C\u7814\u8BA8\u9053\u7384\uFF0C\u4F7F\u6155\u5BB9\u9F99\u83B7\u76CA\u751A\u591A\u3002\u77F3\u880D\u3001\u5B89\u5B50\u5B8F\u4E5F\u662F\u8D70\u5357\u95EF\u5317\uFF0C\u89C1\u591A\u8BC6\u5E7F\u4E4B\u8F88\uFF0C\u56E0\u6B64\u8DEF\u7A0B\u867D\u9065\uFF0C\u9014\u4E2D\u5374\u4E0D\u5BC2\u5BDE\u3002</p><p>\u4F46\u6700\u8BA9\u6155\u5BB9\u9F99\u9500\u9B42\u7684\u8FD8\u662F\u8427\u4F5B\u5974\u3002</p><p>\u81EA\u4ECE\u5F53\u65E5\u6BC1\u613F\u8BB8\u8EAB\u4E4B\u540E\uFF0C\u8427\u4F5B\u5974\u629B\u5F00\u6240\u6709\u7684\u77DC\u6301\u548C\u7F9E\u6DA9\uFF0C\u5FC3\u7518\u60C5\u613F\u505A\u4E86\u513F\u5B50\u7684\u73A9\u7269\u3002\u867D\u7136\u624B\u811A\u762B\u8F6F\u4E0D\u80FD\u52A8\u4F5C\uFF0C\u4F46\u7F8E\u5987\u503E\u5FC3\u76F8\u8BB8\u7684\u67D4\u5A9A\u5A49\u8F6C\uFF0C\u4ECD\u4F7F\u6155\u5BB9\u9F99\u5FC3\u9189\u795E\u8FF7\u3002</p><p>\u7D2B\u73AB\u4E5F\u4E56\u5F97\u5F88\uFF0C\u6BCF\u65E5\u8BA9\u670D\u836F\u5C31\u670D\u836F\uFF0C\u8BA9\u4F8D\u5BDD\u5C31\u4F8D\u5BDD\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u8FDD\u62D7\u4E4B\u5904\u3002</p><p>\u6155\u5BB9\u9F99\u62E5\u7740\u5A07\u7F8E\u5982\u82B1\u800C\u53C8\u6E29\u5A49\u67D4\u987A\u7684\u6BCD\u5973\u4FE9\uFF0C\u53EF\u8C13\u5FD7\u6EE1\u610F\u5F97\uFF0C\u53EA\u7B49\u796D\u8FC7\u6155\u5BB9\u6C0F\u5217\u7956\u5217\u5B97\u4E4B\u540E\uFF0C\u53D6\u51FA\u5B9D\u85CF\u4FBF\u53EF\u89C5\u673A\u8D77\u4E8B\uFF0C\u91CD\u5EFA\u71D5\u56FD\u3002</p><p>\u4F17\u4EBA\u4E00\u8DEF\u7ECF\u957F\u5E73\u3001\u4E0A\u515A\u3001\u8944\u56FD\u3001\u8D75\u90E1\u3001\u4E0A\u8C37\u3001\u6DBF\u90E1\uFF0C\u4E8E\u4E03\u6708\u5E95\u5230\u8FBE\u6E14\u9633\u3002</p><p>\u6E14\u9633\u662F\u5927\u5468\u5317\u65B9\u91CD\u9547\uFF0C\u51FA\u5F97\u8FB9\u5173\u5DF2\u662F\u585E\u5916\uFF0C\u8DDD\u6155\u5BB9\u6C0F\u9F99\u5174\u4E4B\u5730\u53EA\u5269\u4E0B\u534A\u6708\u8DEF\u7A0B\u3002</p><p>\u5317\u56FD\u521D\u79CB\uFF0C\u957F\u7A7A\u5982\u6D17\uFF0C\u957F\u8349\u5982\u6D77\uFF0C\u89C6\u91CE\u6240\u53CA\u5C3D\u662F\u82CD\u82CD\u5929\u7A79\u832B\u832B\u539F\u91CE\u3002\u4E00\u884C\u4EBA\u5DDD\u884C\u5176\u95F4\uFF0C\u987F\u6709\u5929\u8FE5\u5730\u8FDC\uFF0C\u5B87\u5B99\u65E0\u7A77\u4E4B\u53F9\u3002</p><p>\u6155\u5BB9\u9F99\u5174\u81F4\u5927\u53D1\uFF0C\u56DE\u9A6C\u9A70\u5230\u8F66\u65C1\uFF0C\u6311\u5EC9\u9053\uFF1A\u300C\u628A\u5A18\u9012\u7ED9\u6211\u3002\u300D</p><p>\u7D2B\u73AB\u8FDF\u7591\u4E86\u4E00\u4E0B\uFF0C\u5979\u6015\u5916\u9762\u98CE\u5927\uFF0C\u62FF\u4E86\u4E00\u6761\u539A\u4E9B\u7684\u6BDB\u6BEF\u5C06\u8427\u4F5B\u5974\u88F9\u597D\uFF0C\u8FD9\u624D\u4EA4\u7ED9\u6155\u5BB9\u9F99\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u81C2\u4E00\u5C55\uFF0C\u5C06\u6BCD\u5973\u4E00\u5E76\u62B1\u5230\u978D\u4E0A\uFF0C\u7136\u540E\u4E00\u78D5\u9A6C\u523A\uFF0C\u7BAD\u77E2\u822C\u51B2\u4E86\u51FA\u53BB\u3002</p><p>\u91D1\u5F00\u7532\u548C\u7075\u7389\u76F8\u89C6\u4E00\u7B11\uFF0C\u53EA\u968F\u7740\u8F66\u961F\u7F13\u7F13\u800C\u884C\uFF0C\u5E76\u6CA1\u6709\u8DDF\u4E0A\u53BB\u3002</p><p>\u7D2B\u73AB\u53EA\u89C9\u8033\u7554\u98CE\u58F0\u52B2\u6025\uFF0C\u9A6C\u5339\u50CF\u662F\u5288\u5F00\u6CE2\u6D9B\u7684\u5229\u7BAD\uFF0C\u98DE\u9A70\u5728\u65E0\u8FB9\u65E0\u9645\u5730\u8349\u539F\u4E0A\u3002\u65C1\u8FB9\u7684\u8427\u4F5B\u5974\u5168\u8EAB\u90FD\u5305\u5728\u539A\u539A\u7684\u6BDB\u6BEF\u5185\uFF0C\u53EA\u9732\u51FA\u4E00\u5F20\u82B1\u74E3\u822C\u7684\u4FCF\u8138\u3002\u5979\u53D7\u4E0D\u4E86\u6251\u9762\u7684\u52B2\u98CE\uFF0C\u7F8E\u76EE\u772F\u6210\u4E00\u6761\u7EC6\u7F1D\uFF0C\u5A07\u602F\u602F\u5730\u504E\u4F9D\u5728\u6155\u5BB9\u9F99\u6000\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u5DE6\u62E5\u53F3\u62B1\uFF0C\u5355\u9760\u817F\u90E8\u7684\u529B\u91CF\u7EB5\u9A6C\u72C2\u5954\uFF0C\u4FCA\u8138\u4E0A\u795E\u91C7\u98DE\u626C\uFF0C\u9C9C\u8863\u6012\u9A6C\uFF0C\u76F4\u5982\u541B\u4E34\u5927\u5730\u7684\u738B\u4FAF\uFF0C\u53C8\u5982\u62E5\u7740\u4E24\u53EA\u5F69\u51E4\u7684\u86DF\u9F99\uFF0C\u4F3C\u4E4E\u6709\u65E0\u7A77\u65E0\u5C3D\u7684\u7CBE\u529B\uFF0C\u53BB\u8FFD\u9010\u8FDC\u65B9\u7684\u5730\u5E73\u7EBF\u3002</p><p>\u5728\u8D77\u4F0F\u7684\u957F\u8349\u4E0A\u98DE\u63A0\u800C\u8FC7\u7684\u9AD8\u901F\uFF0C\u4F7F\u7D2B\u73AB\u6709\u4E9B\u7729\u6655\u3002\u53EA\u6709\u9760\u5728\u8EAB\u540E\u575A\u5B9E\u7684\u80F8\u819B\u4E0A\uFF0C\u624D\u5B89\u4E0B\u5FC3\u6765\u3002</p><p>\u80CC\u540E\u7684\u80F8\u819B\u6E29\u6696\u5BBD\u5E7F\uFF0C\u5145\u6EE1\u84EC\u52C3\u7684\u7537\u6027\u6C14\u606F\uFF0C\u751A\u81F3\u80FD\u611F\u89C9\u5230\u5FC3\u810F\u5728\u80F8\u8154\u5185\u7684\u8DF3\u52A8\u3002\u8170\u95F4\u7684\u624B\u81C2\u6C89\u7A33\u800C\u4E14\u6709\u529B\uFF0C\u8BA9\u4EBA\u611F\u89C9\u53EA\u8981\u8EB2\u5728\u4ED6\u7684\u7FBD\u7FFC\u4E0B\uFF0C\u5C31\u53EF\u4EE5\u4E0D\u60E7\u4EFB\u4F55\u98CE\u96E8\u3002\u7D2B\u73AB\u95ED\u4E0A\u773C\uFF0C\u53EA\u60F3\u5728\u8FD9\u4E2A\u6000\u62B1\u4E2D\u751C\u751C\u7761\u4E0A\u4E00\u89C9\uFF0C\u4EFB\u4ED6\u5E26\u7740\u81EA\u5DF1\u76F4\u5230\u5929\u5730\u5C3D\u5934\u3002</p><p>\u521A\u5408\u4E0A\u773C\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u4E00\u51DB\u3002\u8EAB\u540E\u7684\u7537\u4EBA\u4E0D\u4EC5\u662F\u81EA\u5DF1\u7684\u5AE1\u4EB2\u54E5\u54E5\uFF0C\u800C\u4E14\u8FD8\u662F\u6709\u8840\u6D77\u6DF1\u4EC7\u7684\u654C\u4EBA\u2014\u2014\u5BF9\u5979\u6765\u8BF4\uFF0C\u4EFB\u4F55\u4E00\u4E2A\u90FD\u662F\u6C38\u8FDC\u4E5F\u89E3\u4E0D\u5F00\u7684\u5FC3\u7ED3\u3002</p><p>\u5979\u77A5\u4E86\u4E00\u773C\u8427\u4F5B\u5974\uFF0C\u53EA\u89C1\u6BCD\u4EB2\u7709\u76EE\u542B\u60C5\uFF0C\u4F9D\u4EBA\u5C0F\u9E1F\u822C\u4F9D\u5728\u6155\u5BB9\u9F99\u6000\u4E2D\uFF0C\u50CF\u662F\u6D51\u5FD8\u4E86\u90A3\u662F\u81EA\u5DF1\u7684\u4EB2\u751F\u513F\u5B50\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u53C8\u9178\u53C8\u6DA9\uFF0C\u773C\u775B\u7981\u4E0D\u4F4F\u6E7F\u4E86\u3002\u4F46\u5979\u5374\u8BF4\u4E0D\u6E05\u7A76\u7ADF\u662F\u4E3A\u81EA\u5DF1\uFF0C\u8FD8\u662F\u4E3A\u6BCD\u4EB2\u800C\u96BE\u8FC7\u3002</p><p>\u5979\u62AC\u8155\u62B9\u53BB\u6CEA\u82B1\uFF0C\u6155\u5BB9\u9F99\u95EE\u9053\uFF1A\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u98CE\u592A\u5927\u2026\u2026\u300D\u7D2B\u73AB\u5C0F\u58F0\u8BF4\u3002\u5FC3\u60F3\uFF1A\u53EA\u8981\u5A18\u80FD\u5F00\u5FC3\uFF0C\u2026\u2026\u8FD9\u4E9B\u90FD\u65E0\u6240\u8C13\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u5939\u9A6C\u8179\uFF0C\u653E\u6162\u901F\u5EA6\uFF0C\u7B11\u9053\uFF1A\u300C\u53EA\u987E\u9AD8\u5174\uFF0C\u7ADF\u7136\u5FD8\u4E86\u4F60\u4EEC\u8FD8\u6000\u7740\u6211\u7684\u5B69\u5B50\u3002\u8981\u4E0D\u8981\u56DE\u8F66\u91CC\u4F11\u606F\uFF1F\u300D</p><p>\u7D2B\u73AB\u8F7B\u8F7B\u7406\u4E86\u7406\u6BCD\u4EB2\u7684\u53D1\u4E1D\uFF0C\u88F9\u7D27\u6BDB\u6BEF\uFF0C\u8F7B\u58F0\u95EE\u9053\uFF1A\u300C\u5A18\uFF0C\u8981\u4E0D\u8981\u56DE\u53BB\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u4E5F\u6478\u7740\u7F8E\u5987\u7684\u5A07\u9765\uFF0C\u4F4E\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u8BF4\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u751F\u6655\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5A18\u542C\u9F99\u54E5\u54E5\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5728\u4E24\u5973\u8138\u4E0A\u5404\u543B\u4E00\u53E3\uFF0C\u7EB5\u58F0\u957F\u7B11\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5A18\uFF0C\u559D\u70B9\u836F\u3002\u300D</p><p>\u8427\u4F5B\u5974\u76B1\u7740\u7709\u5934\u559D\u4E86\u4E24\u53E3\uFF0C\u6447\u4E86\u6447\u5934\u3002</p><p>\u7D2B\u73AB\u653E\u4E0B\u836F\u7897\uFF0C\u62C9\u8D77\u6BCD\u4EB2\u7684\u624B\u81C2\u7EC6\u7EC6\u63C9\u634F\u3002\u8FD9\u4E48\u4E45\u65E0\u6CD5\u8FD0\u52A8\uFF0C\u6BCD\u4EB2\u7684\u8EAB\u4F53\u8FD8\u662F\u90A3\u4E48\u7F8E\u4E3D\u3002\u5982\u679C\u8BF4\u6709\u4EC0\u4E48\u53D8\u5316\uFF0C\u90A3\u5C31\u662F\u808C\u80A4\u66F4\u52A0\u5A07\u5AE9\u6ED1\u817B\uFF0C\u8FD8\u5E26\u7740\u8FF7\u4EBA\u7684\u8309\u8389\u82B1\u9999\uFF1B\u8FD8\u6709\uFF0C\u5C0F\u8179\u5DF2\u7ECF\u9686\u8D77\u3002</p><p>\u5979\u60C5\u4E0D\u81EA\u7981\u5730\u6478\u6478\u4E86\u81EA\u5DF1\u7684\u5C0F\u8179\u3002\u6000\u5B55\u5DF2\u7ECF\u4E09\u4E2A\u6708\u4E86\uFF0C\u82D7\u6761\u7684\u8170\u80A2\u4E5F\u4E30\u6EE1\u8D77\u6765\uFF0C\u53EA\u662F\u6BD4\u6BCD\u4EB2\u7565\u5C0F\u4E00\u4E9B\u800C\u5DF2\u3002</p><p>\u6BCD\u5973\u4FE9\u540C\u65F6\u6000\u5B55\uFF0C\u800C\u4E14\u8FD8\u662F\u540C\u4E00\u4E2A\u7537\u4EBA\u7684\u9AA8\u8840\uFF0C\u800C\u4E14\u8FD9\u4E2A\u7537\u4EBA\u662F\u4E24\u4EBA\u8840\u8109\u76F8\u8FDE\u7684\u513F\u5B50\u3001\u54E5\u54E5\u2014\u2014\u7D2B\u73AB\u4E00\u60F3\u5C31\u8981\u53D1\u75AF\u3002\u5929\uFF0C\u8FD9\u7A76\u7ADF\u662F\u600E\u4E48\u56DE\u4E8B\u2026\u2026</p><p>\u65F6\u5DF2\u9EC4\u660F\uFF0C\u8F66\u961F\u505C\u5728\u8349\u539F\u4E2D\uFF0C\u57CB\u9505\u751F\u706B\u3002\u7B2C\u4E00\u9505\u7167\u4F8B\u662F\u7ED9\u592B\u4EBA\u548C\u5C11\u592B\u4EBA\u714E\u836F\uFF0C\u7B49\u4E24\u4EBA\u5404\u81EA\u559D\u5B8C\uFF0C\u5230\u8FDC\u5904\u6253\u730E\u7684\u6155\u5BB9\u9F99\u7B49\u4EBA\u8FD8\u672A\u56DE\u6765\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6B63\u5728\u8F66\u4E2D\u7ED9\u592B\u4EBA\u6D82\u62B9\u8EAB\u4F53\uFF0C\u5FFD\u7136\u5357\u65B9\u7684\u5929\u9645\u9690\u9690\u4F20\u6765\u4E00\u9635\u6025\u4FC3\u7684\u9A6C\u8E44\u58F0\u3002\u4E24\u5973\u5BF9\u671B\u4E00\u773C\uFF0C\u5F7C\u6B64\u90FD\u770B\u51FA\u5BF9\u65B9\u773C\u4E2D\u7684\u60CA\u8BB6\u3002\u5728\u8349\u539F\u5341\u5929\u534A\u6708\u4E5F\u4E0D\u4E00\u5B9A\u80FD\u89C1\u5230\u4E00\u4E2A\u4EBA\u5F71\uFF0C\u8FD9\u6837\u6025\u9A70\uFF0C\u4F1A\u662F\u8C01\u5462\uFF1F</p><p>\u9A6C\u8E44\u58F0\u6765\u5F97\u597D\u5FEB\uFF0C\u7247\u523B\u95F4\u4FBF\u5954\u5230\u8FD1\u65C1\uFF0C\u63A5\u7740\u6563\u5F00\uFF0C\u5C06\u8F66\u961F\u56E2\u56E2\u56F4\u4F4F\u3002</p><p>\u6155\u5BB9\u9F99\u5E26\u7740\u91D1\u5F00\u7532\u7B49\u4EBA\u6253\u730E\u672A\u56DE\uFF0C\u5728\u573A\u53EA\u5269\u7075\u7389\u3002\u4E0D\u7B49\u957F\u8001\u5429\u5490\uFF0C\u4F11\u61A9\u7684\u661F\u6708\u6E56\u5E2E\u4F17\u90FD\u5DF2\u8DC3\u8EAB\u800C\u8D77\uFF0C\u5404\u6301\u5175\u5203\uFF0C\u5C0F\u5FC3\u6212\u5907\u3002\u6765\u9A91\u5206\u660E\u662F\u51B2\u7740\u4ED6\u4EEC\u4E00\u884C\uFF0C\u770B\u4ED6\u4EEC\u7684\u4E3E\u52A8\uFF0C\u662F\u654C\u975E\u53CB\u3002</p><p>\u6765\u8005\u5171\u662F\u56DB\u5341\u4E03\u9A91\uFF0C\u6BD4\u7559\u5728\u5BBF\u5904\u7684\u661F\u6708\u6E56\u5E2E\u4F17\u591A\u4E86\u8FD1\u4E00\u500D\u3002\u5F53\u5148\u4E00\u4EBA\u96C4\u8EAF\u864E\u76EE\uFF0C\u5F62\u5BB9\u5A01\u731B\uFF0C\u9A6C\u8E44\u7FFB\u98DE\u5904\u8349\u6D77\u5212\u5F00\u4E00\u9053\u957F\u957F\u7684\u75D5\u8FF9\u3002</p><p>\u6765\u4EBA\u8F6C\u77AC\u4FBF\u5954\u5230\u7075\u7389\u8EAB\u524D\uFF0C\u94C1\u81C2\u4E00\u7D27\uFF0C\u6F06\u9ED1\u7684\u9A8F\u9A6C\u4EBA\u7ACB\u800C\u8D77\uFF0C\u90A3\u4EBA\u621F\u6307\u559D\u9053\uFF1A\u300C\u53EF\u662F\u661F\u6708\u6E56\u5996\u5B7D\uFF01\u300D</p><p>\u58F0\u5982\u96F7\u9706\uFF0C\u5728\u8349\u539F\u4E0A\u8FDC\u8FDC\u6EDA\u5F00\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u542C\u5230\u58F0\u97F3\uFF0C\u8138\u8272\u987F\u65F6\u96EA\u767D\uFF0C\u767D\u7389\u9E42\u624B\u8155\u4E00\u98A4\uFF0C\u82AC\u82B3\u7684\u8309\u8389\u82B1\u6CB9\u300C\u546F\u300D</p><p>\u7684\u6389\u5728\u8F66\u53A2\u5185\u3002</p><p>\u300C\u662F\u8C01\uFF1F\u300D\u7D2B\u73AB\u770B\u5230\u4E24\u5973\u7684\u5F02\u6837\uFF0C\u4E0D\u7531\u95EE\u9053\u3002</p><p>\u59D0\u59B9\u4FE9\u76F8\u987E\u5931\u8272\uFF0C\u8C01\u4E5F\u6CA1\u6709\u5F00\u53E3\u3002</p><p>\u7075\u7389\u8F7B\u6447\u4F5B\u5C18\uFF0C\u626B\u53BB\u6E85\u5728\u8EAB\u4E0A\u7684\u8349\u53F6\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u9601\u4E0B\u53EF\u662F\u767E\u6218\u5929\u9F99\uFF1F\u300D</p><p>\u90A3\u4EBA\u96C4\u8EAF\u4E00\u6C89\uFF0C\u75BE\u9A70\u7684\u5750\u9A91\u94C1\u94F8\u822C\u7ACB\u5728\u5730\u4E0A\u4E0D\u79FB\u5206\u6BEB\uFF0C\u300C\u4F60\u5012\u6709\u4E9B\u773C\u529B\u2014\u2014\u4F60\u4EEC\u662F\u4EC0\u4E48\u4EBA\uFF01\u300D\u540E\u4E00\u53E5\u820C\u7EFD\u6625\u96F7\uFF0C\u4F17\u4EBA\u90FD\u662F\u4E00\u9707\u3002</p><p>\u7075\u7389\u6BEB\u4E0D\u4E3A\u610F\u5730\u5C06\u62C2\u5C18\u62B1\u5728\u81C2\u95F4\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u516B\u6781\u95E8\u5A01\u9707\u5173\u4E2D\uFF0C\u96BE\u9053\u8FD9\u585E\u5317\u4E5F\u662F\u9F99\u638C\u95E8\u7684\u5730\u76D8\uFF1F\u300D</p><p>\u6765\u8005\u6B63\u662F\u516B\u6781\u95E8\u638C\u95E8\u767E\u6218\u5929\u9F99\u9F99\u6218\u91CE\uFF0C\u4ED6\u864E\u76EE\u751F\u5A01\uFF0C\u559D\u9053\uFF1A\u300C\u5C14\u7B49\u82E5\u662F\u661F\u6708\u6E56\u5996\u4EBA\uFF0C\u6211\u516B\u6781\u95E8\u4ECA\u65E5\u5C31\u8981\u5728\u585E\u5317\u7ACB\u5A01\uFF01\u300D</p><p>\u7D2B\u73AB\u77A5\u4E86\u767D\u6C0F\u59D0\u59B9\u4E00\u773C\uFF0C\u5FC3\u4E0B\u604D\u7136\uFF0C\u539F\u6765\u662F\u59D0\u59B9\u4FE9\u7684\u5E08\u95E8\u5230\u4E86\u3002\u53EA\u4E0D\u77E5\u516B\u6781\u95E8\u4E3A\u4F55\u4F1A\u5343\u91CC\u8FE2\u8FE2\u4ECE\u5173\u4E2D\u8FFD\u5230\u6B64\u5904\uFF0C\u96BE\u9053\u662F\u4E3A\u4E86\u5979\u4EEC\u59D0\u59B9\uFF1F\u4F46\u4E24\u5973\u4E00\u76F4\u7559\u5728\u5BAB\u5185\uFF0C\u4E3A\u5974\u4E4B\u4E8B\u5E76\u4E0D\u50CF\u81EA\u5DF1\u7684\u5E08\u59D0\u4E00\u6837\u88AB\u661F\u6708\u6E56\u5BA3\u626C\u5929\u4E0B\uFF0C\u4ED6\u4EEC\u600E\u4E48\u4F1A\u77E5\u9053\u6D88\u606F\uFF1F</p><p>\u9F99\u6218\u91CE\u5E76\u4E0D\u77E5\u9053\u81EA\u5DF1\u7684\u5F1F\u5B50\u4E5F\u5728\u6B64\u95F4\u3002\u4ED6\u4E0E\u5E7F\u9633\u5E2E\u7684\u5B59\u540C\u8F89\u6709\u8FC7\u547D\u4EA4\u60C5\uFF0C\u5F53\u65E5\u5B59\u540C\u8F89\u5411\u516B\u6781\u95E8\u6C42\u63F4\uFF0C\u4ED6\u6B63\u8FDC\u5728\u5929\u5C71\u3002\u4E00\u4E2A\u6708\u524D\u56DE\u5230\u5B89\u5B9A\uFF0C\u95FB\u8BF4\u5B59\u540C\u8F89\u60E8\u6B7B\uFF0C\u59BB\u5B50\u9971\u53D7\u6298\u78E8\u542B\u6068\u800C\u7EC8\uFF0C\u987F\u65F6\u6012\u53D1\u51B2\u51A0\uFF0C\u5C3D\u5E26\u95E8\u4E0B\u7CBE\u9510\u6740\u81F3\u6D1B\u9633\u3002</p><p>\u8521\u4E91\u5CF0\u7B49\u4EBA\u731D\u4E0D\u53CA\u9632\u4E0B\u88AB\u4ED6\u653B\u5165\u957F\u9E70\u4F1A\uFF0C\u63B3\u8D70\u4E86\u9648\u5A01\u3002\u4E00\u756A\u5BA1\u8BAF\u4E4B\u540E\uFF0C\u9F99\u6218\u91CE\u624D\u77E5\u9053\u5143\u51F6\u4E43\u662F\u661F\u6708\u6E56\u3002\u5F53\u4E0B\u4ED6\u5C06\u53DB\u5F92\u9648\u5A01\u4E71\u5200\u5206\u5C38\uFF0C\u544A\u796D\u4EA1\u53CB\u5728\u5929\u4E4B\u7075\uFF0C\u7136\u540E\u9A6C\u4E0D\u505C\u8E44\u5730\u8FFD\u5230\u585E\u5916\uFF0C\u7EC8\u4E8E\u5728\u6B64\u5730\u8D76\u4E0A\u661F\u6708\u6E56\u4F17\u4EBA\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u53C8\u60CA\u53C8\u6015\u53C8\u559C\u53C8\u5FE7\uFF0C\u5FC3\u91CC\u767E\u5473\u6742\u9648\u3002\u6B64\u65F6\u672C\u95E8\u9AD8\u624B\u6BD5\u81F3\uFF0C\u6B63\u662F\u8131\u79BB\u82E6\u6D77\u7684\u826F\u673A\uFF0C\u4F46\u5E08\u7236\u751F\u6027\u6897\u76F4\uFF0C\u4E00\u5411\u5AC9\u6076\u5982\u4EC7\uFF0C\u59D0\u59B9\u4FE9\u59D4\u8EAB\u4E8B\u654C\u5DF2\u662F\u5927\u9519\uFF0C\u4F55\u51B5\u2026\u2026\u4E24\u5973\u6123\u6123\u5750\u5728\u8F66\u5185\uFF0C\u5E08\u7236\u5C31\u5728\u773C\u524D\uFF0C\u5374\u4E0D\u6562\u51FA\u53BB\u62DC\u89C1\u3002</p>',68);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
