import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_85" tabindex="-1"><a class="header-anchor" href="#_85" aria-hidden="true">#</a> 85</h1><p>\u4E00\u9A91\u4ECE\u540E\u5954\u6765\uFF0C\u9A91\u58EB\u64CE\u51FA\u957F\u67AA\u671D\u7075\u7389\u80A9\u5934\u523A\u843D\uFF0C\u559D\u9053\uFF1A\u300C\u662F\u4E0D\u662F\u661F\u6708\u6E56\u5996\u5B7D\uFF1F\u5FEB\u8BF4\uFF01\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u4E00\u542C\u58F0\u97F3\uFF0C\u8138\u8272\u66F4\u767D\u4E86\uFF0C\u516D\u5E08\u53D4\u8463\u8C79\u5A01\u4E5F\u6765\u4E86\uFF0C\u4ED6\u6027\u70C8\u5982\u706B\uFF0C\u6BD4\u5E08\u7236\u8FD8\u8981\u4E25\u5386\u51E0\u5206\u2026\u2026</p><p>\u8D2F\u6EE1\u771F\u6C14\u7684\u957F\u67AA\u631F\u7740\u5954\u9A6C\u7684\u51B2\u529B\uFF0C\u5A01\u52BF\u60CA\u4EBA\u3002\u7075\u7389\u957F\u7709\u4E00\u6311\uFF0C\u62C2\u5C18\u626C\u8D77\uFF0C\u5377\u4F4F\u67AA\u950B\uFF0C\u63A5\u7740\u5411\u65C1\u4E00\u5F15\u3002</p><p>\u8463\u8C79\u5A01\u624B\u4E0A\u4E00\u9707\uFF0C\u9669\u4E9B\u88AB\u62C9\u4E0B\u9A6C\u6765\uFF0C\u8FDE\u5FD9\u6536\u81C2\u56DE\u67AA\u3002</p><p>\u4E24\u4EBA\u4E00\u5728\u9A6C\u4E0A\u4E00\u5728\u9A6C\u4E0B\uFF0C\u5404\u81EA\u8FD0\u529F\u76F8\u6297\u3002\u53EA\u89C1\u8463\u8C79\u5A01\u8138\u8272\u53D1\u7EA2\uFF0C\u957F\u67AA\u4E00\u70B9\u70B9\u5782\u4E0B\uFF0C\u663E\u7136\u529F\u529B\u4E0D\u53CA\u3002</p><p>\u7075\u7389\u9762\u4E0D\u6539\u8272\uFF0C\u5FC3\u91CC\u5374\u6697\u81EA\u7740\u6025\u3002\u8463\u8C79\u5A01\u529F\u529B\u867D\u4E0D\u53CA\u81EA\u5DF1\uFF0C\u4F46\u9F99\u6218\u91CE\u58F0\u540D\u663E\u8D6B\uFF0C\u529F\u529B\u4E0D\u5728\u81EA\u5DF1\u4E4B\u4E0B\u3002\u51B5\u4E14\u516B\u6781\u95E8\u7CBE\u82F1\u5C3D\u51FA\uFF0C\u82E5\u4E00\u62E5\u800C\u4E0A\uFF0C\u81EA\u5DF1\u8FD8\u8981\u4FDD\u62A4\u52A8\u5F39\u4E0D\u5F97\u7684\u592B\u4EBA\u548C\u5185\u529F\u88AB\u5236\u7684\u5C11\u592B\u4EBA\uFF0C\u6050\u6015\u96BE\u4EE5\u8BA8\u597D\u3002</p><p>\u601D\u7D22\u95F4\uFF0C\u7075\u7389\u6717\u58F0\u9053\uFF1A\u300C\u4E45\u95FB\u516B\u6781\u95E8\u9F99\u7280\u72EE\u8C61\u3001\u864E\u8C79\u9E70\u72FC\u516B\u6770\u5A01\u540D\u3002\u8D2B\u9053\u4E0D\u624D\uFF0C\u613F\u4E00\u4E00\u9886\u6559\uFF01\u300D\u8BF4\u7F62\u8DC3\u5230\u7A7A\u5904\uFF0C\u62C2\u5C18\u4E00\u626C\uFF0C\u505A\u4E86\u656C\u8BF7\u8D50\u6559\u7684\u624B\u52BF\u3002\u4ED6\u4E00\u76F4\u4E0D\u5410\u9732\u59D3\u540D\u8EAB\u4EFD\uFF0C\u6B63\u662F\u6B3A\u8FD9\u4E9B\u4EBA\u81EA\u8D1F\u4FA0\u4E49\uFF0C\u6015\u9020\u6210\u8BEF\u4F24\u800C\u4E0D\u80FD\u653E\u5C3D\u3002</p><p>\u8463\u8C79\u5A01\u8EAB\u5B50\u4E00\u659C\uFF0C\u957F\u67AA\u624E\u5728\u5730\u4E0A\u3002\u867D\u7136\u529F\u529B\u4E0D\u53CA\u5BF9\u624B\uFF0C\u4F46\u4ED6\u608D\u7136\u4E0D\u60E7\uFF0C\u72C2\u559D\u4E00\u58F0\uFF0C\u4FBF\u5F85\u51B2\u4E0A\u524D\u53BB\u3002</p><p>\u9F99\u6218\u91CE\u4E00\u628A\u6309\u4F4F\u5E08\u5F1F\u80A9\u5934\uFF0C\u6253\u91CF\u7740\u8FD9\u4E2A\u4E0D\u77E5\u5929\u9AD8\u5730\u539A\u7684\u9EC4\u51A0\u9053\u4EBA\u3002\u516B\u6781\u95E8\u5728\u4ED6\u8FD9\u4E00\u4EE3\u4EBA\u624D\u9F0E\u76DB\uFF0C\u516B\u6770\u5404\u5177\u827A\u4E1A\uFF0C\u5A01\u9707\u5173\u4E2D\uFF0C\u4E16\u4E0A\u7ADF\u7136\u6709\u4EBA\u6562\u8BF4\u8981\u72EC\u6597\u516B\u6770\uFF0C\u83AB\u4E0D\u6210\u662F\u5931\u5FC3\u75AF\u4E86\uFF1F</p><p>\u7075\u7389\u5374\u662F\u6709\u82E6\u81EA\u77E5\uFF0C\u6B64\u65F6\u5BAB\u4E3B\u3001\u91D1\u5F00\u7532\u3001\u77F3\u880D\u3001\u5B89\u5B50\u5B8F\uFF0C\u5305\u62EC\u4E5E\u4F0F\u7A77\u9686\u3001\u8840\u65A9\u53CC\u715E\u7B49\u6B21\u4E00\u7EA7\u7684\u597D\u624B\u90FD\u4E0D\u5728\u573A\uFF0C\u53EA\u5269\u4E8C\u5341\u4F59\u540D\u666E\u901A\u5E2E\u4F17\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u662F\u516B\u6781\u95E8\u7684\u5BF9\u624B\u3002\u53EA\u6709\u5148\u7528\u8A00\u8BED\u6324\u5151\u5F3A\u654C\uFF0C\u8BA9\u4ED6\u4EEC\u8F6E\u756A\u51FA\u624B\uFF0C\u6765\u4E00\u573A\u8F66\u8F6E\u5927\u6218\u3002\u82E5\u80FD\u6491\u5230\u5BAB\u4E3B\u8D76\u56DE\u7684\u90A3\u4E00\u523B\u4FBF\u4E07\u4E8B\u5927\u5409\u3002</p><p>\u4E09\u5F53\u5BB6\u8BB8\u72EE\u96C4\u4E00\u62A1\u719F\u94DC\u68CD\uFF0C\u53EB\u9053\uFF1A\u300C\u5927\u54E5\uFF0C\u6211\u53BB\u6559\u8BAD\u8FD9\u725B\u9F3B\u9053\u4EBA\u3002\u300D</p><p>\u9F99\u6218\u91CE\u770B\u4E0D\u900F\u7075\u7389\u7684\u6DF1\u6D45\uFF0C\u53C8\u4E0D\u613F\u501A\u591A\u4E3A\u80DC\uFF0C\u574F\u4E86\u516B\u6781\u95E8\u7684\u5A01\u540D\uFF0C\u4E8E\u662F\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u8BB8\u72EE\u96C4\u5927\u543C\u4E00\u58F0\uFF0C\u817E\u8EAB\u800C\u8D77\uFF0C\u719F\u94DC\u68CD\u5728\u5915\u9633\u4E2D\u5212\u51FA\u4E00\u8F6E\u91D1\u8292\uFF0C\u671D\u7075\u7389\u5934\u4E0A\u7838\u53BB\u3002</p><p>\u7075\u7389\u659C\u8EAB\u98D8\u8D77\uFF0C\u62C2\u5C18\u4E1C\u626B\u897F\u8361\uFF0C\u5374\u4E0D\u4E0E\u8BB8\u72EE\u96C4\u5175\u5203\u76F8\u63A5\uFF0C\u53EA\u65BD\u5C55\u8EAB\u6CD5\uFF0C\u4E0E\u4ED6\u6E38\u6597\u3002</p><p>\u719F\u94DC\u68CD\u821E\u51FA\u7684\u98CE\u58F0\u4F20\u5165\u8F66\u5185\uFF0C\u5728\u767D\u6C0F\u59D0\u59B9\u5FC3\u91CC\u6380\u8D77\u9635\u9635\u6CE2\u6D9B\u3002\u4E24\u5973\u5728\u661F\u6708\u6E56\u53D7\u5C3D\u51CC\u8FB1\uFF0C\u672C\u4EE5\u4E3A\u7EC8\u751F\u518D\u65E0\u51FA\u5934\u4E4B\u65E5\uFF0C\u4E0D\u6599\u5374\u5728\u5F02\u57DF\u7A81\u9047\u751F\u673A\u3002\u53EA\u662F\u2014\u2014\u5E08\u7236\u8FD8\u4F1A\u4E0D\u4F1A\u8BA9\u5979\u4EEC\u91CD\u5F52\u5E08\u95E8\uFF1F\u91CD\u5F52\u5E08\u95E8\u4E4B\u540E\uFF0C\u53C8\u600E\u751F\u4E0E\u540C\u95E8\u76F8\u5904\u2026\u2026</p><p>\u573A\u4E2D\u98CE\u58F0\u8D8A\u6765\u8D8A\u6025\uFF0C\u4E4D\u95FB\u8BB8\u72EE\u96C4\u50CF\u662F\u5F3A\u5F29\u4E4B\u672B\uFF0C\u4F46\u767D\u6C0F\u59D0\u59B9\u77E5\u9053\u4E09\u5E08\u53D4\u5929\u751F\u795E\u529B\uFF0C\u719F\u94DC\u68CD\u4E00\u65E6\u65BD\u5C55\u5F00\u6765\uFF0C\u5FC5\u5C06\u654C\u624B\u903C\u81F3\u7EDD\u5883\u800C\u540E\u5DF2\uFF0C\u7075\u7389\u4E00\u5473\u6E38\u6597\uFF0C\u6B63\u843D\u5165\u5E08\u53D4\u5F40\u4E2D\u3002</p><p>\u91D1\u98CE\u7834\u7A7A\u58F0\u4E2D\uFF0C\u7A81\u7136\u4E00\u4E2A\u67D4\u548C\u7684\u58F0\u97F3\u54CD\u8D77\uFF0C\u300C\u5E08\u54E5\uFF0C\u8FD9\u9053\u4EBA\u7684\u8EAB\u6CD5\u50CF\u662F\u4E0A\u6E05\u89C2\u98DE\u7D6E\u52B2\u4E00\u8DEF\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u7ACB\u65F6\u559C\u5F62\u4E8E\u8272\uFF0C\u300C\u5E08\u5A18\u4E5F\u6765\u4E86\uFF01\u300D\u5E08\u5A18\u5510\u989C\u5BF9\u4E24\u5973\u89C6\u5982\u5DF1\u51FA\uFF0C\u6709\u5979\u5728\uFF0C\u4E07\u4E8B\u90FD\u597D\u5546\u91CF\u3002\u60F3\u8D77\u5F53\u65E5\u5E08\u5A18\u5BF9\u81EA\u5DF1\u7684\u75BC\u7231\uFF0C\u59D0\u59B9\u4FE9\u5708\u987F\u65F6\u7EA2\u4E86\u3002</p><p>\u4E00\u4E2A\u6E05\u4EAE\u7684\u7AE5\u97F3\u54CD\u8D77\uFF0C\u300C\u7239\u7239\uFF0C\u4E09\u5E08\u53D4\u7684\u94DC\u68CD\u597D\u50CF\u6709\u4E9B\u91CD\u5462\u3002\u300D</p><p>\u9F99\u6218\u91CE\u95FB\u58F0\u4E0D\u7531\u4E00\u6115\uFF0C\u4ED6\u65E9\u770B\u51FA\u5E08\u5F1F\u68CD\u6CD5\u867D\u5982\u6C5F\u6CB3\u6CC4\u5730\uFF0C\u58F0\u52BF\u60CA\u4EBA\uFF0C\u4F46\u6BCF\u6B21\u843D\u4E0B\u90FD\u4F1A\u6C89\u4E0B\u5C11\u8BB8\uFF0C\u5DF2\u7ECF\u662F\u96BE\u4EE5\u63A7\u5236\u3002\u8FD9\u91CC\u9762\u7684\u5DEE\u8DDD\u6781\u5176\u7EC6\u5FAE\uFF0C\u6CA1\u60F3\u5230\u513F\u5B50\u7ADF\u7136\u80FD\u770B\u51FA\u6765\u3002</p><p>\u7D2B\u73AB\u6380\u8D77\u8F66\u5EC9\u4E00\u89D2\u671D\u5916\u671B\u53BB\uFF0C\u53EA\u89C1\u4E00\u6761\u5927\u6C49\u6602\u7136\u5750\u5728\u9A6C\u4E0A\uFF0C\u6C14\u5982\u5C71\u5CB3\u3002\u65C1\u8FB9\u662F\u4E00\u4E2A\u4E09\u5341\u4F59\u5C81\u7684\u7F8E\u8C8C\u5C11\u5987\uFF0C\u9A91\u7740\u4E00\u5339\u9752\u82B1\u9A79\uFF0C\u6000\u91CC\u62B1\u7740\u4E00\u4E2A\u516B\u4E5D\u5C81\u7684\u7537\u5B69\u3002\u7537\u5B69\u4E4C\u6E9C\u6E9C\u7684\u5927\u773C\u7D27\u7D27\u76EF\u7740\u573A\u4E2D\u7684\u6076\u6597\uFF0C\u663E\u5F97\u5174\u81F4\u52C3\u52C3\u3002</p><p>\u9F99\u6218\u91CE\u4E00\u628A\u62B1\u8FC7\u513F\u5B50\uFF0C\u300C\u5C0F\u5BB6\u4F19\uFF0C\u4F60\u4E5F\u80FD\u770B\u51FA\u6765\uFF1F\u300D</p><p>\u9F99\u6714\u8BA4\u771F\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u5510\u989C\u767D\u4E86\u4E08\u592B\u4E00\u773C\uFF0C\u300C\u8DDF\u4F60\u4E00\u4E2A\u6837\uFF0C\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u559C\u6B22\u770B\u522B\u4EBA\u6253\u6253\u6740\u6740\u3002\u300D</p><p>\u9F99\u6218\u91CE\u653E\u58F0\u5927\u7B11\uFF0C\u63C9\u7740\u513F\u5B50\u7684\u5934\u9876\u9053\uFF1A\u300C\u8FD9\u5C0F\u5B50\u516B\u5C81\u516D\u5408\u529F\u5C31\u7EC3\u5230\u7B2C\u4E09\u5C42\uFF0C\u6211\u516B\u6781\u95E8\u5386\u4EE3\u65E0\u4EBA\u80FD\u53CA\uFF0C\u53EA\u6015\u4E8C\u5341\u591A\u5C81\u5C31\u80FD\u628A\u8001\u5B50\u6BD4\u4E0B\u53BB\uFF01\u54C8\u54C8\uFF0C\u6211\u8FD9\u513F\u5B50\uFF0C\u600E\u4E48\u751F\u7684\uFF01\u300D</p><p>\u5510\u989C\u89C1\u4E08\u592B\u5982\u6B64\u5F97\u610F\uFF0C\u4E0D\u7981\u559C\u5B5C\u5B5C\u62BF\u5634\u4E00\u7B11\u3002</p><p>\u518D\u770B\u573A\u4E2D\uFF0C\u5F62\u52BF\u5DF2\u7ECF\u5927\u53D8\u3002\u8BB8\u72EE\u96C4\u7684\u719F\u94DC\u68CD\u8D8A\u6765\u8D8A\u6C89\uFF0C\u9053\u4EBA\u7684\u62C2\u5C18\u4ECD\u4E0D\u7D27\u4E0D\u6162\uFF0C\u9634\u67D4\u7684\u771F\u6C14\u4EFF\u4F5B\u900F\u660E\u7684\u86DB\u4E1D\uFF0C\u5C06\u68CD\u8EAB\u7D27\u7D27\u7F20\u4F4F\u3002</p><p>\u9F99\u6218\u91CE\u89C1\u5E08\u5F1F\u8D25\u50CF\u5DF2\u9732\uFF0C\u4E8E\u662F\u9AD8\u58F0\u9053\uFF1A\u300C\u8001\u4E09\uFF0C\u9000\u4E0B\u6765\u5427\u3002\u300D</p><p>\u8BB8\u72EE\u96C4\u5FC3\u6709\u4E0D\u7518\uFF0C\u4F46\u5BF9\u624B\u62DB\u672F\u7CBE\u5999\uFF0C\u518D\u6597\u4E0B\u53BB\u4E5F\u96BE\u4EE5\u53D6\u80DC\uFF0C\u4FBF\u865A\u6643\u4E00\u62DB\uFF0C\u5411\u540E\u8DC3\u51FA\u3002</p><p>\u811A\u8FD8\u672A\u843D\u5728\u5730\u4E0A\uFF0C\u4E00\u6761\u8EAB\u5F71\u9B3C\u9B45\u822C\u6B3A\u5230\u8EAB\u524D\uFF0C\u8BB8\u72EE\u96C4\u53EA\u89C1\u90A3\u9053\u4EBA\u5728\u81EA\u5DF1\u773C\u524D\u4E00\u7B11\uFF0C\u7136\u540E\u80F8\u53E3\u5267\u75DB\u3002</p><p>\u7075\u7389\u4E00\u65B9\u9762\u4E3A\u4E86\u4FDD\u7559\u771F\u6C14\uFF0C\u4E00\u65B9\u9762\u662F\u62D6\u5EF6\u65F6\u95F4\uFF0C\u624D\u6597\u4E86\u8FD9\u4E48\u4E45\uFF0C\u6B64\u65F6\u89C1\u4ED6\u8981\u9000\uFF0C\u7ACB\u523B\u75DB\u4E0B\u6740\u624B\u3002</p><p>\u516B\u6781\u95E8\u4F17\u4EBA\u9F50\u53EB\u4E0D\u597D\uFF0C\u6B63\u5F85\u51FA\u624B\u76F8\u52A9\uFF0C\u5DF2\u7ECF\u6765\u4E0D\u53CA\u4E86\u3002\u53EA\u542C\u8BB8\u72EE\u96C4\u4E00\u58F0\u95F7\u559D\uFF0C\u9AD8\u5927\u7684\u8EAB\u4F53\u5012\u98DE\u51FA\u53BB\uFF0C\u53E3\u4E2D\u9C9C\u8840\u72C2\u55B7\u3002</p><p>\u7075\u7389\u5C48\u819D\u9876\u788E\u8BB8\u72EE\u96C4\u7684\u80F8\u9AA8\uFF0C\u501F\u52BF\u540E\u8DC3\uFF0C\u5BBD\u888D\u5927\u8896\u98D8\u98D8\u6B32\u98DE\uFF0C\u72B6\u5982\u795E\u4ED9\u3002</p><p>\u4E00\u4E2A\u52B2\u88C5\u6C49\u5B50\u98DE\u63A0\u800C\u51FA\uFF0C\u62AC\u624B\u6258\u4F4F\u8BB8\u72EE\u96C4\u7684\u80CC\u5FC3\uFF0C\u5C06\u4ED6\u8F7B\u8F7B\u653E\u5728\u5730\u4E0A\uFF0C\u7136\u540E\u7ACB\u5373\u8FD0\u6C14\u66FF\u4ED6\u7597\u4F24\u3002</p><p>\u516B\u6781\u95E8\u4F17\u4EBA\u7FA4\u60C5\u6D8C\u52A8\uFF0C\u53EB\u9A82\u58F0\u54CD\u6210\u4E00\u7247\u3002</p><p>\u7D2B\u73AB\u76D8\u7B97\u5DF2\u5B9A\uFF0C\u8EAB\u5B50\u4E00\u8F6C\uFF0C\u521A\u60F3\u6380\u5F00\u8F66\u5EC9\uFF0C\u767D\u6C0F\u59D0\u59B9\u5374\u5FFD\u7136\u51FA\u624B\uFF0C\u6263\u4F4F\u5979\u7684\u8109\u95E8\u3002</p><p>\u7D2B\u73AB\u79C0\u76EE\u751F\u5BD2\uFF0C\u300C\u8D31\u5A62\uFF0C\u60F3\u9020\u53CD\u5417\uFF01\u300D</p><p>\u79EF\u5A01\u4E4B\u4E0B\uFF0C\u767D\u7389\u9E42\u624B\u6307\u4E0D\u7531\u5FAE\u5FAE\u53D1\u6296\uFF0C\u767D\u7389\u83BA\u4E5F\u7D27\u5F20\u5F97\u8138\u65E0\u8840\u8272\uFF0C\u5979\u5438\u4E86\u53E3\u6C14\uFF0C\u538B\u4F4F\u5FC3\u91CC\u7684\u6050\u614C\uFF0C\u300C\u5974\u5A62\u4E0D\u6562\u3002\u53EA\u662F\u6015\u5C11\u592B\u4EBA\u2026\u2026\u8D38\u7136\u51FA\u53BB\uFF0C\u88AB\u4EBA\u8BEF\u4F24\u3002\u300D</p><p>\u300C\u6EDA\u5F00\uFF01\u300D\u7D2B\u73AB\u4E00\u58F0\u4F4E\u559D\u3002\u673A\u4F1A\u8F6C\u77AC\u5373\u901D\uFF0C\u6B64\u65F6\u4E0D\u8D81\u673A\u5E26\u6BCD\u4EB2\u8131\u8EAB\uFF0C\u96BE\u9053\u8FD8\u771F\u8DDF\u7740\u6155\u5BB9\u9F99\u53BB\u627E\u300C\u5B9D\u85CF\u300D\uFF1F</p><p>\u5C82\u77E5\u767D\u6C0F\u59D0\u59B9\u4E5F\u662F\u4E00\u822C\u5FC3\u601D\uFF0C\u53EA\u60F3\u64D2\u4E0B\u661F\u6708\u6E56\u5BAB\u4E3B\u7684\u6BCD\u59B9\u4F5C\u4E3A\u91CD\u5F52\u5E08\u95E8\u7684\u793C\u7269\uFF0C\u5374\u53C8\u4E0D\u6562\u771F\u7684\u52A8\u624B\u3002</p><p>\u50F5\u6301\u95F4\uFF0C\u8F66\u5916\u9F99\u6218\u91CE\u5389\u58F0\u9053\uFF1A\u300C\u5996\u9053\uFF01\u4F55\u6545\u4F24\u6211\u5E08\u5F1F\uFF01\u300D\u8BB8\u72EE\u96C4\u5DF2\u7ECF\u7F62\u6597\u9000\u5F00\uFF0C\u8FD9\u9053\u4EBA\u5374\u8D81\u673A\u65BD\u4EE5\u6697\u7B97\uFF0C\u5351\u9119\u65E0\u803B\uFF0C\u53EF\u89C1\u4E0D\u662F\u597D\u4EBA\u3002</p><p>\u7075\u7389\u6EE1\u4E0D\u5728\u4E4E\uFF0C\u81EA\u5DF1\u4E00\u4E2A\u72EC\u6597\u516B\u6770\uFF0C\u85C9\u673A\u91CD\u4F24\u4E00\u4E2A\u5C31\u5C11\u4E00\u4E2A\u52B2\u654C\uFF0C\u53EA\u8981\u80FD\u62A4\u4F4F\u8427\u4F5B\u5974\u548C\u6155\u5BB9\u7D2B\u73AB\uFF0C\u518D\u5351\u9119\u7684\u624B\u6BB5\u4ED6\u4E5F\u65BD\u5F97\u51FA\u6765\u3002</p><p>\u7A33\u4F4F\u5E08\u5F1F\u7684\u6027\u547D\u4E4B\u540E\uFF0C\u675C\u7280\u5065\u5C06\u8BB8\u72EE\u96C4\u9012\u7ED9\u95E8\u4EBA\uFF0C\u957F\u8EAB\u800C\u8D77\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u6211\u6765\u9886\u6559\u9601\u4E0B\u7684\u9AD8\u62DB\u3002\u300D</p><p>\u7075\u7389\u6D12\u7136\u4E00\u7B11\uFF0C\u7F13\u7F13\u9000\u4E86\u4E24\u6B65\uFF0C\u6446\u4E86\u4E2A\u95E8\u6237\u3002</p><p>\u675C\u7280\u5065\u53CC\u81C2\u4E00\u632F\uFF0C\u624B\u4E2D\u5DF2\u591A\u4E86\u4E00\u5BF9\u4E5D\u8282\u97AD\u3002</p><p>\u4E24\u4EBA\u8C01\u4E5F\u4E0D\u6562\u5927\u610F\uFF0C\u5404\u84C4\u52B2\u6C14\uFF0C\u9065\u9065\u76F8\u5BF9\u3002\u7247\u523B\u540E\u675C\u7280\u5065\u8DE8\u51FA\u4E00\u6B65\uFF0C\u6C14\u52BF\u731B\u7136\u6500\u81F3\u5DC5\u5CF0\u3002</p><p>\u573A\u8FB9\u5FFD\u7136\u4F20\u6765\u4E86\u4E00\u4E2A\u58F0\u97F3\uFF0C\u300C\u957F\u8001\u5DF2\u80DC\u4E86\u4E00\u573A\uFF0C\u4E14\u4F11\u606F\u7247\u523B\uFF0C\u8BF7\u77F3\u4F9B\u5949\u4E0E\u675C\u5927\u4FA0\u73A9\u4E00\u573A\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u7B11\u7740\u8D70\u5230\u573A\u8FB9\u3002\u516B\u6781\u95E8\u4F17\u4EBA\u90FD\u53EA\u987E\u76EF\u7740\u573A\u5185\uFF0C\u6D51\u672A\u6CE8\u610F\u4ED6\u4F55\u65F6\u51FA\u73B0\u3002</p><p>\u7075\u7389\u653E\u4E0B\u5FC3\u6765\uFF0C\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u6536\u8D77\u62C2\u5C18\u8F6C\u8EAB\u79BB\u5F00\u3002\u4ED6\u7ADF\u662F\u8BF4\u8D70\u5C31\u8D70\uFF0C\u4E1D\u6BEB\u4E0D\u5728\u610F\u675C\u7280\u5065\u51CC\u5389\u7684\u6740\u6C14\u3002</p><p>\u675C\u7280\u5065\u867D\u607C\u8FD9\u9053\u4EBA\u4E0B\u624B\u6B79\u6BD2\uFF0C\u4F46\u80CC\u540E\u5077\u88AD\u7684\u5351\u9119\u884C\u5F84\u4ED6\u5374\u505A\u4E0D\u51FA\u6765\uFF0C\u51DD\u805A\u7684\u6C14\u52BF\u987F\u65F6\u6CC4\u4E86\u3002</p><p>\u77F3\u880D\u4E45\u7ECF\u6218\u9635\uFF0C\u89C1\u72B6\u7ACB\u523B\u62D4\u5730\u800C\u8D77\uFF0C\u4EBA\u5728\u7A7A\u4E2D\uFF0C\u4FBF\u6325\u51FA\u880D\u5C3E\u97AD\uFF0C\u4E0D\u7ED9\u675C\u7280\u5065\u4E1D\u6BEB\u5598\u606F\u4E4B\u673A\u3002</p><p>\u675C\u7280\u5065\u88AB\u8FD9\u4E2A\u7FAF\u4EBA\u6253\u626E\u7684\u6076\u6C49\u4E00\u756A\u731B\u653B\uFF0C\u4E00\u53E3\u6C14\u59CB\u7EC8\u7F13\u4E0D\u8FC7\u6765\uFF0C\u4E00\u8EAB\u529F\u529B\u53EA\u80FD\u4F7F\u51FA\u4E94\u6210\uFF0C\u6570\u62DB\u5185\u4FBF\u5904\u5728\u4E0B\u98CE\uFF0C\u8FED\u9022\u51F6\u9669\u3002\u77F3\u880D\u5F97\u52BF\u4E0D\u9976\u4EBA\uFF0C\u880D\u5C3E\u97AD\u957F\u51FB\u8FDC\u653B\uFF0C\u62DB\u62DB\u4E0D\u79BB\u8981\u5BB3\u3002</p><p>\u516B\u6781\u95E8\u4F17\u4EBA\u89C1\u52BF\u4E0D\u5999\uFF0C\u518D\u987E\u4E0D\u5F97\u4FA0\u4E49\u9053\uFF0C\u7ACB\u65F6\u4FBF\u8DC3\u51FA\u4E24\u4EBA\uFF0C\u671D\u573A\u4E2D\u6295\u53BB\u3002</p><p>\u661F\u6708\u6E56\u7FA4\u90AA\u6BD5\u81F3\uFF0C\u5F53\u4E0B\u5B89\u5B50\u5B8F\u4E00\u633A\u5F2F\u94A9\uFF0C\u622A\u4F4F\u516B\u6770\u4E2D\u7684\u88D8\u864E\u81E3\u3002\u516B\u6770\u4E2D\u7684\u8001\u4E03\u5415\u9E70\u626C\u521A\u8DC3\u5230\u534A\u7A7A\uFF0C\u7A81\u7136\u8170\u8EAB\u4E00\u626D\uFF0C\u659C\u8155\u53FC\u4F4F\u4E00\u679A\u94A2\u9488\uFF0C\u63A5\u7740\u5F39\u51FA\uFF0C\u6253\u98DE\u4E86\u4E00\u7C92\u98DE\u8757\u77F3\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u8EAB\u4E0A\u6697\u5668\u65E0\u6570\uFF0C\u4F46\u53EA\u6253\u4E86\u4E00\u9488\u4E00\u77F3\u4FBF\u8896\u624B\u800C\u7ACB\u3002\u5415\u9E70\u626C\u6068\u6068\u76EF\u4E86\u4ED6\u4E00\u773C\uFF0C\u63D0\u6C14\u671D\u675C\u7280\u5065\u63A0\u53BB\u3002\u771F\u6C14\u582A\u582A\u8FD0\u884C\u4E00\u5468\uFF0C\u7A81\u7136\u80F8\u53E3\u4E00\u7A92\uFF0C\u91CD\u91CD\u6454\u5728\u5730\u4E0A\u3002</p><p>\u5E2E\u4E2D\u4EE5\u8F7B\u529F\u79F0\u51A0\u7684\u5415\u9E70\u626C\u7ADF\u7136\u4F1A\u6454\u5012\uFF0C\u516B\u6781\u95E8\u4F17\u4EBA\u5C3D\u7686\u5927\u60CA\uFF0C\u53EA\u89C1\u4ED6\u4F0F\u5728\u5730\u4E0A\u4E00\u52A8\u4E0D\u52A8\uFF0C\u5206\u660E\u5DF2\u7136\u6C14\u7EDD\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u4EE5\u6697\u5668\u72EC\u6B65\u6C5F\u6E56\uFF0C\u6295\u5165\u661F\u6708\u6E56\u95E8\u4E0B\u4E4B\u540E\uFF0C\u53C8\u5F97\u5230\u6559\u4E2D\u8BF8\u822C\u6BD2\u7269\uFF0C\u6697\u5668\u5A01\u529B\u500D\u589E\u3002\u4ED6\u5148\u7528\u4E94\u6210\u529F\u529B\u63B7\u51FA\u94A2\u9488\uFF0C\u8BA9\u5415\u9E70\u626C\u80FD\u8F7B\u6613\u63A5\u5230\uFF0C\u7136\u540E\u53C8\u7528\u98DE\u8757\u77F3\u6270\u5176\u5FC3\u795E\uFF0C\u7ED3\u679C\u9760\u7740\u6CBE\u80A4\u7ACB\u6BD9\u7684\u5267\u75DB\u8981\u4E86\u516B\u6770\u4E4B\u4E00\u7684\u6027\u547D\u3002</p><p>\u5C31\u5728\u6B64\u65F6\uFF0C\u675C\u7280\u5065\u4E5F\u5230\u4E86\u5371\u6025\u5173\u5934\u3002\u77F3\u880D\u8D8A\u6218\u8D8A\u52C7\uFF0C\u880D\u5C3E\u97AD\u4E0E\u4E5D\u8282\u97AD\u529B\u62FC\u4E00\u8BB0\uFF0C\u63A5\u7740\u97AD\u5C3E\u5377\u8D77\uFF0C\u5DF2\u7F20\u4F4F\u675C\u7280\u5065\u7684\u53F3\u81C2\u3002\u72C2\u7B11\u58F0\u4E2D\uFF0C\u77F3\u880D\u62AC\u8155\u4E00\u626F\uFF0C\u675C\u7280\u5065\u81C2\u4E0A\u8840\u8089\u6A2A\u98DE\uFF0C\u7531\u80A9\u81F3\u8155\u53EA\u5269\u4E0B\u5149\u6E9C\u6E9C\u4E00\u622A\u767D\u9AA8\u3002</p><p>\u7247\u523B\u95F4\u4FBF\u6709\u4E09\u540D\u5E08\u5F1F\u88AB\u4EBA\u7528\u5351\u9119\u624B\u6BB5\u6240\u4F24\uFF0C\u9F99\u6218\u91CE\u76EE\u7726\u6B32\u88C2\uFF0C\u66B4\u559D\u4E00\u58F0\uFF0C\u626C\u8D77\u7EAF\u94A2\u6253\u5236\u7684\u9752\u9F99\u5173\u5200\uFF0C\u65CB\u98CE\u822C\u51B2\u5165\u573A\u5185\u3002\u5200\u5149\u95EA\u52A8\u5904\uFF0C\u77F3\u880D\u3001\u5B89\u5B50\u5B8F\u7EB7\u7EB7\u9000\u5F00\u3002</p><p>\u9F99\u6218\u91CE\u6321\u5728\u675C\u7280\u5065\u3001\u5415\u9E70\u626C\u7B49\u4EBA\u8EAB\u524D\uFF0C\u6012\u543C\u9053\uFF1A\u300C\u65E0\u803B\u8D3C\u5B50\uFF01\u6709\u79CD\u4E0E\u7237\u7237\u6597\u4E0A\u4E00\u573A\uFF01\u300D</p><p>\u957F\u8349\u5728\u543C\u58F0\u4E2D\u8D77\u4F0F\u4E0D\u5B9A\uFF0C\u6296\u843D\u6EE1\u539F\u8840\u7EA2\u7684\u5915\u8F89\u3002</p>',62);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
