import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_86" tabindex="-1"><a class="header-anchor" href="#_86" aria-hidden="true">#</a> 86</h1><p>\u6155\u5BB9\u9F99\u6CB9\u7136\u4E0A\u524D\uFF0C\u7B11\u9053\uFF1A\u300C\u4EE5\u6B66\u4F1A\u53CB\uFF0C\u8BEF\u4F24\u96BE\u514D\u3002\u8D35\u5E08\u5F1F\u5B66\u827A\u4E0D\u7CBE\uFF0C\u8FD9\u51E0\u4F4D\u670B\u53CB\u4E5F\u662F\u597D\u5FC3\u70B9\u62E8\u4E00\u756A\u3002\u9F99\u638C\u95E8\u4F55\u5FC5\u52A8\u6012\uFF1F\u300D</p><p>\u300C\u5478\uFF01\u300D\u9F99\u6218\u91CE\u72E0\u72E0\u5550\u4E86\u4E00\u53E3\uFF0C\u300C\u5C11\u6765\u8FD9\u4E9B\u82B1\u8A00\u5DE7\u8BED\uFF0C\u6765\u5C1D\u5C1D\u7237\u7237\u5173\u5200\u7684\u5389\u5BB3\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5BF9\u4ED6\u7684\u6012\u543C\u4E0D\u4EE5\u4E3A\u610F\uFF0C\u53CD\u800C\u671B\u7740\u9F99\u6218\u91CE\u8EAB\u540E\uFF0C\u7B11\u541F\u541F\u9053\uFF1A\u300C\u90A3\u4F4D\u662F\u5C0A\u592B\u4EBA\u5427\u3002\u54C8\u54C8\uFF0C\u597D\u4E00\u4E2A\u7F8E\u5987\u4EBA\u3002\u300D\u4E0D\u7B49\u9F99\u6218\u91CE\u6012\u9A82\uFF0C\u4ED6\u7A81\u7136\u6536\u8D77\u5B09\u7B11\uFF0C\u6B63\u5BB9\u9053\uFF1A\u300C\u672C\u5BAB\u4E0E\u9F99\u638C\u95E8\u6BD4\u8BD5\u4E00\u573A\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u9F99\u6218\u91CE\u6301\u5200\u800C\u7ACB\uFF0C\u633A\u80F8\u9053\uFF1A\u300C\u6765\u5427\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6447\u6447\u5934\uFF0C\u300C\u9F99\u638C\u95E8\u8BEF\u4F1A\u4E86\u3002\u672C\u5BAB\u7684\u610F\u601D\u662F\uFF1A\u4F60\u6211\u5404\u4E0E\u5C0A\u592B\u4EBA\u6597\u4E0A\u4E00\u573A\uFF0C\u770B\u770B\u5F7C\u6B64\u7684\u9E21\u5DF4\u8C01\u786C\u8C01\u8F6F\u3002\u300D</p><p>\u9F99\u6218\u91CE\u8EAB\u4E3A\u4E00\u6D3E\u638C\u95E8\uFF0C\u5B9E\u662F\u7C97\u4E2D\u6709\u7EC6\u7684\u6C5F\u6E56\u8C6A\u5BA2\uFF0C\u89C1\u8FD9\u4E2A\u72C2\u5F92\u51FA\u53E3\u5982\u6B64\u4E0B\u6D41\uFF0C\u4E00\u526F\u5403\u5B9A\u81EA\u5DF1\u7684\u6837\u5B50\uFF0C\u53CD\u800C\u6C89\u4E0B\u6C14\u6765\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u4F60\u7A76\u7ADF\u662F\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u300C\u8FD9\u662F\u6211\u661F\u6708\u6E56\u6155\u5BB9\u5BAB\u4E3B\u3002\u300D\u4E00\u6761\u5927\u6C49\u7F13\u7F13\u8D70\u51FA\uFF0C\u767D\u8863\u94F6\u5E26\uFF0C\u864E\u6B65\u9F99\u884C\uFF0C\u6B63\u662F\u661F\u6708\u6E56\u9996\u5E2D\u957F\u8001\u91D1\u5F00\u7532\u3002</p><p>\u5915\u9633\u5DF2\u843D\u5728\u8349\u539F\u5C3D\u5934\uFF0C\u730E\u730E\u79CB\u98CE\u4E2D\uFF0C\u624B\u63D0\u94DC\u8F6E\u5DE8\u65A7\u7684\u91D1\u5F00\u7532\u6012\u53D1\u98DE\u626C\uFF0C\u72B6\u5982\u5929\u795E\u3002</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u516B\u6781\u95E8\u540D\u626C\u5929\u4E0B\uFF0C\u53EF\u60DC\u4ECA\u65E5\u8981\u5728\u8FD9\u585E\u5317\u5168\u519B\u8986\u6CA1\uFF0C\u9F99\u7280\u72EE\u8C61\u3001\u864E\u8C79\u9E70\u72FC\uFF0C\u516B\u6770\u5C3D\u6570\u8840\u67D3\u8352\u8349\uFF0C\u53EF\u4F9B\u4E00\u53F9\u3002\u300D</p><p>\u867D\u7136\u7280\u3001\u72EE\u3001\u9E70\u4E00\u6B7B\u4E24\u4F24\uFF0C\u516B\u6770\u5DF2\u53BB\u5176\u4E09\uFF0C\u4F46\u516B\u6781\u95E8\u6BD4\u661F\u6708\u6E56\u4ECD\u591A\u4E0A\u5341\u4F59\u4EBA\uFF0C\u6155\u5BB9\u9F99\u5982\u6B64\u5927\u8A00\u4E0D\u60ED\uFF0C\u4F17\u4EBA\u987F\u65F6\u6012\u53EB\u8FDE\u58F0\u3002</p><p>\u300C\u2026\u2026\u56DB\u5341\u4E94\u3001\u56DB\u5341\u516D\u3002\u55EF\uFF0C\u8FD8\u6709\u56DB\u5341\u516D\u4EBA\uFF0C\u9F50\u638C\u95E8\u4E3A\u4F55\u4E0D\u628A\u5F1F\u5B50\u5168\u90E8\u5E26\u6765\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u626C\u8138\u76D8\u7B97\u9053\uFF0C\u300C\u672C\u5BAB\u8FD8\u8981\u5343\u91CC\u8FE2\u8FE2\u8D76\u8D74\u5B89\u5B9A\u5C06\u8D35\u95E8\u6740\u5F97\u9E21\u72AC\u4E0D\u7559\u3002\u5B9E\u5728\u9EBB\u70E6\u3002\u300D</p><p>\u9F99\u6714\u5C0F\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u7239\u7239\u6253\u4E0D\u8FC7\u4ED6\u4EEC\u5417\uFF1F\u300D</p><p>\u5510\u989C\u4FCF\u8138\u96EA\u767D\uFF0C\u5C06\u513F\u5B50\u7D27\u7D27\u6402\u5728\u6000\u4E2D\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6709\u4F60\u7239\u7239\u548C\u8BF8\u4F4D\u53D4\u53D4\uFF0C\u7EDD\u4E0D\u4F1A\u8F93\u7684\u3002\u300D\u8BDD\u867D\u5982\u6B64\uFF0C\u5979\u7684\u58F0\u97F3\u5DF2\u6709\u4E9B\u53D1\u98A4\u3002\u51E0\u573A\u6076\u6597\u4E0B\u6765\uFF0C\u5510\u989C\u5DF2\u7ECF\u770B\u51FA\u8FD9\u4E9B\u4EBA\u4E0D\u4F46\u6B66\u529F\u6A2A\u5F3A\uFF0C\u800C\u4E14\u884C\u4E8B\u4E0D\u62E9\u624B\u6BB5\uFF0C\u9634\u9669\u6BD2\u8FA3\uFF0C\u5351\u9119\u65E0\u803B\u4E4B\u6781\u3002\u867D\u7136\u4E08\u592B\u5929\u751F\u795E\u6B66\uFF0C\u4F46\u80FD\u4E0D\u80FD\u6321\u4F4F\u654C\u4EBA\u7684\u767E\u822C\u8BE1\u8BA1\uFF0C\u5B9E\u5728\u96BE\u8BF4\u5F97\u5F88\u3002</p><p>\u5979\u5FC3\u91CC\u6697\u6697\u540E\u6094\uFF0C\u8FD9\u8D9F\u5B9E\u5728\u4E0D\u8BE5\u5E26\u513F\u5B50\u51FA\u6765\u3002\u81F3\u4E8E\u81EA\u5DF1\u2026\u2026\u5979\u6309\u4E86\u6309\u8170\u95F4\u7684\u4F69\u5251\uFF0C\u770B\u4E86\u4E08\u592B\u4E00\u773C\u3002\u9F99\u6218\u91CE\u9AD8\u5927\u7684\u8EAB\u8EAF\u6602\u7136\u633A\u7ACB\uFF0C\u4EFF\u4F5B\u4E0D\u53EF\u6218\u80DC\u7684\u5DE8\u4EBA\uFF0C\u5C11\u5987\u987F\u65F6\u5B89\u4E0B\u5FC3\u6765\uFF0C\u5BF9\u513F\u5B50\u8BF4\uFF1A\u300C\u7EDD\u4E0D\u4F1A\u8F93\u7684\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u62CD\u989D\u5934\uFF0C\u300C\u7ADF\u7136\u5FD8\u4E86\uFF0C\u8FD8\u6709\u4E24\u4F4D\u8D35\u95E8\u5F1F\u5B50\u2026\u2026\u83BA\u5974\u9E42\u5974\uFF0C\u6276\u7740\u592B\u4EBA\u51FA\u6765\u3002\u8FD8\u6709\u59B9\u59B9\uFF0C\u4F60\u4E5F\u51FA\u6765\uFF0C\u770B\u54E5\u54E5\u600E\u4E48\u628A\u516B\u6781\u95E8\u6740\u5F97\u5E72\u5E72\u51C0\u51C0\u3002\u300D</p><p>\u53C8\u4E00\u6B21\u673A\u4F1A\u846C\u9001\u5728\u8FD9\u4E24\u4E2A\u8D31\u4EBA\u624B\u4E2D\uFF0C\u7D2B\u73AB\u6068\u5F97\u54AC\u7259\u5207\u9F7F\uFF0C\u624B\u4E00\u7529\uFF0C\u6380\u5F00\u8F66\u5EC9\u3002</p><p>\u542C\u89C1\u5BAB\u4E3B\u7684\u58F0\u97F3\uFF0C\u767D\u6C0F\u59D0\u59B9\u6EE1\u5FC3\u7684\u5E0C\u5180\u7ACB\u65F6\u5316\u4E3A\u6CE1\u5F71\u3002\u59D0\u59B9\u4FE9\u76F8\u987E\u65E0\u8A00\uFF0C\u5FC3\u5934\u53C8\u9178\u53C8\u82E6\uFF0C\u767D\u7389\u9E42\u66F4\u662F\u6CEA\u6E7F\u8863\u895F\u3002\u6B64\u65F6\u542C\u5230\u5429\u5490\uFF0C\u7EB5\u7136\u767E\u822C\u4E0D\u60C5\u613F\u4E0E\u5E08\u95E8\u76F8\u89C1\uFF0C\u4E24\u5973\u4E5F\u53EA\u80FD\u62ED\u6CEA\u8D77\u8EAB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7F13\u6B65\u4E0B\u8F66\uFF0C\u73AB\u7470\u4ED9\u5B50\u5A40\u5A1C\u751F\u59FF\u7684\u7F8E\u6001\uFF0C\u4F7F\u4F17\u4EBA\u773C\u524D\u5747\u662F\u4E00\u4EAE\u3002\u8F66\u65C1\u65E9\u6709\u5E2E\u4F17\u94FA\u4E0A\u6BE1\u6BEF\uFF0C\u6155\u5BB9\u9F99\u76D8\u819D\u5750\u5728\u6BEF\u4E0A\uFF0C\u62C9\u4F4F\u7D2B\u73AB\u7684\u5C0F\u624B\u7B11\u9053\uFF1A\u300C\u5A18\u5B50\u8BF7\u5750\u3002\u300D</p><p>\u8F66\u5EC9\u53C8\u6709\u662F\u4E00\u52A8\uFF0C\u4E24\u540D\u82B1\u679D\u822C\u7684\u5C11\u5973\u6276\u7740\u4E00\u4E2A\u67D4\u5F31\u7684\u7F8E\u5987\u8D70\u4E86\u51FA\u6765\u3002\u7F8E\u5987\u7684\u76F8\u8C8C\u4E0E\u73AB\u7470\u4ED9\u5B50\u6709\u516B\u5206\u76F8\u4F3C\uFF0C\u4F46\u90A3\u79CD\u96CD\u5BB9\u534E\u8D35\u53C8\u59A9\u5A9A\u5A07\u8273\u7684\u98CE\u97F5\uFF0C\u5374\u6BD4\u73AB\u7470\u4ED9\u5B50\u80DC\u4E0A\u4E00\u7B79\uFF0C\u5C24\u5176\u662F\u8F6F\u7EF5\u7EF5\u624B\u811A\u7684\u6BEB\u65E0\u529B\u9053\uFF0C\u8BA9\u4EBA\u4E00\u89C1\u4FBF\u5FC3\u751F\u601C\u7231\u3002</p><p>\u5510\u989C\u4E3E\u76EE\u770B\u53BB\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u5C0F\u83BA\u5C0F\u9E42\uFF01\u300D\u8FD9\u4E24\u4E2A\u5F92\u513F\u534A\u5E74\u524D\u56DE\u5BB6\u4E4B\u540E\u4FBF\u518D\u65E0\u6D88\u606F\uFF0C\u4E0D\u66FE\u60F3\u5374\u4F1A\u5728\u8FD9\u91CC\u51FA\u73B0\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u7C89\u9888\u4F4E\u5782\uFF0C\u653E\u4E0B\u8427\u4F5B\u5974\u540E\uFF0C\u4E24\u5973\u4FBF\u9ED8\u4E0D\u4F5C\u58F0\u5730\u8DEA\u5728\u4E00\u65C1\uFF0C\u4E0D\u6562\u5411\u66FE\u7ECF\u671D\u5915\u76F8\u5904\u7684\u540C\u95E8\u770B\u4E0A\u4E00\u773C\u3002</p><p>\u516B\u6781\u95E8\u4F17\u4EBA\u5927\u611F\u8BB6\u5F02\uFF0C\u767D\u6C0F\u59D0\u59B9\u5A07\u7F8E\u53EF\u7231\uFF0C\u6DF1\u4E3A\u540C\u95E8\u6240\u559C\u7231\uFF0C\u6B64\u65F6\u89C1\u59D0\u59B9\u4FE9\u5C48\u819D\u670D\u4F8D\u5B9B\u5982\u5974\u5A62\uFF0C\u4F17\u4EBA\u53C8\u662F\u4E00\u9635\u55A7\u54D7\uFF0C\u8463\u8C79\u5A01\u9AD8\u58F0\u53EB\u9053\uFF1A\u300C\u767D\u7389\u83BA\u767D\u7389\u9E42\uFF01\u4F60\u4EEC\u7ED9\u6211\u8FC7\u6765\uFF01\u300D</p><p>\u59D0\u59B9\u4FE9\u9759\u9759\u8DEA\u5728\u6155\u5BB9\u9F99\u8EAB\u540E\uFF0C\u8C01\u4E5F\u6CA1\u6709\u62AC\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u8D35\u5F1F\u5B50\u5DF2\u5165\u6211\u795E\u6559\u4E3A\u5974\uFF0C\u53EA\u6015\u4E0D\u4F1A\u542C\u8463\u5927\u4FA0\u5429\u5490\u4E86\u3002\u8D31\u5974\uFF0C\u4F60\u4EEC\u8BF4\u5462\uFF1F\u300D</p><p>\u300C\u662F\u3002\u300D\u767D\u6C0F\u59D0\u59B9\u4F4E\u58F0\u8BF4\u3002</p><p>\u300C\u5927\u4E9B\u58F0\uFF0C\u544A\u8BC9\u4F60\u5E08\u7236\u5E08\u53D4\uFF0C\u8FD8\u6709\u5E08\u5A18\u3002\u300D</p><p>\u4E24\u5973\u8138\u8272\u82CD\u767D\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5F1F\u5B50\u5DF2\u5165\u795E\u6559\u4E3A\u5974\uFF0C\u7EC8\u8EAB\u4F8D\u5949\u5BAB\u4E3B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u60A0\u7136\u770B\u7740\u516B\u6781\u95E8\u4F17\u4EBA\uFF0C\u5FC3\u91CC\u6697\u9053\uFF1A\u516B\u6781\u95E8\u4EBA\u591A\u52BF\u4F17\uFF0C\u52A8\u8D77\u624B\u6765\u5B8C\u80DC\u4E5F\u4E0D\u5BB9\u6613\uFF0C\u60F3\u5230\u8FD9\u91CC\uFF0C\u4ED6\u6DE1\u7B11\u9053\uFF1A\u300C\u4F60\u4EEC\u53EA\u662F\u5BAB\u4E2D\u8D31\u5974\uFF0C\u4F8D\u5949\u7684\u53EF\u4E0D\u6B62\u662F\u672C\u5BAB\u3002\u300D</p><p>\u4E24\u5973\u8EAB\u5B50\u4E00\u50F5\uFF0C\u53EA\u542C\u5BAB\u4E3B\u6DE1\u6DE1\u9053\uFF1A\u300C\u8863\u670D\u8131\u4E86\uFF0C\u6C42\u6559\u91CC\u7684\u4E3B\u5B50\u4EEC\u53BB\u64CD\u4F60\u4EEC\u4E24\u4E2A\u3002\u300D</p><p>\u573A\u4E2D\u987F\u65F6\u5BC2\u65E0\u58F0\u606F\uFF0C\u6240\u6709\u4EBA\u7684\u76EE\u5149\u90FD\u671B\u5411\u8FD9\u5BF9\u9C9C\u82B1\u822C\u7684\u59D0\u59B9\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u73E0\u6CEA\u6EDA\u6D8C\uFF0C\u826F\u4E45\uFF0C\u767D\u7389\u83BA\u62AC\u624B\u89E3\u5F00\u895F\u53E3\u3002</p><p>\u4E00\u80A1\u51CC\u5389\u7684\u6C14\u52BF\u72C2\u6D8C\u800C\u6765\u3002\u9F99\u6218\u91CE\u671D\u524D\u8DE8\u4E86\u4E00\u6B65\uFF0C\u63E1\u7740\u9752\u9F99\u5173\u5200\u7684\u624B\u81C2\u808C\u8089\u866C\u7ED3\uFF0C\u5B9B\u5982\u94C1\u94F8\u3002\u767E\u6218\u5929\u9F99\u5389\u58F0\u9053\uFF1A\u300C\u5996\u5B7D\uFF01\u5403\u6211\u4E00\u5200\uFF01\u300D\u8BF4\u7740\u5173\u5200\u5316\u4F5C\u4E00\u9053\u98DE\u9F99\uFF0C\u5E26\u7740\u6A2A\u626B\u5343\u519B\u7684\u6C14\u52BF\uFF0C\u76F4\u5954\u6155\u5BB9\u9F99\u3002</p><p>\u300C\u94DB\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u8349\u539F\u4E5F\u4EFF\u4F5B\u4E3A\u4E4B\u9707\u52A8\u3002\u4E00\u67C4\u94DC\u8F6E\u5DE8\u65A7\u500F\u5FFD\u4ECE\u534A\u9014\u8DC3\u51FA\uFF0C\u622A\u4F4F\u5173\u5200\u3002\u9F99\u6218\u91CE\u4E0E\u91D1\u5F00\u7532\u786C\u62FC\u4E00\u8BB0\uFF0C\u4E24\u4EBA\u5404\u9000\u4E00\u6B65\uFF0C\u5747\u89C9\u6C14\u8840\u7FFB\u6D8C\u3002</p><p>\u91D1\u5F00\u7532\u5411\u540E\u9000\u5F00\uFF0C\u624B\u62C4\u94DC\u65A7\u5C48\u4E0B\u4E00\u819D\uFF0C\u9AD8\u58F0\u9053\uFF1A\u300C\u661F\u6708\u6E56\u91D1\u5802\u957F\u8001\u91D1\u5F00\u7532\uFF0C\u6073\u8BF7\u5BAB\u4E3B\u8D50\u6218\u3002\u300D</p><p>\u91D1\u5F00\u7532\u4E0E\u6C90\u58F0\u4F20\u79C1\u4E0B\u5546\u8BAE\u8FC7\uFF0C\u6015\u5BAB\u4E3B\u5E74\u8F7B\u4E0D\u80FD\u670D\u4F17\uFF0C\u56E0\u6B64\u4E24\u4EBA\u5728\u5E2E\u4F17\u9762\u524D\u90FD\u6267\u793C\u6781\u606D\uFF0C\u4EE5\u6811\u7ACB\u6155\u5BB9\u9F99\u7684\u5C0A\u4E25\u3002</p><p>\u6155\u5BB9\u9F99\u6536\u8D77\u8138\u4E0A\u7684\u7B11\u610F\uFF0C\u4E24\u624B\u6309\u819D\u633A\u76F4\u8170\u8EAB\uFF0C\u8083\u5BB9\u9053\uFF1A\u300C\u5C31\u7531\u91D1\u957F\u8001\u51FA\u6218\uFF0C\u4E3A\u795E\u6559\u8BDB\u6740\u767E\u6218\u5929\u9F99\uFF01\u300D</p><p>\u300C\u8C22\u5BAB\u4E3B\uFF01\u300D\u91D1\u5F00\u7532\u96C4\u8EAF\u4E00\u633A\uFF0C\u671B\u5411\u9F99\u6218\u91CE\u3002</p><p>\u7075\u7389\u4E00\u64A9\u9053\u888D\uFF0C\u77EE\u8EAB\u5C48\u819D\u9053\uFF1A\u300C\u661F\u6708\u6E56\u6728\u5802\u957F\u8001\u7075\u7389\uFF0C\u613F\u53D6\u516B\u6781\u95E8\u532A\u7C7B\u9996\u7EA7\uFF0C\u6073\u8BF7\u5BAB\u4E3B\u8D50\u6218\uFF01\u300D</p><p>\u300C\u5982\u957F\u8001\u6240\u8BF7\u3002\u300D</p><p>\u300C\u661F\u6708\u6E56\u4F9B\u5949\u5B89\u5B50\u5B8F\uFF0C\u6073\u8BF7\u51FA\u6218\u3002\u300D</p><p>\u300C\u661F\u6708\u6E56\u4F9B\u5949\u77F3\u880D\uFF0C\u6073\u8BF7\u51FA\u6218\u2026\u2026\u300D</p><p>\u516B\u6781\u95E8\u7FA4\u96C4\u5404\u81EA\u63E1\u7D27\u5175\u5203\uFF0C\u773C\u89C1\u8FD9\u7FA4\u90AA\u6C14\u8FEB\u4EBA\u7684\u51F6\u5F92\u4E00\u4E00\u65BD\u793C\u8BF7\u6218\uFF0C\u90FD\u662F\u5FC3\u5934\u6697\u60CA\u3002\u5510\u989C\u968F\u4E08\u592B\u95EF\u8361\u591A\u5E74\uFF0C\u89C1\u95FB\u5E7F\u535A\uFF0C\u65E9\u5DF2\u542C\u8FC7\u7075\u7389\u3001\u5B89\u5B50\u5B8F\u3001\u77F3\u880D\u7B49\u4EBA\u7684\u540D\u5934\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E9B\u6A2A\u884C\u4E00\u65B9\u7684\u72C2\u5F92\u7ADF\u7136\u90FD\u662F\u661F\u6708\u6E56\u95E8\u4E0B\u3002</p><p>\u9F99\u6714\u611F\u89C9\u5230\u6BCD\u4EB2\u7684\u60CA\u60E7\uFF0C\u626C\u8138\u95EE\u9053\uFF1A\u300C\u5A18\uFF0C\u4ED6\u4EEC\u5728\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u2026\u2026\u4ED6\u4EEC\u8981\u8DDF\u54B1\u4EEC\u516B\u6781\u95E8\u6BD4\u6B66\u2026\u2026\u300D</p><p>\u7537\u5B69\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u4E0D\u60C5\u613F\uFF0C\u534A\u664C\u540E\u4ED6\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5A18\uFF0C\u4ED6\u4EEC\u597D\u50CF\u5F88\u5389\u5BB3\u2026\u2026\u300D</p><p>\u5510\u989C\u52C9\u5F3A\u7B11\u4E86\u4E00\u4E0B\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u6714\u513F\uFF0C\u4E0D\u8981\u6015\uFF0C\u8C01\u90FD\u6253\u4E0D\u8D62\u4F60\u7239\u7239\u2026\u2026\u300D\u5979\u7D27\u7D27\u76EF\u7740\u91D1\u5F00\u7532\u7684\u811A\u6B65\uFF0C\u5FC3\u91CC\u7D27\u5F20\u5F97\u50CF\u8981\u70B8\u5F00\u4E00\u822C\u3002\u8FD9\u4EBA\u6BCF\u4E00\u6B65\u8FC8\u51FA\u90FD\u662F\u4E09\u5C3A\u4E00\u5BF8\uFF0C\u843D\u5730\u867D\u7136\u6C89\u7A33\uFF0C\u4F46\u811A\u4E0B\u7684\u9752\u8349\u6CA1\u6709\u4E00\u6839\u88AB\u8E29\u6298\u7684\uFF0C\u96BE\u9053\u4ED6\u7ADF\u7136\u7531\u81F3\u521A\u7EC3\u5230\u4E86\u81F3\u67D4\u7684\u5883\u754C\u2026\u2026</p><p>\u9F99\u6218\u91CE\u5374\u6CA1\u6709\u7559\u5FC3\u4ED6\u7684\u6B65\u4F10\uFF0C\u8FD9\u4E2A\u661F\u6708\u6E56\u957F\u8001\u540D\u58F0\u5E76\u4E0D\u5F70\u663E\uFF0C\u4F46\u8EAB\u4E0A\u6563\u53D1\u7684\u8FEB\u4EBA\u9738\u6C14\uFF0C\u5374\u662F\u4ED6\u751F\u5E73\u4EC5\u89C1\u3002\u5982\u6B64\u654C\u624B\u4E00\u4E16\u96BE\u9022\uFF01\u9F99\u6218\u91CE\u8C6A\u60C5\u5927\u53D1\uFF0C\u5173\u5200\u4E00\u62A1\uFF0C\u5468\u56F4\u4E08\u8BB8\u65B9\u5706\u7684\u957F\u8349\u5C3D\u88AB\u5200\u6C14\u6467\u6298\uFF0C\u679D\u53F6\u7EB7\u98DE\u3002</p><p>\u91D1\u5F00\u7532\u72EC\u76EE\u7CBE\u5149\u5267\u76DB\uFF0C\u94DC\u65A7\u94FF\u7136\u6325\u51FA\u3002</p><p>\u767D\u7389\u83BA\u5DF2\u7ECF\u89E3\u5F00\u8863\u886B\uFF0C\u9732\u51FA\u7C89\u5AE9\u7684\u5A07\u8EAF\u3002\u7D2B\u73AB\u6DF1\u6068\u4E24\u5973\uFF0C\u53EA\u4FA7\u5750\u6BE1\u4E0A\uFF0C\u4E0D\u7406\u4E0D\u776C\u3002\u8427\u4F5B\u5974\u5FC3\u4E0B\u4E0D\u5FCD\uFF0C\u6084\u6084\u770B\u4E86\u770B\u513F\u5B50\u7684\u8138\u8272\uFF0C\u4E0D\u6562\u4F5C\u58F0\u3002</p><p>\u5510\u989C\u5FCD\u4E0D\u4F4F\u5A07\u559D\u9053\uFF1A\u300C\u5C0F\u83BA\u5C0F\u9E42\uFF01\u4E07\u4E8B\u6709\u5E08\u7236\u7ED9\u4F60\u4EEC\u505A\u4E3B\uFF0C\u8D76\u5FEB\u56DE\u6765\u3002\u300D</p><p>\u767D\u7389\u9E42\u634F\u7740\u80F8\u53E3\u7684\u8863\u895F\uFF0C\u53EB\u4E86\u58F0\uFF1A\u300C\u5E08\u5A18\u2026\u2026\u300D\u4FBF\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u4E00\u540D\u661F\u6708\u6E56\u5E2E\u4F17\u4E00\u811A\u8E29\u4F4F\u767D\u7389\u83BA\u7684\u540E\u9888\uFF0C\u5C06\u4EB5\u88E4\u626F\u5F97\u7C89\u788E\uFF0C\u7136\u540E\u7ACB\u5728\u8DEA\u4F0F\u7684\u5C11\u5973\u8EAB\u540E\uFF0C\u62B1\u7740\u7C89\u81C0\u633A\u8EAB\u523A\u5165\u3002\u767D\u7389\u83BA\u957F\u53D1\u8986\u9762\uFF0C\u80A9\u5934\u4E0D\u4F4F\u62BD\u52A8\u3002</p><p>\u5510\u989C\u6342\u4F4F\u513F\u5B50\u7684\u773C\u775B\uFF0C\u5FC3\u5934\u4E00\u9635\u523A\u75DB\u3002\u516B\u6781\u95E8\u4E2D\u6709\u4E0D\u5C11\u5E74\u8F7B\u5B50\u5F1F\u6697\u604B\u59D0\u59B9\u4FE9\uFF0C\u6012\u9A82\u58F0\u4E2D\uFF0C\u5341\u51E0\u540D\u5F1F\u5B50\u98DE\u8EAB\u800C\u51FA\uFF0C\u8981\u5C06\u8FD9\u7FA4\u79BD\u517D\u788E\u5C38\u4E07\u6BB5\u3002</p><p>\u7075\u7389\u7B49\u4EBA\u5E76\u80A9\u800C\u4E0A\uFF0C\u4E0E\u8C61\u3001\u864E\u3001\u8C79\u3001\u72FC\u56DB\u6770\u6218\u6210\u4E00\u56E2\u3002\u4E5E\u4F0F\u7A77\u9686\u3001\u8840\u65A9\u53CC\u715E\u5219\u671D\u4E24\u7FFC\u7684\u516B\u6781\u95E8\u5F1F\u5B50\u51B2\u53BB\uFF0C\u832B\u832B\u8349\u539F\u987F\u65F6\u6380\u8D77\u4E00\u7247\u5200\u5149\u5251\u5F71\uFF0C\u8840\u96E8\u8165\u98CE\u3002</p><p>\u5510\u989C\u6709\u5FC3\u4E0A\u524D\u6740\u654C\uFF0C\u53C8\u653E\u4E0D\u4E0B\u513F\u5B50\uFF0C\u5728\u9635\u540E\u8E0C\u8E87\u4E0D\u5DF2\u3002\u7537\u5B69\u4EAE\u6676\u6676\u7684\u5927\u773C\u5728\u8840\u8089\u6A2A\u98DE\u7684\u6218\u573A\u626B\u6765\u626B\u53BB\uFF0C\u5174\u594B\u4E2D\u8FD8\u5E26\u7740\u4E00\u4E1D\u6050\u60E7\u3002\u4ED6\u7D27\u7D27\u64E4\u7740\u5C0F\u62F3\u5934\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5A18\uFF0C\u516D\u5E08\u53D4\u53D7\u4F24\u4E86\u3002\u300D</p><p>\u5510\u989C\u4E00\u54AC\u7259\uFF0C\u62B1\u7740\u513F\u5B50\u7FFB\u8EAB\u4E0B\u9A6C\uFF0C\u8E72\u8EAB\u8BF4\uFF1A\u300C\u6714\u513F\u522B\u6015\uFF0C\u5A18\u53BB\u5E2E\u4F60\u7239\u7239\u6740\u654C\u3002\u300D</p><p>\u9F99\u6714\u575A\u5B9A\u5730\u70B9\u70B9\u5934\u3002\u5510\u989C\u89C1\u513F\u5B50\u5982\u6B64\u61C2\u4E8B\uFF0C\u4E0D\u7981\u5FC3\u91CC\u4E00\u9178\uFF0C\u5979\u5429\u5490\u4E24\u540D\u5973\u5F1F\u5B50\u5728\u65C1\u770B\u62A4\uFF0C\u60F3\u4E86\u60F3\uFF0C\u53C8\u4ECE\u6000\u4E2D\u638F\u51FA\u4E00\u628A\u5C0F\u5315\u9996\u653E\u5728\u513F\u5B50\u624B\u91CC\u63E1\u597D\uFF0C\u8FD9\u624D\u63A0\u5411\u6218\u573A\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
