import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_87" tabindex="-1"><a class="header-anchor" href="#_87" aria-hidden="true">#</a> 87</h1><p>\u4E24\u8FB9\u752B\u4E00\u76F8\u9047\uFF0C\u9AD8\u4E0B\u7ACB\u5206\u3002\u516B\u6781\u95E8\u5F1F\u5B50\u4ECE\u56DB\u9762\u516B\u65B9\u4E00\u7A9D\u8702\u671D\u767D\u6C0F\u59D0\u59B9\u6D8C\u53BB\uFF0C\u6839\u672C\u6CA1\u6709\u5217\u6210\u6218\u9635\u5F7C\u6B64\u63A9\u62A4\u3002\u56DB\u6770\u88AB\u7075\u7389\u7B49\u4EBA\u7F20\u4F4F\uFF0C\u81EA\u987E\u4E0D\u6687\uFF0C\u53EA\u80FD\u9AD8\u53EB\u7740\u6307\u70B9\u95E8\u5F92\u5C0F\u5FC3\u3002</p><p>\u5FC3\u4E0A\u4EBA\u5728\u773C\u524D\u8D64\u88F8\u88F8\u88AB\u4EBA\u6DEB\u8FB1\uFF0C\u51B2\u5728\u6700\u524D\u9762\u7684\u5341\u51E0\u4EBA\u90FD\u7EA2\u4E86\u773C\u775B\uFF0C\u72C2\u6012\u4E4B\u4E0B\u771F\u6709\u5F53\u8005\u62AB\u9761\u7684\u9510\u6C14\u3002\u4F46\u6155\u5BB9\u9F99\u6015\u7684\u4E0D\u662F\u4ED6\u4EEC\u66B4\u6012\uFF0C\u800C\u662F\u6015\u8FD9\u4E9B\u4EBA\u4E0D\u6765\u2014\u2014\u5728\u8349\u539F\u4E0A\u8FFD\u4EA1\u9010\u5317\u53EF\u662F\u4E2A\u4F53\u529B\u6D3B\u3002\u4ED6\u54C8\u54C8\u4E00\u7B11\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u5429\u5490\u9053\uFF1A\u300C\u4F7F\u51FA\u624B\u6BB5\uFF0C\u8BA9\u4ED6\u4EEC\u770B\u770B\u8FD9\u4E24\u4E2A\u5A4A\u5B50\u6709\u591A\u6D6A\uFF01\u300D\u7136\u540E\u5BF9\u6000\u4E2D\u7684\u7F8E\u5987\u5FAE\u7B11\u9053\uFF1A\u300C\u6211\u521A\u624D\u730E\u4E86\u53EA\u9EC4\u7F8A\uFF0C\u4E00\u4F1A\u513F\u70E4\u6765\u5403\u3002\u300D</p><p>\u8427\u4F5B\u5974\u4E0D\u6562\u770B\u573A\u4E2D\u7684\u8840\u8165\uFF0C\u4FA7\u8138\u8D34\u5728\u6155\u5BB9\u9F99\u80F8\u524D\uFF0C\u5FAE\u5FAE\u70B9\u4E86\u70B9\u5934\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4ED6\u4EEC\u662F\u8C01\uFF1F\u300D</p><p>\u300C\u5B89\u5B9A\u7684\u516B\u6781\u95E8\u3002\u300D</p><p>\u8427\u4F5B\u5974\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u8FD8\u662F\u8BF4\u9053\uFF1A\u300C\u80FD\u4E0D\u80FD\u4E0D\u6253\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u7B11\uFF0C\u300C\u597D\u5929\u771F\u7684\u5A18\u4EB2\u2026\u2026\u662F\u4ED6\u4EEC\u5343\u91CC\u8FE2\u8FE2\u4ECE\u5B89\u5B9A\u8D76\u6765\u8981\u6740\u6211\u5462\u3002\u300D</p><p>\u7D2B\u73AB\u66F2\u819D\u59D4\u5730\u800C\u5750\uFF0C\u9759\u9759\u770B\u7740\u573A\u4E2D\u98DE\u6E85\u7684\u9C9C\u8840\uFF0C\u4EFF\u4F5B\u6218\u573A\u8FB9\u7F18\u4E00\u6735\u76DB\u5F00\u7684\u73AB\u7470\uFF0C\u6084\u7136\u5410\u9732\u82AC\u82B3\u3002</p><p>\u770B\u4E86\u7247\u523B\u5979\u5DF2\u662F\u5F7B\u5E95\u6B7B\u5FC3\u3002\u516B\u6781\u95E8\u52C7\u5219\u52C7\u77E3\uFF0C\u4F46\u5B9E\u5728\u9C81\u83BD\u7684\u7D27\u3002\u661F\u6708\u6E56\u7B49\u4EBA\u672C\u6765\u88AB\u56F4\u5728\u4E2D\u95F4\uFF0C\u56DB\u9762\u53D7\u654C\u3002\u53EF\u90A3\u5E2E\u70ED\u8840\u9752\u5E74\u53EA\u987E\u62EF\u6551\u767D\u6C0F\u59D0\u59B9\uFF0C\u81EA\u5DF1\u4E71\u4E86\u9635\u811A\u3002</p><p>\u661F\u6708\u6E56\u5E2E\u4F17\u907F\u5F00\u950B\u8292\uFF0C\u4E00\u8F6C\u8EAB\u53CD\u800C\u6210\u4E86\u5305\u56F4\u4E4B\u52BF\u3002\u4E5E\u4F0F\u7A77\u9686\u7B49\u4EBA\u8FDC\u8FDC\u65BD\u653E\u6697\u5668\uFF0C\u7728\u773C\u95F4\u516B\u6781\u95E8\u5C31\u5012\u4E0B\u5341\u51E0\u540D\u5F1F\u5B50\u3002</p><p>\u8FD9\u5E2E\u7B28\u86CB\uFF01\u7D2B\u73AB\u6068\u4E0D\u5F97\u7AD9\u8D77\u6765\u6307\u6325\u4ED6\u4EEC\u5982\u4F55\u7ED3\u9635\u81EA\u5B88\u3002\u4EE5\u516B\u6781\u95E8\u7684\u5B9E\u529B\uFF0C\u5B8C\u5168\u53EF\u4EE5\u8BA9\u8FD9\u4E9B\u5996\u4EBA\u5403\u4E9B\u82E6\u5934\uFF0C\u81EA\u5DF1\u5C31\u6709\u673A\u4F1A\u9003\u8D70\u4E86\u3002</p><p>\u770B\u7740\u4EB2\u5982\u624B\u8DB3\u7684\u540C\u95E8\u4E3A\u6551\u81EA\u5DF1\u4E00\u4E2A\u4E2A\u5012\u4E0B\uFF0C\u767D\u6C0F\u59D0\u59B9\u4E0D\u7EA6\u800C\u540C\u5730\u6342\u4F4F\u9762\u5B54\uFF0C\u653E\u58F0\u75DB\u54ED\u3002</p><p>\u6155\u5BB9\u9F99\u8C8C\u4F3C\u60A0\u7136\u5730\u73AF\u987E\u95E8\u4E0B\u5C60\u6740\u5F0F\u7684\u8840\u6218\uFF0C\u773C\u89D2\u5374\u59CB\u7EC8\u7559\u610F\u7740\u91D1\u5F00\u7532\u548C\u9F99\u6218\u91CE\u3002</p><p>\u4E24\u4EBA\u8EAB\u5F62\u4E00\u822C\u7684\u5A01\u6B66\u795E\u52C7\uFF0C\u5185\u529F\u4E00\u822C\u7684\u521A\u731B\u65E0\u94F8\uFF0C\u62DB\u5F0F\u4E00\u822C\u7684\u5927\u5F00\u5927\u5408\uFF0C\u5175\u5668\u4E5F\u540C\u6837\u662F\u64C5\u4E8E\u575A\u653B\u7684\u5DE8\u578B\u957F\u5175\u3002\u4E00\u756A\u9F99\u4E89\u864E\u6597\uFF0C\u91D1\u94C1\u4EA4\u9E23\u58F0\u54CD\u5F7B\u8349\u539F\u3002\u52B2\u98CE\u8FC7\u5904\uFF0C\u957F\u8349\u5C3D\u6210\u767D\u5730\uFF0C\u75BE\u98DE\u7684\u788E\u53F6\u5F25\u6F2B\u7A7A\u4E2D\uFF0C\u8FDE\u4E24\u4EBA\u7684\u8EAB\u5F71\u90FD\u63A9\u6CA1\u4E86\u3002</p><p>\u661F\u6708\u6E56\u6B7B\u4F24\u4E0D\u8FC7\u4E94\u4EBA\uFF0C\u516B\u6781\u95E8\u5DF2\u7ECF\u6298\u635F\u534A\u6570\u3002\u76F4\u5230\u5510\u989C\u6325\u5251\u6740\u5165\u6218\u573A\uFF0C\u62DB\u5524\u5B50\u5F1F\uFF0C\u624D\u52C9\u5F3A\u7A33\u4F4F\u9635\u811A\u3002</p><p>\u4EC7\u767E\u718A\u3001\u4EC7\u767E\u9CCC\u8840\u65A9\u72C2\u821E\uFF0C\u4E0E\u4F17\u4EBA\u5C06\u516B\u6781\u95E8\u5B50\u5F1F\u56F4\u5728\u4E2D\u95F4\uFF0C\u53CC\u65B9\u90FD\u662F\u5168\u529B\u76F8\u640F\uFF0C\u4E00\u65F6\u95F4\u50F5\u6301\u4E0D\u4E0B\u3002\u5510\u989C\u957F\u5251\u5982\u6C34\uFF0C\u5728\u9635\u4E2D\u5DE6\u7A7F\u53F3\u63D2\uFF0C\u4E0D\u591A\u65F6\u9EC4\u886B\u4FBF\u9C9C\u8840\u5C3D\u67D3\u3002</p><p>\u6155\u5BB9\u9F99\u72B9\u8C6B\u591A\u65F6\uFF0C\u4ED6\u5012\u4E0D\u662F\u62C5\u5FC3\u64D2\u4E0D\u4E0B\u5510\u989C\uFF0C\u800C\u662F\u6015\u6B64\u65F6\u64D2\u4E0B\u5510\u989C\u4F1A\u8BA9\u4EBA\u4EE5\u4E3A\u662F\u7528\u5979\u6765\u5A01\u80C1\u9F99\u6218\u91CE\u2014\u2014\u8FD9\u5012\u5E76\u975E\u51FA\u4E8E\u53EF\u7B11\u7684\u9053\u4E49\uFF0C\u6BD4\u8FD9\u518D\u5351\u9119\u4E07\u500D\u7684\u4E8B\u4ED6\u4E5F\u6BEB\u4E0D\u72B9\u8C6B\u7684\u505A\u4E86\u3002\u53EA\u662F\u91D1\u5F00\u7532\u96BE\u5F97\u4E0E\u767E\u6218\u5929\u9F99\u4E00\u6218\uFF0C\u82E5\u662F\u767E\u6218\u5929\u9F99\u4E3A\u6B64\u5206\u5FC3\uFF0C\u91D1\u5F00\u7532\u5373\u4F7F\u53D6\u80DC\u4E5F\u65E0\u5149\u5F69\u3002</p><p>\u6155\u5BB9\u9F99\u53F9\u4E86\u53E3\u6C14\uFF0C\u6402\u4F4F\u7D2B\u73AB\u7684\u8170\u80A2\uFF0C\u300C\u8FD8\u5410\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u5FC3\u7070\u610F\u51B7\u4E0B\u52C9\u5F3A\u632F\u4F5C\u7CBE\u795E\u9053\uFF1A\u300C\u597D\u591A\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5C06\u6BCD\u5973\u4FE9\u540C\u65F6\u62B1\u5728\u6000\u4E2D\uFF0C\u8033\u9B13\u76F8\u63A5\uFF0C\u78E8\u64E6\u7740\u4E24\u5F20\u7EDD\u7F8E\u7684\u7389\u8138\uFF0C\u7B11\u9053\uFF1A\u300C\u518D\u6709\u516D\u4E2A\u6708\uFF0C\u4F60\u4FE9\u5C31\u4F1A\u5404\u7ED9\u6211\u751F\u4E2A\u5B69\u5B50\u2014\u2014\u6700\u597D\u90FD\u662F\u7537\u5B69\uFF0C\u597D\u5EF6\u7EED\u6211\u6155\u5BB9\u6C0F\u7684\u8840\u8109\u3002\u300D</p><p>\u7D2B\u73AB\u6700\u70E6\u7684\u5C31\u662F\u8FD9\u4E2A\u8BDD\u9898\uFF0C\u677F\u7740\u8138\u9053\uFF1A\u300C\u4E07\u4E00\u662F\u4E2A\u767D\u75F4\u5462\uFF1F\u4E00\u4E07\u4E5F\u662F\u767D\u75F4\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5DF2\u7ECF\u8BF4\u8FC7\u65E0\u6570\u6B21\uFF0C\u8FD8\u662F\u8010\u7740\u6027\u5B50\u7B11\u9053\uFF1A\u300C\u5A18\u5B50\u653E\u5FC3\uFF0C\u80AF\u5B9A\u4F1A\u6709\u4E00\u4E2A\u5929\u624D\u3002\u4E00\u4E2A\u4E0D\u884C\u5C31\u518D\u6765\u4E00\u4E2A\uFF0C\u7EC8\u7A76\u4F1A\u6709\u4E00\u4E2A\u513F\u5B50\u80FD\u7EE7\u627F\u54B1\u4EEC\u5BB6\u65CF\u7684\u8840\u7EDF\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u5B89\u5B50\u5B8F\u7528\u5F2F\u94A9\u6311\u7740\u88D8\u864E\u4F0F\u7684\u5934\u9885\uFF0C\u77F3\u880D\u62CE\u7740\u66F2\u72FC\u75BE\u7684\u5934\u9885\u5148\u540E\u56DE\u5230\u8F66\u65C1\u3002\u4E24\u4EBA\u867D\u7136\u5404\u81EA\u5E26\u4E86\u4E0D\u8F7B\u7684\u4F24\u52BF\uFF0C\u4F46\u90FD\u662F\u5F97\u610F\u6D0B\u6D0B\u3002\u7247\u523B\u540E\u7075\u7389\u4E5F\u7F13\u6B65\u8D70\u56DE\uFF0C\u624B\u4E2D\u63D0\u7740\u5C39\u8C61\u5D07\u4E0E\u8463\u8C79\u5A01\u7684\u9996\u7EA7\u3002</p><p>\u5B89\u5B50\u5B8F\u4F38\u5934\u4E00\u770B\uFF0C\u300C\u725B\u9F3B\u5B50\u4E0B\u624B\u592A\u5FEB\uFF0C\u59D3\u8463\u7684\u540D\u58F0\u4E5F\u4E0D\u5C0F\uFF0C\u600E\u4E48\u4E00\u62DB\u5C31\u683D\u5230\u4F60\u624B\u91CC\uFF1F\u300D</p><p>\u516B\u6781\u95E8\u56DB\u6770\u6B66\u529F\u4E0D\u51E1\uFF0C\u82E5\u975E\u8463\u8C79\u5A01\u4E00\u62DB\u6BD9\u547D\uFF0C\u4EE5\u56DB\u654C\u4E09\uFF0C\u4ED6\u4EEC\u4E5F\u96BE\u4EE5\u8F7B\u6613\u53D6\u80DC\u3002\u7075\u7389\u7B11\u9053\uFF1A\u300C\u8463\u8C79\u5A01\u51B2\u5728\u6700\u524D\uFF0C\u7ACB\u8DB3\u4E0D\u7A33\uFF0C\u8D2B\u9053\u4E0D\u8FC7\u5360\u4E86\u70B9\u4FBF\u5B9C\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u6025\u4E8E\u7ACB\u529F\uFF0C\u6328\u4E86\u88D8\u864E\u4F0F\u4E00\u638C\u3002\u4ED6\u6068\u6068\u5410\u4E86\u53E3\u8840\uFF0C\u4E0D\u670D\u6C14\u5730\u7529\u6389\u88D8\u864E\u4F0F\u7684\u5934\u9885\uFF0C\u64E6\u4E86\u628A\u5634\u5C31\u8981\u6740\u8FC7\u53BB\u53D6\u5510\u989C\u7684\u9996\u7EA7\u3002</p><p>\u77F3\u880D\u80A9\u4E0A\u4E5F\u4E2D\u4E86\u4E00\u5200\uFF0C\u6DF1\u53EF\u89C1\u9AA8\u3002\u770B\u5230\u5DF4\u9675\u67AD\u5982\u6B64\u62FC\u547D\uFF0C\u4ED6\u4E5F\u4E00\u6296\u957F\u97AD\uFF0C\u53BB\u5411\u5374\u662F\u573A\u5916\u7684\u9F99\u6714\u3002</p><p>\u300C\u4E24\u4F4D\u4F9B\u5949\u7559\u6B65\u3002\u300D\u6155\u5BB9\u9F99\u8D77\u8EAB\u7B11\u9053\uFF0C\u300C\u957F\u8001\u548C\u4E24\u4F4D\u4F9B\u5949\u53D6\u6765\u56DB\u6770\u7684\u5934\u9885\u5DF2\u662F\u5927\u529F\uFF0C\u4F59\u4E0B\u8005\u4E0D\u8FC7\u662F\u4E9B\u65E0\u540D\u5C0F\u5352\uFF0C\u83AB\u53BB\u7406\u4ED6\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u4E0E\u77F3\u880D\u60BB\u60BB\u5750\u4E0B\uFF0C\u5404\u81EA\u6CBB\u4F24\u3002\u7075\u7389\u671D\u91D1\u5F00\u7532\u548C\u9F99\u6218\u91CE\u4E24\u4EBA\u770B\u53BB\u3002</p><p>\u767E\u6218\u5929\u9F99\u5173\u5200\u864E\u864E\u751F\u98CE\uFF0C\u4E0E\u91D1\u5F00\u7532\u7684\u94DC\u65A7\u4E00\u9ED1\u4E00\u9EC4\u4E24\u6761\u731B\u9F99\u822C\u72C2\u51FB\u731B\u649E\uFF0C\u6FC0\u6C64\u7684\u52B2\u6C14\u5B9B\u5982\u98D3\u98CE\uFF0C\u65B9\u5706\u5341\u4E08\u5185\u8349\u6728\u7686\u65E0\u3002</p><p>\u7075\u7389\u5FC3\u4E0B\u6697\u670D\uFF0C\u773C\u5149\u4E00\u8F6C\uFF0C\u671B\u7740\u5510\u989C\u9053\uFF1A\u300C\u6B64\u5973\u5012\u8FD8\u8584\u6709\u51E0\u5206\u59FF\u8272\uFF0C\u4E0D\u77E5\u9F0E\u7089\u5982\u4F55\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u83BA\u5974\uFF0C\u4F60\u5E08\u5A18\u751F\u8FC7\u51E0\u4E2A\u5B69\u5B50\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u4EF0\u9762\u5012\u5728\u5730\u4E0A\uFF0C\u4E24\u817F\u67B6\u5728\u7537\u4EBA\u80A9\u4E0A\uFF0C\u82E6\u82E6\u627F\u53D7\u7740\u7C97\u66B4\u7684\u5978\u6DEB\uFF0C\u5E08\u95E8\u6E85\u8840\u7684\u60E8\u72B6\u4F7F\u5979\u809D\u80A0\u5BF8\u65AD\uFF0C\u534A\u660F\u534A\u9192\u4E2D\u6CA1\u6709\u542C\u5230\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u3002\u6B63\u5728\u62BD\u9001\u7684\u5E2E\u4F17\u62E7\u4F4F\u5979\u7684\u811A\u8E1D\u7528\u529B\u4E00\u8F6C\uFF0C\u5C11\u5973\u88AB\u80A1\u95F4\u6495\u88C2\u822C\u5267\u75DB\u60CA\u9192\uFF0C\u7070\u767D\u7684\u5634\u5507\u4E0D\u4F4F\u6218\u6817\u3002\u767D\u7389\u9E42\u89C1\u72B6\u52C9\u5F3A\u8BF4\u9053\uFF1A\u300C\u4E00\u4E2A\u2026\u2026\u554A\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8FDC\u8FDC\u671B\u53BB\uFF0C\u53EA\u89C1\u90A3\u4E2A\u5C0F\u7537\u5B69\u773C\u775B\u4E00\u7728\u4E0D\u7728\uFF0C\u6B7B\u6B7B\u76EF\u7740\u7236\u6BCD\uFF0C\u6E05\u79C0\u7684\u5C0F\u8138\u6EE1\u662F\u5014\u5F3A\u3002\u6155\u5BB9\u9F99\u5634\u89D2\u7684\u5FAE\u7B11\u6E10\u6E10\u892A\u53BB\u3002</p><p>\u5510\u989C\u8EAB\u8FB9\u53EA\u5269\u4E0B\u4E09\u540D\u5F1F\u5B50\uFF0C\u5C3D\u6570\u8D1F\u4F24\uFF0C\u5979\u53F3\u80A9\u4E5F\u4E2D\u4E86\u4E00\u67AA\uFF0C\u53EA\u80FD\u7528\u5DE6\u624B\u4F7F\u5251\u3002</p><p>\u8FDC\u5904\u8FD8\u6709\u4E24\u540D\u5973\u5F1F\u5B50\uFF0C\u5728\u4FDD\u62A4\u9F99\u6714\u3002\u661F\u6708\u6E56\u5E2E\u4F17\u4E5F\u5C11\u4E86\u516B\u4EBA\uFF0C\u4EC7\u767E\u9CCC\u88AB\u5979\u4E00\u5251\u523A\u7A7F\u5927\u817F\uFF0C\u5012\u5728\u4E00\u65C1\u9A82\u9A82\u54A7\u54A7\u3002\u56F4\u653B\u7684\u53EA\u5269\u4E0B\u4EC7\u767E\u718A\u3001\u4E5E\u4F0F\u7A77\u9686\u548C\u5176\u4F59\u56DB\u540D\u5E2E\u4F17\uFF0C\u53E6\u6709\u4E24\u4EBA\u6B63\u5728\u5978\u6DEB\u767D\u6C0F\u59D0\u59B9\u3002</p><p>\u91D1\u94C1\u4E4B\u58F0\u7A81\u7136\u5927\u9707\uFF0C\u9F99\u6218\u91CE\u5267\u559D\u8FDE\u58F0\uFF0C\u9752\u9F99\u5173\u5200\u72B9\u5982\u66B4\u8DF3\u7684\u96F7\u9706\uFF0C\u7834\u5F00\u957F\u7A7A\u72C2\u5288\u5728\u91D1\u5F00\u7532\u7684\u94DC\u65A7\u4E0A\u3002\u767E\u6218\u5929\u9F99\u795E\u5A01\u5927\u632F\uFF0C\u4E00\u5200\u80DC\u4F3C\u4E00\u5200\u3002\u91D1\u5F00\u7532\u8FDE\u8FDE\u5012\u9000\uFF0C\u6321\u5230\u7B2C\u4E03\u5200\u5DF2\u9000\u51FA\u4E24\u4E08\u5F00\u5916\u3002\u9F99\u6218\u91CE\u987B\u53D1\u6012\u5F20\uFF0C\u96C4\u8EAF\u817E\u7A7A\u800C\u8D77\uFF0C\u5173\u5200\u5728\u7A7A\u4E2D\u4E00\u987F\uFF0C\u547C\u5578\u7740\u843D\u4E86\u4E0B\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u6BEB\u4E0D\u72B9\u8C6B\u5730\u5C55\u5F00\u8EAB\u5F62\uFF0C\u53EA\u4E24\u4E2A\u8D77\u843D\u4FBF\u63A0\u8FC7\u4E8C\u5341\u4E08\u7684\u8DDD\u79BB\uFF0C\u4E0D\u7B49\u516B\u6781\u95E8\u4E24\u540D\u5973\u5F1F\u5B50\u51FA\u5251\u4FBF\u8EAB\u5B50\u4E00\u6A2A\uFF0C\u4E00\u638C\u4E00\u811A\u5C01\u4E86\u4E24\u5973\u7684\u7A74\u9053\u3002</p><p>\u9F99\u6714\u867D\u60CA\u4E0D\u4E71\uFF0C\u6C89\u8170\u5750\u9A6C\uFF0C\u4E00\u62F3\u6325\u5411\u6155\u5BB9\u9F99\u8170\u95F4\u3002\u867D\u7136\u4ED6\u8EAB\u5C0F\u81C2\u77ED\uFF0C\u4F46\u8FD9\u4E00\u62DB\u4E94\u4E01\u5F00\u5C71\u4F7F\u5F97\u6CD5\u5EA6\u68EE\u4E25\uFF0C\u4FE8\u7136\u6709\u5927\u5BB6\u4E4B\u98CE\u3002\u6155\u5BB9\u9F99\u5FC3\u5934\u4E00\u8DF3\uFF0C\u5212\u5411\u9F99\u6714\u80A9\u5934\u7684\u624B\u5200\u84E6\u7684\u4E00\u7FFB\uFF0C\u4E00\u6307\u70B9\u5728\u9F99\u6714\u9888\u4E2D\u3002</p><p>\u767E\u6218\u5929\u9F99\u51DD\u805A\u5168\u8EAB\u529F\u529B\u7684\u4E00\u5200\u5288\u4E0B\uFF0C\u91D1\u5F00\u7532\u72EC\u76EE\u7CBE\u5149\u5927\u76DB\uFF0C\u94DC\u65A7\u6A2A\u67B6\uFF0C\u63A5\u4F4F\u8FD9\u60CA\u4E16\u4E00\u5200\u3002\u300C\u94DB\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u4E00\u63E1\u7C97\u7684\u9EC4\u94DC\u65A7\u67C4\u88AB\u751F\u751F\u7838\u5F2F\u3002\u91D1\u5F00\u7532\u53CC\u811A\u9677\u5165\u5730\u4E2D\u5BF8\u8BB8\uFF0C\u5374\u4E00\u6B65\u4E5F\u4E0D\u9000\u8BA9\u3002</p><p>\u9F99\u6218\u91CE\u53CC\u624B\u864E\u53E3\u9707\u88C2\uFF0C\u300C\u54C7\u300D\u7684\u55B7\u51FA\u4E00\u84EC\u8840\u96E8\u3002\u8840\u5149\u4E2D\uFF0C\u767E\u6218\u5929\u9F99\u9F13\u8D77\u4F59\u52C7\uFF0C\u518D\u6B21\u4E3E\u8D77\u9752\u9F99\u5200\u3002</p><p>\u94DC\u65A7\u7A81\u7136\u53D8\u5F97\u8F7B\u5982\u9E3F\u6BDB\uFF0C\u91D1\u5F00\u7532\u4E00\u6B65\u8DE8\u51FA\uFF0C\u65A7\u5C16\u5FAE\u7FFB\uFF0C\u5DF2\u8F7B\u8F7B\u70B9\u5728\u9F99\u6218\u91CE\u80C1\u4E0B\uFF0C\u8FDE\u5916\u888D\u4E5F\u672A\u5212\u7834\u3002</p><p>\u4E08\u592B\u9AD8\u5927\u7684\u8EAB\u8EAF\u9893\u7136\u5012\u5730\uFF0C\u5510\u989C\u8111\u4E2D\u987F\u65F6\u4E00\u7247\u7A7A\u767D\u3002\u5979\u5A07\u8EAF\u5FAE\u5FAE\u4E00\u6643\uFF0C\u60F3\u4E5F\u4E0D\u60F3\u5730\u7FFB\u8155\u5C06\u957F\u5251\u67B6\u5728\u9888\u4E0B\u3002</p><p>\u300C\u9F99\u592B\u4EBA\u3002\u300D\u90A3\u4E2A\u5E74\u9752\u4EBA\u7F13\u7F13\u5C06\u9F99\u6714\u4E3E\u5230\u534A\u7A7A\u3002</p><p>\u91D1\u5F00\u7532\u795E\u8272\u5E73\u9759\u5730\u6536\u8D77\u5F2F\u66F2\u7684\u94DC\u65A7\uFF0C\u5982\u8840\u7684\u5915\u9633\u5728\u4ED6\u811A\u4E0B\u5212\u51FA\u4E00\u9053\u957F\u957F\u7684\u8840\u8272\u5370\u8FF9\u3002</p><p>\u7075\u7389\u98DE\u7D6E\u822C\u98D8\u5230\u573A\u4E2D\uFF0C\u5927\u8896\u4E00\u626C\uFF0C\u65CB\u5373\u98D8\u5F00\u3002\u4E09\u540D\u516B\u6781\u95E8\u5F1F\u5B50\u4E00\u58F0\u4E0D\u54CD\u5730\u5C38\u6A2A\u5C31\u5730\uFF0C\u53EA\u5269\u5510\u989C\u4E00\u4EBA\u5B64\u96F6\u96F6\u7ACB\u5728\u8840\u6CCA\u4E4B\u4E2D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u9F99\u592B\u4EBA\u679C\u7136\u8BC6\u76F8\u3002\u300D\u6155\u5BB9\u9F99\u4E3E\u7740\u9F99\u6714\u7F13\u6B65\u8D70\u56DE\u3002</p><p>\u5510\u989C\u832B\u7136\u770B\u4E86\u4E08\u592B\u4E00\u773C\uFF0C\u773C\u795E\u7A81\u7136\u9510\u5229\u8D77\u6765\uFF0C\u624B\u8155\u4E00\u7D27\uFF0C\u4FBF\u8981\u7528\u529B\u5212\u843D\u3002</p><p>\u6155\u5BB9\u9F99\u505C\u4E0B\u811A\u6B65\uFF0C\u68EE\u7136\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u96BE\u9053\u4E0D\u60F3\u8C08\u8C08\u6761\u4EF6\u5417\uFF1F\u300D</p><p>\u5C11\u5987\u8138\u8272\u60E8\u767D\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u4F60\u4EEC\u8FD9\u4E9B\u65E0\u803B\u5C0F\u4EBA\uFF0C\u6BEB\u65E0\u4FE1\u4E49\u53EF\u8A00\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u626C\u8D77\u8138\uFF0C\u50B2\u7136\u9053\uFF1A\u300C\u672C\u5BAB\u4EE5\u661F\u6708\u6E56\u58F0\u540D\u8D77\u8A93\uFF0C\u53EA\u8981\u4F60\u542C\u4ECE\u5429\u5490\uFF0C\u672C\u5BAB\u5C31\u653E\u6B64\u5B50\u4E00\u6761\u751F\u8DEF\uFF01\u300D\u8BF4\u7740\u89E3\u5F00\u9F99\u6714\u7684\u54D1\u7A74\u3002</p><p>\u300C\u7239\uFF01\u7239\uFF01\u300D\u6E05\u4EAE\u7684\u7AE5\u97F3\u7ACB\u523B\u54CD\u8D77\u3002\u9F99\u6714\u53EB\u4E86\u4E24\u58F0\uFF0C\u89C1\u7239\u7239\u6CA1\u6709\u56DE\u7B54\uFF0C\u53C8\u53EB\u9053\uFF1A\u300C\u5A18\uFF01\u300D</p><p>\u56F4\u653B\u7684\u5E2E\u4F17\u5DF2\u7ECF\u6563\u5F00\uFF0C\u5510\u989C\u4FCF\u751F\u751F\u7ACB\u5728\u4F0F\u5C38\u4E4B\u95F4\uFF0C\u6EF4\u8840\u7684\u957F\u5251\u67B6\u5728\u5589\u5934\uFF0C\u7693\u8155\u5FAE\u5FAE\u98A4\u6296\u3002\u51C4\u51C9\u7684\u76EE\u5149\u7F13\u7F13\u626B\u8FC7\u5168\u573A\u3002\u4E00\u8FB9\u662F\u751F\u6B7B\u672A\u535C\u7684\u4E08\u592B\uFF0C\u4E00\u8FB9\u662F\u806A\u9896\u4F36\u4FD0\u7684\u513F\u5B50\uFF0C\u4E2D\u95F4\u662F\u4E00\u4F17\u540C\u95E8\u7684\u5C38\u9996\u3002\u8FD8\u6709\u90A3\u4E9B\u6DEB\u90AA\u7684\u773C\u795E\u2026\u2026\u6700\u540E\u76EE\u5149\u505C\u7559\u5728\u4E24\u540D\u7231\u5F92\u8EAB\u4E0A\u3002</p><p>\u59D0\u59B9\u4FE9\u8EBA\u5728\u5730\u4E0A\uFF0C\u660F\u8FF7\u4E0D\u9192\uFF0C\u8D64\u88F8\u7684\u5A07\u8EAF\u6CBE\u6EE1\u6C61\u8FF9\uFF0C\u5927\u5F20\u7684\u817F\u95F4\u9971\u53D7\u8E42\u8E8F\u7684\u79D8\u5904\u7EA2\u5F97\u523A\u773C\u3002\u5C11\u5987\u51C4\u7136\u4E00\u7B11\uFF0C\u5FC3\u91CC\u65E0\u58F0\u5730\u8BF4\u9053\uFF1A\u300C\u50CF\u5979\u4EEC\u5417\uFF1F\u6211\u5B81\u613F\u6B7B\u4E86\u5E72\u51C0\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9EDD\u9ED1\u7684\u77B3\u4EC1\u4EFF\u4F5B\u6D1E\u5BDF\u4E86\u5510\u989C\u7684\u5FC3\u4E8B\uFF0C\u4ED6\u54C2\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u8EAB\u4EFD\u5C0A\u8D35\uFF0C\u81EA\u7136\u4E0D\u4F1A\u4E0E\u5979\u4EEC\u76F8\u540C\u3002\u300D\u4ED6\u7AD6\u8D77\u4E00\u6839\u624B\u6307\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u53EA\u8981\u9F99\u592B\u4EBA\u80AF\u4F8D\u5949\u4E00\u65E5\uFF0C\u660E\u65E5\u6B64\u65F6\u672C\u5BAB\u4FBF\u653E\u516C\u5B50\u79BB\u5F00\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u300C\u672C\u5BAB\u7EDD\u4E0D\u98DF\u8A00\u3002\u300D</p><p>\u9F99\u6714\u4E0D\u89E3\u5730\u770B\u7740\u6BCD\u4EB2\uFF0C\u4E0D\u77E5\u9053\u4ED6\u4EEC\u5728\u8BF4\u4EC0\u4E48\u3002</p><p>\u79C0\u53D1\u5728\u51B0\u51C9\u7684\u665A\u98CE\u4E2D\u4E1D\u4E1D\u7F15\u7F15\u98D8\u8361\u7740\u3002\u4E00\u9897\u6676\u83B9\u7684\u6CEA\u73E0\u4ECE\u767D\u7389\u822C\u7684\u8138\u988A\u4E0A\u6084\u7136\u6ED1\u843D\u3002\u5510\u989C\u624B\u6307\u4E00\u677E\uFF0C\u957F\u5251\u6D88\u5931\u5728\u6CBE\u8840\u7684\u8349\u4E1B\u4E2D\u3002</p><p>\u91D1\u5F00\u7532\u5FC3\u4E0B\u6697\u53F9\uFF0C\u4ED6\u4E0E\u9F99\u6218\u91CE\u4E00\u573A\u6076\u6218\uFF0C\u5BF9\u8FD9\u786C\u6C49\u9887\u4E3A\u656C\u91CD\u3002\u5F53\u4E0B\u4E00\u63A8\u94DC\u65A7\uFF0C\u8F7B\u8F7B\u65A9\u4E0B\u767E\u6218\u5929\u9F99\u7684\u5934\u9885\u3002\u9F99\u6218\u91CE\u5927\u5934\u4E00\u6EDA\uFF0C\u864E\u76EE\u671B\u7740\u65E0\u8FB9\u7684\u82CD\u7A79\uFF0C\u6D41\u9732\u51FA\u65E0\u6BD4\u7684\u75DB\u610F\u3002</p><p>\u7D2B\u73AB\u95ED\u4E0A\u773C\uFF0C\u7EA4\u624B\u629A\u5728\u5FAE\u9F13\u7684\u5C0F\u8179\u4E0A\uFF0C\u6697\u9053\uFF1A\u300C\u4F60\u82E5\u6709\u90A3\u4E2A\u755C\u7272\u5341\u5206\u4E4B\u4E00\u7684\u72E1\u8BC8\uFF0C\u5C31\u4F1A\u662F\u5927\u71D5\u56FD\u7684\u592A\u5B50\u4E86\u3002\u300D</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
