import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_88" tabindex="-1"><a class="header-anchor" href="#_88" aria-hidden="true">#</a> 88</h1><p>\u591C\u5E55\u964D\u4E34\uFF0C\u5929\u5730\u4E00\u7247\u5E7D\u6697\u3002\u832B\u832B\u8349\u539F\u4E2D\uFF0C\u4E00\u652F\u5C0F\u5C0F\u7684\u8F66\u961F\u5374\u88AB\u5468\u56F4\u718A\u718A\u71C3\u70E7\u7684\u706B\u67F1\u7167\u5F97\u4EAE\u5982\u767D\u663C\u3002</p><p>\u4E8C\u5341\u4F59\u4EBA\u56F4\u6210\u4E00\u4E2A\u5706\u5708\uFF0C\u5E2D\u5730\u800C\u5750\u3002\u4EBA\u7FA4\u4E2D\u7684\u7BDD\u706B\u4E0A\uFF0C\u6302\u7740\u4E24\u53EA\u6D17\u5265\u8FC7\u7684\u9EC4\u7F8A\uFF0C\u8089\u9999\u9635\u9635\u98D8\u6765\u3002</p><p>\u4E00\u4E2A\u80E1\u670D\u7537\u5B50\u610F\u6C14\u98CE\u53D1\u5730\u4E3E\u676F\u9053\uFF1A\u300C\u4ECA\u65E5\u6211\u661F\u6708\u6E56\u5728\u8FD9\u83BD\u83BD\u8349\u6D77\u5C3D\u6B7C\u516B\u6781\u95E8\uFF0C\u7740\u5B9E\u75DB\u5FEB\uFF01\u300D\u8BF4\u7F62\u4E00\u996E\u800C\u5C3D\u3002</p><p>\u706B\u4EAE\u95EA\u52A8\u4E2D\uFF0C\u6620\u51FA\u5730\u4E0A\u4E00\u6392\u6574\u9F50\u7684\u5934\u9885\u3002\u9F99\u6218\u91CE\u3001\u675C\u7280\u5065\u3001\u8BB8\u72EE\u96C4\u3001\u5C39\u8C61\u5D07\u3001\u88D8\u864E\u4F0F\u3001\u8463\u8C79\u5A01\u3001\u5415\u9E70\u626C\u3001\u66F2\u72FC\u75BE\u2026\u2026\u4E00\u5171\u56DB\u5341\u4E09\u4E2A\u9996\u7EA7\uFF0C\u65AD\u9888\u4E0A\u8840\u8FF9\u5C1A\u65B0\u3002</p><p>\u7FA4\u90AA\u8F70\u7136\u996E\u5E72\uFF0C\u653E\u58F0\u5927\u7B11\u3002</p><p>\u4E00\u4E2A\u6E05\u4E3D\u7684\u5C11\u5987\u6162\u6162\u89E3\u5F00\u8863\u895F\uFF0C\u5C06\u6D12\u6EE1\u9C9C\u8840\u7684\u9EC4\u886B\u653E\u5728\u5730\u4E0A\uFF0C\u88F8\u7740\u96EA\u767D\u7684\u53CC\u80A9\u8DEA\u5728\u4E00\u65C1\u3002\u5728\u5979\u53F3\u80A9\u4E0A\uFF0C\u6709\u4E00\u4E2A\u8840\u8089\u6A21\u7CCA\u7684\u4F24\u53E3\u3002</p><p>\u9EC4\u660F\u65F6\u5206\u7684\u4E00\u573A\u8840\u6218\uFF0C\u516B\u6781\u95E8\u5168\u519B\u8986\u6CA1\uFF0C\u5305\u62EC\u516B\u6770\u5728\u5185\u7684\u56DB\u5341\u4E03\u4EBA\u53EA\u5269\u4E0B\u4E09\u540D\u5973\u5B50\u548C\u4E00\u4E2A\u516B\u5C81\u7684\u5B69\u5B50\u3002</p><p>\u6B64\u5F79\u661F\u6708\u6E56\u4E5F\u6218\u6B7B\u4E5D\u4EBA\uFF0C\u9664\u56DB\u540D\u5973\u7737\u3001\u6155\u5BB9\u9F99\u3001\u91D1\u5F00\u7532\u3001\u7075\u7389\u4EE5\u5916\uFF0C\u5176\u4F59\u5341\u4E94\u4EBA\u5C3D\u6570\u8D1F\u4F24\u3002\u6B64\u65F6\u8840\u6218\u4F59\u751F\uFF0C\u4F17\u4EBA\u5747\u662F\u5174\u81F4\u5927\u53D1\uFF0C\u8FDE\u8EAB\u8D1F\u5185\u4F24\u7684\u5B89\u5B50\u5B8F\u4E5F\u4E3E\u676F\u75DB\u996E\u3002</p><p>\u5C11\u5987\u76F4\u76F4\u770B\u7740\u6155\u5BB9\u9F99\uFF0C\u90A3\u4E2A\u80E1\u670D\u7537\u5B50\u6BCF\u6B21\u4E3E\u676F\uFF0C\u5979\u4FBF\u89E3\u4E0B\u4E00\u4EF6\u8863\u670D\u3002\u7B49\u6155\u5BB9\u9F99\u559D\u5B8C\u7B2C\u4E09\u676F\uFF0C\u5C11\u5987\u5DE6\u624B\u7ED5\u5230\u80CC\u540E\uFF0C\u4E00\u62C9\u8863\u7ED3\uFF0C\u62B9\u80F8\u6ED1\u843D\uFF0C\u9732\u51FA\u4E00\u5BF9\u7C89\u96D5\u7389\u7422\u7684\u9999\u4E73\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u751F\u5F97\u4E00\u5BF9\u597D\u5976\u3002\u867D\u4E0D\u751A\u5927\uFF0C\u5012\u4E5F\u4E30\u8174\u767D\u5AE9\u3002\u6258\u8D77\u6765\u8BA9\u5927\u5BB6\u90FD\u770B\u770B\u3002\u300D</p><p>\u5510\u989C\u7F13\u7F13\u6258\u8D77\u53CC\u4E73\u5C55\u793A\u5728\u4F17\u4EBA\u9762\u524D\u3002\u5750\u5728\u6700\u672B\u4E00\u4F4D\u7684\u4EC7\u767E\u9CCC\u88AB\u5979\u523A\u7A7F\u5927\u817F\uFF0C\u5FC3\u91CC\u6068\u6781\uFF0C\u4E8C\u8BDD\u4E0D\u8BF4\u4FBF\u62E7\u4F4F\u5979\u7684\u4E73\u5934\u72E0\u72E0\u4E00\u626F\u3002</p><p>\u5510\u989C\u75DB\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u4ECD\u54AC\u7259\u7D27\u5FCD\uFF0C\u4EFB\u4ED6\u628A\u81EA\u5DF1\u7684\u4E73\u623F\u626F\u6210\u7EC6\u957F\u7684\u9525\u72B6\u3002</p><p>\u9F99\u6714\u867D\u7136\u4F3C\u61C2\u975E\u61C2\uFF0C\u4F46\u89C1\u6BCD\u4EB2\u5403\u75DB\uFF0C\u7ACB\u523B\u53EB\u9053\uFF1A\u300C\u4F60\u8FD9\u574F\u86CB\uFF01\u653E\u5F00\u6211\u5A18\uFF01\u300D</p><p>\u8BF4\u7740\u4E00\u8DC3\u800C\u8D77\uFF0C\u52A8\u4F5C\u5E72\u51C0\u5229\u7D22\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u628A\u5C06\u4ED6\u62B1\u5728\u6000\u91CC\uFF0C\u7B11\u772F\u772F\u9053\uFF1A\u300C\u51E0\u5C81\u4E86\uFF1F\u300D</p><p>\u9F99\u6714\u660E\u4EAE\u7684\u5927\u773C\u6012\u5149\u95EA\u52A8\uFF0C\u95ED\u7740\u5634\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u300C\u516B\u5C81\u4E86\u3002\u300D\u5510\u989C\u5FCD\u75DB\u9053\u3002</p><p>\u300C\u516B\u5C81\u3002\u6709\u8FD9\u6837\u7684\u529F\u592B\u771F\u662F\u4E86\u4E0D\u8D77\u3002\u300D\u6155\u5BB9\u9F99\u4E3E\u676F\u653E\u5728\u5507\u8FB9\uFF0C\u542B\u7B11\u770B\u7740\u9F99\u6714\u9053\uFF1A\u300C\u77E5\u4E0D\u77E5\u9053\u516B\u5E74\u524D\uFF0C\u4F60\u662F\u4ECE\u54EA\u91CC\u51FA\u6765\u7684\uFF1F\u300D\u8BF4\u7740\u4E00\u996E\u800C\u5C3D\u3002</p><p>\u5510\u989C\u98A4\u58F0\u9053\uFF1A\u300C\u8BA9\u6714\u513F\u5230\u8F66\u91CC\uFF0C\u6211\u2026\u2026\u6211\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8138\u4E0A\u7B11\u610F\u4E0D\u51CF\uFF0C\u671D\u5979\u4EAE\u4EAE\u4E86\u676F\u5E95\u3002\u5510\u989C\u5A07\u8EAF\u4E00\u50F5\uFF0C\u6700\u540E\u8FD8\u662F\u4F9D\u7167\u7EA6\u5B9A\uFF0C\u5728\u4F17\u4EBA\u9762\u524D\u9664\u53BB\u4EB5\u88E4\u3002</p><p>\u6155\u5BB9\u9F99\u62C9\u8D77\u9F99\u6714\u7684\u5C0F\u624B\u6307\u70B9\u7740\u8BF4\uFF1A\u300C\u90A3\u4E2A\u662F\u5973\u4EBA\u7684\u5C44\uFF0C\u4F60\u5C31\u662F\u4ECE\u90A3\u91CC\u9762\u751F\u51FA\u6765\u7684\u3002\u300D</p><p>\u9F99\u6714\u77AA\u5706\u4E86\u773C\u775B\uFF0C\u4E00\u7728\u4E0D\u7728\u5730\u770B\u7740\u6155\u5BB9\u9F99\uFF0C\u7A81\u7136\u72E0\u72E0\u5410\u4E86\u4ED6\u4E00\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u63A0\u8FC7\u4E00\u62B9\u6B23\u8D4F\u7684\u795E\u8272\uFF0C\u6BEB\u4E0D\u4E3A\u610F\u5730\u5927\u7B11\u7740\u64E6\u53BB\u553E\u6CAB\uFF0C\u534A\u664C\u7B11\u58F0\u6E10\u6B47\uFF0C\u300C\u9F99\u592B\u4EBA\u624B\u4E0A\u7684\u529F\u592B\u5927\u5BB6\u90FD\u9886\u6559\u8FC7\u4E86\uFF0C\u4E0D\u77E5\u817F\u95F4\u7684\u529F\u592B\u5982\u4F55\u2026\u2026\u300D\u4ED6\u6307\u4E86\u6307\u56F4\u5750\u7684\u4F17\u4EBA\uFF0C\u300C\u5C31\u6309\u5750\u7684\u987A\u5E8F\uFF0C\u8BA9\u5927\u5BB6\u90FD\u5C1D\u5C1D\u5427\u3002\u300D</p><p>\u5510\u989C\u7B54\u5E94\u7684\u90A3\u4E00\u523B\u4FBF\u77E5\u9053\u6B64\u4E8B\u65E0\u53EF\u907F\u514D\uFF0C\u90A3\u65F6\u5979\u53EA\u6C42\u4FDD\u4F4F\u513F\u5B50\u7684\u6027\u547D\uFF0C\u65E0\u8BBA\u4EFB\u4F55\u803B\u8FB1\u4E5F\u90FD\u613F\u627F\u53D7\uFF0C\u4F46\u4E8B\u5230\u4E34\u5934\uFF0C\u5979\u624D\u77E5\u9053\u8FD9\u79CD\u7F9E\u803B\u662F\u591A\u4E48\u96BE\u4EE5\u5FCD\u53D7\u3002\u5979\u770B\u4E86\u9F99\u6714\u4E00\u773C\uFF0C\u89C1\u513F\u5B50\u5934\u626D\u5230\u4E00\u8FB9\uFF0C\u5FC3\u91CC\u7565\u5FAE\u677E\u4E86\u53E3\u6C14\u3002</p><p>\u4EC7\u767E\u9CCC\u65E9\u5C31\u8131\u6389\u88E4\u5B50\uFF0C\u8D64\u7740\u4E0B\u8EAB\u5750\u5728\u5730\u4E0A\uFF0C\u8089\u68D2\u633A\u5F97\u8001\u9AD8\u3002\u5F53\u90A3\u53CC\u51B0\u51C9\u800C\u53C8\u67D4\u8F6F\u7684\u7389\u624B\u63E1\u4F4F\u9633\u5177\uFF0C\u4ED6\u4E50\u5F97\u7709\u5F00\u773C\u7B11\uFF0C\u671D\u5510\u989C\u81C0\u4E0A\u7528\u529B\u6253\u4E86\u4E00\u638C\uFF0C\u300C\u5FEB\u70B9\u513F\uFF01\u54C8\u54C8\uFF0C\u8FD9\u767E\u6218\u5929\u9F99\u8001\u5A46\u7684\u5C41\u80A1\u54B1\u4E5F\u662F\u8BF4\u6253\u5C31\u6253\u3002\u300D</p><p>\u5510\u989C\u53CC\u819D\u8DEA\u5730\uFF0C\u80CC\u5BF9\u7740\u4EC7\u767E\u9CCC\u7F13\u7F13\u6C89\u8170\u3002\u5F53\u8089\u68D2\u9876\u5230\u81EA\u5DF1\u8D1E\u6D01\u7684\u8089\u4F53\uFF0C\u5FC3\u91CC\u4E0D\u7981\u53C8\u82E6\u53C8\u9178\u53C8\u75DB\u3002</p><p>\u5468\u56F4\u7740\u6570\u5341\u9053\u76EE\u5149\u90FD\u843D\u5728\u5C11\u5987\u7FD8\u8D77\u7684\u5706\u81C0\u4E0A\uFF0C\u5510\u989C\u7389\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u4E00\u5782\u4E0B\u5934\uFF0C\u4ECE\u773C\u89D2\u770B\u5230\u4E00\u6392\u719F\u6089\u7684\u9762\u5B54\u3002\u6240\u6709\u7684\u5934\u9885\u90FD\u662F\u6012\u76EE\u5706\u7741\uFF0C\u4EFF\u4F5B\u8FD8\u6D3B\u7740\u822C\u6012\u89C6\u7740\u5979\u7684\u4E00\u4E3E\u4E00\u52A8\u3002\u5510\u989C\u809D\u80A0\u5BF8\u65AD\uFF0C\u84E6\u7684\u4F0F\u5730\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u4EC7\u767E\u9CCC\u4E1D\u6BEB\u6CA1\u6709\u601C\u9999\u60DC\u7389\u4E4B\u60C5\uFF0C\u9E21\u5DF4\u7A7A\u7B49\u534A\u5929\uFF0C\u4E0D\u8010\u70E6\u8D77\u6765\uFF0C\u4E00\u628A\u4F38\u5230\u5510\u989C\u81C0\u4E0B\uFF0C\u4F7F\u52B2\u638F\u6478\u3002</p><p>\u5510\u989C\u75DB\u5F97\u4FCF\u8138\u626D\u66F2\uFF0C\u6323\u624E\u7740\u6491\u8D77\u7389\u4F53\uFF0C\u91CD\u65B0\u63E1\u4F4F\u8089\u68D2\u9001\u5230\u79D8\u5904\u3002</p><p>\u300C\u5A18\uFF01\u5A18\uFF01\u300D\u9F99\u6714\u6025\u5F97\u5927\u53EB\u8D77\u6765\u3002</p><p>\u300C\u4E56\uFF0C\u522B\u53EB\uFF0C\u300D\u6155\u5BB9\u9F99\u67D4\u58F0\u9053\uFF1A\u300C\u5F53\u5E74\u4F60\u7239\u548C\u4F60\u5A18\u5C31\u662F\u8FD9\u6837\u751F\u4E0B\u4F60\u7684\u3002\u4E00\u4F1A\u513F\u4F60\u5A18\u4F1A\u5F88\u9AD8\u5174\u7684\u2026\u2026\u300D</p><p>\u9F99\u6714\u5C0F\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u62FC\u547D\u9F13\u52B2\u60F3\u6323\u8131\u6155\u5BB9\u9F99\u7684\u624B\u81C2\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u300C\u83BA\u5974\u9E42\u5974\uFF0C\u7167\u987E\u9F99\u516C\u5B50\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u89C1\u5E08\u5A18\u7518\u5FC3\u53D7\u8FB1\uFF0C\u90FD\u662F\u6EE1\u5FC3\u51C4\u82E6\u3002\u4E24\u5973\u95FB\u58F0\u63A5\u8FC7\u9F99\u6714\uFF0C\u62B1\u5728\u6000\u91CC\u5C0F\u58F0\u5475\u62A4\uFF0C\u4E0D\u6562\u770B\u5E08\u5A18\u4E00\u773C\u3002\u53E6\u4E24\u540D\u516B\u6781\u95E8\u5973\u5F92\u6BB5\u79C0\u5BB9\u548C\u65B9\u7389\u73B2\u60CA\u60E7\u4EA4\u52A0\uFF0C\u66F4\u4E0D\u6562\u4F5C\u58F0\u3002</p><p>\u53EA\u8981\u80FD\u4FDD\u4F4F\u513F\u5B50\u7684\u6027\u547D\uFF0C\u4EC0\u4E48\u803B\u8FB1\u4E5F\u65E0\u6240\u8C13\u4E86\uFF0C\u51B5\u4E14\u4EC5\u4EC5\u53EA\u662F\u4E00\u5929\u3002\u5C11\u5987\u64E6\u5E72\u6CEA\u6C34\uFF0C\u770B\u4E86\u513F\u5B50\u4E00\u773C\uFF0C\u300C\u6714\u513F\u8FD8\u5C0F\uFF0C\u4E0D\u4F1A\u77E5\u9053\u81EA\u5DF1\u5728\u505A\u4EC0\u4E48\u3002\u300D\u5979\u8FD9\u6837\u5B89\u6170\u81EA\u5DF1\u3002</p><p>\u9F99\u6714\u786E\u5B9E\u4E0D\u77E5\u9053\u8FD9\u4E9B\u4EBA\u5728\u505A\u4EC0\u4E48\uFF0C\u4F46\u6BCD\u4EB2\u5149\u7740\u8EAB\u5B50\u88AB\u4EBA\u53C8\u6390\u53C8\u62E7\uFF0C\u80AF\u5B9A\u662F\u53D7\u6B3A\u8D1F\u4E86\u3002\u5A18\u8DEA\u5750\u5728\u5730\u4E0A\uFF0C\u628A\u90A3\u4E9B\u7537\u4EBA\u53C8\u9ED1\u53C8\u4E11\u7684\u4E1C\u897F\u653E\u5230\u81EA\u5DF1\u767D\u751F\u751F\u7684\u5927\u817F\u4E2D\u95F4\uFF0C\u54AC\u7740\u7259\u5750\u4E0B\u53BB\u3002\u4ED6\u770B\u89C1\u90A3\u6839\u9ED1\u9ED1\u7684\u4E1C\u897F\u4E00\u70B9\u70B9\u8FDB\u5230\u53EB\u300C\u5C44\u300D\u7684\u90E8\u4F4D\u91CC\uFF0C\u90A3\u4E9B\u7537\u4EBA\u5F88\u5F00\u5FC3\u7684\u7B11\u4E86\u8D77\u6765\uFF0C\u800C\u5A18\u5374\u54ED\u4E2A\u4E0D\u505C\u3002</p><p>\u9F99\u6714\u53CC\u81C2\u4E00\u6323\uFF0C\u767D\u6C0F\u59D0\u59B9\u6CA1\u60F3\u5230\u8FD9\u4E2A\u516B\u5C81\u7684\u5B69\u5B50\u529B\u6C14\u4F1A\u8FD9\u4E48\u5927\uFF0C\u7ADF\u7136\u88AB\u4ED6\u6323\u8131\u3002</p><p>\u9F99\u6714\u731B\u7136\u6251\u5230\u4EC7\u767E\u9CCC\u8EAB\u524D\uFF0C\u5DE6\u624B\u62B1\u4F4F\u6BCD\u4EB2\u7684\u80F3\u818A\uFF0C\u53F3\u624B\u4E00\u62F3\u8F70\u51FA\u3002\u4EC7\u767E\u9CCC\u6B63\u5728\u5F97\u610F\uFF0C\u867D\u7136\u52C9\u5F3A\u907F\u5F00\uFF0C\u4E5F\u8EB2\u5F97\u72FC\u72C8\u4E0D\u582A\u3002</p><p>\u300C\u5C0F\u5154\u5D3D\u5B50\uFF01\u300D\u4ED6\u5927\u9A82\u4E00\u58F0\uFF0C\u53F3\u624B\u63E1\u6210\u9E21\u722A\uFF0C\u671D\u5C0F\u5B69\u80F8\u53E3\u72E0\u72E0\u6293\u4E0B\u3002\u5510\u989C\u614C\u5FD9\u659C\u8098\u6A2A\u6321\uFF0C\u5DF2\u7ECF\u6765\u4E0D\u53CA\u3002</p><p>\u9F99\u6714\u77ED\u81C2\u4E00\u4E3E\uFF0C\u8FDE\u9000\u51E0\u6B65\uFF0C\u5C0F\u8138\u53D1\u767D\u3002</p><p>\u300C\u6714\u513F\uFF01\u6714\u513F\uFF01\u300D\u5510\u989C\u60CA\u53EB\u7740\u722C\u8D77\u6765\uFF0C\u5374\u88AB\u4EC7\u767E\u9CCC\u6402\u4F4F\u8170\u80A2\uFF0C\u91CD\u91CD\u4E00\u6309\u3002\u5C11\u5987\u75DB\u53EB\u58F0\u4E2D\uFF0C\u8089\u68D2\u5DF2\u6345\u5165\u4F53\u5185\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5DE6\u53F3\u6402\u4F4F\u9F99\u6714\uFF0C\u300C\u5C0F\u6714\uFF01\u53D7\u4F24\u4E86\u5417\uFF1F\u300D</p><p>\u534A\u664C\uFF0C\u9F99\u6714\u900F\u51FA\u4E00\u53E3\u6C14\uFF0C\u8138\u4E0A\u6162\u6162\u6062\u590D\u8840\u8272\u3002\u89C1\u8FD9\u5C0F\u5BB6\u4F19\u7ADF\u80FD\u6321\u4F4F\u4EC7\u767E\u9CCC\u5341\u6210\u529F\u529B\u7684\u4E00\u51FB\uFF0C\u5728\u573A\u7684\u4F17\u4EBA\u65E0\u4E0D\u6697\u6697\u79F0\u5947\u3002\u9F99\u6714\u773C\u5708\u53D1\u7EA2\uFF0C\u6241\u7740\u5C0F\u5634\u54ED\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u600E\u4E48\u4E0D\u6253\u4ED6\u554A\u2026\u2026\u4F60\u6253\u4ED6\u554A\u2026\u2026\u300D</p><p>\u5510\u989C\u53CC\u624B\u6342\u4F4F\u9762\u5B54\uFF0C\u6CEA\u6C34\u4ECE\u6307\u7F1D\u91CC\u4E0D\u4F4F\u6D8C\u51FA\u3002</p><p>\u9F99\u6714\u54C7\u7684\u5927\u54ED\u8D77\u6765\uFF0C\u60F9\u5F97\u767D\u6C0F\u59D0\u59B9\u4E5F\u6389\u4E0B\u6CEA\u6765\u3002\u4E24\u5973\u4E00\u8FB9\u7ED9\u9F99\u6714\u64E6\u6CEA\uFF0C\u4E00\u8FB9\u98A4\u58F0\u9053\uFF1A\u300C\u5C0F\u6714\u522B\u54ED\uFF0C\u5E08\u5A18\u8FD9\u90FD\u662F\u4E3A\u4F60\u597D\u2026\u2026\u300D</p><p>\u539F\u6765\u6253\u5B9A\u4E3B\u610F\u4E0D\u7406\u4E0D\u776C\u7684\u7D2B\u73AB\u518D\u4E5F\u770B\u4E0D\u4E0B\u53BB\uFF0C\u6697\u6697\u626F\u4E86\u626F\u6155\u5BB9\u9F99\u7684\u8863\u8896\u3002\u6155\u5BB9\u9F99\u5FC3\u4E0B\u4F1A\u610F\uFF0C\u5429\u5490\u9053\uFF1A\u300C\u62B1\u4ED6\u4E0A\u8F66\u5427\uFF0C\u8BA9\u4ED6\u7761\u4E00\u4F1A\u513F\u3002\u300D</p><p>\u5510\u989C\u611F\u6FC0\u5730\u770B\u7740\u6155\u5BB9\u9F99\uFF0C\u6CA1\u6709\u4E00\u4E2A\u6BCD\u4EB2\u4F1A\u613F\u610F\u5728\u5728\u513F\u5B50\u9762\u524D\u88AB\u4EBA\u5978\u6DEB\uFF0C\u7EB5\u7136\u4ED6\u53EA\u6709\u516B\u5C81\u3002</p><p>\u5F85\u767D\u6C0F\u59D0\u59B9\u5E26\u9F99\u6714\u79BB\u5F00\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u5148\u6309\u987A\u5E8F\u5C1D\u5C1D\u5927\u4F19\u7684\u9E21\u5DF4\uFF0C\u4E00\u4F1A\u513F\u4F60\u81EA\u5DF1\u6311\u7740\u6765\uFF0C\u8BA9\u6BCF\u4E2A\u4EBA\u90FD\u64CD\u4F60\u4E00\u6B21\u3002\u8FD8\u6709\u4E00\u6574\u5929\u7684\u65F6\u95F4\uFF0C\u4E0D\u7528\u6025\u3002\u300D</p><p>\u5510\u989C\u5FCD\u4F4F\u7F9E\u803B\uFF0C\u633A\u7740\u5706\u81C0\uFF0C\u5C06\u4E00\u6839\u6839\u957F\u77ED\u4E0D\u4E00\u7684\u9633\u5177\u4F9D\u6B21\u7EB3\u5165\u4F53\u5185\uFF0C\u7528\u81EA\u5DF1\u6700\u73CD\u8D35\u7684\u8D1E\u6D01\uFF0C\u6700\u67D4\u5AE9\u7684\u8089\u7A74\u6362\u53D6\u513F\u5B50\u7684\u751F\u547D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u4ECA\u65E5\u4E00\u6218\uFF0C\u91D1\u957F\u8001\u640F\u6740\u767E\u6218\u5929\u9F99\uFF1B\u7075\u7389\u957F\u8001\u640F\u6740\u8463\u8C79\u5A01\u3001\u5C39\u8C61\u5D07\uFF0C\u91CD\u4F24\u8BB8\u72EE\u96C4\uFF0C\u7ACB\u4E0B\u5927\u529F\u3002\u672C\u5BAB\u656C\u4E24\u4F4D\u4E00\u676F\u3002\u300D</p><p>\u5F85\u4E24\u4EBA\u996E\u5E72\uFF0C\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u9014\u4E2D\u65E0\u4EE5\u916C\u529F\uFF0C\u4ECA\u65E5\u7684\u6218\u5229\u54C1\u5C31\u8D4F\u4E24\u4F4D\u957F\u8001\u5C1D\u9C9C\u3002\u300D</p><p>\u4E24\u540D\u5973\u5F1F\u5B50\u88AB\u63A8\u5230\u5E2D\u524D\uFF0C\u6BB5\u79C0\u5BB9\u5E74\u7EA6\u4E8C\u5341\u4E09\u56DB\uFF0C\u76F8\u8C8C\u6E05\u4E3D\uFF0C\u65B9\u7389\u73B2\u7565\u5C0F\u51E0\u5C81\uFF0C\u76AE\u80A4\u767D\u7699\u3002\u5E08\u95E8\u5C3D\u6570\u88AB\u5C60\uFF0C\u8FDE\u5E08\u5A18\u90FD\u88AB\u4EBA\u6DEB\u8FB1\uFF0C\u81EA\u5DF1\u7684\u906D\u9047\u53EF\u60F3\u800C\u77E5\u3002\u4E24\u5973\u50CF\u53D7\u60CA\u7684\u7F8A\u7F94\uFF0C\u5413\u5F97\u9762\u65E0\u4EBA\u8272\u3002</p><p>\u300C\u4E24\u4F4D\u957F\u8001\u4EFB\u9009\u4E00\u4EBA\u5427\u3002\u300D</p><p>\u7075\u7389\u6253\u91CF\u4E86\u4E24\u5973\u4E00\u773C\uFF0C\u7B11\u9053\uFF1A\u300C\u90A3\u4E2A\u5C0F\u7684\u5F53\u662F\u5904\u5B50\uFF0C\u5C31\u8BF7\u91D1\u957F\u8001\u7B11\u7EB3\u5427\u3002\u300D</p><p>\u91D1\u5F00\u7532\u4E5F\u4E0D\u63A8\u8F9E\uFF0C\u62CE\u5C0F\u9E21\u822C\u5C06\u65B9\u7389\u73B2\u62CE\u4E86\u8D77\u6765\uFF0C\u4E00\u628A\u5C06\u5C11\u5973\u7684\u8863\u886B\u5C3D\u6570\u626F\u53BB\u3002</p><p>\u7075\u7389\u56F4\u7740\u6BB5\u79C0\u5BB9\u8F6C\u4E86\u4E00\u5708\uFF0C\u9F3B\u7FFC\u4E0D\u4F4F\u62BD\u52A8\u3002</p><p>\u300C\u55EF\uFF0C\u8FD8\u4E0D\u574F\u3002\u300D\u4ED6\u7B11\u9053\uFF1A\u300C\u5BAB\u4E3B\u730E\u4E86\u4E24\u53EA\u9EC4\u7F8A\uFF0C\u8D2B\u9053\u65E0\u4EE5\u4E3A\u62A5\uFF0C\u5C31\u501F\u5BAB\u4E3B\u7684\u8D4F\u8D50\u8BF7\u8BF8\u4F4D\u5C1D\u5C1D\u9C9C\u5427\u3002\u300D</p><p>\u6BB5\u79C0\u5BB9\u83AB\u540D\u5176\u5999\uFF0C\u4F46\u8FD8\u662F\u4F9D\u4ED6\u7684\u5429\u5490\u8131\u4E0B\u8863\u88D9\uFF0C\u8EBA\u5728\u7F8A\u76AE\u4E0A\u3002</p><p>\u7075\u7389\u7EC6\u957F\u7684\u624B\u6307\u6309\u5728\u5973\u5B50\u4F53\u4E0A\uFF0C\u6478\u4E86\u6478\u9AA8\u8089\uFF0C\u70B9\u5934\u7B11\u9053\uFF1A\u300C\u8EAB\u6000\u6B66\u529F\u7684\u5973\u5B50\uFF0C\u808C\u4F53\u67D4\u97E7\uFF0C\u56BC\u8D77\u6765\u5206\u5916\u6709\u5473\u3002\u300D</p><p>\u6BB5\u79C0\u5BB9\u8138\u8272\u5927\u53D8\uFF0C\u60CA\u53EB\u7740\u5750\u8D77\u8EAB\u6765\u3002\u7075\u7389\u62AC\u624B\u4E00\u63A8\uFF0C\u5C06\u5979\u6309\u5728\u5730\u4E0A\uFF0C\u987A\u52BF\u5C01\u4E86\u5979\u5929\u7A81\u3001\u534E\u76D6\u3001\u81BB\u4E2D\u8BF8\u7A74\uFF0C\u7136\u540E\u4ECE\u8896\u4E2D\u638F\u51FA\u4E00\u628A\u624B\u6307\u5BBD\u7A84\u7684\u8584\u5203\u3002</p><p>\u4F17\u4EBA\u90FD\u77E5\u9053\u7075\u7389\u6700\u55DC\u4EBA\u8089\uFF0C\u89C1\u72B6\u90FD\u77AA\u5927\u4E86\u773C\u775B\u3002\u7D2B\u73AB\u9762\u65E0\u8868\u60C5\u5730\u53C9\u8D77\u4E00\u7247\u70E4\u597D\u7684\u7F8A\u8089\uFF0C\u5E73\u9759\u5730\u5403\u4E86\u4E0B\u53BB\u3002\u8FDE\u91CE\u517D\u4E5F\u4E0D\u4F1A\u5403\u540C\u7C7B\u7684\u8089\uFF0C\u4F46\u8FD9\u5E2E\u4EBA\u662F\u79BD\u517D\u4E0D\u5982\u3002</p><p>\u7075\u7389\u6293\u4F4F\u6BB5\u79C0\u5BB9\u80F8\u524D\u7684\u8089\u56E2\uFF0C\u8584\u5203\u4ECE\u4E73\u6839\u7F13\u7F13\u5207\u5165\u3002\u6BB5\u79C0\u5BB9\u7C89\u8EAF\u4E00\u7D27\uFF0C\u88AB\u5C01\u4F4F\u7A74\u9053\u7684\u5589\u5499\u53EA\u53D1\u51FA\u7EC6\u5FAE\u7684\u53EB\u58F0\u3002</p><p>\u4F24\u53E3\u8840\u5982\u6CC9\u6D8C\uFF0C\u4E30\u6EE1\u7684\u4E73\u623F\u671D\u4E0A\u6380\u5F00\uFF0C\u8840\u6DCB\u6DCB\u7684\u5AE9\u8089\u8FD8\u9690\u9690\u8DF3\u52A8\u3002\u8427\u4F5B\u5974\u65E9\u5C31\u95ED\u4E0A\u7F8E\u76EE\uFF0C\u628A\u81FB\u9996\u57CB\u5728\u6155\u5BB9\u9F99\u6E29\u6696\u7684\u6000\u62B1\u91CC\u3002</p><p>\u5510\u989C\u6B64\u65F6\u5DF2\u8D70\u5230\u7B2C\u4E94\u4E2A\u5E2E\u4F17\u8EAB\u524D\uFF0C\u5979\u6EE1\u5FC3\u90FD\u662F\u523B\u9AA8\u7684\u7F9E\u803B\uFF0C\u6CA1\u6709\u7559\u610F\u7075\u7389\u6240\u8BF4\u7684\u8BDD\uFF0C\u5F53\u770B\u5230\u4ED6\u5272\u4E0B\u5F1F\u5B50\u4E73\u623F\u65F6\u624D\u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u987F\u65F6\u4E24\u817F\u4E00\u8F6F\uFF0C\u5750\u5728\u4E5E\u4F0F\u7A77\u9686\u817F\u4E0A\uFF0C\u7AD9\u4E0D\u8D77\u6765\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
