import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_89" tabindex="-1"><a class="header-anchor" href="#_89" aria-hidden="true">#</a> 89</h1><p>\u300C\u5973\u5B50\u4E73\u8089\u6700\u4E3A\u7F8E\u5473\uFF0C\u4EBA\u79F0\u60F3\u8089\uFF0C\u300D\u7075\u7389\u624B\u8155\u7A33\u7A33\u65CB\u4E86\u4E00\u5468\uFF0C\u5200\u950B\u8FC7\u5904\uFF0C\u4E73\u8089\u6CB9\u8102\u822C\u5206\u5F00\u3002\u4E00\u62AC\u624B\uFF0C\u4E73\u623F\u7ACB\u523B\u79BB\u4F53\u800C\u8D77\uFF0C\u6BB5\u79C0\u5BB9\u80F8\u524D\u7559\u4E0B\u4E00\u4E2A\u6574\u6574\u9F50\u9F50\u7684\u6D51\u5706\u4F24\u75D5\u3002\u6DCC\u8840\u7684\u96EA\u4E73\u5E73\u5E73\u60AC\u5728\u638C\u4E0B\uFF0C\u591C\u8272\u4E2D\u663E\u5F97\u8BE1\u4E3D\u65E0\u6BD4\u3002</p><p>\u7075\u7389\u4E00\u8FB9\u5C06\u4E73\u8089\u5185\u7684\u8840\u6DB2\u6CA5\u51C0\uFF0C\u4E00\u8FB9\u89E3\u8BF4\u9053\uFF1A\u300C\u4EBA\u8089\u6781\u662F\u6ECB\u8865\uFF0C\u7136\u5176\u5473\u7518\u6027\u70ED\uFF0C\u591A\u98DF\u6613\u4F7F\u4EBA\u71E5\u72C2\u3002\u300D\u4ED6\u4ECE\u6000\u91CC\u6478\u51FA\u4E00\u4E2A\u767D\u4EAE\u7684\u5370\u82B1\u76AE\u56CA\uFF0C\u5F80\u4E73\u8089\u4E0A\u7565\u6492\u4E86\u4E00\u4E9B\u6DE1\u9EC4\u7684\u7C89\u672B\uFF0C\u300C\u8FD9\u662F\u8D2B\u9053\u8C03\u5236\u7684\u4F50\u6599\uFF0C\u4E0D\u4EC5\u53EF\u89E3\u5176\u706B\u6BD2\uFF0C\u8FD8\u80FD\u9664\u53BB\u4EBA\u8089\u7684\u82E6\u5473\uFF0C\u70E4\u6210\u4E4B\u540E\uFF0C\u5473\u9053\u5206\u5916\u9999\u5AE9\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u602A\u58F0\u9053\uFF1A\u300C\u4F50\u6599\u90FD\u5E26\u5728\u8EAB\u4E0A\uFF0C\u725B\u9F3B\u5B50\u4E0D\u4F1A\u6574\u5929\u90FD\u76D8\u7B97\u7740\u5403\u4EBA\u8089\u5427\uFF1F\u300D</p><p>\u7075\u7389\u7B11\u9053\uFF1A\u300C\u5B89\u5144\u4E0D\u5FC5\u62C5\u5FC3\uFF0C\u8D2B\u9053\u4E0D\u5403\u7537\u4EBA\u3002\u300D</p><p>\u5B89\u5B50\u5B8F\u54C8\u54C8\u5927\u7B11\uFF0C\u7275\u52A8\u4F24\u52BF\uFF0C\u53C8\u5410\u4E86\u53E3\u8840\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u5374\u505C\u5728\u7075\u7389\u624B\u4E2D\u7684\u76AE\u56CA\u4E0A\u3002\u90A3\u53EA\u76AE\u56CA\u6709\u624B\u638C\u5927\u5C0F\uFF0C\u8D28\u5730\u7EC6\u767D\u67D4\u6ED1\uFF0C\u8868\u9762\u5370\u7740\u4E00\u679D\u9C9C\u7EA2\u7684\u6885\u82B1\uFF0C\u8272\u6CFD\u5982\u65B0\u3002\u96BE\u5F97\u7684\u662F\u76AE\u56CA\u5168\u65E0\u7F1D\u8865\u75D5\u8FF9\uFF0C\u5C31\u50CF\u5929\u7136\u751F\u6210\u4E00\u822C\u3002\u6155\u5BB9\u9F99\u4ED4\u7EC6\u770B\u53BB\uFF0C\u53EA\u89C1\u76AE\u56CA\u5E95\u4E0B\u90A3\u6735\u7EA2\u6885\u5F62\u72B6\u7A81\u8D77\uFF0C\u5A07\u4FCF\u53EF\u7231\u3002\u4ED6\u76EE\u5149\u4E00\u95EA\uFF0C\u300C\u9053\u957F\u8FD9\u53EA\u76AE\u56CA\u662F\u4F55\u7269\u5236\u6210\uFF1F\u300D</p><p>\u7075\u7389\u606D\u606D\u656C\u656C\u5448\u4E0A\u76AE\u56CA\uFF0C\u300C\u5BAB\u4E3B\u8BF7\u770B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u63A5\u5230\u624B\u4E2D\uFF0C\u987F\u89C9\u5F02\u6837\u3002\u76AE\u56CA\u5F00\u53E3\u5F88\u5927\uFF0C\u5468\u56F4\u6253\u4E86\u51E0\u4E2A\u5C0F\u5B54\uFF0C\u7A7F\u7740\u7EF3\u7D22\u3002</p><p>\u76AE\u8D28\u53C8\u7EC6\u53C8\u8F6F\uFF0C\u9690\u9690\u80FD\u770B\u5230\u808C\u80A4\u7684\u7EB9\u8DEF\uFF0C\u90A3\u7C92\u7A81\u8D77\u5C0F\u82E5\u6A31\u6843\uFF0C\u5F39\u6027\u5341\u8DB3\uFF0C\u6B64\u65F6\u770B\u6765\uFF0C\u5206\u660E\u662F\u4E00\u53EA\u5B8C\u6574\u7684\u4E73\u623F\u3002\u6155\u5BB9\u9F99\u9976\u6709\u5174\u8DA3\u5730\u770B\u7740\u4E0A\u9762\u7684\u7EB9\u9970\uFF0C\u624D\u53D1\u73B0\u90A3\u679D\u6885\u82B1\u5E76\u975E\u5370\u5236\uFF0C\u800C\u662F\u7528\u7EC6\u9488\u523A\u6210\u3002</p><p>\u300C\u8FD9\u662F\u5C5E\u4E0B\u4ECE\u6C5F\u5357\u540D\u5993\u8C22\u5AE3\u6885\u4F53\u4E0A\u91C7\u6765\u7684\u3002\u53EF\u60DC\u5265\u5236\u4E0D\u5F53\uFF0C\u53EA\u5236\u6210\u4E00\u53EA\u3002\u300D</p><p>\u300C\u8C22\u5AE3\u6885\u2026\u2026\u5355\u770B\u8FD9\u4E73\u623F\u4FBF\u662F\u4E2A\u7EDD\u8272\u5973\u5B50\u3002\u80FD\u5F97\u9053\u957F\u9752\u773C\u6709\u52A0\uFF0C\u4E5F\u662F\u5979\u7684\u798F\u6C14\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u8FD9\u6885\u82B1\u53EF\u662F\u9053\u957F\u6240\u7EB9\uFF1F\u300D</p><p>\u300C\u6B63\u662F\u3002\u300D</p><p>\u300C\u597D\u624B\u827A\uFF01\u597D\u76AE\u80A4\uFF01\u300D\u6155\u5BB9\u9F99\u7231\u4E0D\u91CA\u624B\u5730\u53CD\u8986\u89C2\u8D4F\uFF0C\u7136\u540E\u9012\u7ED9\u7D2B\u73AB\uFF0C\u300C\u4F60\u770B\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u6362\u4F5C\u522B\u7684\u5973\u5B50\u82E5\u975E\u5413\u5F97\u5C16\u53EB\uFF0C\u4FBF\u662F\u5FC3\u60CA\u8089\u8DF3\uFF0C\u96BE\u4EE5\u81EA\u5DF2\u3002\u7D2B\u73AB\u5374\u5766\u7136\u63A5\u8FC7\u8FD9\u53EA\u4E73\u623F\u5236\u6210\u76AE\u56CA\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5F88\u6F02\u4EAE\uFF0C\u9053\u957F\u679C\u7136\u522B\u51FA\u5FC3\u88C1\u3002\u300D</p><p>\u7075\u7389\u5DF2\u7ECF\u5C06\u6BB5\u79C0\u5BB9\u90A3\u53EA\u4E73\u623F\u9C9C\u8840\u6CA5\u5C3D\uFF0C\u62B9\u5300\u4F50\u6599\uFF0C\u6B64\u65F6\u6B63\u5F92\u624B\u634F\u7740\u4E73\u5934\uFF0C\u653E\u5728\u7BDD\u706B\u4E0A\u7EC6\u70E4\u3002\u2192\u6587\xB7\u5187\xB7\u4EBA\xB7\u5187\xB7\u4E66\xB7\u5187\xB7\u5C4B\u2190</p><p>\u9C9C\u8840\u5E72\u7ED3\uFF0C\u5E73\u6574\u7684\u4F24\u53E3\u6E10\u6E10\u6536\u7D27\uFF0C\u663E\u51FA\u808C\u8089\u7684\u7EB9\u8DEF\u3002\u53E6\u4E00\u9762\u7684\u4E73\u7403\u4F9D\u7136\u5706\u6DA6\uFF0C\u767D\u5AE9\u7684\u76AE\u80A4\u6162\u6162\u53D1\u9EC4\uFF0C\u5192\u51FA\u4E00\u5C42\u7EC6\u5BC6\u7684\u6CB9\u8102\u3002\u4E0D\u8FC7\u65F6\u4FBF\u98D8\u51FA\u4E00\u80A1\u8089\u9999\u3002\u661F\u6708\u6E56\u4F17\u4EBA\u998B\u6D8E\u6B32\u6EF4\uFF0C\u987F\u89C9\u5634\u91CC\u7684\u9EC4\u7F8A\u8089\u6BEB\u65E0\u6ECB\u5473\u3002</p><p>\u5510\u989C\u597D\u4E0D\u5BB9\u6613\u6491\u8D77\u8EAB\u5B50\uFF0C\u7C89\u5AE9\u7684\u5706\u81C0\u8038\u52A8\u51E0\u4E0B\uFF0C\u4FBF\u8D77\u8EAB\u722C\u5230\u53E6\u4E00\u4EBA\u8EAB\u524D\uFF0C\u7528\u8089\u7A74\u4F9D\u6B21\u5957\u5F04\u4F17\u4EBA\u7684\u8089\u68D2\u3002\u65B9\u7389\u73B2\u5A07\u8EAF\u6574\u4E2A\u538B\u5728\u91D1\u5F00\u7532\u96C4\u58EE\u7684\u8EAB\u4F53\u4E0B\uFF0C\u53EA\u6709\u4E00\u622A\u767D\u767D\u7684\u5C0F\u817F\uFF0C\u4ECE\u91D1\u5F00\u7532\u8170\u4FA7\u4F38\u51FA\uFF0C\u968F\u7740\u4ED6\u7684\u633A\u5F04\uFF0C\u65E0\u529B\u5730\u6447\u6643\u7740\u3002</p><p>\u6BB5\u79C0\u5BB9\u76F4\u76F4\u8EBA\u5728\u5730\u4E0A\uFF0C\u5DF2\u7136\u660F\u8FF7\u3002\u5979\u5168\u8EAB\u8840\u6DB2\u4F3C\u4E4E\u90FD\u96C6\u4E2D\u5728\u5BBD\u9614\u7684\u4F24\u53E3\u4E2D\uFF0C\u5931\u53BB\u4E00\u53EA\u4E73\u623F\u7684\u7389\u4F53\u50CF\u900F\u660E\u822C\u6BEB\u65E0\u8840\u8272\u3002\u4F46\u7A74\u9053\u88AB\u5236\u540E\u8840\u6D41\u4E0D\u7545\uFF0C\u5931\u8840\u8FD8\u672A\u5371\u53CA\u751F\u547D\u3002</p><p>\u7075\u7389\u4E1D\u6BEB\u4E0D\u60E7\u70C8\u706B\uFF0C\u8D64\u624B\u62FF\u7740\u90A3\u56E2\u4E73\u8089\u4ED4\u7EC6\u7FFB\u5F04\u3002\u5F85\u4E73\u623F\u8272\u6CFD\u53D8\u5F97\u91D1\u9EC4\uFF0C\u624D\u53CC\u624B\u6367\u5230\u6155\u5BB9\u9F99\u9762\u524D\u3002</p><p>\u5706\u4E73\u5F62\u72B6\u4E00\u5982\u751F\u524D\uFF0C\u4F9D\u7136\u9971\u6EE1\u5982\u6545\u3002\u4E73\u5934\u8272\u6CFD\u6697\u7EA2\uFF0C\u786C\u786C\u7ACB\u5728\u6D41\u6EE1\u91D1\u9EC4\u8272\u6CB9\u8102\u7684\u4E73\u7403\u4E0A\u3002\u6155\u5BB9\u9F99\u5C06\u4E73\u6655\u8FDE\u540C\u4E73\u5934\u4E00\u5E76\u5207\u4E0B\uFF0C\u653E\u5728\u53E3\u5185\u3002\u4E73\u5934\u67D4\u8F6F\u800C\u53C8\u575A\u97E7\uFF0C\u4E73\u6655\u5916\u76AE\u7126\u8106\uFF0C\u91CC\u9762\u5374\u7EC6\u5AE9\u65E0\u6BD4\uFF0C\u4E00\u54AC\u4E4B\u4E0B\u987F\u65F6\u7126\u9999\u6EE1\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u5207\u4E0B\u4E00\u7247\u9012\u5230\u8427\u4F5B\u5974\u5507\u8FB9\uFF0C\u7B11\u9053\uFF1A\u300C\u6765\uFF0C\u5F20\u5F00\u5634\uFF0C\u54AC\u4E00\u53E3\u3002\u300D</p><p>\u7F8E\u5987\u7709\u5934\u62E7\u7D27\uFF0C\u76F4\u76F4\u76EF\u7740\u90A3\u7247\u5AE9\u8089\uFF0C\u773C\u4E2D\u53C8\u662F\u5BB3\u6015\u53C8\u662F\u6076\u5FC3\u3002\u534A\u664C\uFF0C\u5979\u95ED\u4E0A\u773C\uFF0C\u52C9\u5F3A\u5F20\u5F00\u5C0F\u5634\u3002</p><p>\u7D2B\u73AB\u5288\u624B\u593A\u8FC7\u8089\u7247\uFF0C\u72E0\u72E0\u585E\u5230\u5634\u91CC\uFF0C\u54AC\u7259\u77AA\u7740\u6155\u5BB9\u9F99\u3002\u6155\u5BB9\u9F99\u4E00\u7B11\u4F5C\u7F62\u3002\u7D2B\u73AB\u767D\u7740\u8138\uFF0C\u820C\u5934\u4E00\u52A8\u4E5F\u4E0D\u6562\u52A8\u3002\u8FC7\u4E86\u7247\u523B\uFF0C\u6084\u6084\u5410\u5230\u4E00\u65C1\uFF0C\u6155\u5BB9\u9F99\u4E5F\u8BC8\u505A\u4E0D\u77E5\u3002</p><p>\u5B89\u5B50\u5B8F\u7B49\u4E0D\u6025\u4E86\uFF0C\u53EB\u9053\uFF1A\u300C\u725B\u9F3B\u5B50\uFF01\u4F60\u5FEB\u70B9\uFF0C\u7ED9\u5144\u5F1F\u5F04\u5757\u5927\u7684\uFF01\u300D\u77F3\u880D\u4E5F\u53EB\u9053\uFF1A\u300C\u9053\u957F\uFF0C\u7ED9\u5C0F\u5F1F\u4E5F\u6765\u4E00\u5757\u3002\u300D</p><p>\u7075\u7389\u7B11\u9053\uFF1A\u300C\u4F60\u4E00\u5757\u4ED6\u4E00\u5757\uFF0C\u4E5F\u4E0D\u6015\u7D2F\u6B7B\u8D2B\u9053\u3002\u5E72\u8106\u4E00\u6B21\u70E4\u5B8C\uFF01\u300D</p><p>\u4F17\u4EBA\u7EB7\u7EB7\u53EB\u597D\u3002</p><p>\u300C\u70E4\u8089\u91CD\u5728\u65B0\u9C9C\uFF0C\u82E5\u662F\u6B7B\u5C38\uFF0C\u5473\u9053\u5C31\u5DEE\u5F97\u8FDC\u4E86\u3002\u300D\u7075\u7389\u4E00\u8FB9\u4F20\u6388\u7ECF\u9A8C\uFF0C\u4E00\u8FB9\u8FD0\u529F\u62CD\u9192\u660F\u8FF7\u7684\u5973\u5B50\u3002</p><p>\u6BB5\u79C0\u5BB9\u832B\u7136\u7741\u5F00\u53CC\u773C\uFF0C\u5F85\u770B\u6E05\u6155\u5BB9\u9F99\u624B\u91CC\u7684\u8089\u56E2\u6B63\u662F\u81EA\u5DF1\u7684\u4E73\u623F\u65F6\uFF0C\u987F\u65F6\u53C8\u660F\u4E86\u8FC7\u53BB\u3002</p><p>\u7075\u7389\u501F\u6765\u880D\u5C3E\u97AD\uFF0C\u624B\u8155\u4E00\u632F\uFF0C\u5E03\u6EE1\u5012\u523A\u7684\u97AD\u8EAB\u7ACB\u523B\u7AD6\u5F97\u7B14\u76F4\u3002\u4ED6\u89E3\u5F00\u6BB5\u79C0\u5BB9\u7684\u7A74\u9053\uFF0C\u4F38\u811A\u8E0F\u4F4F\u5979\u7684\u4E00\u53EA\u811A\u8E1D\uFF0C\u7136\u540E\u63E1\u4F4F\u53E6\u4E00\u53EA\u811A\u8E1D\u5411\u4E0A\u4E00\u63A8\uFF0C\u63A5\u7740\u5C06\u880D\u5C3E\u97AD\u76F4\u76F4\u523A\u8FDB\u5973\u5B50\u7684\u83CA\u809B\u4E2D\u3002\u523A\u5165\u4E09\u5BF8\u6DF1\u6D45\u540E\uFF0C\u7F13\u7F13\u56DE\u62C9\u3002</p><p>\u6BB5\u79C0\u5BB9\u75DB\u6781\u800C\u9192\uFF0C\u4E24\u624B\u62FC\u547D\u6309\u4F4F\u817F\u95F4\u3002</p><p>\u54C0\u53F7\u58F0\u4E2D\uFF0C\u880D\u5C3E\u97AD\u950B\u5229\u7684\u5012\u523A\u5212\u7834\u6BB5\u79C0\u5BB9\u7684\u624B\u6307\uFF0C\u4ECE\u767D\u7699\u7684\u7EA4\u624B\u4E4B\u95F4\u94A9\u51FA\u4E00\u622A\u6E7F\u6DCB\u6DCB\u7684\u8089\u4F53\uFF0C\u8D8A\u62D6\u8D8A\u957F\u3002</p><p>\u7075\u7389\u7A33\u4F4F\u529B\u9053\uFF0C\u5C0F\u5FC3\u5730\u94A9\u51FA\u4E00\u6BB5\u809B\u80A0\uFF0C\u7136\u540E\u653E\u4E0B\u880D\u5C3E\u97AD\uFF0C\u5C06\u80A0\u9053\u4E0E\u83CA\u809B\u76F8\u8FDE\u7684\u90E8\u4F4D\u5207\u5F00\u3002\u5B8C\u5168\u5410\u9732\u7684\u809B\u7AA6\u7ACB\u523B\u6536\u7F29\uFF0C\u53C8\u56DE\u590D\u6210\u6700\u521D\u7684\u7D27\u7F29\u6A21\u6837\uFF0C\u62D6\u51FA\u4E09\u5BF8\u7684\u5927\u80A0\u50CF\u662F\u63D2\u5728\u809B\u95E8\u4E2D\u7684\u5F02\u7269\uFF0C\u8F6F\u8F6F\u62D6\u5728\u81C0\u95F4\u3002</p><p>\u7075\u7389\u677E\u5F00\u6BB5\u79C0\u5BB9\u7684\u4E24\u817F\uFF0C\u5973\u5B50\u7ACB\u523B\u7684\u6323\u624E\u7740\u5411\u5916\u722C\u53BB\uFF0C\u53EA\u60F3\u8FDC\u8FDC\u79BB\u5F00\u8FD9\u4E2A\u6076\u9B54\u3002\u722C\u51FA\u4E08\u8BB8\uFF0C\u5979\u624D\u89C9\u51FA\u5F02\u6837\uFF0C\u56DE\u5934\u4E00\u770B\uFF0C\u53EA\u89C1\u81EA\u5DF1\u7684\u80A0\u4F53\u8FD8\u63E1\u5728\u9053\u4EBA\u624B\u4E2D\uFF0C\u4E00\u6761\u957F\u957F\u7684\u9C9C\u7EA2\u8089\u80A0\u4E00\u76F4\u8FDE\u5230\u81C0\u4E0B\u3002</p><p>\u7075\u7389\u626C\u81C2\u75BE\u626F\uFF0C\u76D8\u66F2\u7684\u80A0\u9053\u4ECE\u809B\u95E8\u4E2D\u4E00\u6D8C\u800C\u51FA\u3002\u6BB5\u79C0\u5BB9\u5589\u5934\u4E00\u9707\uFF0C\u80A0\u3001\u80C3\u3001\u98DF\u9053\uFF0C\u6574\u4E2A\u6D88\u5316\u5668\u5B98\u4E00\u53E4\u8111\u4ECE\u6392\u6CC4\u5B54\u4E2D\u6389\u843D\u51FA\u6765\u3002</p><p>\u5973\u5B50\u8D64\u88F8\u88F8\u4F0F\u5728\u5730\u4E0A\uFF0C\u96EA\u767D\u7684\u53CC\u817F\u95F4\u6254\u7740\u4E00\u56E2\u6E7F\u6F09\u6F09\u7684\u810F\u5668\u3002\u6BB5\u79C0\u5BB9\u6323\u624E\u6E10\u6E10\u65E0\u529B\uFF0C\u6700\u540E\u53EA\u5269\u4E0B\u9690\u7EA6\u7684\u62BD\u6410\u3002\u604D\u60DA\u4E2D\uFF0C\u4E00\u6839\u5C16\u9510\u800C\u51B0\u51B7\u7684\u7269\u4F53\u523A\u5165\u79D8\u5904\uFF0C\u7A7F\u8FC7\u7A7A\u6D1E\u6D1E\u7684\u8154\u4F53\uFF0C\u4ECE\u5589\u5934\u4F38\u51FA\u3002\u5979\u5DF2\u7ECF\u4E0D\u77E5\u9053\u75BC\u75DB\uFF0C\u53EA\u89C9\u5F97\u521D\u79CB\u7684\u5BD2\u610F\u8D8A\u6765\u8D8A\u6D53\u3002</p><p>\u7075\u7389\u4E3E\u7740\u8463\u8C79\u5A01\u7684\u94C1\u67AA\uFF0C\u5C06\u5782\u6B7B\u7684\u5973\u5B50\u67B6\u5728\u7BDD\u706B\u4E0A\u3002\u4E00\u62E7\u94C1\u67AA\uFF0C\u5973\u4F53\u8F7B\u76C8\u5730\u8F6C\u4E86\u4E00\u5468\uFF0C\u624B\u811A\u8212\u5C55\uFF0C\u72B9\u5982\u751F\u65F6\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u53EF\u6709\u4E2D\u610F\u7684\uFF1F\u300D</p><p>\u5510\u989C\u4F4E\u58F0\u9053\uFF1A\u300C\u662F\u4E0D\u662F\u53EA\u9700\u4E00\u65E5\uFF0C\u660E\u5929\u4FBF\u53EF\u653E\u8FC7\u6211\u4EEC\u6BCD\u5B50\uFF1F\u300D</p><p>\u300C\u53EA\u8981\u592B\u4EBA\u542C\u4ECE\u5429\u5490\uFF0C\u8BA4\u771F\u4F8D\u5949\uFF0C\u4E00\u65E5\u4E4B\u540E\uFF0C\u672C\u5BAB\u7EDD\u4E0D\u76F8\u5F3A\uFF0C\u660E\u65E5\u508D\u665A\u4EE4\u516C\u5B50\u4FBF\u53EF\u56DE\u5BB6\u3002\u300D</p><p>\u5510\u989C\u601D\u7D22\u7247\u523B\uFF0C\u4E00\u54AC\u94F6\u7259\uFF0C\u629B\u5F00\u7F9E\u803B\u6050\u60E7\uFF0C\u8DEA\u5728\u573A\u4E2D\uFF0C\u65E0\u8A00\u5730\u4E3E\u8D77\u5706\u81C0\u3002</p><p>\u300C\u8FD9\u53EF\u4E0D\u884C\u2026\u2026\u300D\u6155\u5BB9\u9F99\u60A0\u7136\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u8981\u4E00\u4E2A\u4E2A\u6C42\u5927\u4F19\u64CD\u4F60\u3002\u300D</p><p>\u5510\u989C\u522B\u65E0\u9009\u62E9\uFF0C\u53EA\u80FD\u8DEA\u5728\u6155\u5BB9\u9F99\u9762\u524D\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6C42\u4F60\u2026\u2026\u64CD\u6211\u3002\u300D</p><p>\u8FD9\u8D31\u4EBA\u5012\u8FD8\u61C2\u4E8B\uFF0C\u77E5\u9053\u5148\u8BF7\u81EA\u5DF1\u3002\u6155\u5BB9\u9F99\u51B7\u51B7\u9053\uFF1A\u300C\u4EC0\u4E48\u4F60\u7684\u6211\u7684\uFF0C\u5A4A\u5B50\u6709\u8FD9\u4E48\u8BF4\u8BDD\u7684\u5417\uFF1F\u300D</p><p>\u5510\u989C\u8138\u8272\u4E00\u767D\uFF0C\u534A\u664C\uFF0C\u5979\u5B66\u7740\u5993\u5973\u7684\u53E3\u543B\u9053\uFF1A\u300C\u6C42\u5927\u7237\u64CD\u2026\u2026\u64CD\u59BE\u8EAB\u2026\u2026\u300D</p><p>\u5510\u989C\u8EAB\u4E3A\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\uFF0C\u4E0D\u4EC5\u8C8C\u7F8E\u5982\u82B1\uFF0C\u800C\u4E14\u806A\u9896\u679C\u65AD\uFF0C\u662F\u6B66\u6797\u4E2D\u6709\u6570\u7684\u540D\u5A9B\uFF0C\u6B64\u523B\u8BF4\u51FA\u8FD9\u79CD\u8BDD\uFF0C\u4F17\u4EBA\u4E0D\u7531\u8F70\u7136\u5927\u7B11\u3002\u5F53\u4E0B\u6709\u4EBA\u53EB\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u662F\u4E0D\u662F\u5F53\u8FC7\u5A4A\u5B50\uFF1F\u300D</p><p>\u5510\u989C\u5F3A\u5FCD\u7F9E\u8FB1\uFF0C\u5782\u7740\u5934\u9ED8\u4E0D\u4F5C\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u4EC0\u4E48\u5927\u7237\uFF1F\u54B1\u4EEC\u64CD\u4F60\u53C8\u4E0D\u7ED9\u94B1\uFF0C\u8FD9\u4E00\u65E5\u4E4B\u4E2D\uFF0C\u4F60\u5C31\u662F\u6211\u6559\u7684\u6DEB\u5974\u3002\u300D</p><p>\u5510\u989C\u538B\u4F4F\u6CEA\u6C34\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u6C42\u4E3B\u5B50\u64CD\u6DEB\u5974\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u8212\u817F\uFF0C\u653E\u5728\u5C11\u5987\u80A9\u5934\uFF0C\u61D2\u6D0B\u6D0B\u8BF4\u9053\uFF1A\u300C\u5341\u51E0\u4E2A\u4E3B\u5B50\u7684\u9E21\u5DF4\u90FD\u5C1D\u8FC7\u4E86\uFF0C\u8FD8\u88C5\u4EC0\u4E48\u6DD1\u5973\u3002\u722C\u8FC7\u6765\u5427\u3002\u300D</p><p>\u5510\u989C\u632A\u52A8\u53CC\u8170\uFF0C\u72D7\u4E00\u822C\u722C\u5230\u6155\u5BB9\u9F99\u80EF\u95F4\u3002</p><p>\u8089\u68D2\u521A\u521A\u5165\u624B\uFF0C\u5510\u989C\u5FC3\u5934\u987F\u65F6\u4E00\u98A4\u3002\u90A3\u6839\u8089\u68D2\u6E10\u6E10\u52C3\u8D77\uFF0C\u5148\u4ECE\u8863\u95F4\u4F38\u51FA\u4E00\u4E2A\u513F\u62F3\u5927\u5C0F\u7684\u9F9F\u5934\uFF0C\u7136\u540E\u662F\u904D\u5E03\u9897\u7C92\u7684\u68D2\u8EAB\u3002\u5F85\u770B\u5230\u90A3\u4E2A\u6EE1\u662F\u5012\u523A\u7684\u8089\u7624\uFF0C\u5C11\u5987\u7684\u624B\u638C\u4E0D\u7531\u5FAE\u5FAE\u53D1\u6296\u3002\u5982\u6B64\u72F0\u72DE\u5DE8\u7269\uFF0C\u53EA\u4F1A\u5728\u6700\u53EF\u6015\u7684\u7684\u5669\u68A6\u91CC\u51FA\u73B0\u3002</p><p>\u5510\u989C\u770B\u5F97\u80F8\u53E3\u53D1\u95F7\uFF0C\u4F46\u8FD8\u662F\u5F20\u53E3\u5C06\u9F9F\u5934\u541E\u5230\u5634\u5185\u3002\u4EC5\u9F9F\u5934\u5C31\u585E\u6EE1\u4E86\u6574\u4E2A\u53E3\u8154\uFF0C\u5C11\u5987\u62FC\u547D\u4F38\u76F4\u5589\u5499\uFF0C\u4E5F\u6CD5\u89E6\u5230\u8089\u7624\uFF0C\u53EA\u80FD\u7528\u7EA2\u5507\u88F9\u4F4F\u68D2\u8EAB\uFF0C\u52C9\u5F3A\u8214\u5F04\u3002</p><p>\u6155\u5BB9\u9F99\u4ECD\u62B1\u7740\u8427\u4F5B\u5974\uFF0C\u7B11\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u7684\u5634\u5DF4\u8DDF\u5A18\u7684\u5DEE\u4E0D\u591A\uFF0C\u53EF\u6CA1\u6709\u4F60\u5356\u529B\u5462\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u4E00\u7EA2\uFF0C\u5468\u56F4\u5750\u6EE1\u65C1\u4EBA\uFF0C\u5979\u7F9E\u4E8E\u542F\u9F7F\uFF0C\u67D4\u9888\u4E00\u4FA7\uFF0C\u5A74\u513F\u822C\u628A\u5934\u57CB\u5728\u6155\u5BB9\u9F99\u6000\u4E2D\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u628A\u8427\u4F5B\u5974\u9012\u5230\u7D2B\u73AB\u624B\u91CC\uFF0C\u7136\u540E\u6309\u4F4F\u5510\u989C\u7684\u79C0\u53D1\uFF0C\u72E0\u72E0\u4E00\u538B\u3002</p><p>\u9F9F\u5934\u786C\u751F\u751F\u6324\u5165\u54BD\u5589\uFF0C\u5510\u989C\u545B\u5F97\u773C\u6CEA\u90FD\u51FA\u6765\u4E86\u3002\u5979\u54B3\u55FD\u7740\u5410\u51FA\u8089\u68D2\uFF0C\u4E0D\u4F4F\u5598\u6C14\u3002</p><p>\u300C\u767E\u6218\u5929\u9F99\u5E73\u65F6\u662F\u600E\u4E48\u64CD\u4F60\u7684\uFF1F\u300D\uFF3B\uFF37\uFF37\uFF37\u3002\uFF3Aei8\u3002\uFF23\uFF2F\uFF2D\u8D3C\u5427\u7535\u5B50\u4E66\uFF3D</p><p>\u5510\u989C\u63A9\u7740\u5589\u5499\u54B3\u58F0\u6E10\u6B47\uFF0C\u5979\u542B\u7740\u6CEA\u82B1\uFF0C\u8F6C\u8FC7\u8EAB\u53BB\uFF0C\u6162\u6162\u62AC\u8D77\u4E0B\u4F53\u3002</p><p>\u300C\u5594\uFF0C\u8D24\u4F09\u4FEA\u539F\u6765\u559C\u6B22\u72D7\u4EA4\u5F0F\u3002\u300D</p><p>\u5176\u5B9E\u9F99\u6218\u91CE\u6700\u559C\u6B22\u4ECE\u6B63\u9762\u4E0E\u5979\u4EA4\u5408\uFF0C\u5510\u989C\u6446\u6210\u8FD9\u4E2A\u59FF\u52BF\uFF0C\u53EA\u662F\u4E0D\u60F3\u770B\u8FD9\u4E9B\u79BD\u517D\u620F\u8C11\u7684\u8868\u60C5\uFF0C\u66F4\u4E0D\u613F\u8BA9\u4ED6\u4EEC\u770B\u5230\u81EA\u5DF1\u8138\u4E0A\u7684\u803B\u8FB1\u3002</p><p>\u8089\u7A74\u4E00\u7D27\uFF0C\u9F9F\u5934\u6324\u5F00\u5AE9\u8089\uFF0C\u91CD\u91CD\u6345\u5165\u4F53\u5185\u3002\u4ECD\u7136\u5E72\u6DA9\u7684\u79D8\u5904\u4E00\u9635\u5267\u75DB\u3002\u5510\u989C\u628A\u8138\u57CB\u5728\u8349\u4E1B\u4E2D\uFF0C\u773C\u6C34\u4E00\u6EF4\u6EF4\u843D\u5728\u5E72\u71E5\u7684\u6CE5\u571F\u3002</p><p>\u4EC7\u767E\u9CCC\u602A\u53EB\u9053\uFF1A\u300C\u6D6A\u5A4A\u5B50\uFF0C\u6211\u4EEC\u6155\u5BB9\u5BAB\u4E3B\u7684\u9E21\u5DF4\u600E\u4E48\u6837\uFF1F\u6BD4\u4F60\u6B7B\u9B3C\u7537\u4EBA\u5F3A\u5427\u3002\u300D</p><p>\u65C1\u8FB9\u6709\u4EBA\u5E94\u9053\uFF1A\u300C\u80FD\u8BA9\u5BAB\u4E3B\u64CD\u4F60\uFF0C\u90A3\u662F\u4F60\u5C44\u4E0A\u7684\u798F\u6C14\uFF0C\u6709\u4F60\u4E50\u7684\u5462\uFF0C\u597D\u597D\u4EAB\u53D7\u5427\u3002\u300D</p><p>\u4EC7\u767E\u718A\u66F4\u662F\u722C\u8D77\u6765\u4ECE\u5C38\u5806\u4E2D\u6252\u51FA\u9F99\u6218\u91CE\u5C38\u4F53\uFF0C\u53C9\u624B\u53C9\u811A\u6254\u5230\u5510\u989C\u9762\u524D\u3002</p><p>\u770B\u5230\u4E08\u592B\u65E0\u5934\u7684\u5C38\u8EAB\uFF0C\u5510\u989C\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u4EC7\u767E\u718A\u6495\u5F00\u5C38\u4F53\u7684\u88E4\u88C6\uFF0C\u62E7\u7740\u5934\u53D1\u628A\u5C11\u5987\u6309\u5728\u5C38\u4F53\u80EF\u4E0B\uFF0C\u53EB\u9053\uFF1A\u300C\u59D3\u9F99\u7684\u9E21\u5DF4\u4F60\u53EF\u6CA1\u5C11\u4EB2\u5427\uFF0C\u597D\u597D\u8214\uFF0C\u8BA9\u8001\u5B50\u4EEC\u770B\u770B\u4F60\u4EEC\u600E\u4E48\u800D\u4E50\u3002\u300D</p><p>\u5510\u989C\u6CE3\u4E0D\u6210\u58F0\uFF0C\u534A\u664C\uFF0C\u5979\u5F20\u5F00\u6731\u5507\uFF0C\u5C06\u8F6F\u7EF5\u7EF5\u7684\u9633\u5177\u542B\u5230\u53E3\u5185\u3002\u4E08\u592B\u8EAB\u4E0A\u8FD8\u6709\u90A3\u80A1\u719F\u6089\u7684\u5473\u9053\uFF0C\u4F46\u4E00\u5411\u70C8\u706B\u822C\u70BD\u70ED\u7684\u8EAB\u4F53\u5374\u51B7\u5F97\u50CF\u4E00\u56E2\u51B0\u5757\u3002</p><p>\u91D1\u5F00\u7532\u5C06\u65B9\u7389\u73B2\u6254\u4E86\u8FC7\u6765\uFF0C\u6155\u5BB9\u9F99\u53C8\u53EB\u6765\u767D\u6C0F\u59D0\u59B9\uFF0C\u661F\u6708\u6E56\u4F17\u4EBA\u4E00\u8FB9\u996E\u9152\u5403\u8089\uFF0C\u4E00\u8FB9\u8F6E\u6D41\u5978\u6DEB\u516B\u6781\u95E8\u4F17\u5973\u3002</p><p>\u706B\u67F1\u8D8A\u70E7\u8D8A\u65FA\uFF0C\u8349\u4E1B\u4E2D\u7EB5\u6A2A\u4EA4\u9519\u7684\u5C38\u4F53\u5728\u706B\u5149\u4E2D\u65F6\u9690\u65F6\u73B0\u3002\u4E00\u6392\u6574\u9F50\u7684\u5934\u9885\u4E4B\u95F4\uFF0C\u4E00\u7FA4\u6076\u5F62\u6076\u72B6\u7684\u5927\u6C49\u72C2\u7B11\u6B22\u996E\uFF0C\u62FF\u7740\u70E4\u719F\u7684\u4EBA\u81C2\u4EBA\u817F\u5F00\u6000\u5927\u56BC\u3002\u7BDD\u706B\u65C1\uFF0C\u51E0\u5177\u767D\u5AE9\u7684\u8089\u4F53\u88AB\u4EBA\u7C97\u66B4\u7684\u5978\u6DEB\u7740\u3002\u5176\u4E2D\u4E00\u4E2A\u8FD8\u8DB4\u5728\u4E00\u5177\u65E0\u5934\u7684\u5C38\u4F53\u4E0A\uFF0C\u541E\u5410\u7740\u5C38\u4F53\u7684\u9633\u5177\u3002\u5047\u5982\u771F\u6709\u5730\u72F1\uFF0C\u8FD9\u5C31\u662F\u5730\u72F1\u4E86\u3002</p>',71);function t(i,_){return a}var d=e(r,[["render",t]]);export{d as default};
