import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_90" tabindex="-1"><a class="header-anchor" href="#_90" aria-hidden="true">#</a> 90</h1><p>\u5929\u8272\u7834\u6653\uFF0C\u7CBE\u75B2\u529B\u5C3D\u7684\u5C11\u5987\u8F6F\u7EF5\u7EF5\u5012\u5728\u8349\u5730\u4E0A\u3002\u90A3\u6839\u5931\u53BB\u751F\u547D\u7684\u9633\u5177\u4ECE\u5634\u89D2\u6389\u51FA\uFF0C\u6CBE\u6EE1\u6CEA\u6C34\u548C\u553E\u6DB2\u3002</p><p>\u4E00\u6761\u5927\u6C49\u6293\u7740\u5934\u53D1\u628A\u5510\u989C\u63D0\u4E86\u8D77\u6765\uFF0C\u54C2\u7B11\u9053\uFF1A\u300C\u8FD9\u624D\u4E00\u591C\uFF0C\u9F99\u592B\u4EBA\u5C31\u60F3\u4F11\u606F\u4E86\uFF1F\u300D\u8BF4\u7740\u5C06\u4E00\u6839\u7F30\u7EF3\u5957\u5728\u5C11\u5987\u9888\u4E2D\uFF0C\u5C06\u5979\u62D6\u5230\u8F66\u540E\uFF0C\u6813\u5728\u8F66\u6869\u4E0A\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u56DE\u5230\u8F66\u4E0A\u4F8D\u5949\u8427\u4F5B\u5974\uFF0C\u65B9\u7389\u73B2\u88AB\u9001\u5230\u9762\u524D\u7684\u9A6C\u8F66\uFF0C\u53EA\u5269\u4E0B\u5510\u989C\u4E00\u4EBA\u8D64\u8EAB\u88F8\u4F53\u5B64\u96F6\u96F6\u7AD9\u8F66\u540E\u3002\u5979\u832B\u7136\u770B\u7740\u56DB\u5468\uFF0C\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF01\u6714\u513F\uFF01\u300D</p><p>\u300C\u5A18\uFF01\u300D\u6E05\u4EAE\u7684\u58F0\u97F3\u4ECE\u9762\u524D\u4F20\u51FA\u3002</p><p>\u5510\u989C\u987F\u65F6\u677E\u4E86\u53E3\u6C14\uFF0C\u5728\u5FC3\u91CC\u9ED8\u5FF5\u9053\uFF1A\u300C\u611F\u8C22\u7687\u5929\u83E9\u8428\u2026\u2026\u6714\u513F\u6CA1\u4E8B\u5C31\u597D\u3002\u300D</p><p>\u6B64\u65F6\u5728\u5979\u5FC3\u91CC\uFF0C\u8FD9\u4E00\u591C\u7684\u75DB\u82E6\u548C\u7F9E\u803B\u4E5F\u662F\u503C\u5F97\u7684\u4E86\u3002</p><p>\u8F66\u91CC\u4F20\u6765\u51E0\u58F0\u54CD\u52A8\uFF0C\u5510\u989C\u5FC3\u5934\u7ACB\u523B\u63EA\u7D27\u3002\u63A5\u7740\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u54CD\u8D77\uFF0C\u300C\u5C0F\u5B50\u8FD8\u6709\u51E0\u5206\u529B\u6C14\u3002\u60F3\u89C1\u4F60\u5A18\uFF1F\u90A3\u597D\u3002\u300D</p><p>\u8F66\u5EC9\u4E00\u6380\uFF0C\u513F\u5B50\u53EF\u7231\u7684\u8138\u86CB\u51FA\u73B0\u5728\u773C\u524D\u3002</p><p>\u770B\u5230\u513F\u5B50\u5B89\u7136\u65E0\u6059\uFF0C\u5510\u989C\u5FC3\u5934\u987F\u65F6\u88AB\u6B22\u559C\u6DF9\u6CA1\uFF0C\u5979\u7B11\u7740\u8F7B\u58F0\u53EB\u9053\uFF1A\u300C\u6714\u513F\u3002\u300D</p><p>\u9F99\u6714\u5374\u6CA1\u6709\u5F00\u53E3\uFF0C\u53EA\u662F\u660E\u4EAE\u7684\u5927\u773C\u91CC\u6D41\u9732\u51FA\u4E00\u4E1D\u6000\u7591\u3002\u5510\u989C\u8FD9\u65F6\u624D\u610F\u8BC6\u5230\u81EA\u5DF1\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u7389\u8138\u4E00\u4E0B\u7EA2\u4E86\u3002</p><p>\u534A\u664C\uFF0C\u9F99\u6714\u8F7B\u8F7B\u53EB\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u600E\u4E48\u4E86\u2026\u2026\u300D</p><p>\u5510\u989C\u7528\u624B\u81C2\u63A9\u4F4F\u80F8\u4E73\uFF0C\u6EE1\u8138\u6EDA\u70EB\u5730\u8BF4\uFF1A\u300C\u5A18\u6CA1\u4E8B\u2026\u2026\u6714\u513F\uFF0C\u4F60\u8FDB\u53BB\u5427\u3002\u508D\u665A\u6211\u4EEC\u5C31\u80FD\u56DE\u5BB6\u4E86\u3002\u300D</p><p>\u9F99\u6714\u4F3C\u4E4E\u7A81\u7136\u95F4\u957F\u5927\u4E86\u5341\u5C81\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u56DE\u5230\u8F66\u5185\uFF0C\u8EB2\u5728\u8F66\u53A2\u9ED1\u6697\u7684\u89D2\u843D\u91CC\u3002</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u518D\u653E\u4E0B\u8F66\u5EC9\uFF0C\u53CD\u800C\u5C06\u56DB\u58C1\u7684\u53A2\u7A97\u5168\u90E8\u6253\u5F00\u3002\u8FD9\u65F6\u5510\u989C\u624D\u770B\u5230\u5F92\u513F\u65B9\u7389\u73B2\u76F4\u633A\u633A\u8EBA\u5728\u8F66\u5185\uFF0C\u65C1\u8FB9\u8FD8\u5750\u7740\u4E00\u4E2A\u9053\u4EBA\u3002</p><p>\u9888\u4E2D\u4E00\u7D27\uFF0C\u7F30\u7EF3\u62C9\u5F97\u7B14\u76F4\u3002\u5510\u989C\u4E0D\u7531\u81EA\u4E3B\u5730\u8DDF\u7740\u9A6C\u8F66\u8DD1\u4E86\u8D77\u6765\uFF0C\u5979\u52C9\u5F3A\u56DE\u5934\u671D\u4E08\u592B\u7684\u5C38\u4F53\u671B\u53BB\uFF0C\u8BD5\u56FE\u8BB0\u4E0B\u8FD9\u4E2A\u5199\u6EE1\u81EA\u5DF1\u803B\u8FB1\u548C\u75DB\u82E6\u7684\u5730\u65B9\uFF0C\u597D\u6765\u7ED9\u4E08\u592B\u548C\u540C\u95E8\u6536\u5C38\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u4FCF\u8138\u8D34\u5728\u6BCD\u4EB2\u767D\u817B\u7684\u5C0F\u8179\u4E0A\uFF0C\u7591\u60D1\u5730\u8BF4\uFF1A\u300C\u771F\u7684\u52A8\u4E86\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u98DE\u7EA2\uFF0C\u8F7B\u8F7B\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u7D2B\u73AB\u5FC3\u91CC\u53F9\u4E86\u53E3\u6C14\uFF0C\u62FF\u8FC7\u8309\u8389\u82B1\u6CB9\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u6211\u6765\u7ED9\u4F60\u64E6\u8EAB\u5B50\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u7740\u8138\u8BF4\u9053\uFF1A\u300C\u4F60\u4E5F\u6000\u7740\u5B69\u5B50\uFF0C\u4E0D\u8981\u7D2F\u7740\u4E86\u3002\u8FD8\u662F\u7B49\u5979\u4EEC\u4E24\u4E2A\u5427\u3002\u300D</p><p>\u4E0D\u63D0\u5219\u7F62\uFF0C\u4E00\u63D0\u8D77\u767D\u6C0F\u59D0\u59B9\uFF0C\u7D2B\u73AB\u4E0D\u7531\u5FC3\u5934\u706B\u8D77\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u90A3\u4E24\u4E2A\u8D31\u4EBA\uFF01\u6068\u6B7B\u6211\u4E86\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u795E\u8272\u590D\u6742\u5730\u770B\u4E86\u5973\u513F\u4E00\u773C\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u6D82\u8FC7\u8309\u8389\u82B1\u6CB9\u7684\u7389\u4F53\u6563\u53D1\u7740\u83B9\u767D\u7684\u5149\u8F89\uFF0C\u53C8\u9999\u53C8\u8F6F\uFF0C\u8273\u4E3D\u593A\u76EE\u3002\u7D2B\u73AB\u5E2E\u6BCD\u4EB2\u62AB\u4E0A\u8863\u886B\uFF0C\u6276\u5979\u5750\u5728\u7A97\u524D\u89C2\u8D4F\u5927\u8349\u539F\u7684\u666F\u8272\u3002</p><p>\u8349\u539F\u72B9\u5982\u4E0D\u7AED\u7684\u6CB3\u6C34\u4ECE\u7A97\u53E3\u5954\u6D41\u800C\u8FC7\u3002\u8349\u4E1B\u4E2D\uFF0C\u9E1F\u8FDB\u517D\u8D70\uFF0C\u4E00\u6D3E\u751F\u673A\u76CE\u7136\uFF0C\u5404\u79CD\u52A8\u7269\u8E66\u8E66\u8DF3\u8DF3\u5F80\u4E24\u65C1\u9003\u5F00\uFF0C\u9694\u8FDC\u60CA\u5947\u5730\u770B\u7740\u8F66\u961F\u3002\u5FFD\u7136\uFF0C\u9A6C\u8E44\u58F0\u60CA\u8D77\u4E00\u7FA4\u5927\u96C1\uFF0C\u5B83\u4EEC\u5639\u53EB\u7740\u632F\u7FC5\u98DE\u4E0A\u84DD\u5929\uFF0C\u6E10\u6E10\u6D88\u5931\u5728\u767D\u4E91\u6DF1\u5904\u3002</p><p>\u8427\u4F5B\u5974\u7FA1\u6155\u5730\u671B\u7740\u90A3\u7FA4\u53EF\u4EE5\u81EA\u7531\u98DE\u7FD4\u7684\u5927\u96C1\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u5B83\u4EEC\u98DE\u5F97\u591A\u9AD8\u554A\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u65E0\u8A00\u4EE5\u5BF9\uFF0C\u53EA\u80FD\u6276\u7740\u6BCD\u4EB2\u7684\u8170\u80A2\uFF0C\u9759\u9759\u770B\u7740\u5979\u6BEB\u65E0\u7455\u75B5\u7684\u9999\u808C\u7389\u9AA8\uFF0C\u8FD8\u6709\u90A3\u53CC\u5145\u6EE1\u6E34\u671B\u7684\u52A8\u4EBA\u7F8E\u76EE\uFF0C\u5FC3\u91CC\u6697\u6697\u60F3\uFF1A\u300C\u5982\u679C\u5A18\u4E0D\u662F\u957F\u5F97\u8FD9\u4E48\u7F8E\uFF0C\u4F1A\u4E0D\u4F1A\u66F4\u5E78\u798F\u5462\uFF1F\u300D</p><p>\u6BCD\u5973\u4FE9\u6B63\u5728\u6B23\u8D4F\u7F8E\u666F\uFF0C\u8427\u4F5B\u5974\u8138\u4E0A\u7A81\u7136\u4E00\u7EA2\u3002\u5FCD\u4E86\u7247\u523B\u540E\uFF0C\u5979\u5C0F\u58F0\u9053\uFF1A\u300C\u6211\u2026\u2026\u300D\u8FD9\u8BDD\u5B9E\u5728\u96BE\u4EE5\u542F\u9F7F\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u4F1A\u610F\uFF0C\u8FDE\u5FD9\u628A\u6BCD\u4EB2\u6276\u5230\u88AB\u8925\u4E2D\uFF0C\u4FEF\u8EAB\u8EBA\u597D\uFF0C\u7136\u540E\u89E3\u5F00\u5C3F\u5E03\uFF0C\u5265\u5F00\u6ED1\u817B\u7684\u81C0\u8089\uFF0C\u5C06\u6C61\u7269\u7EC6\u7EC6\u63E9\u62B9\u5E72\u51C0\u3002</p><p>\u5C3F\u5E03\u64E6\u5230\u83CA\u809B\u65F6\uFF0C\u8427\u4F5B\u5974\u7389\u4F53\u8F7B\u98A4\uFF0C\u79D8\u5904\u987F\u65F6\u6E7F\u4E86\u3002\u5979\u62C5\u5FC3\u5973\u513F\u770B\u51FA\u7AEF\u502A\uFF0C\u7F9E\u5F97\u8033\u6735\u4E5F\u7EA2\u4E86\u8D77\u6765\uFF0C\u5FC3\u91CC\u5374\u4E0D\u671F\u7136\u60F3\u8D77\u4E86\u9F99\u54E5\u54E5\u7684\u8089\u68D2\u2026\u2026\u4ED6\u4E00\u6574\u5929\u90FD\u6CA1\u6709\u78B0\u81EA\u5DF1\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9A6C\u8F66\u6EDA\u6EDA\u5317\u4E0A\uFF0C\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u88AB\u8D64\u8EAB\u9732\u4F53\u6813\u5728\u6700\u672B\u4E00\u8F86\u8F66\u5C3E\uFF0C\u5F92\u6B65\u8DDF\u7740\u75BE\u9A70\u7684\u9A6C\u8F66\u3002\u4E00\u8FC8\u6B65\uFF0C\u5979\u624D\u77E5\u9053\u6628\u591C\u6240\u53D7\u7684\u5978\u6DEB\u6709\u591A\u4E48\u7C97\u66B4\u3002\u9634\u6237\u80BF\u8D77\uFF0C\u9F13\u9F13\u80C0\u80C0\u78E8\u64E6\u5728\u4E24\u817F\u4E4B\u95F4\u3002\u540E\u5EAD\u4E5F\u540C\u6837\u7A81\u8D77\uFF0C\u809B\u7AA6\u7FFB\u51FA\uFF0C\u5939\u5728\u81C0\u8089\u4E2D\u3002\u6BCF\u8FC8\u4E00\u6B65\uFF0C\u4E0B\u4F53\u90FD\u706B\u8FA3\u8FA3\u7684\u75BC\u75DB\u3002</p><p>\u9664\u4E86\u51E0\u540D\u4F24\u91CD\u65E0\u6CD5\u4E58\u9A6C\u7684\u4EE5\u5916\uFF0C\u5176\u4F59\u5341\u51E0\u540D\u5E2E\u4F17\u8F6E\u756A\u7EB5\u9A6C\u56F4\u7740\u5510\u989C\u8C03\u7B11\u53D6\u4E50\u3002</p><p>\u4E0D\u65F6\u671D\u5706\u81C0\u62BD\u4E0A\u4E00\u97AD\uFF0C\u6216\u8005\u62FF\u5175\u5203\u6311\u5F04\u5979\u7684\u4E73\u623F\u3001\u4E0B\u9634\u3002</p><p>\u5510\u989C\u4E00\u8FB9\u5954\u8DD1\uFF0C\u4E00\u8FB9\u5FCD\u53D7\u4F17\u4EBA\u8BF8\u822C\u73A9\u5F04\uFF0C\u4E0D\u591A\u65F6\u4FBF\u9999\u6C57\u6DCB\u6F13\uFF0C\u4E24\u817F\u9178\u75DB\u3002\u79C0\u53D1\u88AB\u6C57\u6C34\u6253\u6E7F\uFF0C\u6CBE\u5728\u9888\u4E2D\uFF0C\u5C11\u5987\u6258\u7740\u8DF3\u52A8\u7684\u7389\u4E73\uFF0C\u4E0D\u65F6\u671D\u8F66\u5185\u770B\u53BB\u3002\u53EA\u8981\u4E0D\u8BA9\u513F\u5B50\u770B\u5230\uFF0C\u518D\u591A\u7684\u7F9E\u8FB1\u5979\u90FD\u80FD\u627F\u53D7\u3002</p><p>\u9F99\u6714\u50CF\u77E5\u9053\u5979\u7684\u5FC3\u4E8B\uFF0C\u4E00\u76F4\u8EB2\u5728\u89D2\u843D\u91CC\uFF0C\u6CA1\u6709\u56DE\u5934\u3002</p><p>\u7075\u7389\u62FF\u7740\u65B9\u7389\u73B2\u7684\u53F3\u4E73\uFF0C\u4E00\u8FB9\u7EB9\u523A\uFF0C\u4E00\u8FB9\u8BB2\u89E3\u3002\u6155\u5BB9\u9F99\u4F9D\u7167\u6307\u70B9\uFF0C\u7528\u5C11\u5973\u7684\u5DE6\u4E73\u7EC3\u624B\u3002\u65B9\u7389\u73B2\u6D51\u8EAB\u51B7\u6C57\u4E5F\u4E0D\u6562\u52A8\u4F5C\uFF0C\u4EFB\u4ED6\u5C06\u81EA\u5DF1\u96EA\u767D\u7684\u4E73\u7403\u523A\u6210\u4E00\u56E2\u9C9C\u7EA2\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u62AC\u8D77\u5934\uFF0C\u5FAE\u7B11\u7740\u62FF\u6BDB\u5DFE\u64E6\u53BB\u9C9C\u8840\u3002\u8FD9\u8FB9\u7075\u7389\u65E9\u5DF2\u523A\u5B8C\uFF0C\u6B63\u7528\u6731\u7802\u3001\u77F3\u9752\u7B49\u989C\u6599\u52FE\u753B\u7EB9\u8DEF\u3002\u7B49\u4ED6\u505C\u4E0B\u624B\uFF0C\u9999\u8F6F\u7684\u53F3\u4E73\u663E\u51FA\u4E00\u6735\u6829\u6829\u5982\u751F\u7684\u7261\u4E39\uFF0C\u7EA2\u82B1\u7EFF\u53F6\uFF0C\u5A07\u8273\u6B32\u6EF4\u3002\u518D\u770B\u6155\u5BB9\u9F99\u6240\u523A\uFF0C\u5374\u662F\u4E00\u6761\u98DE\u9F99\u3002</p><p>\u7075\u7389\u7B11\u9053\uFF1A\u300C\u5BAB\u4E3B\u7528\u9488\u8FD8\u6B20\u719F\u7EC3\uFF0C\u529B\u9053\u8F7B\u91CD\u4E0D\u4E00\uFF0C\u8FD9\u9F99\u722A\u6709\u4E9B\u8D70\u578B\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u7B49\u7075\u7389\u5C06\u4E0D\u8DB3\u4E00\u4E00\u6307\u51FA\uFF0C\u4ED6\u638F\u51FA\u7247\u7389\uFF0C\u4E00\u5200\u5207\u4E0B\u3002\u6D51\u5706\u7684\u5DE6\u4E73\u9F50\u9F50\u5206\u6210\u4E24\u534A\uFF0C\u6574\u9F50\u7684\u5200\u53E3\u4ECE\u4E73\u5934\u76F4\u5230\u4E73\u6839\uFF0C\u5C06\u98DE\u9F99\u65A9\u6210\u4E24\u622A\u3002\u5C11\u5973\u51C4\u60E8\u7684\u53EB\u58F0\u4E2D\uFF0C\u6155\u5BB9\u9F99\u624B\u8D77\u5200\u843D\uFF0C\u628A\u81EA\u5DF1\u7684\u4F5C\u54C1\u780D\u5F97\u7C89\u788E\u3002</p><p>\u7075\u7389\u6293\u4F4F\u53F3\u4E73\u7565\u4E00\u7528\u529B\uFF0C\u4E73\u7403\u5E94\u624B\u7206\u88C2\u3002\u63A5\u7740\u5DE6\u624B\u64AE\u6307\u6210\u5200\uFF0C\u5288\u5728\u65B9\u7389\u73B2\u80EF\u95F4\u3002\u9634\u961C\u50CF\u88AB\u5200\u780D\u822C\u7EFD\u88C2\uFF0C\u8FDE\u803B\u9AA8\u4E5F\u4E00\u5E76\u7C89\u88C2\u3002</p><p>\u6FD2\u6B7B\u7684\u5C11\u5973\u50CF\u4E00\u56E2\u5783\u573E\u822C\u88AB\u968F\u624B\u6254\u5230\u8F66\u5916\uFF0C\u5728\u8349\u4E1B\u91CC\u7FFB\u6EDA\u54C0\u53F7\u3002\u5510\u989C\u811A\u4E0B\u4E00\u4E2A\u8E09\u8DC4\uFF0C\u6454\u5012\u5728\u5730\u3002\u5979\u6B66\u529F\u672A\u5931\uFF0C\u88AB\u9A6C\u8F66\u62D6\u4E86\u4E24\u6B65\uFF0C\u4FBF\u6323\u624E\u7740\u722C\u4E86\u8D77\u6765\u3002</p><p>\u60E8\u53EB\u58F0\u6E10\u6E10\u8FDC\u53BB\uFF0C\u5510\u989C\u5FC3\u5982\u5200\u5272\uFF0C\u9762\u5BF9\u8FD9\u5E2E\u89C6\u4EBA\u5982\u8C56\u72AC\u7684\u6076\u6C49\uFF0C\u5979\u53EA\u6709\u5782\u6CEA\u4E0D\u5DF2\u3002</p><p>\u6B63\u6D41\u6CEA\u95F4\uFF0C\u5FFD\u7136\u80A1\u95F4\u4E00\u75DB\uFF0C\u4E00\u4E2A\u575A\u786C\u7684\u4E1C\u897F\u91CD\u91CD\u6253\u5728\u79D8\u5904\u3002\u5510\u989C\u82B1\u5BB9\u5931\u8272\uFF0C\u8FDE\u5FD9\u7528\u624B\u63A9\u4F4F\u4E0B\u4F53\u3002</p><p>\u8EAB\u540E\u4F20\u6765\u4E00\u9635\u5927\u7B11\uFF0C\u4EC7\u767E\u718A\u9053\uFF1A\u300C\u6CA1\u6253\u8FDB\u53BB\u561B\u3002\u300D</p><p>\u4E5E\u4F0F\u7A77\u9686\u53C8\u6478\u51FA\u4E00\u9897\u94C1\u83B2\u5B50\uFF0C\u53EB\u9053\uFF1A\u300C\u624B\u62FF\u5F00\uFF01\u300D</p><p>\u8FD9\u4E9B\u4EBA\u7ADF\u62FF\u81EA\u5DF1\u7684\u8EAB\u4F53\u5F53\u6807\u9776\u53D6\u4E50\uFF0C\u5510\u989C\u53C8\u7F9E\u53C8\u6068\u2014\u2014\u4F46\u5979\u8FD8\u662F\u79FB\u5F00\u4E86\u624B\u638C\u3002</p><p>\u94C1\u83B2\u5B50\u5212\u51FA\u4E00\u6761\u5F27\u7EBF\uFF0C\u81EA\u4E0B\u800C\u4E0A\u6253\u5728\u80BF\u80C0\u7684\u82B1\u74E3\u95F4\u3002\u8FD9\u4E0B\u4E5E\u4F0F\u7A77\u9686\u7528\u4E0A\u4E86\u4E03\u5206\u52B2\u529B\uFF0C\u867D\u7136\u6CA1\u6709\u6B63\u4E2D\u8089\u7A74\uFF0C\u4F46\u94C1\u83B2\u5B50\u5728\u5AE9\u8089\u95F4\u4E00\u6ED1\uFF0C\u8FD8\u662F\u94BB\u5165\u5C11\u5987\u4F53\u5185\u3002</p><p>\u5510\u989C\u8EAB\u5B50\u4E00\u6643\uFF0C\u9669\u4E9B\u8DEA\u5728\u5730\u4E0A\u3002\u5979\u6015\u60CA\u52A8\u513F\u5B50\uFF0C\u5F3A\u5FCD\u7740\u75DB\u695A\uFF0C\u4E00\u58F0\u4E0D\u542D\u3002\u94C1\u83B2\u5B50\u65CB\u8F6C\u7740\u649E\u4F4F\u5BAB\u9888\uFF0C\u7136\u540E\u987A\u7740\u6E7F\u6DA6\u7684\u82B1\u5F84\u6E10\u6E10\u4E0B\u6C89\u3002\u521A\u6E9C\u4E0B\u4E00\u534A\uFF0C\u53C8\u4E00\u679A\u94C1\u83B2\u5B50\u500F\u5FFD\u6CA1\u5165\u8089\u7A74\u3002\u4E24\u53EA\u94C1\u83B2\u5B50\u76F8\u51FB\uFF0C\u5728\u4F53\u5185\u53D1\u51FA\u4E00\u58F0\u95F7\u54CD\u3002</p><p>\u5230\u7B2C\u4E94\u679A\u94C1\u83B2\u5B50\u8FDB\u5165\uFF0C\u4E00\u8FDE\u4E32\u7684\u94C1\u4E38\u4E92\u76F8\u649E\u51FB\u4E4B\u540E\uFF0C\u6709\u4E00\u679A\u4E0D\u77E5\u4F55\u65F6\u6253\u5165\u7684\u94C1\u83B2\u5B50\u6ED1\u51FA\u8089\u7A74\uFF0C\u5E26\u7740\u9ECF\u6DB2\u6E7F\u6DCB\u6DCB\u6389\u5728\u957F\u8349\u4E2D\u3002\u63A5\u7740\u53C8\u6389\u51FA\u4E24\u679A\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u7EB5\u9A6C\u4E0A\u524D\uFF0C\u626C\u8D77\u9A6C\u97AD\u6253\u5728\u5510\u989C\u81C0\u95F4\uFF0C\u300C\u4ED6\u5988\u7684\uFF0C\u5939\u7D27\u4E86\uFF01\u300D</p><p>\u5510\u989C\u7F9E\u6012\u4EA4\u52A0\uFF0C\u5FC3\u5E95\u4E00\u80A1\u6068\u610F\u5347\u8D77\uFF0C\u5C31\u60F3\u4E0E\u8FD9\u4E9B\u65E0\u803B\u4E4B\u5F92\u62FC\u547D\u3002\u53EF\u62AC\u773C\u770B\u5230\u9F99\u6714\u5C0F\u5C0F\u7684\u8EAB\u5F71\uFF0C\u90A3\u80A1\u6C14\u987F\u65F6\u6563\u4E86\u3002\u5979\u4F7F\u529B\u6536\u7D27\u8089\u7A74\uFF0C\u4F46\u94C1\u83B2\u5B50\u8FD8\u662F\u65E0\u6CD5\u963B\u6321\u5730\u6ED1\u843D\u3002\u5510\u989C\u7709\u5934\u62E7\u7D27\uFF0C\u7528\u624B\u6309\u4F4F\u79D8\u5904\u3002</p><p>\u300C\u556A\u300D\uFF0C\u53C8\u662F\u4E00\u97AD\uFF0C\u300C\u8001\u5B50\u8BF4\u8FC7\uFF0C\u624B\u62FF\u5F00\uFF01\u300D</p><p>\u5510\u989C\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u628A\u624B\u6307\u63A2\u5165\u8089\u7A74\uFF0C\u5C06\u94C1\u83B2\u5B50\u671D\u91CC\u63A8\u4E86\u63A8\u3002\u5C31\u8FD9\u6837\uFF0C\u5979\u4E00\u8FB9\u5954\u8DD1\uFF0C\u4E00\u8FB9\u6536\u7D27\u5AE9\u8089\uFF0C\u8FD8\u4E0D\u65F6\u7528\u624B\u628A\u4ED6\u4EEC\u6295\u6765\u7684\u5404\u79CD\u5F02\u7269\u63A8\u8FDB\u8089\u7A74\u6DF1\u5904\u3002</p><p>\u6155\u5BB9\u9F99\u77A5\u4E86\u51C4\u60F6\u7684\u5C11\u5987\u4E00\u773C\uFF0C\u51B7\u51B7\u4E00\u7B11\u3002\u80C6\u6562\u72AF\u6211\u661F\u6708\u6E56\u795E\u5A01\uFF0C\u5C31\u8BE5\u77E5\u9053\u4F1A\u4ED8\u51FA\u4EC0\u4E48\u6837\u7684\u4EE3\u4EF7\u3002\u987A\u6211\u8005\u660C\uFF0C\u9006\u6211\u8005\u2026\u2026\u4EA1\u4E5F\u4F1A\u8BA9\u4F60\u4EA1\u5F97\u523B\u9AA8\u94ED\u5FC3\u3002</p>',55);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
