import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_91" tabindex="-1"><a class="header-anchor" href="#_91" aria-hidden="true">#</a> 91</h1><p>\u300C\u8FD9\u4E00\u5E26\u6CA1\u6709\u5B9A\u5C45\u7684\u519C\u6237\uFF0C\u552F\u4E00\u7684\u5927\u57CE\u6E14\u9633\u53C8\u662F\u519B\u9547\uFF0C\u5C45\u6C11\u7A00\u5C11\uFF0C\u56E0\u6B64\u6CA1\u6709\u5E2E\u4F1A\u3002\u300D\u7075\u7389\u5468\u6E38\u5929\u4E0B\uFF0C\u5BF9\u5404\u5904\u98CE\u571F\u591A\u6709\u4E86\u89E3\u3002</p><p>\u91D1\u5F00\u7532\u60F3\u4E86\u60F3\uFF0C\u9053\uFF1A\u300C\u6709\u6CA1\u6709\u9A6C\u8D3C\uFF1F\u300D</p><p>\u7075\u7389\u6C89\u601D\u9053\uFF1A\u300C\u5F53\u5E74\u5927\u71D5\u8986\u6CA1\u540E\uFF0C\u5468\u56FD\u5C61\u6B21\u5174\u5175\u5317\u4F10\uFF0C\u65A9\u8349\u9664\u6839\uFF0C\u5C60\u63A0\u6781\u751A\u3002</p><p>\u73B0\u5728\u6BCF\u9694\u4E00\u5E74\u59DA\u5174\u8FD8\u8981\u9063\u519B\u81F3\u9F99\u57CE\u5C60\u6740\uFF0C\u67D4\u7136\u738B\u5EAD\u53C8\u5728\u5317\u6F20\uFF0C\u65E0\u6687\u4E1C\u987E\uFF0C\u56E0\u6B64\u8FD9\u91CC\u5982\u4ECA\u662F\u65E0\u4E3B\u4E4B\u5730\uFF0C\u7EB5\u6709\u9A6C\u8D3C\u4E5F\u4E0D\u6210\u6C14\u5019\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u539F\u672C\u60F3\u5728\u6B64\u6536\u62E2\u4E00\u652F\u52BF\u529B\uFF0C\u8FD9\u65F6\u624D\u77E5\u9053\u66FE\u7ECF\u8F70\u8F70\u70C8\u70C8\u94C1\u8E44\u8E0F\u7834\u4E2D\u539F\u7684\u9C9C\u5351\u6155\u5BB9\uFF0C\u5728\u5176\u6545\u5730\u5DF2\u662F\u70DF\u9500\u4E91\u6563\u3002</p><p>\u300C\u597D\u4E2A\u59DA\u5174\u3002\u300D\u6155\u5BB9\u9F99\u8F7B\u8F7B\u9F13\u4E86\u9F13\u638C\uFF0C\u300C\u624B\u6BB5\u591F\u72E0\uFF0C\u6211\u6155\u5BB9\u9F99\u53D7\u6559\u4E86\u3002\u300D</p><p>\u91D1\u5F00\u7532\u6C89\u541F\u9053\uFF1A\u300C\u65E2\u7136\u662F\u9694\u5E74\u4E00\u51FA\u5175\uFF0C\u4E0A\u6B21\u51FA\u5175\u662F\u4EC0\u4E48\u65F6\u5019\uFF1F\u300D</p><p>\u300C\u5C31\u662F\u4ECA\u5E74\u6625\u5B63\u3002\u300D</p><p>\u7075\u7389\u8BF4\u5B8C\uFF0C\u4E09\u4EBA\u76EE\u5149\u76F8\u9047\uFF0C\u773C\u775B\u90FD\u4EAE\u4E86\u8D77\u6765\u3002</p><p>\u300C\u4EC7\u767E\u718A\u3001\u4EC7\u767E\u9CCC\u3002\u300D\u91D1\u5F00\u7532\u626C\u58F0\u5524\u9053\u3002</p><p>\u8840\u65A9\u53CC\u715E\u95FB\u58F0\u8D76\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u4F60\u4EEC\u5144\u5F1F\u7ACB\u523B\u56DE\u5230\u96C1\u95E8\uFF0C\u4F20\u4EE4\u8D6B\u8FDE\u96C4\uFF1A\u5373\u523B\u8D77\uFF0C\u5C06\u8D2D\u6765\u7684\u9A6C\u5339\u5C3D\u6570\u9001\u5230\u9F99\u57CE\u3002\u300D</p><p>\u8BDD\u97F3\u521A\u843D\uFF0C\u7075\u7389\u5DF2\u5199\u597D\u4FE1\u67EC\u9012\u7ED9\u5BAB\u4E3B\u3002</p><p>\u6155\u5BB9\u9F99\u7565\u4E00\u8FC7\u76EE\uFF0C\u9012\u7ED9\u91D1\u5F00\u7532\u3002</p><p>\u4FE1\u4E0A\u5BE5\u5BE5\u6570\u8BED\uFF0C\u9664\u5168\u6B7C\u516B\u6781\u95E8\u4E4B\u5916\uFF0C\u4FBF\u662F\u8BA9\u6C90\u58F0\u4F20\u901A\u77E5\u6559\u5185\u9009\u62D4\u7684\u7CBE\u9510\uFF0C\u5206\u6279\u5317\u4E0A\u9F99\u57CE\uFF0C\u64CD\u7EC3\u6218\u9635\u3002</p><p>\u91D1\u5F00\u7532\u9053\uFF1A\u300C\u518D\u52A0\u4E00\u53E5\uFF1A\u4ECE\u7EC8\u5357\u76F4\u5230\u9F99\u57CE\uFF0C\u6BCF\u4E00\u57CE\u9547\u90FD\u9700\u6709\u4FE1\u9E3D\u3002\u300D</p><p>\u8FC7\u4E86\u4E0A\u8C37\u4E4B\u540E\uFF0C\u5C31\u518D\u6CA1\u6709\u661F\u6708\u6E56\u5C5E\u4E0B\u7684\u5E2E\u4F1A\u3002\u56E0\u6B64\u4ED6\u4EEC\u867D\u7136\u8FD8\u80FD\u653E\u56DE\u4FE1\u9E3D\uFF0C\u77E5\u4F1A\u5BAB\u4E3B\u6240\u5904\u4F4D\u7F6E\uFF0C\u5374\u65E0\u6CD5\u63A5\u5230\u6559\u5185\u4F20\u6765\u7684\u6D88\u606F\uFF0C\u56E0\u6B64\u8FDE\u516B\u6781\u95E8\u503E\u6D3E\u800C\u51FA\u4E5F\u4E0D\u77E5\u6653\u3002\u5E78\u597D\u5F53\u65F6\u672A\u917F\u6210\u5927\u7978\uFF0C\u6B64\u65F6\u56DE\u60F3\u8D77\u6765\uFF0C\u82E5\u975E\u516B\u6781\u95E8\u4EE5\u6B66\u6797\u6B63\u9053\u81EA\u8BB8\uFF0C\u800C\u662F\u4E00\u4E0A\u6765\u5C31\u7ACB\u5373\u52A8\u624B\uFF0C\u540E\u679C\u96BE\u6599\u3002\u75DB\u5B9A\u601D\u75DB\uFF0C\u91D1\u5F00\u7532\u624D\u6709\u8FD9\u4E2A\u63D0\u8BAE\u3002</p><p>\u6155\u5BB9\u9F99\u70B9\u5934\u9053\uFF1A\u300C\u52A0\u4E0A\u3002\u7ACB\u523B\u98DE\u9E3D\u4F20\u4E66\uFF0C\u8BF8\u4E8B\u90FD\u7531\u6C90\u62A4\u6CD5\u5B9A\u593A\u3002\u300D</p><p>\u7075\u7389\u9886\u547D\u800C\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u671B\u7740\u4E00\u671B\u65E0\u9645\u7684\u832B\u832B\u8349\u6D77\uFF0C\u81EA\u8A00\u81EA\u8BED\u9053\uFF1A\u300C\u59DA\u5174\u554A\u59DA\u5174\uFF0C\u8981\u4E0D\u80FD\u8BA9\u4F60\u5C1D\u5C3D\u4E16\u95F4\u6240\u6709\u7684\u82E6\u695A\uFF0C\u6211\u6155\u5BB9\u9F99\u6789\u59D3\u4E86\u6155\u5BB9\u8FD9\u4E2A\u59D3\u6C0F\u3002\u300D</p><p>\u5510\u989C\u8DDF\u5728\u8F66\u540E\uFF0C\u5C06\u4ED6\u4EEC\u7684\u8A00\u8C08\u542C\u5F97\u4E00\u5B57\u4E0D\u6F0F\u3002\u5979\u6CA1\u60F3\u5230\u661F\u6708\u6E56\u4F1A\u4E0E\u5F53\u5E74\u7684\u5927\u71D5\u6709\u5982\u6B64\u6DF1\u7684\u74DC\u845B\uFF0C\u66F4\u6CA1\u60F3\u5230\u4E00\u7EDF\u6C5F\u6E56\uFF0C\u4E0D\u8FC7\u662F\u6155\u5BB9\u9F99\u7684\u7B2C\u4E00\u6B65\uFF0C\u4ED6\u7684\u76EE\u6807\u7ADF\u662F\u6574\u4E2A\u5929\u4E0B\u3002</p><p>\u5FC3\u5FF5\u8F6C\u52A8\u95F4\uFF0C\u5510\u989C\u53C8\u5927\u60D1\u4E0D\u89E3\uFF0C\u4E3A\u4F55\u4ED6\u4EEC\u5BF9\u81EA\u5DF1\u6BEB\u4E0D\u5FCC\u8BB3\uFF0C\u7ADF\u7136\u5F53\u9762\u5546\u8C08\u8FD9\u4E9B\u673A\u5BC6\uFF1F\u83AB\u975E\u2026\u2026</p><p>\u5C11\u5987\u6253\u4E86\u5BD2\u6218\uFF0C\u5FC3\u5934\u53D8\u5F97\u51B0\u51B7\u3002\u5979\u8D4C\u7684\u662F\u6155\u5BB9\u9F99\u4EE5\u5BAB\u4E3B\u4E4B\u5C0A\u4E0D\u4F1A\u8F7B\u6613\u6BC1\u8BFA\u3002</p><p>\u4F46\u4E07\u4E00\u4ED6\u65E0\u803B\u5230\u65E0\u8D56\u7684\u5730\u6B65\u5462\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8F66\u961F\u5728\u4E00\u6761\u5C0F\u6CB3\u524D\u505C\u4E86\u4E0B\u6765\u3002\u9976\u662F\u5510\u989C\u6B66\u529F\u4E0D\u51E1\uFF0C\u4E0D\u505C\u6B47\u7684\u5954\u6CE2\u4E86\u4E00\u4E2A\u4E0A\u5348\uFF0C\u6B64\u65F6\u4E5F\u5185\u606F\u4E0D\u7545\u3002\u9888\u540E\u88AB\u7F30\u7EF3\u78E8\u7834\uFF0C\u8D64\u88F8\u7684\u5C0F\u817F\u3001\u811A\u638C\u66F4\u662F\u88AB\u5212\u5F97\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u5979\u5750\u5728\u5730\u4E0A\uFF0C\u54AC\u7259\u62D4\u51FA\u811A\u4E0A\u7684\u5C0F\u523A\uFF0C\u7136\u540E\u6162\u6162\u64A9\u6C34\u6D17\u51C0\u3002\u6B64\u65F6\u4EBA\u4EBA\u90FD\u5728\u559D\u6C34\u996E\u9A6C\uFF0C\u65E0\u4EBA\u524D\u6765\u8C03\u620F\uFF0C\u7B97\u662F\u6709\u4E86\u7247\u523B\u7684\u6E05\u51C0\u3002</p><p>\u5F2F\u66F2\u7684\u5C0F\u6CB3\u6E05\u6F88\u800C\u5E95\uFF0C\u5728\u8349\u539F\u4E2D\u65F6\u9690\u65F6\u73B0\u5730\u8FDC\u8FDC\u4E1C\u6D41\u3002\u6CB3\u6C34\u6E29\u51C9\u5408\u5EA6\uFF0C\u53D7\u4F24\u7684\u811A\u638C\u653E\u5728\u91CC\u9762\uFF0C\u4E00\u80A1\u900F\u5FC3\u7684\u9165\u723D\u4F7F\u5510\u989C\u95ED\u4E0A\u773C\u775B\u3002\u4F46\u53EA\u8FC7\u4E86\u7247\u523B\uFF0C\u5979\u5C31\u7741\u5F00\u773C\uFF0C\u91CD\u65B0\u9762\u5BF9\u73B0\u5B9E\u7684\u75DB\u82E6\u3002</p><p>\u5979\u62AC\u8D77\u811A\uFF0C\u51C6\u5907\u64E6\u5E72\u5305\u597D\u4F24\u53E3\uFF0C\u624D\u60F3\u8D77\u81EA\u5DF1\u8EAB\u4E0A\u8FDE\u4E00\u7247\u906E\u7F9E\u7684\u5E03\u90FD\u6CA1\u6709\u3002</p><p>\u8D64\u7740\u8EAB\u5B50\u88AB\u4EBA\u6813\u5728\u9A6C\u8F66\u540E\u62D6\u884C\u4E00\u8DEF\uFF0C\u8FD9\u79CD\u96BE\u4EE5\u60F3\u50CF\u7684\u803B\u8FB1\u4F7F\u5510\u989C\u6014\u6014\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u300C\u5A18\u3002\u300D</p><p>\u5510\u989C\u4E00\u56DE\u5934\uFF0C\u53EA\u89C1\u767D\u6C0F\u59D0\u59B9\u5DE6\u53F3\u62C9\u7740\u513F\u5B50\u7684\u5C0F\u624B\u6B63\u7AD9\u5728\u8EAB\u540E\u3002</p><p>\u5979\u8FDE\u5FD9\u64E6\u5E72\u773C\u6CEA\uFF0C\u9732\u51FA\u4E00\u4E2A\u6E29\u6696\u7684\u7B11\u5BB9\uFF0C\u300C\u6714\u513F\u3002\u300D</p><p>\u9F99\u6714\u8D70\u4E86\u8FC7\u6765\uFF0C\u5374\u6CA1\u6709\u50CF\u4ECE\u524D\u90A3\u6837\u6251\u5230\u6BCD\u4EB2\u6000\u91CC\uFF0C\u800C\u662F\u505C\u5728\u79BB\u5510\u989C\u4E24\u6B65\u7684\u5730\u65B9\u9759\u9759\u770B\u7740\u5979\u3002</p><p>\u5510\u989C\u6728\u7136\u5F20\u7740\u53CC\u81C2\uFF0C\u5634\u5507\u98A4\u6296\u8D77\u6765\u3002\u81EA\u5DF1\u662F\u4E0D\u662F\u5DF2\u7ECF\u4F24\u4E86\u513F\u5B50\u7684\u5FC3\u2026\u2026</p><p>\u6BCD\u5B50\u4FE9\u8FDC\u8FDC\u5BF9\u89C6\u7740\uFF0C\u867D\u7136\u53EA\u6709\u4E24\u6B65\u7684\u8DDD\u79BB\uFF0C\u5510\u989C\u5374\u89C9\u5F97\u6C38\u8FDC\u4E5F\u65E0\u6CD5\u518D\u628A\u513F\u5B50\u62B1\u5728\u6000\u91CC\u3002\u5C11\u5987\u70ED\u6CEA\u6EC2\u6CB1\uFF0C\u5FFD\u7136\u63A9\u9762\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u767D\u7389\u83BA\u8E72\u5728\u5510\u989C\u8EAB\u8FB9\uFF0C\u6495\u4E0B\u8863\u895F\u5C06\u5E08\u5A18\u4F24\u75D5\u7D2F\u7D2F\u7684\u7389\u8DB3\u4ED4\u7EC6\u5305\u597D\u3002\u767D\u7389\u9E42\u5219\u54C4\u7740\u9F99\u6714\uFF0C\u8BA9\u4ED6\u53BB\u5B89\u6170\u6BCD\u4EB2\u3002</p><p>\u300C\u5A18\u3002\u300D\u9F99\u6714\u7684\u58F0\u97F3\u5F88\u5E73\u9759\uFF0C\u4E00\u70B9\u4E5F\u4E0D\u50CF\u4E00\u4E2A\u516B\u5C81\u7684\u5B69\u5B50\uFF0C\u300C\u6211\u4F1A\u7ED9\u7239\u7239\u3001\u7ED9\u5A18\u62A5\u4EC7\u7684\u3002\u300D</p><p>\u5510\u989C\u82B3\u5FC3\u788E\u6210\u4E00\u7247\u4E00\u7247\uFF0C\u65E2\u56E0\u4E3A\u513F\u5B50\u7684\u61C2\u4E8B\uFF0C\u53C8\u56E0\u4E3A\u513F\u5B50\u5DF2\u7ECF\u77E5\u9053\u4E86\u81EA\u5DF1\u6240\u505A\u7684\u90FD\u662F\u53EF\u803B\u7684\u4E8B\u60C5\u3002\u5979\u6D41\u7740\u6CEA\u5728\u5FC3\u91CC\u53D1\u8A93\uFF0C\u53EA\u8981\u5C06\u513F\u5B50\u9001\u56DE\u5B89\u5B9A\uFF0C\u6258\u4ED8\u7ED9\u4EB2\u4EBA\uFF0C\u81EA\u5DF1\u4FBF\u7ACB\u523B\u81EA\u5C3D\uFF0C\u518D\u65E0\u989C\u591A\u6D3B\u4E00\u523B\u3002</p><p>\u5979\u4E00\u628A\u634F\u4F4F\u767D\u7389\u83BA\u7684\u624B\u8155\uFF0C\u95EE\u9053\uFF1A\u300C\u4ED6\u8BF4\u8BDD\u771F\u7684\u7B97\u6570\u5417\uFF1F\u300D</p><p>\u5E08\u5A18\u7684\u529B\u6C14\u5927\u5F97\u5F02\u4E4E\u5BFB\u5E38\uFF0C\u767D\u7389\u83BA\u75DB\u5F97\u62E7\u4F4F\u7709\u5934\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u5BAB\u4E3B\u8BF4\u8BDD\u4ECE\u6765\u90FD\u6CA1\u6709\u4E0D\u4F5C\u6570\u7684\u3002\u300D</p><p>\u5510\u989C\u653E\u4E0B\u5FC3\u4E8B\uFF0C\u6162\u6162\u677E\u5F00\u624B\u3002</p><p>\u767D\u7389\u83BA\u4E5F\u89C9\u5F97\u6155\u5BB9\u9F99\u5F00\u51FA\u7684\u6761\u4EF6\u5BBD\u5927\u5F97\u4E0D\u53EF\u601D\u8BAE\uFF0C\u7ED9\u5BAB\u4E3B\u5F53\u4E86\u6570\u6708\u5974\u5A62\uFF0C\u5BF9\u4ED6\u7684\u624B\u6BB5\u4E5F\u7565\u77E5\u4E00\u4E8C\uFF0C\u4E8E\u662F\u8BF4\u9053\uFF1A\u300C\u5E08\u5A18\u8FD8\u662F\u5C0F\u5FC3\u4E9B\u2026\u2026\u300D</p><p>\u8BDD\u672A\u8BF4\u5B8C\uFF0C\u4E00\u4F17\u7537\u4EBA\u53C8\u56F4\u4E86\u8FC7\u6765\u3002\u4E5E\u4F0F\u7A77\u9686\u4E00\u628A\u63A8\u5F00\u9F99\u6714\uFF0C\u53EB\u9053\uFF1A\u300C\u8D31\u5974\uFF0C\u722C\u8FC7\u6765\uFF01\u300D</p><p>\u9F99\u6714\u6B7B\u6B7B\u634F\u4F4F\u62F3\u5934\uFF0C\u626D\u5934\u79BB\u5F00\u3002\u767D\u7389\u83BA\u51B2\u59B9\u59B9\u4F7F\u4E2A\u773C\u8272\uFF0C\u8BA9\u5979\u8DDF\u8FC7\u53BB\u7167\u6599\uFF0C\u81EA\u5DF1\u5A9A\u7B11\u7740\u62B1\u4F4F\u4E5E\u4F0F\u7A77\u9686\u7684\u624B\u81C2\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u4E3B\u5B50\u8981\u64CD\u4EBA\u5BB6\u561B\u2026\u2026\u300D</p><p>\u4E5E\u4F0F\u7A77\u9686\u5728\u5979\u8138\u4E0A\u626D\u4E86\u4E00\u628A\uFF0C\u300C\u4E3B\u5B50\u8FD9\u4F1A\u6CA1\u5DE5\u592B\uFF0C\u665A\u4E9B\u518D\u64CD\u4F60\u597D\u4E86\u3002\u300D\u4ED6\u63D0\u9AD8\u58F0\u97F3\uFF0C\u51B2\u5510\u989C\u8BF4\u9053\uFF1A\u300C\u817F\u5206\u5F00\uFF01\u8BA9\u8001\u5B50\u628A\u4E1C\u897F\u638F\u51FA\u6765\u3002\u300D</p><p>\u5510\u989C\u8EBA\u5728\u5730\u4E0A\uFF0C\u5F20\u5F00\u53CC\u817F\u3002\u7389\u6237\u88AB\u94C1\u83B2\u5B50\u3001\u98DE\u8757\u77F3\u6253\u5F97\u7EA2\u80BF\u4E0D\u582A\uFF0C\u6709\u51E0\u5904\u9690\u9690\u8FD8\u6E17\u7740\u8840\u8FF9\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u62AC\u624B\u4F38\u5230\u82B1\u74E3\u5185\uFF0C\u7C97\u66B4\u5730\u6405\u5F04\u8D77\u6765\u3002\u7EA2\u80BF\u7684\u82B1\u74E3\u5728\u7C97\u7CD9\u7684\u624B\u638C\u8FB9\u7F18\u4E0D\u4F4F\u9F13\u80C0\u7FFB\u5377\uFF0C\u76F4\u5230\u541E\u6CA1\u4E86\u6574\u53EA\u624B\u638C\u3002\u5510\u989C\u75DB\u5F7B\u5FC3\u80BA\uFF0C\u67D4\u9888\u652F\u5728\u5730\u4E0A\uFF0C\u82E6\u82E6\u5FCD\u8010\u3002</p><p>\u300C\u4E00\u3001\u4E8C\u3001\u4E09\u2026\u2026\u5341\u4E94\u3002\u300D\u4E5E\u4F0F\u7A77\u9686\u628A\u5E26\u7740\u5C11\u5987\u4F53\u6DB2\u7684\u6697\u5668\u4E00\u4E00\u638F\u51FA\uFF0C\u6392\u5728\u5730\u4E0A\uFF0C\u7B97\u9053\uFF1A\u300C\u5341\u4E94\u9897\u94C1\u83B2\u5B50\uFF0C\u4E03\u9897\u94C1\u83E9\u63D0\uFF0C\u4E94\u9897\u98DE\u8757\u77F3\u2026\u2026\u4ED6\u5988\u7684\uFF01\u300D\u4ED6\u638F\u51FA\u4E00\u5757\u788E\u8089\uFF0C\u4E0D\u7531\u5413\u4E86\u4E00\u8DF3\u3002</p><p>\u5510\u989C\u98A4\u58F0\u9053\uFF1A\u300C\u8FD9\u662F\u4EC7\u4E8C\u7237\u585E\u5230\u5974\u5A62\u5C44\u91CC\u7684\u3002\u300D</p><p>\u4E5E\u4F0F\u7A77\u9686\u62CE\u7740\u4ED4\u7EC6\u4E00\u770B\uFF0C\u4F9D\u7A00\u8BA4\u51FA\u662F\u9633\u5177\u7684\u6A21\u6837\uFF0C\u300C\u4EC7\u5BB6\u5144\u5F1F\u602A\u4E0D\u5F97\u59D3\u7403\u5462\u3002</p><p>\u8FD9\u662F\u8C01\u7684\uFF1F\u300D</p><p>\u5C11\u5987\u8138\u8272\u82CD\u767D\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u662F\u5974\u5A62\u4E08\u592B\u7684\u3002\u300D</p><p>\u300C\u5662\uFF0C\u300D\u4E5E\u4F0F\u7A77\u9686\u604D\u60DA\u5927\u609F\uFF0C\u300C\u767E\u6218\u5929\u9F99\u5C31\u5269\u8FD9\u4E48\u4E00\u70B9\u4E86\uFF1F\u4EC7\u8001\u4E8C\u60F3\u5F97\u5468\u5230\uFF0C\u8BA9\u4F60\u4EEC\u592B\u59BB\u56E2\u805A\u3002\u8FD8\u6709\u5417\uFF1F\u300D</p><p>\u5510\u989C\u54AC\u7740\u7259\u4ECE\u809B\u95E8\u53C8\u638F\u51FA\u4E00\u622A\u788E\u8089\u3002</p><p>\u4EC7\u767E\u9CCC\u6628\u65E5\u88AB\u5979\u523A\u4E86\u4E00\u5251\uFF0C\u665A\u4E0A\u72E0\u72E0\u64CD\u4E86\u5979\u51E0\u756A\uFF0C\u5FC3\u5934\u8FD8\u662F\u6C14\u6068\u96BE\u6D88\uFF0C\u4E34\u8D70\u65F6\u4E0D\u5149\u5272\u4E0B\u9F99\u6218\u91CE\u7684\u9633\u5177\u6765\u7F9E\u8FB1\u5510\u989C\uFF0C\u8FD8\u628A\u516B\u6770\u7684\u9633\u5177\u90FD\u5272\u4E86\u4E0B\u6765\uFF0C\u8BF4\u662F\u8BA9\u5979\u540C\u95E8\u5C3D\u6B22\u3002\u597D\u5728\u8840\u65A9\u53CC\u715E\u5306\u5306\u79BB\u5F00\uFF0C\u53EA\u585E\u4E86\u4E00\u4E2A\u4E0D\u77E5\u662F\u8C01\u7684\u9633\u5177\u3002</p><p>\u300C\u522B\u95F9\u4E86\u3002\u8D76\u8DEF\u8981\u7D27\u3002\u300D\u77F3\u880D\u5728\u65C1\u8FB9\u558A\u4E86\u4E00\u58F0\u3002</p><p>\u5510\u989C\u6323\u624E\u7740\u7AD9\u8D77\u6765\uFF0C\u671D\u8F66\u540E\u8D70\u53BB\u3002\u53EA\u5269\u4E0B\u4E24\u4E2A\u65F6\u8FB0\uFF0C\u8FD9\u4E00\u5207\u90FD\u7ED3\u675F\u4E86\u3002\u4E3A\u4E86\u6714\u513F\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u8981\u6491\u4E0B\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8FD9\u6B21\u53EA\u8D70\u4E86\u534A\u4E2A\u65F6\u8FB0\uFF0C\u5510\u989C\u5C31\u88AB\u53EB\u5165\u8F66\u5185\u3002</p><p>\u6628\u65E5\u8FD8\u662F\u82F1\u59FF\u98D2\u723D\u79C0\u7F8E\u5982\u8BD7\u7684\u638C\u95E8\u592B\u4EBA\uFF0C\u6B64\u65F6\u6D51\u8EAB\u6CBE\u6EE1\u7070\u5C18\uFF0C\u8D64\u88F8\u7684\u8089\u4F53\u4E00\u8DEF\u66B4\u6652\uFF0C\u5FAE\u5FAE\u6709\u4E9B\u53D1\u7EA2\u3002\u6C57\u6C34\u4ECE\u4E73\u4E0A\u51B2\u5F00\u4E00\u9053\u873F\u8712\u7684\u5370\u75D5\uFF0C\u9732\u51FA\u808C\u80A4\u7684\u672C\u8272\u3002</p><p>\u6155\u5BB9\u9F99\u5148\u8BA9\u767D\u6C0F\u59D0\u59B9\u628A\u5510\u989C\u64E6\u6D17\u5E72\u51C0\uFF0C\u7136\u540E\u62FF\u51FA\u94A2\u9488\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u628A\u5976\u5B50\u6258\u8D77\u6765\u3002\u300D</p><p>\u5510\u989C\u4E00\u6014\uFF0C\u65CB\u5373\u660E\u767D\u4ED6\u662F\u8981\u7ED9\u81EA\u5DF1\u7EB9\u8EAB\uFF0C\u8FD9\u53EF\u662F\u4E00\u8F88\u5B50\u4E5F\u65E0\u6CD5\u6D17\u53BB\u7684\u5370\u8FF9\u2026\u2026\u5979\u6014\u4E86\u7247\u523B\uFF0C\u6162\u6162\u6258\u8D77\u81EA\u5DF1\u4E30\u6EE1\u7684\u4E73\u623F\u3002\u53CD\u6B63\u5979\u4E5F\u4E0D\u613F\u518D\u82DF\u6D3B\u4E16\u4E0A\uFF0C\u8FD9\u5177\u810F\u900F\u7684\u8EAB\u4F53\u8FD8\u6709\u4EC0\u4E48\u503C\u5F97\u73CD\u60DC\u7684\u5462\uFF1F</p><p>\u950B\u5229\u7684\u94A2\u9488\u523A\u7834\u76AE\u80A4\uFF0C\u5E26\u51FA\u4E00\u6EF4\u6BB7\u7EA2\u7684\u8840\u73E0\u3002\u6155\u5BB9\u9F99\u4E00\u8FB9\u523A\u4E00\u8FB9\u5411\u7075\u7389\u8BA8\u6559\u3002</p><p>\u7075\u7389\u81EA\u7136\u662F\u77E5\u65E0\u4E0D\u8A00\uFF0C\u8A00\u65E0\u4E0D\u5C3D\uFF0C\u5C31\u5DEE\u6CA1\u6709\u624B\u628A\u624B\u7ED9\u5BAB\u4E3B\u6307\u70B9\u4E86\u3002</p><p>\u9F99\u6714\u50CF\u4E00\u4E2A\u6C89\u9ED8\u7684\u5C0F\u548C\u5C1A\uFF0C\u4E00\u76F4\u9762\u58C1\u5750\u5728\u89D2\u843D\u91CC\u3002\u660E\u77E5\u6BCD\u4EB2\u5C31\u5728\u8EAB\u540E\uFF0C\u5374\u6CA1\u6709\u56DE\u5934\u770B\u4E00\u773C\u3002\u5510\u989C\u5145\u6EE1\u601C\u7231\u5730\u671B\u7740\u513F\u5B50\uFF0C\u8FDE\u8089\u4F53\u7684\u75DB\u82E6\u4F3C\u4E4E\u4E5F\u6DE1\u5FD8\u4E86\u3002</p><p>\u7B49\u6155\u5BB9\u9F99\u523A\u5B8C\uFF0C\u5C11\u5987\u7684\u5706\u4E73\u5DF2\u7ECF\u53D8\u6210\u4E24\u53EA\u6EF4\u8840\u7684\u8089\u7403\u3002\u767D\u6C0F\u59D0\u59B9\u542B\u7740\u6CEA\u64E6\u51C0\u8840\u8FF9\uFF0C\u6155\u5BB9\u9F99\u968F\u624B\u62FF\u8D77\u65C1\u8FB9\u7684\u58A8\u6C41\u6D82\u5728\u5510\u989C\u4E73\u4E0A\u3002</p><p>\u5510\u989C\u5782\u5934\u770B\u53BB\uFF0C\u53EA\u89C1\u53F3\u4E73\u523A\u7684\u975E\u82B1\u975E\u9E1F\uFF0C\u800C\u662F\u4E00\u884C\u5B57\u300C\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u300D\uFF0C\u5DE6\u4E73\u523A\u7740\u300C\u661F\u6708\u6E56\u6DEB\u5974\u5510\u989C\u300D\u3002\u6F06\u9ED1\u7684\u5B57\u8FF9\u5370\u5728\u96EA\u767D\u7684\u808C\u80A4\u4E0A\uFF0C\u63D0\u9192\u5979\u6240\u53D7\u5230\u7684\u79CD\u79CD\u803B\u8FB1\u3002</p><p>\u300C\u672C\u5BAB\u523A\u5F97\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u4E00\u6EF4\u6CEA\u6389\u5728\u5B57\u8FF9\u4E0A\uFF0C\u51B2\u5F00\u4E00\u9053\u6DE1\u6DE1\u58A8\u8272\uFF0C\u63A5\u7740\u8D8A\u6765\u8D8A\u591A\u3002\u5510\u989C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u597D\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u6EE1\u610F\u5C31\u597D\u3002\u8FD8\u6709\u4E00\u4E2A\u65F6\u8FB0\u7EA6\u5B9A\u7684\u65F6\u95F4\u5C31\u5230\u4E86\uFF0C\u9F99\u592B\u4EBA\u662F\u4E0D\u662F\u7B49\u4E0D\u53CA\u4E86\u5462\uFF1F\u300D</p><p>\u5510\u989C\u76EE\u5149\u505C\u5728\u4E73\u4E0A\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002\u5979\u5728\u60F3\uFF0C\u81EA\u5C3D\u65F6\u4E00\u5B9A\u8981\u8BA9\u4EBA\u627E\u4E0D\u5230\u81EA\u5DF1\u7684\u5C38\u8EAB\uFF0C\u800C\u4E14\u8981\u5148\u6BC1\u6389\u8FD9\u4E9B\u5B57\u8FF9\u3002\u6216\u8005\u4EB2\u624B\u5272\u4E0B\u81EA\u5DF1\u7684\u4E73\u623F\u2026\u2026\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u80FD\u8BA9\u4EBA\u770B\u5230\u3002</p><p>\u6155\u5BB9\u9F99\u638F\u51FA\u8089\u68D2\uFF0C\u300C\u8FD9\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u9F99\u592B\u4EBA\u8FD8\u662F\u672C\u5BAB\u7684\u6DEB\u5974\u2026\u2026\u300D</p>',74);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
