import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_92" tabindex="-1"><a class="header-anchor" href="#_92" aria-hidden="true">#</a> 92</h1><p>\u4EC5\u5269\u4E00\u4E2A\u65F6\u8FB0\u5C31\u53EF\u8131\u79BB\u82E6\u6D77\uFF0C\u5374\u88AB\u4EBA\u5728\u4E73\u623F\u4E0A\u523A\u4E0B\u65E0\u6CD5\u78E8\u706D\u7684\u803B\u8FB1\u5370\u8FF9\uFF0C\u5510\u989C\u5FC3\u5982\u6B7B\u7070\u3002\u5979\u8DE8\u5750\u5728\u6155\u5BB9\u9F99\u8170\u95F4\uFF0C\u63E1\u7740\u5DE8\u7269\u6162\u6162\u9001\u5165\u4F53\u5185\u3002\u9971\u53D7\u6467\u6B8B\u7684\u8089\u7A74\u904D\u5E03\u4F24\u75D5\uFF0C\u6B64\u65F6\u6155\u5BB9\u9F99\u6709\u610F\u64A9\u62E8\uFF0C\u987F\u65F6\u9C9C\u8840\u56DB\u6EA2\u3002\u5979\u5403\u529B\u5730\u4E3E\u81C0\u5957\u5F04\uFF0C\u5A07\u8EAF\u4E0D\u505C\u6218\u6817\u3002\u5510\u989C\u5FC3\u9053\uFF1A\u4E5F\u8BB8\u4E0D\u7528\u81EA\u6740\uFF0C\u81EA\u5DF1\u5E26\u7740\u5B69\u5B50\u548C\u8FD9\u4E0B\u4F53\u7684\u4F24\u52BF\uFF0C\u5982\u4F55\u80FD\u8D70\u51FA\u8FD9\u832B\u832B\u8349\u539F\u3002</p><p>\u300C\u9F99\u592B\u4EBA\u50CF\u662F\u4E0D\u5927\u9AD8\u5174\u554A\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\u3002</p><p>\u5510\u989C\u4ECE\u6765\u6CA1\u6709\u8FD9\u4E48\u75DB\u82E6\u5730\u4EA4\u5408\u8FC7\uFF0C\u5373\u4F7F\u662F\u65B0\u5A5A\u4E4B\u591C\uFF0C\u9F99\u6218\u91CE\u4E5F\u5BF9\u5979\u601C\u7231\u4E07\u5206\u3002</p><p>\u5979\u5FC3\u91CC\u4E00\u9178\uFF0C\u4E08\u592B\u4E00\u5411\u662F\u5F88\u6E29\u67D4\u7684\uFF0C\u4ECE\u6765\u90FD\u4E0D\u4F1A\u5F04\u75BC\u81EA\u5DF1\u3002</p><p>\u300C\u53EB\u51FA\u6765\uFF01\u300D\u6155\u5BB9\u9F99\u58F0\u97F3\u4E00\u51B7\u3002</p><p>\u5510\u989C\u50F5\u4E86\u4E00\u4E0B\uFF0C\u300C\u554A\u300D\u5730\u4F4E\u53EB\u4E00\u58F0\u3002\u58F0\u97F3\u53C8\u5E72\u53C8\u6DA9\u3002</p><p>\u6155\u5BB9\u9F99\u7FFB\u8EAB\u628A\u5C11\u5987\u538B\u5728\u4E0B\u9762\uFF0C\u4E00\u8FB9\u633A\u5F04\uFF0C\u4E00\u8FB9\u5389\u58F0\u9053\uFF1A\u300C\u53EB\uFF01\u300D</p><p>\u300C\u554A\u2026\u2026\u554A\u2026\u2026\u300D\u5510\u989C\u80FD\u611F\u53D7\u5230\u7684\u53EA\u6709\u75DB\u82E6\uFF0C\u4F46\u8FD8\u8981\u88C5\u505A\u6B22\u6B23\u5730\u6D6A\u53EB\u51FA\u58F0\uFF0C\u6ECB\u5473\u82E6\u4E0D\u582A\u8A00\u3002</p><p>\u6155\u5BB9\u9F99\u9633\u5177\u4E00\u633A\uFF0C\u9876\u4F4F\u82B1\u5FC3\u6765\u56DE\u7814\u78E8\uFF0C\u8089\u68D2\u6839\u90E8\u7684\u89E6\u624B\u4E5F\u8702\u6D8C\u800C\u4E0A\uFF0C\u5728\u7EA2\u80BF\u7684\u79D8\u5904\u56DB\u4E0B\u62E8\u5F04\u3002</p><p>\u4E0D\u591A\u65F6\u5510\u989C\u5C31\u5FEB\u611F\u5982\u6F6E\uFF0C\u60C5\u4E0D\u81EA\u7981\u5730\u6D6A\u53EB\u8FDE\u8FDE\u3002\u53EB\u4E86\u51E0\u58F0\uFF0C\u5979\u7A81\u89C9\u4E0D\u5BF9\uFF0C\u4E00\u7741\u773C\uFF0C\u6B63\u770B\u5230\u513F\u5B50\u75DB\u6068\u7684\u76EE\u5149\u3002</p><p>\u6155\u5BB9\u9F99\u626D\u5934\u4E00\u770B\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u5A18\u88AB\u6211\u64CD\u5F97\u5F88\u5F00\u5FC3\u5462\u3002\u662F\u4E0D\u662F\uFF1F\u300D\u540E\u4E00\u53E5\u95EE\u7684\u5374\u662F\u5510\u989C\u3002</p><p>\u5510\u989C\u8138\u4E0A\u7684\u8840\u8272\u6E10\u6E10\u892A\u53BB\uFF0C\u6123\u6123\u770B\u7740\u513F\u5B50\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u662F\u4E0D\u662F\u4E5F\u60F3\u5C1D\u5C1D\u4F60\u5A18\u7684\u6ECB\u5473\u554A\uFF1F\u300D</p><p>\u9F99\u6714\u773C\u4E2D\u6012\u706B\u95EA\u52A8\uFF0C\u7A81\u7136\u8DF3\u8D77\u6765\uFF0C\u65BD\u51FA\u8FDE\u73AF\u817F\u671D\u6155\u5BB9\u9F99\u80F8\u53E3\u8E22\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u54EA\u4F1A\u628A\u4ED6\u653E\u5728\u5FC3\u4E0A\uFF0C\u4E00\u62AC\u624B\u4FBF\u62E7\u4F4F\u7A1A\u5AE9\u7684\u5C0F\u77ED\u817F\uFF0C\u5C06\u9F99\u6714\u4E3E\u5230\u534A\u7A7A\u3002</p><p>\u5510\u989C\u6323\u624E\u7740\u671D\u513F\u5B50\u4F38\u51FA\u53CC\u81C2\uFF0C\u53EB\u9053\uFF1A\u300C\u522B\u2026\u2026\u522B\u4F24\u6211\u7684\u5B69\u513F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6162\u6162\u628A\u9F99\u6714\u653E\u5728\u5730\u4E0A\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u653E\u5FC3\uFF0C\u672C\u5BAB\u7B54\u5E94\u8FC7\u4E0D\u4F24\u4ED6\u7684\u6027\u547D\u3002\u300D</p><p>\u5510\u989C\u4E00\u53E0\u58F0\u5730\u8BF4\u9053\uFF1A\u300C\u591A\u8C22\u5BAB\u4E3B\uFF0C\u591A\u8C22\u5BAB\u4E3B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u62CD\u62CD\u5979\u7684\u96EA\u81C0\uFF0C\u300C\u7528\u70B9\u529B\u3002\u300D</p><p>\u5C11\u5987\u611F\u6FC0\u4E0D\u5C3D\uFF0C\u4E0D\u987E\u8089\u7A74\u7684\u5267\u75DB\uFF0C\u5FC3\u7518\u60C5\u613F\u5730\u4E3E\u81C0\u5E94\u5408\uFF0C\u7AED\u529B\u626D\u52A8\u8170\u80A2\uFF0C\u4F7F\u8089\u68D2\u80FD\u8FDB\u5F97\u66F4\u6DF1\uFF0C\u597D\u8BA9\u5BAB\u4E3B\u6EE1\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u659C\u773C\u770B\u7740\u9F99\u6714\uFF0C\u53EA\u89C1\u5B69\u5B50\u773C\u91CC\u6162\u6162\u6D8C\u51FA\u900F\u660E\u7684\u6DB2\u4F53\uFF0C\u5634\u89D2\u4E5F\u671D\u4E0B\u5F2F\u53BB\uFF0C\u6E05\u79C0\u7684\u8138\u4E0A\u6EE1\u662F\u59D4\u5C48\u3002</p><p>\u300C\u8FD9\u5B69\u5B50\u8DDF\u767E\u6218\u5929\u9F99\u957F\u5F97\u53EF\u4E0D\u50CF\uFF0C\u662F\u4E0D\u662F\u522B\u4EBA\u7684\u79CD\uFF1F\u300D\u6155\u5BB9\u9F99\u8C03\u7B11\u9053\u3002</p><p>\u5510\u989C\u5403\u529B\u5730\u633A\u8D77\u96EA\u81C0\uFF0C\u5C06\u7855\u5927\u7684\u8089\u7624\u541E\u5165\u4F53\u5185\uFF0C\u7AED\u529B\u7528\u5A07\u5AE9\u7684\u8089\u7A74\u541E\u5410\u7740\u4E0A\u9762\u7684\u8089\u523A\uFF0C\u542C\u5230\u5BAB\u4E3B\u7684\u5632\u5F04\uFF0C\u5979\u5A07\u5598\u7740\u4F4E\u58F0\u9053\uFF1A\u300C\u5B69\u5B50\u8138\u578B\u50CF\u5974\u5A62\uFF0C\u773C\u775B\u50CF\u4ED6\u7239\u7239\u3002\u300D</p><p>\u679C\u7136\uFF0C\u90A3\u53CC\u773C\u775B\u53C8\u5927\u53C8\u9ED1\uFF0C\u77B3\u4EC1\u91CC\u9690\u9690\u71C3\u70E7\u7740\u65E0\u7A77\u7684\u6597\u5FD7\uFF0C\u4E0E\u6E05\u79C0\u7684\u9762\u5E9E\u8FE5\u7136\u76F8\u5F02\u3002\u6155\u5BB9\u9F99\u671B\u7740\u9F99\u6714\u770B\u4E86\u534A\u664C\uFF0C\u5634\u89D2\u6162\u6162\u6311\u8D77\u4E00\u4E1D\u7B11\u610F\uFF1A\u300C\u83BA\u5974\u3001\u9E42\u5974\uFF0C\u53BB\u8BA9\u9F99\u516C\u5B50\u5C1D\u5C1D\u5F53\u7537\u4EBA\u7684\u6ECB\u5473\u3002\u300D</p><p>\u5510\u989C\u6B63\u62FC\u547D\u6536\u7D27\u8089\u7A74\uFF0C\u529B\u6C14\u987F\u65F6\u677E\u4E86\uFF0C\u300C\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u72E0\u72E0\u4E00\u6345\uFF0C\u300C\u653E\u5FC3\uFF0C\u6211\u5BF9\u7537\u5B69\u6CA1\u5174\u8DA3\uFF0C\u4E0D\u4F1A\u64CD\u4ED6\u7684\u3002\u4EE4\u516C\u5B50\u8FD8\u662F\u7AE5\u7537\uFF0C\u4E0D\u64CD\u5973\u4EBA\u600E\u4E48\u80FD\u957F\u5927\uFF1F\u4F60\u8FD9\u4E24\u4F4D\u9AD8\u5F92\u7684\u7ECF\u9A8C\u53EF\u4E30\u5BCC\u5F97\u7D27\uFF0C\u80AF\u5B9A\u4F1A\u8BA9\u4EE4\u516C\u5B50\u6EE1\u610F\u3002\u300D</p><p>\u5510\u989C\u770B\u7740\u767D\u6C0F\u59D0\u59B9\u671D\u513F\u5B50\u8D70\u53BB\uFF0C\u5FC3\u4E00\u4E0B\u5B50\u63D0\u5230\u5589\u5934\u3002\u6714\u513F\u53EA\u6709\u516B\u5C81\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9F99\u6714\u53EA\u6323\u624E\u4E86\u51E0\u4E0B\u5C31\u88AB\u767D\u6C0F\u59D0\u59B9\u5236\u4F4F\u3002\u4E24\u5973\u4E00\u8FB9\u89E3\u5F00\u5B69\u5B50\u7684\u8863\u670D\uFF0C\u4E00\u8FB9\u5B89\u6170\u9053\uFF1A\u300C\u6CA1\u4E8B\u7684\uFF0C\u6CA1\u4E8B\u7684\u3002\u5C0F\u6714\u4E0D\u8981\u6015\uFF0C\u59D0\u59D0\u53EA\u662F\u5E2E\u5C0F\u6714\u957F\u5927\uFF0C\u4E0D\u4F1A\u75BC\u7684\u2026\u2026\u300D</p><p>\u9F99\u6714\u4E24\u624B\u88AB\u767D\u7389\u9E42\u63E1\u4F4F\uFF0C\u767D\u7389\u83BA\u5219\u5206\u5F00\u4ED6\u7684\u5C0F\u817F\uFF0C\u7528\u819D\u76D6\u538B\u7D27\uFF0C\u63A5\u7740\u892A\u4E0B\u4ED6\u7684\u88E4\u5B50\u3002</p><p>\u5C0F\u7537\u5B69\u7684\u808C\u80A4\u50CF\u5C11\u5973\u822C\u7C89\u5AE9\uFF0C\u80EF\u4E0B\u53C8\u5149\u53C8\u6ED1\uFF0C\u6CA1\u6709\u4E00\u6839\u6BDB\u53D1\u3002\u5C0F\u9E21\u9E21\u53EA\u6709\u624B\u6307\u5927\u5C0F\uFF0C\u53C8\u767D\u53C8\u7EC6\uFF0C\u8FD8\u6CA1\u6709\u8272\u7D20\u6C89\u6DC0\u3002\u9876\u7AEF\u987D\u76AE\u5730\u7FD8\u8D77\u4E00\u4E2A\u5C0F\u5C16\uFF0C\u8FD8\u662F\u5305\u830E\u3002</p><p>\u767D\u7389\u83BA\u5BF9\u8FD9\u4E2A\u5C0F\u5E08\u5F1F\u7231\u5982\u4EB2\u5F1F\uFF0C\u4F46\u4E3B\u547D\u96BE\u8FDD\uFF0C\u53EA\u597D\u5982\u6B64\u3002\u5979\u4E00\u8FB9\u7528\u8F7B\u67D4\u5730\u629A\u6478\u4E00\u8FB9\u5FAE\u7B11\u8BF4\uFF1A\u300C\u5C0F\u6714\u7684\u5C0F\u9E21\u9E21\u597D\u53EF\u7231\u54E6\u2026\u2026\u300D\u8BD5\u56FE\u6D88\u9664\u9F99\u6714\u7684\u6050\u60E7\u3002</p><p>\u8F7B\u8F7B\u5957\u5F04\u51E0\u4E0B\uFF0C\u767D\u7389\u83BA\u5F20\u5F00\u6A31\u5507\uFF0C\u5148\u5475\u4E86\u53E3\u6C14\uFF0C\u7136\u540E\u5C06\u5C0F\u9E21\u9E21\u542B\u5230\u5634\u4E2D\uFF0C\u7528\u6ED1\u817B\u7684\u9999\u820C\u7FFB\u5F00\u5305\u76AE\u3002</p><p>\u5979\u4EEC\u7684\u52A8\u4F5C\u5F88\u6E29\u67D4\uFF0C\u4F46\u9F99\u6714\u5374\u50CF\u88AB\u706B\u70E7\u822C\u53EB\u4E86\u8D77\u6765\u3002</p><p>\u65C1\u8FB9\u7684\u5510\u989C\u5FCD\u4E0D\u4F4F\u8BF4\u9053\uFF1A\u300C\u5C0F\u83BA\uFF0C\u4F60\u8F7B\u4E00\u4E9B\u2026\u2026\u522B\u52C9\u5F3A\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u70B9\u4E86\u70B9\u5934\uFF0C\u820C\u5C16\u8F7B\u8F7B\u6311\u5F04\u5305\u76AE\u5C16\u7AEF\u3002</p><p>\u9F99\u6714\u4E0D\u660E\u767D\uFF0C\u83BA\u59D0\u59D0\u4E3A\u4EC0\u4E48\u8981\u628A\u81EA\u5DF1\u6492\u5C3F\u7684\u4E1C\u897F\u5403\u5230\u5634\u91CC\uFF0C\u8FD8\u4E00\u4E2A\u52B2\u513F\u7684\u7528\u820C\u5934\u53BB\u8214\uFF0C\u5F04\u5F97\u4ED6\u53C8\u75D2\u53C8\u75DB\u3002</p><p>\u8F6F\u5AE9\u5AE9\u7684\u5C0F\u9E21\u9E21\u6CA1\u6709\u4E1D\u6BEB\u5F02\u5473\uFF0C\u4F3C\u4E4E\u7528\u820C\u5934\u5C31\u53EF\u4EE5\u5B8C\u5168\u5377\u4F4F\u3002\u767D\u7389\u83BA\u8D8A\u8214\u8D8A\u7231\uFF0C\u4F7F\u51FA\u6D51\u8EAB\u89E3\u672F\u5356\u529B\u8214\u5F04\u3002\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u5979\u51ED\u7740\u9AD8\u8D85\u7684\u820C\u6280\uFF0C\u7EC8\u4E8E\u5C06\u5B69\u5B50\u7684\u5C0F\u9E21\u9E21\u8214\u5F97\u786C\u4E86\u8D77\u6765\u3002</p><p>\u7EA2\u5507\u4E00\u5F20\uFF0C\u6CBE\u6EE1\u553E\u6DB2\u7684\u5C0F\u9E21\u9E21\u786C\u786C\u7FD8\u8D77\uFF0C\u5305\u76AE\u5DF2\u7ECF\u7FFB\u5F00\uFF0C\u9732\u51FA\u7C89\u7EA2\u7684\u5C0F\u9F9F\u5934\uFF0C\u50CF\u4E00\u6735\u65B0\u751F\u7684\u8611\u83C7\uFF0C\u9C9C\u5AE9\u53EF\u53E3\u3002</p><p>\u767D\u7389\u83BA\u4F0F\u5728\u9F99\u6714\u5C0F\u5C0F\u7684\u8EAB\u4F53\u4E0A\uFF0C\u601C\u7231\u5730\u770B\u7740\u4ED6\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5C0F\u6714\uFF0C\u8BA9\u59D0\u59D0\u5E2E\u4F60\u6210\u4E3A\u7537\u4EBA\u5427\u3002\u300D</p><p>\u9F99\u6714\u5C0F\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u547C\u547C\u5730\u5598\u7740\u6C14\u3002\u4ED6\u770B\u7740\u83BA\u59D0\u59D0\u62FF\u8D77\u81EA\u5DF1\u53D1\u786C\u7684\u5C0F\u9E21\u9E21\uFF0C\u671D\u8179\u4E0B\u9001\u53BB\uFF0C\u7A81\u7136\u60F3\u8D77\u6628\u665A\u6BCD\u4EB2\u7684\u4E3E\u52A8\u3002\u5979\u4EEC\u7A76\u7ADF\u662F\u5E72\u4EC0\u4E48\u5462\uFF1F</p><p>\u786C\u8D77\u7684\u5C0F\u9E21\u9E21\u50CF\u4E00\u6839\u5149\u6E9C\u6E9C\u7684\u624B\u6307\uFF0C\u6162\u6162\u7EB3\u5165\u6E29\u6DA6\u7684\u8089\u7A74\u4E2D\u3002\u6ED1\u817B\u7684\u8089\u58C1\u6BD4\u83BA\u59D0\u59D0\u7684\u5507\u820C\u66F4\u8212\u670D\uFF0C\u5C0F\u9E21\u9E21\u653E\u5728\u91CC\u9762\uFF0C\u9F99\u6714\u51FA\u4E8E\u672C\u80FD\u5730\u633A\u52A8\u8D77\u6765\u3002</p><p>\u767D\u7389\u83BA\u5BFC\u5F15\u7740\u8BA9\u4ED6\u8FDB\u5165\u5973\u6027\u7684\u795E\u79D8\u5883\u5730\uFF0C\u7528\u8EAB\u4F53\u544A\u8BC9\u4ED6\u7537\u5973\u4EA4\u5408\u7684\u6B22\u6109\u3002</p><p>\u9F99\u6714\u8D8A\u633A\u8D8A\u5FEB\uFF0C\u7A81\u7136\u5927\u53EB\u4E00\u58F0\uFF0C\u8EAB\u4F53\u6296\u52A8\u7740\u5C04\u51FA\u81EA\u5DF1\u5E73\u751F\u7B2C\u4E00\u6B21\u7CBE\u6DB2\u3002\u4E5F\u662F\u6BD5\u751F\u552F\u4E00\u4E00\u6B21\u3002</p><p>\u767D\u7389\u83BA\u7B11\u76C8\u76C8\u8D77\u8EAB\uFF0C\u4ED4\u7EC6\u8214\u51C0\u5C0F\u9E21\u9E21\u4E0A\u7684\u9ECF\u6DB2\uFF0C\u5728\u5979\u8273\u7EA2\u7684\u82B1\u74E3\u95F4\uFF0C\u4E00\u7F15\u6DE1\u6DE1\u7684\u767D\u8272\u6DB2\u4F53\u7F13\u7F13\u6D41\u51FA\u3002</p><p>\u5510\u989C\u7D27\u5F20\u5730\u770B\u7740\u513F\u5B50\uFF0C\u53EA\u89C1\u4ED6\u8138\u8272\u6E10\u6E10\u56DE\u590D\u6B63\u5E38\uFF0C\u773C\u775B\u5446\u5446\u770B\u7740\u8F66\u9876\uFF0C\u770B\u4E0D\u6E05\u662F\u559C\u60A6\u8FD8\u662F\u8FF7\u832B\u3002\u5C11\u5987\u95ED\u4E0A\u773C\uFF0C\u653E\u4E0B\u5FC3\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7684\u62BD\u9001\u6108\u53D1\u6FC0\u70C8\uFF0C\u5FFD\u7136\u6402\u4F4F\u5C11\u5987\u7684\u8170\u80A2\uFF0C\u72F0\u72DE\u7684\u8089\u68D2\u5168\u6839\u800C\u5165\u3002\u5510\u989C\u4EE5\u4E3A\u4ED6\u8981\u5C04\u7CBE\uFF0C\u8FDE\u5FD9\u633A\u8D77\u96EA\u81C0\uFF0C\u7528\u6DCC\u8840\u7684\u8089\u7A74\u88F9\u7D27\u6574\u652F\u5DE8\u9633\u3002</p><p>\u53EF\u8089\u68D2\u5E76\u6CA1\u6709\u50CF\u5979\u9884\u671F\u7684\u90A3\u6837\u5C04\u51FA\u6D53\u6D53\u7684\u6DB2\u4F53\uFF0C\u800C\u662F\u7D27\u7D27\u9876\u4F4F\u82B1\u5FC3\uFF0C\u4F3C\u4E4E\u8981\u7A7F\u900F\u4E00\u822C\u3002</p><p>\u7247\u523B\u540E\uFF0C\u9F9F\u5934\u9876\u7AEF\u7A81\u7136\u4F20\u6765\u4E00\u9635\u5F3A\u5927\u7684\u5438\u529B\uFF0C\u900F\u8FC7\u5B50\u5BAB\u76F4\u5165\u4E39\u7530\u3002\u5510\u989C\u60CA\u9A87\u6B32\u7EDD\uFF0C\u8EAB\u5B50\u4E00\u52A8\uFF0C\u624D\u53D1\u73B0\u81EA\u5DF1\u624B\u811A\u4E00\u70B9\u529B\u6C14\u4E5F\u65E0\u3002</p><p>\u4E39\u7530\u72B9\u5982\u503E\u659C\u7684\u6C34\u76C6\uFF0C\u8FD0\u8F6C\u7684\u771F\u6C14\u6D41\u6C34\u822C\u4E00\u6CC4\u800C\u51FA\uFF0C\u5C3D\u6570\u88AB\u9F9F\u5934\u5438\u5165\u3002\u4E00\u76CF\u8336\u5DE5\u592B\u540E\uFF0C\u5510\u989C\u82E6\u4FEE\u591A\u5E74\u7684\u771F\u5143\u5DF2\u7ECF\u88AB\u643E\u53D6\u5F97\u70B9\u6EF4\u65E0\u5B58\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u4E00\u677E\uFF0C\u5C11\u5987\u8F6F\u7EF5\u7EF5\u4F0F\u5728\u5730\u4E0A\uFF0C\u67D3\u6210\u901A\u7EA2\u7684\u5DE8\u9633\u4ECE\u96EA\u81C0\u4E2D\u6162\u6162\u8131\u51FA\uFF0C\u6700\u540E\u5411\u4E0A\u4E00\u6311\uFF0C\u98A4\u5FAE\u5FAE\u7AD6\u5728\u7A7A\u4E2D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5915\u9633\u897F\u4E0B\uFF0C\u8F66\u961F\u5728\u65E0\u8FB9\u7684\u8349\u539F\u4E2D\u75BE\u9A70\u3002</p><p>\u4E00\u540D\u9A91\u624B\u5954\u5230\u6700\u540E\u4E00\u8F86\u5927\u8F66\u8FB9\u4FEF\u8EAB\u51D1\u5728\u7A97\u8FB9\u4ED4\u7EC6\u542C\u7740\uFF0C\u7136\u540E\u626C\u81C2\u9AD8\u547C\u3002</p><p>\u75BE\u9A70\u7684\u8F66\u9A6C\u8F70\u7136\u505C\u4E0B\uFF0C\u4ECE\u8F66\u4E0A\u4E0B\u6765\u4E00\u884C\u4EBA\u3002</p><p>\u51E0\u540D\u5E2E\u4F17\u625B\u4E0B\u4E00\u6839\u5DE8\u6728\uFF0C\u5728\u8349\u4E1B\u4E2D\u5FD9\u788C\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u672C\u5BAB\u9075\u5B88\u627F\u8BFA\uFF0C\u5373\u523B\u653E\u8FC7\u4EE4\u516C\u5B50\u3002\u300D</p><p>\u5510\u989C\u5A07\u8EAF\u60E8\u767D\uFF0C\u552F\u6709\u4E73\u4E0A\u7684\u4E24\u884C\u58A8\u8FF9\u89E6\u76EE\u60CA\u5FC3\u3002\u5979\u795E\u60C5\u59D4\u987F\u5730\u4F9D\u5728\u767D\u6C0F\u59D0\u59B9\u81C2\u4E2D\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u591A\u8C22\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u65E0\u6240\u8C13\u5730\u6446\u4E86\u6446\u624B\uFF0C\u6307\u7740\u521A\u521A\u6811\u8D77\u7684\u5DE8\u6728\u9053\uFF1A\u300C\u4F60\u4EEC\u628A\u9F99\u592B\u4EBA\u653E\u4E0A\u53BB\u5427\u3002\u300D</p><p>\u4E09\u5973\u62AC\u773C\u770B\u53BB\uFF0C\u5747\u662F\u4E00\u60CA\u3002</p><p>\u90A3\u5DE8\u6728\u662F\u7528\u6765\u7167\u660E\u7684\u706B\u67F1\uFF0C\u9732\u5728\u5916\u9762\u7684\u90E8\u5206\u9AD8\u8FD1\u4E00\u4EBA\uFF0C\u7C97\u903E\u5C3A\u534A\u3002\u6B64\u65F6\u9876\u7AEF\u5C3A\u8BB8\u88AB\u524A\u6210\u9510\u5C16\uFF0C\u76F4\u6307\u84DD\u5929\u3002</p><p>\u6155\u5BB9\u9F99\u770B\u51FA\u5979\u4EEC\u7684\u6115\u7136\uFF0C\u89E3\u91CA\u9053\uFF1A\u300C\u53BB\u628A\u9F99\u592B\u4EBA\u7684\u5C44\u5957\u5728\u4E0A\u9762\u3002\u300D</p><p>\u5510\u989C\u8033\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u534A\u664C\u624D\u56DE\u8FC7\u795E\u6765\uFF0C\u8FD9\u65E0\u803B\u4E4B\u5F92\u679C\u7136\u4E0D\u5B88\u8BFA\u8A00\uFF0C\u8FD8\u8981\u4F7F\u7528\u8FD9\u79CD\u803B\u5211\u2026\u2026\u5979\u5389\u58F0\u9053\uFF1A\u300C\u4F60\u4E0D\u662F\u7B54\u5E94\u653E\u8FC7\u6211\u4EEC\u6BCD\u5B50\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u9F99\u592B\u4EBA\u8EAB\u4E3A\u638C\u95E8\u592B\u4EBA\uFF0C\u600E\u4E48\u8FDE\u672C\u5BAB\u7684\u8BDD\u90FD\u542C\u4E0D\u6E05\u695A\u5462\uFF1F\u672C\u5BAB\u7B54\u5E94\u653E\u8FC7\u516C\u5B50\uFF0C\u4EC0\u4E48\u65F6\u5019\u8BF4\u8FC7\u9976\u4F60\u6027\u547D\u5462\uFF1F\u300D</p><p>\u5510\u989C\u56DE\u60F3\u8D77\u4ED6\u8BF4\u8FC7\u7684\u8BDD\uFF0C\u4E0D\u7531\u5A07\u8EAF\u5267\u98A4\u3002\u53EF\u6068\u81EA\u5DF1\u62A4\u5B50\u5FC3\u5207\uFF0C\u7ADF\u6CA1\u6709\u542C\u51FA\u4ED6\u8BDD\u4E2D\u7684\u5708\u5957\u3002\u6C89\u9ED8\u7247\u523B\u540E\uFF0C\u5C11\u5987\u5FC3\u5934\u6EF4\u8840\u5730\u54ED\u53EB\u9053\uFF1A\u300C\u6211\u5316\u4F5C\u5389\u9B3C\u4E5F\u7EDD\u4E0D\u653E\u8FC7\u4F60\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5F00\u5FC3\u5730\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u8FD9\u8BDD\u672C\u5BAB\u4E5F\u542C\u8FC7\u51E0\u53E5\u3002\u53EF\u60DC\u6CA1\u6709\u4E00\u4E2A\u9B3C\u6562\u56DE\u6765\u2026\u2026\u300D\u4ED6\u8138\u4E00\u677F\uFF0C\u300C\u83BA\u5974\u3001\u9E42\u5974\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u6B64\u65F6\u75DB\u6094\u4E4B\u6781\uFF0C\u53EA\u6068\u5F53\u65F6\u6CA1\u6709\u529D\u5E08\u5A18\u9003\u751F\uFF0C\u800C\u8BA9\u5E08\u5A18\u53D7\u5C3D\u51CC\u8FB1\u3002\u4E24\u5973\u54ED\u7740\u8DEA\u5730\u62FC\u547D\u78D5\u5934\uFF0C\u300C\u6C42\u5BAB\u4E3B\u5F00\u6069\uFF0C\u653E\u8FC7\u6211\u5E08\u5A18\u5427\u3002\u300D\u767D\u7389\u83BA\u6EE1\u8138\u662F\u6CEA\u5730\u54C0\u6C42\u9053\uFF1A\u300C\u4E0D\u7136\u5C31\u8BA9\u5E08\u5A18\u7559\u5728\u6559\u5185\u4E3A\u5974\uFF0C\u4F3A\u5019\u4E3B\u5B50\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u8FD9\u8981\u770B\u9F99\u592B\u4EBA\u7684\u5FC3\u610F\u3002\u300D</p><p>\u4E0E\u5176\u4E00\u8F88\u5B50\u88AB\u4ED6\u4EEC\u6DEB\u8FB1\uFF0C\u5B81\u613F\u7ACB\u523B\u5C31\u6B7B\uFF01\u5510\u989C\u62AC\u8D77\u5934\uFF0C\u6068\u4E4B\u5165\u9AA8\u5730\u77AA\u7740\u6155\u5BB9\u9F99\u3002</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u300C\u9F99\u592B\u4EBA\u52C7\u6C14\u53EF\u5609\uFF0C\u90A3\u5C31\u8BF7\u592B\u4EBA\u8BD5\u8BD5\u8FD9\u6839\u67F1\u5B50\u5427\u3002\u300D</p><p>\u4E8B\u60C5\u518D\u65E0\u633D\u56DE\u4F59\u5730\uFF0C\u767D\u6C0F\u59D0\u59B9\u53EA\u80FD\u62B1\u4F4F\u5E08\u5A18\u653E\u58F0\u75DB\u54ED\u3002\u5728\u6155\u5BB9\u9F99\u7684\u5389\u58F0\u50AC\u4FC3\u4E0B\uFF0C\u4E24\u5973\u6276\u8D77\u5510\u989C\uFF0C\u4E00\u6B65\u4E00\u6643\u5730\u8D70\u5230\u67F1\u65C1\u3002</p><p>\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u53D7\u5C3D\u51CC\u8FB1\uFF0C\u53C8\u8981\u88AB\u8FD9\u79CD\u975E\u4EBA\u7684\u5211\u5177\u8650\u6740\uFF0C\u7D2B\u73AB\u5FC3\u4E0B\u53C8\u662F\u53F9\u606F\uFF0C\u53C8\u662F\u4F24\u611F\uFF0C\u6B63\u8981\u653E\u4E0B\u8F66\u5EC9\uFF0C\u773C\u89D2\u5374\u63A5\u89E6\u5230\u4E00\u9053\u5145\u6EE1\u6068\u610F\u7684\u76EE\u5149\u3002\u5979\u62AC\u773C\u770B\u53BB\uFF0C\u53EA\u89C1\u90A3\u4E2A\u5C0F\u5B69\u773C\u795E\u9489\u5B50\u822C\uFF0C\u4E00\u4E2A\u4E2A\u4ECE\u5728\u573A\u7684\u6BCF\u4E2A\u4EBA\u8138\u4E0A\u770B\u8FC7\u53BB\uFF0C\u4F3C\u4E4E\u8981\u628A\u4ED6\u4EEC\u7684\u6837\u5B50\u7EDF\u7EDF\u8BB0\u5230\u5FC3\u5E95\u3002</p>',73);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
