import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_93" tabindex="-1"><a class="header-anchor" href="#_93" aria-hidden="true">#</a> 93</h1><p>\u5510\u989C\u53CC\u624B\u88AB\u7F1A\u5230\u80CC\u540E\uFF0C\u767D\u7389\u83BA\u767D\u7389\u9E42\u6258\u7740\u5979\u4FEE\u957F\u7684\u7389\u817F\u6162\u6162\u4E3E\u8D77\u3002\u5C11\u5987\u9971\u7ECF\u8E42\u8E8F\u7684\u7389\u6237\u9C9C\u8840\u6D41\u6DCC\uFF0C\u7EA2\u80BF\u7684\u82B1\u74E3\u9F13\u6210\u4E00\u56E2\uFF0C\u5373\u4F7F\u4E24\u817F\u5E73\u5206\uFF0C\u4E5F\u65E0\u6CD5\u5206\u5F00\u3002</p><p>\u4EB2\u624B\u5C06\u7231\u5982\u6BCD\u4EB2\u7684\u5E08\u5A18\u9001\u4E0A\u5C16\u67F1\uFF0C\u767D\u6C0F\u59D0\u59B9\u5FC3\u4E2D\u7EDE\u75DB\uFF0C\u54ED\u5F97\u56DB\u624B\u4E71\u98A4\uFF0C\u600E\u4E48\u4E5F\u65E0\u6CD5\u5BF9\u51C6\u5C16\u9510\u7684\u67F1\u9876\u3002</p><p>\u4E5E\u4F0F\u7A77\u9686\u4E0A\u524D\u626F\u4F4F\u82B1\u74E3\u5411\u4E24\u8FB9\u72E0\u72E0\u4E00\u6495\uFF0C\u7136\u540E\u63E1\u62F3\u6345\u5165\u8089\u7A74\uFF0C\u6269\u5F00\u5510\u989C\u4E0B\u4F53\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6CEA\u773C\u6A21\u7CCA\u5730\u8F7B\u8F7B\u4E00\u653E\uFF0C\u628A\u5E08\u5A18\u7684\u8089\u7A74\u5957\u5728\u67F1\u5C16\uFF0C\u5374\u4E0D\u5FCD\u677E\u624B\u3002</p><p>\u5510\u989C\u5408\u4E0A\u7F8E\u76EE\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u653E\u624B\uFF01\u300D</p><p>\u767D\u7389\u9E42\u300C\u54C7\u300D\u7684\u4E00\u58F0\u54ED\u4E86\u8D77\u6765\uFF0C\u53EB\u9053\uFF1A\u300C\u5E08\u5A18\uFF01\u5E08\u5A18\uFF01\u300D\u767D\u7389\u83BA\u54FD\u54BD\u7740\u8BF4\uFF1A\u300C\u5E08\u5A18\u4E0D\u8981\u602A\u6211\u4EEC\uFF0C\u6211\u4EEC\u2026\u2026\u300D</p><p>\u300C\u5E08\u5A18\u77E5\u9053\u3002\u8BA9\u5E08\u5A18\u65E9\u4E9B\u6B7B\u5427\u3002\u300D</p><p>\u8089\u7A74\u7F13\u7F13\u4E0B\u964D\uFF0C\u5C06\u67F1\u5C16\u541E\u5165\u4F53\u5185\u3002\u541E\u5165\u4E09\u5BF8\u540E\uFF0C\u8089\u7A74\u5DF2\u88AB\u585E\u6EE1\uFF0C\u7EA2\u80BF\u7684\u82B1\u74E3\u56F4\u7740\u88AB\u70C8\u706B\u70E7\u9ED1\u7684\u67F1\u8EAB\uFF0C\u9F13\u8D77\u7EA2\u8273\u8273\u4E00\u5708\u5AE9\u8089\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u8BD5\u7740\u677E\u5F00\u624B\uFF0C\u5C11\u5987\u8EAB\u5B50\u731B\u7136\u4E00\u6C89\uFF0C\u90A3\u5708\u5AE9\u8089\u7ACB\u65F6\u88AB\u67F1\u8EAB\u5377\u5165\u4F53\u5185\uFF0C\u7136\u540E\u53C8\u5B9A\u4F4F\u4E86\u3002</p><p>\u5510\u989C\u53EA\u89C9\u8EAB\u4F53\u7684\u91CD\u91CF\u5168\u90E8\u538B\u5728\u4E0B\u4F53\uFF0C\u5A07\u5AE9\u7684\u8089\u7A74\u6495\u88C2\u822C\u88AB\u6574\u4E2A\u6491\u5F00\uFF0C\u5C16\u9510\u7684\u786C\u6728\u6B63\u624E\u5728\u82B1\u5FC3\u4E0A\uFF0C\u53C8\u75D2\u53C8\u75DB\u3002\u6C42\u751F\u7684\u672C\u80FD\u4F7F\u5979\u4E24\u817F\u5408\u62E2\uFF0C\u540C\u65F6\u62FC\u547D\u6536\u7D27\u4E0B\u8179\uFF0C\u7AED\u529B\u7528\u6ED1\u5AE9\u7684\u8154\u4F53\u5939\u7D27\u7C97\u7CD9\u7684\u6728\u67F1\u3002</p><p>\u7247\u523B\u95F4\uFF0C\u6728\u67F1\u5DF2\u523A\u5165\u516D\u5BF8\u6709\u4F59\uFF0C\u7A74\u53E3\u7684\u67F1\u4F53\u8DB3\u6709\u56DB\u5BF8\u7C97\u7EC6\uFF0C\u7D27\u7D27\u5361\u4F4F\u803B\u9AA8\u3002</p><p>\u5C11\u5987\u4E24\u819D\u7528\u529B\u5408\u7D27\uFF0C\u5A07\u8EAF\u7EC8\u4E8E\u505C\u4F4F\u67F1\u4E0A\uFF0C\u4E0D\u518D\u4E0B\u6ED1\u3002</p><p>\u4E00\u540D\u5E2E\u4F17\u6478\u51FA\u77ED\u5200\uFF0C\u8D70\u5230\u5510\u989C\u8EAB\u540E\uFF0C\u5728\u4F1A\u9634\u5904\u8F7B\u8F7B\u4E00\u5212\uFF0C\u5C06\u8089\u7A74\u5207\u81F3\u83CA\u809B\u3002</p><p>\u4F53\u5185\u6EE1\u6EA2\u7684\u9C9C\u8840\u7ACB\u5373\u4E00\u6D8C\u800C\u51FA\uFF0C\u5728\u67F1\u8EAB\u4E0A\u5212\u51FA\u9053\u9053\u8840\u75D5\u3002</p><p>\u8FD8\u6709\u4E00\u540D\u5E2E\u4F17\u627E\u6765\u4E24\u5757\u5DE8\u77F3\uFF0C\u7528\u7EF3\u7D22\u6346\u5728\u5510\u989C\u8E1D\u4E0A\u3002</p><p>\u5510\u989C\u77E5\u9053\u81EA\u5DF1\u5355\u9760\u4E24\u817F\uFF0C\u518D\u65E0\u6CD5\u652F\u6491\u591A\u4E45\uFF0C\u4E8E\u662F\u7741\u5F00\u773C\uFF0C\u4E07\u5206\u96BE\u820D\u5730\u671B\u7740\u513F\u5B50\u3002</p><p>\u300C\u5A18\u3002\u300D\u9F99\u6714\u53EA\u558A\u4E86\u4E00\u58F0\uFF0C\u4FBF\u6525\u7D27\u62F3\u5934\uFF0C\u50CF\u4E00\u5934\u5C0F\u8C79\u5B50\u822C\u84C4\u6EE1\u529B\u6C14\u3002</p><p>\u6155\u5BB9\u9F99\u8E72\u4E0B\u8EAB\uFF0C\u62CD\u62CD\u4ED6\u7684\u8138\u86CB\uFF0C\u7B11\u9053\uFF1A\u300C\u521A\u624D\u7684\u6E38\u620F\u597D\u4E0D\u597D\u73A9\u554A\uFF1F\u300D</p><p>\u9F99\u6714\u5C0F\u8138\u4E00\u7EA2\uFF0C\u7A81\u7136\u5C48\u819D\uFF0C\u95EA\u7535\u822C\u671D\u4ED6\u988C\u4E0B\u51FB\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u65E9\u6709\u9632\u5907\uFF0C\u54C8\u54C8\u4E00\u7B11\u5C01\u4E86\u4ED6\u7684\u7A74\u9053\uFF0C\u63A5\u7740\u626F\u5F00\u4ED6\u7684\u8863\u88E4\uFF0C\u7528\u811A\u5C16\u62E8\u5F04\u7740\u4ED6\u7684\u5C0F\u9E21\u9E21\uFF0C\u7B11\u9053\uFF1A\u300C\u65E2\u7136\u4F60\u5A18\u88AB\u6211\u4EEC\u64CD\u8FC7\u4E86\uFF0C\u6211\u5C31\u4E0D\u6740\u4F60\u2014\u2014\u4F46\u2026\u2026\u300D\u8BF4\u7740\u62AC\u8D77\u811A\u3002</p><p>\u67F1\u9876\u7684\u5C11\u5987\u75AF\u72C2\u5730\u53EB\u558A\u8D77\u6765\uFF0C\u60C5\u6025\u4E4B\u4E0B\uFF0C\u53EA\u662F\u5C16\u53EB\uFF0C\u5374\u8BF4\u4E0D\u51FA\u4E00\u53E5\u8BDD\u3002</p><p>\u90A3\u53EA\u811A\u5728\u7A7A\u4E2D\u4E00\u987F\uFF0C\u63A5\u7740\u500F\u5FFD\u843D\u4E0B\uFF0C\u76F4\u76F4\u8E29\u5728\u5B69\u5B50\u80EF\u95F4\uFF0C\u53D1\u51FA\u300C\u556A\u53FD\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\u3002\u9F99\u6714\u8116\u5B50\u4E00\u62AC\uFF0C\u5589\u4E2D\u53D1\u51FA\u4E00\u58F0\u4F4E\u53EB\uFF0C\u63A5\u7740\u4E24\u773C\u7FFB\u767D\uFF0C\u987F\u65F6\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u541F\u541F\u62AC\u8D77\u811A\uFF0C\u9F99\u6714\u80EF\u95F4\u9634\u830E\u4E0E\u777E\u4E38\u5DF2\u7ECF\u53D8\u6210\u4E00\u7247\u6241\u6241\u7684\u8840\u8089\uFF0C\u8FDE\u5728\u4E00\u8D77\u5206\u4E0D\u6E05\u695A\u3002</p><p>\u5510\u989C\u773C\u524D\u4E00\u9ED1\uFF0C\u4E5F\u660F\u4E86\u8FC7\u53BB\u3002</p><p>\u8F66\u961F\u518D\u6B21\u542F\u52A8\uFF0C\u6620\u7740\u5915\u9633\u671D\u4E1C\u884C\u8FDB\u3002\u8F66\u961F\u540E\u9762\uFF0C\u7559\u4E0B\u4E00\u6839\u6728\u67F1\u548C\u5782\u6B7B\u6BCD\u5B50\u3002</p><p>\u5B69\u5B50\u8EBA\u5728\u67F1\u65C1\uFF0C\u4E0B\u8EAB\u8840\u8089\u6A21\u7CCA\u3002\u5373\u4F7F\u4ED6\u80FD\u9192\u6765\uFF0C\u88AB\u5C01\u7684\u7A74\u9053\u4E5F\u8981\u5341\u4E8C\u4E2A\u65F6\u8FB0\u624D\u80FD\u89E3\u5F00\u3002</p><p>\u5728\u4ED6\u5934\u9876\uFF0C\u6BCD\u4EB2\u7684\u8EAB\u4F53\u4F9D\u7136\u767D\u5AE9\u800C\u4F18\u7F8E\uFF0C\u4F46\u96EA\u767D\u7684\u53CC\u817F\u95F4\uFF0C\u5374\u662F\u4E00\u6839\u6DF1\u5165\u8179\u8154\u7684\u6F06\u9ED1\u6728\u67F1\u3002\u67F1\u8EAB\u5C06\u8089\u7A74\u6491\u5F97\u6D51\u5706\uFF0C\u90A3\u4E9B\u66FE\u7ECF\u67D4\u7F8E\u52A8\u4EBA\u7684\u82B1\u74E3\u5DF2\u7ECF\u5C3D\u6570\u88AB\u5377\u5165\u4F53\u5185\uFF0C\u53EA\u5269\u4E0B\u767D\u767D\u7684\u9634\u961C\u3002</p><p>\u5A07\u8EAF\u9AD8\u9AD8\u6311\u5728\u67F1\u9876\uFF0C\u6CBF\u7740\u88AB\u9C9C\u8840\u6E7F\u6DA6\u7684\u67F1\u8EAB\u6E10\u6E10\u4E0B\u6C89\u3002\u7528\u4E0D\u4E86\u591A\u4E45\uFF0C\u67F1\u5C16\u5C31\u4F1A\u7A7F\u7834\u5B50\u5BAB\uFF0C\u7136\u540E\u6216\u8005\u4E00\u5929\uFF0C\u6216\u8005\u4E24\u5929\uFF0C\u7F13\u6162\u4F46\u7EDD\u4E0D\u505C\u987F\u5730\u4E00\u8DEF\u523A\u5230\u5589\u5934\u3002\u800C\u5C11\u5987\u5C31\u53EA\u80FD\u8FD9\u6837\u7B49\u5F85\u6B7B\u4EA1\u7F13\u6162\u7684\u6765\u4E34\u3002</p><p>\u4E30\u6EE1\u7684\u7389\u4E73\u4E0A\uFF0C\u5206\u522B\u662F\u4E24\u884C\u5B57\u300C\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u300D\uFF0C\u300C\u661F\u6708\u6E56\u6DEB\u5974\u5510\u989C\u3002\u300D</p><p>\u58A8\u8FF9\u6DF1\u5165\u96EA\u767D\u7684\u808C\u80A4\uFF0C\u5206\u660E\u662F\u523A\u4E0A\u7684\u5B57\u8FF9\u3002</p><p>\u4E5F\u8BB8\u4F1A\u6709\u4EBA\u8DEF\u8FC7\u6B64\u5904\uFF0C\u5C06\u767E\u6218\u5929\u9F99\u59BB\u513F\u7684\u4E0B\u843D\u4F20\u81F3\u4E2D\u539F\uFF0C\u4E5F\u8BB8\u6C38\u8FDC\u4E5F\u4E0D\u4F1A\u6709\u4EBA\u7ECF\u8FC7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u6536\u62FE\u4E86\u8F66\u5185\u7684\u7269\u54C1\uFF0C\u5305\u6210\u4E00\u56E2\uFF0C\u521A\u4ECE\u7A97\u53E3\u6254\u5230\u8F66\u5916\uFF0C\u6155\u5BB9\u9F99\u5C31\u95EA\u8EAB\u5165\u5185\u3002</p><p>\u5979\u64A9\u4E86\u64A9\u88AB\u665A\u98CE\u5439\u4E71\u7684\u79C0\u53D1\uFF0C\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\uFF0C\u300C\u8FD8\u6709\u591A\u4E45\u624D\u80FD\u5230\u9F99\u57CE\uFF1F\u300D</p><p>\u300C\u5FEB\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u8BF4\u7740\u5F20\u5F00\u53CC\u81C2\u3002</p><p>\u7D2B\u73AB\u5DF2\u7ECF\u6709\u4E86\u4E09\u4E2A\u6708\u7684\u8EAB\u5B55\uFF0C\u5FAE\u9686\u7684\u5C0F\u8179\u4F7F\u5979\u65E0\u6CD5\u8F7B\u6613\u8E72\u4E0B\uFF0C\u53EA\u597D\u8DEA\u5728\u5730\u4E0A\u89E3\u5F00\u6155\u5BB9\u9F99\u7684\u8170\u5E26\uFF0C\u8131\u53BB\u52B2\u88C5\u80E1\u670D\uFF0C\u6362\u4E0A\u4E00\u4EF6\u8F7B\u4FBF\u7684\u8584\u886B\u3002</p><p>\u6155\u5BB9\u9F99\u9760\u5728\u6905\u4E2D\uFF0C\u534A\u772F\u7740\u773C\u4EAB\u53D7\u5A07\u59BB\u7684\u670D\u4F8D\u3002</p><p>\u7D2B\u73AB\u4E00\u8FB9\u7ED9\u4ED6\u68B3\u5934\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u8DEF\u4E0A\u98A0\u7C38\u592A\u5389\u5BB3\u4E86\uFF0C\u6211\u6015\u5A18\u53D7\u4E0D\u4E86\uFF0C\u80FD\u4E0D\u80FD\u4F11\u606F\u51E0\u5929\uFF1F\u300D</p><p>\u300C\u5662\uFF1F\u300D\u6155\u5BB9\u9F99\u7741\u5F00\u773C\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u7D2F\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u7740\u8138\u4F4E\u58F0\u8BF4\uFF1A\u300C\u54E5\u54E5\uFF0C\u5A18\u4E0D\u7D2F\u2026\u2026\u300D</p><p>\u79BB\u5F00\u6D1B\u9633\u4E4B\u540E\uFF0C\u8427\u4F5B\u5974\u5BF9\u6155\u5BB9\u9F99\u7684\u79F0\u547C\u4FBF\u662F\u300C\u54E5\u54E5\u300D\u3002\u6BCF\u6B21\u8FD9\u6837\u558A\uFF0C\u5979\u4FBF\u50CF\u56DE\u5230\u5F88\u4E45\u4EE5\uFF0C\u81EA\u5DF1\u8FD8\u662F\u71D5\u5BAB\u53D7\u5C3D\u5BA0\u7231\u7684\u5C0F\u7687\u5983\uFF0C\u53EA\u7528\u5A07\u602F\u602F\u504E\u4F9D\u5728\u541B\u738B\u6000\u91CC\u4FBF\u662F\u4E00\u751F\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u5927\u7B11\u3002\u7D2B\u73AB\u7528\u68B3\u5B50\u671D\u4ED6\u80A9\u5934\u4E00\u6253\uFF0C\u300C\u4F60\u4E0D\u5FC3\u75BC\u5A18\uFF0C\u4E5F\u8981\u5FC3\u75BC\u5A18\u809A\u5B50\u91CC\u7684\u5B69\u5B50\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u5F97\u66F4\u5F00\u5FC3\u4E86\uFF0C\u4ED6\u5C55\u81C2\u5C06\u81EA\u5DF1\u7684\u5A07\u59BB\u7F8E\u59BE\u62B1\u5728\u6000\u4E2D\uFF0C\u8212\u8212\u670D\u670D\u5730\u4F38\u4E2A\u61D2\u8170\uFF0C\u300C\u90A3\u5C31\u6162\u4E00\u4E9B\uFF0C\u6BCF\u5929\u591A\u4F11\u606F\u4E00\u4E2A\u65F6\u8FB0\u3002\u6709\u7A7A\u513F\u6211\u5C31\u5E26\u4F60\u4EEC\u53BB\u8349\u539F\u4E2D\u6253\u730E\uFF0C\u6563\u6563\u5FC3\u3002\u300D\u8BF4\u7740\u8BDD\u98CE\u4E00\u8F6C\uFF0C\u300C\u90A3\u5B9D\u85CF\u5728\u9F99\u57CE\u4EC0\u4E48\u5730\u65B9\uFF1F\u6155\u5BB9\u536B\u90A3\u8001\u5934\u5B50\u600E\u4E48\u8BF4\u7684\uFF1F\u300D</p><p>\u7D2B\u73AB\u4E4B\u6240\u4EE5\u627E\u501F\u53E3\u62D6\u5EF6\u65F6\u95F4\uFF0C\u5176\u5B9E\u5C31\u662F\u6015\u627E\u4E0D\u5230\u5B9D\u85CF\u60F9\u4ED6\u66B4\u6012\u3002\u4E00\u8DEF\u4E0A\u6155\u5BB9\u9F99\u5DF2\u7ECF\u95EE\u8FC7\u591A\u6B21\uFF0C\u6BCF\u6B21\u8BE2\u95EE\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u90FD\u4E0D\u7531\u4E00\u7D27\u3002\u5979\u786C\u7740\u5934\u76AE\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u544A\u8BC9\u4F60\u4E00\u5343\u904D\u90FD\u6709\u5566\uFF0C\u7239\u7239\u2014\u2014\u6155\u5BB9\u536B\u4E34\u7EC8\u524D\u53EA\u8BF4\u4E86\u4E24\u53E5\u534A\u7684\u8BDD\uFF1A\u9F99\u57CE\u4EE5\u897F\uFF0C\u4E91\u96FE\u5C71\u7B2C\u4E8C\u5EA7\u5C71\u5CAD\u4E0B\uFF0C\u4E03\u91CC\u2026\u2026\u5476\uFF0C\u5C31\u8FD9\u6837\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u4ED6\u6015\u8FD9\u4E2A\u5C0F\u4E2B\u5934\u9A97\u4ED6\uFF0C\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\u5C31\u4F1A\u51B7\u4E0D\u9632\u95EE\u4E00\u6B21\uFF0C\u770B\u5979\u8BF4\u7684\u524D\u540E\u6709\u6CA1\u6709\u77DB\u76FE\u3002\u4F46\u4ECE\u7B2C\u4E00\u6B21\u5230\u73B0\u5728\uFF0C\u8FD9\u4E2B\u5934\u59CB\u7EC8\u8BF4\u7684\u4E1D\u6BEB\u4E0D\u5DEE\uFF0C\u770B\u6765\u662F\u771F\u7684\u3002</p><p>\u8D77\u5B9D\u85CF\u4E4B\u540E\uFF0C\u8D81\u9F99\u57CE\u4E00\u5E26\u8352\u65E0\u4EBA\u70DF\uFF0C\u795E\u4E0D\u77E5\u9B3C\u4E0D\u89C9\u5730\u5EFA\u7ACB\u4E00\u652F\u7CBE\u5175\uFF0C\u5F85\u673A\u800C\u52A8\u3002</p><p>\u6155\u5BB9\u9F99\u95ED\u76EE\u76D8\u7B97\uFF1A\u4ECE\u8FD9\u4E00\u8DEF\u4E0A\u6240\u89C1\u6240\u95FB\u770B\u6765\uFF0C\u5468\u56FD\u4E5F\u662F\u5F92\u6709\u5176\u8868\u3002\u59DA\u5174\u91CD\u519C\u6291\u7267\uFF0C\u867D\u6BD4\u5176\u4ED6\u51E0\u56FD\u6BB7\u5B9E\uFF0C\u4F46\u9A91\u5175\u76F8\u5E94\u7F3A\u4E4F\uFF0C\u4E0D\u5F97\u4E0D\u4E0E\u67D4\u7136\u8054\u76DF\uFF0C\u6C42\u8D2D\u9A6C\u5339\u3002</p><p>\u82E5\u80FD\u52A9\u5EFA\u4E00\u652F\u7684\u7CBE\u9510\u9A91\u5175\uFF0C\u731D\u4E0D\u53CA\u9632\u4E0B\u7ED5\u8FC7\u6E14\u9633\u76F4\u903C\u9EC4\u6CB3\uFF0C\u7136\u540E\u5C5E\u4E0B\u5404\u5E2E\u56DB\u5904\u8D77\u4E8B\uFF0C\u5468\u56FD\u5B9A\u7136\u5927\u4E71\u3002\u79E6\u5B8B\u7B49\u56FD\u81EA\u987E\u4E0D\u6687\uFF0C\u672A\u5FC5\u6562\u7ACB\u5373\u8FDB\u653B\uFF0C\u7B49\u6211\u653B\u9677\u6D1B\u9633\uFF0C\u7A33\u4F4F\u5927\u5C40\uFF0C\u4ED6\u4EEC\u5C31\u662F\u60F3\u6765\uFF0C\u4E5F\u518D\u65E0\u4E1D\u6BEB\u673A\u4F1A\uFF01</p><p>\u6155\u5BB9\u9F99\u6362\u4E86\u4E2A\u59FF\u52BF\u3002\u8FD9\u652F\u9A91\u5175\u6700\u5C11\u8981\u6709\u4E03\u5343\uFF0C\u5728\u9F99\u57CE\u867D\u7136\u9690\u853D\uFF0C\u4F46\u8865\u7ED9\u4F9B\u5E94\u4E07\u5206\u9EBB\u70E6\u3002\u5403\u7A7F\u7528\u5EA6\u4EE5\u5916\uFF0C\u8FD8\u8981\u6709\u79CD\u79CD\u529E\u6CD5\u7A33\u5B9A\u519B\u5FC3\u3002\u8FD9\u7B14\u5F00\u652F\u2026\u2026\u5B9D\u85CF\u7A76\u7ADF\u6709\u591A\u5C11\u91D1\u94F6\uFF1F</p><p>\u300C\u8D77\u6765\u5566\u2026\u2026\u300D\u5927\u8F66\u505C\u4E0B\uFF0C\u5E2E\u4F17\u5F00\u59CB\u751F\u706B\u505A\u996D\u3002\u7D2B\u73AB\u63A8\u5F00\u6155\u5BB9\u9F99\u7684\u624B\u81C2\uFF0C\u5750\u8D77\u6765\u62C9\u5E73\u538B\u76B1\u7684\u8863\u670D\u3002</p><p>\u6155\u5BB9\u9F99\u652F\u7740\u4E0B\u5DF4\uFF0C\u5165\u8FF7\u7684\u770B\u7740\u59B9\u59B9\u3002\u7389\u4EBA\u4E00\u4E3E\u624B\u4E00\u6295\u8DB3\u65E0\u4E0D\u5E26\u7740\u64A9\u4EBA\u7684\u98CE\u60C5\uFF0C\u5355\u662F\u79C0\u53D1\u95F4\u9732\u51FA\u7684\u4E00\u70B9\u7389\u767D\u7684\u8033\u8F6E\uFF0C\u4FBF\u8BA9\u4EBA\u546F\u7136\u5FC3\u52A8\uFF0C\u679C\u7136\u662F\u5929\u751F\u5C24\u7269\u3002\u76EE\u5149\u843D\u5728\u5FAE\u5FAE\u53D8\u7C97\u7684\u8170\u80A2\u4E0A\uFF0C\u6155\u5BB9\u9F99\u6697\u9053\uFF1A\u300C\u5B69\u5B50\u90FD\u6709\u4E86\uFF0C\u5979\u4E5F\u8BE5\u6536\u4F4F\u5FC3\u601D\uFF0C\u4E56\u4E56\u505A\u6211\u7684\u5C0F\u59BB\u5B50\u4E86\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u5728\u4ED6\u81C2\u95F4\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u53C8\u53D1\u51FA\u9999\u751C\u7684\u9F3E\u58F0\uFF0C\u539F\u6765\u5DF2\u7ECF\u7761\u5F97\u719F\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u62E8\u5F00\u5979\u8138\u4E0A\u7684\u53D1\u4E1D\u3002\u7F8E\u5987\u6D77\u68E0\u822C\u7684\u9762\u5BB9\uFF0C\u4F7F\u4ED6\u5FCD\u4E0D\u4F4F\u4FEF\u8EAB\uFF0C\u543B\u4F4F\u5A07\u8273\u800C\u53C8\u82AC\u82B3\u7684\u5507\u74E3\u3002</p><p>\u8427\u4F5B\u5974\u4ECE\u7761\u68A6\u4E2D\u60CA\u9192\uFF0C\u661F\u7738\u6726\u80E7\u4E2D\u95FB\u51FA\u6155\u5BB9\u9F99\u7684\u6C14\u606F\uFF0C\u4FBF\u5A07\u7F9E\u5730\u5410\u51FA\u9999\u820C\uFF0C\u4EFB\u4ED6\u91C7\u64B7\u3002</p><p>\u6155\u5BB9\u9F99\u9971\u543B\u4E00\u756A\uFF0C\u604B\u604B\u4E0D\u820D\u5730\u62AC\u8D77\u5934\uFF0C\u4E00\u628A\u62C9\u4F4F\u7D2B\u73AB\uFF0C\u300C\u628A\u8863\u670D\u8131\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u6C14\u607C\u5730\u8BF4\uFF1A\u300C\u600E\u4E48\u8FD9\u4E48\u70E6\u54EA\uFF0C\u4EBA\u5BB6\u521A\u6574\u7406\u597D\u2026\u2026\u300D\u8BDD\u672A\u8BF4\u5B8C\u5C31\u88AB\u6155\u5BB9\u9F99\u6402\u7740\u5A07\u8EAF\uFF0C\u653E\u5728\u8427\u4F5B\u5974\u8EAB\u4FA7\u3002\u5979\u6CA1\u597D\u6C14\u5730\u677E\u5F00\u8863\u5E26\uFF0C\u89E3\u4E0B\u8F7B\u886B\u3002</p><p>\u6155\u5BB9\u9F99\u5C06\u8427\u4F5B\u5974\u7684\u8863\u6263\u4E00\u9897\u9897\u89E3\u5F00\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u4EEC\u4ECA\u5929\u600E\u4E48\u4F3A\u5019\u592B\u541B\u554A\uFF1F\u300D</p><p>\u7D2B\u73AB\u7529\u5F00\u5C0F\u8863\uFF0C\u677F\u7740\u8138\u8BF4\uFF1A\u300C\u592B\u541B\u5927\u4EBA\u5728\u4E0A\uFF0C\u5C0F\u5973\u5B50\u6709\u5B55\u5728\u8EAB\uFF0C\u8FD8\u6C42\u592B\u541B\u5782\u601C\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u563B\u563B\u5265\u5F00\u82B1\u74E3\uFF0C\u637B\u4F4F\u82B1\u8482\uFF0C\u9017\u5F97\u5979\u82B1\u679D\u4E71\u98A4\uFF0C\u5A07\u547C\u8FDE\u58F0\uFF0C\u624D\u677E\u5F00\u624B\uFF0C\u5708\u4F4F\u8427\u4F5B\u5974\u7684\u67D4\u9888\u9053\uFF1A\u300C\u5A18\uFF0C\u8BA9\u5B69\u513F\u64CD\u4F60\u54EA\u4E2A\u6D1E\u5462\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u7F9E\u6DA9\u5730\u4F4E\u58F0\u9053\uFF1A\u300C\u540E\u9762\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5927\u7B11\u7740\u5C06\u7F8E\u5987\u7FFB\u8F6C\u8FC7\u6765\u3002\u80A5\u767D\u7684\u96EA\u81C0\u6ED1\u5AE9\u5F02\u5E38\uFF0C\u4F3C\u4E4E\u9971\u542B\u7740\u82AC\u82B3\u7684\u8309\u8389\u82B1\u6CB9\u3002\u6155\u5BB9\u9F99\u63B0\u5F00\u5706\u81C0\uFF0C\u53EA\u89C1\u81C0\u7F1D\u5185\uFF0C\u7EA2\u5AE9\u7684\u809B\u7AA6\u5706\u5706\u9F13\u8D77\uFF0C\u5E26\u7740\u8FF7\u4EBA\u7684\u5149\u6CFD\uFF0C\u50CF\u4E00\u5F20\u5C0F\u5DE7\u7CBE\u81F4\u7684\u5634\u5DF4\uFF0C\u6B63\u561F\u8D77\u7EA2\u5507\uFF0C\u987D\u76AE\u800C\u53C8\u53EF\u7231\u3002\u6BCF\u4E00\u6761\u76B1\u7EB9\u90FD\u53C8\u7EC6\u53C8\u6DF1\uFF0C\u6E05\u6670\u53EF\u8FA8\u3002</p><p>\u8089\u68D2\u987A\u7740\u96EA\u767D\u7684\u81C0\u7F1D\u5185\u4E0A\u4E0B\u6311\u5F04\uFF0C\u809B\u8089\u88AB\u6324\u5F97\u4E00\u5F00\u4E00\u5408\uFF0C\u8427\u4F5B\u5974\u987F\u65F6\u5A07\u5598\u7740\u6218\u6817\u8D77\u6765\u3002\u6311\u5F04\u7247\u523B\u540E\uFF0C\u9F9F\u5934\u9876\u4F4F\u5AE9\u8089\u6B63\u4E2D\uFF0C\u7565\u4E00\u4F7F\u529B\uFF0C\u4FBF\u6CA1\u5165\u809B\u6D1E\u3002\u7F8E\u5987\u54AC\u4F4F\u7EA2\u5507\uFF0C\u53CC\u76EE\u7D27\u95ED\uFF0C\u5634\u4E2D\u53D1\u51FA\u4F3C\u53F9\u4F3C\u559C\u7684\u67D4\u5A9A\u58F0\u97F3\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u9633\u5177\u52A0\u529B\u63D2\u5165\u3002\u8427\u4F5B\u5974\u4E00\u58F0\u6D6A\u53EB\uFF0C\u6C34\u5AE9\u6ED1\u817B\u7684\u7684\u83CA\u809B\u50CF\u88AB\u8089\u68D2\u6324\u51FA\u6CB9\u8102\u4E00\u822C\uFF0C\u6E17\u51FA\u5927\u91CF\u871C\u6C41\u3002\u871C\u6C41\u968F\u7740\u5DE8\u9633\u7684\u8FDB\u5165\uFF0C\u53FD\u53FD\u4F5C\u54CD\u5730\u6EA2\u51FA\u809B\u6D1E\uFF0C\u8D8A\u8FC7\u6324\u6210\u4E00\u9053\u7EC6\u7EC6\u8273\u7EA2\u7684\u5AE9\u8089\uFF0C\u56DB\u4E0B\u6E85\u843D\u3002</p><p>\u5982\u6B64\u80A5\u7F8E\u591A\u6C41\u7684\u5999\u81C0\uFF0C\u53EF\u8C13\u4E3E\u4E16\u65E0\u53CC\u3002</p>',65);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
