import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_94" tabindex="-1"><a class="header-anchor" href="#_94" aria-hidden="true">#</a> 94</h1><p>\u5F53\u65E5\u7528\u8FC7\u711A\u60C5\u818F\u540E\uFF0C\u6155\u5BB9\u9F99\u5E76\u672A\u5C31\u6B64\u7F62\u624B\uFF0C\u65E0\u8BBA\u662F\u8309\u8389\u82B1\u6CB9\uFF0C\u8FD8\u662F\u5C3F\u5E03\u4E2D\uFF0C\u90FD\u542B\u6709\u5C11\u91CF\u7684\u711A\u60C5\u818F\u3002\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\uFF0C\u8FD8\u501F\u6E05\u7406\u80A0\u9053\u4E4B\u673A\uFF0C\u8BA9\u767D\u6C0F\u59D0\u59B9\u5F80\u8427\u4F5B\u5974\u809B\u5185\u6D82\u5165\u5927\u91CF\u711A\u60C5\u818F\u3002</p><p>\u711A\u60C5\u818F\u5947\u6548\u60CA\u4EBA\uFF0C\u6B64\u65F6\u8427\u4F5B\u5974\u540E\u5EAD\u5DF2\u88AB\u5B8C\u5168\u6539\u9020\uFF0C\u4E0D\u4EC5\u654F\u611F\u5F02\u5E38\uFF0C\u800C\u4E14\u8FD8\u4F1A\u5728\u4EA4\u5408\u4E2D\u6E17\u51FA\u7C7B\u4F3C\u6DEB\u6DB2\u7684\u871C\u6C41\u3002\u4E0D\u5FC5\u518D\u7528\u4ED6\u7269\u6DA6\u6ED1\u5373\u53EF\u8BA9\u6155\u5BB9\u9F99\u8FD9\u7B49\u5DE8\u7269\u6DF1\u5165\u5176\u4E2D\u3002</p><p>\u8089\u68D2\u8FDB\u5165\u8FD9\u4E2A\u4E16\u95F4\u72EC\u4E00\u65E0\u4E8C\u7684\u7EDD\u7F8E\u83CA\u809B\uFF0C\u67D4\u8F6F\u7684\u80A0\u58C1\u7ACB\u523B\u9965\u6E34\u5730\u7F20\u4F4F\u68D2\u8EAB\uFF0C\u8815\u52A8\u4E0D\u5DF2\u3002\u6155\u5BB9\u9F99\u6015\u538B\u574F\u8427\u4F5B\u5974\u809A\u91CC\u7684\u5B69\u5B50\uFF0C\u4FBF\u7528\u53CC\u624B\u6258\u7740\u5979\u7684\u8170\u80EF\uFF0C\u5C06\u83CA\u809B\u5BF9\u51C6\u8089\u68D2\uFF0C\u62B1\u7740\u80A5\u7F8E\u7684\u7389\u81C0\u6025\u901F\u62BD\u9001\u3002</p><p>\u8427\u4F5B\u5974\u5C0F\u5634\u534A\u5F20\uFF0C\u5F2F\u7709\u62E7\u7D27\uFF0C\u633A\u7740\u5706\u81C0\u4E00\u52A8\u4E0D\u52A8\u5730\u4EFB\u4ED6\u72C2\u62BD\u731B\u9001\u3002\u4E0D\u591A\u65F6\uFF0C\u5979\u5A07\u8EAF\u4E00\u7D27\uFF0C\u8089\u7A74\u98A4\u6296\u7740\u55B7\u51FA\u80A1\u80A1\u9634\u7CBE\u3002\u73B0\u5728\u5979\u5DF2\u7ECF\u4E60\u60EF\u4E86\u7531\u809B\u4EA4\u83B7\u5F97\u9AD8\u6F6E\uFF0C\u6B63\u5E38\u7684\u6027\u4EA4\u53CD\u800C\u4E0D\u53CA\u540E\u5EAD\u7F8E\u5999\u3002</p><p>\u6155\u5BB9\u9F99\u677E\u5F00\u5931\u795E\u7684\u7F8E\u5987\uFF0C\u300C\u5575\u300D\u7684\u62D4\u51FA\u8089\u68D2\u3002</p><p>\u68D2\u8EAB\u6D82\u7740\u4E00\u5C42\u6CB9\u8102\u822C\u7684\u871C\u6C41\uFF0C\u6BCF\u4E00\u9897\u7A81\u8D77\u90FD\u95EA\u95EA\u53D1\u4EAE\uFF0C\u4EFF\u4F5B\u4E00\u6839\u72F0\u72DE\u7684\u5175\u5668\u95EA\u52A8\u5BD2\u5149\u3002</p><p>\u7D2B\u73AB\u88AB\u4ED6\u521A\u624D\u4E00\u9635\u6311\u9017\uFF0C\u79D8\u5904\u5DF2\u7ECF\u6E7F\u6DA6\uFF0C\u4E8E\u662F\u5206\u5F00\u7389\u817F\uFF0C\u4E24\u624B\u6309\u4F4F\u7C89\u7EA2\u7684\u82B1\u74E3\u8FB9\u7F18\u67D4\u67D4\u7EFD\u5F00\u3002</p><p>\u6155\u5BB9\u9F99\u652F\u8D77\u8EAB\u4F53\uFF0C\u628A\u5A07\u5C0F\u7684\u7389\u4EBA\u7B3C\u7F69\u81EA\u5DF1\u7684\u9634\u5F71\u4E4B\u4E0B\uFF0C\u51DD\u89C6\u7740\u7D2B\u73AB\u542B\u7F9E\u5E26\u559C\u7684\u59A9\u5A9A\u795E\u60C5\u3002</p><p>\u7D2B\u73AB\u88AB\u4ED6\u770B\u5F97\u7F9E\u6DA9\u8D77\u6765\uFF0C\u626D\u5934\u907F\u5F00\u4ED6\u706B\u8FA3\u8FA3\u7684\u773C\u795E\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u4F60\u8FD8\u4E0D\u8FDB\u6765\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9732\u51FA\u4E00\u4E2A\u9633\u5149\u822C\u7684\u52A8\u4EBA\u7B11\u5BB9\uFF0C\u9633\u5177\u7F13\u7F13\u8FDB\u5165\u59B9\u59B9\u4F53\u5185\u3002</p><p>\u706B\u70ED\u7684\u8089\u68D2\u6E29\u5B58\u5730\u8FDB\u5165\u8EAB\u4F53\u91CC\u9762\uFF0C\u6491\u6EE1\u6574\u4E2A\u8089\u7A74\uFF0C\u7D2B\u73AB\u8138\u8272\u5FAE\u7EA2\uFF0C\u547C\u5438\u4E5F\u53D8\u5F97\u65AD\u65AD\u7EED\u7EED\u3002\u7B49\u8089\u523A\u6CA1\u5165\u5AE9\u8089\uFF0C\u9633\u5177\u731B\u7136\u4E00\u633A\uFF0C\u9876\u4F4F\u82B1\u5FC3\u3002</p><p>\u7D2B\u73AB\u4F4E\u53EB\u4E00\u58F0\uFF0C\u8EAB\u5B50\u50CF\u88AB\u70B9\u71C3\u822C\u77AC\u65F6\u70ED\u4E86\u8D77\u6765\uFF0C\u5FC3\u91CC\u4E0D\u671F\u7136\u60F3\u5230\uFF1A\u5047\u5982\u4ED6\u4E0D\u662F\u81EA\u5DF1\u4EB2\u54E5\u54E5\uFF0C\u90A3\u8BE5\u591A\u597D\u2026\u2026\u65CB\u5373\u5E08\u4EC7\u5BB6\u6068\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u5C11\u5973\u6697\u6697\u54AC\u7D27\u7259\u5173\u3002</p><p>\u300C\u75BC\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u770B\u51FA\u5979\u7684\u5F02\u6837\uFF0C\u8FDE\u5FD9\u505C\u4F4F\u52A8\u4F5C\u3002</p><p>\u300C\u2026\u2026\u6709\u4E00\u70B9\u2026\u2026\u300D\u7D2B\u73AB\u8F7B\u58F0\u8BF4\u3002</p><p>\u8089\u68D2\u7684\u62BD\u9001\u52A0\u500D\u6E29\u67D4\uFF0C\u7D2B\u73AB\u89C9\u5F97\u81EA\u5DF1\u50CF\u8EBA\u5728\u6E29\u6696\u7684\u6CE2\u6D9B\u4E0A\uFF0C\u968F\u7740\u6F6E\u6C34\u7684\u8D77\u843D\uFF0C\u7F13\u7F13\u8D77\u4F0F\u3002\u6D6A\u5934\u4E0D\u4F4F\u6D8C\u6765\uFF0C\u8EAB\u4F53\u4E5F\u4E00\u8361\u4E00\u8361\uFF0C\u878D\u5316\u822C\u8D8A\u98D8\u8D8A\u8FDC\u3002\u5076\u7136\u6709\u51E0\u6735\u6D6A\u82B1\u6E85\u8D77\uFF0C\u6253\u6E7F\u4E86\u81EA\u5DF1\u8D64\u88F8\u7684\u808C\u80A4\u2026\u2026</p><p>\u5979\u7741\u773C\u4E00\u770B\uFF0C\u8138\u4E0A\u987F\u65F6\u7EA2\u4E86\u3002\u4E0B\u4F53\u6C34\u6DCB\u6DCB\u53C8\u6E7F\u53C8\u6ED1\uFF0C\u4ECE\u80A1\u95F4\u5230\u5927\u817F\u5185\u4FA7\uFF0C\u5C3D\u662F\u81EA\u5DF1\u7684\u6DEB\u6DB2\u3002</p><p>\u6155\u5BB9\u9F99\u52A8\u4F5C\u9661\u7136\u52A0\u5FEB\uFF0C\u8089\u68D2\u8FDB\u51FA\u95F4\u6DEB\u6DB2\u56DB\u6EA2\u3002\u7D2B\u73AB\u4E24\u624B\u6342\u5728\u5634\u4E0A\uFF0C\u4F4E\u53EB\u4E0D\u7EDD\u3002</p><p>\u6676\u83B9\u7684\u9165\u4E73\u524D\u540E\u629B\u52A8\uFF0C\u6643\u51FA\u4E00\u7247\u7C89\u5149\u3002</p><p>\u6155\u5BB9\u9F99\u89C1\u7D2B\u73AB\u7389\u4F53\u5C3D\u6210\u7C89\u5AE9\u7684\u67D4\u7EA2\uFF0C\u77E5\u9053\u5979\u9AD8\u6F6E\u5C06\u81F3\uFF0C\u9633\u5177\u6839\u90E8\u4E00\u6839\u7EC6\u957F\u7684\u89E6\u624B\u7A81\u7136\u6311\u8D77\uFF0C\u76F4\u76F4\u94BB\u5165\u8089\u7A74\u4E0A\u65B9\u7684\u5C0F\u5B54\u5185\u3002</p><p>\u7D2B\u73AB\u4E00\u58F0\u60CA\u547C\uFF0C\u8FD8\u6CA1\u53CD\u5E94\u8FC7\u6765\uFF0C\u89E6\u624B\u5DF2\u4E00\u6345\u5230\u5E95\uFF0C\u65CB\u5373\u62D4\u4E86\u51FA\u6765\uFF0C\u8089\u68D2\u5374\u8FD8\u9876\u4F4F\u82B1\u5FC3\u4E0D\u4F4F\u8DF3\u52A8\u3002\u7D2B\u73AB\u4E0B\u4F53\u4E00\u9635\u75C9\u631B\uFF0C\u63A5\u7740\u4E0A\u4E0B\u4E24\u4E2A\u8089\u6D1E\u5185\u540C\u65F6\u55B7\u51FA\u6DB2\u4F53\u3002</p><p>\u6155\u5BB9\u9F99\u5C06\u9633\u7CBE\u5C3D\u6570\u5C04\u5728\u7D2B\u73AB\u4F53\u5185\uFF0C\u8FD9\u624D\u62D4\u51FA\u8089\u68D2\uFF0C\u7B11\u541F\u541F\u9053\uFF1A\u300C\u7ADF\u7136\u88AB\u54E5\u54E5\u5E72\u51FA\u5C3F\u6765\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u53C8\u7F9E\u53C8\u6C14\uFF0C\u6068\u6068\u8BF4\uFF1A\u300C\u4F60\u597D\u574F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u5374\u89C1\u8427\u4F5B\u5974\u81C0\u8089\u4E00\u9635\u6536\u7F29\uFF0C\u4E00\u80A1\u6DE1\u9EC4\u7684\u6C61\u7269\u6EA2\u4E86\u51FA\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5927\u7B11\u9053\uFF1A\u300C\u4E00\u4E2A\u88AB\u592B\u541B\u5E72\u51FA\u5C3F\u6765\uFF0C\u4E00\u4E2A\u88AB\u592B\u541B\u5E72\u51FA\u5C4E\u6765\uFF0C\u5A07\u59BB\u7231\u59BE\uFF0C\u4F60\u4EEC\u591F\u5FEB\u6D3B\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u7EA2\u7740\u8138\u64E6\u53BB\u4E0B\u4F53\u7684\u6DEB\u6C34\u5C3F\u6DB2\uFF0C\u6CA1\u6709\u7406\u4F1A\u4ED6\u3002\u8427\u4F5B\u5974\u65E0\u6CD5\u52A8\u4F5C\uFF0C\u53EA\u80FD\u7B49\u522B\u4EBA\u5E2E\u5979\u64E6\u51C0\uFF0C\u4E8E\u662F\u5C0F\u58F0\u6C42\u9053\uFF1A\u300C\u9F99\u513F\uFF0C\u7ED9\u5A18\u64E6\u64E6\u2026\u2026\u5C41\u80A1\u5427\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6293\u4F4F\u4E24\u534A\u80A5\u767D\u7684\u5706\u81C0\u4E00\u9635\u78E8\u64E6\u3002\u677E\u5F00\u624B\uFF0C\u96EA\u767D\u7684\u81C0\u8089\u7F13\u7F13\u5206\u5F00\uFF0C\u81C0\u7F1D\u95F4\u6CBE\u6EE1\u7C98\u4E4E\u4E4E\u7684\u6DE1\u9EC4\u6C61\u7269\u3002</p><p>\u8427\u4F5B\u5974\u6CA1\u60F3\u5230\u4ED6\u7ADF\u7136\u4F1A\u62FF\u90A3\u4E48\u810F\u7684\u4E1C\u897F\u73A9\u4E86\u8D77\u6765\uFF0C\u5FC3\u4E0B\u4E00\u6025\uFF0C\u51E0\u4E4E\u54ED\u4E86\u51FA\u6765\uFF0C\u300C\u9F99\u54E5\u54E5\uFF0C\u4F60\u5FEB\u7ED9\u4EBA\u5BB6\u64E6\u5E72\u51C0\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5E72\u8106\u5C31\u8FD9\u6837\u7528\u5C3F\u5E03\u5305\u4F4F\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u4E0D\u597D\u4E0D\u597D\u3002\u300D\u8427\u4F5B\u5974\u76B1\u7740\u7709\u5934\u6025\u5207\u5730\u8BF4\u9053\uFF0C\u300C\u810F\u516E\u516E\u7684\u597D\u6076\u5FC3\u2026\u2026\u9F99\u54E5\u54E5\u4F1A\u4E0D\u559C\u6B22\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u770B\u7740\u5979\u7684\u5A07\u6001\u5FC3\u82B1\u6012\u653E\uFF0C\u4F38\u624B\u6402\u8D77\u7F8E\u5987\u7684\u8170\u80A2\uFF0C\u5C06\u5979\u5C48\u819D\u653E\u7A33\uFF0C\u6446\u6210\u81C0\u90E8\u9AD8\u4E3E\u7684\u6A21\u6837\uFF0C\u7136\u540E\u7AD9\u5728\u5979\u8EAB\u540E\uFF0C\u63E1\u7740\u8089\u68D2\uFF0C\u4E00\u6CE1\u5C3F\u5C3D\u6570\u6492\u5728\u7F8E\u5987\u81C0\u95F4\u3002</p><p>\u5C3F\u6DB2\u51B2\u5F00\u6C61\u7269\uFF0C\u9732\u51FA\u767D\u5AE9\u7684\u808C\u80A4\u548C\u81C0\u7F1D\u4E2D\u8273\u7EA2\u7684\u809B\u7AA6\u3002\u6155\u5BB9\u9F99\u6B63\u73A9\u5F97\u9AD8\u5174\uFF0C\u5374\u542C\u5230\u4E00\u9635\u4F4E\u4F4E\u7684\u62BD\u6CE3\u58F0\uFF0C\u4ED6\u6536\u8D77\u9633\u5177\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u62BD\u54BD\u534A\u664C\uFF0C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u9F99\u54E5\u54E5\u2026\u2026\u8FD9\u6837\u7CDF\u8DF5\u5A18\u2026\u2026\u5A18\u597D\u96BE\u8FC7\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u53EA\u987E\u81EA\u5DF1\u9AD8\u5174\uFF0C\u5F04\u5F97\u5979\u8FD9\u4E48\u4F24\u5FC3\uFF0C\u4E0D\u7531\u5FC3\u75BC\u8D77\u6765\u3002\u4ED6\u628A\u8427\u4F5B\u5974\u62B1\u5728\u6000\u91CC\uFF0C\u4ED4\u7EC6\u5E2E\u5979\u64E6\u51C0\u4E0B\u4F53\uFF0C\u53C8\u67D4\u58F0\u5475\u54C4\u534A\u664C\uFF0C\u624D\u4F7F\u7F8E\u5987\u7834\u557C\u4E3A\u7B11\u3002</p><p>\u7D2B\u73AB\u7A7F\u597D\u8863\u670D\uFF0C\u62B1\u819D\u4F9D\u5728\u58C1\u89D2\uFF0C\u5FC3\u91CC\u4E00\u9635\u60B2\u51C9\u3002\u96BE\u9053\u50CF\u5A18\u4E00\u6837\uFF0C\u4E00\u8F88\u5B50\u90FD\u5F53\u4ED6\u7684\u73A9\u7269\u5417\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u516B\u6708\u4E2D\u65EC\uFF0C\u8DCB\u6D89\u6570\u5343\u91CC\u7684\u4E00\u884C\u4EBA\u7EC8\u4E8E\u6765\u5230\u5E73\u5DDE\u9F99\u57CE\u3002</p><p>\u8FD9\u91CC\u662F\u6155\u5BB9\u6C0F\u9F99\u5174\u4E4B\u5730\uFF0C\u66FE\u7ECF\u7E41\u534E\u4E00\u65F6\u3002\u4F46\u5341\u4F59\u5E74\u6765\u5468\u519B\u4E0E\u9AD8\u53E5\u4E3D\u52FE\u7ED3\uFF0C\u7D2F\u756A\u70E7\u6740\u5C60\u6233\uFF0C\u5C45\u6C11\u6216\u6B7B\u6216\u9003\uFF0C\u6570\u5343\u91CC\u5185\u8352\u65E0\u4EBA\u70DF\u3002\u6155\u5BB9\u9F99\u7B49\u4EBA\u8D70\u5165\u7684\uFF0C\u5C31\u662F\u8FD9\u5EA7\u4E86\u65E0\u4EBA\u8FF9\u7684\u8352\u57CE\u3002</p><p>\u57CE\u5899\u65E9\u5DF2\u88AB\u62C6\u6BC1\uFF0C\u623F\u820D\u4E5F\u8361\u7136\u65E0\u5B58\uFF0C\u53EA\u5269\u51E0\u6839\u70E7\u6B8B\u7684\u5DE8\u67F1\u534A\u63A9\u5728\u8352\u8349\u4E2D\uFF0C\u8BC9\u8BF4\u7740\u6614\u65E5\u7684\u8F89\u714C\u3002</p><p>\u8F66\u961F\u505C\u5728\u4E00\u5EA7\u5DE8\u5927\u7684\u77F3\u9636\u524D\u3002\u6155\u5BB9\u9F99\u8138\u4E0A\u51B7\u51B0\u51B0\u6CA1\u6709\u4E00\u70B9\u8868\u60C5\u3002\u6C89\u9ED8\u534A\u664C\uFF0C\u95EE\u9053\uFF1A\u300C\u6155\u5BB9\u6C0F\u7956\u9675\u5728\u54EA\u91CC\uFF1F\u300D</p><p>\u91D1\u5F00\u7532\u4E8C\u5341\u5E74\u524D\u66FE\u6765\u8FC7\u6B64\u5904\uFF0C\u5F53\u65F6\u6B63\u503C\u9F99\u57CE\u76DB\u65F6\uFF0C\u8C01\u80FD\u60F3\u5230\u5982\u4ECA\u7ADF\u4F1A\u8FD9\u822C\u8352\u51C9\u3002\u611F\u6168\u95F4\uFF0C\u4ED6\u626C\u97AD\u6307\u5411\u897F\u65B9\uFF0C\u300C\u5F80\u897F\u4E8C\u5341\u91CC\u4FBF\u662F\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u542C\u5230\u897F\u65B9\uFF0C\u8FDE\u5FD9\u62AC\u773C\u770B\u53BB\uFF0C\u53EA\u89C1\u6B8B\u7834\u7684\u74E6\u783E\u5916\u662F\u4E00\u9A6C\u5E73\u5DDD\u7684\u8349\u539F\uFF0C\u89C6\u91CE\u6240\u53CA\u83AB\u8BF4\u4E91\u96FE\u5C71\uFF0C\u8FDE\u4E00\u4E2A\u7565\u6709\u8D77\u4F0F\u7684\u4E18\u9675\u90FD\u6CA1\u6709\u3002\u4ED6\u4ECE\u9A6C\u4E0A\u626D\u5934\u56DB\u4E0B\u73AF\u987E\uFF0C\u7247\u523B\u95F4\u4FBF\u53EF\u4EE5\u80AF\u5B9A\uFF0C\u5468\u56F4\u6570\u5341\u91CC\u4E4B\u5185\u7EDD\u65E0\u4EFB\u4F55\u5C71\u5CF0\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u5934\u546F\u546F\u76F4\u8DF3\uFF0C\u4ED6\u7A33\u4F4F\u58F0\u97F3\uFF0C\u5E73\u9759\u5730\u5411\u91D1\u5F00\u7532\u95EE\u9053\uFF1A\u300C\u9F99\u57CE\u9644\u8FD1\u53EF\u6709\u4EC0\u4E48\u540D\u5C71\uFF1F\u300D</p><p>\u4F17\u4EBA\u76F8\u987E\u6447\u5934\uFF0C\u300C\u5C5E\u4E0B\u4E0D\u77E5\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u50CF\u88AB\u4EBA\u515C\u5934\u6D47\u4E86\u76C6\u51B7\u6C34\uFF0C\u4ED6\u5FC3\u6709\u4E0D\u7518\u5730\u671D\u7075\u7389\u95EE\u9053\uFF1A\u300C\u9053\u957F\u53EF\u77E5\u6B64\u5904\u6709\u4F55\u5C71\u6797\uFF1F\u300D</p><p>\u7075\u7389\u6447\u4E86\u6447\u5934\uFF0C\u300C\u8D2B\u9053\u66FE\u8FFD\u6740\u4E00\u4E2A\u4EC7\u4EBA\u76F4\u81F3\u957F\u767D\uFF0C\u9014\u7ECF\u6B64\u5904\u65F6\uFF0C\u672A\u7559\u610F\u6709\u4F55\u5C71\u5CF0\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u63D0\u58F0\u9053\uFF1A\u300C\u9664\u6B64\u5916\u8C01\u77E5\u9053\u9F99\u57CE\u9644\u8FD1\u6709\u4F55\u5C71\u5CF0\uFF1F\u300D</p><p>\u4F17\u4EBA\u90FD\u6447\u5934\u4E0D\u77E5\u3002</p><p>\u6155\u5BB9\u9F99\u6C89\u9ED8\u534A\u664C\uFF0C\u5FFD\u7136\u81EA\u5931\u5730\u4E00\u7B11\uFF0C\u6162\u6162\u95EE\u9053\uFF1A\u300C\u8BF8\u4F4D\u53EF\u77E5\u9053\u4E91\u96FE\u5C71\u5728\u4F55\u5904\uFF1F\u300D</p><p>\u7075\u7389\u601D\u7D22\u9053\uFF1A\u300C\u8C6B\u5DDE\u5883\u5185\u6709\u4E00\u5EA7\u4E91\u53F0\u5C71\uFF0C\u4E91\u96FE\u5C71\u2026\u2026\u8D2B\u9053\u4E0D\u77E5\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E0D\u518D\u8BE2\u95EE\uFF0C\u7FFB\u8EAB\u4E0B\u9A6C\uFF0C\u5E73\u9759\u5730\u5429\u5490\u9053\uFF1A\u300C\u5C31\u5728\u6B64\u624E\u8425\u5B89\u6B47\uFF0C\u660E\u65E5\u672C\u5BAB\u53BB\u7956\u9675\u796D\u7940\u3002\u300D</p><p>\u7D2B\u73AB\u5728\u65C1\u542C\u5F97\u6E05\u6E05\u695A\u695A\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u6697\u6697\u53EB\u82E6\u3002\u672C\u6765\u634F\u9020\u4E00\u4E2A\u5C71\u540D\uFF0C\u627E\u4E0D\u5230\u5C31\u63A8\u8BF4\u542C\u9519\u4E86\uFF0C\u8BA9\u6155\u5BB9\u9F99\u968F\u4FBF\u62E3\u4E00\u5EA7\u5C71\u778E\u627E\u597D\u4E86\u3002\u53EF\u6CA1\u60F3\u5230\u8FD9\u91CC\u7ADF\u7136\u5149\u79C3\u79C3\u4EC0\u4E48\u5C71\u90FD\u6CA1\u6709\uFF0C\u8FD9\u4E0B\u53EF\u5B8C\u4E86\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u671D\u5979\u770B\u4E00\u773C\uFF0C\u72EC\u81EA\u671D\u57CE\u5916\u8D70\u53BB\u3002</p><p>\u7D2B\u73AB\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u7EC8\u7A76\u662F\u8EB2\u4E0D\u8FC7\u7684\uFF0C\u8FD8\u4E0D\u5982\u8D81\u65E9\u60F3\u529E\u6CD5\u628A\u8FD9\u4E8B\u62B9\u8FC7\u53BB\uFF0C\u514D\u5F97\u4ED6\u84C4\u6EE1\u4E86\u6012\u6C14\u518D\u56DE\u6765\u627E\u81EA\u5DF1\u7B97\u5E10\u3002</p><p>\u4E00\u54AC\u7259\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8DF3\u4E0B\u9A6C\u8F66\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u4E0D\u659C\u89C6\u5730\u7A7F\u8FC7\u8352\u57CE\uFF0C\u8FF3\u76F4\u8D70\u5411\u8349\u539F\u3002\u7D2B\u73AB\u4E00\u8DEF\u5C0F\u8DD1\u8FFD\u4E86\u4E0A\u53BB\uFF0C\u4ECE\u65C1\u8FB9\u626C\u8D77\u8138\uFF0C\u5C0F\u5FC3\u5730\u89C2\u5BDF\u4ED6\u7684\u795E\u8272\u3002</p><p>\u6155\u5BB9\u9F99\u8D8A\u8D70\u8D8A\u5FEB\uFF0C\u5374\u59CB\u7EC8\u6CA1\u6709\u65BD\u5C55\u8F7B\u529F\uFF0C\u56E0\u6B64\u7D2B\u73AB\u8FD8\u80FD\u52C9\u5F3A\u8DDF\u4E0A\u3002</p><p>\u8D70\u51FA\u5341\u91CC\u5DE6\u53F3\uFF0C\u6155\u5BB9\u9F99\u505C\u4E0B\u811A\u6B65\uFF0C\u51B7\u51B7\u671B\u7740\u5929\u9645\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002\u7D2B\u73AB\u4E5F\u4E0D\u6562\u4F5C\u58F0\uFF0C\u53EA\u4E24\u624B\u652F\u5728\u8170\u540E\uFF0C\u633A\u7740\u5706\u9F13\u9F13\u7684\u5C0F\u8179\uFF0C\u6EE1\u5934\u6C57\u6C34\u5730\u5598\u7740\u6C14\u3002</p><p>\u6155\u5BB9\u9F99\u957F\u957F\u5401\u4E86\u53E3\u6C14\uFF0C\u300C\u4F60\u4E00\u76F4\u90FD\u5728\u9A97\u6211\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u59D4\u5C48\u5730\u8BF4\uFF1A\u300C\u6211\u4E5F\u4E0D\u77E5\u9053\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u6155\u5BB9\u536B\u5F53\u65F6\u5C31\u662F\u8FD9\u4E48\u8BF4\u7684\u3002\u53EF\u80FD\u662F\u4ED6\u8BB0\u9519\u4E86\uFF0C\u6216\u8005\u90A3\u5B9D\u85CF\u6839\u672C\u5C31\u6CA1\u2026\u2026\u300D</p><p>\u300C\u4F4F\u53E3\uFF01\u300D\u6155\u5BB9\u9F99\u4E00\u58F0\u66B4\u559D\u3002</p><p>\u7D2B\u73AB\u5413\u5F97\u4E00\u4E2A\u54C6\u55E6\uFF0C\u5979\u6536\u4F4F\u58F0\uFF0C\u773C\u91CC\u6CEA\u6C34\u6162\u6162\u6D8C\u51FA\u3002</p><p>\u72C2\u98CE\u50CF\u88AB\u70B9\u71C3\u822C\u6BEB\u65E0\u5F81\u5146\u5730\u62D4\u5730\u800C\u8D77\uFF0C\u6155\u5BB9\u9F99\u8863\u8882\u730E\u730E\u98DE\u821E\uFF0C\u6D51\u8EAB\u9AA8\u8282\u5FAE\u5FAE\u4F5C\u54CD\uFF0C\u4ED6\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u95ED\u76EE\u671D\u5929\uFF0C\u8FCE\u7740\u72C2\u98CE\u5316\u77F3\u822C\u51DD\u56FA\u5728\u9EC4\u660F\u7684\u8349\u539F\u4E2D\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u6155\u5BB9\u9F99\u7D27\u54AC\u7684\u7259\u5173\u6162\u6162\u677E\u5F00\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u6CA1\u6709\u5B9D\u85CF\uFF0C\u6211\u6155\u5BB9\u9F99\u4E5F\u4E00\u6837\u80FD\u5F97\u5230\u5929\u4E0B\uFF01\u300D</p><p>\u7D2B\u73AB\u5FD9\u4E0D\u8FED\u5730\u70B9\u5934\u79F0\u662F\uFF0C\u300C\u54E5\u54E5\u8FD9\u4E48\u5389\u5BB3\uFF0C\u6839\u672C\u4E0D\u9700\u8981\u4EC0\u4E48\u5B9D\u85CF\u2014\u2014\u51B5\u4E14\u5B9D\u85CF\u80AF\u5B9A\u662F\u9A97\u4EBA\u7684\uFF0C\u8981\u6709\u7684\u8BDD\uFF0C\u6155\u5BB9\u536B\u600E\u4E48\u4E0D\u53BB\u53D6\u554A\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u51B0\u51B0\u8F6C\u8EAB\u56DE\u57CE\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u8BF4\uFF1A\u300C\u660E\u65E5\u796D\u7956\uFF0C\u5C0F\u5FC3\u7167\u987E\u4F60\u809A\u91CC\u7684\u5B69\u5B50\u3002\u300D</p><p>\u7D2B\u73AB\u5FC3\u91CC\u4E03\u4E0A\u516B\u4E0B\uFF0C\u5F04\u4E0D\u61C2\u4ED6\u662F\u5173\u5FC3\u8FD8\u662F\u5A01\u80C1\u3002\u60F3\u7740\uFF0C\u5979\u4E0D\u7531\u6253\u4E86\u4E2A\u5BD2\u5664\uFF0C\u8FD9\u755C\u7272\u4E0D\u4F1A\u662F\u8981\u5728\u7956\u9675\u518D\u5E72\u90A3\u79CD\u4E8B\u5427\uFF1F</p><p>\u8427\u4F5B\u5974\u4E5F\u611F\u89C9\u513F\u5973\u95F4\u5F02\u6837\u7684\u6C14\u6C1B\u3002\u5403\u996D\u65F6\u6155\u5BB9\u9F99\u4E0D\u518D\u50CF\u4EE5\u524D\u90A3\u6837\u62B1\u7740\u5979\u8FB9\u9017\u8FB9\u5582\uFF0C\u665A\u95F4\u751A\u81F3\u6CA1\u6709\u5728\u8F66\u5185\u8FC7\u591C\uFF0C\u5374\u628A\u767D\u6C0F\u59D0\u59B9\u62DB\u8D70\u4F8D\u5BDD\u3002\u800C\u7D2B\u73AB\u4E5F\u662F\u5FC3\u4E8B\u91CD\u91CD\u7684\u6837\u5B50\u3002</p><p>\u8427\u4F5B\u5974\u72B9\u8C6B\u534A\u664C\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u73AB\u513F\uFF0C\u4F60\u4EEC\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u52C9\u5F3A\u4E00\u7B11\uFF0C\u300C\u6CA1\u4E8B\u513F\u7684\u3002\u5A18\uFF0C\u4F60\u65E9\u4E9B\u7761\u5427\uFF0C\u660E\u5929\u8FD8\u8981\u8D77\u65E9\u2026\u2026\u300D</p><p>\u300C\u554A\u54DF\u2026\u2026\u300D\u8F66\u5916\u5FFD\u7136\u4F20\u6765\u767D\u6C0F\u59D0\u59B9\u8FDE\u58F0\u75DB\u53EB\u3002</p><p>\u8427\u4F5B\u5974\u8138\u8272\u53D1\u767D\uFF0C\u671B\u7740\u5973\u513F\u4F4E\u58F0\u9053\uFF1A\u300C\u73AB\u513F\uFF0C\u4F60\u2026\u2026\u300D</p><p>\u5979\u7F8E\u76EE\u4E00\u9EEF\uFF0C\u534A\u664C\u540E\u624D\u5634\u89D2\u62BD\u52A8\u5730\u8BF4\u9053\uFF1A\u300C\u73B0\u5728\u5DF2\u7ECF\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u4F60\u5C31\u987A\u7740\u4ED6\u4E9B\u2026\u2026\u300D\u60F3\u5230\u81EA\u5DF1\u8FD9\u662F\u529D\u5973\u513F\u4E0E\u513F\u5B50\u82DF\u5408\uFF0C\u8427\u4F5B\u5974\u53C8\u662F\u96BE\u8FC7\u53C8\u662F\u96BE\u582A\uFF0C\u6014\u6014\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u7D2B\u73AB\u6402\u4F4F\u6BCD\u4EB2\u7684\u80A9\u8180\u4F4E\u58F0\u529D\u6170\uFF0C\u5FC3\u91CC\u5374\u4E0D\u7531\u60F3\u8D77\u53E6\u4E00\u4E2A\u72B9\u5982\u6BCD\u4EB2\u7684\u8EAB\u5F71\u3002\u5E08\u7236\u7EDD\u5BF9\u4E0D\u4F1A\u59A5\u534F\u2026\u2026</p>',74);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
