import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_95" tabindex="-1"><a class="header-anchor" href="#_95" aria-hidden="true">#</a> 95</h1><p>\u53F6\u884C\u5357\u653E\u4E0B\u4E66\u4FE1\uFF0C\u76B1\u7709\u9053\uFF1A\u300C\u8001\u6C90\uFF0C\u4F60\u770B\u5462\uFF1F\u300D</p><p>\u6C90\u58F0\u4F20\u53F9\u4E86\u53E3\u6C14\uFF0C\u5FC3\u91CC\u59D4\u51B3\u96BE\u4E0B\u3002\u534A\u664C\u5F00\u53E3\u9053\uFF1A\u300C\u6B64\u4E8B\u6709\u5229\u6709\u5F0A\u3002\u9F99\u57CE\u867D\u53EF\u907F\u4EBA\u773C\u76EE\uFF0C\u4F46\u8DDD\u7EC8\u5357\u6570\u5343\u91CC\uFF0C\u8FDC\u79BB\u6211\u6559\u6839\u672C\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u70B9\u5934\u9053\uFF1A\u300C\u4EC5\u8FD0\u7CAE\u4FBF\u4E07\u5206\u56F0\u96BE\u3002\u300D</p><p>\u300C\u7CAE\u98DF\u5012\u5728\u5176\u6B21\u3002\u9F99\u57CE\u90BB\u8FD1\u6E1D\u6C34\uFF0C\u6E14\u730E\u6781\u5BCC\uFF0C\u53EF\u8865\u4E0D\u8DB3\u3002\u53EA\u662F\u6765\u5F80\u4FE1\u606F\u4F20\u9012\u6781\u8D39\u65F6\u65E5\u3002\u6B64\u4FE1\u662F\u4E03\u65E5\u4E4B\u524D\u53D1\u51FA\uFF0C\u5F53\u65F6\u5BAB\u4E3B\u8FD8\u672A\u5230\u9F99\u57CE\u3002\u7B97\u8D77\u6765\uFF0C\u5373\u4F7F\u98DE\u9E3D\u4F20\u4E66\u4E00\u6765\u4E00\u56DE\u6700\u5C11\u4E5F\u9700\u534A\u6708\u3002\u300D</p><p>\u53F6\u884C\u5357\u63A8\u7A76\u591A\u65F6\uFF0C\u4E5F\u60F3\u4E0D\u529E\u6CD5\u6765\uFF0C\u4FBF\u653E\u4E0B\u6B64\u4E8B\uFF0C\u7B11\u9053\uFF1A\u300C\u5F53\u65E5\u8521\u4E91\u5CF0\u4F20\u6765\u6D88\u606F\uFF0C\u6211\u8FD8\u5728\u4E3A\u5BAB\u4E3B\u62C5\u5FC3\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E48\u5FEB\u516B\u6781\u95E8\u4FBF\u5168\u519B\u8986\u6CA1\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u6EE1\u662F\u76B1\u7EB9\u7684\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u516B\u6781\u95E8\u662F\u5173\u4E2D\u7B2C\u4E00\u5927\u6D3E\uFF0C\u6B64\u756A\u5728\u585E\u5916\u88AB\u795E\u6559\u706D\u95E8\u3002\u8D81\u6D88\u606F\u8FD8\u672A\u4F20\u5230\u4E2D\u539F\uFF0C\u8981\u7ACB\u523B\u6D3E\u4EBA\u53BB\u5B89\u5B9A\u65A9\u8349\u9664\u6839\u3002\u300D\u89C1\u53F6\u884C\u5357\u7AD9\u8D77\u8EAB\u6765\uFF0C\u53C8\u9053\uFF1A\u300C\u6025\u4EC0\u4E48\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u5475\u5475\u7B11\u9053\uFF1A\u300C\u51FA\u8C0B\u5212\u7B56\u6211\u6BD4\u4F60\u53EF\u5DEE\u8FDC\u4E86\uFF0C\u8FD9\u4E8B\u4F60\u770B\u7740\u529E\uFF0C\u6211\u53BB\u77A7\u77A7\u593A\u80CE\u82B1\u3002\u300D\u4ED6\u770B\u4E86\u770B\u5929\u8272\uFF0C\u300C\u5DF2\u8FD1\u5348\u65F6\uFF0C\u8BE5\u5582\u5B83\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6797\u9999\u8FDC\u4ECD\u88AB\u94C1\u94FE\u88F8\u8EAB\u6813\u5728\u795E\u6BBF\u5916\u88AB\u4EBA\u5978\u6DEB\u7740\u3002\u53EA\u662F\u53F0\u9636\u65C1\u7684\u6811\u6748\u4E0A\u7528\u6811\u76AE\u642D\u4E86\u4E2A\u53EA\u5BB9\u4E00\u4EBA\u8737\u5367\u7684\u7A9D\u68DA\uFF0C\u52C9\u5F3A\u53EF\u4EE5\u906E\u98CE\u907F\u96E8\u3002\u6DF1\u591C\uFF0C\u5F53\u6240\u6709\u4EBA\u90FD\u79BB\u5F00\u4E4B\u540E\uFF0C\u5979\u4FBF\u6478\u7D22\u7740\u94BB\u5230\u91CC\u9762\uFF0C\u7B49\u5F85\u9ECE\u660E\u7684\u5230\u6765\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u81EA\u5DF1\u6D3B\u7740\u9664\u4E86\u88AB\u4EBA\u5978\u6DEB\u73A9\u5F04\u4EE5\u5916\uFF0C\u8FD8\u6709\u4EC0\u4E48\u610F\u4E49\uFF0C\u4F46\u5979\u4ECD\u7136\u5728\u65E0\u5C3D\u7684\u51CC\u8FB1\u4E2D\u6323\u624E\u7740\u751F\u5B58\u4E0B\u6765\u3002\u6216\u8005\u662F\u56E0\u4E3A\u98D8\u6885\u5CF0\u4ECE\u6765\u90FD\u4E0D\u8F7B\u8A00\u653E\u5F03\uFF0C\u6216\u8005\u662F\u56E0\u4E3A\u5FC3\u5E95\u90A3\u4E00\u70B9\u70B9\u6E3A\u832B\u7684\u5E0C\u671B\u3002</p><p>\u300C\u5149\u5577\u300D\u4E00\u58F0\uFF0C\u4E00\u540D\u5E2E\u4F17\u628A\u94C1\u76AE\u6876\u6254\u5728\u9636\u4E0A\u3002</p><p>\u6B63\u5728\u6797\u9999\u8FDC\u4F53\u5185\u633A\u5F04\u7684\u6C49\u5B50\u7ACB\u523B\u52A0\u5FEB\u901F\u5EA6\u3002</p><p>\u7B49\u4ED6\u5C04\u5B8C\u7CBE\uFF0C\u6797\u9999\u8FDC\u4E00\u624B\u6342\u7740\u4E0B\u8179\uFF0C\u4E00\u624B\u6478\u7D22\u7740\u591F\u5230\u94C1\u6876\uFF0C\u7136\u540E\u5206\u817F\u5750\u5728\u6876\u4E0A\uFF0C\u7528\u624B\u6307\u5C06\u5149\u6E9C\u6E9C\u7684\u8089\u6D1E\u6491\u5F00\u3002</p><p>\u6EE1\u6EA2\u7684\u6D53\u7CBE\u4ECE\u7EA2\u5AE9\u7684\u8089\u6D1E\u6EDA\u843D\uFF0C\u987A\u7740\u624B\u6307\u6EF4\u6EF4\u7B54\u7B54\u6389\u5728\u6876\u5E95\uFF0C\u767D\u8272\u7684\u7CBE\u6DB2\u76F4\u6D41\u51FA\u534A\u7897\u4EFD\u91CF\uFF0C\u624D\u6E10\u6E10\u505C\u6B62\u3002\u6797\u9999\u8FDC\u4ECD\u8DE8\u5728\u6876\u4E0A\uFF0C\u7B49\u7CBE\u6DB2\u6D41\u5F97\u5DEE\u4E0D\u591A\u4E86\uFF0C\u4FBF\u5F13\u8170\u4E3E\u8D77\u96EA\u81C0\u3002</p><p>\u90A3\u540D\u5E2E\u4F17\u4ECE\u6876\u8FB9\u62FF\u8D77\u4E00\u679D\u9E21\u86CB\u7C97\u7EC6\u7684\u6F0F\u52FA\uFF0C\u671D\u6797\u9999\u8FDC\u4E0B\u4F53\u4E00\u6345\u3002\u94DC\u5236\u7684\u5706\u52FA\u7ACB\u65F6\u6CA1\u5165\u5149\u79C3\u79C3\u7684\u80A1\u95F4\uFF0C\u5728\u4E24\u817F\u4EA4\u5408\u5904\u7684\u5149\u6ED1\u4E09\u89D2\u5F62\u4E0A\u7559\u4E0B\u4E00\u4E2A\u6D51\u5706\u7684\u5165\u53E3\u3002</p><p>\u6F0F\u52FA\u4E0A\u4E0B\u524D\u540E\u4E00\u9635\u4E71\u6405\uFF0C\u5C06\u8089\u7A74\u5185\u7684\u6B8B\u7CBE\u522E\u5F97\u4E00\u6EF4\u4E0D\u5269\uFF0C\u7136\u540E\u53C8\u63D2\u8FDB\u540E\u5EAD\u5982\u6CD5\u70AE\u5236\u3002\u522E\u5B8C\u4E4B\u540E\uFF0C\u90A3\u5E2E\u4F17\u4E3E\u8D77\u6F0F\u52FA\u5728\u6876\u6CBF\u78D5\u4E86\u78D5\uFF0C\u6CA5\u5C3D\u6B8B\u7CBE\uFF0C\u63D0\u7740\u94C1\u6876\u626C\u957F\u800C\u53BB\u3002</p><p>\u542C\u5230\u6572\u51FB\u58F0\uFF0C\u6797\u9999\u8FDC\u4FBF\u4FEF\u8EAB\u8DEA\u5728\u5730\u4E0A\uFF0C\u4E24\u624B\u62B1\u7740\u5706\u81C0\uFF0C\u7B49\u5F85\u4E0B\u4E00\u6839\u8089\u68D2\u7684\u8FDB\u5165\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u90A3\u5E2E\u4F17\u7ED5\u8FC7\u795E\u6BBF\uFF0C\u66F2\u66F2\u6298\u6298\u8D70\u4E86\u534A\u664C\uFF0C\u6765\u5230\u6000\u6708\u5CF0\u4E0B\u7684\u4E00\u4E2A\u5C71\u6D1E\u524D\u3002</p><p>\u5BF8\u8349\u4E0D\u751F\u7684\u5C71\u5CF0\u602A\u77F3\u5D99\u5CCB\uFF0C\u7B14\u76F4\u4F38\u5411\u5929\u7A7A\u3002\u4E0B\u65B9\u7684\u6D1E\u53E3\u5929\u7136\u751F\u6210\u6843\u53F6\u5F62\u72B6\uFF0C\u8272\u5448\u891A\u7EA2\u3002\u8FD9\u4FBF\u662F\u5723\u5BAB\u7684\u4EB2\u5B57\u752C\u9053\uFF0C\u4E5F\u662F\u8FD9\u5EA7\u5E9E\u5927\u5BAB\u6BBF\u7684\u4E24\u4E2A\u51FA\u53E3\u4E4B\u4E00\u3002</p><p>\u300C\u8001\u9648\uFF0C\u4ECA\u513F\u8BE5\u4F60\u7684\u73ED\u54EA\u3002\u300D\u95E8\u53E3\u6709\u4EBA\u62DB\u547C\u9053\u3002</p><p>\u300C\u54CE\u3002\u300D\u90A3\u4EBA\u7B54\u5E94\u4E00\u58F0\uFF0C\u95EE\u9053\uFF1A\u300C\u4E0A\u5348\u591A\u4E0D\u591A\uFF1F\u300D</p><p>\u300C\u563F\u563F\uFF0C\u6E05\u6C5F\u4F1A\u7684\u5434\u5A4A\u5B50\u6765\u4E86\uFF0C\u4E00\u4E0A\u5348\u63A5\u4E86\u4E09\u5341\u6765\u4E2A\uFF0C\u591F\u4F60\u76DB\u4E24\u7897\u3002\u300D</p><p>\u8001\u9648\u63A2\u5934\u770B\u4E86\u770B\uFF0C\u300C\u54A6\uFF1F\u4ECA\u513F\u98CE\u5A4A\u5B50\u6CA1\u5BA2\uFF1F\u300D</p><p>\u90A3\u4EBA\u9886\u4ED6\u5165\u5185\uFF0C\u8BF4\u9053\uFF1A\u300C\u98CE\u5A4A\u5B50\u7678\u6C34\u6765\u4E86\uFF0C\u5927\u4F19\u5ACC\u6076\u5FC3\uFF0C\u6CA1\u4EBA\u64CD\u5979\u3002\u4E0D\u8FC7\u5979\u4E5F\u6CA1\u95F2\u7740\u2026\u2026\u300D\u8BF4\u7740\u63A8\u5F00\u77F3\u95E8\u3002</p><p>\u5165\u76EE\u662F\u4E00\u6761\u8349\u9EC4\u8272\u7684\u571F\u72D7\uFF0C\u76AE\u6BDB\u6591\u9A73\uFF0C\u6CBE\u6EE1\u6CE5\u571F\u3002\u4E24\u6761\u53C8\u7626\u53C8\u957F\u7684\u817F\u722A\u6491\u5728\u5730\u4E0A\uFF0C\u5F13\u7740\u8170\u8EAB\u4E0D\u4F4F\u633A\u52A8\u3002\u5B83\u8EAB\u4E0B\u662F\u4E00\u53EA\u767D\u4EAE\u4EAE\u7684\u80A5\u81C0\uFF0C\u7EC6\u7D27\u7684\u517D\u6839\u5728\u8089\u7A74\u91CC\u4E0D\u4F4F\u8FDB\u51FA\u3002\u7ECF\u8840\u805A\u5728\u9AD8\u8038\u7684\u9634\u961C\u4E0A\uFF0C\u987A\u7740\u4E4C\u4EAE\u7684\u9634\u6BDB\u8840\u7EBF\u822C\u5782\u5728\u5730\u4E0A\u3002</p><p>\u300C\u4ECE\u54EA\u513F\u627E\u6765\u8FD9\u4E48\u6761\u72D7\uFF1F\u300D\u8001\u9648\u770B\u5F97\u6D25\u6D25\u6709\u5473\u3002</p><p>\u300C\u4E0D\u77E5\u9053\u5BAB\u4E3B\u600E\u4E48\u5F04\u7684\uFF0C\u786C\u628A\u6D41\u971C\u5251\u8111\u5B50\u7ED9\u6BC1\u4E86\u3002\u53EA\u4F1A\u50BB\u53EB\uFF0C\u4E0D\u4F1A\u8BF4\u8BDD\uFF0C\u8FDE\u5403\u4E1C\u897F\u90FD\u4E0D\u77E5\u9053\u7528\u624B\uFF0C\u5929\u5929\u6447\u7740\u5C41\u80A1\u8BA9\u4EBA\u64CD\uFF0C\u6574\u4E2A\u6210\u4E86\u6761\u6BCD\u72D7\u3002\u8FD9\u4E0D\uFF0C\u5144\u5F1F\u4EEC\u8D81\u8FD9\u673A\u4F1A\u4ECE\u5916\u9762\u627E\u4E86\u6761\u91CE\u72D7\u7ED9\u5979\u914D\u5BF9\u3002\u300D</p><p>\u300C\u6211\u8BF4\u5462\uFF0C\u4EBA\u90FD\u64CD\u4E0D\u8FC7\u6765\uFF0C\u8FD8\u8BA9\u72D7\u5F04\u3002\u300D\u8001\u9648\u653E\u4E0B\u94C1\u6876\uFF0C\u62FF\u6F0F\u52FA\u5728\u98CE\u665A\u534E\u8EAB\u4E0A\u522E\u4E86\u522E\uFF0C\u300C\u4ECA\u513F\u5012\u5E72\u51C0\u3002\u4EE5\u524D\u5976\u5B50\u91CC\u90FD\u80FD\u6324\u51FA\u534A\u7897\u3002\u300D</p><p>\u90A3\u4EBA\u53EA\u662F\u9886\u4ED6\u770B\u770B\u65B0\u9C9C\uFF0C\u89C1\u72B6\u4E0D\u7531\u95EE\u9053\uFF1A\u300C\u72D7\u7684\u4E5F\u80FD\u7528\uFF1F\u300D</p><p>\u300C\u7BA1\u5B83\u5462\u3002\u9A74\u7684\u9A6C\u7684\u90FD\u4E00\u6837\u4F7F\u3002\u300D</p><p>\u300C\u8FD8\u5E26\u7740\u8840\u5462\u3002\u300D</p><p>\u9648\u672F\u563F\u563F\u4E00\u7B11\uFF0C\u300C\u6B63\u597D\uFF0C\u591A\u4E00\u5473\u513F\uFF0C\u514D\u5F97\u90A3\u9A9A\u5C3C\u59D1\u603B\u5403\u4E00\u6837\uFF0C\u5403\u817B\u4E86\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\u98CE\u665A\u534E\u54A6\u54A6\u5440\u5440\u53EB\u4E86\u8D77\u6765\uFF0C\u4E0D\u65F6\u8FD8\u5939\u7740\u4E24\u58F0\u6E05\u8106\u7684\u72AC\u5420\u3002\u571F\u72D7\u8DB4\u5728\u5979\u80CC\u4E0A\u4E24\u817F\u4E00\u4E2A\u52B2\u54C6\u55E6\uFF0C\u63A5\u7740\u4E00\u80A1\u767D\u8272\u7684\u72D7\u7CBE\u6DF7\u5728\u9C9C\u8840\u4E2D\u6DCC\u4E86\u51FA\u6765\u3002</p><p>\u8001\u9648\u628A\u6876\u8E22\u5230\u98CE\u665A\u534E\u817F\u95F4\uFF0C\u7B49\u4E86\u534A\u5929\uFF0C\u89C1\u90A3\u72D7\u8FD8\u63D2\u5728\u8089\u7A74\u91CC\u4E0D\u820D\u5F97\u62D4\u51FA\u6765\uFF0C\u4E8E\u662F\u4E0D\u8010\u70E6\u5730\u6293\u4F4F\u72D7\u97AD\u4E00\u62FD\u3002\u82B1\u74E3\u5E94\u624B\u7FD5\u5F20\uFF0C\u8089\u7A74\u9F13\u8D77\u5706\u5706\u4E00\u56E2\uFF0C\u5374\u6CA1\u80FD\u62D4\u51FA\u6765\u3002</p><p>\u518D\u4E00\u4F7F\u529B\uFF0C\u98CE\u665A\u534E\u5440\u5730\u53EB\u4E86\u8D77\u6765\uFF0C\u5C41\u80A1\u6025\u5F80\u540E\u9000\u3002</p><p>\u8001\u9648\u4E00\u811A\u8E29\u4F4F\u5979\u7684\u96EA\u81C0\uFF0C\u7528\u529B\u4E00\u626F\uFF0C\u8089\u7A74\u50CF\u70B8\u5F00\u822C\u4E00\u4E0B\u7FFB\u5F00\uFF0C\u6389\u51FA\u4E00\u4E2A\u62F3\u5934\u5927\u7684\u8089\u7624\u3002\u72D7\u7CBE\u54D7\u7684\u4E00\u58F0\u6D41\u5230\u6876\u5185\u3002</p><p>\u8001\u9648\u63D0\u6876\u79BB\u5F00\uFF0C\u98CE\u665A\u534E\u56DB\u80A2\u75DB\u82E6\u5730\u8737\u7F29\u7740\u5012\u5728\u5730\u4E0A\uFF0C\u7D27\u5E76\u7684\u817F\u6839\u5904\uFF0C\u82B1\u74E3\u7F13\u7F13\u5408\u62E2\uFF0C\u9690\u9690\u9732\u51FA\u6EA2\u8840\u7684\u8089\u7A74\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u79BB\u795E\u6BBF\u4E0D\u8FDC\u7684\u6B66\u51E4\u522B\u9662\u672C\u662F\u56DB\u9547\u795E\u5C06\u5728\u661F\u6708\u6E56\u7684\u884C\u820D\uFF0C\u5982\u4ECA\u5DF2\u7A7A\u7F6E\u591A\u5E74\u3002\u6B64\u65F6\u9662\u4FA7\u8033\u5BA4\u5E7D\u6697\u7684\u89D2\u843D\u91CC\uFF0C\u5374\u9759\u6084\u6084\u8EBA\u7740\u4E00\u5177\u60E8\u767D\u7684\u5973\u4F53\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u53CC\u817F\u5F2F\u66F2\uFF0C\u811A\u8E1D\u88AB\u7C97\u91CD\u94C1\u73AF\u9501\u5728\u81C0\u540E\uFF0C\u6311\u9732\u5728\u5916\u7684\u811A\u7B4B\u5DF2\u7ECF\u53D1\u9EC4\u3002</p><p>\u53CC\u81C2\u7EDE\u5728\u80CC\u540E\uFF0C\u62E7\u6210\u4E00\u4E2A\u4E0D\u53EF\u601D\u8BAE\u7684\u5F62\u72B6\u3002</p><p>\u6574\u4E2A\u4EBA\u50CF\u5C31\u4E00\u53EA\u4EF0\u9762\u671D\u5929\u7684\u9752\u86D9\uFF0C\u8EBA\u5728\u4E00\u6761\u7EC6\u7A84\u7684\u94A2\u677F\u4E0A\u3002\u659C\u7F6E\u7684\u94A2\u677F\u53EA\u6709\u534A\u5C3A\u5BBD\uFF0C\u957F\u5EA6\u4EC5\u5230\u5C3E\u9AA8\uFF0C\u539A\u5EA6\u5374\u6709\u4E00\u624B\u5BBD\u3002\u4E00\u6761\u539A\u539A\u7684\u9ED1\u8272\u5EC9\u5E55\u6328\u7740\u94A2\u677F\u5C3D\u5934\u5782\u4E0B\uFF0C\u5C06\u8EAB\u4F53\u9694\u6210\u4E24\u4E2A\u6781\u4E0D\u5747\u5300\u7684\u90E8\u5206\u3002</p><p>\u9732\u5728\u5EC9\u5916\u7684\u90E8\u5206\u53EA\u6709\u80A5\u767D\u7684\u5706\u81C0\uFF0C\u6B64\u65F6\u51CC\u7A7A\u7FD8\u8D77\uFF0C\u597D\u50CF\u4E00\u4E2A\u5355\u72EC\u7684\u6027\u5668\uFF0C\u5B64\u96F6\u96F6\u98D8\u6D6E\u5728\u7A7A\u4E2D\u3002\u9AD8\u8038\u7684\u9634\u961C\u6210\u4E3A\u5168\u8EAB\u7684\u9876\u70B9\uFF0C\u4E2D\u95F4\u9F13\u80C0\u7684\u8089\u82B1\u4F9D\u7136\u80A5\u5AE9\u67D4\u7F8E\uFF0C\u4F46\u5EC9\u540E\u96EA\u767D\u7684\u5C0F\u8179\u5374\u8D6B\u7136\u9F13\u6210\u4E00\u4E2A\u5706\u6EDA\u6EDA\u7684\u7403\u4F53\uFF0C\u4ECE\u5927\u5C0F\u6765\u770B\uFF0C\u6700\u5C11\u4E5F\u6709\u4E86\u516D\u4E2A\u6708\u7684\u8EAB\u5B55\u3002</p><p>\u8001\u9648\u62CE\u7740\u4ECE\u5404\u5904\u641C\u96C6\u6765\u7684\u534A\u6876\u7CBE\u6DB2\uFF0C\u8F7B\u8F7B\u6572\u4E86\u6572\u95E8\uFF0C\u6BD5\u606D\u6BD5\u656C\u5730\u8BF4\u9053\uFF1A\u300C\u542F\u7980\u62A4\u6CD5\uFF0C\u82B1\u98DF\u5E26\u5230\u3002\u300D</p><p>\u6B63\u5728\u5207\u8109\u7684\u53F6\u884C\u5357\u795E\u8272\u4E0D\u52A8\uFF0C\u6DE1\u6DE1\u300C\u55EF\u300D\u4E86\u4E00\u58F0\u3002</p><p>\u8001\u9648\u63A8\u95E8\u800C\u5165\uFF0C\u5148\u62FF\u8D77\u4E00\u4E2A\u5F39\u7C27\u6A21\u6837\u672A\u5408\u53E3\u7684\u94A2\u73AF\uFF0C\u5377\u4E66\u822C\u62E7\u7D27\uFF0C\u7136\u540E\u9001\u5230\u795E\u5C3C\u8089\u7A74\u5185\u3002\u677E\u5F00\u624B\uFF0C\u94A2\u73AF\u7ACB\u523B\u5F39\u8D77\uFF0C\u6491\u5F00\u624B\u8155\u7C97\u7EC6\u4E00\u4E2A\u7B14\u76F4\u7684\u8089\u6D1E\uFF0C\u8FDE\u8089\u7A74\u6700\u6DF1\u5904\u7684\u82B1\u5FC3\u4E5F\u6E05\u6670\u53EF\u8FA8\u3002</p><p>\u7ACB\u5728\u795E\u5C3C\u8179\u524D\uFF0C\u53EF\u4EE5\u6E05\u695A\u5730\u770B\u5230\u8089\u58C1\u4E0A\u6302\u7740\u7684\u9ECF\u7A20\u9633\u7CBE\uFF0C\u4E00\u7F15\u7F15\u6389\u5728\u5BAB\u9888\u4E0A\u3002</p><p>\u5B50\u5BAB\u53E3\u5FAE\u5FAE\u8815\u52A8\uFF0C\u72B9\u5982\u4E00\u5F20\u8D2A\u5A6A\u5730\u5C0F\u5634\uFF0C\u5C06\u7CBE\u6DB2\u5438\u5F97\u4E00\u6EF4\u4E0D\u5269\u3002</p><p>\u8001\u9648\u62E3\u8D77\u6F0F\u6597\uFF0C\u5C06\u7EC6\u957F\u7684\u6597\u5634\u6D45\u6D45\u63D2\u8FDB\u82B1\u5FC3\uFF0C\u7136\u540E\u5782\u624B\u7B49\u5019\u62A4\u6CD5\u7684\u5429\u5490\u3002</p><p>\u53F6\u884C\u5357\u624B\u6307\u6162\u6162\u7F29\u56DE\u8863\u8896\uFF0C\u53F9\u606F\u9053\uFF1A\u300C\u5E08\u592A\u529F\u529B\u4E4B\u5F3A\uFF0C\u5B9E\u662F\u6211\u53F6\u884C\u5357\u751F\u5E73\u4EC5\u89C1\uFF0C\u5728\u4E0B\u4F69\u670D\u5F97\u7D27\u3002\u300D</p><p>\u300C\u5373\u4F7F\u7A7F\u9AA8\u6311\u7B4B\uFF0C\u8098\u819D\u5C3D\u788E\uFF0C\u5E08\u592A\u8FD8\u80FD\u5C06\u771F\u6C14\u4E09\u5EA6\u805A\u5165\u4E39\u7530\u2026\u2026\u5982\u6B64\u795E\u529F\uFF0C\u53F6\u67D0\u95FB\u6240\u672A\u95FB\u3002\u300D</p><p>\u53F6\u884C\u5357\u4E00\u8FDE\u4E32\u95EE\u9053\uFF1A\u300C\u5E08\u592A\u771F\u6C14\u65E2\u4E0D\u5165\u5341\u4E8C\u7ECF\u7EDC\uFF0C\u53C8\u4E0D\u4F9D\u5947\u7ECF\u516B\u8109\uFF0C\u7A76\u7ADF\u5982\u4F55\u8FD0\u8F6C\uFF1F\u771F\u6C14\u6563\u5F00\u4E4B\u540E\uFF0C\u4E39\u7530\u6240\u4F59\u4E0D\u8FC7\u5341\u4E4B\u4E00\u4E8C\uFF0C\u5176\u4F59\u7A76\u7ADF\u85CF\u5728\u4F55\u5904\uFF1F\u5E08\u592A\u5185\u606F\u70BD\u70ED\u5982\u706B\uFF0C\u805A\u62E2\u65F6\u5347\u817E\u7FFB\u52A8\uFF0C\u5176\u72B6\u751A\u5F02\uFF0C\u8FD9\u7A76\u7ADF\u662F\u4E0D\u662F\u51E4\u51F0\u5B9D\u5178\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u604D\u82E5\u672A\u95FB\uFF0C\u7389\u5BB9\u65E0\u6CE2\u3002</p><p>\u53F6\u884C\u5357\u6380\u5F00\u5E03\u5EC9\uFF0C\u671D\u795E\u5C3C\u4E0B\u4F53\u77A5\u4E86\u4E00\u773C\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4EE5\u540E\u7F6E\u5165\u65F6\u518D\u6D45\u534A\u5206\uFF0C\u5343\u4E07\u4E0D\u53EF\u7834\u819C\u3002\u300D</p><p>\u8001\u9648\u8FDE\u5FD9\u8EAC\u8EAB\u7B54\u5E94\uFF0C\u628A\u6F0F\u6597\u671D\u5916\u62D4\u4E86\u5C11\u8BB8\u3002</p><p>\u53F6\u884C\u5357\u4E0D\u518D\u5F00\u53E3\uFF0C\u6446\u4E86\u6446\u624B\u653E\u4E0B\u5E03\u5EC9\u3002</p><p>\u8001\u9648\u4E3E\u8D77\u94C1\u6876\uFF0C\u5C06\u6DF7\u7740\u8840\u4E1D\u7684\u6D4A\u7CBE\u5F90\u5F90\u5012\u8FDB\u6F0F\u6597\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7EA2\u5507\u4E00\u7D27\uFF0C\u6B7B\u6B7B\u54AC\u4F4F\u7259\u5173\u3002</p><p>\u9F13\u80C0\u7684\u5C0F\u8179\u731B\u7136\u4E00\u9707\uFF0C\u6DF1\u85CF\u5176\u4E2D\u7684\u7269\u4F53\u50CF\u662F\u5728\u5927\u53E3\u5927\u53E3\u5730\u541E\u566C\u4E00\u822C\uFF0C\u5267\u70C8\u5730\u7FFB\u6EDA\u8D77\u6765\u3002</p><p>\u4E0D\u591A\u65F6\uFF0C\u72D7\u7CBE\u548C\u6570\u767E\u540D\u5927\u6C49\u7684\u7CBE\u6DB2\u4EE5\u53CA\u7ECF\u8840\u7684\u6DF7\u5408\u7269\u5DF2\u5C3D\u6570\u6D41\u5165\u795E\u5C3C\u8179\u4E2D\u3002</p><p>\u8001\u9648\u62FF\u8D77\u6F0F\u6597\uFF0C\u6643\u52A8\u7740\u7F13\u7F13\u62D4\u51FA\u3002\u6597\u5634\u79BB\u5F00\u540E\uFF0C\u4E00\u7F15\u9633\u7CBE\u4ECE\u6765\u4E0D\u53CA\u5408\u62E2\u7684\u82B1\u5FC3\u6D8C\u51FA\uFF0C\u65CB\u5373\u53C8\u88AB\u5438\u5165\u3002</p><p>\u53D6\u51FA\u94A2\u73AF\uFF0C\u795E\u5C3C\u4E0B\u4F53\u7684\u8089\u82B1\u6E10\u6E10\u6062\u590D\u539F\u72B6\uFF0C\u4F46\u9F13\u80C0\u7684\u5C0F\u8179\u5374\u9707\u52A8\u5F97\u6108\u53D1\u731B\u70C8\u3002</p><p>\u5438\u9971\u4E86\u7CBE\u6DB2\u7684\u593A\u80CE\u82B1\u4E0D\u5B89\u4EFD\u5730\u4E00\u8D77\u4E00\u4F0F\uFF0C\u50CF\u662F\u8981\u6491\u7834\u809A\u76AE\u8DF3\u51FA\u6765\u4F3C\u7684\u3002\u80C0\u8D77\u65F6\u5C0F\u8179\u767D\u817B\u7684\u76AE\u80A4\u88AB\u6491\u5F97\u53C8\u7EC6\u53C8\u8584\uFF0C\u51E0\u4E4E\u80FD\u770B\u5230\u5E95\u4E0B\u6D41\u52A8\u7684\u8840\u6DB2\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6EE1\u8138\u662F\u6C57\uFF0C\u82E6\u82E6\u5FCD\u8010\u90A3\u79CD\u80C0\u88C2\u822C\u7684\u5267\u75DB\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u8F6F\u8F6F\u6B6A\u5728\u80F8\u524D\u7684\u80A5\u4E73\u6E10\u6E10\u53D8\u5F97\u575A\u786C\u3002</p><p>\u300C\u4F11\u606F\u4E00\u523B\u949F\uFF0C\u518D\u884C\u63A5\u5BA2\u3002\u300D\u53F6\u884C\u5357\u8BF4\u5B8C\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p>',67);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
