import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_96" tabindex="-1"><a class="header-anchor" href="#_96" aria-hidden="true">#</a> 96</h1><p>\u521D\u5347\u7684\u9633\u5149\u4EFF\u4F5B\u4E00\u6C60\u900F\u660E\u7684\u6C34\u6676\uFF0C\u6CBF\u7740\u624B\u6307\u548C\u8033\u6735\u7684\u8F6E\u5ED3\u7EC6\u7EC6\u6D41\u6DCC\u3002\u6155\u5BB9\u9F99\u548C\u6155\u5BB9\u7D2B\u73AB\u5E76\u9A91\u800C\u884C\uFF0C\u4E24\u4E2A\u7EC6\u957F\u7684\u5F71\u5B50\u6643\u52A8\u7740\u9760\u8FD1\uFF0C\u53C8\u6643\u52A8\u7740\u5206\u5F00\uFF0C\u6C38\u8FDC\u4E5F\u65E0\u6CD5\u6C47\u5408\u3002</p><p>\u7D2B\u73AB\u77A7\u4E86\u77A7\u6155\u5BB9\u9F99\u90A3\u5F20\u6CA1\u6709\u8868\u60C5\u7684\u51B7\u8138\uFF0C\u5FC3\u91CC\u5600\u5600\u5495\u5495\uFF1A\u8FD8\u8BF4\u4E0D\u5728\u4E4E\u5B9D\u85CF\uFF0C\u5927\u6E05\u65E9\u677F\u7740\u90A3\u5F20\u81ED\u8138\u7ED9\u8C01\u770B\u5462\u3002</p><p>\u300C\u90A3\u91CC\u6709\u53EA\u5154\u5B50\u5462\uFF0C\u597D\u53EF\u7231\u2026\u2026\u300D\u7D2B\u73AB\u8BD5\u56FE\u4F7F\u6C14\u6C1B\u878D\u6D3D\u4E00\u4E9B\uFF0C\u6307\u7740\u8FDC\u5904\u7684\u8349\u4E1B\u8BF4\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u77A5\u4E86\u4E00\u773C\uFF0C\u5C48\u6307\u4E00\u5F39\u3002\u90A3\u53EA\u5154\u5B50\u4EF0\u8EAB\u6454\u5012\uFF0C\u4E24\u773C\u95F4\u9732\u51FA\u4E00\u4E2A\u5C0F\u5C0F\u7684\u8840\u6D1E\u3002</p><p>\u7D2B\u73AB\u5012\u62BD\u53E3\u51C9\u6C14\uFF0C\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u52C9\u5F3A\u8D5E\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u4F60\u7684\u529F\u592B\u771F\u597D\uFF0C\u8FDE\u53EA\u5154\u5B50\u2026\u2026\u6253\u5F97\u771F\u51C6\uFF01\u300D</p><p>\u89C1\u6155\u5BB9\u9F99\u5BF9\u81EA\u5DF1\u7684\u9A6C\u5C41\u6BEB\u4E0D\u7406\u776C\uFF0C\u7D2B\u73AB\u773C\u73E0\u4E00\u8F6C\uFF0C\u53C8\u8BF4\u9053\uFF1A\u300C\u90A3\u53EA\u96C1\u98DE\u5F97\u597D\u9AD8\u54E6\uFF0C\u771F\u6F02\u4EAE\u2026\u2026\u300D\u5FC3\u9053\uFF0C\u6709\u672C\u4E8B\u4F60\u628A\u5B83\u4E5F\u6253\u4E0B\u6765\u8BA9\u6211\u770B\u770B\u3002</p><p>\u6155\u5BB9\u9F99\u624B\u4E00\u626C\uFF0C\u4E00\u4E2A\u7528\u6765\u88C5\u9970\u9A6C\u978D\u7684\u94F6\u7247\u8D34\u7740\u5730\u9762\u75BE\u5C04\u800C\u51FA\uFF0C\u5C06\u8FDC\u5904\u5B09\u620F\u7684\u51E0\u53EA\u5C0F\u96C1\u9F50\u9888\u65A9\u65AD\u3002</p><p>\u7D2B\u73AB\u6123\u4E86\u4E00\u4E0B\uFF0C\u4F0F\u5728\u978D\u4E0A\u5267\u70C8\u7684\u5455\u5410\u8D77\u6765\uFF0C\u5FC3\u91CC\u8E66\u8E66\u8DF3\u8DF3\u5168\u662F\u53EF\u6016\u53C8\u6076\u5FC3\u7684\u4E00\u5E55\u3002</p><p>\u8349\u6D77\u4E2D\u9732\u51FA\u4E00\u7247\u74E6\u783E\u3002\u66FE\u7ECF\u91D1\u74A7\u8F89\u714C\u7684\u9675\u5893\u65E9\u5DF2\u88AB\u4EBA\u5937\u4E3A\u5E73\u5730\uFF0C\u5E7F\u8FBE\u6570\u91CC\u7684\u9675\u56ED\u5185\u5230\u5904\u662F\u5F62\u5F62\u8272\u8272\u7684\u7409\u7483\u788E\u7247\u548C\u6B8B\u7F3A\u7684\u77F3\u517D\uFF0C\u8FDE\u5468\u56F4\u7684\u6811\u6728\u4E5F\u5C3D\u88AB\u70E7\u6BC1\uFF0C\u53EA\u5269\u4E0B\u7126\u9ED1\u7684\u6811\u5E72\u3002</p><p>\u7A81\u7136\u95F4\u6155\u5BB9\u9F99\u5FC3\u5934\u4E00\u9635\u832B\u7136\uFF0C\u96BE\u9053\u8FD9\u5C31\u662F\u66FE\u7ECF\u56DB\u5EA6\u79F0\u5E1D\u7684\u6155\u5BB9\u6C0F\u7956\u9675\uFF1F\u96BE\u9053\u90A3\u4E9B\u52C7\u6B66\u98DE\u626C\u7684\u7956\u5148\u6A2A\u7A7A\u51FA\u4E16\uFF0C\u5E26\u7740\u6EDA\u6EDA\u94C1\u9A91\u5929\u795E\u822C\u8E0F\u7834\u5929\u4E0B\uFF0C\u7136\u540E\u5C31\u98CE\u4E00\u822C\u7684\u6D88\u5931\u4E86\u5417\uFF1F</p><p>\u7D2B\u73AB\u4E5F\u5927\u611F\u610F\u5916\uFF0C\u5979\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u7B56\u9A6C\u907F\u5F00\u904D\u5E03\u7684\u6D1E\u7A74\uFF0C\u56DB\u4E0B\u5F20\u671B\u7740\u95EE\u9053\uFF1A\u300C\u600E\u4E48\u5230\u5904\u90FD\u662F\u571F\u5751\u554A\uFF1F\u300D</p><p>\u300C\u90FD\u88AB\u59DA\u5174\u6398\u8FC7\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u5E73\u9759\u4E0B\u6765\uFF0C\u6DE1\u6DE1\u9053\u3002</p><p>\u7D2B\u73AB\u8DF3\u4E0B\u9A6C\uFF0C\u4ECE\u957F\u8349\u91CC\u62E3\u8D77\u4E00\u5757\u4E03\u5F69\u7409\u7483\u653E\u5728\u65AD\u88C2\u7684\u77F3\u7891\u4E0A\uFF0C\u8DEA\u4E0B\u5583\u5583\u9053\uFF1A\u300C\u5217\u4F4D\u7956\u5B97\uFF0C\u7D2B\u73AB\u6765\u770B\u4F60\u4EEC\u6765\u4E86\u3002\u7D2B\u73AB\u2026\u2026\u2026\u6CA1\u6709\u5E26\u796D\u54C1\uFF0C\u8FD8\u8BF7\u7956\u5B97\u4EEC\u539F\u8C05\u3002\u300D</p><p>\u4E2D\u95F4\u51E0\u53E5\u8BDD\u542B\u542B\u7CCA\u7CCA\uFF0C\u58F0\u97F3\u538B\u5F97\u6781\u4F4E\uFF0C\u8BF4\u7684\u662F\uFF1A\u300C\u7D2B\u73AB\u88AB\u4E00\u4E2A\u4E5F\u59D3\u6155\u5BB9\u7684\u6DF7\u86CB\u5BB3\u5F97\u597D\u82E6\u3002\u7956\u5B97\u5728\u5929\u6709\u7075\uFF0C\u4E00\u5B9A\u8981\u4FDD\u4F51\u6211\uFF0C\u4E0D\u8981\u4FDD\u4F51\u6155\u5BB9\u9F99\u90A3\u4E2A\u6DF7\u86CB\u3002\u8FD9\u6B21\u6CA1\u6709\u5E26\u796D\u54C1\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B14\u76F4\u7ACB\u5728\u7D2B\u73AB\u8EAB\u8FB9\uFF0C\u8FDE\u8170\u90FD\u6CA1\u6709\u5F2F\uFF0C\u53EA\u51B7\u51B7\u9053\uFF1A\u300C\u5217\u7956\u5217\u5B97\u5728\u4E0A\uFF0C\u6211\u6155\u5BB9\u9F99\u7ACB\u5FD7\u590D\u5174\u71D5\u56FD\uFF0C\u91CD\u632F\u6155\u5BB9\u6C0F\u5A01\u540D\uFF0C\u5373\u4EE5\u6B64\u8840\u4E3A\u796D\u3002\u300D\u8BF4\u7740\u62D4\u51FA\u7247\u7389\u63E1\u5728\u624B\u4E2D\u4E00\u62BD\uFF0C\u7136\u540E\u6162\u6162\u4E3E\u8D77\u6EF4\u8840\u7684\u624B\u638C\u3002\u522B\u4EBA\u796D\u7940\u7528\u7684\u662F\u9152\uFF0C\u4ED6\u7528\u7684\u5374\u662F\u6155\u5BB9\u6C0F\u7684\u9C9C\u8840\u3002</p><p>\u7D2B\u73AB\u88AB\u4ED6\u75AF\u72C2\u7684\u76EE\u5149\u5413\u5F97\u4E00\u98A4\uFF0C\u62B1\u7740\u80A9\u5934\u4EE5\u547D\u4EE4\u7684\u53E3\u6C14\u8BF4\u9053\uFF1A\u300C\u4E0D\u8BB8\u4F60\u62FF\u5200\u5F80\u6211\u8EAB\u4E0A\u5272\uFF01\u300D</p><p>\u6BB7\u7EA2\u7684\u9C9C\u8840\u4E00\u6EF4\u6EF4\u6CBE\u5728\u8352\u8349\u4E0A\uFF0C\u50CF\u4E00\u4E32\u8DF3\u52A8\u7684\u706B\u79CD\u3002</p><p>\u300C\u8131\u3002\u300D</p><p>\u7D2B\u73AB\u5438\u4E86\u53E3\u6C14\uFF0C\u300C\u4F60\u628A\u5200\u6536\u8D77\u6765\u3002\u300D</p><p>\u300C\u53EE\u300D\uFF0C\u5229\u5203\u8D34\u7740\u8138\u988A\u523A\u5165\u6B8B\u7891\uFF0C\u76F4\u6CA1\u81F3\u67C4\u3002</p><p>\u300C\u2026\u2026\u8FD9\u662F\u7956\u5B97\u7684\u9675\u5BDD\u2026\u2026\u300D\u7D2B\u73AB\u5C0F\u58F0\u54C0\u6C42\u9053\uFF0C\u300C\u56DE\u53BB\u6211\u518D\u7528\u5FC3\u4F3A\u5019\u54E5\u54E5\u597D\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u300C\u7956\u5B97\u90FD\u846C\u5728\u8FD9\u91CC\u2026\u2026\u6211\u4EEC\u2026\u2026\u54E5\uFF0C\u6C42\u4F60\u4E86\u2026\u2026\u300D</p><p>\u4ED9\u5B50\u822C\u7684\u5C11\u5973\u8F6F\u8BED\u76F8\u6C42\uFF0C\u4EFB\u662F\u77F3\u4EBA\u4E5F\u4F1A\u5FC3\u52A8\u3002\u4F46\u6155\u5BB9\u9F99\u53EA\u662F\u51B7\u51B0\u51B0\u770B\u7740\u5979\uFF0C\u51B7\u51B0\u51B0\u91CD\u590D\u4E86\u90A3\u4E2A\u5B57\uFF1A\u300C\u8131\u3002\u300D</p><p>\u7D2B\u73AB\u5E76\u4E0D\u662F\u4E2A\u5F88\u56FA\u6267\u7684\u5973\u5B69\uFF0C\u5979\u4F1A\u6492\u8C0E\u3001\u4F1A\u6311\u8845\uFF0C\u4E5F\u4F1A\u5728\u9002\u5F53\u7684\u65F6\u5019\u505A\u51FA\u8BA9\u6B65\u6765\u907F\u514D\u51B2\u7A81\u3002</p><p>\u5979\u4E0D\u80DC\u59D4\u5C48\u5730\u5782\u4E0B\u5934\uFF0C\u4E00\u9762\u89E3\u8863\uFF0C\u4E00\u9762\u56DB\u4E0B\u5F20\u671B\uFF0C\u300C\u4E0D\u77E5\u9053\u8FD9\u4E2A\u6DF7\u5E10\u8981\u600E\u4E48\u5F04\u3002\u5230\u5904\u90FD\u662F\u788E\u77F3\u74E6\u7247\uFF0C\u600E\u4E48\u8EBA\u554A\u2026\u2026\u4E0D\u5982\u62FF\u4ED6\u5F53\u57AB\u5B50\u2026\u2026\u300D</p><p>\u773C\u89D2\u4E00\u4E2A\u767D\u751F\u751F\u7684\u7269\u4F53\u4E00\u95EA\u800C\u8FC7\uFF0C\u7D2B\u73AB\u4E0D\u7ECF\u610F\u62AC\u76EE\u770B\u53BB\uFF0C\u4FCF\u8138\u731B\u7136\u6DA8\u5F97\u901A\u7EA2\uFF0C\u63A5\u7740\u53C8\u53D8\u5F97\u6BEB\u65E0\u8840\u8272\u3002</p><p>\u5751\u5E95\u534A\u63A9\u7740\u4E00\u4E2A\u7070\u767D\u7684\u9AB7\u9AC5\uFF0C\u9ED1\u6D1E\u6D1E\u7684\u773C\u7736\u4F3C\u4E4E\u6B63\u6CE8\u89C6\u7740\u773C\u524D\u7684\u5C11\u5973\u3002</p><p>\u7D2B\u73AB\u539F\u672C\u5E76\u4E0D\u5F88\u770B\u91CD\u81EA\u5DF1\u7684\u59D3\u6C0F\uFF0C\u4E5F\u4E0D\u5341\u5206\u5728\u610F\u7956\u5148\uFF0C\u56E0\u6B64\u624D\u4F1A\u73A9\u4E00\u4E9B\u5C0F\u5C0F\u7684\u82B1\u62DB\u3002\u4F46\u6B64\u523B\u9AB7\u9AC5\u7A7A\u6D1E\u7684\u773C\u7736\u5374\u7ED9\u5C11\u5973\u5E26\u6765\u65E0\u6BD4\u7684\u9707\u64BC\u3002\u5B83\u4F3C\u4E4E\u6B63\u51B7\u6F20\u5730\u770B\u7740\u81EA\u5DF1\uFF0C\u770B\u7A7F\u4E86\u81EA\u5DF1\u7684\u5FC3\u4E8B\u3002</p><p>\u9762\u5BF9\u585A\u4E2D\u67AF\u9AA8\uFF0C\u7D2B\u73AB\u4E0D\u7981\u4E3A\u81EA\u5DF1\u521A\u624D\u4E0D\u77E5\u7684\u7F9E\u803B\u7684\u6DEB\u7325\u5FF5\u5934\u800C\u7F9E\u6127\uFF0C\u65CB\u5373\u5FC3\u5934\u53C8\u5347\u8D77\u4E00\u80A1\u83AB\u660E\u7684\u611F\u89C9\uFF0C\u6709\u4E9B\u4EB2\u5207\uFF0C\u53C8\u6709\u4E9B\u7F9E\u803B\uFF0C\u66F4\u591A\u7684\u5219\u662F\u656C\u754F\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\uFF0C\u4F60\u77E5\u9053\u81EA\u5DF1\u5728\u505A\u4EC0\u4E48\u5417\uFF1F\u662F\u5728\u901D\u53BB\u7684\u7956\u5148\u9762\u524D\u5144\u59B9\u4E71\u4F26\u554A\u2026\u2026</p><p>\u7D2B\u73AB\u4FCF\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u7389\u6307\u50F5\u5728\u8170\u95F4\uFF0C\u518D\u65E0\u6CD5\u89E3\u5F00\u7F57\u5E26\u3002</p><p>\u8863\u9886\u300C\u54E7\u300D\u7684\u5206\u5F00\uFF0C\u7EEF\u8863\u88C2\u6210\u4E24\u7247\u6389\u5728\u8170\u95F4\uFF0C\u9732\u51FA\u4E00\u6BB5\u96EA\u7389\u822C\u7684\u8089\u4F53\u3002\u90A3\u662F\u6155\u5BB9\u9F99\u5BF9\u5979\u7684\u6C89\u9ED8\u4E0D\u8010\u70E6\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u53CC\u624B\u98A4\u6296\u7740\u63A9\u4F4F\u9165\u4E73\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u9F99\u3002\u4F60\u8FD8\u662F\u4EBA\u4E0D\u662F\uFF1F\u300D</p><p>\u300C\u6211\u77E5\u9053\u4F60\u6068\u6211\u3002\u300D\u6155\u5BB9\u9F99\u58F0\u97F3\u6CA1\u6709\u4E00\u4E1D\u611F\u60C5\uFF0C\u300C\u4F46\u6211\u4E0D\u5728\u4E4E\u3002\u53EA\u8981\u4F60\u7ED9\u6211\u751F\u5B69\u5B50\uFF0C\u4F60\u628A\u6211\u5F53\u4EC0\u4E48\u90FD\u53EF\u4EE5\u3002\u300D</p><p>\u7D2B\u73AB\u98CE\u4E00\u822C\u8F6C\u8EAB\uFF0C\u6E05\u4EAE\u7684\u7F8E\u76EE\u4E2D\u9971\u542B\u6CEA\u6C34\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u9F99\uFF0C\u4F60\u4E0D\u8981\u8138\uFF0C\u6211\u8FD8\u8981\u8138\u3002\u5F53\u7740\u7956\u5B97\u7684\u9762\u505A\u8FD9\u79CD\u65E0\u803B\u4E0B\u6D41\u7684\u79BD\u517D\u52FE\u5F53\uFF0C\u4F60\u5C31\u4E0D\u6015\u4EB5\u6E0E\u4E86\u7956\u5B97\u5728\u5929\u4E4B\u7075\uFF01\u300D</p><p>\u300C\u4EB5\u6E0E\uFF1F\u300D\u6155\u5BB9\u9F99\u4E00\u54C2\uFF0C\u4ED6\u626C\u624B\u6307\u7740\u9AB7\u9AC5\u4E0D\u5C51\u5730\u8BF4\uFF1A\u300C\u4ED6\u4EEC\u4EFB\u7531\u90A3\u4E9B\u8D31\u6C11\u6765\u73B7\u6C61\u6211\u6155\u5BB9\u6C0F\u7684\u8840\u7EDF\uFF0C\u4EE5\u81F3\u56DB\u4EA1\u5927\u71D5\uFF0C\u5982\u4ECA\u5893\u575F\u90FD\u88AB\u4EBA\u6398\u4E86\uFF0C\u8FDE\u673D\u9AA8\u88AB\u6252\u51FA\u6765\u793A\u4F17\uFF0C\u8FD8\u8C08\u4EB5\u6E0E\uFF1F\u300D</p><p>\u9A6C\u8F66\u58F0\u4ECE\u540E\u4F20\u6765\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u7136\u9053\uFF1A\u300C\u83AB\u8BF4\u4F60\u662F\u6211\u59B9\u59B9\uFF0C\u6211\u4ECA\u65E5\u8FD8\u8981\u5F53\u7740\u7956\u5B97\u7684\u9762\uFF0C\u6B63\u5F0F\u7EB3\u5A18\u4EB2\u4E3A\u59BE\uFF01\u300D</p><p>\u7D2B\u73AB\u671B\u7740\u4ED6\u8EAB\u540E\uFF0C\u5165\u76EE\u7684\u8273\u5149\u4F7F\u5979\u4E0D\u7531\u9000\u4E86\u4E00\u6B65\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E00\u4E2A\u96EA\u80A4\u82B1\u8C8C\u7684\u76DB\u88C5\u7F8E\u5987\uFF0C\u7531\u4E24\u540D\u5C11\u5973\u6400\u6276\u7740\u4E0B\u4E86\u8F66\uFF0C\u82B1\u679D\u822C\u4FCF\u751F\u751F\u7ACB\u5728\u6742\u8349\u4E1B\u751F\u7684\u74E6\u783E\u95F4\u3002</p><p>\u8427\u4F5B\u5974\u4E91\u9AFB\u9AD8\u76D8\uFF0C\u7D20\u624B\u7EA2\u88F3\uFF0C\u4E00\u8EAB\u534E\u8D35\u7684\u65B0\u5A18\u6253\u626E\u3002\u4E00\u679D\u78A7\u7C2A\u659C\u659C\u6311\u5728\u9AFB\u4E0A\uFF0C\u4E4C\u4EAE\u7684\u9B13\u89D2\u68B3\u7406\u5F97\u7EB9\u4E1D\u4E0D\u4E71\u3002\u6C34\u7EA2\u8272\u7684\u5AC1\u8863\u7EA4\u519C\u5408\u5EA6\uFF0C\u5E26\u7740\u9C9C\u660E\u7684\u585E\u5916\u98CE\u97F5\u3002</p><p>\u8863\u895F\u7684\u8FB9\u7F18\u6EDA\u4E86\u4E00\u9053\u7EC6\u7EC6\u7684\u96EA\u767D\u7ED2\u6BDB\uFF0C\u91D1\u7EA2\u4EA4\u9519\u7684\u5706\u9886\u5411\u4E0A\u7AD6\u8D77\uFF0C\u62E5\u7740\u7EC6\u767D\u7684\u67D4\u9888\uFF0C\u8863\u8896\u6309\u9C9C\u5351\u98CE\u4FD7\u5E26\u7740\u675F\u8155\uFF0C\u66F4\u663E\u5F97\u5341\u6307\u7EA4\u7F8E\u5982\u7389\u3002\u98D8\u9038\u7684\u88D9\u6446\u4E0B\u662F\u4E00\u53CC\u7CBE\u81F4\u7684\u5C0F\u76AE\u9774\uFF0C\u8F7B\u76C8\u76C8\u8E0F\u5728\u67AF\u8349\u4E0A\uFF0C\u7247\u5C18\u4E0D\u67D3\u3002</p><p>\u5AC1\u8863\u63A9\u6620\u4E0B\uFF0C\u8427\u4F5B\u5974\u7389\u988A\u5E26\u7740\u51E0\u5206\u5A07\u7F9E\u7684\u7EA2\u6655\uFF0C\u7F8E\u8273\u7EDD\u4F26\u3002\u5979\u602F\u751F\u751F\u770B\u4E86\u513F\u5973\u4E00\u773C\uFF0C\u7F9E\u8D67\u5730\u8F6C\u8FC7\u8138\u3002</p><p>\u7D2B\u73AB\u626D\u5934\u770B\u4E86\u770B\u5E72\u67AF\u7684\u9AB7\u9AC5\uFF0C\u53C8\u770B\u4E86\u770B\u8273\u5149\u56DB\u5C04\u7684\u6BCD\u4EB2\uFF0C\u4E00\u80A1\u5BD2\u610F\u4ECE\u811A\u5E95\u5347\u8D77\u3002</p><p>\u300C\u6155\u5BB9\u6C0F\u5217\u7956\u5217\u5B97\uFF01\u4E0D\u8096\u5B50\u5B59\u6155\u5BB9\u9F99\uFF0C\u4ECA\u65E5\u5A36\u59B9\u4E3A\u59BB\u3001\u7EB3\u6BCD\u4E3A\u59BE\uFF0C\u8BF7\u5217\u7956\u5217\u5B97\u4E3A\u8BC1\uFF01\u300D\u6155\u5BB9\u9F99\u56DE\u8FC7\u5934\u3002\u5BD2\u58F0\u9053\uFF1A\u300C\u59B9\u59B9\u662F\u6B63\u5BA4\uFF0C\u4F60\u662F\u59BE\u4F8D\u3002\u5A18\uFF0C\u4F60\u7ED9\u5927\u5987\u884C\u793C\u5427\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u94FA\u5F00\u4E00\u6761\u6D01\u767D\u7684\u6BDB\u6BEF\uFF0C\u7136\u540E\u5C06\u8427\u4F5B\u5974\u6276\u5230\u6BEF\u4E0A\u3002\u8427\u4F5B\u5974\u8DEA\u5728\u5973\u513F\u9762\u524D\u78D5\u4E86\u4E09\u4E2A\u5934\uFF0C\u7136\u540E\u6162\u6162\u626C\u8D77\u81FB\u9996\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u7F8E\u76EE\u4E2D\u6CEA\u6C34\u76F4\u8F6C\u3002\u7247\u523B\u540E\u7EA2\u5507\u5FAE\u98A4\u5730\u8F7B\u8F7B\u53EB\u4E86\u58F0\uFF1A\u300C\u59D0\u59D0\u2026\u2026\u300D</p><p>\u8FD9\u58F0\u300C\u59D0\u59D0\u300D\u53EB\u5F97\u6155\u5BB9\u7D2B\u73AB\u5468\u8EAB\u53D1\u51B7\uFF0C\u5979\u54C6\u55E6\u7740\u62FC\u547D\u6447\u5934\uFF0C\u5374\u8BF4\u4E0D\u51FA\u4E00\u4E2A\u5B57\u6765\u3002</p><p>\u8427\u4F5B\u5974\u7F9E\u60ED\u5F97\u65E0\u5730\u81EA\u5BB9\uFF0C\u5728\u300C\u59D0\u59D0\u300D\u60CA\u6050\u7684\u76EE\u5149\u4E2D\u5782\u4E0B\u67D4\u9888\uFF0C\u5FC3\u91CC\u4E0D\u671F\u7136\u60F3\u5230\u300C\u9F99\u54E5\u54E5\u300D\u6709\u529B\u7684\u624B\u81C2\u2014\u2014\u53EA\u6709\u8EB2\u5728\u90A3\u91CC\uFF0C\u624D\u80FD\u9003\u907F\u4E00\u5207\u2026\u2026</p><p>\u767D\u6C0F\u59D0\u59B9\u5C06\u8427\u4F5B\u5974\u9999\u8EAF\u653E\u5728\u6BEF\u4E0A\uFF0C\u4E00\u4EF6\u4EF6\u9664\u53BB\u90A3\u4E9B\u534E\u4E3D\u7684\u670D\u9970\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u606D\u559C\u5982\u592B\u4EBA\uFF0C\u5BAB\u4E3B\u5F00\u6069\u6536\u4E86\u60A8\uFF0C\u8FD9\u4E0B\u6709\u4E86\u540D\u4EFD\u3002\u4ECA\u540E\u5982\u592B\u4EBA\u548C\u5C11\u592B\u4EBA\u5171\u4E8B\u4E00\u592B\uFF0C\u5408\u5BB6\u5C3D\u6B22\uFF0C\u53EF\u5706\u6EE1\u5F97\u7D27\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u53CC\u76EE\u6CDB\u8D77\u7EA2\u5149\uFF0C\u50CF\u76EF\u7740\u90A3\u4E2A\u9AB7\u9AC5\u53D1\u8A93\u822C\u68EE\u7136\u9053\uFF0C\u300C\u4ECE\u4ECA\u4E4B\u540E\uFF0C\u6211\u6155\u5BB9\u6C0F\u5B50\u5B50\u5B59\u5B59\u7537\u5973\u4E92\u4E3A\u5A5A\u914D\uFF0C\u7EDD\u4E0D\u5BB9\u5916\u4EBA\u73B7\u6C61\u6211\u6155\u5BB9\u6C0F\u7684\u8840\u7EDF\uFF01\u300D</p><p>\u8FD9\u4F1A\u513F\u7D2B\u73AB\u771F\u88AB\u6155\u5BB9\u9F99\u7684\u75AF\u72C2\u5413\u4F4F\u4E86\uFF0C\u5728\u7956\u5B97\u9675\u524D\u7ACB\u4E0B\u8FD9\u6837\u5927\u9006\u4E0D\u9053\u6709\u8FDD\u5929\u7406\u7684\u8A93\u8A00\uFF0C\u4E0D\u4EC5\u4EB5\u6E0E\u7956\u5B97\uFF0C\u800C\u4E14\u4E5F\u4EB5\u6E0E\u4E86\u5B50\u5B59\u540E\u4EE3\uFF0C\u4ED6\u96BE\u9053\u771F\u7684\u75AF\u4E86\uFF1F</p><p>\u81EA\u5DF1\u548C\u8FD9\u4E2A\u79BD\u517D\u4E71\u4F26\u751F\u4E0B\u7684\u767D\u75F4\u5B50\u5973\uFF0C\u5728\u6CE5\u6C34\u4E2D\u7FFB\u7740\u767D\u773C\uFF0C\u732A\u72D7\u4E00\u6837\u4EA4\u914D\u2026</p><p>\u7D2B\u73AB\u84E6\u5730\u60F3\u8D77\u8349\u4E1B\u4E2D\u90A3\u4E9B\u626D\u52A8\u6323\u624E\u7684\u65AD\u9888\uFF0C\u5FC3\u5934\u53C8\u662F\u4E00\u9635\u4F5C\u5455\u3002</p><p>\u8427\u4F5B\u5974\u5DF2\u88AB\u8131\u5C3D\u9774\u889C\uFF0C\u4E5F\u89E3\u53BB\u90A3\u5757\u4EE4\u5979\u65E0\u5730\u81EA\u5BB9\u7684\u5C3F\u5E03\u3002\u83B9\u767D\u7684\u7389\u4F53\u8D64\u6761\u6761\u653E\u5728\u6BEF\u4E0A\uFF0C\u51E0\u4E4E\u6BD4\u8EAB\u4E0B\u7EC6\u8F6F\u7684\u7ED2\u6BDB\u66F4\u52A0\u6D01\u767D\u9C9C\u4EAE\u3002</p><p>\u6155\u5BB9\u9F99\u4E94\u6307\u5F20\u5F00\uFF0C\u51CC\u7A7A\u4E00\u6293\uFF0C\u9AB7\u9AC5\u4E00\u8DC3\u843D\u5165\u624B\u4E2D\u3002</p><p>\u300C\u666E\u5929\u4E4B\u4E0B\uFF0C\u53EA\u6709\u6211\u6155\u5BB9\u6C0F\u8840\u7EDF\u6700\u4E3A\u9AD8\u8D35\u3002\u300D\u6155\u5BB9\u9F99\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u628A\u9AB7\u9AC5\u653E\u5728\u811A\u8FB9\uFF0C\u300C\u6211\u4E0E\u4F60\u751F\u4E0B\u7684\u5B69\u5B50\uFF0C\u5C06\u62E5\u6709\u6700\u7EAF\u6B63\u7684\u6155\u5BB9\u6C0F\u8840\u7EDF\u3002\u300D</p><p>\u300C\u4F60\u53EA\u4F1A\u751F\u4E0B\u4E00\u7FA4\u767D\u75F4\uFF01\u300D\u7D2B\u73AB\u8BDD\u97F3\u672A\u843D\uFF0C\u5DF2\u88AB\u6155\u5BB9\u9F99\u7C97\u66B4\u5730\u8FDB\u5165\u4F53\u5185\u3002</p><p>\u300C\u5341\u4E2A\uFF1F\u4E8C\u5341\u4E2A\uFF1F\u300D\u6155\u5BB9\u9F99\u51B7\u51B7\u4E00\u7B11\uFF0C\u300C\u6211\u90FD\u4E0D\u5728\u4E4E\u3002\u7EE7\u627F\u6211\u5927\u71D5\u7687\u4F4D\u7684\u592A\u5B50\u53EA\u8981\u4E00\u4E2A\u5C31\u591F\u4E86\u3002\u4E0B\u4E2A\u6708\u4F60\u624D\u6EE1\u5341\u516D\u5427\uFF0C\u50CF\u5A18\u8FD9\u6837\uFF0C\u4F60\u8FD8\u6709\u4E8C\u5341\u5E74\u7684\u65F6\u95F4\u7ED9\u6211\u751F\u5B69\u5B50\u3002\u8DB3\u591F\u4E86\u3002\u300D</p><p>\u867D\u7136\u6155\u5BB9\u9F99\u7559\u610F\u6CA1\u6709\u538B\u81EA\u5DF1\u7684\u5C0F\u8179\uFF0C\u4F46\u8FDB\u5165\u7684\u75DB\u695A\u8FD8\u662F\u4F7F\u7D2B\u73AB\u62E7\u7D27\u7709\u5934\uFF0C\u5979\u968F\u624B\u6293\u8D77\u9AB7\u9AC5\u671D\u6155\u5BB9\u9F99\u8138\u4E0A\u6253\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u82E5\u65E0\u5176\u4E8B\u5730\u53D7\u4E86\u4E00\u8BB0\uFF0C\u76F4\u8D77\u8170\u8EAB\uFF0C\u300C\u5F88\u597D\u3002\u6211\u4EEC\u7684\u513F\u5B50\u4E5F\u4F1A\u7EE7\u627F\u4F60\u7684\u52C7\u6C14\u3002\u8FD8\u6709\u5014\u5F3A\u3002\u300D</p><p>\u8089\u68D2\u4E00\u6345\u5230\u5E95\uFF0C\u6155\u5BB9\u9F99\u4E3E\u8D77\u624B\u638C\uFF0C\u6307\u95F4\u7684\u94A2\u9488\u5BD2\u5149\u51DB\u51BD\uFF0C\u4ED6\u6DE1\u6DE1\u9053\uFF1A\u300C\u6211\u4EEC\u65CF\u4EBA\u7684\u4E60\u60EF\uFF0C\u4F1A\u5728\u9A6C\u5339\u8EAB\u4E0A\u70D9\u4E0B\u8BB0\u53F7\u6765\u6807\u8BB0\u4E3B\u4EBA\u3002\u6211\u4F1A\u5728\u7956\u5B97\u9762\u524D\u7ED9\u6211\u7684\u59BB\u5B50\u548C\u4F8D\u59BE\u523A\u4E0B\u6C38\u8FDC\u4E0D\u4F1A\u5931\u53BB\u7684\u5370\u8BB0\u3002\u4ECE\u6B64\uFF0C\u4F60\u4EEC\u4FBF\u662F\u6211\u7684\u5BA0\u7269\u3002\u300D</p>',63);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
