import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_97" tabindex="-1"><a class="header-anchor" href="#_97" aria-hidden="true">#</a> 97</h1><p>\u300C\u6155\u5BB9\u9F99\uFF0C\u6211\u6068\u4F60\uFF01\u6068\u4F60\u4E00\u751F\u4E00\u4E16\u3002\u300D\u7D2B\u73AB\u677E\u5F00\u624B\uFF0C\u9AB7\u9AC5\u7FFB\u6EDA\u7740\u5012\u5728\u4E00\u65C1\uFF0C\u773C\u7736\u4E2D\u4F3C\u4E4E\u5E26\u7740\u65E0\u9650\u7684\u4F24\u75BC\u3002</p><p>\u300C\u2026\u2026\u4E5F\u597D\u3002\u8FD9\u6837\u6211\u5C31\u53EF\u4EE5\u5B89\u5FC3\u628A\u4F60\u5F53\u6210\u751F\u5B69\u5B50\u7684\u5DE5\u5177\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u8868\u60C5\u6709\u4E9B\u751F\u786C\uFF0C\u4ED6\u81EA\u8D1F\u65E0\u8BBA\u6B66\u529F\u667A\u6167\uFF0C\u8FD8\u662F\u76F8\u8C8C\u90FD\u8BE5\u662F\u7D2B\u73AB\u8FD9\u79CD\u5C0F\u5973\u5B69\u503E\u5FC3\u7684\u7537\u5B50\uFF0C\u66F4\u4F55\u51B5\u2026\u2026\u81EA\u5DF1\u5BF9\u5979\u90A3\u4E48\u597D\u3002\u53EF\u5979\u7684\u56DE\u7B54\u53EA\u6709\u300C\u6068\u300D\u3002</p><p>\u94A2\u9488\u65E0\u60C5\u5730\u523A\u5165\u582A\u79F0\u5B8C\u7F8E\u7684\u808C\u80A4\uFF0C\u9488\u811A\u4E0B\u5192\u51FA\u4E00\u6EF4\u8840\u73E0\uFF0C\u8273\u5982\u739B\u7459\u3002\u6155\u5BB9\u9F99\u628A\u9C9C\u8840\u91AE\u5728\u6307\u5C16\uFF0C\u7AEF\u8BE6\u7740\u5C0F\u58F0\u9053\uFF1A\u300C\u8FD9\u5C31\u662F\u6211\u6155\u5BB9\u6C0F\u7684\u9C9C\u8840\u2026\u2026\u300D\u4ED6\u4ED4\u7EC6\u54C1\u5C1D\u7740\u9C9C\u8840\u7684\u6ECB\u5473\uFF0C\u8138\u4E0A\u9732\u51FA\u5982\u75F4\u5982\u9189\u7684\u795E\u60C5\uFF0C\u300C\u5B83\u5C06\u6C38\u8FDC\u5982\u6B64\u7EAF\u6B63\uFF01\u300D</p><p>\u300C\u8F70\u9686\u300D\u4E00\u58F0\u5DE8\u54CD\uFF0C\u4E07\u91CC\u6674\u7A7A\u7A81\u7136\u6BEB\u65E0\u6765\u7531\u5730\u54CD\u8D77\u4E00\u58F0\u9739\u96F3\uFF0C\u4EFF\u4F5B\u5C31\u5728\u5934\u9876\u70B8\u5F00\u3002\u63A5\u7740\u53C8\u662F\u4E00\u4E2A\u3002</p><p>\u8FDE\u4E32\u60CA\u96F7\u54CD\u8FC7\uFF0C\u4F17\u4EBA\u90FD\u662F\u5FC3\u60CA\u8089\u8DF3\u3002\u6155\u5BB9\u9F99\u5374\u604D\u82E5\u672A\u95FB\uFF0C\u968F\u7740\u7389\u4EBA\u5A07\u8EAF\u4E0A\u8840\u73E0\u6E10\u589E\uFF0C\u4ED6\u7684\u53CC\u773C\u4E5F\u8D8A\u6765\u8D8A\u7EA2\u3002</p><p>\u96F7\u58F0\u9707\u6C64\u7740\u6EDA\u5411\u8FDC\u65B9\uFF0C\u8FDC\u8FDC\u6D88\u5931\u5929\u5730\u4EA4\u6C47\u5904\u3002\u63A5\u7740\uFF0C\u4E00\u9635\u9690\u9690\u7684\u8F70\u9E23\u4EFF\u4F5B\u5954\u817E\u7684\u9A6C\u7FA4\u4ECE\u96F7\u58F0\u6D88\u5931\u7684\u5929\u9645\u75BE\u9A70\u800C\u81F3\u3002</p><p>\u5E73\u9759\u7684\u8349\u539F\u817E\u8D77\u4E00\u6761\u957F\u65E0\u5C3D\u5934\u7684\u5DE8\u9F99\uFF0C\u7FFB\u6EDA\u5347\u817E\uFF0C\u8D8A\u6765\u8D8A\u9AD8\uFF0C\u76F4\u81F3\u5145\u585E\u4E86\u6574\u4E2A\u5929\u5730\u3002</p><p>\u9AB7\u9AC5\u5728\u98CE\u4E2D\u4E0D\u4F4F\u6643\u52A8\uFF0C\u5927\u5F20\u7684\u4E0B\u988C\u4F3C\u4E4E\u5728\u53D1\u51FA\u65E0\u58F0\u7684\u75DB\u65A5\uFF0C\u53C8\u4F3C\u4E4E\u5E26\u7740\u8BE1\u5F02\u7684\u7B11\u610F\u3002</p><p>\u8427\u4F5B\u5974\u88AB\u5973\u513F\u8EAB\u4E0A\u7684\u8840\u8FF9\u5413\u5F97\u8138\u8272\u82CD\u767D\uFF0C\u5047\u5982\u80FD\u591F\u52A8\u4F5C\uFF0C\u5979\u4E00\u5B9A\u4F1A\u4E0D\u987E\u4E00\u5207\u5730\u62B1\u4F4F\u513F\u5B50\u7684\u624B\u81C2\uFF0C\u8BA9\u4ED6\u653E\u8FC7\u73AB\u513F\u3002\u4F46\u6B64\u65F6\u5979\u53EA\u80FD\u542C\u7740\u81EA\u5DF1\u4F4E\u5F31\u7684\u547C\u558A\u5728\u98CE\u4E2D\u98D8\u6563\u3002</p><p>\u300C\u4E0D\u8981\u6025\u3002\u300D\u72C2\u98CE\u4E2D\u6155\u5BB9\u9F99\u4ECD\u542C\u5F97\u4E00\u5B57\u4E0D\u6F0F\uFF0C\u300C\u4E00\u4F1A\u513F\u6211\u4F1A\u4E00\u8FB9\u64CD\u7740\u6211\u7684\u7231\u59BE\uFF0C\u4E00\u8FB9\u7ED9\u5979\u7EB9\u8EAB\u3002\u4F60\u60F3\u60F3\uFF0C\u8BA9\u6211\u64CD\u4F60\u54EA\u4E2A\u6D1E\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u602F\u751F\u751F\u5730\u770B\u4E86\u4ED6\u4E00\u773C\uFF0C\u7528\u80FD\u591F\u8BF4\u8BDD\u7684\u7F8E\u76EE\u4E5E\u6C42\u4ED6\u9976\u8FC7\u81EA\u5DF1\u3002</p><p>\u72C2\u98CE\u50CF\u6CA1\u6709\u6765\u8FC7\u822C\u7A81\u7136\u6D88\u5931\u4E86\uFF0C\u56DB\u5468\u77AC\u95F4\u5B89\u9759\u4E0B\u6765\u3002\u9633\u5149\u4F9D\u65E7\u707F\u70C2\uFF0C\u5929\u5730\u4F9D\u65E7\u6C89\u9ED8\u3002\u4F46\u8FD9\u79CD\u53CD\u5E38\u7684\u5B89\u5B81\u4E2D\uFF0C\u5374\u4F3C\u4E4E\u6B63\u8574\u917F\u7740\u4E00\u80A1\u6D53\u91CD\u7684\u4E0D\u7965\u6C14\u606F\u3002</p><p>\u7834\u4F53\u540E\uFF0C\u7D2B\u73AB\u7684\u8EAB\u4F53\u4E00\u5929\u4E00\u5929\u6210\u719F\u8D77\u6765\u3002\u5706\u6DA6\u7684\u7389\u4E73\u6676\u83B9\u5982\u7389\uFF0C\u6BD4\u65B0\u5A5A\u65F6\u5927\u4E86\u8BB8\u591A\uFF0C\u4E73\u6655\u7684\u8272\u6CFD\u4E5F\u5FAE\u5FAE\u52A0\u6DF1\uFF0C\u6BD4\u4EE5\u524D\u7565\u663E\u7A1A\u5AE9\u7684\u7C89\u7EA2\u66F4\u591A\u4E86\u51E0\u5206\u5A07\u8273\u3002\u7EA2\u5AE9\u7684\u4E73\u5934\u5A07\u5C0F\u73B2\u73D1\uFF0C\u5E26\u7740\u73E0\u5B9D\u822C\u7684\u5149\u8F89\u3002</p><p>\u5A07\u5AE9\u7684\u808C\u80A4\u6BD4\u8700\u4E2D\u6700\u7CBE\u81F4\u7684\u4E1D\u7EF8\u8FD8\u8981\u5149\u6ED1\uFF0C\u767D\u817B\u7684\u5C0F\u8179\u9686\u8D77\u4E00\u4E2A\u5706\u5F27\uFF0C\u5728\u6E29\u6696\u7684\u5B50\u5BAB\u91CC\u9762\uFF0C\u5144\u59B9\u4E71\u4F26\u7684\u79CD\u5B50\u5DF2\u7ECF\u751F\u957F\u4E86\u5C06\u8FD1\u4E94\u4E2A\u6708\u3002\u5706\u9F13\u9F13\u7684\u5C0F\u8179\uFF0C\u5E76\u6CA1\u6709\u4F7F\u73AB\u7470\u4ED9\u5B50\u7684\u8EAB\u4F53\u5931\u53BB\u539F\u6709\u7684\u5A07\u7F8E\uFF0C\u53CD\u800C\u591A\u4E86\u4E00\u5206\u6175\u61D2\u7684\u98CE\u60C5\u3002</p><p>\u7136\u800C\u5C31\u662F\u5982\u6B64\u7F8E\u4E3D\u7684\u8EAB\u4F53\uFF0C\u5374\u88AB\u950B\u5229\u7684\u94A2\u9488\u6B8B\u5FCD\u5730\u7EB9\u523A\u3002\u7D2B\u73AB\u75BC\u5F97\u7389\u5BB9\u626D\u66F2\uFF0C\u5168\u9760\u4E00\u80A1\u6068\u610F\u652F\u6491\u7740\u6CA1\u6709\u660F\u5012\u3002\u8FD9\u5E76\u4E0D\u662F\u5979\u592A\u8FC7\u8106\u5F31\uFF0C\u800C\u662F\u94A2\u9488\u523A\u5165\u808C\u80A4\u540E\uFF0C\u4E0D\u4EC5\u5212\u4E86\u4E2A\u534A\u5708\uFF0C\u751A\u81F3\u8FD8\u5E26\u7740\u4E00\u4E1D\u82E5\u6709\u82E5\u65E0\u7684\u771F\u6C14\uFF0C\u5728\u808C\u80A4\u4E0B\u9020\u6210\u4E00\u4E2A\u7EC6\u5C0F\u7684\u7A7A\u6D1E\u3002</p><p>\u9488\u5C16\u706B\u661F\u822C\u6389\u5728\u8EAB\u4E0A\uFF0C\u53C8\u5728\u80A4\u4E0B\u70B8\u5F00\u3002\u4ECE\u4E73\u4E0B\u76F4\u5230\u817F\u6839\uFF0C\u5927\u7247\u808C\u80A4\u5E03\u6EE1\u7EC6\u5BC6\u7684\u9488\u5B54\uFF0C\u6BCF\u4E2A\u9488\u5B54\u90FD\u6D8C\u51FA\u4E00\u6EF4\u9C9C\u8840\uFF0C\u6E10\u6E10\u8FDE\u6210\u4E00\u7247\uFF0C\u6700\u540E\u4ECE\u8170\u80A2\u6D41\u5230\u6BEF\u4E0A\u3002\u7D2B\u73AB\u5A07\u8EAF\u7EF7\u7D27\uFF0C\u6B7B\u6B7B\u54AC\u4F4F\u7259\u5173\uFF0C\u5FC3\u9053\uFF1A\u523A\u5F97\u7A00\u70C2\u6700\u597D\uFF01\u94A2\u9488\u5FFD\u7136\u4E00\u6C89\uFF0C\u843D\u5728\u9634\u961C\u4E0A\uFF0C\u610F\u6599\u4E4B\u5916\u7684\u75DB\u695A\u4F7F\u7D2B\u73AB\u7981\u4E0D\u4F4F\u300C\u5440\u300D\u7684\u4E00\u58F0\u53EB\u4E86\u51FA\u6765\u3002</p><p>\u6ED1\u5AE9\u7684\u82B1\u74E3\u4F9D\u7136\u5C0F\u5DE7\u79C0\u7F8E\uFF0C\u5E26\u7740\u4E00\u62B9\u5A07\u67D4\u7684\u7EA2\u8272\uFF0C\u7F8E\u7EDD\u4EBA\u5BF0\u3002\u53EA\u662F\u51FA\u5165\u5176\u4E2D\u7684\u5DE8\u7269\u72F0\u72DE\u65E0\u6BD4\uFF0C\u4EFF\u4F5B\u8981\u5F7B\u5E95\u6BC1\u6389\u8FD9\u6735\u5947\u82B1\u5F02\u5349\u822C\u51F6\u731B\u5730\u62BD\u9001\u7740\u3002\u4E0D\u4EC5\u5982\u6B64\uFF0C\u4E00\u6839\u95EA\u4EAE\u7684\u94A2\u9488\u6B63\u5BF9\u7740\u5AE9\u8089\u731B\u7136\u523A\u843D\u3002</p><p>\u7D2B\u73AB\u75DB\u53EB\u975E\u4F46\u6CA1\u6709\u5524\u8D77\u6155\u5BB9\u9F99\u7684\u601C\u60DC\uFF0C\u53CD\u800C\u5F15\u6765\u4E00\u9635\u5F00\u6000\u5927\u7B11\u3002\u6155\u5BB9\u9F99\u4F3C\u4E4E\u4E0D\u518D\u628A\u5979\u5F53\u4F5C\u73CD\u7231\u7684\u5A07\u59BB\uFF0C\u800C\u4EC5\u4EC5\u53EA\u662F\u4E2A\u7528\u6765\u53D6\u4E50\u7684\u73A9\u7269\u822C\uFF0C\u5728\u5979\u6700\u5A07\u5AE9\u7684\u90E8\u4F4D\u75AF\u72C2\u7684\u7EB9\u523A\u3002</p><p>\u5F53\u94A2\u9488\u523A\u8FDB\u82B1\u8482\u7684\u4E00\u77AC\uFF0C\u7D2B\u73AB\u518D\u5FCD\u4E0D\u4F4F\u59D4\u5C48\u548C\u4F24\u75BC\uFF0C\u54ED\u6CE3\u7740\u671D\u8FD9\u4E2A\u79BD\u517D\u80F8\u53E3\u6253\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u63E1\u7740\u5979\u7684\u4E00\u53EA\u7EA4\u8E1D\u4E00\u62E7\uFF0C\u5C06\u6000\u5B55\u7684\u5C11\u5973\u6380\u8F6C\u8FC7\u6765\u3002\u63A5\u7740\u94A2\u9488\u72E0\u72E0\u523A\u5165\u4F1A\u9634\u3002</p><p>\u67D4\u5AE9\u7684\u8089\u7A74\u84E6\u7684\u4E00\u7D27\uFF0C\u98A4\u6296\u7740\u5939\u4F4F\u8089\u68D2\u3002\u6155\u5BB9\u9F99\u8D81\u673A\u72E0\u72E0\u4E00\u62BD\uFF0C\u786C\u751F\u751F\u5E26\u51FA\u4E00\u5927\u7247\u7EA2\u8089\uFF0C\u63A5\u7740\u4E00\u633A\uFF0C\u5F3A\u70C8\u5730\u5C04\u8D77\u7CBE\u6765\u3002</p><p>\u4EE5\u5F80\u6155\u5BB9\u9F99\u4F1A\u5F88\u7EC6\u5FC3\u5730\u505A\u4E00\u4E9B\u7231\u629A\uFF0C\u64A9\u62E8\u8D77\u59B9\u59B9\u7684\u9AD8\u6F6E\uFF0C\u8BA9\u5979\u4EAB\u53D7\u6027\u7231\u7684\u6781\u4E50\u3002\u7136\u800C\u8FD9\u4E00\u6B21\uFF0C\u4ED6\u5374\u4E1D\u6BEB\u6CA1\u6709\u7406\u4F1A\u7D2B\u73AB\u7684\u611F\u53D7\uFF0C\u751A\u81F3\u4E0D\u60DC\u4EE5\u4F24\u5BB3\u7D2B\u73AB\u6765\u6EE1\u8DB3\u81EA\u5DF1\u7684\u6B32\u671B\u3002</p><p>\u51B0\u51B7\u7684\u9C9C\u8840\u67D3\u7EA2\u7684\u6D01\u767D\u7684\u6BDB\u6BEF\uFF0C\u7D2B\u73AB\u4F0F\u5728\u6BEF\u4E0A\u75DB\u82E6\u5730\u6218\u6817\u7740\u3002\u4E00\u79CD\u88AB\u4EBA\u5F7B\u5E95\u6DEB\u8650\u7684\u803B\u8FB1\u611F\u6DF9\u6CA1\u4E86\u4E00\u5207\u3002\u5979\u8FD9\u65F6\u624D\u8BA4\u8BC6\u5230\uFF0C\u81EA\u5DF1\u5728\u6155\u5BB9\u9F99\u773C\u4E2D\uFF0C\u4EC5\u4EC5\u53EA\u662F\u4E2A\u6709\u7740\u59BB\u5B50\u540D\u4EFD\u7684\u73A9\u7269\u800C\u5DF2\u3002</p><p>\u5979\u5728\u5FC3\u91CC\u5BF9\u81EA\u5DF1\u51C4\u7136\u4E00\u7B11\uFF0C\u300C\u8FD9\u5177\u8EAB\u4F53\u4E0D\u4EC5\u7559\u4E0B\u4ED6\u7684\u5B69\u5B50\uFF0C\u8FD8\u7559\u4E0B\u4E86\u803B\u8FB1\u7684\u6807\u8BB0\u3002\u4E5F\u8BB8\u4ED6\u523A\u7684\u4E5F\u662F\u4E24\u884C\u5B57\u8FF9\u3002\u4E0E\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u4E0D\u540C\u7684\u662F\uFF0C\u6211\u8FD9\u4E2A\u59BB\u5B50\u662F\u4ED6\u7684\u4E13\u6709\u73A9\u7269\u2026\u2026\u300D</p><p>\u300C\u60F3\u597D\u4E86\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u95EE\u9053\uFF0C\u8FD8\u6EF4\u7740\u9633\u7CBE\u7684\u8089\u68D2\u6BEB\u4E0D\u505C\u987F\u5730\u633A\u7136\u76F4\u7ACB\u3002</p><p>\u8427\u4F5B\u5974\u5982\u6C34\u7684\u773C\u6CE2\u8499\u4E0A\u4E00\u5C42\u6E7F\u6E7F\u7684\u96FE\u6C14\uFF0C\u5979\u54AC\u7740\u5507\u74E3\u6323\u624E\u826F\u4E45\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u540E\u9762\u2026\u2026\u300D</p><p>\u300C\u556A\uFF01\u300D\u6155\u5BB9\u9F99\u5728\u7F8E\u5987\u81C0\u4E0A\u91CD\u91CD\u62CD\u4E86\u4E00\u638C\uFF0C\u300C\u5C31\u77E5\u9053\u5C41\u773C\u513F\uFF01\u513F\u5B50\u5728\u7956\u5B97\u9762\u524D\u6536\u4F60\u5F53\u5C0F\u59BE\uFF0C\u53EF\u4E0D\u662F\u53EA\u4E3A\u64CD\u4F60\u7684\u5C41\u773C\u513F\u2014\u2014\u64CD\u5C41\u773C\u513F\u80FD\u751F\u5B69\u5B50\u5417\uFF1F\u770B\u4F60\u90A3\u9A9A\u6837\uFF0C\u90A3\u5934\u9AA8\u8BF4\u4E0D\u5B9A\u5C31\u662F\u6211\u6B7B\u9B3C\u8001\u7239\uFF0C\u4E5F\u4E0D\u6015\u5B83\u7B11\u8BDD\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u88AB\u513F\u5B50\u595A\u843D\u5F97\u7F9E\u6127\u96BE\u5F53\uFF0C\u5F53\u542C\u5230\u6700\u540E\u4E00\u53E5\uFF0C\u987F\u65F6\u300C\u54C7\u300D\u7684\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u63B0\u5F00\u8F6F\u7EF5\u7EF5\u7684\u7389\u817F\uFF0C\u5728\u767D\u99A5\u99A5\u7684\u9634\u961C\u4E0A\u63C9\u634F\u7740\u9AD8\u58F0\u9053\uFF1A\u300C\u5217\u4F4D\u7956\u5B97\u8BF7\u770B\uFF0C\u8FD9\u9A9A\u8D27\u7684\u5C44\u53C8\u6ED1\u53C8\u5AE9\uFF0C\u8FD9\u4F1A\u513F\u54ED\u5F97\u5389\u5BB3\uFF0C\u64CD\u4E0D\u4E86\u51E0\u4E0B\u5C31\u723D\u5F97\u76F4\u53EB\u5462\uFF01\u300D</p><p>\u300C\u9F99\u513F\u2026\u2026\u6C42\u6C42\u4F60\uFF0C\u4E0D\u8981\u518D\u7CDF\u8E4B\u5A18\u4E86\u2026\u2026\u300D</p><p>\u5DE8\u68D2\u8F70\u7136\u800C\u5165\uFF0C\u5C06\u7F8E\u5987\u7684\u54C0\u6C42\u5835\u5728\u5589\u5934\uFF0C\u5316\u4F5C\u4E00\u7F15\u547B\u541F\u98D8\u6563\u800C\u51FA\u3002</p><p>\u7531\u4E8E\u957F\u4E45\u4F7F\u7528\u63BA\u7740\u836F\u7269\u7684\u8309\u8389\u82B1\u6CB9\uFF0C\u8427\u4F5B\u5974\u7684\u808C\u80A4\u6108\u52A0\u5149\u6ED1\u767D\u817B\uFF0C\u9999\u8F6F\u80A5\u5AE9\u7684\u4E73\u623F\u50CF\u5145\u6EE1\u6DB2\u4F53\u822C\u9F13\u80C0\u8D77\u6765\uFF0C\u8FDE\u4E73\u6655\u4E5F\u88AB\u6491\u5F97\u5411\u5468\u56F4\u6269\u6563\u3002\u6BB7\u7EA2\u7684\u4E73\u5934\u7A81\u7FD8\u5176\u4E0A\uFF0C\u968F\u7740\u6025\u4FC3\u7684\u5598\u606F\u4E0D\u4F4F\u98A4\u6296\u3002</p><p>\u94A2\u9488\u523A\u4E0B\uFF0C\u88AB\u8089\u6B32\u5F81\u670D\u7684\u8427\u4F5B\u5974\u987F\u65F6\u75DB\u53EB\u5931\u58F0\uFF0C\u5A07\u8EAF\u5267\u98A4\u3002</p><p>\u7D2B\u73AB\u7AED\u529B\u6491\u8D77\u8EAB\u4F53\uFF0C\u80F8\u524D\u8179\u4E0A\u5C3D\u662F\u6DCB\u6F13\u7684\u9C9C\u8840\uFF0C\u5979\u4E00\u811A\u8E22\u5728\u6155\u5BB9\u9F99\u8170\u95F4\uFF0C\u4F4E\u53EB\u9053\uFF1A\u300C\u6EDA\u5F00\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u987F\u4E86\u4E00\u4E0B\uFF0C\u65CB\u5373\u54AC\u7D27\u7259\u5173\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u4E00\u8FB9\u5978\u6DEB\u4E00\u8FB9\u7EB9\u523A\u3002\u94A2\u9488\u523A\u5728\u6BCD\u4EB2\u8EAB\u4E0A\uFF0C\u6BD4\u523A\u5728\u81EA\u5DF1\u8EAB\u4E0A\u66F4\u8BA9\u7D2B\u73AB\u75BC\u75DB\uFF0C\u5979\u53C8\u8E22\u53C8\u54AC\u8017\u5C3D\u529B\u6C14\uFF0C\u6700\u7EC8\u4E5F\u65E0\u6CD5\u963B\u6B62\u6155\u5BB9\u9F99\u7684\u75AF\u72C2\u3002\u8427\u4F5B\u5974\u54C0\u54ED\u4E0D\u7EDD\uFF0C\u762B\u8F6F\u7684\u624B\u811A\u5374\u4F7F\u5979\u65E0\u6CD5\u8EB2\u907F\u3002\u4E0D\u591A\u65F6\uFF0C\u96EA\u767D\u7684\u5C0F\u8179\u4E0A\u4FBF\u9C9C\u8840\u6A2A\u6D41\u3002</p><p>\u6155\u5BB9\u9F99\u66F2\u6307\u4E00\u5F39\uFF0C\u5C06\u94A2\u9488\u786C\u751F\u751F\u9489\u5165\u77F3\u7891\u3002\u7136\u540E\u8BA9\u767D\u6C0F\u59D0\u59B9\u64E6\u51C0\u4E24\u5973\u8EAB\u4E0A\u7684\u8840\u8FF9\u3002</p><p>\u4E4D\u770B\u6765\u6BCD\u5973\u4FE9\u7389\u4F53\u6A2A\u9648\uFF0C\u6BEB\u65E0\u5F02\u72B6\u3002\u4F46\u7247\u523B\u540E\uFF0C\u4E24\u5177\u7C89\u5AE9\u7684\u5973\u4F53\u540C\u65F6\u6CDB\u51FA\u7EC6\u5BC6\u7684\u8840\u8FF9\u3002</p><p>\u8427\u4F5B\u5974\u5446\u5446\u770B\u7740\u81EA\u5DF1\u7684\u5C0F\u8179\u3002\u5706\u6EDA\u6EDA\u7684\u809A\u76AE\u4E0A\uFF0C\u4E00\u6735\u7855\u5927\u7684\u7261\u4E39\u6B63\u5728\u767D\u51C0\u7684\u808C\u80A4\u95F4\u6084\u7136\u76DB\u5F00\u3002\u4F18\u7F8E\u7684\u82B1\u74E3\u4ECE\u9634\u961C\u4E0A\u7F18\u4E00\u76F4\u5EF6\u4F38\u5230\u80F8\u4E0B\uFF0C\u8986\u76D6\u4E86\u6574\u4E2A\u5C0F\u8179\u3002</p><p>\u7D2B\u73AB\u6CA1\u6709\u671D\u81EA\u5DF1\u8EAB\u4E0A\u770B\u4E00\u773C\uFF0C\u53EA\u51B7\u51B7\u76EF\u7740\u6155\u5BB9\u9F99\u3002</p><p>\u4ED9\u5B50\u822C\u7684\u5A07\u8EAF\u4E0A\u663E\u51FA\u4E00\u53EA\u5C55\u7FFC\u9AD8\u98DE\u7684\u8840\u8272\u51E4\u51F0\u3002\u51E4\u51F0\u5DE6\u7FFC\u4ECE\u4E73\u4E0B\u63A0\u8FC7\uFF0C\u7FFC\u5C16\u7ED5\u5230\u4E73\u623F\u4E0A\u4FA7\uFF0C\u5B9B\u5982\u4E00\u53EA\u5F20\u5F00\u7684\u624B\u638C\u8F7B\u8F7B\u6258\u4F4F\u5927\u534A\u53EA\u5DE6\u4E73\uFF1B\u53F3\u7FFC\u7565\u77ED\uFF0C\u7FFC\u5C16\u5374\u5F84\u76F4\u4F38\u5165\u575A\u633A\u7684\u53F3\u4E73\uFF0C\u4E00\u76F4\u89E6\u5230\u7C89\u7EA2\u7684\u4E73\u6655\uFF1B\u51E4\u5934\u626C\u5728\u5DE6\u80C1\u4E4B\u4E0B\uFF0C\u51E4\u4F53\u6A2A\u8FC7\u5C0F\u8179\uFF0C\u8DB3\u5C16\u843D\u5728\u7EA2\u5AE9\u7684\u82B1\u74E3\u95F4\uFF1B\u957F\u957F\u7684\u5C3E\u7FCE\u6CBF\u7740\u8D77\u4F0F\u7684\u9999\u808C\uFF0C\u4ECE\u8179\u80A1\u6C9F\u7A7F\u8FC7\uFF0C\u6700\u540E\u6D88\u5931\u5728\u53F3\u817F\u5916\u4FA7\u3002</p><p>\u6155\u5BB9\u9F99\u4E45\u4E45\u6CE8\u89C6\u7740\u8FD9\u53EA\u5360\u636E\u4E86\u5927\u534A\u5A07\u8EAF\u7684\u51E4\u51F0\uFF0C\u76EE\u4E2D\u5F02\u5F69\u8FDE\u73B0\u3002\u76F4\u5230\u6A2A\u6EA2\u7684\u9C9C\u8840\u6A21\u7CCA\u4E86\u51E4\u51F0\u7684\u8F6E\u5ED3\uFF0C\u4ED6\u624D\u76F4\u8D77\u8170\u8EAB\uFF0C\u6B64\u65F6\u518D\u4E5F\u65E0\u6CD5\u538B\u6291\u5FC3\u4E2D\u7684\u6FC0\u52A8\uFF0C\u58F0\u97F3\u53D1\u98A4\u5730\u8BF4\u9053\uFF1A\u300C\u4EE5\u6155\u5BB9\u6C0F\u4EC5\u5269\u7684\u9C9C\u8840\u4E3A\u796D\uFF0C\u7956\u5B97\u4EEC\u5E94\u8BE5\u7791\u76EE\u4E86\u5427\u3002\u300D</p><p>\u8BF4\u7740\u62AC\u624B\u6309\u5728\u81EA\u5DF1\u80A9\u5934\uFF0C\u6307\u5C16\u4ECE\u53F3\u80A9\u5230\u5DE6\u80EF\u8F7B\u8F7B\u4E00\u5212\u3002\u7ED3\u5B9E\u7684\u76AE\u80A4\u5E94\u6307\u7EFD\u88C2\uFF0C\u5212\u51FA\u4E00\u9053\u957F\u957F\u7684\u8840\u75D5\u3002</p><p>\u6155\u5BB9\u9F99\u6218\u6817\u7740\u62B1\u7D27\u7D2B\u73AB\uFF0C\u5C06\u5F7C\u6B64\u7684\u4F24\u53E3\u7D27\u7D27\u8D34\u4F4F\uFF0C\u8BA9\u5144\u59B9\u4FE9\u7684\u9C9C\u8840\u5C3D\u60C5\u6D41\u6DCC\u5728\u4E00\u8D77\u3002</p><p>\u4F46\u7D2B\u73AB\u770B\u7740\u4ED6\u7684\u773C\u795E\u91CC\u5374\u6CA1\u6709\u4E1D\u6BEB\u611F\u60C5\uFF0C\u5982\u679C\u6709\uFF0C\u4E5F\u662F\u618E\u6076\u4E0E\u4EC7\u6068\u3002</p><p>\u4E0D\u77E5\u4F55\u65F6\uFF0C\u5929\u5730\u95F4\u5DF2\u7ECF\u6697\u4E86\u4E0B\u6765\u3002\u9ED1\u6C89\u6C89\u7684\u4E4C\u4E91\u906E\u6CA1\u4E86\u9633\u5149\uFF0C\u7A7A\u6C14\u4E2D\u4F3C\u4E4E\u9971\u542B\u7740\u51B0\u51B7\u7684\u6C34\u6C14\uFF0C\u538B\u5F97\u4EBA\u5598\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u5FFD\u7136\u95F4\uFF0C\u4E00\u9053\u95EA\u7535\u5212\u7834\u957F\u7A7A\uFF0C\u7D27\u63A5\u7740\u70B8\u96F7\u63A5\u8FDE\u54CD\u8D77\u3002</p><p>\u4F34\u968F\u7740\u96F7\u58F0\uFF0C\u6155\u5BB9\u9F99\u568E\u53EB\u7740\u8FDB\u5165\u7D2B\u73AB\u4F53\u5185\u3002</p><p>\u66B4\u96E8\u503E\u76C6\uFF0C\u72C2\u98CE\u5927\u4F5C\uFF0C\u53D7\u60CA\u7684\u5750\u9A91\u75AF\u72C2\u5730\u6323\u52A8\u8F94\u5934\uFF0C\u4E0D\u987E\u4E00\u5207\u5730\u626F\u5230\u7F30\u7EF3\u3002</p><p>\u7247\u523B\u95F4\uFF0C\u767D\u6C0F\u59D0\u59B9\u4FBF\u5468\u8EAB\u5C3D\u6E7F\uFF0C\u4E24\u5973\u7AD9\u5728\u8F66\u65C1\uFF0C\u8C01\u90FD\u4E0D\u6562\u5230\u8F66\u5185\u907F\u96E8\u3002</p><p>\u5929\u5730\u7684\u72C2\u5578\u63A9\u76D6\u4E86\u6240\u6709\u7684\u58F0\u97F3\uFF0C\u773C\u524D\u7684\u4E00\u5207\u4F3C\u4E4E\u662F\u4E00\u573A\u65E0\u58F0\u65E0\u606F\u7684\u54D1\u5267\u3002\u96EA\u767D\u7684\u6BDB\u6BEF\u4EFF\u4F5B\u6CE5\u6CDE\u4E2D\u7684\u4E00\u7247\u767D\u5E06\uFF0C\u4E09\u5177\u9C9C\u8840\u4EA4\u6D41\u7684\u8EAB\u4F53\u5728\u5176\u4E2D\u7FFB\u6EDA\u7EA0\u7F20\uFF0C\u5206\u4E0D\u6E05\u5F7C\u6B64\u3002</p><p>\u72F0\u72DE\u7684\u5DE8\u9633\u5076\u7136\u4E00\u73B0\uFF0C\u65CB\u5373\u53C8\u94BB\u8FDB\u96EA\u767D\u7684\u8EAB\u4F53\u3002\u81F3\u4E8E\u662F\u6BCD\u4EB2\u8FD8\u662F\u5973\u513F\uFF0C\u662F\u524D\u9634\u8FD8\u662F\u540E\u5EAD\uFF0C\u6CA1\u6709\u4EBA\u96BE\u591F\u5206\u6E05\u695A\u3002\u751A\u81F3\u8FDE\u6155\u5BB9\u9F99\u3001\u8FDE\u6155\u5BB9\u7D2B\u73AB\u3001\u8FDE\u8427\u4F5B\u5974\u90FD\u65E0\u6CD5\u5206\u6E05\u3002</p><p>\u5927\u5730\u9690\u9690\u9707\u52A8\uFF0C\u65E0\u8FB9\u7684\u957F\u8349\u5C3D\u6570\u5728\u98CE\u96E8\u4E2D\u5043\u4F0F\u3002\u767D\u6BEF\u4E0A\u6EE1\u6EA2\u7684\u9C9C\u8840\u6DF7\u7740\u96E8\u6C34\u56DB\u4E0B\u6D41\u6DCC\uFF0C\u6700\u540E\u4ECE\u6BDB\u6BEF\u8FB9\u7F18\u6EDA\u843D\u3002\u4F5C\u4E3A\u796D\u54C1\u4E00\u6EF4\u6EF4\u6E17\u5165\u6155\u5BB9\u6C0F\u7956\u9675\u7684\u6CE5\u571F\u4E2D\u3002</p>',53);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
