import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_98" tabindex="-1"><a class="header-anchor" href="#_98" aria-hidden="true">#</a> 98</h1><p>\u9ECE\u660E\u65F6\u5206\uFF0C\u4E09\u9A91\u56DB\u4E58\u79BB\u5F00\u8352\u57CE\uFF0C\u9A70\u5165\u832B\u832B\u8349\u6D77\u3002</p><p>\u5343\u91CC\u5BFB\u5B9D\u5374\u7A7A\u624B\u800C\u8FD4\uFF0C\u8FD9\u7B14\u610F\u6599\u4E4B\u5185\u7684\u8D22\u5BCC\u843D\u7A7A\uFF0C\u5F71\u54CD\u4E86\u6574\u4E2A\u590D\u56FD\u5927\u8BA1\uFF0C\u4E8E\u662F\u6155\u5BB9\u9F99\u796D\u8FC7\u7956\u9675\u540E\u4E0D\u7B49\u4F11\u606F\u7ACB\u523B\u4FBF\u8E0F\u4E0A\u5F52\u7A0B\u3002\u6B64\u756A\u4ED6\u8F7B\u9A91\u7F13\u4ECE\uFF0C\u53EA\u4E0E\u7D2B\u73AB\u3001\u7075\u7389\u5404\u4E58\u4E00\u9A91\uFF0C\u81EA\u5DF1\u62B1\u7740\u8427\u4F5B\u5974\u4E00\u8DEF\u5357\u4E0B\u3002</p><p>\u300C\u6211\u4E0D\u5728\u4E4E\u4F60\u7684\u6B7B\u6D3B\uFF0C\u53EA\u662F\u4E3A\u6211\u7684\u513F\u5B50\u7740\u60F3\u3002\u300D\u6155\u5BB9\u9F99\u8FD9\u6837\u8BF4\u7740\uFF0C\u7ED9\u4E0D\u5B9C\u4E58\u9A6C\u7684\u7D2B\u73AB\u6062\u590D\u4E86\u4E09\u6210\u529F\u529B\u3002</p><p>\u5316\u771F\u6563\u88AB\u7D2B\u73AB\u5012\u6389\u4E4B\u540E\uFF0C\u6240\u4F59\u65E0\u51E0\u7684\u836F\u6563\u90FD\u7559\u7ED9\u4E86\u96EA\u5CF0\u795E\u5C3C\u3002\u6155\u5BB9\u9F99\u3001\u6C90\u58F0\u4F20\u548C\u53F6\u884C\u5357\u4E09\u4EBA\u8054\u624B\uFF0C\u7ED9\u7D2B\u73AB\u65BD\u4E0B\u91CD\u697C\u6C14\u9501\uFF0C\u4EE5\u51DD\u6C14\u548C\u622A\u8109\u7684\u624B\u6CD5\u5236\u4F4F\u5979\u7684\u771F\u6C14\u3002</p><p>\u6B64\u6CD5\u4EE5\u533B\u7406\u5165\u624B\uFF0C\u82E5\u975E\u6DF1\u6089\u5176\u4E2D\u5965\u5999\uFF0C\u5373\u4F7F\u8EAB\u6000\u7EDD\u4E16\u6B66\u529F\uFF0C\u4E5F\u65E0\u6CD5\u89E3\u5F00\u3002\u8FD9\u4E09\u6210\u529F\u529B\u53EA\u80FD\u4F7F\u7D2B\u73AB\u5C11\u53D7\u4E9B\u98A0\u7C38\u4E4B\u82E6\u800C\u5DF2\u3002</p><p>\u91D1\u5F00\u7532\u4E0E\u5341\u4F59\u540D\u5E2E\u4F17\u7559\u5728\u9F99\u57CE\uFF0C\u4E00\u65B9\u9762\u63A2\u67E5\u56DB\u5468\u5EFA\u7ACB\u8425\u5E10\uFF0C\u4E00\u65B9\u9762\u7B49\u5F85\u8D6B\u8FDE\u96C4\u7B49\u4EBA\u7684\u5230\u6765\u3002\u767D\u7389\u83BA\u767D\u7389\u9E42\u5219\u4E0E\u4E5E\u4F0F\u7A77\u9686\u7B49\u4EBA\u540C\u884C\u3002\u8FDE\u8FD9\u5BF9\u4F3A\u5019\u7231\u59BE\u7684\u59D0\u59B9\u82B1\u4E5F\u4E0D\u5E26\uFF0C\u53EF\u89C1\u6155\u5BB9\u9F99\u786E\u662F\u5F52\u5FC3\u4F3C\u7BAD\u3002</p><p>\u56DB\u4EBA\u6653\u884C\u591C\u5BBF\uFF0C\u4E00\u8DEF\u6025\u884C\u3002\u4E0D\u8FC7\u56DB\u5929\u65F6\u95F4\uFF0C\u4ED6\u4EEC\u4FBF\u9A70\u8FC7\u6765\u65F6\u8D70\u4E86\u5341\u5929\u7684\u8DEF\u7A0B\uFF0C\u6765\u5230\u5F53\u65E5\u8650\u6740\u5510\u989C\u7684\u5730\u65B9\u3002</p><p>\u6155\u5BB9\u9F99\u6E38\u76EE\u56DB\u987E\uFF0C\u5374\u4E0D\u89C1\u90A3\u6839\u7A7F\u7740\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u7684\u6728\u6869\u3002</p><p>\u300C\u5BAB\u4E3B\uFF01\u300D\u7075\u7389\u4E00\u63D0\u7F30\u7EF3\uFF0C\u6307\u7740\u8FDC\u5904\u7684\u8349\u4E1B\u3002</p><p>\u8349\u5730\u4E0A\u7AD6\u7740\u4E00\u622A\u77ED\u77ED\u7684\u6B8B\u6869\uFF0C\u9AD8\u4EC5\u53CA\u624B\uFF0C\u65AD\u53E3\u53C2\u5DEE\u4E0D\u9F50\uFF0C\u56DB\u4E0B\u6728\u5C51\u7EB7\u7EB7\uFF0C\u50CF\u88AB\u949D\u5668\u4E00\u70B9\u70B9\u6311\u788E\u4E00\u822C\u3002\u5230\u4E09\u5206\u4E4B\u4E00\u5904\u5374\u7A81\u7136\u4E00\u6298\u800C\u65AD\u3002</p><p>\u64C5\u957F\u8FFD\u8E2A\u7684\u7075\u7389\u95ED\u76EE\u51DD\u606F\uFF0C\u5FFD\u7136\u5954\u5230\u4E1C\u4FA7\u6311\u5F00\u957F\u8349\u3002</p><p>\u8349\u95F4\u6254\u7740\u53E6\u4E00\u6BB5\u4E03\u5C3A\u957F\u7684\u6728\u6869\uFF0C\u65AD\u53E3\u4E0E\u6B8B\u6869\u4E00\u822C\u65E0\u4E8C\uFF0C\u7C97\u903E\u5927\u817F\u7684\u6869\u8EAB\u904D\u5E03\u8840\u8FF9\u3002\u6869\u5C16\u4E09\u5C3A\u5DE6\u53F3\u5C3D\u6570\u88AB\u5E72\u6DB8\u7684\u8840\u8FF9\u67D3\u6210\u9ED1\u8272\u3002\u8349\u53F6\u95F4\u65F6\u9690\u65F6\u73B0\u7684\u8840\u8FF9\u4E00\u8DEF\u671D\u4E1C\u6D12\u53BB\u3002</p><p>\u7D2B\u73AB\u6084\u6084\u5F20\u671B\uFF0C\u6CA1\u770B\u5230\u81EA\u5DF1\u6254\u4E0B\u7684\u5305\u88F9\uFF0C\u5FC3\u91CC\u7565\u5FAE\u5BBD\u4E86\u4E9B\u3002</p><p>\u300C\u8FD9\u5C0F\u5B50\u5148\u662F\u7259\u54AC\uFF0C\u89E3\u5F00\u7A74\u9053\u540E\u51FB\u65AD\u6728\u6869\uFF0C\u5E26\u8D70\u5510\u5A4A\u5B50\u7684\u5C38\u4F53\u3002\u54FC\u54FC\uFF0C\u4E0D\u77E5\u9053\u5510\u5A4A\u5B50\u5C44\u91CC\u6345\u8FDB\u4E09\u5C3A\u957F\u7684\u6728\u6869\u662F\u600E\u4E48\u8DDF\u513F\u5B50\u8BF4\u8BDD\u7684\u3002\u62D4\u51FA\u8FD9\u6869\u5B50\u4E5F\u8D39\u4E86\u4E0D\u5C11\u5DE5\u592B\u5427\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u671B\u7740\u65E0\u8FB9\u7684\u8349\u539F\uFF0C\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u597D\u5C0F\u5B50\uFF0C\u597D\u79CD\uFF01\u300D</p><p>\u9F99\u6714\u4E0D\u8FC7\u662F\u4E2A\u516B\u5C81\u7684\u5B69\u5B50\uFF0C\u600E\u4E48\u53EF\u80FD\u9003\u51FA\u8FD9\u8352\u65E0\u4EBA\u70DF\u7684\u8349\u6D77\uFF1F\u4E24\u4EBA\u5E76\u4E0D\u628A\u4ED6\u653E\u5728\u5FC3\u4E0A\uFF0C\u7EB5\u7136\u770B\u5230\u8349\u95F4\u7684\u8840\u8FF9\u4E5F\u4E0D\u52A0\u7406\u4F1A\uFF0C\u8FF3\u76F4\u6253\u9A6C\u5357\u4E0B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u516B\u6708\u4E8C\u5341\u4E03\uFF0C\u6DBF\u90E1\u3002</p><p>\u5317\u56FD\u79CB\u65E9\uFF0C\u672A\u5165\u4E5D\u6708\u5DF2\u662F\u904D\u5C71\u7EA2\u53F6\u3002</p><p>\u665A\u98CE\u5E26\u7740\u91D1\u94C1\u822C\u7684\u521A\u5389\u626B\u8FC7\u67AB\u6797\uFF0C\u7EA2\u53F6\u6F6E\u6C34\u822C\u6D8C\u8FC7\u5F2F\u66F2\u7684\u5C71\u8DEF\u3002\u5BC2\u9759\u7684\u66AE\u8272\u4E2D\uFF0C\u4E00\u9635\u9A6C\u8E44\u58F0\u6E10\u884C\u6E10\u8FD1\u3002</p><p>\u5F53\u524D\u4E00\u9A91\u662F\u4E2A\u82F1\u4FCA\u7684\u7537\u5B50\uFF0C\u767D\u6670\u7684\u76AE\u80A4\u548C\u633A\u76F4\u7684\u9F3B\u6881\u663E\u793A\u51FA\u9C9C\u5351\u4EBA\u7684\u8840\u7EDF\u3002</p><p>\u4ED6\u6000\u4E2D\u62B1\u7740\u4E00\u56E2\u8C82\u88D8\uFF0C\u8DCB\u6D89\u7ADF\u65E5\u6BEB\u65E0\u75B2\u6001\uFF0C\u795E\u8272\u5E73\u9759\u5F97\u6709\u4E9B\u9634\u51B7\u3002\u65C1\u8FB9\u4E00\u9A91\u906E\u7740\u9762\u7EB1\uFF0C\u4ECE\u62AB\u98CE\u4E0B\u4F9D\u7A00\u663E\u9732\u7684\u7A88\u7A95\u9999\u80A9\u770B\u5F97\u51FA\u662F\u4E00\u4E2A\u5C11\u5973\u3002\u5728\u65C1\u4EBA\u773C\u4E2D\uFF0C\u3010www.Zei8.me \u8D3C\u5427\u7535\u5B50\u4E66\u3011\u8FD9\u591A\u534A\u662F\u4E00\u5BF9\u65B0\u5A5A\u7684\u5C0F\u592B\u59BB\u7740\u6025\u7740\u8D76\u8DEF\u3002\u6700\u540E\u4E00\u9A91\u5374\u662F\u4E2A\u9EC4\u51A0\u9053\u4EBA\uFF0C\u6025\u9A70\u95F4\u5927\u8896\u98D8\u98D8\uFF0C\u4ED9\u8C8C\u5CB8\u7136\u3002</p><p>\u4E09\u4EBA\u90FD\u662F\u9A91\u672F\u8FC7\u4EBA\uFF0C\u5373\u4F7F\u662F\u5D0E\u5C96\u7684\u5C71\u8DEF\u4E5F\u7EB5\u9A6C\u5982\u98DE\uFF0C\u7728\u773C\u4FBF\u7ED5\u8FC7\u5C71\u5773\uFF0C\u6765\u5230\u4E00\u7247\u7A7A\u65F7\u7684\u5C71\u8C37\u3002</p><p>\u9053\u4EBA\u795E\u8272\u4E00\u52A8\uFF0C\u6B63\u5F85\u5F00\u53E3\uFF0C\u5F53\u5148\u7684\u5E74\u8F7B\u7537\u5B50\u5DF2\u7ECF\u52D2\u4F4F\u9A6C\u5339\u3002</p><p>\u6025\u5267\u7684\u8E44\u58F0\u560E\u7136\u800C\u6B62\uFF0C\u9A6C\u5339\u539F\u5730\u8E0F\u7740\u788E\u6B65\uFF0C\u5728\u843D\u53F6\u4E2D\u8E29\u51FA\u4E00\u9635\u8106\u8106\u7684\u7EC6\u54CD\u3002</p><p>\u5E74\u8F7B\u7537\u5B50\u633A\u76F4\u80F8\u819B\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u661F\u6708\u6E56\u6155\u5BB9\u9F99\u9014\u7ECF\u6B64\u5730\uFF0C\u4E0D\u77E5\u54EA\u4F4D\u670B\u53CB\u5C48\u5C0A\u6765\u89C1\uFF1F\u300D</p><p>\u4E00\u58F0\u51B7\u54FC\u4ECE\u524D\u65B9\u4F20\u6765\uFF0C\u63A5\u7740\u4E00\u6761\u4EBA\u5F71\u4ECE\u5D16\u4E0A\u4E00\u8DC3\u800C\u4E0B\uFF0C\u4EBA\u5728\u534A\u7A7A\uFF0C\u51CC\u5389\u7684\u5200\u6C14\u5DF2\u7136\u53CA\u4F53\u3002\u6155\u5BB9\u9F99\u53F3\u638C\u4E00\u7FFB\uFF0C\u4E00\u628A\u634F\u4F4F\u5200\u950B\uFF0C\u51B0\u5BD2\u7684\u592A\u4E00\u52B2\u4E00\u5410\u5373\u6536\u3002\u90A3\u4EBA\u5982\u53D7\u96F7\u6B9B\uFF0C\u843D\u5728\u5730\u4E0A\u8FDE\u9000\u6570\u6B65\u624D\u7A33\u4F4F\u8EAB\u5F62\u3002</p><p>\u6155\u5BB9\u9F99\u6253\u91CF\u7740\u957F\u5200\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u539F\u6765\u662F\u6CB3\u95F4\u5B9A\u9633\u5200\u738B\u5FB7\u738B\u5927\u4FA0\u3002\u300D</p><p>\u5C71\u6797\u4E2D\u4EBA\u5F71\u7EB7\u73B0\uFF0C\u6321\u4F4F\u53BB\u8DEF\u3002\u4E00\u6761\u5927\u6C49\u6392\u4F17\u800C\u51FA\uFF0C\u671D\u4E09\u4EBA\u6012\u76EE\u800C\u89C6\u3002</p><p>\u6155\u5BB9\u9F99\u5DF2\u542C\u5230\u7075\u7389\u7684\u6307\u70B9\uFF0C\u6717\u58F0\u957F\u7B11\u9053\uFF1A\u300C\u7A0B\u5821\u4E3B\u4ECE\u4E1C\u83B1\u8D76\u5230\u6B64\u5904\uFF0C\u4E0D\u77E5\u6709\u4F55\u6307\u6559\uFF1F\u300D</p><p>\u6765\u8005\u6B63\u662F\u4E1C\u83B1\u5A01\u8FDC\u5821\u5821\u4E3B\u7A0B\u4E00\u9E4F\uFF0C\u4ED6\u9A88\u6307\u558A\u9053\uFF1A\u300C\u661F\u6708\u6E56\u5996\u5B7D\uFF01\u8FC7\u6765\u53D7\u6B7B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u300C\u5728\u4E0B\u662F\u661F\u6708\u6E56\u5BAB\u4E3B\u4E0D\u5047\uFF0C\u4F46\u675C\u5821\u4E3B\u4E3A\u4F55\u9A82\u5728\u4E0B\u5996\u5B7D\uFF1F\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u4F17\u4EBA\u987F\u65F6\u54D7\u7136\u3002</p><p>\u300C\u4F60\u4EEC\u661F\u6708\u6E56\u4F5C\u6076\u591A\u7AEF\uFF0C\u600E\u4E48\u4E0D\u662F\u5996\u5B7D\uFF01\u300D</p><p>\u300C\u5C11\u8DDF\u4ED6\u4EEC\u5E9F\u8BDD\uFF0C\u8BA9\u6211\u780D\u4E86\u4ED6\uFF0C\u4E3A\u5B59\u5E2E\u4E3B\u62A5\u4EC7\uFF01\u300D</p><p>\u5F53\u65E5\u516B\u6781\u95E8\u6765\u88AD\uFF0C\u6155\u5BB9\u9F99\u5DF2\u662F\u5927\u5927\u540E\u6094\uFF0C\u600E\u4E48\u5FD8\u4E86\u6740\u6389\u9648\u5A01\u706D\u53E3\uFF0C\u8BA9\u4ED6\u4EEC\u6B7B\u65E0\u5BF9\u8BC1\u3002\u597D\u5728\u767E\u6218\u5929\u9F99\u5DF2\u5728\u585E\u5916\u88AB\u91D1\u5F00\u7532\u65A9\u6740\uFF0C\u6155\u5BB9\u9F99\u5FC3\u4E0B\u51B7\u7B11\uFF0C\u9762\u4E0A\u5374\u4E00\u65E0\u6240\u52A8\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u5404\u4F4D\u6307\u8D23\u6211\u661F\u6708\u6E56\u4F5C\u6076\u591A\u7AEF\uFF0C\u53EF\u6709\u4EC0\u4E48\u8BC1\u636E\uFF1F\u300D</p><p>\u7A0B\u4E00\u9E4F\u7B49\u4EBA\u9762\u9762\u76F8\u89D1\uFF0C\u661F\u6708\u6E56\u884C\u4E8B\u4E00\u5411\u9690\u853D\uFF0C\u6C5F\u6E56\u4E2D\u77E5\u8005\u751A\u5C11\u3002\u4ECA\u5E74\u521D\u7A81\u7136\u8F70\u4F20\u98D8\u6885\u5CF0\u8BF8\u5973\u88AB\u661F\u6708\u6E56\u63B3\u5165\u6559\u4E2D\u4E3A\u5974\uFF0C\u6240\u53D7\u6DEB\u8650\u4EE4\u4EBA\u53D1\u6307\u3002</p><p>\u63A5\u7740\u661F\u6708\u6E56\u53C8\u5BA3\u5E03\u5BAB\u4E3B\u8FCE\u5A36\u98D8\u6885\u5CF0\u5173\u95E8\u5F1F\u5B50\uFF0C\u73AB\u7470\u4ED9\u5B50\u6155\u5BB9\u7D2B\u73AB\u4E3A\u59BB\uFF0C\u5E7F\u9080\u90AA\u9053\u4EBA\u7269\u4E0E\u4F1A\uFF0C\u6B32\u56FE\u4E0D\u5229\u4E8E\u6B66\u6797\u3002\u751A\u81F3\u4F20\u95FB\u6709\u5929\u4E0B\u7B2C\u4E00\u4E4B\u79F0\u7684\u96EA\u5CF0\u795E\u5C3C\u4E5F\u5728\u5A5A\u793C\u4E2D\u51FA\u73B0\uFF0C\u88AB\u5F53\u4F5C\u6027\u5974\u4F9B\u6765\u5BBE\u6DEB\u8FB1\u3002</p><p>\u5C31\u5728\u4F17\u4EBA\u60CA\u7591\u4E0D\u5B9A\u4E2D\uFF0C\u5FFD\u7136\u53C8\u4F20\u51FA\u5E7F\u9633\u5E2E\u88AB\u706D\u4E5F\u662F\u661F\u6708\u6E56\u6240\u4E3A\uFF0C\u53EF\u8FD9\u4E9B\u90FD\u662F\u4F20\u95FB\uFF0C\u82E5\u8981\u62FF\u51FA\u8BC1\u636E\u5374\u6709\u6240\u4E0D\u80FD\u3002</p><p>\u98D8\u6885\u5CF0\u867D\u7136\u540D\u6EE1\u5929\u4E0B\uFF0C\u4F46\u6781\u5C11\u4E0E\u6C5F\u6E56\u4EBA\u58EB\u6765\u5F80\uFF0C\u53CD\u4E0D\u53CA\u5E7F\u9633\u5E2E\u8FD9\u6837\u7684\u5C0F\u5E2E\u4F1A\u4EB2\u53CB\u4F17\u591A\uFF0C\u5F53\u4E0B\u5A01\u8FDC\u5821\u3001\u94C1\u9CA8\u5E2E\u7B49\u5341\u4F59\u4E2A\u5E2E\u4F1A\u8054\u624B\u51FA\u51FB\uFF0C\u8981\u4E3A\u6B66\u6797\u9664\u53BB\u6B64\u5BB3\u3002</p><p>\u4F17\u4FA0\u5BA2\u539F\u672C\u8BA1\u5212\u5728\u5C71\u8DEF\u4E2D\u8BBE\u4E0B\u5708\u5957\uFF0C\u56F4\u6B7C\u661F\u6708\u6E56\u7FA4\u5996\uFF0C\u6B64\u65F6\u88AB\u8BC6\u7834\u673A\u5173\uFF0C\u5728\u524D\u65B9\u57CB\u4F0F\u7684\u4F17\u4EBA\u4E5F\u5954\u4E86\u56DE\u6765\uFF0C\u4E94\u516D\u5341\u5F20\u5634\u5BF9\u7740\u4E09\u4EBA\u559D\u9A82\u8FDE\u58F0\u3002</p><p>\u7D2B\u73AB\u5FC3\u82B1\u6012\u653E\uFF0C\u53EA\u7B49\u4E24\u8FB9\u4EA4\u4E0A\u624B\uFF0C\u81EA\u5DF1\u5C31\u8D81\u4E71\u9003\u8D70\u3002\u7A0B\u4E00\u9E4F\u8FD9\u6279\u4EBA\u518D\u4E0D\u80FD\u6253\uFF0C\u4E5F\u80FD\u628A\u6155\u5BB9\u9F99\u7F20\u4E0A\u4E00\u4E2A\u65F6\u8FB0\u5427\u3002</p><p>\u6155\u5BB9\u9F99\u626C\u58F0\u9053\uFF1A\u300C\u8BF8\u4F4D\u4EE5\u4FA0\u4E49\u81EA\u5C45\uFF0C\u65E0\u51ED\u65E0\u636E\u4E3A\u4F55\u6307\u8D23\u6211\u7B49\u4E3A\u5996\u5B7D\uFF1F\u300D</p><p>\u7A0B\u4E00\u9E4F\u53EB\u9053\uFF1A\u300C\u4F60\u661F\u6708\u6E56\u9080\u96C6\u6B66\u6797\u8D25\u7C7B\uFF0C\u72FC\u72C8\u4E3A\u5978\uFF0C\u5982\u4F55\u4E0D\u662F\u5996\u5B7D\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u9053\uFF1A\u300C\u5728\u4E0B\u5927\u5A5A\u65F6\u6765\u7684\u53EA\u6709\u5BBE\u5BA2\uFF0C\u6CA1\u89C1\u8FC7\u4EC0\u4E48\u6B66\u6797\u8D25\u7C7B\u3002\u300D</p><p>\u94C1\u9CA8\u5E2E\u526F\u5E2E\u4E3B\u6C99\u5FD7\u52C7\u4E00\u626C\u94C1\u6756\uFF0C\u53EB\u9053\uFF1A\u300C\u5996\u5B7D\uFF01\u8FD8\u6562\u72E1\u8FA9\uFF0C\u5148\u5403\u6211\u4E00\u6756\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u9A91\u5728\u9A6C\u4E0A\u8EAB\u4E0D\u52A8\u624B\u4E0D\u8D77\uFF0C\u811A\u5C16\u4E00\u62AC\u6B63\u8E22\u4E2D\u6756\u5C16\u3002\u4ED6\u8FD9\u4E00\u811A\u5B8C\u5168\u80FD\u5C06\u6C99\u5FD7\u52C7\u8E22\u4E2A\u65A4\u6597\uFF0C\u4F46\u5410\u52B2\u65F6\u5374\u7559\u4E86\u4E03\u5206\uFF0C\u53EA\u8BA9\u4ED6\u9000\u4E86\u4E00\u6B65\u3002</p><p>\u4F17\u4EBA\u89C1\u4ED6\u968F\u624B\u6325\u6D12\u4FBF\u903C\u9000\u4E24\u4EBA\uFF0C\u5FC3\u77E5\u6B64\u4EBA\u6781\u662F\u96BE\u7F20\uFF0C\u82E5\u975E\u8FD9\u4E00\u8D9F\u6709\u767D\u9053\u7684\u9876\u5C16\u9AD8\u624B\u62BC\u9635\uFF0C\u80DC\u8D1F\u96BE\u6599\u3002</p><p>\u7A0B\u4E00\u9E4F\u9AD8\u58F0\u9053\uFF1A\u300C\u4ECA\u65E5\u767D\u9053\u5341\u4E03\u6D3E\u8054\u624B\uFF0C\u8A93\u8981\u6E05\u527F\u661F\u6708\u6E56\u5996\u5B7D\uFF0C\u4E3A\u6B66\u6797\u9664\u5BB3\uFF01\u300D</p><p>\u300C\u5594\uFF0C\u5341\u4E03\u6D3E\uFF0C\u597D\u5389\u5BB3\u597D\u5389\u5BB3\u3002\u6570\u5341\u4EBA\u56F4\u653B\u5728\u4E0B\u533A\u533A\u6570\u4EBA\uFF0C\u7A0B\u5821\u4E3B\u771F\u80FD\u5F20\u5F00\u5634\u3002\u300D\u6155\u5BB9\u9F99\u4E0D\u5C51\u5730\u4E00\u54C2\uFF0C\u8BDD\u867D\u8FD9\u4E48\u8BF4\uFF0C\u4F46\u4EE5\u81EA\u5DF1\u548C\u7075\u7389\u4E24\u4EBA\u4E4B\u529B\u5BF9\u4ED8\u5341\u4E03\u6D3E\u6570\u5341\u4EBA\uFF0C\u8EAB\u8FB9\u8FD8\u6709\u4E2A\u65E0\u6CD5\u884C\u52A8\u7684\u8427\u4F5B\u5974\uFF0C\u4E00\u4E2A\u4E0D\u5B89\u4EFD\u7684\u7D2B\u73AB\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u601D\u7D22\uFF0C\u4E00\u8FB9\u76EE\u5149\u7F13\u7F13\u626B\u8FC7\u5168\u573A\u3002\u8FD9\u4E9B\u4EBA\u6B66\u529F\u5E73\u5E73\uFF0C\u4F46\u6697\u5904\u80AF\u5B9A\u8FD8\u6709\u9AD8\u624B\u57CB\u4F0F\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u91CC\u54AC\u4E86\u54AC\u7259\uFF0C\u9762\u4E0A\u5374\u59CB\u7EC8\u5E26\u7740\u6DE1\u6DE1\u7684\u7B11\u5BB9\uFF0C\u4ED6\u628A\u8C82\u76AE\u56F4\u88F9\u7684\u8427\u4F5B\u5974\u4EA4\u7ED9\u7D2B\u73AB\uFF0C\u7FFB\u8EAB\u4E0B\u9A6C\uFF0C\u6717\u58F0\u9053\uFF1A\u300C\u4E0D\u77E5\u6211\u661F\u6708\u6E56\u4F55\u5904\u5F97\u7F6A\u5404\u4F4D\uFF0C\u5728\u4E0B\u613F\u4E00\u4E00\u8D54\u7F6A\u3002\u300D</p><p>\u8BF4\u7740\u56E2\u56E2\u4E00\u63D6\u3002</p><p>\u542C\u95FB\u661F\u6708\u6E56\u884C\u4E8B\u7684\u5351\u52A3\uFF0C\u4F17\u4EBA\u539F\u4EE5\u4E3A\u5BAB\u4E3B\u662F\u4E2A\u7A77\u51F6\u6781\u6076\u7684\u72C2\u5F92\uFF0C\u6155\u5BB9\u9F99\u5982\u6B64\u8C26\u606D\u6709\u793C\uFF0C\u7740\u5B9E\u8BA9\u4EBA\u51FA\u4E4E\u610F\u6599\u3002</p><p>\u51B7\u573A\u7247\u523B\u540E\uFF0C\u4E00\u4E2A\u7CBE\u58EE\u7684\u6C49\u5B50\u8DC3\u5230\u573A\u4E2D\uFF0C\u559D\u9053\uFF1A\u300C\u5E73\u539F\u5B5F\u4EF2\u575A\u4E3A\u5B59\u5927\u54E5\u62A5\u4EC7\uFF01\u300D</p><p>\u8BF4\u7740\u9F50\u7709\u68CD\u8FCE\u9762\u51FB\u6765\u3002</p><p>\u300C\u5E7F\u9633\u5E2E\u88AB\u957F\u9E70\u4F1A\u6240\u706D\uFF0C\u300D\u8BF4\u4E86\u4E5D\u4E2A\u5B57\u6155\u5BB9\u9F99\u4E5F\u8FDE\u51FA\u4E5D\u62DB\uFF0C\u4E24\u624B\u4E0A\u683C\u4E0B\u5C01\uFF0C\u53EA\u5B88\u4E0D\u653B\uFF0C\u6700\u540E\u624B\u638C\u5947\u5999\u7684\u4E00\u9519\uFF0C\u5C06\u821E\u5F97\u6B63\u6025\u7684\u9F50\u7709\u68CD\u8F7B\u8F7B\u593A\u4E0B\uFF0C\u300C\u4E0D\u5E72\u6211\u661F\u6708\u6E56\u4E4B\u4E8B\u3002\u300D\u6155\u5BB9\u9F99\u7B11\u7740\u5C06\u8BDD\u8BF4\u5B8C\uFF0C\u628A\u9F50\u7709\u68CD\u9012\u5728\u5B5F\u4EF2\u575A\u624B\u4E2D\u3002</p><p>\u5B5F\u4EF2\u575A\u6123\u6123\u63A5\u8FC7\u9F50\u7709\u68CD\uFF0C\u8FDE\u4ED6\u5982\u4F55\u51FA\u624B\u90FD\u672A\u770B\u6E05\u3002</p><p>\u300C\u957F\u9E70\u4F1A\u662F\u6D1B\u9633\u7B2C\u4E00\u5927\u5E2E\uFF0C\u859B\u5E2E\u4E3B\u667A\u52C7\u53CC\u5168\uFF0C\u5728\u4E0B\u751A\u662F\u76F8\u656C\u3002\u5B59\u5E2E\u4E3B\u4E4B\u6B7B\u7684\u786E\u4F7F\u4EBA\u610F\u5916\uFF0C\u4F46\u4E0E\u6211\u661F\u6708\u6E56\u2026\u2026\u300D\u6155\u5BB9\u9F99\u6B63\u4F83\u4F83\u800C\u8A00\uFF0C\u7A81\u7136\u62AC\u5934\u9053\uFF1A\u300C\u9601\u4E0B\u51FA\u6765\u5427\u3002\u300D</p><p>\u4E00\u9752\u4E00\u9EC4\u4E24\u6761\u4EBA\u5F71\u4ECE\u6811\u5DC5\u6D41\u661F\u822C\u5760\u4E0B\uFF0C\u79BB\u5730\u5C3A\u8BB8\u5FAE\u5FAE\u4E00\u987F\uFF0C\u8F7B\u98D8\u98D8\u843D\u5728\u5730\u4E0A\uFF0C\u751A\u81F3\u6CA1\u6709\u8E0F\u788E\u4E00\u7247\u67AF\u53F6\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u4E00\u95EA\uFF0C\u5355\u8FD9\u4E00\u624B\u8F7B\u529F\uFF0C\u6765\u8005\u5DF2\u662F\u6C5F\u6E56\u4E2D\u4E00\u7B49\u4E00\u7684\u4EBA\u7269\uFF0C\u6CA1\u60F3\u5230\u5B59\u540C\u8F89\u7ADF\u7136\u8FD9\u4E48\u6709\u9762\u5B50\u3002</p><p>\u4ECE\u6811\u4E0B\u8DC3\u4E0B\u7684\u662F\u4E00\u7537\u4E00\u5973\uFF0C\u4E24\u4EBA\u90FD\u662F\u4E09\u5341\u4F59\u5C81\uFF0C\u7537\u5B50\u9752\u886B\u5E03\u5DFE\uFF0C\u6C14\u5B87\u8F69\u6602\uFF0C\u5973\u5B50\u8EAB\u7740\u9EC4\u886B\uFF0C\u6DE1\u96C5\u5982\u5170\u3002\u770B\u6E05\u4E24\u4EBA\u7684\u76F8\u8C8C\uFF0C\u4F17\u4EBA\u90FD\u6697\u6697\u558A\u4E86\u58F0\u5F69\u3002</p><p>\u7075\u7389\u81EA\u6043\u51ED\u81EA\u5DF1\u7684\u529F\u592B\u4ECE\u5341\u4F59\u4E08\u7684\u9AD8\u5904\u4E00\u8DC3\u800C\u4E0B\uFF0C\u4E0D\u8E29\u788E\u4E00\u7247\u67AF\u53F6\uFF0C\u52C9\u5F3A\u4E5F\u53EF\u4EE5\u529E\u5230\uFF0C\u4F46\u50CF\u8FD9\u4E24\u4EBA\u822C\u4E3E\u91CD\u82E5\u8F7B\uFF0C\u5374\u6709\u6240\u4E0D\u53CA\uFF0C\u6697\u6697\u601D\u7D22\u7247\u523B\uFF0C\u4E0D\u7531\u5FC3\u5934\u4E00\u7D27\u3002</p><p>\u90A3\u7537\u5B50\u6C89\u58F0\u9053\uFF1A\u300C\u957F\u9E70\u4F1A\u859B\u5E2E\u4E3B\u706D\u6389\u5E7F\u9633\u5E2E\uFF0C\u53C8\u7A81\u7136\u4F20\u4F4D\uFF0C\u53EF\u662F\u661F\u6708\u6E56\u5728\u5E55\u540E\u6307\u4F7F\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u5FAE\u7B11\u9053\uFF1A\u300C\u539F\u6765\u662F\u4E5D\u534E\u5251\u6D3E\u5251\u7434\u4F09\u4FEA\u3002\u300D</p><p>\u4F17\u4EBA\u95FB\u8A00\u53C8\u60CA\u53C8\u559C\uFF0C\u5251\u6C14\u6C5F\u6CB3\u5468\u5B50\u6C5F\u548C\u7434\u58F0\u82B1\u5F71\u51CC\u96C5\u7434\u662F\u4E5D\u534E\u5251\u6D3E\u672C\u4EE3\u6700\u6770\u51FA\u7684\u4EBA\u7269\uFF0C\u5251\u6CD5\u8FDC\u5728\u5E08\u5144\u859B\u957F\u9E70\u4E4B\u4E0A\u3002\u4E5F\u6B63\u662F\u788D\u7740\u4E5D\u534E\u5251\u6D3E\u7684\u9762\u5B50\uFF0C\u4F17\u4EBA\u624D\u6CA1\u6709\u50CF\u9F99\u6218\u91CE\u90A3\u6837\u76F4\u63A5\u51B2\u8FDB\u957F\u9E70\u4F1A\u627E\u859B\u957F\u9E70\u95EE\u4E2A\u660E\u767D\u3002\u6B64\u65F6\u4ED6\u4EEC\u592B\u59BB\u7A81\u7136\u51FA\u73B0\uFF0C\u53C8\u4E0E\u661F\u6708\u6E56\u662F\u654C\u975E\u53CB\uFF0C\u7A0B\u4E00\u9E4F\u7B49\u4EBA\u987F\u89C9\u80DC\u5C40\u5DF2\u5B9A\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u6B64\u4E8B\u4E24\u4F4D\u95EE\u95EE\u859B\u5E2E\u4E3B\u5373\u53EF\uFF0C\u300D\u4ED6\u82E6\u7B11\u7740\u6447\u4E86\u6447\u5934\uFF0C\u300C\u2026\u2026\u5728\u4E0B\u65E0\u8BDD\u53EF\u8BF4\u3002\u300D</p><p>\u51CC\u96C5\u7434\u9053\uFF1A\u300C\u6211\u4EEC\u592B\u59BB\u4E09\u6B21\u767B\u95E8\u62DC\u8BBF\uFF0C\u859B\u5E08\u5144\u90FD\u63A8\u8F9E\u4E0D\u89C1\uFF0C\u8FDE\u6B23\u598D\u4E5F\u4E0D\u9732\u9762\u3002</p><p>\u6C5F\u6E56\u4F20\u8A00\u6B64\u4E8B\u4E0E\u661F\u6708\u6E56\u6709\u5173\uFF0C\u56E0\u6B64\u624D\u5192\u6627\u8BF7\u6559\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6B63\u5BB9\u9053\uFF1A\u300C\u5468\u592B\u4EBA\u592A\u5BA2\u6C14\u4E86\u3002\u6C5F\u6E56\u5982\u6B64\u4F20\u8A00\uFF0C\u5728\u4E0B\u6709\u53E3\u96BE\u8FA9\uFF0C\u8D24\u4F09\u4FEA\u6700\u597D\u8FD8\u662F\u627E\u859B\u5E2E\u4E3B\u95EE\u4E2A\u660E\u767D\u3002\u300D</p><p>\u5468\u5B50\u6C5F\u65C1\u89C2\u826F\u4E45\uFF0C\u867D\u89C9\u6155\u5BB9\u9F99\u8A00\u4E2D\u4E0D\u5C3D\u4E0D\u5B9E\uFF0C\u5927\u6709\u53EF\u7591\u4E4B\u5904\uFF0C\u4F46\u4ED6\u81EA\u91CD\u8EAB\u4EFD\uFF0C\u4E0D\u613F\u4E0E\u6570\u5341\u4EBA\u4E00\u540C\u56F4\u653B\u4E09\u4EBA\uFF0C\u5F53\u4E0B\u62B1\u4E86\u62B1\u62F3\uFF0C\u98D8\u8EAB\u800C\u53BB\u3002</p><p>\u4E00\u9752\u4E00\u9EC4\u4E24\u9053\u8EAB\u5F71\u7728\u773C\u4FBF\u6CA1\u4EBA\u67AB\u6797\uFF0C\u8EAB\u6CD5\u4E4B\u5FEB\uFF0C\u4F17\u4EBA\u65E0\u4E0D\u5FC3\u670D\u3002</p>',73);function t(i,_){return a}var d=e(r,[["render",t]]);export{d as default};
