import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_99" tabindex="-1"><a class="header-anchor" href="#_99" aria-hidden="true">#</a> 99</h1><p>\u6155\u5BB9\u9F99\u521A\u677E\u4E86\u53E3\u6C14\uFF0C\u8033\u4E2D\u53C8\u4F20\u6765\u4E00\u58F0\u67D4\u548C\u7684\u68B5\u5531\u3002</p><p>\u300C\u963F\u5F25\u9640\u4F5B\u3002\u8D2B\u50E7\u5706\u76F8\u8BF7\u6559\u65BD\u4E3B\uFF0C\u9119\u5BFA\u9996\u5EA7\u5706\u901A\uFF0C\u660E\u5B9A\u3001\u660E\u6B62\u4E24\u540D\u5F1F\u5B50\u4E4B\u6B7B\u53EF\u4E0E\u65BD\u4E3B\u6709\u5173\uFF1F\u300D\u8BF4\u8BDD\u8005\u767D\u987B\u767D\u7709\uFF0C\u6B63\u662F\u5927\u5B5A\u7075\u9E6B\u5BFA\u65B9\u4E08\u5706\u76F8\u3002</p><p>\u5FC3\u5FF5\u767E\u8F6C\u95F4\uFF0C\u6155\u5BB9\u9F99\u6C89\u58F0\u9053\uFF1A\u300C\u65B9\u4E08\u4F55\u51FA\u6B64\u8A00\uFF1F\u300D</p><p>\u300C\u5706\u901A\u5E08\u5F1F\u4E09\u4E2A\u6708\u524D\u5728\u6D1B\u9633\u5931\u8E2A\uFF0C\u5E78\u5F97\u7A0B\u5821\u4E3B\u7B49\u4EBA\u76F8\u52A9\uFF0C\u5728\u57CE\u5916\u627E\u5230\u4E09\u4EBA\u7684\u5C38\u8EAB\u3002\u5706\u901A\u5E08\u5F1F\u88AB\u4EBA\u4E00\u5200\u65AD\u5589\uFF0C\u89C2\u660E\u5B9A\u3001\u660E\u6B62\u4E24\u4EBA\u7684\u4F24\u52BF\uFF0C\u5E94\u4E3A\u5DF4\u9675\u4E00\u67AD\u5B89\u5B50\u5B8F\u5F2F\u94A9\u6240\u4F24\u3002\u5B89\u5B50\u5B8F\u81EA\u8D74\u65BD\u4E3B\u5A5A\u5BB4\u540E\u4FBF\u672A\u51FA\u73B0\uFF0C\u56E0\u6B64\u8D2B\u50E7\u624D\u6709\u6B64\u95EE\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u542C\u4ED6\u7D6E\u7D6E\u53E8\u53E8\u8BF4\u4E86\u534A\u664C\uFF0C\u5FC3\u77E5\u6B64\u4E8B\u96BE\u4EE5\u5584\u4E86\uFF0C\u5F53\u4E0B\u6717\u58F0\u9053\uFF1A\u300C\u6B64\u4E8B\u6709\u8BF8\u591A\u96BE\u660E\u4E4B\u5904\uFF0C\u5DF4\u9675\u67AD\u53C8\u4E0D\u5728\u6B64\u95F4\uFF0C\u65E0\u6CD5\u5BF9\u8BC1\u3002\u53EA\u51ED\u4F24\u60C5\u8BBA\u65AD\u6050\u6015\u96BE\u4EE5\u670D\u4EBA\u3002\u300D</p><p>\u5706\u76F8\u6697\u9053\u8FD9\u4E2A\u8C26\u548C\u6709\u793C\u7684\u5E74\u8F7B\u4EBA\u6240\u8A00\u751A\u662F\uFF0C\u81EA\u5DF1\u53EA\u51ED\u4E24\u4EBA\u7684\u4F24\u52BF\u548C\u5B89\u5B50\u5B8F\u66FE\u8D74\u661F\u6708\u6E56\u8FD9\u4E24\u6761\u6A21\u7CCA\u4E0D\u6E05\u7684\u7EBF\u7D22\uFF0C\u4FBF\u6307\u8D23\u662F\u661F\u6708\u6E56\u6240\u4E3A\uFF0C\u786E\u5B9E\u5B5F\u6D6A\u4E86\u4E9B\u3002</p><p>\u4E0D\u6599\u6155\u5BB9\u9F99\u8BDD\u98CE\u4E00\u8F6C\uFF0C\u300C\u4F46\u5728\u4E0B\u76F8\u4FE1\u5927\u5E08\u975E\u662F\u4FE1\u53E3\u5F00\u6CB3\u4E4B\u8F88\u3002\u5B89\u5B50\u5B8F\u73B0\u5DF2\u5165\u6211\u795E\u6559\uFF0C\u6B64\u4E8B\u6155\u5BB9\u9F99\u613F\u4E3A\u5B89\u4F9B\u5949\u627F\u62C5\u3002\u300D\u8BF4\u7740\u8EAC\u8170\u6DF1\u65BD\u4E00\u793C\uFF0C\u300C\u8BF7\u5927\u5E08\u8D50\u6559\u3002\u300D</p><p>\u8FD9\u4E2A\u5E74\u8F7B\u4EBA\u7ADF\u7136\u4E00\u53E3\u5E94\u627F\u5B89\u5B50\u5B8F\u5DF2\u5165\u661F\u6708\u6E56\uFF0C\u53EA\u56E0\u81EA\u5DF1\u6709\u6B64\u6000\u7591\u4FBF\u5168\u7136\u76F8\u4FE1\uFF0C\u66F4\u613F\u66FF\u5C5E\u4E0B\u627F\u62C5\u8D23\u4EFB\u2014\u2014\u975E\u4F46\u6709\u4FE1\u6709\u4E49\u800C\u4E14\u6709\u4EC1\u6709\u52C7\uFF0C\u5B9E\u662F\u96BE\u5F97\u3002\u661F\u6708\u6E56\u6076\u540D\u662D\u7740\uFF0C\u600E\u4F1A\u6709\u8FD9\u6837\u7684\u5BAB\u4E3B\uFF1F</p><p>\u6155\u5BB9\u9F99\u6BEB\u65E0\u52A8\u4F5C\uFF0C\u663E\u7136\u662F\u7B49\u5706\u76F8\u5148\u52A8\u624B\u3002\u5706\u76F8\u7565\u4E00\u6C89\u541F\uFF0C\u50E7\u888D\u8F7B\u626C\uFF0C\u9694\u7A7A\u4E00\u638C\u671D\u6155\u5BB9\u9F99\u80F8\u53E3\u5370\u53BB\u3002\u4ED6\u6709\u5FC3\u8BD5\u63A2\u6155\u5BB9\u9F99\u6B66\u529F\u6DF1\u6D45\uFF0C\u8FD9\u4E00\u638C\u53EA\u7528\u4E0A\u4E86\u4E94\u6210\u529F\u529B\u3002</p><p>\u5927\u5B5A\u7075\u9E6B\u5BFA\u5EFA\u5BFA\u516D\u767E\u4F59\u5E74\uFF0C\u5386\u4EE3\u9AD8\u624B\u8F88\u51FA\uFF0C\u65B9\u4E08\u5706\u76F8\u4E00\u5411\u5C11\u6D89\u4E16\u52A1\uFF0C\u6B64\u756A\u4E3A\u5E08\u5F1F\u4E4B\u6B7B\u800C\u4EB2\u4E0B\u6E05\u51C9\u5C71\uFF0C\u7A0B\u4E00\u9E4F\u7B49\u4EBA\u624D\u6709\u80C6\u4F0F\u51FB\u8FD9\u4E2A\u795E\u79D8\u83AB\u6D4B\u7684\u661F\u6708\u6E56\u5BAB\u4E3B\u3002\u89C1\u72B6\u4F17\u4EBA\u7EB7\u7EB7\u6563\u5F00\uFF0C\u90FD\u77AA\u5927\u773C\u775B\uFF0C\u770B\u8FD9\u573A\u90AA\u6559\u81F3\u5C0A\u4E0E\u767D\u9053\u9886\u8896\u4E4B\u4E89\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u95EA\u4E0D\u907F\uFF0C\u4EFB\u7531\u52B2\u6C14\u5411\u80F8\u53E3\u8981\u5BB3\u62CD\u6765\uFF0C\u5706\u76F8\u5FC3\u4E0B\u5927\u5947\uFF0C\u4ED6\u8FD9\u53C2\u7985\u638C\u770B\u4F3C\u5E73\u5E73\u65E0\u5947\uFF0C\u5176\u5B9E\u5A01\u529B\u6781\u5927\uFF0C\u7985\u5FC3\u901A\u900F\u4E0B\uFF0C\u65E0\u8BBA\u654C\u624B\u5982\u4F55\u53CD\u5E94\u90FD\u4F1A\u5F15\u8D77\u638C\u6CD5\u7684\u5FAE\u5999\u6539\u53D8\uFF0C\u6559\u4EBA\u65E0\u6CD5\u6478\u6E05\u638C\u52BF\u3002\u4F46\u6155\u5BB9\u9F99\u7684\u53CD\u5E94\u5374\u662F\u6BEB\u65E0\u53CD\u5E94\uFF0C\u4E00\u52A8\u4E0D\u52A8\u50CF\u662F\u7B49\u7740\u6328\u638C\u4E00\u822C\u3002</p><p>\u5F53\u638C\u98CE\u89E6\u5230\u8863\u670D\uFF0C\u5706\u76F8\u7EC8\u4E8E\u660E\u767D\u6155\u5BB9\u9F99\u771F\u662F\u8981\u751F\u751F\u6328\u81EA\u5DF1\u4E00\u638C\uFF0C\u6B64\u65F6\u6536\u52BF\u5DF2\u7136\u4E0D\u53CA\uFF0C\u8FDE\u5FD9\u624B\u638C\u4E00\u6643\uFF0C\u546F\u7684\u6253\u5728\u6155\u5BB9\u9F99\u80A9\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u5E94\u624B\u98D8\u98DE\u4E08\u8BB8\uFF0C\u52C9\u5F3A\u7A33\u4F4F\u8EAB\u5F62\uFF0C\u5410\u4E86\u53E3\u9C9C\u8840\uFF0C\u5598\u606F\u9053\uFF1A\u300C\u591A\u8C22\u65B9\u4E08\u624B\u4E0B\u7559\u60C5\u3002\u5728\u4E0B\u5148\u4E3A\u5B89\u4F9B\u5949\u8D54\u793C\uFF0C\u65E5\u540E\u5B9A\u8BA9\u4ED6\u4EB2\u8D74\u8D35\u5BFA\u89E3\u91CA\u6B64\u4E8B\u3002\u300D</p><p>\u5706\u76F8\u9ED8\u7136\u7247\u523B\uFF0C\u5408\u4EC0\u9053\uFF1A\u300C\u65BD\u4E3B\u820D\u8EAB\u9972\u864E\uFF0C\u5982\u6B64\u5927\u4E49\u5927\u52C7\uFF0C\u8001\u8872\u4F69\u670D\u3002\u6B64\u4E8B\u5C31\u6B64\u4F5C\u7F62\u3002\u300D\u8BF4\u7F62\u53EA\u6000\u91CC\u638F\u51FA\u4E00\u679A\u5927\u5982\u86CB\u9EC4\u7684\u836F\u4E38\uFF0C\u300C\u6B64\u836F\u662F\u9119\u5BFA\u7075\u4E39\uFF0C\u5E0C\u671B\u80FD\u6709\u52A9\u4E8E\u65BD\u4E3B\u4F24\u52BF\u3002\u300D\u4ED6\u53F9\u4E86\u53E3\u6C14\uFF0C\u65BD\u793C\u79BB\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u5766\u7136\u670D\u4E0B\u4E39\u836F\uFF0C\u95ED\u76EE\u8C03\u606F\u3002\u7A0B\u4E00\u9E4F\u7B49\u4EBA\u5927\u773C\u77AA\u5C0F\u773C\uFF0C\u62FF\u4E0D\u5B9A\u4E3B\u610F\u662F\u8BE5\u4E58\u673A\u51FA\u624B\u8FD8\u662F\u8BB2\u7A76\u4FA0\u4E49\u3002</p><p>\u6C89\u5BC2\u4E2D\uFF0C\u4E00\u4E2A\u6E05\u60A6\u7684\u5973\u58F0\u4ECE\u4EBA\u7FA4\u540E\u4F4E\u4F4E\u54CD\u8D77\uFF0C\u300C\u73B2\u59D0\uFF0C\u6211\u8981\u56DE\u53BB\u4E86\u2026\u2026\u300D</p><p>\u300C\u600E\u4E48\u4E86\uFF0C\u5C0F\u9526\uFF1F\u300D</p><p>\u7A0B\u4E00\u9E4F\u76B1\u8D77\u7709\u5934\uFF0C\u626D\u5934\u770B\u4E86\u770B\u90A3\u4E24\u540D\u5973\u5B50\u3002\u5E74\u7EAA\u7565\u5927\u7684\u662F\u9752\u9633\u5927\u4FA0\u7530\u542F\u4E1C\u7684\u9057\u5B40\u6BB5\u73B2\uFF0C\u53E6\u4E00\u4E2A\u5999\u9F84\u5C11\u5973\uFF0C\u662F\u4E0E\u5979\u7ED3\u4F34\u800C\u6765\u7684\u5BB9\u9526\u3002</p><p>\u5BB9\u9526\u6C89\u9ED8\u7247\u523B\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4ED6\u4E0D\u50CF\u574F\u4EBA\u2026\u2026\u300D</p><p>\u6BB5\u73B2\u4E5F\u6709\u4E9B\u72B9\u8C6B\uFF0C\u63E1\u7740\u67F3\u53F6\u5200\u626C\u58F0\u9053\uFF1A\u300C\u6D41\u971C\u5251\u98CE\u5973\u4FA0\u662F\u4E0D\u662F\u5728\u4F60\u4EEC\u6559\u4E2D\uFF1F\u300D</p><p>\u5979\u53D7\u8FC7\u98CE\u665A\u534E\u5927\u6069\uFF0C\u6B64\u4E8B\u975E\u8981\u95EE\u4E2A\u660E\u767D\u3002</p><p>\u98CE\u665A\u534E\u5165\u6559\u4E3A\u5974\u4E4B\u4E8B\uFF0C\u5728\u661F\u6708\u6E56\u523B\u610F\u5BA3\u626C\u4E0B\u65E9\u5DF2\u4F20\u904D\u6C5F\u6E56\uFF0C\u4F46\u6D41\u971C\u5251\u58F0\u540D\u663E\u8D6B\uFF0C\u6B66\u6797\u4E2D\u80DC\u8FC7\u5979\u7684\u4E5F\u6CA1\u6709\u591A\u5C11\uFF0C\u4F17\u4EBA\u8FD8\u4FE1\u7591\u53C2\u534A\uFF0C\u5F53\u4E0B\u90FD\u5C4F\u4F4F\u547C\u5438\uFF0C\u7B49\u5F85\u6155\u5BB9\u9F99\u7684\u56DE\u7B54\u3002</p><p>\u5706\u76F8\u8FD9\u4E00\u638C\u53EA\u7528\u4E0A\u4E86\u4E09\u6210\u529B\uFF0C\u4F46\u4ED6\u529F\u529B\u6DF1\u539A\uFF0C\u65E9\u6709\u6212\u5907\u7684\u6155\u5BB9\u9F99\u8FD8\u662F\u53D7\u4E86\u4E9B\u5185\u4F24\u3002\u54FC\uFF0C\u7528\u8FD9\u70B9\u4F24\u6362\u5F97\u654C\u65B9\u6700\u5F3A\u7684\u51E0\u4EBA\u5148\u540E\u79BB\u53BB\uFF0C\u7B97\u6765\u8FD8\u662F\u5927\u5360\u4FBF\u5B9C\u3002</p><p>\u89C1\u6155\u5BB9\u9F99\u9ED8\u4E0D\u4F5C\u58F0\uFF0C\u4F17\u4EBA\u53EB\u58F0\u8D8A\u6765\u8D8A\u54CD\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u5927\u9A82\uFF0C\u300C\u8FD9\u5E2E\u53EA\u4F1A\u5E9F\u8BDD\u7684\u50BB\u74DC\uFF0C\u4E00\u4F1A\u513F\u600E\u4E48\u6B7B\u7684\u90FD\u4E0D\u77E5\u9053\uFF01\u300D\u5979\u5C0F\u5FC3\u6253\u91CF\u7740\u5468\u56F4\u7684\u5730\u52BF\uFF0C\u76D8\u7B97\u600E\u4E48\u51ED\u81EA\u5DF1\u4E0D\u8DB3\u4E09\u6210\u7684\u529F\u529B\u9003\u51FA\u751F\u5929\u3002</p><p>\u6000\u91CC\u7684\u8C82\u88D8\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u7D2B\u73AB\u6697\u53F9\u4E00\u58F0\uFF0C\u62E8\u5F00\u88D8\u9886\u3002</p><p>\u8427\u4F5B\u5974\u542C\u5230\u5916\u9762\u7684\u58F0\u54CD\uFF0C\u5FCD\u4E0D\u4F4F\u5C0F\u58F0\u95EE\u9053\uFF1A\u300C\u4ED6\u2026\u2026\u600E\u4E48\u6837\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u5C06\u6BCD\u4EB2\u5C0F\u5FC3\u5730\u653E\u5728\u978D\u524D\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u6CA1\u6B7B\u3002\u300D</p><p>\u9ED1\u8272\u7684\u8C82\u88D8\u9732\u51FA\u4E00\u62B9\u8273\u5149\uFF0C\u8427\u4F5B\u5974\u626C\u8D77\u81FB\u9996\uFF0C\u6084\u6084\u770B\u4E86\u6155\u5BB9\u9F99\u4E00\u773C\u3002</p><p>\u300C\u767E\u82B1\u89C2\u97F3\uFF1F\u300D\u4E00\u77A5\u95F4\uFF0C\u5C31\u6709\u4EBA\u770B\u6E05\u4E86\u5979\u7684\u9762\u5BB9\u3002</p><p>\u8427\u4F5B\u5974\u7389\u8138\u98DE\u7EA2\uFF0C\u8FDE\u5FD9\u8EB2\u8FDB\u8863\u5185\u3002</p><p>\u300C\u5AC2\u592B\u4EBA\uFF01\u300D\u4E00\u4E2A\u4E09\u7EFA\u957F\u987B\u7684\u5112\u96C5\u6587\u58EB\u6392\u4F17\u800C\u51FA\uFF0C\u60CA\u53EB\u9053\uFF0C\u300C\u4F60\u600E\u4E48\u5728\u8FD9\u91CC\uFF1F\u300D</p><p>\u7D2B\u73AB\u4E00\u6014\uFF0C\u773C\u524D\u8FD9\u4EBA\u4F9D\u7A00\u6709\u4E9B\u9762\u719F\uFF0C\u4F3C\u4E4E\u662F\u5F53\u65E5\u300C\u7236\u4EB2\u300D\u6155\u5BB9\u536B\u9886\u81EA\u5DF1\u89C1\u8FC7\u7684\u957F\u8F88\u3002</p><p>\u8427\u4F5B\u5974\u542C\u51FA\u662F\u300C\u4E08\u592B\u300D\u7684\u597D\u53CB\u51DD\u5149\u5251\u4E1C\u65B9\u5E86\uFF0C\u987F\u65F6\u9762\u7EA2\u8FC7\u8033\uFF0C\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\u3002</p><p>\u8FD9\u4E9B\u65E5\u5B50\u5979\u5C48\u670D\u5728\u6155\u5BB9\u9F99\u6DEB\u5A01\u4E4B\u4E0B\uFF0C\u65E9\u5DF2\u6DE1\u5FD8\u4E86\u5F80\u65E5\u7684\u8EAB\u4EFD\u3002\u6B64\u65F6\u88AB\u6545\u8BC6\u4E00\u558A\uFF0C\u60F3\u5230\u7AEF\u5E84\u5723\u6D01\u7684\u300C\u767E\u82B1\u89C2\u97F3\u300D\u5982\u4ECA\u5374\u662F\u4E0E\u4EB2\u5B50\u4E71\u4F26\u7684\u65E0\u803B\u6DEB\u5987\uFF0C\u8427\u4F5B\u5974\u7F9E\u602F\u96BE\u5F53\uFF0C\u9669\u4E9B\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u300C\u4F0F\u9F99\u6DA7\u88AB\u706D\u679C\u7136\u662F\u661F\u6708\u6E56\u6240\u4E3A\uFF01\u300D\u4E1C\u65B9\u5E86\u6EE1\u8154\u6FC0\u6124\u671B\u7740\u9762\u906E\u8F7B\u7EB1\u7684\u5C11\u5973\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u662F\u4E0D\u662F\u7D2B\u73AB\u4F84\u5973\uFF1F\u300D</p><p>\u4E00\u76F4\u6C89\u9ED8\u7684\u5C11\u5973\u64A9\u8D77\u8F7B\u7EB1\uFF0C\u9732\u51FA\u4E00\u5F20\u4ED9\u5B50\u7684\u7389\u5BB9\uFF0C\u8F7B\u542F\u6731\u5507\u9053\uFF1A\u300C\u4F2F\u4F2F\u2026\u2026\u4F60\u597D\u3002\u300D</p><p>\u4E1C\u65B9\u5E86\u76EE\u5149\u505C\u5728\u7D2B\u73AB\u5FAE\u9686\u7684\u8179\u4E0A\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u6B64\u4EBA\u662F\u4F60\u6740\u7236\u4EC7\u4EBA\uFF0C\u4F60\u4E3A\u4F55\u8FD8\u8981\u59D4\u8EAB\u4E8B\u654C\uFF01\u8FD9\u822C\u4E0D\u77E5\u7F9E\u803B\uFF01\u300D</p><p>\u7075\u7389\u6321\u5728\u4E24\u5973\u8EAB\u524D\uFF0C\u8BF4\u9053\uFF1A\u300C\u8FD9\u662F\u6211\u5BAB\u4E3B\u660E\u5A92\u6B63\u5A36\u7684\u592B\u4EBA\uFF0C\u9601\u4E0B\u653E\u5C0A\u91CD\u4E9B\u3002\u300D</p><p>\u4E1C\u65B9\u5E86\u8138\u8272\u94C1\u9752\uFF0C\u9AD8\u58F0\u9053\uFF1A\u300C\u5AC2\u592B\u4EBA\uFF01\u7D2B\u73AB\u5E74\u7EAA\u5C1A\u5C0F\uFF0C\u4E3A\u4F55\u4F60\u4E5F\u4E0D\u52A0\u963B\u62E6\uFF01\u6B7B\u540E\u6709\u4F55\u9762\u76EE\u89C1\u6211\u6155\u5BB9\u5927\u54E5\uFF01\u300D</p><p>\u65E0\u989C\u4EE5\u5BF9\u7684\u8427\u4F5B\u5974\u65E9\u5DF2\u662F\u6CEA\u5982\u96E8\u4E0B\u3002</p><p>\u4E1C\u65B9\u5E86\u5FC3\u4E0B\u8D77\u7591\uFF0C\u957F\u5251\u6D12\u51FA\u70B9\u70B9\u5BD2\u5149\u8FEB\u5F00\u7075\u7389\uFF0C\u7EB5\u8EAB\u4E00\u628A\u626F\u4F4F\u8C82\u88D8\u3002\u7D2B\u73AB\u8FDE\u5FD9\u62B1\u8D77\u6BCD\u4EB2\uFF0C\u4F46\u5DF2\u665A\u4E86\u4E00\u6B65\u3002</p><p>\u8C82\u76AE\u4E2D\u5206\uFF0C\u9732\u51FA\u4E00\u4E2A\u8273\u4E3D\u65E0\u5339\u7684\u7F8E\u5987\uFF0C\u4E0E\u6614\u65E5\u7684\u300C\u767E\u82B1\u89C2\u97F3\u300D\u76F8\u6BD4\uFF0C\u773C\u524D\u7684\u7F8E\u5987\u8273\u8272\u72B9\u80DC\u4EE5\u5F80\uFF0C\u4F46\u7709\u76EE\u95F4\u7684\u7AEF\u5E84\u534E\u8D35\u5374\u8361\u7136\u65E0\u5B58\uFF0C\u66F4\u4EE4\u4EBA\u60CA\u8BB6\u7684\u662F\u5979\u5706\u9F13\u9F13\u7684\u5C0F\u8179\uFF0C\u770B\u4E0A\u53BB\u6BD4\u5973\u513F\u8FD8\u5927\u3002</p><p>\u4E1C\u65B9\u5E86\u9707\u60CA\u4E4B\u6001\u65E0\u4EE5\u590D\u52A0\uFF0C\u4EA1\u53CB\u5C38\u9AA8\u672A\u5BD2\uFF0C\u59BB\u5973\u5374\u53CC\u53CC\u6000\u5B55\uFF0C\u591A\u534A\u8FD8\u662F\u6BCD\u5973\u4FE9\u5171\u4E8B\u4E00\u592B\u3002\u767E\u82B1\u89C2\u97F3\u548C\u73AB\u7470\u4ED9\u5B50\u90FD\u662F\u540D\u95E8\u95FA\u79C0\uFF0C\u5374\u4F1A\u505A\u51FA\u8FD9\u7B49\u4E11\u4E8B\uFF01</p><p>\u300C\u653E\u5F00\u6211\u5A18\u3002\u300D\u8EAB\u540E\u4F20\u6765\u4E00\u9053\u51B7\u51B0\u51B0\u7684\u58F0\u97F3\u3002</p><p>\u4E1C\u65B9\u5E86\u957F\u5251\u5FAE\u98A4\uFF0C\u60CA\u8BB6\u5730\u671D\u540E\u770B\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u9762\u5982\u5BD2\u51B0\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u6211\u6155\u5BB9\u5BB6\u7684\u4E8B\uFF0C\u6CA1\u6709\u5916\u4EBA\u8BF4\u8BDD\u7684\u4F59\u5730\u3002\u300D</p><p>\u4E1C\u65B9\u5E86\u770B\u770B\u6155\u5BB9\u9F99\uFF0C\u53C8\u770B\u770B\u7D2B\u73AB\uFF0C\u6700\u540E\u76EE\u5149\u843D\u5728\u767E\u82B1\u89C2\u97F3\u8138\u4E0A\uFF0C\u300C\u5AC2\u592B\u4EBA\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u5A18\uFF0C\u544A\u8BC9\u4ED6\u4F60\u73B0\u5728\u7684\u8EAB\u4EFD\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u5507\u98A4\u6296\uFF0C\u534A\u664C\u624D\u8F7B\u58F0\u9053\uFF1A\u300C\u5974\u5BB6\u73B0\u5728\u662F\u9F99\u54E5\u54E5\u7684\u5C0F\u59BE\u2026\u2026\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u573A\u4E2D\u987F\u65F6\u54D7\u7136\uFF0C\u8427\u4F5B\u5974\u96BE\u582A\u5F97\u65E0\u5730\u81EA\u5BB9\uFF0C\u5E78\u597D\u7D2B\u73AB\u62C9\u8D77\u8C82\u88D8\uFF0C\u906E\u4F4F\u4E86\u4F17\u4EBA\u5229\u7BAD\u822C\u7684\u76EE\u5149\uFF0C\u5979\u624D\u5F97\u4EE5\u653E\u58F0\u5927\u54ED\u3002</p><p>\u4E1C\u65B9\u5E86\u54C6\u55E6\u7740\u628A\u5251\u653E\u5165\u9798\u4E2D\uFF0C\u4EE5\u4ED6\u4E00\u5251\u8FEB\u5F00\u7684\u7075\u7389\u7684\u529F\u529B\uFF0C\u6B64\u65F6\u7ADF\u7136\u8FDE\u5251\u90FD\u62FF\u4E0D\u7A33\uFF0C\u56DE\u9798\u65F6\u5272\u7834\u4E86\u624B\u6307\u3002</p><p>\u4ED6\u4F3C\u4E4E\u77AC\u95F4\u8001\u4E86\u5341\u5E74\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u63D0\u7740\u957F\u5251\u8E52\u8DDA\u5730\u8D70\u4E0B\u5C71\u8DEF\u3002\u6F2B\u5929\u7EA2\u53F6\u5377\u8D77\uFF0C\u63A9\u6CA1\u6709\u51DD\u5149\u5251\u8427\u7D22\u7684\u8EAB\u5F71\u3002\u826F\u4E45\u540E\uFF0C\u8EAB\u5F71\u6D88\u5931\u5904\u7A81\u7136\u7206\u53D1\u51FA\u4E00\u9635\u51C4\u51C9\u7684\u5927\u7B11\uFF0C\u6E10\u884C\u6E10\u8FDC\u3002</p><p>\u300C\u4E16\u4E0A\u600E\u4E48\u6709\u8FD9\u822C\u4E0B\u8D31\u7684\u5973\u4EBA\uFF01\u300D\u82B1\u6E90\u5E2E\u5E2E\u4E3B\u66F2\u7389\u5A07\u9119\u5937\u5730\u9A82\u9053\u3002\u4F17\u4EBA\u7FA4\u60C5\u6FC0\u6124\uFF0C\u7EB7\u7EB7\u75DB\u65A5\u6155\u5BB9\u9F99\u79BD\u517D\u4E0D\u5982\u3002\u66F4\u6709\u4EBA\u6C61\u8A00\u79FD\u8BED\uFF0C\u8FB1\u9A82\u767E\u82B1\u89C2\u97F3\u548C\u73AB\u7470\u4ED9\u5B50\u80CC\u5FB7\u5931\u8282\u3002</p><p>\u88AB\u4EBA\u5F53\u9762\u4E00\u9635\u300C\u6DEB\u5987\u300D\u3001\u300C\u8D31\u8D27\u300D\u7684\u4E71\u9A82\uFF0C\u8427\u4F5B\u5974\u56FA\u7136\u54ED\u5F97\u5929\u660F\u5730\u6697\uFF0C\u7D2B\u73AB\u4FCF\u8138\u4E5F\u65F6\u7EA2\u65F6\u767D\u3002</p><p>\u6307\u8D23\u58F0\u94FA\u5929\u76D6\u5730\u800C\u6765\uFF0C\u8815\u52A8\u7684\u5634\u5DF4\u8FDE\u6210\u4E00\u7247\uFF0C\u98DE\u6E85\u7684\u553E\u6CAB\u5C06\u6BCD\u5973\u4FE9\u5F7B\u5E95\u6DF9\u6CA1\u3002</p><p>\u300C\u4E0D\u80FD\u54ED\uFF0C\u4E0D\u80FD\u54ED\u2026\u2026\u300D\u6155\u5BB9\u7D2B\u73AB\u5014\u5F3A\u5730\u6311\u8D77\u4E0B\u5DF4\uFF0C\u6F20\u7136\u51B7\u89C6\u8FD9\u5E2E\u4E49\u6124\u586B\u81BA\u7684\u6B66\u6797\u767D\u9053\u3002</p><p>\u5FFD\u7136\uFF0C\u4E00\u80A1\u51B7\u5F97\u8BA9\u4EBA\u8840\u6DB2\u51DD\u7ED3\u7684\u5BD2\u610F\u6D8C\u5165\u67AB\u6797\uFF0C\u53EB\u9A82\u58F0\u50CF\u88AB\u4E00\u5200\u65A9\u65AD\u822C\u6D88\u5931\u4E86\u3002</p><p>\u4F17\u4EBA\u8D6B\u7136\u53D1\u73B0\uFF0C\u90A3\u4E2A\u5F6C\u5F6C\u6709\u793C\u7684\u5E74\u8F7B\u4EBA\u6B63\u9759\u6084\u6084\u7ACB\u5728\u5982\u8840\u7684\u7EA2\u53F6\u4E4B\u95F4\uFF0C\u6D51\u8EAB\u6563\u7740\u4E00\u80A1\u9634\u51B7\u7684\u6B7B\u4EA1\u6C14\u606F\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u51B0\u51B0\u7AD6\u8D77\u4E00\u6839\u624B\u6307\uFF0C\u300C\u6BCF\u4EBA\u90FD\u6709\u4E00\u62DB\u7684\u673A\u4F1A\u3002\u300D</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
